	[4000101] = { id = 4000101, teach_id = 40001, close_delay_time = 0, content = "<color=#dc8c00>噬脉猎手</color>藏匿于世界中\n在旅途中靠近发现的猎手后，可在地图中查看他们", img = "Textures/Icon/Single/TipsImg/mercenary1.png", res_type = 1, show_close_button = true, teach_stage = 1, topic = "噬脉猎手", video = "",},
	[4000102] = { id = 4000102, teach_id = 40001, close_delay_time = 0, content = "在旅途中，任何对悬天不利的行为（如<color=#dc8c00>击败噬脉生物、攻克据点领取奖励</color>等）\n都将提高悬天对青乌的<color=#dc8c00>警戒值</color>", img = "Textures/Icon/Single/TipsImg/mercenary2.png", res_type = 1, show_close_button = true, teach_stage = 2, topic = "警戒值", video = "",},
	[4000103] = { id = 4000103, teach_id = 40001, close_delay_time = 0, content = "警戒值达到一定值，悬天将发出<color=#dc8c00>密令</color>，将会有猎手前来<color=#dc8c00>追击</color>青乌。\n警戒值越高，将会有越强的猎手前来追击，<color=#dc8c00>击败</color>他们可以获得<color=#dc8c00>奖励</color>", img = "Textures/Icon/Single/TipsImg/mercenary3.png", res_type = 1, show_close_button = true, teach_stage = 3, topic = "猎手追击", video = "",},
	[4000104] = { id = 4000104, teach_id = 40001, close_delay_time = 0, content = "躲匿猎手的追击，一段时间后将逐渐减少警戒值\n或可以选择支付金币，清除所有警戒值", img = "Textures/Icon/Single/TipsImg/mercenary4.png", res_type = 1, show_close_button = true, teach_stage = 4, topic = "清除警戒", video = "",},
	[4000105] = { id = 4000105, teach_id = 40001, close_delay_time = 0, content = "<color=#dc8c00>每日前4次</color>击败猎手，除了常规掉落外，还将获得<color=#dc8c00>每日悬赏奖励</color>\n在<color=#dc8c00>每日4:00</color>将重置次数", img = "Textures/Icon/Single/TipsImg/mercenary5.png", res_type = 1, show_close_button = true, teach_stage = 5, topic = "每日悬赏", video = "",},
	[4000106] = { id = 4000106, teach_id = 40001, close_delay_time = 0, content = "每次击败猎手，都将获得<color=#dc8c00>密令位阶经验</color>。\n每达到新的位阶可领取对应<color=#dc8c00>位阶奖励</color>", img = "Textures/Icon/Single/TipsImg/mercenary6.png", res_type = 1, show_close_button = true, teach_stage = 6, topic = "密令位阶", video = "",},
