	[4000203] = { id = 4000203, teach_id = 40002, close_delay_time = 0, content = "警戒值达到一定值，悬天将发出<color=#dc8c00>密令</color>，将会有猎手前来<color=#dc8c00>追击</color>青乌。\n警戒值越高，将会有越强的猎手前来追击，<color=#dc8c00>击败</color>他们可以获得<color=#dc8c00>奖励</color>", img = "Textures/Icon/Single/TipsImg/mercenary3.png", res_type = 1, show_close_button = true, teach_stage = 3, topic = "猎手追击", video = "",},
