	[4000205] = { id = 4000205, teach_id = 40002, close_delay_time = 0, content = "<color=#dc8c00>每日前4次</color>击败猎手，除了常规掉落外，还将获得<color=#dc8c00>每日悬赏奖励</color>\n在<color=#dc8c00>每日4:00</color>将重置次数", img = "Textures/Icon/Single/TipsImg/mercenary5.png", res_type = 1, show_close_button = true, teach_stage = 5, topic = "每日悬赏", video = "",},
	[4000206] = { id = 4000206, teach_id = 40002, close_delay_time = 0, content = "每次击败猎手，都将获得<color=#dc8c00>密令位阶经验</color>。\n每达到新的位阶可领取对应<color=#dc8c00>位阶奖励</color>", img = "Textures/Icon/Single/TipsImg/mercenary6.png", res_type = 1, show_close_button = true, teach_stage = 6, topic = "密令位阶", video = "",},
	[4000301] = { id = 4000301, teach_id = 40003, close_delay_time = 0, content = "<color=#dc8c00>气脉竹</color>是气脉能量的汇聚体，拥有改变天赋的能力", img = "Textures/Icon/Single/TipsImg/talent1.png", res_type = 1, show_close_button = true, teach_stage = 1, topic = "气脉竹", video = "",},
	[4000302] = { id = 4000302, teach_id = 40003, close_delay_time = 0, content = "与气脉竹交互，可选择天赋，进行解锁/提升\n共有<color=#dc8c00>运动能力、战斗能力、生活能力</color>三种维度的天赋可解锁/提升", img = "Textures/Icon/Single/TipsImg/talent2.png", res_type = 1, show_close_button = true, teach_stage = 2, topic = "天赋分类", video = "",},
	[4000303] = { id = 4000303, teach_id = 40003, close_delay_time = 0, content = "天赋的解锁/提升，需要消耗一定数量的<color=#dc8c00>【脉灵】</color>", img = "Textures/Icon/Single/TipsImg/talent3.png", res_type = 1, show_close_button = true, teach_stage = 3, topic = "天赋提升消耗", video = "",},
	[4000304] = { id = 4000304, teach_id = 40003, close_delay_time = 0, content = "脉灵的获取：在世界的各处，藏匿着脉灵\n满足它们的要求，它们将回归气脉竹，并赠予玩家<color=#dc8c00>脉灵</color>", img = "Textures/Icon/Single/TipsImg/talent4.png", res_type = 1, show_close_button = true, teach_stage = 4, topic = "脉灵的获取", video = "",},
	[4000401] = { id = 4000401, teach_id = 40004, close_delay_time = 0, content = "完成<color=#dc8c00>每日活跃任务</color>是气脉能量的汇聚体，可以获得活跃度。", img = "Textures/Icon/Single/TipsImg/activity1.png", res_type = 1, show_close_button = true, teach_stage = 1, topic = "每日活跃", video = "",},
	[4000402] = { id = 4000402, teach_id = 40004, close_delay_time = 0, content = "<color=#dc8c00>活跃度</color>达到指定进度，可以获得对应的<color=#dc8c00>奖励</color>。", img = "Textures/Icon/Single/TipsImg/activity2.png", res_type = 1, show_close_button = true, teach_stage = 2, topic = "活跃度奖励", video = "",},
	[4000403] = { id = 4000403, teach_id = 40004, close_delay_time = 0, content = "每日活跃度获取会有<color=#dc8c00>上限</color>，达到上限后不再获得活跃度。\n<color=#dc8c00>每日四点</color>会刷新每日活跃任务，重置活跃度奖励。", img = "Textures/Icon/Single/TipsImg/activity3.png", res_type = 1, show_close_button = true, teach_stage = 3, topic = "任务重置", video = "",},
	[4000501] = { id = 4000501, teach_id = 40005, close_delay_time = 0, content = "玩家可通过任务奖励、商店购买等途径获得<color=#dc8c00>冶炼配方</color>。\n当玩家学习冶炼配方后可制作相关道具增强自身。", img = "Textures/Icon/Single/TipsImg/alchemy1.png", res_type = 1, show_close_button = true, teach_stage = 1, topic = "配方学习", video = "",},
	[4000502] = { id = 4000502, teach_id = 40005, close_delay_time = 0, content = "每种冶炼材料都含有<color=#dc8c00>五行元素</color>，\n进行冶炼时，<color=#dc8c00>五行元素</color>会转换为<color=#dc8c00>阴阳元素</color>并根据数量大小影响最终产物。", img = "Textures/Icon/Single/TipsImg/alchemy2.png", res_type = 1, show_close_button = true, teach_stage = 2, topic = "五行元素", video = "",},
	[4000503] = { id = 4000503, teach_id = 40005, close_delay_time = 0, content = "不同的<color=#dc8c00>阴阳属性</color>产物获得的加成也会有不同。\n在冶炼时选择自己需要的产物进行冶炼是最佳方案。", img = "Textures/Icon/Single/TipsImg/alchemy3.png", res_type = 1, show_close_button = true, teach_stage = 3, topic = "阴阳平衡", video = "",},
	[4000504] = { id = 4000504, teach_id = 40005, close_delay_time = 0, content = "<color=#dc8c00>已解锁配方</color>列表可储存玩家不同的冶炼方案，\n相同阴阳状态的配方最多储存三条，列表最多储存九条冶炼方案。", img = "Textures/Icon/Single/TipsImg/alchemy4.png", res_type = 1, show_close_button = true, teach_stage = 4, topic = "已解锁配方", video = "",},
	[4000601] = { id = 4000601, teach_id = 40006, close_delay_time = 0, content = "冒险等级达到<color=#dc8c00>10/20/30/35/40/45/50级</color>时，将会卡等级。\n需要完成对应<color=#dc8c00>世界等级任务</color>，才能<color=#dc8c00>继续升级</color>", img = "Textures/Icon/Single/TipsImg/worldlevel1.png", res_type = 1, show_close_button = true, teach_stage = 1, topic = "世界等级", video = "",},
	[4000602] = { id = 4000602, teach_id = 40006, close_delay_time = 0, content = "卡等级时，获得的经验<color=#dc8c00>不会浪费</color>，可继续获得。\n在突破世界等级后，将一次性<color=#dc8c00>转换</color>成对应等级", img = "Textures/Icon/Single/TipsImg/worldlevel2.png", res_type = 1, show_close_button = true, teach_stage = 2, topic = "经验保存", video = "",},
	[4000603] = { id = 4000603, teach_id = 40006, close_delay_time = 0, content = "突破世界等级，冒险中的<color=#dc8c00>奖励</color>将变得更加丰厚。\n相应的，世界中的敌人也将变得<color=#dc8c00>更加强大</color>", img = "Textures/Icon/Single/TipsImg/worldlevel3.png", res_type = 1, show_close_button = true, teach_stage = 3, topic = "世界等级效果", video = "",},
	[4000604] = { id = 4000604, teach_id = 40006, close_delay_time = 0, content = "可以选择<color=#dc8c00>降低世界等级</color>。\n只可在当前世界等级相邻的世界等级之间进行切换。切换后，敌人等级和获得奖励将相应变化", img = "Textures/Icon/Single/TipsImg/worldlevel4.png", res_type = 1, show_close_button = true, teach_stage = 4, topic = "世界等级调整", video = "",},
