	["100106_1"] = { id = 100106, level = 1, desc_info = {{ [2] = "出现<color=#e89e1d>红眼预警</color>时，该次攻击只能<color=#e89e1d>闪避</color>无法弹反\n在该次攻击前按下<color=#e89e1d>闪避按钮</color>可触发<color=#e89e1d>念虚领域</color>\n成功闪避可增加角色日相，按下普攻获得额外收益",},}, fight_marks = {99100106,},},
	["100107_1"] = { id = 100107, level = 1, desc_info = {{ [2] = "头顶出现<color=#e89e1d>向上预警</color>时，该次攻击可被<color=#e89e1d>跳跃反击</color>\n在被攻击之前按下<color=#e89e1d>跳跃按钮</color>可触发\n跳跃反击能够<color=#e89e1d>打断</color>敌人技能、积累敌方弱点槽、增加角色日相",},}, fight_marks = {99100107,},},
	["100108_1"] = { id = 100108, level = 1, desc_info = {{"弱点槽","成功命中敌人都将积累敌人弱点槽：<color=#e89e1d>普攻</color>可<color=#e89e1d>小幅</color>积累敌人弱点槽；<color=#e89e1d>释放战技</color>或<color=#e89e1d>反击成功</color>（闪避反击、弹刀反击、跳跃反击）可<color=#e89e1d>中幅</color>积累敌人弱点槽；<color=#e89e1d>核心被动</color>可<color=#e89e1d>大幅</color>积累敌人弱点槽。\n敌人失衡：弱点槽满时，敌人将<color=#e89e1d>失衡眩晕</color>，此时可选择释放全队任意队员的<color=#e89e1d>五行绝技</color>",},{"日相","消耗日相可<color=#e89e1d>释放战技</color>。\n获得日相：<color=#e89e1d>普攻命中/非战斗中</color>将逐渐恢复日相；<color=#e89e1d>反击成功</color>（闪避反击、弹刀反击、跳跃反击）时，将直接获得<color=#e89e1d>1</color>格日相。最多可积攒<color=#e89e1d>3</color>格日相。\n失衡：被敌人命中时，将损失一定日相。当日相不足时，将会失衡<color=#e89e1d>眩晕</color>。",},{"月相","消耗月相将释放<color=#e89e1d>强力战技</color>。\n获得月相：成功释放<color=#e89e1d>五行绝技</color>时，获得<color=#e89e1d>3</color>格月相；最多可积攒<color=#e89e1d>3</color>格月相",},}, fight_marks = {99100108,},},
	["100109_1"] = { id = 100109, level = 1, desc_info = {{ [2] = "叙慕火属性伤害加成提升30%。\n青乌火焰的根源实际是司命，得益于司命的契约，就算是余烬也能如此自由行走于大地之上。",},}, fight_attrs = {{57,3000,},},},
	["100151_1"] = { id = 100151, level = 1, desc_info = {{ [2] = "战技-青乌术·起等级上限提升3级",},}, fight_marks = {99100151,}, skill_level_addition = {{100102,3,},},},
