	[13001] = { talk_id = 13001, group_id = 13, type = 4, next_id = 13002, aside = "<color=#FFEBA2>？？？：</color>我记得你是上次帮了我的对吧，在这干嘛", talk_content = "我记得你是上次帮了我的对吧，在这干嘛", talk_name = "？？？",},
	[13002] = { talk_id = 13002, group_id = 13, type = 4, aside = "<color=#FFEBA2>？？？：</color>呃，借车啊……你可要完整开回来。", talk_content = "呃，借车啊……你可要完整开回来。", talk_name = "？？？",},
	[14001] = { talk_id = 14001, group_id = 14, type = 1, next_id = 14002, fade_in = 1, fade_out = true, not_skip = true, talk_content = "谢谢你，要不是你及时赶到我可能就……", talk_name = "？？？",},
	[14002] = { talk_id = 14002, group_id = 14, type = 2, not_skip = true, options = {{"熙来是？",14003,},{"（脉者……）",14004,},{"<color=#FFEBA2>那你为什么会在这里？</color>",14005,},}, talk_content = "对了，我叫于静，是住在小城熙来的居民，你是脉者对吧！", talk_name = "于静",},
	[14003] = { talk_id = 14003, group_id = 14, type = 3, next_id = 14002, not_skip = true, talk_content = "熙来是一个主要由创生地科研队管理的生态小城，有着些民居和商铺，\n就在这前面不远哦。", talk_name = "于静",},
	[14004] = { talk_id = 14004, group_id = 14, type = 3, next_id = 14002, not_skip = true, talk_content = "（脉者……外面世界貌似都这么称呼可以使用气脉力量的人。）", talk_name = "叙慕",},
	[14005] = { talk_id = 14005, group_id = 14, type = 3, next_id = 14006, not_skip = true, talk_content = "所以为什么会远离城市来这种地方？", talk_name = "叙慕",},
	[14006] = { talk_id = 14006, group_id = 14, type = 3, next_id = 14007, talk_content = "因为熙来刚刚遭到了噬脉生物的入侵，而且还有个拿着镰刀的女人……", talk_name = "于静",},
	[14007] = { talk_id = 14007, group_id = 14, type = 3, next_id = 14008, talk_content = "她不知为何能控制噬脉生命，我只好匆忙逃到了这里。", talk_name = "于静",},
	[14008] = { talk_id = 14008, group_id = 14, type = 3, next_id = 14009, talk_content = "（她口中的应该就是离歌，\n而那把镰刀……大概就是我要找的漆金了。）", talk_name = "叙慕",},
	[14009] = { talk_id = 14009, group_id = 14, type = 3, next_id = 14010, talk_content = "对了，既然话都说到这里了能请你救救我们吗！", talk_name = "于静",},
	[14010] = { talk_id = 14010, group_id = 14, type = 3, next_id = 14011, talk_content = "城里面没太多守卫，要是再继续拖下去的话……", talk_name = "于静",},
	[14011] = { talk_id = 14011, group_id = 14, type = 3, next_id = 14012, talk_content = "就交给我吧，我正好也要走这边。", talk_name = "叙慕",},
	[14012] = { talk_id = 14012, group_id = 14, type = 3, next_id = 14013, talk_content = "谢谢，熙来就在前面，拜托你了……呃。", talk_name = "于静",},
	[14013] = { talk_id = 14013, group_id = 14, type = 3, next_id = 14014, talk_content = "叙慕，这是我名字。", talk_name = "叙慕",},
	[14014] = { talk_id = 14014, group_id = 14, type = 3, next_id = 14015, talk_content = "话说回来，我记得脉者可以通过星碑来和气脉对话来着。", talk_name = "于静",},
	[14015] = { talk_id = 14015, group_id = 14, type = 3, next_id = 14016, talk_content = "你稍后要不要摸摸眼前这个星碑，或许能有什么收获也说不定哦。", talk_name = "于静",},
	[14016] = { talk_id = 14016, group_id = 14, type = 3, next_id = 14017, talk_content = "在摸过星碑之后，一路过桥往有路的地方走就能到熙来了！", talk_name = "于静",},
	[14017] = { talk_id = 14017, group_id = 14, type = 3, talk_content = "（星碑……然后过桥，距离不远，赶紧前进吧。）", talk_name = "叙慕",},
	[16001] = { talk_id = 16001, group_id = 16, type = 4, aside = "<color=#FFEBA2>叙慕：</color>光是毁灭我们的故乡还不够吗，\n离歌。", talk_content = "光是毁灭我们的故乡还不够吗，\n离歌。", talk_name = "叙慕",},
	[17001] = { talk_id = 17001, group_id = 17, type = 4, next_id = 17002, aside = "<color=#FFEBA2>？？？：</color>喂，那边那位，到这边来！", talk_content = "喂，那边那位，到这边来！", talk_name = "？？？",},
	[17002] = { talk_id = 17002, group_id = 17, type = 4, aside = "<color=#FFEBA2>叙慕：</color>谁?", talk_content = "谁?", talk_name = "叙慕",},
	[18001] = { talk_id = 18001, group_id = 18, type = 1, next_id = 18002, fade_out = true, not_skip = true, talk_content = "刚刚在对岸战斗的人是你对吧？", talk_name = "刻刻",},
	[18002] = { talk_id = 18002, group_id = 18, type = 1, next_id = 18003, talk_content = "嗯，刚好碰见需要帮助的人，那你是？", talk_name = "叙慕",},
	[18003] = { talk_id = 18003, group_id = 18, type = 1, next_id = 18004, talk_content = "我的名字是刻刻，红城闪灵宗的脉者。", talk_name = "刻刻",},
	[18004] = { talk_id = 18004, group_id = 18, type = 1, next_id = 18005, talk_content = "叙慕，游浪的青乌。", talk_name = "叙慕",},
	[18005] = { talk_id = 18005, group_id = 18, type = 1, next_id = 18006, talk_content = "青乌……我还是第一次遇见四方土地的人呢，难道说是来治愈气脉的吗？", talk_name = "刻刻",},
	[18006] = { talk_id = 18006, group_id = 18, type = 1, next_id = 18007, talk_content = "虽然那是我的使命，但现在不是来做这个的。", talk_name = "叙慕",},
	[18007] = { talk_id = 18007, group_id = 18, type = 2, options = {{"为什么？",18008,},{"可以。",18009,},}, talk_content = "唔……耐人寻味呢，怎么样，假如要办什么的话也带我一个如何？", talk_name = "刻刻",},
	[18008] = { talk_id = 18008, group_id = 18, type = 3, not_skip = true, talk_content = "所谓多个人多分力，我对青乌也很感兴趣呢。", talk_name = "刻刻",},
	[18009] = { talk_id = 18009, group_id = 18, type = 3, not_skip = true, talk_content = "那就成了，我们走吧。", talk_name = "刻刻",},
	[19001] = { talk_id = 19001, group_id = 19, type = 4, next_id = 19002, aside = "<color=#FFEBA2>刻刻：</color>顺带一提，我使用的武器是咒符和手枪，要瞄准什么就交给我吧。", talk_content = "顺带一提，我使用的武器是咒符和手枪，要瞄准什么就交给我吧。", talk_name = "刻刻",},
	[19002] = { talk_id = 19002, group_id = 19, type = 4, next_id = 19003, aside = "<color=#FFEBA2>刻刻：</color>倒是之后能不能也让我见识一下\n青乌‘治愈’的本事？", talk_content = "倒是之后能不能也让我见识一下\n青乌‘治愈’的本事？", talk_name = "刻刻",},
