	[20002] = { talk_id = 20002, group_id = 20, type = 5, next_id = 20003, aside = "<color=#FFEBA2>刻刻：</color>这个状况……叙慕，你在追的到底是什么东西？", talk_content = "这个状况……叙慕，你在追的到底是什么东西？", talk_name = "刻刻",},
