	[24002] = { talk_id = 24002, group_id = 24, type = 5, next_id = 24003, aside = "<color=#FFEBA2>叙慕：</color>嗯？", talk_content = "嗯？", talk_name = "叙慕",},
	[24003] = { talk_id = 24003, group_id = 24, type = 5, aside = "<color=#FFEBA2>？？？：</color>果然我没看错呢。", talk_content = "果然我没看错呢。", talk_name = "？？？",},
	[25001] = { talk_id = 25001, group_id = 25, type = 1, next_id = 25002, fade_in = 1, fade_out = true, not_skip = true, talk_content = "太厉害了，没想到竟然会有能打倒龙的脉者路过此地。", talk_name = "？？？",},
	[25002] = { talk_id = 25002, group_id = 25, type = 1, next_id = 25003, talk_content = "我的名字是多闻，现在城里的负责人。", talk_name = "多闻",},
	[25003] = { talk_id = 25003, group_id = 25, type = 1, next_id = 25004, talk_content = "我们只是刚好受了于静拜托而已。", talk_name = "刻刻",},
	[25004] = { talk_id = 25004, group_id = 25, type = 1, next_id = 25005, talk_content = "也就是说她没事对吧，太好了。", talk_name = "多闻",},
	[25005] = { talk_id = 25005, group_id = 25, type = 1, next_id = 25006, talk_content = "嗯，她一个人跑到外面去找救兵呢。", talk_name = "刻刻",},
	[25006] = { talk_id = 25006, group_id = 25, type = 1, next_id = 25007, talk_content = "对了，关于刚刚骑在龙上的女人，你们有知道些什么吗？", talk_name = "叙慕",},
	[25007] = { talk_id = 25007, group_id = 25, type = 1, next_id = 25008, talk_content = "呃，知道些什么是指？", talk_name = "多闻",},
	[25008] = { talk_id = 25008, group_id = 25, type = 1, next_id = 25009, talk_content = "她在这里的目的，她藏身于哪里，\n我是因为追捕她才一路来到这个地方的。", talk_name = "叙慕",},
	[25009] = { talk_id = 25009, group_id = 25, type = 1, next_id = 25010, talk_content = "这个我不太清楚，但城里有好些居民\n在之前都说看见过龙飞在天上的样子。", talk_name = "多闻",},
	[25010] = { talk_id = 25010, group_id = 25, type = 1, next_id = 25011, talk_content = "也就是说可以从这些地方入手呢。", talk_name = "刻刻",},
	[25011] = { talk_id = 25011, group_id = 25, type = 1, next_id = 25012, talk_content = "……言下之意是你要继续和我一起行动？", talk_name = "叙慕",},
	[25012] = { talk_id = 25012, group_id = 25, type = 1, next_id = 25013, talk_content = "相遇是缘，再说也不能放着那样的魔头不管。", talk_name = "刻刻",},
	[25013] = { talk_id = 25013, group_id = 25, type = 1, next_id = 25014, talk_content = "说见过龙的居民就在广场附近，我还得确认居民的状况，就……", talk_name = "多闻",},
	[25014] = { talk_id = 25014, group_id = 25, type = 1, talk_content = "嗯，你先处理城里的事情，我会四处打听的。", talk_name = "叙慕",},
	[26001] = { talk_id = 26001, group_id = 26, type = 1, next_id = 26002, talk_content = "噬脉生物和龙突然往小城附近聚集，对我来说那景象和世界末日差不多。", talk_name = "多闻",},
	[26002] = { talk_id = 26002, group_id = 26, type = 1, talk_content = "请让我再次为刚才的事情感谢你们，两位脉者。", talk_name = "多闻",},
	[27001] = { talk_id = 27001, group_id = 27, type = 4, next_id = 27002, aside = "<color=#FFEBA2>刻刻：</color>广场指的是那棵星辰木附近的区域吧。", talk_content = "广场指的是那棵星辰木附近的区域吧。", talk_name = "刻刻",},
	[27002] = { talk_id = 27002, group_id = 27, type = 4, next_id = 27003, aside = "<color=#FFEBA2>叙慕：</color>原来那样的叫星辰木。", talk_content = "原来那样的叫星辰木。", talk_name = "叙慕",},
	[27003] = { talk_id = 27003, group_id = 27, type = 4, next_id = 27004, aside = "<color=#FFEBA2>刻刻：</color>你不是没见过吧？", talk_content = "你不是没见过吧？", talk_name = "刻刻",},
	[27004] = { talk_id = 27004, group_id = 27, type = 4, next_id = 27005, aside = "<color=#FFEBA2>叙慕：</color>嗯，我故乡的气脉根本没法凝聚成那种状态。", talk_content = "嗯，我故乡的气脉根本没法凝聚成那种状态。", talk_name = "叙慕",},
	[27005] = { talk_id = 27005, group_id = 27, type = 4, aside = "<color=#FFEBA2>刻刻：</color>那到底是什么乡下地方。", talk_content = "那到底是什么乡下地方。", talk_name = "刻刻",},
	[28001] = { talk_id = 28001, group_id = 28, type = 1, next_id = 28002, fade_in = 1, fade_out = true, not_skip = true, talk_content = "你好，关于刚刚骑在龙上的那个女人，你知道些什么吗？", talk_name = "叙慕",},
	[28002] = { talk_id = 28002, group_id = 28, type = 1, next_id = 28003, talk_content = "那位女性的事情我不太清楚，但那条骑龙我上周曾见过它一次。", talk_name = "莉莉",},
	[28003] = { talk_id = 28003, group_id = 28, type = 1, next_id = 28004, talk_content = "位置是？", talk_name = "叙慕",},
	[28004] = { talk_id = 28004, group_id = 28, type = 1, next_id = 28005, talk_content = "在前往港口城市何罗的路上，它就在天空中盘旋\n……我还以为要被吃掉了。", talk_name = "莉莉",},
	[28005] = { talk_id = 28005, group_id = 28, type = 1, next_id = 28006, talk_content = "盘旋，那是在找什么的动作吧？", talk_name = "刻刻",},
	[28006] = { talk_id = 28006, group_id = 28, type = 1, talk_content = "可到底是要找什么……", talk_name = "叙慕",},
	[29001] = { talk_id = 29001, group_id = 29, type = 1, talk_content = "话说你们就是打败了那条龙的人吧，实在是太帅了！", talk_name = "莉莉",},
	[30001] = { talk_id = 30001, group_id = 30, type = 1, next_id = 30002, fade_in = 1, fade_out = true, not_skip = true, talk_content = "你好，关于刚刚骑在龙上的那个女人，你知道些什么吗？", talk_name = "叙慕",},
	[30002] = { talk_id = 30002, group_id = 30, type = 1, next_id = 30003, talk_content = "不清楚，但我听说最近通往生命环的道路似乎因为什么原因被封闭了。", talk_name = "安晴",},
	[30003] = { talk_id = 30003, group_id = 30, type = 1, next_id = 30004, talk_content = "生命环是什么地方？", talk_name = "叙慕",},
	[30004] = { talk_id = 30004, group_id = 30, type = 2, options = {{"叫生命环却放残骸吗？",30006,},{"噬脉太阳的残骸？",30005,},}, talk_content = "是放着噬脉太阳残骸的地方，本身也是创生地最高的建筑哦。", talk_name = "安晴",},
	[30005] = { talk_id = 30005, group_id = 30, type = 3, next_id = 30007, not_skip = true, talk_content = "就是坠日战争中被打败的噬脉太阳的躯壳，虽然我也没见过就是了。", talk_name = "安晴",},
	[30006] = { talk_id = 30006, group_id = 30, type = 3, next_id = 30007, not_skip = true, talk_content = "嗯，因为那似乎是用来维持残骸不让其完全失去活性的设施。", talk_name = "安晴",},
	[30007] = { talk_id = 30007, group_id = 30, type = 1, not_skip = true, talk_content = "看来值得一去呢，谢谢了。", talk_name = "叙慕",},
	[31001] = { talk_id = 31001, group_id = 31, type = 1, next_id = 31002, talk_content = "这些日子我总有不详的预感，然后那条飞龙就突然出现在城外了。", talk_name = "安晴",},
	[31002] = { talk_id = 31002, group_id = 31, type = 2, options = {{"嗯，再见。",31003,},{"能和我详细说说噬脉太阳吗？",31004,},}, talk_content = "接下来不要再发生什么就好了。", talk_name = "安晴",},
	[31003] = { talk_id = 31003, group_id = 31, type = 3, not_skip = true, talk_content = "嗯，再见。", talk_name = "安晴",},
	[31004] = { talk_id = 31004, group_id = 31, type = 3, next_id = 31005, not_skip = true, talk_content = "详细说……我想想，你应该知道数百年前发生的坠日战争吧？", talk_name = "安晴",},
	[31005] = { talk_id = 31005, group_id = 31, type = 3, next_id = 31006, talk_content = "九个噬脉太阳突然升起，噬脉生物以太阳为旗聚集在一起攻击生灵。", talk_name = "安晴",},
	[31006] = { talk_id = 31006, group_id = 31, type = 3, next_id = 31007, talk_content = "而人类这边也团结起来一同抗击敌人，最终在付出巨大代价后获胜。", talk_name = "安晴",},
	[31007] = { talk_id = 31007, group_id = 31, type = 3, next_id = 31008, talk_content = "这就是那场战争的简述，而这些噬脉太阳被打败后都留下了大小不一的‘残骸’。", talk_name = "安晴",},
	[31008] = { talk_id = 31008, group_id = 31, type = 3, next_id = 31009, talk_content = "然后就被人保存起来了对吧。", talk_name = "叙慕",},
	[31009] = { talk_id = 31009, group_id = 31, type = 3, next_id = 31010, talk_content = "嗯，不过即便是残骸听说也不断散发着诡异的能量。", talk_name = "安晴",},
	[31010] = { talk_id = 31010, group_id = 31, type = 3, talk_content = "唉，要是我的话肯定会当场把那种可怕的东西毁掉。", talk_name = "安晴",},
	[32001] = { talk_id = 32001, group_id = 32, type = 1, next_id = 32002, fade_in = 1, fade_out = true, not_skip = true, talk_content = "你好，关于刚刚骑在龙上的那个女人，你知道些什么吗？", talk_name = "叙慕",},
	[32002] = { talk_id = 32002, group_id = 32, type = 1, next_id = 32003, talk_content = "对不起，我不知道。", talk_name = "于用",},
	[32003] = { talk_id = 32003, group_id = 32, type = 1, next_id = 32004, talk_content = "但我可是注意到了，她手上有和噬脉生物一样的东西……", talk_name = "于用",},
	[32004] = { talk_id = 32004, group_id = 32, type = 1, next_id = 32005, talk_content = "……", talk_name = "叙慕",},
	[32005] = { talk_id = 32005, group_id = 32, type = 1, next_id = 32006, talk_content = "（在游历回到故乡以后……离歌的手就变成那样了。）", talk_name = "叙慕",},
	[32006] = { talk_id = 32006, group_id = 32, type = 1, talk_content = "（恐怕不止是手，就连身体的其他地方也……）", talk_name = "叙慕",},
	[33001] = { talk_id = 33001, group_id = 33, type = 1, talk_content = "话说小静怎么还不回来呢。", talk_name = "于用",},
	[34001] = { talk_id = 34001, group_id = 34, type = 4, next_id = 34002, aside = "<color=#FFEBA2>刻刻：</color>该问的都问过了，接下来要不要到附近整理一下情报？", talk_content = "该问的都问过了，接下来要不要到附近整理一下情报？", talk_name = "刻刻",},
	[34002] = { talk_id = 34002, group_id = 34, type = 4, aside = "<color=#FFEBA2>刻刻：</color>我刚听说那边有辉光蜻蜓的货车，\n还可以顺便修理一下装备哦。", talk_content = "我刚听说那边有辉光蜻蜓的货车，\n还可以顺便修理一下装备哦。", talk_name = "刻刻",},
	[35001] = { talk_id = 35001, group_id = 35, type = 1, next_id = 35002, fade_in = 1, fade_out = true, not_skip = true, talk_content = "你好，我听说这里能修理装备对吗，我手上的剑……", talk_name = "叙慕",},
	[35002] = { talk_id = 35002, group_id = 35, type = 2, options = {{"失踪？",35003,},{"你先冷静下来。",35004,},}, talk_content = "不，不好了，我的店员失踪了！", talk_name = "温度纱",},
	[35003] = { talk_id = 35003, group_id = 35, type = 3, next_id = 35005, not_skip = true, talk_content = "对，阿飞她不知道到哪去了！", talk_name = "温度纱",},
	[35004] = { talk_id = 35004, group_id = 35, type = 3, next_id = 35005, not_skip = true, talk_content = "对，对不起……", talk_name = "温度纱",},
	[35005] = { talk_id = 35005, group_id = 35, type = 1, next_id = 35006, not_skip = true, talk_content = "感觉走到哪都能碰上麻烦事呢，总之能和我们说说你员工吗？", talk_name = "刻刻",},
	[35006] = { talk_id = 35006, group_id = 35, type = 1, next_id = 35007, talk_content = "嗯，失踪的员工叫阿飞，是我最近招聘的帮工。", talk_name = "温度纱",},
	[35007] = { talk_id = 35007, group_id = 35, type = 1, next_id = 35008, talk_content = "阿飞……你是什么时候发现她失踪的呢？", talk_name = "刻刻",},
	[35008] = { talk_id = 35008, group_id = 35, type = 1, next_id = 35009, talk_content = "就刚才，在龙之前有一批噬脉生物冲进了城里，于是我们分头避难……", talk_name = "温度纱",},
	[35009] = { talk_id = 35009, group_id = 35, type = 1, next_id = 35010, talk_content = "对，她可能是被噬脉生物捉住了，当时我躲在房里听见了某人的惨叫！", talk_name = "温度纱",},
	[35010] = { talk_id = 35010, group_id = 35, type = 1, next_id = 35011, talk_content = "要真是那样就麻烦了，你知道它们往哪个方向去了吗？", talk_name = "叙慕",},
	[35011] = { talk_id = 35011, group_id = 35, type = 1, next_id = 35012, talk_content = "那时候我往窗外轻轻瞟了眼……大概是从城的后门，往星碑走道去了。", talk_name = "温度纱",},
	[35012] = { talk_id = 35012, group_id = 35, type = 1, next_id = 35013, talk_content = "那不是我们来的方向吗？", talk_name = "刻刻",},
	[35013] = { talk_id = 35013, group_id = 35, type = 1, next_id = 35014, talk_content = "擦肩而过……还是说噬脉生物有什么奇怪的移动方法呢。", talk_name = "叙慕",},
	[35014] = { talk_id = 35014, group_id = 35, type = 1, next_id = 35015, talk_content = "想太多也没用，救人要紧，我们先回头看看吧。", talk_name = "刻刻",},
	[35015] = { talk_id = 35015, group_id = 35, type = 1, talk_content = "哎，你们是要帮我吗，太好了，阿飞有救了！", talk_name = "温度纱",},
	[36001] = { talk_id = 36001, group_id = 36, type = 1, next_id = 36002, talk_content = "捉住阿飞的噬脉生物往星碑走道的方向去了。", talk_name = "温度纱",},
	[36002] = { talk_id = 36002, group_id = 36, type = 1, talk_content = "求求你们，救救阿飞吧！", talk_name = "温度纱",},
	[37001] = { talk_id = 37001, group_id = 37, type = 4, next_id = 37002, aside = "<color=#FFEBA2>？？？：</color>逃不掉了吗？", talk_content = "逃不掉了吗？", talk_name = "？？？",},
	[37002] = { talk_id = 37002, group_id = 37, type = 4, next_id = 37003, aside = "<color=#FFEBA2>刻刻：</color>叙慕，前面有人被噬脉生物围住了。", talk_content = "叙慕，前面有人被噬脉生物围住了。", talk_name = "刻刻",},
	[37003] = { talk_id = 37003, group_id = 37, type = 4, aside = "<color=#FFEBA2>叙慕：</color>解决他们！", talk_content = "解决他们！", talk_name = "叙慕",},
	[38001] = { talk_id = 38001, group_id = 38, type = 1, next_id = 38002, fade_in = 1, fade_out = true, not_skip = true, talk_content = "感谢你们出手相助……呃，你们是？", talk_name = "？？？",},
	[38002] = { talk_id = 38002, group_id = 38, type = 1, next_id = 38003, talk_content = "受托来救人的脉者，看你这身打扮，你是阿飞没错吧。", talk_name = "刻刻",},
	[38003] = { talk_id = 38003, group_id = 38, type = 1, next_id = 38004, talk_content = "对，对，我就是阿飞，难道是店长拜托你们的吗？", talk_name = "阿飞",},
	[38004] = { talk_id = 38004, group_id = 38, type = 1, next_id = 38005, talk_content = "嗯，你没事就好，我们先回去吧。", talk_name = "叙慕",},
	[38005] = { talk_id = 38005, group_id = 38, type = 1, next_id = 38006, talk_content = "等等，还有其他人被捉住了！", talk_name = "阿飞",},
	[38006] = { talk_id = 38006, group_id = 38, type = 1, next_id = 38007, talk_content = "还有？", talk_name = "刻刻",},
	[38007] = { talk_id = 38007, group_id = 38, type = 1, next_id = 38008, talk_content = "嗯，除了我还有一个被捉住的人，我本以为能带她一起逃出来，但……", talk_name = "阿飞",},
	[38008] = { talk_id = 38008, group_id = 38, type = 1, next_id = 38009, talk_content = "那你是从哪逃出来的？", talk_name = "叙慕",},
	[38009] = { talk_id = 38009, group_id = 38, type = 1, next_id = 38010, talk_content = "过了桥以后就能看见一个怪物据点，怪物都盘踞在那里。", talk_name = "阿飞",},
	[38010] = { talk_id = 38010, group_id = 38, type = 1, next_id = 38011, talk_content = "过桥，是刚刚看漏了什么吗？", talk_name = "叙慕",},
	[38011] = { talk_id = 38011, group_id = 38, type = 1, next_id = 38012, talk_content = "或许是之后才创造出来的说不定，\n噬脉生物操控点什么这事再正常不过了。", talk_name = "刻刻",},
	[38012] = { talk_id = 38012, group_id = 38, type = 1, talk_content = "接下来的路我一个人走回去就行，剩下的那位就拜托你们了。", talk_name = "阿飞",},
	[39001] = { talk_id = 39001, group_id = 39, type = 1, next_id = 39002, talk_content = "好久没跑步，现在有些呼吸不过来。", talk_name = "阿飞",},
	[39002] = { talk_id = 39002, group_id = 39, type = 1, talk_content = "我再休息一下就会回去，你们就不用担心我了。", talk_name = "阿飞",},
	[40001] = { talk_id = 40001, group_id = 40, type = 4, next_id = 40002, aside = "<color=#FFEBA2>刻刻：</color>那个就是噬脉生物的据点，人好像也在里面。", talk_content = "那个就是噬脉生物的据点，人好像也在里面。", talk_name = "刻刻",},
	[40002] = { talk_id = 40002, group_id = 40, type = 4, aside = "<color=#FFEBA2>叙慕：</color>那身影总觉得在哪见过。", talk_content = "那身影总觉得在哪见过。", talk_name = "叙慕",},
	[41001] = { talk_id = 41001, group_id = 41, type = 2, fade_in = 1, fade_out = true, not_skip = true, options = {{"怎么又是你？",41003,},{"你是谁？",41002,},}, talk_content = "我就知道你会来救我的，叙慕！", talk_name = "于静",},
	[41002] = { talk_id = 41002, group_id = 41, type = 3, next_id = 41004, not_skip = true, talk_content = "我是于静，之前被你救下来的被噬脉生物袭击的于静！", talk_name = "于静",},
	[41003] = { talk_id = 41003, group_id = 41, type = 3, next_id = 41004, not_skip = true, talk_content = "总感觉你的话语中带了点轻蔑……", talk_name = "于静",},
	[41004] = { talk_id = 41004, group_id = 41, type = 1, next_id = 41005, not_skip = true, talk_content = "总之谢谢你们，我还以为要被怪物们吃掉了。", talk_name = "于静",},
	[41005] = { talk_id = 41005, group_id = 41, type = 1, next_id = 41006, talk_content = "所以你为什么会被捉住了，之前追赶你的怪物都被我解决了不是吗？", talk_name = "叙慕",},
	[41006] = { talk_id = 41006, group_id = 41, type = 1, next_id = 41007, talk_content = "可在那之后我就被另一群噬脉生物盯上……早知道就和你一起走了。", talk_name = "于静",},
	[41007] = { talk_id = 41007, group_id = 41, type = 1, next_id = 41008, talk_content = "哎，还真是多灾多难，不过没事就好，我们回去吧。", talk_name = "叙慕",},
	[41008] = { talk_id = 41008, group_id = 41, type = 1, talk_content = "嗯，这次我会好好跟着你们的。", talk_name = "于静",},
	[42001] = { talk_id = 42001, group_id = 42, type = 4, next_id = 42002, aside = "<color=#FFEBA2>于静：</color>终于到家了，我去和姐姐报个平安！", talk_content = "终于到家了，我去和姐姐报个平安！", talk_name = "于静",},
	[42002] = { talk_id = 42002, group_id = 42, type = 4, aside = "<color=#FFEBA2>刻刻：</color>这人行动力还真高。", talk_content = "这人行动力还真高。", talk_name = "刻刻",},
	[43001] = { talk_id = 43001, group_id = 43, type = 1, next_id = 43002, talk_content = "谢谢你们把小静带回来，那孩子什么都好，但就是太活泼了。", talk_name = "于用",},
	[43002] = { talk_id = 43002, group_id = 43, type = 1, talk_content = "方才城里被袭击的时候也是一溜烟地不知道跑哪了。", talk_name = "于用",},
	[44001] = { talk_id = 44001, group_id = 44, type = 1, next_id = 44002, talk_content = "对了，这是帮了我的报酬，谢谢你们啦！", talk_name = "于静",},
	[44002] = { talk_id = 44002, group_id = 44, type = 1, next_id = 44003, talk_content = "呃，这是什么？", talk_name = "叙慕",},
	[44003] = { talk_id = 44003, group_id = 44, type = 1, next_id = 44004, talk_content = "是附近难得一遇的果子哦。", talk_name = "于静",},
	[44004] = { talk_id = 44004, group_id = 44, type = 1, talk_content = "……", talk_name = "叙慕",},
	[45001] = { talk_id = 45001, group_id = 45, type = 1, talk_content = "从明天开始得把锻炼加到每日计划里面……", talk_name = "阿飞",},
	[46001] = { talk_id = 46001, group_id = 46, type = 1, next_id = 46002, talk_content = "呜，是活生生的阿飞，谢谢你们，呜呜呜。", talk_name = "温度纱",},
	[46002] = { talk_id = 46002, group_id = 46, type = 1, talk_content = "（这人真的适合当店长吗？）", talk_name = "叙慕",},
	[47001] = { talk_id = 47001, group_id = 47, type = 1, next_id = 47002, talk_content = "熙来就在前面，就拜托你了，呃……叙慕！", talk_name = "于静",},
	[47002] = { talk_id = 47002, group_id = 47, type = 1, talk_content = "你刚不是忘记我叫什么了吧？", talk_name = "叙慕",},
	[48001] = { talk_id = 48001, group_id = 48, type = 1, talk_content = "下次再碰见麻烦的话也拜托你们了哦！", talk_name = "于静",},
	[49001] = { talk_id = 49001, group_id = 49, type = 5, next_id = 49002, aside = "<color=#FFEBA2>离歌：</color>你要清楚，青乌。", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你要清楚，青乌。", talk_name = "离歌",},
	[49002] = { talk_id = 49002, group_id = 49, type = 5, aside = "<color=#FFEBA2>离歌：</color>黎明快要来了。", talk_content = "黎明快要来了。", talk_name = "离歌",},
	[50001] = { talk_id = 50001, group_id = 50, type = 4, next_id = 50002, aside = "<color=#FFEBA2>叙慕：</color>前面的路……", talk_content = "前面的路……", talk_name = "叙慕",},
	[50002] = { talk_id = 50002, group_id = 50, type = 4, aside = "<color=#FFEBA2>刻刻：</color>附近有几个类似机关的东西，要不让我来射击试试看？", talk_content = "附近有几个类似机关的东西，要不让我来射击试试看？", talk_name = "刻刻",},
	[51001] = { talk_id = 51001, group_id = 51, type = 1, hide_all_entity = true, hide_player = true, talk_content = "熙来就在前面，就拜托你了，叙慕！", talk_name = "于静",},
	[52001] = { talk_id = 52001, group_id = 52, type = 5, next_id = 52002, aside = "在星辰世界中存在着名为气脉的生命之源以及蚕食气脉的噬脉生物。", back_text = "simingwulunfashengheshiwodoubuhuigaibianzijideyuwang\nshijiebidinghuibeihuimieshengmingbixuzuochugaibian\nzaiyiqiezhongjiedeshihou\nwoliangzaiyiqixiangzhexingchenzhiwangdeyuzuoqianxingba\n\n			beimoqudemingzi", fade_in = -1, not_skip = true, talk_content = "在星辰世界中存在着名为气脉的生命之源以及蚕食气脉的噬脉生物。",},
	[52002] = { talk_id = 52002, group_id = 52, type = 5, next_id = 52003, aside = "遭到蚕食的气脉会被破坏，最终导致环境恶化，生灵涂炭。", back_text = "simingwulunfashengheshiwodoubuhuigaibianzijideyuwang\nshijiebidinghuibeihuimieshengmingbixuzuochugaibian\nzaiyiqiezhongjiedeshihou\nwoliangzaiyiqixiangzhexingchenzhiwangdeyuzuoqianxingba\n\n			beimoqudemingzi", talk_content = "遭到蚕食的气脉会被破坏，最终导致环境恶化，生灵涂炭。",},
	[52003] = { talk_id = 52003, group_id = 52, type = 5, next_id = 52004, aside = "能够遏制这些侵蚀，治愈气脉之伤的人则被世人称为青乌。", back_text = "simingwulunfashengheshiwodoubuhuigaibianzijideyuwang\nshijiebidinghuibeihuimieshengmingbixuzuochugaibian\nzaiyiqiezhongjiedeshihou\nwoliangzaiyiqixiangzhexingchenzhiwangdeyuzuoqianxingba\n\n			beimoqudemingzi", talk_content = "能够遏制这些侵蚀，治愈气脉之伤的人则被世人称为青乌。",},
	[52004] = { talk_id = 52004, group_id = 52, type = 5, aside = "如今，最后的青乌叙慕为了追回故乡的神器‘漆金’踏上了追逐恶徒的旅途。", back_text = "simingwulunfashengheshiwodoubuhuigaibianzijideyuwang\nshijiebidinghuibeihuimieshengmingbixuzuochugaibian\nzaiyiqiezhongjiedeshihou\nwoliangzaiyiqixiangzhexingchenzhiwangdeyuzuoqianxingba\n\n			beimoqudemingzi", talk_content = "如今，最后的青乌叙慕为了追回故乡的神器‘漆金’踏上了追逐恶徒的旅途。",},
	[53001] = { talk_id = 53001, group_id = 53, type = 5, next_id = 53002, fade_in = 1, fade_out = true,},
	[53002] = { talk_id = 53002, group_id = 53, type = 5, next_id = 53003,},
	[53003] = { talk_id = 53003, group_id = 53, type = 5, next_id = 53004,},
	[53004] = { talk_id = 53004, group_id = 53, type = 5, back_groud = "Textures/Icon/Single/StoryImg/xy.png",},
	[54001] = { talk_id = 54001, group_id = 54, type = 4, next_id = 54002, aside = "<color=#FFEBA2>刻刻：</color>事情都办完了的话要不要到创生地其他地方看看？", talk_content = "事情都办完了的话要不要到创生地其他地方看看？", talk_name = "刻刻",},
	[54002] = { talk_id = 54002, group_id = 54, type = 4, next_id = 54003, aside = "<color=#FFEBA2>叙慕：</color>可是城的出入口似乎被什么堵上了。", talk_content = "可是城的出入口似乎被什么堵上了。", talk_name = "叙慕",},
	[54003] = { talk_id = 54003, group_id = 54, type = 4, aside = "<color=#FFEBA2>刻刻：</color>这种事情向‘市长’问问不就好了。", talk_content = "这种事情向‘市长’问问不就好了。", talk_name = "刻刻",},
	[55001] = { talk_id = 55001, group_id = 55, type = 1, next_id = 55002, not_skip = true, talk_content = "我听说你们把失散的人都找回来了，谢谢你们。", talk_name = "多闻",},
	[55002] = { talk_id = 55002, group_id = 55, type = 1, next_id = 55003, talk_content = "只是举手之劳而已，倒是我们想要出城，挡在出入口的东西是什么呢？", talk_name = "刻刻",},
	[55003] = { talk_id = 55003, group_id = 55, type = 1, next_id = 55004, talk_content = "是利用气脉形成的无形之墙，在怪物靠近的时候就会启动。", talk_name = "多闻",},
	[55004] = { talk_id = 55004, group_id = 55, type = 1, next_id = 55005, talk_content = "本是没有危险后就会自动关闭的东西，但似乎在之前袭击的时候被弄坏了。", talk_name = "多闻",},
	[55005] = { talk_id = 55005, group_id = 55, type = 1, next_id = 55006, talk_content = "该不会现在谁也没办法出城吧？", talk_name = "叙慕",},
	[55006] = { talk_id = 55006, group_id = 55, type = 1, next_id = 55007, talk_content = "也不是这样，现在要出城的话可以在城里面找找门的钥匙。", talk_name = "多闻",},
	[55007] = { talk_id = 55007, group_id = 55, type = 1, next_id = 55008, talk_content = "找？", talk_name = "刻刻",},
	[55008] = { talk_id = 55008, group_id = 55, type = 1, next_id = 55009, talk_content = "嗯，维护这道门的人将钥匙分成三份放在了各个建筑的高处。", talk_name = "多闻",},
	[55009] = { talk_id = 55009, group_id = 55, type = 2, next_id = 55010, options = {{"为什么要放在高处？",55010,},{"让维护门的人开门不就好了？",55011,},}, talk_content = "仔细找找应该是能找到的。", talk_name = "多闻",},
	[55010] = { talk_id = 55010, group_id = 55, type = 3, next_id = 55012, not_skip = true, talk_content = "是维护人的癖好，因为是主城派来的我也不好说些什么……", talk_name = "多闻",},
	[55011] = { talk_id = 55011, group_id = 55, type = 3, next_id = 55012, not_skip = true, talk_content = "那位在前几天到另一个城去研究学习了，恐怕还要好几天才能回来。", talk_name = "多闻",},
	[55012] = { talk_id = 55012, group_id = 55, type = 1, not_skip = true, talk_content = "唉，总之我们就在房顶上找找看吧，总比什么都不干要强。", talk_name = "刻刻",},
	[56001] = { talk_id = 56001, group_id = 56, type = 4, next_id = 56002, aside = "<color=#FFEBA2>刻刻：</color>没想到还有在房顶上寻宝的一天呢。", talk_content = "没想到还有在房顶上寻宝的一天呢。", talk_name = "刻刻",},
	[56002] = { talk_id = 56002, group_id = 56, type = 4, next_id = 56003, aside = "<color=#FFEBA2>叙慕：</color>到底是什么人才会把钥匙丢那种地方……", talk_content = "到底是什么人才会把钥匙丢那种地方……", talk_name = "叙慕",},
	[56003] = { talk_id = 56003, group_id = 56, type = 4, aside = "<color=#FFEBA2>刻刻：</color>像离歌那样骑龙的人？", talk_content = "像离歌那样骑龙的人？", talk_name = "刻刻",},
	[57001] = { talk_id = 57001, group_id = 57, type = 4, next_id = 57002, aside = "<color=#FFEBA2>叙慕：</color>一二，三，都找齐了。", fade_out = true, talk_content = "一二，三，都找齐了。", talk_name = "叙慕",},
	[57002] = { talk_id = 57002, group_id = 57, type = 4, aside = "<color=#FFEBA2>刻刻：</color>那现在就到门那边去吧。", talk_content = "那现在就到门那边去吧。", talk_name = "刻刻",},
	[58001] = { talk_id = 58001, group_id = 58, type = 1, next_id = 58002, fade_in = 1, fade_out = true, not_skip = true, talk_content = "这是……？", talk_name = "叙慕",},
	[58002] = { talk_id = 58002, group_id = 58, type = 1, next_id = 58003, talk_content = "星辰木，气脉的一种特殊存在形式，\n那里面的庞大能量可是噬脉生物最喜欢的东西呢。", talk_name = "刻刻",},
	[58003] = { talk_id = 58003, group_id = 58, type = 1, next_id = 58004, talk_content = "你以前没见过吗？", talk_name = "刻刻",},
	[58004] = { talk_id = 58004, group_id = 58, type = 1, next_id = 58005, talk_content = "我只是第一次看见围绕星辰木建造的城市而已。", talk_name = "叙慕",},
	[58005] = { talk_id = 58005, group_id = 58, type = 1, next_id = 58006, talk_content = "嗯，毕竟这种大小的星辰木可不常见，\n这样看来这一带可以说是‘能源自由’了呢。", talk_name = "刻刻",},
	[58006] = { talk_id = 58006, group_id = 58, type = 1, next_id = 58007, talk_content = "所以这地方才会大白天也开灯吗？", talk_name = "叙慕",},
	[58007] = { talk_id = 58007, group_id = 58, type = 1, next_id = 58008, talk_content = "这么一说……", talk_name = "刻刻",},
	[58008] = { talk_id = 58008, group_id = 58, type = 1, next_id = 58009, talk_content = "这里是。", talk_name = "刻刻",},
	[58009] = { talk_id = 58009, group_id = 58, type = 1, next_id = 58010, talk_content = "那里是。", talk_name = "刻刻",},
	[58010] = { talk_id = 58010, group_id = 58, type = 1, next_id = 58011, talk_content = "这里也是……", talk_name = "刻刻",},
	[58011] = { talk_id = 58011, group_id = 58, type = 1, talk_content = "感觉城里面的人是挺懂浪费的。", talk_name = "刻刻",},
	[59001] = { talk_id = 59001, group_id = 59, type = 4, next_id = 59002, aside = "<color=#FFEBA2>刻刻：</color>龙和女人，哪个是离歌？", talk_content = "龙和女人，哪个是离歌？", talk_name = "刻刻",},
	[59002] = { talk_id = 59002, group_id = 59, type = 4, aside = "<color=#FFEBA2>叙慕：</color>你是想我怎么回复你！", talk_content = "你是想我怎么回复你！", talk_name = "叙慕",},
	[60001] = { talk_id = 60001, group_id = 60, type = 1, talk_content = "（不知名的某物挡住了去路。）", talk_name = "叙慕",},
	[61001] = { talk_id = 61001, group_id = 61, type = 1, talk_content = "能打开门的钥匙被放到了城里的各个房顶，你们可以多找找看。", talk_name = "多闻",},
	[62001] = { talk_id = 62001, group_id = 62, type = 1, next_id = 62002, talk_content = "熙来这个城市实在是太偏僻了……", talk_name = "市民",},
	[62002] = { talk_id = 62002, group_id = 62, type = 1, talk_content = "像是刚刚的袭击，即使联系了主城也需要等好一阵子才能有人赶来。", talk_name = "市民",},
	[63001] = { talk_id = 63001, group_id = 63, type = 1, talk_content = "某些地理学家说创生地这里曾有过星辰之穴。", talk_name = "市民",},
	[64001] = { talk_id = 64001, group_id = 64, type = 1, next_id = 64002, talk_content = "星辰之穴是气脉能量的巨大喷出口，\n能量的含量比城里的星辰木可多多了。", talk_name = "市民",},
	[64002] = { talk_id = 64002, group_id = 64, type = 1, next_id = 64003, talk_content = "但相对的那周围基本都会有强大的噬脉生物，一般人可靠近不了。", talk_name = "市民",},
	[64003] = { talk_id = 64003, group_id = 64, type = 1, talk_content = "话说……我们点的饮料还没好吗？", talk_name = "市民",},
	[65001] = { talk_id = 65001, group_id = 65, type = 1, talk_content = "何罗那边最近发布了海上交易的禁令……真令人不安。", talk_name = "市民",},
	[66001] = { talk_id = 66001, group_id = 66, type = 1, talk_content = "噬脉生物活跃的季节货物的运输量就会明显变少，能撑过这个月就好了。", talk_name = "市民",},
	[67001] = { talk_id = 67001, group_id = 67, type = 1, talk_content = "幸好龙没有停在城里……", talk_name = "市民",},
	[68001] = { talk_id = 68001, group_id = 68, type = 2, options = {{"主城指的是？",68002,},{"有报酬的话……",68003,},}, talk_content = "你们是脉者对吧，之后能请你们带我到主城去吗？", talk_name = "市民",},
	[68002] = { talk_id = 68002, group_id = 68, type = 3, talk_content = "创生地的主城名为时珍，\n是以创建者的名字来命名的生态研究据点。", talk_name = "市民",},
	[68003] = { talk_id = 68003, group_id = 68, type = 3, talk_content = "唔……假如能好好护送的话我自然会付钱的，拜托了。", talk_name = "市民",},
	[69001] = { talk_id = 69001, group_id = 69, type = 1, talk_content = "星辰木真的很漂亮呢，真想在下面睡午觉。", talk_name = "市民",},
	[70001] = { talk_id = 70001, group_id = 70, type = 1, talk_content = "这里可是珍品百宝样样俱全的百玲珑分店，\n各位有什么想要的都可以到这里来看看哦！", talk_name = "百玲珑店员",},
	[71001] = { talk_id = 71001, group_id = 71, type = 1, talk_content = "野外不仅仅有噬脉生物，还有各种危险的动物，唉……", talk_name = "市民",},
	[72001] = { talk_id = 72001, group_id = 72, type = 1, talk_content = "要留下青乌传书似乎需要一种奇怪的铃铛，\n可到底要到哪才能拿到那个呢……", talk_name = "市民",},
	[73001] = { talk_id = 73001, group_id = 73, type = 1, talk_content = "总有一天我也想到王土去看看。", talk_name = "市民",},
	[74001] = { talk_id = 74001, group_id = 74, type = 1, next_id = 74002, talk_content = "你那位同行者是闪灵宗的门生吧，\n我以前到红城的时候受过闪灵宗人的帮助。", talk_name = "市民",},
	[74002] = { talk_id = 74002, group_id = 74, type = 1, next_id = 74003, talk_content = "所以闪灵宗是个什么样的组织？", talk_name = "叙慕",},
	[74003] = { talk_id = 74003, group_id = 74, type = 1, talk_content = "就是个研习术法，镇压邪灵的宗派而已。", talk_name = "刻刻",},
	[75001] = { talk_id = 75001, group_id = 75, type = 1, talk_content = "在创生地的下方有着名叫巡礼森林的地区，\n你也是从那边借道过来的对吧？", talk_name = "市民",},
	[76001] = { talk_id = 76001, group_id = 76, type = 1, talk_content = "噬脉生物虽然有着植物形成的身体，\n但他们貌似并不是从植物变来的……", talk_name = "市民",},
	[77001] = { talk_id = 77001, group_id = 77, type = 1, next_id = 77002, fade_in = 1, fade_out = true, not_skip = true, talk_content = "前面的路……", talk_name = "叙慕",},
	[77002] = { talk_id = 77002, group_id = 77, type = 1, next_id = 77003, talk_content = "是进出城里的某种装置吧。", talk_name = "刻刻",},
	[77003] = { talk_id = 77003, group_id = 77, type = 1, next_id = 77004, talk_content = "附近有几个类似机关的东西，要不让我来射击试试看？", talk_name = "刻刻",},
	[77004] = { talk_id = 77004, group_id = 77, type = 1, next_id = 77005, talk_content = "的确光用剑的难以够到……", talk_name = "叙慕",},
	[77005] = { talk_id = 77005, group_id = 77, type = 1, talk_content = "总之要怎么做就交给你，不过要前进的话大概就要对那些机关做什么就是了。", talk_name = "刻刻",},
	[78001] = { talk_id = 78001, group_id = 78, type = 5, aside = "<color=#FFEBA2>1：</color>1", talk_content = "1", talk_name = "1",},
