	[101010101] = { talk_id = 101010101, group_id = 1010101, type = 1, next_id = 101010102, hide_player = true, talk_content = "代号是青乌……喂，能听见吗！", talk_name = "？？？",},
	[101010102] = { talk_id = 101010102, group_id = 1010101, type = 1, next_id = 101010103, talk_content = "还是没有反应……为什么会这样，明明能用的办法都已经用上了。", talk_name = "？？？",},
	[101010103] = { talk_id = 101010103, group_id = 1010101, type = 1, next_id = 101010104, talk_content = "难道要找其他人吗，可是……这可是千辛万苦才找到的……", talk_name = "？？？",},
	[101010104] = { talk_id = 101010104, group_id = 1010101, type = 1, next_id = 101010105, talk_content = "曾经拯救过世界的青乌……", talk_name = "？？？",},
	[101010105] = { talk_id = 101010105, group_id = 1010101, type = 2, options = {{"能听见。",101010106,},{"（……）",101010107,},}, talk_content = "等等，刚刚是动了对吧，喂，喂，你能听见我的话吗？", talk_name = "？？？",},
	[101010106] = { talk_id = 101010106, group_id = 1010101, type = 3, talk_content = "太好了，不枉我花费了这么多功夫，青乌，你能记起你本来的名字吗？", talk_name = "？？？",},
	[101010107] = { talk_id = 101010107, group_id = 1010101, type = 3, talk_content = "应该能听见，但是还不能好好回复……不管了，青乌，你能记起你本来的名字吗？", talk_name = "？？？",},
