	[101030304] = { talk_id = 101030304, group_id = 1010303, type = 5, next_id = 101030305, aside = "<color=#FFEBA2>司命：</color>这个叫作淤脉，是气脉能量遭到扭曲堆积而成的一种实体。", talk_content = "这个叫作淤脉，是气脉能量遭到扭曲堆积而成的一种实体。", talk_name = "司命",},
