	[101030401] = { talk_id = 101030401, group_id = 1010304, type = 1, next_id = 101030402, fade_in = 1, fade_out = true, hide_player = true, talk_content = "总算是解脱了，呼呼。", talk_name = "司命",},
	[101030402] = { talk_id = 101030402, group_id = 1010304, type = 1, next_id = 101030403, talk_content = "只是暂时而已，那条龙应该还在附近寻找我们。", talk_name = "青乌",},
	[101030403] = { talk_id = 101030403, group_id = 1010304, type = 1, next_id = 101030404, talk_content = "司命，你真的不知道我被人追捕的理由吗？", talk_name = "青乌",},
	[101030404] = { talk_id = 101030404, group_id = 1010304, type = 1, next_id = 101030405, talk_content = "……不知道，我在唤醒你之前连有人监视着那地方也不知道。", talk_name = "司命",},
	[101030405] = { talk_id = 101030405, group_id = 1010304, type = 1, next_id = 101030406, talk_content = "我只是出于我自己的考虑才将你从那个地方带出来的。", talk_name = "司命",},
	[101030406] = { talk_id = 101030406, group_id = 1010304, type = 1, next_id = 101030407, talk_content = "虽然只是猜测，但我想“你”应该会知道些什么。", talk_name = "司命",},
	[101030407] = { talk_id = 101030407, group_id = 1010304, type = 1, next_id = 101030408, talk_content = "你是指失去记忆之前的我对吧。", talk_name = "青乌",},
	[101030408] = { talk_id = 101030408, group_id = 1010304, type = 1, next_id = 101030409, talk_content = "嗯，毕竟你沉睡的地方也应该是那个组织在管理着的。", talk_name = "司命",},
	[101030409] = { talk_id = 101030409, group_id = 1010304, type = 2, options = {{"有没有可能我是他们的同伴？",101030410,},{"有没有可能我被他们囚禁着。",101030411,},}, talk_content = "（也就是说……在醒来之前我一直都沉睡在别人的领地之中。）", talk_name = "青乌",},
	[101030410] = { talk_id = 101030410, group_id = 1010304, type = 3, next_id = 101030412, hide_player = true, talk_content = "要真是同伴的话就不会有龙追着我们跑了。", talk_name = "司命",},
	[101030411] = { talk_id = 101030411, group_id = 1010304, type = 3, next_id = 101030412, hide_player = true, talk_content = "这个倒有可能……可是囚禁你到底是为了什么呢。", talk_name = "司命",},
	[101030412] = { talk_id = 101030412, group_id = 1010304, type = 1, next_id = 101030413, hide_player = true, talk_content = "要得出结论好像有些太早了，\n现在先趁那条龙没追过来先走出这地方如何？", talk_name = "司命",},
	[101030413] = { talk_id = 101030413, group_id = 1010304, type = 1, next_id = 101030414, talk_content = "沿着山路走的话应该能到山顶，在高处也能更好观察下一步该往哪里去。", talk_name = "司命",},
	[101030414] = { talk_id = 101030414, group_id = 1010304, type = 1, next_id = 101030415, talk_content = "下一步……话说你叫醒我的时候说过想让我拯救世界？", talk_name = "青乌",},
	[101030415] = { talk_id = 101030415, group_id = 1010304, type = 1, talk_content = "那个也等到山顶再说，在这里也很难说明情况呢。", talk_name = "司命",},
