	[101031301] = { talk_id = 101031301, group_id = 1010313, type = 5, next_id = 101031302, aside = "<color=#FFEBA2>青乌：</color>好多了，接下来就一路往山顶去吧。", talk_content = "好多了，接下来就一路往山顶去吧。", talk_name = "青乌",},
	[101031302] = { talk_id = 101031302, group_id = 1010313, type = 5, aside = "<color=#FFEBA2>青乌：</color>太阳……为了望月，绝对不能让那个升起……", talk_content = "太阳……为了望月，绝对不能让那个升起……", talk_name = "青乌",},
	[101031401] = { talk_id = 101031401, group_id = 1010314, type = 4, next_id = 101031402, aside = "<color=#FFEBA2>传音中的声音：</color>山路间有很多噬脉生物，全部人都给我打醒精神！", talk_content = "山路间有很多噬脉生物，全部人都给我打醒精神！", talk_name = "传音中的声音",},
	[101031402] = { talk_id = 101031402, group_id = 1010314, type = 4, aside = "<color=#FFEBA2>青乌：</color>悬天的其他人都在战斗……", talk_content = "悬天的其他人都在战斗……", talk_name = "青乌",},
	[101031501] = { talk_id = 101031501, group_id = 1010315, type = 1, next_id = 101031502, fade_in = 1, fade_out = true, hide_player = true, talk_content = "竟然连淤脉都有，是气脉被怪物劫持了，还是……", talk_name = "青乌",},
	[101031502] = { talk_id = 101031502, group_id = 1010315, type = 1, next_id = 101031503, talk_content = "脚下的大地在拒绝我们呢。", talk_name = "青乌",},
	[101031503] = { talk_id = 101031503, group_id = 1010315, type = 1, talk_content = "总之先斩开它吧，没时间浪费了。", talk_name = "青乌",},
	[101031601] = { talk_id = 101031601, group_id = 1010316, type = 4, aside = "<color=#FFEBA2>青乌：</color>掉下去的话会很麻烦，得注意点走。", talk_content = "掉下去的话会很麻烦，得注意点走。", talk_name = "青乌",},
	[101040101] = { talk_id = 101040101, group_id = 1010401, type = 4, next_id = 101040102, aside = "<color=#FFEBA2>怪物：</color>！", talk_content = "！", talk_name = "怪物",},
	[101040102] = { talk_id = 101040102, group_id = 1010401, type = 4, next_id = 101040103, aside = "<color=#FFEBA2>青乌：</color>突然就冲上来了……这是什么，野兽吗？", talk_content = "突然就冲上来了……这是什么，野兽吗？", talk_name = "青乌",},
	[101040103] = { talk_id = 101040103, group_id = 1010401, type = 4, next_id = 101040104, aside = "<color=#FFEBA2>司命：</color>哇……这里竟然有噬脉生物，\n青乌，是你上场的时候了！", talk_content = "哇……这里竟然有噬脉生物，\n青乌，是你上场的时候了！", talk_name = "司命",},
	[101040104] = { talk_id = 101040104, group_id = 1010401, type = 4, aside = "<color=#FFEBA2>青乌：</color>等下可得给我好好说明！", talk_content = "等下可得给我好好说明！", talk_name = "青乌",},
	[101040201] = { talk_id = 101040201, group_id = 1010402, type = 1, next_id = 101040202, talk_content = "这些怪物你说他们叫噬脉生物……和气脉有什么关系吗？", talk_name = "青乌",},
	[101040202] = { talk_id = 101040202, group_id = 1010402, type = 1, next_id = 101040203, talk_content = "就是一群专门靠吞噬气脉而生的怪物，你只要简单理解为它们是\n全世界生命的敌人就好了。", talk_name = "司命",},
	[101040203] = { talk_id = 101040203, group_id = 1010402, type = 1, next_id = 101040204, talk_content = "在你沉睡之前也应该和这些怪物战斗过才是。", talk_name = "司命",},
	[101040204] = { talk_id = 101040204, group_id = 1010402, type = 1, next_id = 101040205, talk_content = "生命的敌人……还是没有太多印象。", talk_name = "青乌",},
	[101040205] = { talk_id = 101040205, group_id = 1010402, type = 1, next_id = 101040206, talk_content = "唉，你就和刚诞生的婴儿一样脑袋空空呢。", talk_name = "司命",},
	[101040206] = { talk_id = 101040206, group_id = 1010402, type = 1, next_id = 101040207, talk_content = "……", talk_name = "青乌",},
	[101040207] = { talk_id = 101040207, group_id = 1010402, type = 1, talk_content = "出山以后再想想办法吧，\n毕竟你要是能恢复记忆的话对接下来的旅途也有益呢。", talk_name = "司命",},
	[101040301] = { talk_id = 101040301, group_id = 1010403, type = 4, next_id = 101040302, aside = "<color=#FFEBA2>青乌：</color>穿过前面这条山道应该\n很快就到山顶了。", talk_content = "穿过前面这条山道应该\n很快就到山顶了。", talk_name = "青乌",},
	[101040302] = { talk_id = 101040302, group_id = 1010403, type = 4, next_id = 101040303, aside = "<color=#FFEBA2>司命：</color>唉，为什么好不容易找到的\n救世主会失忆呢。", talk_content = "唉，为什么好不容易找到的\n救世主会失忆呢。", talk_name = "司命",},
	[101040303] = { talk_id = 101040303, group_id = 1010403, type = 4, next_id = 101040304, aside = "<color=#FFEBA2>青乌：</color>我可没说过自己是什么救世主。", talk_content = "我可没说过自己是什么救世主。", talk_name = "青乌",},
	[101040304] = { talk_id = 101040304, group_id = 1010403, type = 4, aside = "<color=#FFEBA2>司命：</color>但总之是失忆了，有没有什么方法能帮你回忆起一切呢。", talk_content = "但总之是失忆了，有没有什么方法能帮你回忆起一切呢。", talk_name = "司命",},
	[101040401] = { talk_id = 101040401, group_id = 1010404, type = 1, next_id = 101040402, fade_in = 1, fade_out = true, hide_player = true, talk_content = "这些怪物你说他们叫噬脉生物……和气脉有什么关系吗？", talk_name = "青乌",},
	[101040402] = { talk_id = 101040402, group_id = 1010404, type = 1, next_id = 101040403, talk_content = "就是一群专门靠吞噬气脉而生的怪物，你只要简单理解为它们是\n全世界生命的敌人就好了。", talk_name = "司命",},
	[101040403] = { talk_id = 101040403, group_id = 1010404, type = 1, next_id = 101040404, talk_content = "有注意到路上那些半透明的竹子吗，那个就是我一直提到的气脉。", talk_name = "司命",},
	[101040404] = { talk_id = 101040404, group_id = 1010404, type = 1, next_id = 101040405, talk_content = "按正常来说，气脉应该是挺拔漂亮的竹子，\n但一旦受到噬脉侵蚀，就会开始枯萎……", talk_name = "司命",},
	[101040405] = { talk_id = 101040405, group_id = 1010404, type = 1, next_id = 101040406, talk_content = "变成那种样子吗？", talk_name = "青乌",},
	[101040406] = { talk_id = 101040406, group_id = 1010404, type = 1, next_id = 101040407, talk_content = "嗯，那个是遭污染，快要坏死的气脉。", talk_name = "司命",},
	[101040407] = { talk_id = 101040407, group_id = 1010404, type = 1, next_id = 101040408, talk_content = "坏死了以后会怎么样？", talk_name = "青乌",},
	[101040408] = { talk_id = 101040408, group_id = 1010404, type = 1, next_id = 101040409, talk_content = "就不再发挥功效呗，气脉实际上是一个巨大的包围着星球的网。", talk_name = "司命",},
	[101040409] = { talk_id = 101040409, group_id = 1010404, type = 1, next_id = 101040410, talk_content = "要是其中有坏死的地方就会出现断点，断点多了，\n那流通的能量就会越来越少。", talk_name = "司命",},
	[101040410] = { talk_id = 101040410, group_id = 1010404, type = 1, next_id = 101040411, talk_content = "流通的能量变少，赖以生存的生命也就只能随之灭亡了。", talk_name = "司命",},
	[101040411] = { talk_id = 101040411, group_id = 1010404, type = 1, next_id = 101040412, talk_content = "破坏其他生物赖以生存的能量，所以才说那些怪物是生命的敌人……", talk_name = "青乌",},
	[101040412] = { talk_id = 101040412, group_id = 1010404, type = 1, next_id = 101040413, talk_content = "不行，即便听了这些也想不起来……没什么印象。", talk_name = "青乌",},
	[101040413] = { talk_id = 101040413, group_id = 1010404, type = 1, next_id = 101040414, talk_content = "唉，你就和刚诞生的婴儿一样脑袋空空呢。", talk_name = "司命",},
	[101040414] = { talk_id = 101040414, group_id = 1010404, type = 1, next_id = 101040415, talk_content = "……", talk_name = "青乌",},
	[101040415] = { talk_id = 101040415, group_id = 1010404, type = 1, talk_content = "出山以后再想想办法吧，\n毕竟你要是能恢复记忆的话对接下来的旅途也有益呢。", talk_name = "司命",},
	[101040501] = { talk_id = 101040501, group_id = 1010405, type = 5, next_id = 101040502, aside = "<color=#FFEBA2>司命：</color>又……又来了，青乌，救驾！", talk_content = "又……又来了，青乌，救驾！", talk_name = "司命",},
	[101040502] = { talk_id = 101040502, group_id = 1010405, type = 5, aside = "<color=#FFEBA2>青乌：</color>你把自己当王爷吗？", talk_content = "你把自己当王爷吗？", talk_name = "青乌",},
	[101040601] = { talk_id = 101040601, group_id = 1010406, type = 4, next_id = 101040602, aside = "<color=#FFEBA2>青乌：</color>司命，现在的人类该不会过着很原始的生活吧？", talk_content = "司命，现在的人类该不会过着很原始的生活吧？", talk_name = "青乌",},
	[101040602] = { talk_id = 101040602, group_id = 1010406, type = 4, next_id = 101040603, aside = "<color=#FFEBA2>司命：</color>为什么这么说？", talk_content = "为什么这么说？", talk_name = "司命",},
	[101040603] = { talk_id = 101040603, group_id = 1010406, type = 4, next_id = 101040604, aside = "<color=#FFEBA2>青乌：</color>因为一路上除了大山就是木桥……\n我该不会穿越到古代了吧？", talk_content = "因为一路上除了大山就是木桥……\n我该不会穿越到古代了吧？", talk_name = "青乌",},
	[101040604] = { talk_id = 101040604, group_id = 1010406, type = 4, aside = "<color=#FFEBA2>司命：</color>放心吧，我没有穿越时空的能力，\n现在的人类也过着很文明的生活。", talk_content = "放心吧，我没有穿越时空的能力，\n现在的人类也过着很文明的生活。", talk_name = "司命",},
	[101040701] = { talk_id = 101040701, group_id = 1010407, type = 4, aside = "<color=#FFEBA2>司命：</color>现在是发挥你真正作用的时候了，青乌！", talk_content = "现在是发挥你真正作用的时候了，青乌！", talk_name = "司命",},
	[101040801] = { talk_id = 101040801, group_id = 1010408, type = 4, next_id = 101040802, aside = "<color=#FFEBA2>怪物：</color>！", talk_content = "！", talk_name = "怪物",},
	[101040802] = { talk_id = 101040802, group_id = 1010408, type = 4, next_id = 101040803, aside = "<color=#FFEBA2>司命：</color>快点，干掉这些噬脉生物！", talk_content = "快点，干掉这些噬脉生物！", talk_name = "司命",},
	[101040803] = { talk_id = 101040803, group_id = 1010408, type = 4, aside = "<color=#FFEBA2>青乌：</color>等下可得给我好好说明！", talk_content = "等下可得给我好好说明！", talk_name = "青乌",},
	[101040901] = { talk_id = 101040901, group_id = 1010409, type = 4, next_id = 101040902, aside = "<color=#FFEBA2>青乌：</color>我还在想怎么一路上都没碰见，原来盘踞在这附近。", talk_content = "我还在想怎么一路上都没碰见，原来盘踞在这附近。", talk_name = "青乌",},
	[101040902] = { talk_id = 101040902, group_id = 1010409, type = 4, aside = "<color=#FFEBA2>青乌：</color>噬脉生物……我一只都不会放过。", talk_content = "噬脉生物……我一只都不会放过。", talk_name = "青乌",},
	[101041001] = { talk_id = 101041001, group_id = 1010410, type = 1, next_id = 101041002, fade_in = 1, fade_out = true, hide_player = true, talk_content = "这些透明的竹是气脉的一种形态……红色的……", talk_name = "青乌",},
	[101041002] = { talk_id = 101041002, group_id = 1010410, type = 1, talk_content = "果然有一部分气脉已经被“噬脉”了。", talk_name = "青乌",},
	[101041101] = { talk_id = 101041101, group_id = 1010411, type = 4, next_id = 101041102, aside = "<color=#FFEBA2>青乌：</color>能看见山顶了，尽快穿过这条山道吧。", call_icon = "Textures/Icon/Single/CampIcon/Chuangshengzhuyi.png", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "能看见山顶了，尽快穿过这条山道吧。", talk_name = "青乌",},
	[101041102] = { talk_id = 101041102, group_id = 1010411, type = 4, next_id = 101041103, aside = "<color=#FFEBA2>传音中的声音：</color>青乌，我们快要到达山顶了，你在哪里？", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "青乌，我们快要到达山顶了，你在哪里？", talk_name = "传音中的声音",},
