	[101041104] = { talk_id = 101041104, group_id = 1010411, type = 4, next_id = 101041105, aside = "<color=#FFEBA2>传音中的声音：</color>状况……不……", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "状况……不……", talk_name = "传音中的声音",},
