	[101041201] = { talk_id = 101041201, group_id = 1010412, type = 4, next_id = 101041202, aside = "<color=#FFEBA2>传音中的声音：</color>悬天可不会倒在这里……青乌，接下来就交给你了。", talk_content = "悬天可不会倒在这里……青乌，接下来就交给你了。", talk_name = "传音中的声音",},
	[101041202] = { talk_id = 101041202, group_id = 1010412, type = 4, aside = "<color=#FFEBA2>青乌：</color>就交给我吧。", talk_content = "就交给我吧。", talk_name = "青乌",},
	[101050101] = { talk_id = 101050101, group_id = 1010501, type = 1, next_id = 101050102, fade_in = 1, fade_out = true, talk_content = "怎么停下来了？", talk_name = "司命",},
	[101050102] = { talk_id = 101050102, group_id = 1010501, type = 1, next_id = 101050103, talk_content = "路上也有不少这样的东西，这些到底是？", talk_name = "青乌",},
	[101050103] = { talk_id = 101050103, group_id = 1010501, type = 1, next_id = 101050104, talk_content = "我忘记说了吗，这个就是刚才提到的“气脉”。", talk_name = "司命",},
	[101050104] = { talk_id = 101050104, group_id = 1010501, type = 1, next_id = 101050105, talk_content = "它们常常以这样的形态出现在土地之上哦。", talk_name = "司命",},
	[101050105] = { talk_id = 101050105, group_id = 1010501, type = 1, next_id = 101050106, talk_content = "气脉之间形态差异挺大呢。", talk_name = "青乌",},
	[101050106] = { talk_id = 101050106, group_id = 1010501, type = 1, next_id = 101050107, talk_content = "噢，那个是遭污染，快要坏死的气脉。", talk_name = "司命",},
	[101050107] = { talk_id = 101050107, group_id = 1010501, type = 1, next_id = 101050108, talk_content = "坏死了以后会怎么样？", talk_name = "青乌",},
	[101050108] = { talk_id = 101050108, group_id = 1010501, type = 1, next_id = 101050109, talk_content = "就不再发挥功效呗，气脉实际上是一个巨大的包围着星球的网。", talk_name = "司命",},
	[101050109] = { talk_id = 101050109, group_id = 1010501, type = 1, next_id = 101050110, talk_content = "要是其中有坏死的地方就会出现断点，断点多了，那流通的能量就会越来越少。", talk_name = "司命",},
	[101050110] = { talk_id = 101050110, group_id = 1010501, type = 1, next_id = 101050111, talk_content = "流通的能量越来越少，那赖以生存的生命也就只能随之灭亡了。", talk_name = "司命",},
	[101050111] = { talk_id = 101050111, group_id = 1010501, type = 1, next_id = 101050112, talk_content = "这不是很危险吗？", talk_name = "青乌",},
	[101050112] = { talk_id = 101050112, group_id = 1010501, type = 2, options = {{"（伸出手）",101050113,},}, talk_content = "当然很危险，但气脉的坏死是不可逆的，人类能做的大概就只有延缓了吧。", talk_name = "司命",},
	[101050113] = { talk_id = 101050113, group_id = 1010501, type = 3, next_id = 101050114, talk_content = "等等，你刚刚做了什么，为什么要坏死的气脉突然就回复过来了？", talk_name = "司命",},
	[101050114] = { talk_id = 101050114, group_id = 1010501, type = 3, next_id = 101050115, talk_content = "不清楚……但是我总觉得自己应该能做些什么。", talk_name = "青乌",},
	[101050115] = { talk_id = 101050115, group_id = 1010501, type = 3, talk_content = "（这就是救世之人……青乌的力量吗？）", talk_name = "司命",},
	[101050201] = { talk_id = 101050201, group_id = 1010502, type = 5, fade_in = 1, hide_player = true,},
