	[101062403] = { talk_id = 101062403, group_id = 1010624, type = 1, next_id = 101062404, talk_content = "那是因为我有让你马上变强的方法。", talk_name = "司命",},
	[101062404] = { talk_id = 101062404, group_id = 1010624, type = 1, next_id = 101062405, talk_content = "该不会是要在这里教我用什么必杀技吧？", talk_name = "青乌",},
	[101062405] = { talk_id = 101062405, group_id = 1010624, type = 1, next_id = 101062406, talk_content = "真有那种东西我自己上不就好了，\n我所说的是我能提升你的基础实力。", talk_name = "司命",},
	[101062406] = { talk_id = 101062406, group_id = 1010624, type = 1, next_id = 101062407, talk_content = "对于其他人来说，提升实力的方式或许就是日夜苦练，日积月累。", talk_name = "司命",},
	[101062407] = { talk_id = 101062407, group_id = 1010624, type = 2, options = {{"（那是什么？）",101062408,},}, talk_content = "但是对于拥有我一半生命的你来说，变强的方式还有一个。", talk_name = "司命",},
	[101062408] = { talk_id = 101062408, group_id = 1010624, type = 3, next_id = 101062409, talk_content = "那就是吸收敌人的“残念”，所谓的念……呃", talk_name = "司命",},
	[101062409] = { talk_id = 101062409, group_id = 1010624, type = 3, next_id = 101062410, talk_content = "总之那个是一瞬间产生的代表了情绪的能量，而物体被消灭时所产生的念，往往是浓度最高的。", talk_name = "司命",},
	[101062410] = { talk_id = 101062410, group_id = 1010624, type = 3, next_id = 101062411, talk_content = "只要吸收了那个的话，那就算不去特意做什么努力你的能力也能得到进步。", talk_name = "司命",},
	[101062411] = { talk_id = 101062411, group_id = 1010624, type = 3, talk_content = "刚好在路上我有收起来几个这样的东西，你就按我说的试试吧。", talk_name = "司命",},
