	[101105002] = { talk_id = 101105002, group_id = 1011050, type = 1, talk_content = "就拜托你了。", talk_name = "张哥",},
	[101105101] = { talk_id = 101105101, group_id = 1011051, type = 1, talk_content = "有脉者愿意帮忙，这事稳了！", talk_name = "工人丙",},
	[101110101] = { talk_id = 101110101, group_id = 1011101, type = 1, next_id = 101110102, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "天月城，太阳，还有反转在天上的“大地”。", talk_name = "青乌",},
	[101110102] = { talk_id = 101110102, group_id = 1011101, type = 1, next_id = 101110103, talk_content = "不，离歌好像说反转的是天月城才对。", talk_name = "青乌",},
	[101110103] = { talk_id = 101110103, group_id = 1011101, type = 1, talk_content = "失去记忆的我到底能干到些什么呢……", talk_name = "青乌",},
	[101110201] = { talk_id = 101110201, group_id = 1011102, type = 4, aside = "<color=#FFEBA2>青乌：</color>噬脉生物应该都解决干净了，直接跳下去吧。", talk_content = "噬脉生物应该都解决干净了，直接跳下去吧。", talk_name = "青乌",},
	[101110301] = { talk_id = 101110301, group_id = 1011103, type = 1, next_id = 101110302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "事情我都解决了。", talk_name = "青乌",},
	[101110302] = { talk_id = 101110302, group_id = 1011103, type = 1, next_id = 101110303, talk_content = "脉者大人，真是谢谢你了！", talk_name = "救下的工人",},
	[101110303] = { talk_id = 101110303, group_id = 1011103, type = 1, next_id = 101110304, talk_content = "不用叫大人……", talk_name = "青乌",},
	[101110304] = { talk_id = 101110304, group_id = 1011103, type = 2, options = {{"噬脉生物平时都这么成群结队吗？",101110306,},{"其他脉者都是什么水平？",101110305,},}, talk_content = "你还真厉害啊，能单独对付一批噬脉生物的脉者可不多见。", talk_name = "工人乙",},
	[101110305] = { talk_id = 101110305, group_id = 1011103, type = 3, next_id = 101110308, talk_content = "大概是一对三四的水平吧，毕竟世道还算和平，也不是每个脉者都和你一样善战呢。", talk_name = "工人乙",},
	[101110306] = { talk_id = 101110306, group_id = 1011103, type = 3, next_id = 101110307, talk_content = "这个不好说，毕竟天月城中的噬脉生物都是因为“乱流”凭空出现的。", talk_name = "工人乙",},
	[101110307] = { talk_id = 101110307, group_id = 1011103, type = 3, next_id = 101110308, talk_content = "每次会出现多少可没人知道。", talk_name = "工人乙",},
	[101110308] = { talk_id = 101110308, group_id = 1011103, type = 1, next_id = 101110309, talk_content = "这下工地也能重新开工了，对了，你刚刚说是要修车是吗？", talk_name = "工人丙",},
	[101110309] = { talk_id = 101110309, group_id = 1011103, type = 1, next_id = 101110310, talk_content = "嗯，能拜托你们吧？", talk_name = "青乌",},
	[101110310] = { talk_id = 101110310, group_id = 1011103, type = 1, next_id = 101110311, talk_content = "你在上面替我们清理工地的时候张哥去修了，那辆蓝色的车就是你的吧？", talk_name = "工人丙",},
	[101110311] = { talk_id = 101110311, group_id = 1011103, type = 1, talk_content = "按时间来算也该修好了，你要不要去看看。", talk_name = "工人丙",},
	[101110401] = { talk_id = 101110401, group_id = 1011104, type = 4, next_id = 101110402, aside = "<color=#FFEBA2>张哥：</color>刚刚工友们发短信和我说你都把噬脉生物搞定了，我果然没看错啊。", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "刚刚工友们发短信和我说你都把噬脉生物搞定了，我果然没看错啊。", talk_name = "张哥",},
	[101110402] = { talk_id = 101110402, group_id = 1011104, type = 4, next_id = 101110403, aside = "<color=#FFEBA2>青乌：</color>只是互相帮助而已，我的车怎么样了？", talk_content = "只是互相帮助而已，我的车怎么样了？", talk_name = "青乌",},
	[101110403] = { talk_id = 101110403, group_id = 1011104, type = 4, next_id = 101110404, aside = "<color=#FFEBA2>张哥：</color>再等等……", talk_content = "再等等……", talk_name = "张哥",},
	[101110404] = { talk_id = 101110404, group_id = 1011104, type = 4, next_id = 101110405, aside = "<color=#FFEBA2>张哥：</color>哼！", talk_content = "哼！", talk_name = "张哥",},
	[101110405] = { talk_id = 101110405, group_id = 1011104, type = 4, next_id = 101110406, aside = "<color=#FFEBA2>张哥：</color>好了，你这车跟垃圾场里捡来似的，一般人可修不了。", talk_content = "好了，你这车跟垃圾场里捡来似的，一般人可修不了。", talk_name = "张哥",},
	[101110406] = { talk_id = 101110406, group_id = 1011104, type = 4, next_id = 101110407, aside = "<color=#FFEBA2>青乌：</color>总之现在应该是能开了吧？", talk_content = "总之现在应该是能开了吧？", talk_name = "青乌",},
	[101110407] = { talk_id = 101110407, group_id = 1011104, type = 4, next_id = 101110408, aside = "<color=#FFEBA2>张哥：</color>当然，开个半天不成问题。", talk_content = "当然，开个半天不成问题。", talk_name = "张哥",},
	[101110408] = { talk_id = 101110408, group_id = 1011104, type = 4, aside = "<color=#FFEBA2>青乌：</color>这时间限制听着还真刺激。", talk_content = "这时间限制听着还真刺激。", talk_name = "青乌",},
	[101110501] = { talk_id = 101110501, group_id = 1011105, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"那我可就收下了。",101110503,},{"不用那么客气。",101110502,},}, talk_content = "对了，这里还有些小钱，多亏了你工地才能正常开工。", talk_name = "张哥",},
	[101110502] = { talk_id = 101110502, group_id = 1011105, type = 3, talk_content = "你就收下吧，这些钱本来是打算等巡卫来了以后孝敬他们，给他们当辛苦费的。", talk_name = "张哥",},
	[101110503] = { talk_id = 101110503, group_id = 1011105, type = 3, next_id = 101110504, talk_content = "嗯嗯，收下吧收下吧，只要你还在这城里生活钱总是不嫌多的。", talk_name = "张哥",},
	[101110504] = { talk_id = 101110504, group_id = 1011105, type = 3, talk_content = "至理名言呢。", talk_name = "青乌",},
