	[101120102] = { talk_id = 101120102, group_id = 1011201, type = 4, next_id = 101120103, aside = "<color=#FFEBA2><nick_name>：</color>你刚刚倒是跟哑火一样安静。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "你刚刚倒是跟哑火一样安静。", talk_name = "<nick_name>",},
	[101120103] = { talk_id = 101120103, group_id = 1011201, type = 4, next_id = 101120104, aside = "<color=#FFEBA2>离歌：</color>我只是不想你被当作怪人而已。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "我只是不想你被当作怪人而已。", talk_name = "离歌",},
	[101120104] = { talk_id = 101120104, group_id = 1011201, type = 4, next_id = 101120105, aside = "<color=#FFEBA2>离歌：</color>总之这次应该没走空，先回来吧，还记得我的店在哪里吧？", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "总之这次应该没走空，先回来吧，还记得我的店在哪里吧？", talk_name = "离歌",},
	[101120105] = { talk_id = 101120105, group_id = 1011201, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>嗯，记得。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "嗯，记得。", talk_name = "<nick_name>",},
	[101120201] = { talk_id = 101120201, group_id = 1011202, type = 4, next_id = 101120202, aside = "<color=#FFEBA2><nick_name>：</color>要回“离歌”的话最好还是搞台车，不过走路也未尝不可。", talk_content = "要回“离歌”的话最好还是搞台车，不过走路也未尝不可。", talk_name = "<nick_name>",},
	[101120202] = { talk_id = 101120202, group_id = 1011202, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>到底是有多自恋才会用自己的名字当店名啊。", talk_content = "到底是有多自恋才会用自己的名字当店名啊。", talk_name = "<nick_name>",},
	[101120301] = { talk_id = 101120301, group_id = 1011203, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>这里就是茶室离歌了，也是我在天月城的“据点”。", talk_content = "这里就是茶室离歌了，也是我在天月城的“据点”。", talk_name = "<nick_name>",},
	[101120401] = { talk_id = 101120401, group_id = 1011204, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>一如既往的没人。", talk_content = "一如既往的没人。", talk_name = "<nick_name>",},
	[101120402] = { talk_id = 101120402, group_id = 1011204, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>好像是要用那个和离歌联系把。", talk_content = "好像是要用那个和离歌联系把。", talk_name = "<nick_name>",},
	[101120501] = { talk_id = 101120501, group_id = 1011205, type = 1, next_id = 101120502, fade_in = 1, fade_out = true, hide_player = true, talk_content = "你让我拿的这佩从玉到底是什么，我刚刚从它身上听到的声音是？", talk_name = "<nick_name>",},
	[101120502] = { talk_id = 101120502, group_id = 1011205, type = 1, next_id = 101120503, talk_content = "我只能回答你第一个问题，这佩从玉是和你一起被运到天月城的，不过在中途遭遇噬脉生物，发生了意外。", talk_name = "离歌",},
	[101120503] = { talk_id = 101120503, group_id = 1011205, type = 1, next_id = 101120504, talk_content = "而结果就是你在路上游荡的时候被我收留，而这块玉则继续在悬天手上。", talk_name = "离歌",},
	[101120504] = { talk_id = 101120504, group_id = 1011205, type = 1, next_id = 101120505, talk_content = "……等等，所以我和这玉其实原本都是悬天运来的？", talk_name = "<nick_name>",},
	[101120505] = { talk_id = 101120505, group_id = 1011205, type = 1, next_id = 101120506, talk_content = "嗯，在悬天内部把你们称为能撼动天月城根本的存在。", talk_name = "离歌",},
	[101120506] = { talk_id = 101120506, group_id = 1011205, type = 1, next_id = 101120507, talk_content = "我可没有力量，这块玉我估计应该也没有。", talk_name = "<nick_name>",},
	[101120507] = { talk_id = 101120507, group_id = 1011205, type = 1, next_id = 101120508, talk_content = "也是，毕竟那块玉是空壳。", talk_name = "离歌",},
	[101120508] = { talk_id = 101120508, group_id = 1011205, type = 1, next_id = 101120509, talk_content = "你说这里面没有佩从吗？", talk_name = "<nick_name>",},
	[101120510] = { talk_id = 101120510, group_id = 1011205, type = 1, next_id = 101120511, talk_content = "我的情报是这么告诉我的，那实际如何，你能叫出来佩从吗？", talk_name = "离歌",},
	[101120511] = { talk_id = 101120511, group_id = 1011205, type = 1, next_id = 101120512, talk_content = "我想应该是不行的，可以的话我早就用上了……离歌，我能再问一次你到底想做什么吗？", talk_name = "<nick_name>",},
	[101120512] = { talk_id = 101120512, group_id = 1011205, type = 1, next_id = 101120513, talk_content = "你现在只要知道我是遵守我们之间的交易来办事就好。", talk_name = "离歌",},
	[101120513] = { talk_id = 101120513, group_id = 1011205, type = 1, next_id = 101120514, talk_content = "你替我找回记忆，而我则要在你手下办事……之前是这样说的吧。", talk_name = "<nick_name>",},
	[101120514] = { talk_id = 101120514, group_id = 1011205, type = 1, next_id = 101120515, talk_content = "话就说到这里吧，青乌，有新委托了。", talk_name = "离歌",},
	[101120515] = { talk_id = 101120515, group_id = 1011205, type = 1, next_id = 101120516, talk_content = "新委托？", talk_name = "<nick_name>",},
	[101120516] = { talk_id = 101120516, group_id = 1011205, type = 1, next_id = 101120517, talk_content = "嗯，不过在那之前我给你备了辆新车，你先开去翻新一下再说，然后……", talk_name = "离歌",},
	[101120517] = { talk_id = 101120517, group_id = 1011205, type = 1, next_id = 101120518, talk_content = "然后？", talk_name = "<nick_name>",},
	[101120518] = { talk_id = 101120518, group_id = 1011205, type = 1, talk_content = "让我补上一句你大概没听过的话吧……欢迎来到天月城，青乌。", talk_name = "离歌",},
	[101120601] = { talk_id = 101120601, group_id = 1011206, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>又是车，这次应该能好好启动的吧。", talk_content = "又是车，这次应该能好好启动的吧。", talk_name = "<nick_name>",},
	[101120701] = { talk_id = 101120701, group_id = 1011207, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>看上去没什么问题，将车翻新的地方我记得就在那附近……", talk_content = "看上去没什么问题，将车翻新的地方我记得就在那附近……", talk_name = "<nick_name>",},
	[101120801] = { talk_id = 101120801, group_id = 1011208, type = 4, next_id = 101120802, aside = "<color=#FFEBA2><nick_name>：</color>离歌与我建立的交易的确对我有利，但话又说回来。", talk_content = "离歌与我建立的交易的确对我有利，但话又说回来。", talk_name = "<nick_name>",},
	[101120802] = { talk_id = 101120802, group_id = 1011208, type = 4, next_id = 101120803, aside = "<color=#FFEBA2><nick_name>：</color>她一直都是与我远程联系，我根本连她是不是“人类”也不知道。", talk_content = "她一直都是与我远程联系，我根本连她是不是“人类”也不知道。", talk_name = "<nick_name>",},
	[101120803] = { talk_id = 101120803, group_id = 1011208, type = 4, next_id = 101120804, aside = "<color=#FFEBA2><nick_name>：</color>而且她知道的事情肯定远比她说出来的要多。", talk_content = "而且她知道的事情肯定远比她说出来的要多。", talk_name = "<nick_name>",},
	[101120804] = { talk_id = 101120804, group_id = 1011208, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>……只能随机应变了。", talk_content = "……只能随机应变了。", talk_name = "<nick_name>",},
	[101130201] = { talk_id = 101130201, group_id = 1011302, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>只要把车停进去就好。", talk_content = "只要把车停进去就好。", talk_name = "<nick_name>",},
	[101130301] = { talk_id = 101130301, group_id = 1011303, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>接下来就是去找委托人了，离歌刚刚应该有把碰面地点发来才是。", talk_content = "接下来就是去找委托人了，离歌刚刚应该有把碰面地点发来才是。", talk_name = "<nick_name>",},
	[101130401] = { talk_id = 101130401, group_id = 1011304, type = 4, next_id = 101130402, aside = "<color=#FFEBA2>？？？：</color>这边，在这边！", talk_content = "这边，在这边！", talk_name = "？？？",},
	[101130402] = { talk_id = 101130402, group_id = 1011304, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>这个应该是委托人吧。", talk_content = "这个应该是委托人吧。", talk_name = "<nick_name>",},
	[101130501] = { talk_id = 101130501, group_id = 1011305, type = 1, next_id = 101130502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，你是”离歌“那边派来的脉者对吧，我还以为自己上当受骗了呢。", talk_name = "？？？",},
	[101130502] = { talk_id = 101130502, group_id = 1011305, type = 1, next_id = 101130503, talk_content = "被骗了？", talk_name = "<nick_name>",},
	[101130503] = { talk_id = 101130503, group_id = 1011305, type = 2, options = {{"（报上名号）",101130508,},{"你为什么一眼就知道我是离歌派来的？",101130504,},}, talk_content = "因为你们的收费很低也不问来历……只需要我去办点小事，对了，我叫陈塔。", talk_name = "陈塔",},
	[101130504] = { talk_id = 101130504, group_id = 1011305, type = 3, next_id = 101130505, talk_content = "额，难道你不是吗？我是看车型和颜色和说好的是一样的。", talk_name = "陈塔",},
	[101130505] = { talk_id = 101130505, group_id = 1011305, type = 3, next_id = 101130506, talk_content = "原来这个翻新是为了碰头用的啊……我还在奇怪为什么颜色都换了。", talk_name = "<nick_name>",},
	[101130506] = { talk_id = 101130506, group_id = 1011305, type = 3, next_id = 101130507, talk_content = "而且也说了会派一位辰属的脉者，符合这个条件的也只有你了。", talk_name = "陈塔",},
	[101130507] = { talk_id = 101130507, group_id = 1011305, type = 3, next_id = 101130508, talk_content = "嗯……看来我这边也没有认错委托人，我的名字叫<nick_name>，你也可以叫我青乌。", talk_name = "<nick_name>",},
	[101130508] = { talk_id = 101130508, group_id = 1011305, type = 1, next_id = 101130509, talk_content = "那接下来说说我的委托吧，我想让你帮我拿……不，找回一块佩从玉。", talk_name = "陈塔",},
	[101130509] = { talk_id = 101130509, group_id = 1011305, type = 1, next_id = 101130510, talk_content = "找失物吗？", talk_name = "<nick_name>",},
	[101130510] = { talk_id = 101130510, group_id = 1011305, type = 2, child_type = 21, options = {{"有什么线索吗？",101130511,},{"（关于佩从玉的常识）",101130513,},}, talk_content = "嗯，我之前上街的时候不小心把佩玉弄丢了。", talk_name = "陈塔",},
	[101130511] = { talk_id = 101130511, group_id = 1011305, type = 3, next_id = 101130512, icon = "interaction_4", talk_content = "有的，玉我是在方街小巷附近弄丢的，而且玉上也画了印。", talk_name = "陈塔",},
	[101130512] = { talk_id = 101130512, group_id = 1011305, type = 3, next_id = 101130517, talk_content = "什么样的印？", talk_name = "<nick_name>",},
	[101130513] = { talk_id = 101130513, group_id = 1011305, type = 3, next_id = 101130514, icon = "interaction_4", talk_content = "我之前看见过有人在佩从玉上做标记，你那块玉上有做吗？", talk_name = "<nick_name>",},
	[101130514] = { talk_id = 101130514, group_id = 1011305, type = 3, next_id = 101130515, talk_content = "有，当然有。", talk_name = "陈塔",},
	[101130515] = { talk_id = 101130515, group_id = 1011305, type = 3, next_id = 101130516, talk_content = "那至少也不算是大海捞针，佩从玉这东西基本都长一样，要是丢了估计还真不好找。", talk_name = "<nick_name>",},
	[101130516] = { talk_id = 101130516, group_id = 1011305, type = 3, next_id = 101130517, talk_content = "所以你给你的佩从玉做了什么标记？", talk_name = "<nick_name>",},
	[101130517] = { talk_id = 101130517, group_id = 1011305, type = 1, next_id = 101130518, talk_content = "额，我想想……对了，是一个印，上面写着一个松字。", talk_name = "陈塔",},
	[101130518] = { talk_id = 101130518, group_id = 1011305, type = 1, next_id = 101130519, talk_content = "松……是吧。", talk_name = "<nick_name>",},
	[101130519] = { talk_id = 101130519, group_id = 1011305, type = 1, talk_content = "嗯嗯，那块玉是很重要的东西，拜托你了青乌，请尽快去帮我找回来吧。", talk_name = "陈塔",},
	[101130601] = { talk_id = 101130601, group_id = 1011306, type = 1, next_id = 101130602, talk_content = "丢玉的地方可能有些危险……但是那块玉真的很重要。", talk_name = "陈塔",},
	[101130602] = { talk_id = 101130602, group_id = 1011306, type = 1, talk_content = "就拜托你了，青乌。", talk_name = "陈塔",},
	[101130701] = { talk_id = 101130701, group_id = 1011307, type = 4, next_id = 101130702, aside = "<color=#FFEBA2><nick_name>：</color>明明人叫陈塔，但是玉上却印的是松字……", talk_content = "明明人叫陈塔，但是玉上却印的是松字……", talk_name = "<nick_name>",},
	[101130702] = { talk_id = 101130702, group_id = 1011307, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>该不会有什么内情吧。", talk_content = "该不会有什么内情吧。", talk_name = "<nick_name>",},
	[101130801] = { talk_id = 101130801, group_id = 1011308, type = 4, next_id = 101130802, aside = "<color=#FFEBA2><nick_name>：</color>佩从玉就是在这边丢下的，四处找找看。", talk_content = "佩从玉就是在这边丢下的，四处找找看。", talk_name = "<nick_name>",},
	[101130802] = { talk_id = 101130802, group_id = 1011308, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>噬脉生物……！", talk_content = "噬脉生物……！", talk_name = "<nick_name>",},
	[101131001] = { talk_id = 101131001, group_id = 1011310, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"我想找点东西。",101131002,},}, talk_content = "脉者不愧是脉者，要是我也是那就好了……", talk_name = "混混",},
	[101131002] = { talk_id = 101131002, group_id = 1011310, type = 3, next_id = 101131003, talk_content = "要找佩从玉？这附近每天来来往往几十个，我可不记得每个的样貌。", talk_name = "混混",},
	[101131003] = { talk_id = 101131003, group_id = 1011310, type = 2, options = {{"我不知道佩从玉里面是什么。",101131004,},{"这样是在骗人。",101131005,},}, talk_content = "倒是你是脉者的话也不用特意来找吧，直接找个一模一样的怪物将它变成玉不就得了？", talk_name = "混混",},
	[101131004] = { talk_id = 101131004, group_id = 1011310, type = 3, talk_content = "不知道那就没办法了，你去问其他人吧，我没见过什么印着松的玉。", talk_name = "混混",},
	[101131005] = { talk_id = 101131005, group_id = 1011310, type = 3, talk_content = "你不做那就没办法了，毕竟我是真没见过你说的佩从玉。", talk_name = "混混",},
	[101131101] = { talk_id = 101131101, group_id = 1011311, type = 1, next_id = 101131102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "外人别在这里四处逛悠，更别提什么找失物。", talk_name = "混混",},
	[101131102] = { talk_id = 101131102, group_id = 1011311, type = 1, next_id = 101131103, talk_content = "有什么问题吗？", talk_name = "<nick_name>",},
	[101131103] = { talk_id = 101131103, group_id = 1011311, type = 1, next_id = 101131104, talk_content = "你是真傻还是装不懂，这里是我们混混的街道，到底有什么人会在这里”丢“东西。", talk_name = "混混",},
	[101131104] = { talk_id = 101131104, group_id = 1011311, type = 1, next_id = 101131105, talk_content = "但是我的委托人是这么说的。", talk_name = "<nick_name>",},
	[101131105] = { talk_id = 101131105, group_id = 1011311, type = 1, talk_content = "委托人，哼，原来又是一个不要命的，既然是这样的话就去问问那个人吧。", talk_name = "混混",},
	[101131201] = { talk_id = 101131201, group_id = 1011312, type = 1, next_id = 101131202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我想找个佩从玉，上面做了个”松“字的标记，有人说我可以来问你。", talk_name = "<nick_name>",},
	[101131202] = { talk_id = 101131202, group_id = 1011312, type = 1, next_id = 101131203, talk_content = "松字是吧，没想到那老头还能有钱委托脉者。", talk_name = "混混",},
	[101131203] = { talk_id = 101131203, group_id = 1011312, type = 1, next_id = 101131204, talk_content = "老头？你在说什么。", talk_name = "<nick_name>",},
	[101131204] = { talk_id = 101131204, group_id = 1011312, type = 1, next_id = 101131205, talk_content = "你不认也没关系，兄弟们，有人来砸场子了！", talk_name = "混混",},
	[101131205] = { talk_id = 101131205, group_id = 1011312, type = 1, talk_content = "就不讲道理的吗！", talk_name = "<nick_name>",},
	[101131301] = { talk_id = 101131301, group_id = 1011313, type = 1, next_id = 101131302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "可恶，有钱老头请来的脉者就是不一样，我投降！", talk_name = "混混",},
	[101131302] = { talk_id = 101131302, group_id = 1011313, type = 1, next_id = 101131303, talk_content = "你从刚才就一直在说的老头是什么意思，委托我的可不是什么老头。", talk_name = "<nick_name>",},
	[101131303] = { talk_id = 101131303, group_id = 1011313, type = 1, next_id = 101131304, talk_content = "不是老头……那你找那东西干嘛，那可是我小弟从老头身上偷来的货，还是说你是同行？", talk_name = "混混",},
	[101131304] = { talk_id = 101131304, group_id = 1011313, type = 1, next_id = 101131305, talk_content = "偷来的？", talk_name = "<nick_name>",},
	[101131305] = { talk_id = 101131305, group_id = 1011313, type = 2, options = {{"那个小弟现在在哪？",101131306,},}, talk_content = "是啊，前阵子我收了个小弟，让那家伙偷的。", talk_name = "混混",},
	[101131306] = { talk_id = 101131306, group_id = 1011313, type = 3, next_id = 101131307, talk_content = "那家伙消失好几天了，打架不会打，收保护费也不敢，就一软蛋.", talk_name = "混混",},
	[101131307] = { talk_id = 101131307, group_id = 1011313, type = 3, next_id = 101131308, talk_content = "总之你说你要找带松字的佩从玉是吧，那东西我卖给大街上的回收铺了。", talk_name = "混混",},
	[101131308] = { talk_id = 101131308, group_id = 1011313, type = 2, child_type = 21, options = {{"把卖玉的钱交出来",101131309,},{"我要带你去见巡卫",101131313,},}, talk_content = "要找那玉的话你就去回收铺上找，我可不奉陪了！", talk_name = "混混",},
	[101131309] = { talk_id = 101131309, group_id = 1011313, type = 3, next_id = 101131310, icon = "interaction_4", talk_content = "你这家伙你知道你在说什么吗？", talk_name = "混混",},
	[101131310] = { talk_id = 101131310, group_id = 1011313, type = 3, next_id = 101131311, talk_content = "玉本来就不是你的，那卖玉得来的钱也不会是你的。", talk_name = "<nick_name>",},
	[101131311] = { talk_id = 101131311, group_id = 1011313, type = 3, next_id = 101131312, talk_content = "我只想拿那份钱去把玉拿回来，你也不想我们之间再起一次争执吧？", talk_name = "<nick_name>",},
	[101131312] = { talk_id = 101131312, group_id = 1011313, type = 3, talk_content = "算……算你狠！", talk_name = "混混",},
	[101131313] = { talk_id = 101131313, group_id = 1011313, type = 3, next_id = 101131314, icon = "interaction_4", talk_content = "等等，你这家伙也太不厚道了吧！", talk_name = "混混",},
	[101131314] = { talk_id = 101131314, group_id = 1011313, type = 3, next_id = 101131315, talk_content = "你把玉卖到店里面才是不厚道。", talk_name = "<nick_name>",},
	[101131315] = { talk_id = 101131315, group_id = 1011313, type = 3, talk_content = "啧，我明白了，我把卖玉的钱给你行了吧，你自己去找老板买回来就好了！", talk_name = "混混",},
	[101131401] = { talk_id = 101131401, group_id = 1011314, type = 4, next_id = 101131402, aside = "<color=#FFEBA2><nick_name>：</color>街上看着就不是正经人该呆的地方，而且那个人说了小弟是吧……", talk_content = "街上看着就不是正经人该呆的地方，而且那个人说了小弟是吧……", talk_name = "<nick_name>",},
	[101131402] = { talk_id = 101131402, group_id = 1011314, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>先去回收铺看看吧。", talk_content = "先去回收铺看看吧。", talk_name = "<nick_name>",},
	[101131501] = { talk_id = 101131501, group_id = 1011315, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"我在找一个佩从玉。",101131502,},{"我委托人的佩从玉被小偷卖到这里了。",101131503,},}, talk_content = "欢迎光临，要买点什么吗？", talk_name = "回收铺店主",},
	[101131502] = { talk_id = 101131502, group_id = 1011315, type = 3, next_id = 101131504, talk_content = "找一个带松印记的佩从玉，嗯……", talk_name = "回收铺店主",},
	[101131503] = { talk_id = 101131503, group_id = 1011315, type = 3, next_id = 101131504, talk_content = "我这里没有失物，只是会收购些别人拿来的东西而已。", talk_name = "回收铺店主",},
	[101131504] = { talk_id = 101131504, group_id = 1011315, type = 1, next_id = 101131505, talk_content = "把玉卖到这里的人我已经见过了，店主你也别装了怎么样。", talk_name = "<nick_name>",},
	[101131505] = { talk_id = 101131505, group_id = 1011315, type = 1, next_id = 101131506, talk_content = "……我也不是装，只是谨慎为上。", talk_name = "回收铺店主",},
	[101131506] = { talk_id = 101131506, group_id = 1011315, type = 2, options = {{"那该怎么办？",101131507,},{"要怎么样才能变成熟面孔？",101131507,},}, talk_content = "我看你也应该不像是那边的人，可本店原则上不跟生面孔做生意。", talk_name = "回收铺店主",},
	[101131507] = { talk_id = 101131507, group_id = 1011315, type = 3, next_id = 101131508, talk_content = "当然是在这里先消费一点当见面费了，桌上的几个货物挑一个如何？", talk_name = "回收铺店主",},
	[101131508] = { talk_id = 101131508, group_id = 1011315, type = 3, talk_content = "你在这里买过东西了，我们再谈你要找的东西。", talk_name = "回收铺店主",},
	[101131601] = { talk_id = 101131601, group_id = 1011316, type = 1, next_id = 101131602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "老板，这东西价格真不是乱写的吗？", talk_name = "<nick_name>",},
	[101131602] = { talk_id = 101131602, group_id = 1011316, type = 1, next_id = 101131603, talk_content = "我的店我爱怎么卖就怎么卖，怎么样，要买吗？", talk_name = "回收铺店主",},
	[101131603] = { talk_id = 101131603, group_id = 1011316, type = 1, talk_content = "（就算有钱也不能乱花……试着讲讲价吧。）", talk_name = "<nick_name>",},
	[101131701] = { talk_id = 101131701, group_id = 1011317, type = 1, next_id = 101131702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "行，就这价亏本卖你吧。", talk_name = "回收铺店主",},
	[101131702] = { talk_id = 101131702, group_id = 1011317, type = 1, next_id = 101131703, talk_content = "无论怎么样你都有赚头吧？", talk_name = "<nick_name>",},
	[101131703] = { talk_id = 101131703, group_id = 1011317, type = 1, talk_content = "这可是见面费，那些俱乐部啥的不也要开会员，一个意思。", talk_name = "回收铺店主",},
	[101131801] = { talk_id = 101131801, group_id = 1011318, type = 1, next_id = 101131802, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你要找”松“字的佩从玉是吧，很可惜，那个前几天卖掉了。", talk_name = "回收铺店主",},
	[101131802] = { talk_id = 101131802, group_id = 1011318, type = 1, next_id = 101131803, talk_content = "卖掉了……买了玉的人现在在哪？", talk_name = "<nick_name>",},
	[101131803] = { talk_id = 101131803, group_id = 1011318, type = 1, next_id = 101131804, talk_content = "这就是客户隐私了，你不会觉得我会把这个说出来吧？", talk_name = "回收铺店主",},
	[101131804] = { talk_id = 101131804, group_id = 1011318, type = 2, child_type = 21, options = {{"你真会占便宜。",101131805,},{"要做点什么？",101131807,},{"我差不多该生气了。",101131806,},}, talk_content = "当然，要是你能帮我再做点事情的话……", talk_name = "回收铺店主",},
	[101131805] = { talk_id = 101131805, group_id = 1011318, type = 3, next_id = 101131807, icon = "interaction_4", talk_content = "谢谢夸奖。", talk_name = "回收铺店主",},
	[101131806] = { talk_id = 101131806, group_id = 1011318, type = 3, next_id = 101131807, icon = "interaction_4", talk_content = "……我没有为难脉者大人你的意思，但老人家我的确需要你再做点事情。", talk_name = "回收铺店主",},
	[101131807] = { talk_id = 101131807, group_id = 1011318, type = 1, next_id = 101131808, icon = "interaction_4", talk_content = "是这样的，我有一群不太喜欢付货款的客户，我希望你能去教训他们一下。", talk_name = "回收铺店主",},
	[101131808] = { talk_id = 101131808, group_id = 1011318, type = 1, next_id = 101131809, talk_content = "只是教训就行了吗？", talk_name = "<nick_name>",},
	[101131809] = { talk_id = 101131809, group_id = 1011318, type = 1, next_id = 101131810, talk_content = "嗯，毕竟那些小混混手上大概也不会有钱付之前的账，接着这次干脆让他们之后都不要来就好了。", talk_name = "回收铺店主",},
	[101131810] = { talk_id = 101131810, group_id = 1011318, type = 1, talk_content = "将这件事办完，回来我就把佩从玉到底去了哪告诉你。", talk_name = "回收铺店主",},
	[101131901] = { talk_id = 101131901, group_id = 1011319, type = 1, next_id = 101131902, talk_content = "我知道你想要抗议我这个老人家处处为难你。", talk_name = "回收铺店主",},
	[101131902] = { talk_id = 101131902, group_id = 1011319, type = 1, next_id = 101131903, talk_content = "这样吧，钱其实那群家伙是有的，吃了教训之后大概也会如数吐出来。", talk_name = "回收铺店主",},
	[101131903] = { talk_id = 101131903, group_id = 1011319, type = 1, next_id = 101131904, talk_content = "然后那钱我就不要了，都当作给你的报酬吧，呵呵。", talk_name = "回收铺店主",},
	[101131904] = { talk_id = 101131904, group_id = 1011319, type = 1, talk_content = "……", talk_name = "<nick_name>",},
	[101132001] = { talk_id = 101132001, group_id = 1011320, type = 4, next_id = 101132002, aside = "<color=#FFEBA2>混混：</color>喂，这里闲人免进，没看见牌子吗？", talk_content = "喂，这里闲人免进，没看见牌子吗？", talk_name = "混混",},
	[101132002] = { talk_id = 101132002, group_id = 1011320, type = 4, next_id = 101132003, aside = "<color=#FFEBA2><nick_name>：</color>我没看见牌子，而且我是回收店那派来的，算不上闲人。", talk_content = "我没看见牌子，而且我是回收店那派来的，算不上闲人。", talk_name = "<nick_name>",},
	[101132003] = { talk_id = 101132003, group_id = 1011320, type = 4, next_id = 101132004, aside = "<color=#FFEBA2>混混：</color>收债的是吧，兄弟们上！", talk_content = "收债的是吧，兄弟们上！", talk_name = "混混",},
	[101132004] = { talk_id = 101132004, group_id = 1011320, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>也省功夫了。", talk_content = "也省功夫了。", talk_name = "<nick_name>",},
	[101132101] = { talk_id = 101132101, group_id = 1011321, type = 4, next_id = 101132102, aside = "<color=#FFEBA2>混混：</color>别找什么钱箱了，快来帮忙！", talk_content = "别找什么钱箱了，快来帮忙！", talk_name = "混混",},
	[101132102] = { talk_id = 101132102, group_id = 1011321, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>钱箱？", talk_content = "钱箱？", talk_name = "<nick_name>",},
	[101132201] = { talk_id = 101132201, group_id = 1011322, type = 4, next_id = 101132202, aside = "<color=#FFEBA2>混混：</color>可恶……明明差一些就能东山再起。", talk_content = "可恶……明明差一些就能东山再起。", talk_name = "混混",},
	[101132202] = { talk_id = 101132202, group_id = 1011322, type = 4, aside = "<color=#FFEBA2>混混：</color>撤，撤退！", talk_content = "撤，撤退！", talk_name = "混混",},
	[101132301] = { talk_id = 101132301, group_id = 1011323, type = 4, next_id = 101132302, aside = "<color=#FFEBA2><nick_name>：</color>这样就算是完成委托了吧，倒是刚刚他们好像提到钱箱啥的……", talk_content = "这样就算是完成委托了吧，倒是刚刚他们好像提到钱箱啥的……", talk_name = "<nick_name>",},
	[101132302] = { talk_id = 101132302, group_id = 1011323, type = 4, next_id = 101132303, aside = "<color=#FFEBA2><nick_name>：</color>店主说的“钱”该不会就是那个吧。", talk_content = "店主说的“钱”该不会就是那个吧。", talk_name = "<nick_name>",},
	[101132303] = { talk_id = 101132303, group_id = 1011323, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>要找找看吗……", talk_content = "要找找看吗……", talk_name = "<nick_name>",},
	[101132401] = { talk_id = 101132401, group_id = 1011324, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"你不怕他们报复吗？",101132402,},{"他们应该是受教训了。",101132403,},}, talk_content = "你回来就代表事情已经办好了对吧。", talk_name = "回收铺店主",},
	[101132402] = { talk_id = 101132402, group_id = 1011324, type = 3, next_id = 101132404, talk_content = "接下来的事情我也有我的办法，就不需要你操心了。", talk_name = "回收铺店主",},
	[101132403] = { talk_id = 101132403, group_id = 1011324, type = 3, next_id = 101132404, talk_content = "那就好，那些人倚仗自己年轻就不守规矩，真的不行。", talk_name = "回收铺店主",},
	[101132404] = { talk_id = 101132404, group_id = 1011324, type = 1, next_id = 101132405, talk_content = "既然事情妥了那我也说你想知道的事情吧。", talk_name = "回收铺店主",},
	[101132405] = { talk_id = 101132405, group_id = 1011324, type = 1, next_id = 101132406, talk_content = "买走玉是一个新兴帮派的老大，那个帮派盘踞的地方我也可以告诉你。", talk_name = "回收铺店主",},
	[101132406] = { talk_id = 101132406, group_id = 1011324, type = 1, next_id = 101132407, talk_content = "老大……有种又要闯龙潭的感觉。", talk_name = "<nick_name>",},
	[101132407] = { talk_id = 101132407, group_id = 1011324, type = 1, talk_content = "呵呵，那就是你的事情了。", talk_name = "回收铺店主",},
	[101132501] = { talk_id = 101132501, group_id = 1011325, type = 1, next_id = 101132502, talk_content = "那块佩从玉内容物不怎么样，但年份可久远了。", talk_name = "回收铺店主",},
	[101132502] = { talk_id = 101132502, group_id = 1011325, type = 1, next_id = 101132503, talk_content = "真可惜呐，要是你早来几天那玉当作报酬给你也没关系。", talk_name = "回收铺店主",},
	[101132503] = { talk_id = 101132503, group_id = 1011325, type = 1, talk_content = "毕竟比起那点钱……坏规矩的人才是最留不得的。", talk_name = "回收铺店主",},
	[101132601] = { talk_id = 101132601, group_id = 1011326, type = 1, next_id = 101132602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "……", talk_name = "回收铺店主",},
	[101132602] = { talk_id = 101132602, group_id = 1011326, type = 1, next_id = 101132603, talk_content = "……", talk_name = "<nick_name>",},
	[101132603] = { talk_id = 101132603, group_id = 1011326, type = 1, next_id = 101132604, talk_content = "我还特意放水了……你这年轻人。", talk_name = "回收铺店主",},
	[101132604] = { talk_id = 101132604, group_id = 1011326, type = 1, talk_content = "也罢也罢，就当是首次筹备了。", talk_name = "回收铺店主",},
	[101140101] = { talk_id = 101140101, group_id = 1011401, type = 4, next_id = 101140102, aside = "<color=#FFEBA2>离歌：</color>我想你也差不多想和我联系了。", call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "我想你也差不多想和我联系了。", talk_name = "离歌",},
	[101140102] = { talk_id = 101140102, group_id = 1011401, type = 4, next_id = 101140103, aside = "<color=#FFEBA2><nick_name>：</color>离歌，委托人的身份到底是？", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "离歌，委托人的身份到底是？", talk_name = "<nick_name>",},
	[101140103] = { talk_id = 101140103, group_id = 1011401, type = 4, next_id = 101140104, aside = "<color=#FFEBA2>离歌：</color>如你查到的那样，是个小贼，他并不是佩从玉的主人。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "如你查到的那样，是个小贼，他并不是佩从玉的主人。", talk_name = "离歌",},
	[101140104] = { talk_id = 101140104, group_id = 1011401, type = 4, next_id = 101140105, aside = "<color=#FFEBA2><nick_name>：</color>……", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "……", talk_name = "<nick_name>",},
	[101140105] = { talk_id = 101140105, group_id = 1011401, type = 4, aside = "<color=#FFEBA2>离歌：</color>我既然会接下就代表这委托并不是什么坏事，你还想知道些什么的话就去找委托人吧。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "我既然会接下就代表这委托并不是什么坏事，你还想知道些什么的话就去找委托人吧。", talk_name = "离歌",},
	[101140201] = { talk_id = 101140201, group_id = 1011402, type = 5, next_id = 101140202, aside = "<color=#FFEBA2>陈塔：</color>等等！", child_type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "等等！", talk_name = "陈塔",},
	[101140202] = { talk_id = 101140202, group_id = 1011402, type = 5, next_id = 101140203, aside = "<color=#FFEBA2>？？？：</color>你这小贼，别想跑！", child_type = 2, talk_content = "你这小贼，别想跑！", talk_name = "？？？",},
	[101140203] = { talk_id = 101140203, group_id = 1011402, type = 5, next_id = 101140204, aside = "<color=#FFEBA2>？？？：</color>总之先用符把人捉住……拘！", child_type = 2, talk_content = "总之先用符把人捉住……拘！", talk_name = "？？？",},
	[101140204] = { talk_id = 101140204, group_id = 1011402, type = 5, next_id = 101140205, aside = "<color=#FFEBA2>？？？：</color>什么！？", child_type = 2, talk_content = "什么！？", talk_name = "？？？",},
	[101140205] = { talk_id = 101140205, group_id = 1011402, type = 5, next_id = 101140206, aside = "<color=#FFEBA2>？？？：</color>你是什么人！", child_type = 2, talk_content = "你是什么人！", talk_name = "？？？",},
	[101140206] = { talk_id = 101140206, group_id = 1011402, type = 5, aside = "<color=#FFEBA2><nick_name>：</color>青乌，接了这个人任务的脉者。", child_type = 2, talk_content = "青乌，接了这个人任务的脉者。", talk_name = "<nick_name>",},
	[101140301] = { talk_id = 101140301, group_id = 1011403, type = 1, next_id = 101140302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我的名字是来来，维安司的成员，你说你叫青乌是吧。", talk_name = "来来",},
	[101140302] = { talk_id = 101140302, group_id = 1011403, type = 1, next_id = 101140303, talk_content = "你知道你后面的这个是个窃贼吗？", talk_name = "来来",},
	[101140303] = { talk_id = 101140303, group_id = 1011403, type = 2, options = {{"（沉默）",101140304,},{"我知道他是。",101140305,},}, talk_content = "呃……，不是这样的。", talk_name = "陈塔",},
	[101140304] = { talk_id = 101140304, group_id = 1011403, type = 3, next_id = 101140306, talk_content = "哼，你这表情就表示你也知道了是吧。", talk_name = "来来",},
	[101140305] = { talk_id = 101140305, group_id = 1011403, type = 3, next_id = 101140306, talk_content = "那为什么还包庇他，你和他是一伙的吗？", talk_name = "来来",},
	[101140306] = { talk_id = 101140306, group_id = 1011403, type = 1, next_id = 101140307, talk_content = "我想要问清楚一些事情，毕竟我的“老板”也让我这么做。", talk_name = "<nick_name>",},
	[101140307] = { talk_id = 101140307, group_id = 1011403, type = 1, talk_content = "……行，就信你一次，要问什么就问清楚吧。", talk_name = "来来",},
	[101140401] = { talk_id = 101140401, group_id = 1011404, type = 1, talk_content = "怎么，不是要问清楚吗，不问我就把人带走了。", talk_name = "来来",},
	[101140501] = { talk_id = 101140501, group_id = 1011405, type = 1, next_id = 101140502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "陈塔，你应该知道我要问点什么吧？", talk_name = "<nick_name>",},
	[101140502] = { talk_id = 101140502, group_id = 1011405, type = 1, next_id = 101140503, talk_content = "……在回答之前我想要问个问题，佩从玉找回来了吗？", talk_name = "陈塔",},
	[101140503] = { talk_id = 101140503, group_id = 1011405, type = 1, next_id = 101140504, talk_content = "没有，在几次转手之后现在在一个新兴帮派的老大的手中。", talk_name = "<nick_name>",},
	[101140504] = { talk_id = 101140504, group_id = 1011405, type = 2, options = {{"你为什么要骗我？",101140505,},{"这跟原本的委托内容不一样。",101140509,},}, talk_content = "怎么会这样……拜托了，能请你把那块玉带回来交给原本的主人吗？", talk_name = "陈塔",},
	[101140505] = { talk_id = 101140505, group_id = 1011405, type = 3, next_id = 101140506, talk_content = "因为我第一次见你的时候就觉得你不是道上的人，而且茶室离歌也说会保护委托人的隐私。", talk_name = "陈塔",},
	[101140506] = { talk_id = 101140506, group_id = 1011405, type = 3, next_id = 101140507, talk_content = "所以我才隐瞒了一些实情……我本来是打算在你把玉带回来再向你坦白的。", talk_name = "陈塔",},
	[101140507] = { talk_id = 101140507, group_id = 1011405, type = 3, next_id = 101140508, talk_content = "但没想到老大竟然把玉卖掉了……", talk_name = "陈塔",},
	[101140508] = { talk_id = 101140508, group_id = 1011405, type = 3, next_id = 101140510, talk_content = "那家伙说的小弟果然是你。", talk_name = "<nick_name>",},
	[101140509] = { talk_id = 101140509, group_id = 1011405, type = 3, next_id = 101140510, talk_content = "是的，这和我与你说的委托不一样，但是拜托你了。", talk_name = "陈塔",},
	[101140510] = { talk_id = 101140510, group_id = 1011405, type = 1, next_id = 101140511, talk_content = "我在偷了那块佩从玉以后才发现那块佩从玉对于原本的主人来说是十分重要的存在。", talk_name = "陈塔",},
	[101140511] = { talk_id = 101140511, group_id = 1011405, type = 1, next_id = 101140512, talk_content = "我实在不忍心看见那位老人总是失了神一样的样子。", talk_name = "陈塔",},
	[101140512] = { talk_id = 101140512, group_id = 1011405, type = 1, next_id = 101140513, talk_content = "但因为打不过老大……所以才想到去拜托之前听说过的“离歌”。", talk_name = "陈塔",},
	[101140513] = { talk_id = 101140513, group_id = 1011405, type = 1, next_id = 101140514, talk_content = "拿你的真正委托其实是让我把玉找回来，并且物归原主，是吗？", talk_name = "<nick_name>",},
	[101140514] = { talk_id = 101140514, group_id = 1011405, type = 1, next_id = 101140515, talk_content = "是……是的，拜托了，假如你愿意答应我的话我现在跟那位维安司去自首也没所谓。", talk_name = "陈塔",},
	[101140515] = { talk_id = 101140515, group_id = 1011405, type = 1, talk_content = "（这么说来……倒是忘记详细问那个来来到底是什么人了。）", talk_name = "<nick_name>",},
	[101140601] = { talk_id = 101140601, group_id = 1011406, type = 1, next_id = 101140602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "先和你说声对不起，你们的对话我都听见了。", talk_name = "来来",},
	[101140602] = { talk_id = 101140602, group_id = 1011406, type = 1, next_id = 101140603, talk_content = "真没想到这年头还有这么不干脆的小贼，有些犯人就是这样，因为过于害怕导致思维混乱。", talk_name = "来来",},
	[101140603] = { talk_id = 101140603, group_id = 1011406, type = 2, options = {{"你到底是什么人？",101140604,},}, talk_content = "自然说话也毫无逻辑，我看你是跑了一趟无用功的路对吧？", talk_name = "来来",},
	[101140604] = { talk_id = 101140604, group_id = 1011406, type = 3, next_id = 101140605, talk_content = "……我刚刚应该有说过自己是维安司的人吧，还是说你没记住我的名字。", talk_name = "来来",},
	[101140605] = { talk_id = 101140605, group_id = 1011406, type = 3, next_id = 101140606, talk_content = "就是那个维安司到底是什么东西？", talk_name = "<nick_name>",},
	[101140606] = { talk_id = 101140606, group_id = 1011406, type = 2, child_type = 21, options = {{"我听说主持这个的是巡卫",101140607,},{"你是警察之类的？",101140609,},}, talk_content = "你该不会是外地来的人吧，维安司可是城里最能代表公正的组织。", talk_name = "来来",},
	[101140607] = { talk_id = 101140607, group_id = 1011406, type = 3, next_id = 101140608, icon = "interaction_4", talk_content = "别把我们把巡卫混为一谈，那些人不过是收钱办事的佣兵。", talk_name = "来来",},
	[101140608] = { talk_id = 101140608, group_id = 1011406, type = 3, next_id = 101140610, talk_content = "而我们维安司是真正为民众和正义办事的，虽然非官方就是了。", talk_name = "来来",},
	[101140609] = { talk_id = 101140609, group_id = 1011406, type = 3, next_id = 101140610, icon = "interaction_4", talk_content = "不，我们并没有那种权力，只是个非官方的维护正义的组织。", talk_name = "来来",},
	[101140610] = { talk_id = 101140610, group_id = 1011406, type = 2, options = {{"（赞同来来的说法）",101140611,},{"（不赞同来来的说法）",101140613,},}, talk_content = "正因如此，我才会在城中各处奔跑，惩戒像你委托人这样的小贼。", talk_name = "来来",},
	[101140611] = { talk_id = 101140611, group_id = 1011406, type = 3, next_id = 101140612, talk_content = "不论什么官方不官方，捉贼的确是见义勇为之人该做的事情。", talk_name = "<nick_name>",},
	[101140612] = { talk_id = 101140612, group_id = 1011406, type = 3, next_id = 101140617, talk_content = "哼，你挺明白的嘛。", talk_name = "来来",},
	[101140613] = { talk_id = 101140613, group_id = 1011406, type = 3, next_id = 101140614, talk_content = "既然非官方的话那这样到处捉贼是不是有多少不妥，再说我认为我的委托人刚刚应该没有做什么偷摸之事。", talk_name = "<nick_name>",},
	[101140614] = { talk_id = 101140614, group_id = 1011406, type = 3, next_id = 101140615, talk_content = "但他之前有做，这可是我通过特殊渠道得来的信息，而实际也是如此，你该不会是要扭曲是非黑白吧？", talk_name = "来来",},
	[101140615] = { talk_id = 101140615, group_id = 1011406, type = 3, next_id = 101140616, talk_content = "那要是我让你带走他的话你之后会做什么。", talk_name = "<nick_name>",},
	[101140616] = { talk_id = 101140616, group_id = 1011406, type = 3, next_id = 101140617, talk_content = "先询问，然后再根据情况将他交给巡卫，先说好，这是我们总司的意思。", talk_name = "来来",},
	[101140617] = { talk_id = 101140617, group_id = 1011406, type = 2, options = {{"我还没答应。",101140618,},{"我只是去取回委托物。",101140619,},}, talk_content = "不过嘛，从你们刚刚的对话我听见了一个有趣的事情，你是要准备捣毁一个新兴帮派的窝点对吧？", talk_name = "来来",},
	[101140618] = { talk_id = 101140618, group_id = 1011406, type = 3, next_id = 101140620, talk_content = "那现在答应下来就行，小恶引出大恶可是经典桥段，接下来也算上我吧。", talk_name = "来来",},
	[101140619] = { talk_id = 101140619, group_id = 1011406, type = 3, next_id = 101140620, talk_content = "用指头想都知道不可能那么简单就能拿回来，捣毁帮派可是大功，接下来也算上我吧。", talk_name = "来来",},
	[101140620] = { talk_id = 101140620, group_id = 1011406, type = 1, next_id = 101140621, talk_content = "等等，我刚才好像听见你说“算上你”？", talk_name = "<nick_name>",},
	[101140621] = { talk_id = 101140621, group_id = 1011406, type = 1, talk_content = "是啊，接下来我两就组队行动吧。", talk_name = "来来",},
	[101140701] = { talk_id = 101140701, group_id = 1011407, type = 4, next_id = 101140702, aside = "<color=#FFEBA2>来来：</color>青乌，那个新兴帮派你知道在哪的吧。", talk_content = "青乌，那个新兴帮派你知道在哪的吧。", talk_name = "来来",},
	[101140702] = { talk_id = 101140702, group_id = 1011407, type = 4, next_id = 101140703, aside = "<color=#FFEBA2><nick_name>：</color>知道是知道，倒是为什么突然我们就要一起行动了？", talk_content = "知道是知道，倒是为什么突然我们就要一起行动了？", talk_name = "<nick_name>",},
	[101140703] = { talk_id = 101140703, group_id = 1011407, type = 4, next_id = 101140704, aside = "<color=#FFEBA2>来来：</color>你能帮人，我也能帮人顺带立功，这不是利益一致吗？", talk_content = "你能帮人，我也能帮人顺带立功，这不是利益一致吗？", talk_name = "来来",},
	[101140704] = { talk_id = 101140704, group_id = 1011407, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>我听出来了，你想立功。", talk_content = "我听出来了，你想立功。", talk_name = "<nick_name>",},
	[101140801] = { talk_id = 101140801, group_id = 1011408, type = 1, next_id = 101140802, talk_content = "抱歉……虽说是被老大威胁，但偷了东西是事实，我还骗了青乌你，抱歉。", talk_name = "陈塔",},
	[101140802] = { talk_id = 101140802, group_id = 1011408, type = 1, next_id = 101140803, talk_content = "就算有悔过之心贼还是贼，我会让维安司的其他人过来的。", talk_name = "来来",},
	[101140803] = { talk_id = 101140803, group_id = 1011408, type = 1, next_id = 101140804, talk_content = "可你们又不是官方组织，真的能擅自带走人吗？", talk_name = "<nick_name>",},
	[101140804] = { talk_id = 101140804, group_id = 1011408, type = 1, next_id = 101140805, talk_content = "这也能保护他，无论小恶还是大恶，一旦不顺巡卫的意思那最终都是一个下场。", talk_name = "来来",},
	[101140805] = { talk_id = 101140805, group_id = 1011408, type = 1, next_id = 101140806, talk_content = "是真恶的话我们只能交给巡卫，但假如是能改过的恶的话，维安司也会一起为补过而努力。", talk_name = "来来",},
	[101140806] = { talk_id = 101140806, group_id = 1011408, type = 1, talk_content = "这就是现在维安司要做的事情。", talk_name = "来来",},
	[101140901] = { talk_id = 101140901, group_id = 1011409, type = 4, next_id = 101140902, aside = "<color=#FFEBA2>来来：</color>突然想起今天出门的时候忘带了东西。", talk_content = "突然想起今天出门的时候忘带了东西。", talk_name = "来来",},
	[101140902] = { talk_id = 101140902, group_id = 1011409, type = 4, next_id = 101140903, aside = "<color=#FFEBA2><nick_name>：</color>忘带了啥？", talk_content = "忘带了啥？", talk_name = "<nick_name>",},
	[101140903] = { talk_id = 101140903, group_id = 1011409, type = 4, next_id = 101140904, aside = "<color=#FFEBA2>来来：</color>正好公园里面有些噬脉生物。", talk_content = "正好公园里面有些噬脉生物。", talk_name = "来来",},
	[101140904] = { talk_id = 101140904, group_id = 1011409, type = 4, aside = "<color=#FFEBA2>来来：</color>先揍它们一顿吧，青乌。", talk_content = "先揍它们一顿吧，青乌。", talk_name = "来来",},
	[101141001] = { talk_id = 101141001, group_id = 1011410, type = 4, next_id = 101141002, aside = "<color=#FFEBA2><nick_name>：</color>就连这种地方也有噬脉生物。", talk_content = "就连这种地方也有噬脉生物。", talk_name = "<nick_name>",},
	[101141002] = { talk_id = 101141002, group_id = 1011410, type = 4, next_id = 101141003, aside = "<color=#FFEBA2>来来：</color>最近噬脉生物出现的的确有些频繁，青门那边也有通告……", talk_content = "最近噬脉生物出现的的确有些频繁，青门那边也有通告……", talk_name = "来来",},
	[101141003] = { talk_id = 101141003, group_id = 1011410, type = 4, next_id = 101141004, aside = "<color=#FFEBA2>来来：</color>不过危机也是机遇，青乌，有看见那边的东西吧。", talk_content = "不过危机也是机遇，青乌，有看见那边的东西吧。", talk_name = "来来",},
	[101141004] = { talk_id = 101141004, group_id = 1011410, type = 4, next_id = 101141005, aside = "<color=#FFEBA2><nick_name>：</color>佩从玉吗？", talk_content = "佩从玉吗？", talk_name = "<nick_name>",},
	[101141005] = { talk_id = 101141005, group_id = 1011410, type = 4, aside = "<color=#FFEBA2>来来：</color>嗯，去把那个能量凝聚成佩从玉吧。", talk_content = "嗯，去把那个能量凝聚成佩从玉吧。", talk_name = "来来",},
	[101141101] = { talk_id = 101141101, group_id = 1011411, type = 4, next_id = 101141102, aside = "<color=#FFEBA2><nick_name>：</color>你忘带的该不会是佩从玉吧。", talk_content = "你忘带的该不会是佩从玉吧。", talk_name = "<nick_name>",},
	[101141102] = { talk_id = 101141102, group_id = 1011411, type = 4, next_id = 101141103, aside = "<color=#FFEBA2>来来：</color>哼哼，你还挺聪明的嘛。", talk_content = "哼哼，你还挺聪明的嘛。", talk_name = "来来",},
	[101141103] = { talk_id = 101141103, group_id = 1011411, type = 4, next_id = 101141104, aside = "<color=#FFEBA2>来来：</color>这样战斗准备就齐全了，我们去扫荡目标的帮派吧。", talk_content = "这样战斗准备就齐全了，我们去扫荡目标的帮派吧。", talk_name = "来来",},
	[101141104] = { talk_id = 101141104, group_id = 1011411, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>这拦也拦不住……", talk_content = "这拦也拦不住……", talk_name = "<nick_name>",},
	[101141201] = { talk_id = 101141201, group_id = 1011412, type = 4, next_id = 101141202, aside = "<color=#FFEBA2>帮派成员：</color>你们是什么人！", talk_content = "你们是什么人！", talk_name = "帮派成员",},
	[101141202] = { talk_id = 101141202, group_id = 1011412, type = 4, next_id = 101141203, aside = "<color=#FFEBA2>来来：</color>帝江维安司，来消灭罪恶了！", talk_content = "帝江维安司，来消灭罪恶了！", talk_name = "来来",},
	[101141203] = { talk_id = 101141203, group_id = 1011412, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>……我就不用报名号了吧。", talk_content = "……我就不用报名号了吧。", talk_name = "<nick_name>",},
	[101141301] = { talk_id = 101141301, group_id = 1011413, type = 1, next_id = 101141302, fade_in = 1, fade_out = true, hide_player = true, talk_content = "你不去捉那些混混吗？", talk_name = "<nick_name>",},
	[101141302] = { talk_id = 101141302, group_id = 1011413, type = 2, child_type = 21, options = {{"（继续追问）",101141303,},{"推进话题",101141308,},}, talk_content = "这些人教训一顿应该就差不多了，要是再犯也就是再揍一顿的事情而已。", talk_name = "来来",},
	[101141303] = { talk_id = 101141303, group_id = 1011413, type = 3, next_id = 101141304, icon = "interaction_4", talk_content = "所以不是每个人都会和我委托人一样处理吗？", talk_name = "<nick_name>",},
	[101141304] = { talk_id = 101141304, group_id = 1011413, type = 3, next_id = 101141305, talk_content = "毕竟我又不能分身，随机应变才是本身，而且光是能让捣毁他们的据点就已经算是大功一件了。", talk_name = "来来",},
	[101141305] = { talk_id = 101141305, group_id = 1011413, type = 3, next_id = 101141306, talk_content = "大功……你们又不是官方组织，打击罪恶到底能获得什么利益。", talk_name = "<nick_name>",},
	[101141306] = { talk_id = 101141306, group_id = 1011413, type = 3, next_id = 101141307, talk_content = "要说的话其实是维安司背后有些支援者，所以我才能把这个当成职业。", talk_name = "来来",},
	[101141307] = { talk_id = 101141307, group_id = 1011413, type = 3, next_id = 101141308, talk_content = "（感觉城里面有着各种各样的关系。）", talk_name = "<nick_name>",},
	[101141308] = { talk_id = 101141308, group_id = 1011413, type = 1, next_id = 101141309, icon = "interaction_4", talk_content = "还是说回正题吧，来来，你有看见我要找的东西吗？", talk_name = "<nick_name>",},
	[101141309] = { talk_id = 101141309, group_id = 1011413, type = 1, next_id = 101141310, talk_content = "佩从玉是吧，刚刚跑掉的混混应该是没带走那种东西。", talk_name = "来来",},
	[101141310] = { talk_id = 101141310, group_id = 1011413, type = 1, next_id = 101141311, talk_content = "所以接下来就在这四处找找就好，像是那边的箱子不是挺像会放贵重品的？", talk_name = "来来",},
	[101141311] = { talk_id = 101141311, group_id = 1011413, type = 1, talk_content = "的确如此。", talk_name = "<nick_name>",},
	[101141401] = { talk_id = 101141401, group_id = 1011414, type = 4, next_id = 101141402, aside = "<color=#FFEBA2><nick_name>：</color>佩从玉，和些其他的东西。", talk_content = "佩从玉，和些其他的东西。", talk_name = "<nick_name>",},
	[101141402] = { talk_id = 101141402, group_id = 1011414, type = 4, next_id = 101141403, aside = "<color=#FFEBA2>来来：</color>都是用来战斗的东西……这群人该不会是准备做什么武斗大帮吧。", talk_content = "都是用来战斗的东西……这群人该不会是准备做什么武斗大帮吧。", talk_name = "来来",},
	[101141403] = { talk_id = 101141403, group_id = 1011414, type = 4, next_id = 101141404, aside = "<color=#FFEBA2>来来：</color>等等，这把枪看起来不错，嗯，拿上看看吧。", talk_content = "等等，这把枪看起来不错，嗯，拿上看看吧。", talk_name = "来来",},
	[101141404] = { talk_id = 101141404, group_id = 1011414, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>喂。", talk_content = "喂。", talk_name = "<nick_name>",},
	[101141501] = { talk_id = 101141501, group_id = 1011415, type = 1, next_id = 101141502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "青乌，你知道你的委托人是窃贼吗？", talk_name = "来来",},
	[101141502] = { talk_id = 101141502, group_id = 1011415, type = 2, options = {{"（沉默）",101141503,},{"我知道他是。",101141504,},}, talk_content = "呃……，不是这样的。", talk_name = "陈塔",},
	[101141503] = { talk_id = 101141503, group_id = 1011415, type = 3, next_id = 101141505, talk_content = "你这表情就表示你也知道了是吧。", talk_name = "来来",},
	[101141504] = { talk_id = 101141504, group_id = 1011415, type = 3, next_id = 101141505, talk_content = "……青乌，该不会一段时间不见你沦落到靠偷抢为生了吧？", talk_name = "来来",},
	[101141505] = { talk_id = 101141505, group_id = 1011415, type = 1, next_id = 101141506, talk_content = "我想要问清楚一些事情，毕竟我的“老板”也让我这么做。", talk_name = "<nick_name>",},
	[101141506] = { talk_id = 101141506, group_id = 1011415, type = 1, talk_content = "行，就信你一次，要问什么就问清楚吧。", talk_name = "来来",},
	[101141601] = { talk_id = 101141601, group_id = 1011416, type = 1, next_id = 101141602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "先和你说声对不起，你们的对话我都听见了。", talk_name = "来来",},
	[101141602] = { talk_id = 101141602, group_id = 1011416, type = 2, options = {{"我没想到会再见到你。",101141603,},{"话说你管这事干啥？",101141606,},}, talk_content = "真没想到这年头还有这么不干脆的小贼，我看你是跑了一趟无用功了对吧。", talk_name = "来来",},
	[101141603] = { talk_id = 101141603, group_id = 1011416, type = 3, next_id = 101141604, talk_content = "我倒是刚刚逼问那人的时候就想到了，所以这人能交给我们维安司吗？", talk_name = "来来",},
	[101141604] = { talk_id = 101141604, group_id = 1011416, type = 3, next_id = 101141605, talk_content = "维安司到底是什么组织？", talk_name = "<nick_name>",},
	[101141605] = { talk_id = 101141605, group_id = 1011416, type = 3, next_id = 101141607, talk_content = "维安司的目的是建立新的秩序，而要达成这个目的最基本的就是解决每一件可能危害天月城的小事。", talk_name = "来来",},
	[101141606] = { talk_id = 101141606, group_id = 1011416, type = 3, next_id = 101141607, talk_content = "虽然不是巡卫，但维安司也是以维护天月城和平为目的的组织，路见不平对于我们来说是家常便饭。", talk_name = "来来",},
	[101141607] = { talk_id = 101141607, group_id = 1011416, type = 1, next_id = 101141608, talk_content = "所以维安司并不是什么官方组织吗？", talk_name = "<nick_name>",},
	[101141608] = { talk_id = 101141608, group_id = 1011416, type = 1, next_id = 101141609, talk_content = "嗯，严格上不算。", talk_name = "来来",},
	[101141609] = { talk_id = 101141609, group_id = 1011416, type = 2, options = {{"我还没答应。",101141610,},{"我只是去取回委托物。",101141611,},}, talk_content = "话说从你们刚刚的对话我听见了一个有趣的事情，你是要准备捣毁一个新兴帮派的窝点对吧？", talk_name = "来来",},
	[101141610] = { talk_id = 101141610, group_id = 1011416, type = 3, next_id = 101141612, talk_content = "那现在答应下来就行，小恶引出大恶可是经典桥段，接下来也算上我吧。", talk_name = "来来",},
	[101141611] = { talk_id = 101141611, group_id = 1011416, type = 3, next_id = 101141612, talk_content = "用指头想都知道不可能那么简单就能拿回来，捣毁帮派可是大功，接下来也算上我吧。", talk_name = "来来",},
	[101141612] = { talk_id = 101141612, group_id = 1011416, type = 1, next_id = 101141613, talk_content = "“算上你”？", talk_name = "<nick_name>",},
	[101141613] = { talk_id = 101141613, group_id = 1011416, type = 1, talk_content = "是啊，接下来我两就组队行动吧。", talk_name = "来来",},
	[101150101] = { talk_id = 101150101, group_id = 1011501, type = 1, next_id = 101150102, fade_in = 1, fade_out = true, hide_player = true, talk_content = "既然玉也拿回来了，接下来直接去找这玉原本的主人吧。", talk_name = "来来",},
	[101150102] = { talk_id = 101150102, group_id = 1011501, type = 1, next_id = 101150103, talk_content = "陈塔呢？", talk_name = "<nick_name>",},
	[101150103] = { talk_id = 101150103, group_id = 1011501, type = 1, next_id = 101150104, talk_content = "我的同伴刚刚已经带他回司了，他那边留言说是希望由我们来转交。", talk_name = "来来",},
	[101150104] = { talk_id = 101150104, group_id = 1011501, type = 1, next_id = 101150105, talk_content = "按陈塔所说这位老人基本都会在公园里望天，我们四处找找吧。", talk_name = "来来",},
	[101150105] = { talk_id = 101150105, group_id = 1011501, type = 1, talk_content = "望天？", talk_name = "<nick_name>",},
	[101150201] = { talk_id = 101150201, group_id = 1011502, type = 4, next_id = 101150202, aside = "<color=#FFEBA2><nick_name>：</color>这里有不少人，我们要怎么找到原主？", talk_content = "这里有不少人，我们要怎么找到原主？", talk_name = "<nick_name>",},
	[101150202] = { talk_id = 101150202, group_id = 1011502, type = 4, next_id = 101150203, aside = "<color=#FFEBA2>来来：</color>按陈塔所说那位老人基本每天都穿着蓝衣。", talk_content = "按陈塔所说那位老人基本每天都穿着蓝衣。", talk_name = "来来",},
	[101150203] = { talk_id = 101150203, group_id = 1011502, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>蓝衣是吧。", talk_content = "蓝衣是吧。", talk_name = "<nick_name>",},
	[101150301] = { talk_id = 101150301, group_id = 1011503, type = 1, next_id = 101150302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "等等，这位浑身上下都没有蓝色不是吗？", talk_name = "来来",},
	[101150302] = { talk_id = 101150302, group_id = 1011503, type = 1, next_id = 101150303, talk_content = "的确。", talk_name = "<nick_name>",},
	[101150303] = { talk_id = 101150303, group_id = 1011503, type = 1, talk_content = "？", talk_name = "老人",},
	[101150401] = { talk_id = 101150401, group_id = 1011504, type = 1, next_id = 101150402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "老人家你好，请问你最近有发现什么东西不见了吗？", talk_name = "来来",},
	[101150402] = { talk_id = 101150402, group_id = 1011504, type = 1, next_id = 101150403, talk_content = "我的老伴……", talk_name = "老人",},
	[101150403] = { talk_id = 101150403, group_id = 1011504, type = 1, next_id = 101150404, talk_content = "……抱歉。", talk_name = "来来",},
	[101150404] = { talk_id = 101150404, group_id = 1011504, type = 1, next_id = 101150405, talk_content = "他又不知道去哪下棋了，那个混账老头。", talk_name = "老人",},
	[101150405] = { talk_id = 101150405, group_id = 1011504, type = 1, talk_content = "……", talk_name = "<nick_name>",},
	[101150501] = { talk_id = 101150501, group_id = 1011505, type = 1, next_id = 101150502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，请问这个是你丢的东西吗？", talk_name = "<nick_name>",},
	[101150502] = { talk_id = 101150502, group_id = 1011505, type = 1, next_id = 101150503, talk_content = "……", talk_name = "老人",},
	[101150503] = { talk_id = 101150503, group_id = 1011505, type = 1, next_id = 101150504, talk_content = "你好？", talk_name = "<nick_name>",},
	[101150504] = { talk_id = 101150504, group_id = 1011505, type = 1, next_id = 101150505, talk_content = "……", talk_name = "老人",},
	[101150505] = { talk_id = 101150505, group_id = 1011505, type = 1, next_id = 101150506, talk_content = "这是怎么回事？", talk_name = "<nick_name>",},
	[101150506] = { talk_id = 101150506, group_id = 1011505, type = 1, next_id = 101150507, talk_content = "你是打算把东西硬塞给我然后收我钱对吧，我可不会上这种档！", talk_name = "老人",},
	[101150507] = { talk_id = 101150507, group_id = 1011505, type = 1, next_id = 101150508, talk_content = "……", talk_name = "<nick_name>",},
	[101150508] = { talk_id = 101150508, group_id = 1011505, type = 1, talk_content = "被当诈骗犯了呢。", talk_name = "来来",},
	[101150601] = { talk_id = 101150601, group_id = 1011506, type = 4, aside = "<color=#FFEBA2>来来：</color>那边好像有位刚来的老人，我们去问问？", talk_content = "那边好像有位刚来的老人，我们去问问？", talk_name = "来来",},
	[101150701] = { talk_id = 101150701, group_id = 1011507, type = 1, next_id = 101150702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "老人家你好，这个带”松“字的佩从玉是你的东西吗？", talk_name = "来来",},
	[101150702] = { talk_id = 101150702, group_id = 1011507, type = 2, options = {{"阿松是指佩从玉？",101150703,},{"真是你的？",101150704,},}, talk_content = "我没想到还会有见到阿松那天。", talk_name = "老人",},
	[101150703] = { talk_id = 101150703, group_id = 1011507, type = 3, next_id = 101150705, talk_content = "是的，我给里面的佩从起名叫阿松。", talk_name = "老人",},
	[101150704] = { talk_id = 101150704, group_id = 1011507, type = 3, next_id = 101150705, talk_content = "是的，阿松是我的佩从玉。", talk_name = "老人",},
	[101150705] = { talk_id = 101150705, group_id = 1011507, type = 1, next_id = 101150706, talk_content = "那这下就可以物归原主了，这块佩从玉就还给老先生你吧。", talk_name = "来来",},
	[101150706] = { talk_id = 101150706, group_id = 1011507, type = 2, options = {{"你知道陈塔？",101150707,},{"他改过自新了。",101150708,},}, talk_content = "唔，阿松是你们带回来的……难道说陈塔出了什么事吗？", talk_name = "老人",},
	[101150707] = { talk_id = 101150707, group_id = 1011507, type = 3, next_id = 101150709, talk_content = "当然知道，阿松是我特意让他拿走的，那孩子一脸想要的样子，哎。", talk_name = "老人",},
	[101150708] = { talk_id = 101150708, group_id = 1011507, type = 3, next_id = 101150709, talk_content = "这样……我本以为这能让他好过些。", talk_name = "老人",},
	[101150709] = { talk_id = 101150709, group_id = 1011507, type = 1, next_id = 101150710, talk_content = "所以说老先生你是故意让佩从玉被偷走的吗？", talk_name = "来来",},
	[101150710] = { talk_id = 101150710, group_id = 1011507, type = 1, next_id = 101150711, talk_content = "毕竟阿松也在这天月城上陪我过了五十年了，自从那天得知自己没有出城资格以后，我的人生就只剩下了阿松。", talk_name = "老人",},
	[101150711] = { talk_id = 101150711, group_id = 1011507, type = 1, next_id = 101150712, talk_content = "就算老了退休了，也还是每日带着阿松来看看真正的大地，想象自己真正能脚踏实地的样子。", talk_name = "老人",},
	[101150712] = { talk_id = 101150712, group_id = 1011507, type = 1, next_id = 101150713, talk_content = "出城资格是……？", talk_name = "<nick_name>",},
	[101150713] = { talk_id = 101150713, group_id = 1011507, type = 1, next_id = 101150714, talk_content = "你连这个也不知道吗，天月城的出入管制是当下最严格的城市，对出入有一套特殊的审核基准。", talk_name = "来来",},
	[101150714] = { talk_id = 101150714, group_id = 1011507, type = 1, next_id = 101150715, talk_content = "怎么审核？", talk_name = "<nick_name>",},
	[101150715] = { talk_id = 101150715, group_id = 1011507, type = 1, next_id = 101150716, talk_content = "抽血，然后经过一套谁也不知道的检查，最后落到我们手上的就只有一张通过或者是不通过的证明。", talk_name = "来来",},
	[101150716] = { talk_id = 101150716, group_id = 1011507, type = 1, next_id = 101150717, talk_content = "证明虽薄，但那就是能决定天月城人一生的事情。", talk_name = "来来",},
	[101150717] = { talk_id = 101150717, group_id = 1011507, type = 1, next_id = 101150718, talk_content = "不通过的人就会和我一样，冠上禁闭者的名号，永生不得踏出天月城半步。", talk_name = "老人",},
	[101150718] = { talk_id = 101150718, group_id = 1011507, type = 1, next_id = 101150719, talk_content = "踏出天月城……", talk_name = "<nick_name>",},
	[101150719] = { talk_id = 101150719, group_id = 1011507, type = 1, next_id = 101150720, talk_content = "咳咳。", talk_name = "老人",},
	[101150720] = { talk_id = 101150720, group_id = 1011507, type = 1, next_id = 101150721, talk_content = "老先生？", talk_name = "来来",},
	[101150721] = { talk_id = 101150721, group_id = 1011507, type = 1, next_id = 101150722, talk_content = "只是话说得有些多了，这样，阿松请你们帮我交给陈塔，跟他说这是我送他的。", talk_name = "老人",},
	[101150722] = { talk_id = 101150722, group_id = 1011507, type = 1, next_id = 101150723, talk_content = "请不要让他接下来过得太难堪，可以吧？", talk_name = "老人",},
	[101150723] = { talk_id = 101150723, group_id = 1011507, type = 1, next_id = 101150724, talk_content = "这……", talk_name = "来来",},
	[101150724] = { talk_id = 101150724, group_id = 1011507, type = 1, next_id = 101150725, talk_content = "比起我这种禁闭者，还是让阿松跟着能到地上去的人吧。", talk_name = "老人",},
	[101150725] = { talk_id = 101150725, group_id = 1011507, type = 1, talk_content = "总有一天，能到地上去就好了。", talk_name = "老人",},
	[101150801] = { talk_id = 101150801, group_id = 1011508, type = 1, next_id = 101150802, talk_content = "总有一天，能到地上去就好了。", talk_name = "老人",},
	[101150802] = { talk_id = 101150802, group_id = 1011508, type = 1, talk_content = "（不要打扰他吧。）", talk_name = "<nick_name>",},
	[101150901] = { talk_id = 101150901, group_id = 1011509, type = 4, next_id = 101150902, aside = "<color=#FFEBA2>来来：</color>就按那位老先生所说的，我接下来要回维安司了。", talk_content = "就按那位老先生所说的，我接下来要回维安司了。", talk_name = "来来",},
	[101150902] = { talk_id = 101150902, group_id = 1011509, type = 4, next_id = 101150903, aside = "<color=#FFEBA2><nick_name>：</color>那就在这里分道扬镳了？", talk_content = "那就在这里分道扬镳了？", talk_name = "<nick_name>",},
	[101150903] = { talk_id = 101150903, group_id = 1011509, type = 4, aside = "<color=#FFEBA2>来来：</color>只是暂别而已，你的电话我留着了，有事我们再联络吧。", talk_content = "只是暂别而已，你的电话我留着了，有事我们再联络吧。", talk_name = "来来",},
	[101151101] = { talk_id = 101151101, group_id = 1011511, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>委托办完就直接回茶室吧。", talk_content = "委托办完就直接回茶室吧。", talk_name = "<nick_name>",},
	[101160101] = { talk_id = 101160101, group_id = 1011601, type = 1, next_id = 101160102, fade_in = 1, fade_out = true, hide_player = true, talk_content = "委托完成得不错，青乌。", talk_name = "离歌",},
	[101160102] = { talk_id = 101160102, group_id = 1011601, type = 1, next_id = 101160103, talk_content = "我只觉得自己被人玩弄在鼓掌之间。", talk_name = "<nick_name>",},
	[101160103] = { talk_id = 101160103, group_id = 1011601, type = 1, next_id = 101160104, talk_content = "离歌，为什么一开始不告诉我真相。", talk_name = "<nick_name>",},
	[101160104] = { talk_id = 101160104, group_id = 1011601, type = 1, next_id = 101160105, talk_content = "我只是怕你丢失追求真相的心而已。", talk_name = "离歌",},
	[101160105] = { talk_id = 101160105, group_id = 1011601, type = 1, next_id = 101160106, talk_content = "我最想追求的是我的记忆，不过……", talk_name = "<nick_name>",},
	[101160106] = { talk_id = 101160106, group_id = 1011601, type = 1, next_id = 101160107, talk_content = "你想问关于禁闭者的事情是吧。", talk_name = "离歌",},
	[101160107] = { talk_id = 101160107, group_id = 1011601, type = 1, next_id = 101160108, talk_content = "那到底是怎么回事，这样的制度真的能存在吗？", talk_name = "<nick_name>",},
	[101160108] = { talk_id = 101160108, group_id = 1011601, type = 1, next_id = 101160109, talk_content = "每个地方有每个地方的风俗，规则自然也是如此，在天月城中，禁闭者的规则已经持续了两百年以上了。", talk_name = "离歌",},
	[101160109] = { talk_id = 101160109, group_id = 1011601, type = 1, next_id = 101160110, talk_content = "据天月城的古籍记载，某天统治天月城的封侯就发布了相关的规则，并开始了最初的审查。", talk_name = "离歌",},
	[101160110] = { talk_id = 101160110, group_id = 1011601, type = 1, next_id = 101160111, talk_content = "然后……第一批的禁闭者就出现了。", talk_name = "离歌",},
	[101160111] = { talk_id = 101160111, group_id = 1011601, type = 1, next_id = 101160112, talk_content = "这期间自然有过反抗，但结果是全数失败，权力加上绝对的力量，这就是这个制度存在到今的原因。", talk_name = "离歌",},
	[101160112] = { talk_id = 101160112, group_id = 1011601, type = 1, next_id = 101160113, talk_content = "所以说不通过那个审核我应该也是不能离开天月城的？", talk_name = "<nick_name>",},
	[101160113] = { talk_id = 101160113, group_id = 1011601, type = 1, next_id = 101160114, talk_content = "就是这样。", talk_name = "离歌",},
	[101160114] = { talk_id = 101160114, group_id = 1011601, type = 1, next_id = 101160115, talk_content = "……很好，除了记忆以外又多一件烦心事了。", talk_name = "<nick_name>",},
	[101160115] = { talk_id = 101160115, group_id = 1011601, type = 1, next_id = 101160116, talk_content = "要忘记烦心事的最好方式就是劳动，青乌，到搬货的时间了。", talk_name = "离歌",},
	[101160116] = { talk_id = 101160116, group_id = 1011601, type = 1, next_id = 101160117, talk_content = "……我明白了。", talk_name = "<nick_name>",},
	[101160117] = { talk_id = 101160117, group_id = 1011601, type = 1, talk_content = "对了，这次委托的报酬也会在货物搬完之后一起打给你。", talk_name = "离歌",},
	[101160201] = { talk_id = 101160201, group_id = 1011602, type = 4, next_id = 101160202, aside = "<color=#FFEBA2><nick_name>：</color>一直放在哪里的应该就是离歌说的货了吧。", talk_content = "一直放在哪里的应该就是离歌说的货了吧。", talk_name = "<nick_name>",},
	[101160202] = { talk_id = 101160202, group_id = 1011602, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>搬运……我记得佩从应该能轻松办到这件事。", talk_content = "搬运……我记得佩从应该能轻松办到这件事。", talk_name = "<nick_name>",},
