	[101160501] = { talk_id = 101160501, group_id = 1011605, type = 4, next_id = 101160502, aside = "<color=#FFEBA2><nick_name>：</color>只要把货都堆在外面之后就是收货人的事了。", talk_content = "只要把货都堆在外面之后就是收货人的事了。", talk_name = "<nick_name>",},
	[101160502] = { talk_id = 101160502, group_id = 1011605, type = 4, next_id = 101160503, aside = "<color=#FFEBA2><nick_name>：</color>话说回来，刚刚把货物搬开以后墙上的是龙的画像。", talk_content = "话说回来，刚刚把货物搬开以后墙上的是龙的画像。", talk_name = "<nick_name>",},
	[101160503] = { talk_id = 101160503, group_id = 1011605, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>通体黑色的龙……应该不会是离歌的真貌吧。", talk_content = "通体黑色的龙……应该不会是离歌的真貌吧。", talk_name = "<nick_name>",},
