	[101170201] = { talk_id = 101170201, group_id = 1011702, type = 1, next_id = 101170202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，你应该向“离歌”发了委托对吧？", talk_name = "<nick_name>",},
	[101170202] = { talk_id = 101170202, group_id = 1011702, type = 2, next_id = 101170203, options = {{"出发去哪？",101170203,},{"也太心急了吧？",101170204,},}, talk_content = "终于来了，我们赶紧出发吧！", talk_name = "委托人",},
	[101170203] = { talk_id = 101170203, group_id = 1011702, type = 3, talk_content = "当然是去找那个骗子，你老板应该都和你说过了吧！", talk_name = "委托人",},
	[101170204] = { talk_id = 101170204, group_id = 1011702, type = 3, talk_content = "那个江湖郎中每天就摆几小时的摊，晚了就得明天了！", talk_name = "委托人",},
	[101170301] = { talk_id = 101170301, group_id = 1011703, type = 4, next_id = 101170302, aside = "<color=#FFEBA2><nick_name>：</color>看起来是气得没法好好说话了……", talk_content = "看起来是气得没法好好说话了……", talk_name = "<nick_name>",},
	[101170302] = { talk_id = 101170302, group_id = 1011703, type = 4, next_id = 101170303, aside = "<color=#FFEBA2>委托人：</color>那人明明说过我前途似锦，可现在我都被辞退了……", talk_content = "那人明明说过我前途似锦，可现在我都被辞退了……", talk_name = "委托人",},
	[101170303] = { talk_id = 101170303, group_id = 1011703, type = 4, next_id = 101170304, aside = "<color=#FFEBA2>委托人：</color>今天我不把这骗子摊砸了我誓不罢休。", talk_content = "今天我不把这骗子摊砸了我誓不罢休。", talk_name = "委托人",},
	[101170304] = { talk_id = 101170304, group_id = 1011703, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（我可不想惹什么麻烦。）", talk_content = "（我可不想惹什么麻烦。）", talk_name = "<nick_name>",},
	[101170401] = { talk_id = 101170401, group_id = 1011704, type = 4, next_id = 101170402, aside = "<color=#FFEBA2>委托人：</color>我记得那摊应该就在这附近，你也来一起找找……", talk_content = "我记得那摊应该就在这附近，你也来一起找找……", talk_name = "委托人",},
	[101170402] = { talk_id = 101170402, group_id = 1011704, type = 4, next_id = 101170403, aside = "<color=#FFEBA2>委托人：</color>呃，哇……救，救命！", talk_content = "呃，哇……救，救命！", talk_name = "委托人",},
	[101170403] = { talk_id = 101170403, group_id = 1011704, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>城市里面随处都可以出现怪物这怎么想都不对吧！", talk_content = "城市里面随处都可以出现怪物这怎么想都不对吧！", talk_name = "<nick_name>",},
	[101170501] = { talk_id = 101170501, group_id = 1011705, type = 4, next_id = 101170502, aside = "<color=#FFEBA2><nick_name>：</color>应该就这些了吧，委托人……", talk_content = "应该就这些了吧，委托人……", talk_name = "<nick_name>",},
	[101170502] = { talk_id = 101170502, group_id = 1011705, type = 4, next_id = 101170503, aside = "<color=#FFEBA2><nick_name>：</color>委托人呢？", talk_content = "委托人呢？", talk_name = "<nick_name>",},
	[101170503] = { talk_id = 101170503, group_id = 1011705, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>四处找找吧。", talk_content = "四处找找吧。", talk_name = "<nick_name>",},
	[101170601] = { talk_id = 101170601, group_id = 1011706, type = 4, next_id = 101170602, aside = "<color=#FFEBA2>？？？：</color>那边的脉者，你要找人是吧？", talk_content = "那边的脉者，你要找人是吧？", talk_name = "？？？",},
	[101170602] = { talk_id = 101170602, group_id = 1011706, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（那个摊……难道就是委托人口中那个？）", talk_content = "（那个摊……难道就是委托人口中那个？）", talk_name = "<nick_name>",},
	[101180101] = { talk_id = 101180101, group_id = 1011801, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"说明来意",101180102,},{"告知",101180103,},}, talk_content = "你好，我的名字叫采桑子，敢问大名是？", talk_name = "采桑子",},
	[101180102] = { talk_id = 101180102, group_id = 1011801, type = 3, next_id = 101180105, talk_content = "青乌是吧，那位想来投诉的人刚刚被噬脉生物吓走了，看他那神色一时半会是恢复不过来了吧。", talk_name = "采桑子",},
	[101180103] = { talk_id = 101180103, group_id = 1011801, type = 3, next_id = 101180104, talk_content = "<nick_name>，也可以叫青乌，我记得了，顺带一提，你的委托人刚刚被噬脉生物吓得不轻。", talk_name = "采桑子",},
	[101180104] = { talk_id = 101180104, group_id = 1011801, type = 3, next_id = 101180105, talk_content = "现在大概跑老远了吧。", talk_name = "采桑子",},
	[101180105] = { talk_id = 101180105, group_id = 1011801, type = 1, next_id = 101180106, talk_content = "那我得先去追他才行。", talk_name = "<nick_name>",},
	[101180106] = { talk_id = 101180106, group_id = 1011801, type = 1, next_id = 101180107, talk_content = "稍等，能请你顺带也接上我的委托吗？", talk_name = "采桑子",},
	[101180107] = { talk_id = 101180107, group_id = 1011801, type = 1, next_id = 101180108, talk_content = "什么委托？", talk_name = "<nick_name>",},
	[101180108] = { talk_id = 101180108, group_id = 1011801, type = 1, next_id = 101180109, talk_content = "我估计带你来的那位委托人也已经和你说了我是个骗子之类的事情了吧。", talk_name = "采桑子",},
	[101180109] = { talk_id = 101180109, group_id = 1011801, type = 1, next_id = 101180110, talk_content = "但实际上这并非我意，或者换句话说，马有失蹄，人有失手。", talk_name = "采桑子",},
	[101180110] = { talk_id = 101180110, group_id = 1011801, type = 1, next_id = 101180111, talk_content = "你的意思是你真能“咨询一切”？", talk_name = "<nick_name>",},
	[101180111] = { talk_id = 101180111, group_id = 1011801, type = 2, options = {{"是要我处理掉来投诉的人吗？",101180112,},{"我可不想帮骗子。",101180113,},}, talk_content = "毕竟摊位是这么宣传的，说回委托吧，我希望青乌你能替我做点售后服务。", talk_name = "采桑子",},
	[101180112] = { talk_id = 101180112, group_id = 1011801, type = 3, next_id = 101180114, talk_content = "不不不，我这又不是道上生意。", talk_name = "采桑子",},
	[101180113] = { talk_id = 101180113, group_id = 1011801, type = 3, next_id = 101180114, talk_content = "我也不会让你骗人，反而希望你能够帮人。", talk_name = "采桑子",},
	[101180114] = { talk_id = 101180114, group_id = 1011801, type = 1, next_id = 101180115, talk_content = "除开你的委托人，我这里还有两位让人放不下心的客人。", talk_name = "采桑子",},
	[101180115] = { talk_id = 101180115, group_id = 1011801, type = 1, next_id = 101180116, talk_content = "为了减少投诉，可以请你分别去看看他们，然后视情况出手帮一下吗？", talk_name = "采桑子",},
	[101180116] = { talk_id = 101180116, group_id = 1011801, type = 1, next_id = 101180117, talk_content = "……也就是说让我给你兜底？", talk_name = "<nick_name>",},
	[101180117] = { talk_id = 101180117, group_id = 1011801, type = 1, next_id = 101180118, talk_content = "说的直白一点是这样，我在这摆摊也有一段时日，赚的也还算可以。", talk_name = "采桑子",},
	[101180118] = { talk_id = 101180118, group_id = 1011801, type = 2, options = {{"说说看？",101180119,},}, talk_content = "只要青乌你愿意的话……对了，出于好奇我想问你一个问题。", talk_name = "采桑子",},
	[101180119] = { talk_id = 101180119, group_id = 1011801, type = 3, next_id = 101180120, talk_content = "青乌你为何用以前人的名字当称号，是有什么讲究吗？", talk_name = "采桑子",},
	[101180120] = { talk_id = 101180120, group_id = 1011801, type = 1, next_id = 101180121, talk_content = "……！", talk_name = "<nick_name>",},
	[101180121] = { talk_id = 101180121, group_id = 1011801, type = 1, next_id = 101180122, talk_content = "你刚刚说的是什么意思，以前的名字指的就是青乌没错吧，这个以前到底……", talk_name = "<nick_name>",},
	[101180122] = { talk_id = 101180122, group_id = 1011801, type = 1, next_id = 101180123, talk_content = "看来报酬可以更丰厚一些了，我的部分收入搭上有关于“青乌”的事情。", talk_name = "采桑子",},
	[101180123] = { talk_id = 101180123, group_id = 1011801, type = 2, options = {{"……我要考虑一下。",101180124,},{"我未必能帮到他们。",101180125,},}, talk_content = "这两者就是这次委托的报酬，如何？", talk_name = "采桑子",},
	[101180124] = { talk_id = 101180124, group_id = 1011801, type = 3, talk_content = "考虑就是有意了，信息我先给你，至于做不做就你自己定吧。", talk_name = "采桑子",},
	[101180125] = { talk_id = 101180125, group_id = 1011801, type = 3, talk_content = "重要的是过程而不是结果，曾就拜托你了啊，青乌。", talk_name = "采桑子",},
	[101180201] = { talk_id = 101180201, group_id = 1011802, type = 4, next_id = 101180202, aside = "<color=#FFEBA2>离歌：</color>我没想到能从一个江湖郎中口中听见“青乌”这两个字。", call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "我没想到能从一个江湖郎中口中听见“青乌”这两个字。", talk_name = "离歌",},
	[101180202] = { talk_id = 101180202, group_id = 1011802, type = 4, next_id = 101180203, aside = "<color=#FFEBA2><nick_name>：</color>我也没想到你是每时每刻都在偷听。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "我也没想到你是每时每刻都在偷听。", talk_name = "<nick_name>",},
	[101180203] = { talk_id = 101180203, group_id = 1011802, type = 4, next_id = 101180204, aside = "<color=#FFEBA2>离歌：</color>这是我对你的关心，青乌。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "这是我对你的关心，青乌。", talk_name = "离歌",},
	[101180204] = { talk_id = 101180204, group_id = 1011802, type = 4, next_id = 101180205, aside = "<color=#FFEBA2>离歌：</color>总之委托人我会另外让人去处理，你就去帮这位郎中做售后吧。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "总之委托人我会另外让人去处理，你就去帮这位郎中做售后吧。", talk_name = "离歌",},
	[101180205] = { talk_id = 101180205, group_id = 1011802, type = 4, next_id = 101180206, aside = "<color=#FFEBA2><nick_name>：</color>……所以他说的是真的吗，我是“以前的人”。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "……所以他说的是真的吗，我是“以前的人”。", talk_name = "<nick_name>",},
	[101180206] = { talk_id = 101180206, group_id = 1011802, type = 4, next_id = 101180207, aside = "<color=#FFEBA2>离歌：</color>我不知道，直到刚才我都还在考虑该从哪开始调查你的事情。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "我不知道，直到刚才我都还在考虑该从哪开始调查你的事情。", talk_name = "离歌",},
	[101180207] = { talk_id = 101180207, group_id = 1011802, type = 4, next_id = 101180208, aside = "<color=#FFEBA2><nick_name>：</color>我还以为你什么都知道呢。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "我还以为你什么都知道呢。", talk_name = "<nick_name>",},
	[101180208] = { talk_id = 101180208, group_id = 1011802, type = 4, aside = "<color=#FFEBA2>离歌：</color>我也希望是这样。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "我也希望是这样。", talk_name = "离歌",},
	[101180301] = { talk_id = 101180301, group_id = 1011803, type = 2, options = {{"和我详细说说需要“售后”的人",101180302,},{"关于“青乌”",101180307,},}, talk_content = "见到人以后你直接说你是我的售后服务就好。", talk_name = "采桑子",},
	[101180302] = { talk_id = 101180302, group_id = 1011803, type = 3, next_id = 101180303, talk_content = "我给你的信息上都有写，第一位是个脉灵研究员，先前来我这咨询事业前程。", talk_name = "采桑子",},
	[101180303] = { talk_id = 101180303, group_id = 1011803, type = 3, next_id = 101180304, talk_content = "而第二位则是一个学习不太好，但希望能在学业上有所进步的学生。", talk_name = "采桑子",},
	[101180304] = { talk_id = 101180304, group_id = 1011803, type = 3, next_id = 101180305, talk_content = "我的职业你也明白，是一位带给别人希望的咨询师，所以我都给与了他们正面的回复。", talk_name = "采桑子",},
	[101180305] = { talk_id = 101180305, group_id = 1011803, type = 3, next_id = 101180306, talk_content = "这不就是骗吗？", talk_name = "<nick_name>",},
	[101180306] = { talk_id = 101180306, group_id = 1011803, type = 3, talk_content = "只是需要些售后而已，就交给你了。", talk_name = "采桑子",},
	[101180307] = { talk_id = 101180307, group_id = 1011803, type = 3, talk_content = "我的确知道关于些许青乌的事情，等事成以后就告诉你吧。", talk_name = "采桑子",},
	[101180401] = { talk_id = 101180401, group_id = 1011804, type = 4, next_id = 101180402, aside = "<color=#FFEBA2><nick_name>：</color>有关于“青乌”的事情……", talk_content = "有关于“青乌”的事情……", talk_name = "<nick_name>",},
	[101180402] = { talk_id = 101180402, group_id = 1011804, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>目标有两个，按照顺序去找吧。", talk_content = "目标有两个，按照顺序去找吧。", talk_name = "<nick_name>",},
	[101180501] = { talk_id = 101180501, group_id = 1011805, type = 4, next_id = 101180502, aside = "<color=#FFEBA2><nick_name>：</color>第一个人是位脉灵研究员，平时在这附近活动。", talk_content = "第一个人是位脉灵研究员，平时在这附近活动。", talk_name = "<nick_name>",},
	[101180502] = { talk_id = 101180502, group_id = 1011805, type = 4, next_id = 101180503, aside = "<color=#FFEBA2><nick_name>：</color>这里人也不算少，逐个问过去太没有效率了。", talk_content = "这里人也不算少，逐个问过去太没有效率了。", talk_name = "<nick_name>",},
	[101180503] = { talk_id = 101180503, group_id = 1011805, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>……试试用佩从吧。", talk_content = "……试试用佩从吧。", talk_name = "<nick_name>",},
	[101180601] = { talk_id = 101180601, group_id = 1011806, type = 1, next_id = 101180602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "唉，要不行了。", talk_name = "？？？",},
	[101180602] = { talk_id = 101180602, group_id = 1011806, type = 1, next_id = 101180603, talk_content = "你好，我是“咨询师”那边派来的售后的服务。", talk_name = "<nick_name>",},
	[101180603] = { talk_id = 101180603, group_id = 1011806, type = 2, options = {{"说明情况",101180604,},}, talk_content = "咨询师……售后？", talk_name = "？？？",},
	[101180604] = { talk_id = 101180604, group_id = 1011806, type = 3, next_id = 101180605, talk_content = "青乌……我还是第一次知道街边的咨询还有售后服务，对了，我叫沐优。", talk_name = "沐优",},
	[101180605] = { talk_id = 101180605, group_id = 1011806, type = 3, next_id = 101180606, talk_content = "我也是第一次知道，所以你刚刚是为了什么而叹气？", talk_name = "<nick_name>",},
	[101180606] = { talk_id = 101180606, group_id = 1011806, type = 2, options = {{"脉灵是什么东西？",101180607,},{"那现在当不上了吗？",101180612,},}, talk_content = "既然你会来找我就代表梦想是真的离我越来越远了，明明之前去咨询的时候才和我说能当上脉灵权威研究员的。", talk_name = "沐优",},
	[101180607] = { talk_id = 101180607, group_id = 1011806, type = 3, next_id = 101180608, talk_content = "呃，脉灵就是黑白相间，小小的圆圆的可爱生物啊，你难道没见过吗？", talk_name = "沐优",},
	[101180608] = { talk_id = 101180608, group_id = 1011806, type = 3, next_id = 101180609, talk_content = "没太多印象，但这么说的话你应该就是希望成为研究这种生物的权威是吧？", talk_name = "<nick_name>",},
	[101180609] = { talk_id = 101180609, group_id = 1011806, type = 3, next_id = 101180610, talk_content = "是的，因为我真的很喜欢那些小家伙，可现在我遇到了一个天大的问题。", talk_name = "沐优",},
	[101180610] = { talk_id = 101180610, group_id = 1011806, type = 3, next_id = 101180611, talk_content = "那是？", talk_name = "<nick_name>",},
	[101180611] = { talk_id = 101180611, group_id = 1011806, type = 3, next_id = 101180614, talk_content = "我发现自己没有寻找脉灵的才能，当研究员这么久找到的脉灵也不过尔尔，想凑样本都不够数。", talk_name = "沐优",},
	[101180612] = { talk_id = 101180612, group_id = 1011806, type = 3, next_id = 101180613, talk_content = "嗯，因为我没有寻找脉灵的才能，我真的很喜欢那些黑白相间的小家伙。", talk_name = "沐优",},
	[101180613] = { talk_id = 101180613, group_id = 1011806, type = 3, next_id = 101180614, talk_content = "但是无论我怎么找都没法找到新的脉灵……好多研究都因为缺样本而被逼停止。", talk_name = "沐优",},
	[101180614] = { talk_id = 101180614, group_id = 1011806, type = 1, next_id = 101180615, talk_content = "再这样下去不说什么权威，我连饭碗都要保不住了，哎……", talk_name = "沐优",},
	[101180615] = { talk_id = 101180615, group_id = 1011806, type = 1, next_id = 101180616, talk_content = "听上去是不是我只要帮你找到脉灵就好了。", talk_name = "<nick_name>",},
	[101180616] = { talk_id = 101180616, group_id = 1011806, type = 1, next_id = 101180617, talk_content = "呃，你真的会帮我找吗，你这个脉者真的会帮我找吗！", talk_name = "沐优",},
	[101180617] = { talk_id = 101180617, group_id = 1011806, type = 1, next_id = 101180618, talk_content = "毕竟是“售后”，找应该是没问题，但我也不知道能不能找到。", talk_name = "<nick_name>",},
	[101180618] = { talk_id = 101180618, group_id = 1011806, type = 1, next_id = 101180619, talk_content = "肯定可以，我前些日子才在附近听到了脉灵标志性的“嘻哈”。", talk_name = "沐优",},
	[101180619] = { talk_id = 101180619, group_id = 1011806, type = 1, next_id = 101180620, talk_content = "嘻哈？", talk_name = "<nick_name>",},
	[101180620] = { talk_id = 101180620, group_id = 1011806, type = 1, next_id = 101180621, talk_content = "嗯，脉灵们会发出这种声音来逗弄同伴，所以我才会一直呆在这。", talk_name = "沐优",},
	[101180621] = { talk_id = 101180621, group_id = 1011806, type = 1, next_id = 101180622, talk_content = "对了，在你出发去找脉灵之前要不要到附近捉个“若龙”的佩从，追逐脉灵的时候难免会追到些狭隘的地方。", talk_name = "沐优",},
	[101180622] = { talk_id = 101180622, group_id = 1011806, type = 1, talk_content = "这时候要是有“若龙”的遁地能力的话就能追上去了。", talk_name = "沐优",},
	[101180701] = { talk_id = 101180701, group_id = 1011807, type = 1, next_id = 101180702, talk_content = "脉灵是黑白相间的，很可爱的小家伙，有时候也会用竹叶当遮挡隐藏身影。", talk_name = "沐优",},
	[101180702] = { talk_id = 101180702, group_id = 1011807, type = 1, next_id = 101180703, talk_content = "还有着很标志性的叫声，嘻哈！", talk_name = "沐优",},
	[101180703] = { talk_id = 101180703, group_id = 1011807, type = 1, talk_content = "这附近肯定有一只脉灵，请你帮我找找吧！", talk_name = "沐优",},
	[101180801] = { talk_id = 101180801, group_id = 1011808, type = 4, next_id = 101180802, aside = "<color=#FFEBA2><nick_name>：</color>假如是“小家伙”的话应该会在些狭隘的地方。", talk_content = "假如是“小家伙”的话应该会在些狭隘的地方。", talk_name = "<nick_name>",},
	[101180802] = { talk_id = 101180802, group_id = 1011808, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>到各种小道里看看吧。", talk_content = "到各种小道里看看吧。", talk_name = "<nick_name>",},
	[101180901] = { talk_id = 101180901, group_id = 1011809, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>看起来不在这。", talk_content = "看起来不在这。", talk_name = "<nick_name>",},
	[101181001] = { talk_id = 101181001, group_id = 1011810, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>也不在……黑白相间的小家伙……", talk_content = "也不在……黑白相间的小家伙……", talk_name = "<nick_name>",},
	[101181101] = { talk_id = 101181101, group_id = 1011811, type = 4, next_id = 101181102, aside = "<color=#FFEBA2>远处的声音：</color>嘻哈！", talk_content = "嘻哈！", talk_name = "远处的声音",},
	[101181102] = { talk_id = 101181102, group_id = 1011811, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>声音是从这后面传来的……看起来是使用遁地的时候了。", talk_content = "声音是从这后面传来的……看起来是使用遁地的时候了。", talk_name = "<nick_name>",},
	[101181201] = { talk_id = 101181201, group_id = 1011812, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>黑白相间，就是这个！", talk_content = "黑白相间，就是这个！", talk_name = "<nick_name>",},
	[101181301] = { talk_id = 101181301, group_id = 1011813, type = 4, next_id = 101181302, aside = "<color=#FFEBA2>脉灵：</color>嘻哈！", talk_content = "嘻哈！", talk_name = "脉灵",},
	[101181302] = { talk_id = 101181302, group_id = 1011813, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>想要我和它玩吗？", talk_content = "想要我和它玩吗？", talk_name = "<nick_name>",},
	[101181401] = { talk_id = 101181401, group_id = 1011814, type = 1, next_id = 101181402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "嘻哈，球球被捉住了嘻嘻。", talk_name = "脉灵球球",},
	[101181402] = { talk_id = 101181402, group_id = 1011814, type = 1, next_id = 101181403, talk_content = "你会说人话？", talk_name = "<nick_name>",},
	[101181403] = { talk_id = 101181403, group_id = 1011814, type = 1, next_id = 101181404, talk_content = "啊优她没和你说吗？", talk_name = "脉灵球球",},
	[101181404] = { talk_id = 101181404, group_id = 1011814, type = 1, next_id = 101181405, talk_content = "等等，阿优指的是沐优吗？", talk_name = "<nick_name>",},
	[101181405] = { talk_id = 101181405, group_id = 1011814, type = 1, next_id = 101181406, talk_content = "是噢，每天在这里和阿优玩捉迷藏可开心哩，不过找脉者可是作弊。", talk_name = "脉灵球球",},
	[101181406] = { talk_id = 101181406, group_id = 1011814, type = 1, next_id = 101181407, talk_content = "和普通人类不一样，脉者太容易听见我们的声音了。", talk_name = "脉灵球球",},
	[101181407] = { talk_id = 101181407, group_id = 1011814, type = 1, next_id = 101181408, talk_content = "……我想我知道为什么她找不到脉灵了。", talk_name = "<nick_name>",},
	[101181408] = { talk_id = 101181408, group_id = 1011814, type = 2, options = {{"说明沐优的情况。",101181409,},}, talk_content = "嘻哈？", talk_name = "脉灵球球",},
	[101181409] = { talk_id = 101181409, group_id = 1011814, type = 3, next_id = 101181410, talk_content = "嘻哈，听不明白，但是阿优要我帮忙是吗？", talk_name = "脉灵球球",},
	[101181410] = { talk_id = 101181410, group_id = 1011814, type = 3, next_id = 101181411, talk_content = "是的。", talk_name = "<nick_name>",},
	[101181411] = { talk_id = 101181411, group_id = 1011814, type = 3, talk_content = "嘻哈，那你带我去找她!", talk_name = "脉灵球球",},
	[101181501] = { talk_id = 101181501, group_id = 1011815, type = 4, next_id = 101181502, aside = "<color=#FFEBA2>脉灵球球：</color>大家都说阿优是个好玩的人类，嘻嘻，真的好玩。", talk_content = "大家都说阿优是个好玩的人类，嘻嘻，真的好玩。", talk_name = "脉灵球球",},
	[101181502] = { talk_id = 101181502, group_id = 1011815, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>大家……哎，她也太倒霉了。", talk_content = "大家……哎，她也太倒霉了。", talk_name = "<nick_name>",},
	[101181601] = { talk_id = 101181601, group_id = 1011816, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"要打你一巴掌吗？",101181602,},{"你没有在做梦。",101181603,},}, talk_content = "是……是脉灵，我没有在做梦吧！", talk_name = "沐优",},
	[101181602] = { talk_id = 101181602, group_id = 1011816, type = 3, next_id = 101181604, talk_content = "不了，我有点怕痛……", talk_name = "沐优",},
	[101181603] = { talk_id = 101181603, group_id = 1011816, type = 3, next_id = 101181604, talk_content = "嗯嗯，毕竟要是梦的话那就亏大发了。", talk_name = "沐优",},
	[101181604] = { talk_id = 101181604, group_id = 1011816, type = 1, next_id = 101181605, talk_content = "嘻哈？", talk_name = "脉灵球球",},
	[101181605] = { talk_id = 101181605, group_id = 1011816, type = 1, next_id = 101181606, talk_content = "对了，可不能把脉灵放在一边，你，你好，我的名字叫沐优，可以告诉我你的名字吗？", talk_name = "沐优",},
	[101181606] = { talk_id = 101181606, group_id = 1011816, type = 1, next_id = 101181607, talk_content = "嘻哈，球球是球球哦，阿优，你要球球帮忙是吗？", talk_name = "脉灵球球",},
	[101181607] = { talk_id = 101181607, group_id = 1011816, type = 1, next_id = 101181608, talk_content = "是，是的，我正在做一些关于脉灵的研究，但无奈样本不够。", talk_name = "沐优",},
	[101181608] = { talk_id = 101181608, group_id = 1011816, type = 1, next_id = 101181609, talk_content = "对了，只要你愿意的话那一天五餐，睡觉的地方我也会准备好的，求求你帮帮我吧！", talk_name = "沐优",},
	[101181609] = { talk_id = 101181609, group_id = 1011816, type = 1, next_id = 101181610, talk_content = "既然是阿优应该可以吧，但是大家之前约好不能独食，我能把朋友都带过来吗？", talk_name = "脉灵球球",},
	[101181610] = { talk_id = 101181610, group_id = 1011816, type = 1, next_id = 101181611, talk_content = "当然可以，有多少来多少！", talk_name = "沐优",},
	[101181611] = { talk_id = 101181611, group_id = 1011816, type = 1, next_id = 101181612, talk_content = "嘻哈，那等我一会。", talk_name = "脉灵球球",},
	[101181612] = { talk_id = 101181612, group_id = 1011816, type = 1, next_id = 101181613, talk_content = "走掉了……", talk_name = "<nick_name>",},
	[101181613] = { talk_id = 101181613, group_id = 1011816, type = 1, next_id = 101181614, talk_content = "脉灵都是些很纯真的生物，它肯定会回来的，不过真是感谢你多少次都不够。", talk_name = "沐优",},
	[101181614] = { talk_id = 101181614, group_id = 1011816, type = 1, next_id = 101181615, talk_content = "也不用谢我，我觉得你之前只要表演一下假哭脉灵应该就会聚过来的了。", talk_name = "<nick_name>",},
	[101181615] = { talk_id = 101181615, group_id = 1011816, type = 2, options = {{"（说刚才的经历）",101181616,},}, talk_content = "啊？", talk_name = "沐优",},
	[101181616] = { talk_id = 101181616, group_id = 1011816, type = 3, next_id = 101181617, talk_content = "脉灵们以为我是在跟它们玩，所以才一直躲起来吗？", talk_name = "沐优",},
	[101181617] = { talk_id = 101181617, group_id = 1011816, type = 3, next_id = 101181618, talk_content = "应该是这样，但至少你应该是挺受它们喜欢的就是了，话说回来，脉灵这种生物到底是什么？", talk_name = "<nick_name>",},
	[101181618] = { talk_id = 101181618, group_id = 1011816, type = 2, child_type = 21, options = {{"（回答）",101181619,},{"不知道。",101181621,},}, talk_content = "就是气脉之灵，青乌你应该明白气脉这个概念吧？", talk_name = "沐优",},
	[101181619] = { talk_id = 101181619, group_id = 1011816, type = 3, next_id = 101181620, icon = "interaction_4", talk_content = "就是构成这个世界万物的特殊能量对吧，说是时刻都以“无形”的样貌流动在世界的表面。", talk_name = "<nick_name>",},
	[101181620] = { talk_id = 101181620, group_id = 1011816, type = 3, next_id = 101181624, talk_content = "嗯，大概没错。", talk_name = "沐优",},
	[101181621] = { talk_id = 101181621, group_id = 1011816, type = 3, next_id = 101181622, icon = "interaction_4", talk_content = "那我就说明一下吧，气脉指的是流淌在世界之中的无形的能量脉络。", talk_name = "沐优",},
	[101181622] = { talk_id = 101181622, group_id = 1011816, type = 3, next_id = 101181623, talk_content = "其中流动的能量构成了世间万物，你，我的身体之中都有气脉的存在。", talk_name = "沐优",},
	[101181623] = { talk_id = 101181623, group_id = 1011816, type = 3, next_id = 101181624, talk_content = "而身体能流通的能量越多，生物本身就会越强，你这样的脉者也是因此而出现的。", talk_name = "沐优",},
	[101181624] = { talk_id = 101181624, group_id = 1011816, type = 1, next_id = 101181625, talk_content = "所以搞明白气脉的概念和认知脉灵有什么帮助吗？", talk_name = "<nick_name>",},
	[101181625] = { talk_id = 101181625, group_id = 1011816, type = 1, next_id = 101181626, talk_content = "因为脉灵都是从气脉之中直接诞生的，它们与气脉的联系比其他生物都要深。", talk_name = "沐优",},
	[101181626] = { talk_id = 101181626, group_id = 1011816, type = 1, next_id = 101181627, talk_content = "要详细解释可能有些困难，但简单来说的话我认为你可以把它们当作是气脉意志的延申。", talk_name = "沐优",},
	[101181627] = { talk_id = 101181627, group_id = 1011816, type = 1, next_id = 101181628, talk_content = "自古以来脉灵群聚的地方会风调雨顺，万物平安，而没有脉灵的地方则会逐渐变成荒地，最后甚至无法住人。", talk_name = "<nick_name>",},
	[101181628] = { talk_id = 101181628, group_id = 1011816, type = 1, next_id = 101181629, talk_content = "所以说它们的多少会影响一个地方的环境之类的吗，看着也不像有这么大威力的样子。", talk_name = "<nick_name>",},
	[101181629] = { talk_id = 101181629, group_id = 1011816, type = 1, next_id = 101181630, talk_content = "但这些都是千真万确的事情，之后也有人对这个现象进行了研究。", talk_name = "沐优",},
	[101181630] = { talk_id = 101181630, group_id = 1011816, type = 1, next_id = 101181631, talk_content = "而得出的结果就是地方气脉流动的能量会随着脉灵的多少而变化。", talk_name = "沐优",},
	[101181631] = { talk_id = 101181631, group_id = 1011816, type = 1, next_id = 101181632, talk_content = "它们控制着气脉吗？", talk_name = "<nick_name>",},
	[101181632] = { talk_id = 101181632, group_id = 1011816, type = 1, next_id = 101181633, talk_content = "我想象不出这么可爱的小家伙们故意让一片地方荒废的场面。", talk_name = "沐优",},
	[101181633] = { talk_id = 101181633, group_id = 1011816, type = 1, next_id = 101181634, talk_content = "也是，而且不是还有噬脉生物这种东西吗？", talk_name = "<nick_name>",},
	[101181634] = { talk_id = 101181634, group_id = 1011816, type = 1, next_id = 101181635, talk_content = "嗯，噬脉生物也是会导致气脉衰弱的元凶，所以我才选择了脉灵作为自己的研究方向，为了证明它们不是坏人。", talk_name = "沐优",},
	[101181635] = { talk_id = 101181635, group_id = 1011816, type = 1, next_id = 101181636, talk_content = "说着说着我自己都有点害羞，总之请让我再说一次。", talk_name = "沐优",},
	[101181636] = { talk_id = 101181636, group_id = 1011816, type = 1, talk_content = "感谢你帮我找到脉灵，青乌！", talk_name = "沐优",},
	[101181701] = { talk_id = 101181701, group_id = 1011817, type = 1, talk_content = "在球球回来之前我在这里等就好，多亏了青乌你我现在感觉梦想离自己越来越近了。", talk_name = "沐优",},
	[101181801] = { talk_id = 101181801, group_id = 1011818, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>第一个人的售后应该算是结束了，去找剩下那个吧。", talk_content = "第一个人的售后应该算是结束了，去找剩下那个吧。", talk_name = "<nick_name>",},
	[101181901] = { talk_id = 101181901, group_id = 1011819, type = 4, next_id = 101181902, aside = "<color=#FFEBA2><nick_name>：</color>第二个人是位希望能取得好成绩的学生，可要怎么辨认呢？", talk_content = "第二个人是位希望能取得好成绩的学生，可要怎么辨认呢？", talk_name = "<nick_name>",},
	[101181902] = { talk_id = 101181902, group_id = 1011819, type = 4, next_id = 101181903, aside = "<color=#FFEBA2>凶暴的声音：</color>快追过去！", talk_content = "快追过去！", talk_name = "凶暴的声音",},
	[101181903] = { talk_id = 101181903, group_id = 1011819, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>这是……？", talk_content = "这是……？", talk_name = "<nick_name>",},
	[101182001] = { talk_id = 101182001, group_id = 1011820, type = 4, next_id = 101182002, aside = "<color=#FFEBA2>混混：</color>你这学生听不懂人话是吧，今天得好好教训你一顿！", talk_content = "你这学生听不懂人话是吧，今天得好好教训你一顿！", talk_name = "混混",},
	[101182002] = { talk_id = 101182002, group_id = 1011820, type = 4, next_id = 101182003, aside = "<color=#FFEBA2>？？？：</color>我只是想让你们到天上去拿些东西而已，我会付钱的！", talk_content = "我只是想让你们到天上去拿些东西而已，我会付钱的！", talk_name = "？？？",},
	[101182003] = { talk_id = 101182003, group_id = 1011820, type = 4, next_id = 101182004, aside = "<color=#FFEBA2>混混：</color>就那点钱还不够哥塞牙缝，我们没时间跟你这种穷鬼做生意！", talk_content = "就那点钱还不够哥塞牙缝，我们没时间跟你这种穷鬼做生意！", talk_name = "混混",},
	[101182004] = { talk_id = 101182004, group_id = 1011820, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>学生……不管了，救人要紧。", talk_content = "学生……不管了，救人要紧。", talk_name = "<nick_name>",},
	[101182101] = { talk_id = 101182101, group_id = 1011821, type = 4, aside = "<color=#FFEBA2>混混：</color>哼，算你好运，我们走。", talk_content = "哼，算你好运，我们走。", talk_name = "混混",},
	[101182201] = { talk_id = 101182201, group_id = 1011822, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"请问你找过“咨询师”吗？",101182202,},{"询问伤势",101182203,},}, talk_content = "谢谢你救了我，但我应该不认识你才是。", talk_name = "“学生”",},
	[101182202] = { talk_id = 101182202, group_id = 1011822, type = 3, next_id = 101182206, talk_content = "咨询师？找过是找过，那家伙根本就是个信口开河的神棍。", talk_name = "“学生”",},
	[101182203] = { talk_id = 101182203, group_id = 1011822, type = 3, next_id = 101182204, talk_content = "受了点皮外伤，咳……早知道就把去看神棍的钱留着买药了。", talk_name = "“学生”",},
	[101182204] = { talk_id = 101182204, group_id = 1011822, type = 3, next_id = 101182205, talk_content = "所以你去找过“咨询师”？", talk_name = "<nick_name>",},
	[101182205] = { talk_id = 101182205, group_id = 1011822, type = 3, next_id = 101182206, talk_content = "嗯，找过。", talk_name = "“学生”",},
	[101182206] = { talk_id = 101182206, group_id = 1011822, type = 1, next_id = 101182207, talk_content = "那还有个问题，刚刚的混混好像叫你做学生，你是在哪上学吗？", talk_name = "<nick_name>",},
	[101182207] = { talk_id = 101182207, group_id = 1011822, type = 1, next_id = 101182208, talk_content = "现在没上了，反正怎么学都学不好。", talk_name = "“学生”",},
	[101182208] = { talk_id = 101182208, group_id = 1011822, type = 1, next_id = 101182209, talk_content = "所以你问这些来做啥，虽然我很感谢你帮了我，但我可没什么能给你的。", talk_name = "“学生”",},
	[101182209] = { talk_id = 101182209, group_id = 1011822, type = 1, next_id = 101182210, talk_content = "报酬有人替你付了，对了，我是咨询师派来的售后，是来看看你有什么需要帮忙的。", talk_name = "<nick_name>",},
	[101182210] = { talk_id = 101182210, group_id = 1011822, type = 1, next_id = 101182211, talk_content = "售后？我都退学了还售后干嘛，你知道我去咨询学业的时候那神棍说了啥吗？", talk_name = "“学生”",},
	[101182211] = { talk_id = 101182211, group_id = 1011822, type = 2, options = {{"听上去像是学业一定能成的感觉",101182212,},{"这回复有种模棱两可的感觉",101182213,},}, talk_content = "“至今所学，必不白费。”", talk_name = "“学生”",},
	[101182212] = { talk_id = 101182212, group_id = 1011822, type = 3, next_id = 101182215, talk_content = "对吧，我当时也这么想的，可结果就我成绩和之前一样差，实在没法干脆就退学回家给老爸打工了。", talk_name = "“学生”",},
	[101182213] = { talk_id = 101182213, group_id = 1011822, type = 3, next_id = 101182214, talk_content = "当时我也没多想，因为大家都说那神棍是”金口“，说的都很准。", talk_name = "“学生”",},
	[101182214] = { talk_id = 101182214, group_id = 1011822, type = 3, next_id = 101182215, talk_content = "可实际上他就是个神棍，我也因为成绩差干脆回家给老爸打工了。", talk_name = "“学生”",},
	[101182215] = { talk_id = 101182215, group_id = 1011822, type = 1, next_id = 101182216, talk_content = "你和我说这些我也不知道怎么安慰你好，毕竟那神棍算是我现在的委托人。", talk_name = "<nick_name>",},
	[101182216] = { talk_id = 101182216, group_id = 1011822, type = 2, child_type = 21, options = {{"嗯，账单算神棍头上",101182217,},{"是要我教你读书吗",101182218,},}, talk_content = "我也不用什么安慰，怪就怪自己太笨了……对了，你说你能帮我个忙是吗？", talk_name = "“学生”",},
	[101182217] = { talk_id = 101182217, group_id = 1011822, type = 3, next_id = 101182219, icon = "interaction_4", talk_content = "那能请你帮我们夺回弃置场吗？", talk_name = "“学生”",},
	[101182218] = { talk_id = 101182218, group_id = 1011822, type = 3, next_id = 101182219, icon = "interaction_4", talk_content = "要读书我找以前同学不就好了，我是想让你帮忙夺回弃置场。", talk_name = "“学生”",},
	[101182219] = { talk_id = 101182219, group_id = 1011822, type = 1, next_id = 101182220, talk_content = "刚才你和那些混混也有提到这个地方，那是混混的地盘吗？", talk_name = "<nick_name>",},
	[101182220] = { talk_id = 101182220, group_id = 1011822, type = 1, next_id = 101182221, talk_content = "那才不是谁的地盘，弃置场本身是个谁都能进的废品堆填区，大家一直都会在里面找些有用的东西。", talk_name = "“学生”",},
	[101182221] = { talk_id = 101182221, group_id = 1011822, type = 1, next_id = 101182222, talk_content = "那他们为什么会把那地方叫地盘？", talk_name = "<nick_name>",},
	[101182222] = { talk_id = 101182222, group_id = 1011822, type = 1, next_id = 101182223, talk_content = "强占呗，弃置场偶尔能找到能卖大钱的好东西，那些流氓听说后就用武力把我们都赶出去了。", talk_name = "“学生”",},
	[101182223] = { talk_id = 101182223, group_id = 1011822, type = 2, child_type = 21, options = {{"对付混混不是什么事",101182224,},{"赶跑他们只能解决一时问题",101182226,},{"我试试吧。",101182230,},}, talk_content = "怎么样，这个忙你可以帮吗？", talk_name = "“学生”",},
	[101182224] = { talk_id = 101182224, group_id = 1011822, type = 3, next_id = 101182225, icon = "interaction_4", talk_content = "没什么问题，我一个打他们十个也不是什么事。", talk_name = "<nick_name>",},
	[101182225] = { talk_id = 101182225, group_id = 1011822, type = 3, next_id = 101182230, talk_content = "我也是见你是脉者所以才拜托你这个的，我等你好消息。", talk_name = "“学生”",},
	[101182226] = { talk_id = 101182226, group_id = 1011822, type = 3, next_id = 101182227, icon = "interaction_4", talk_content = "夺回应该不难，但谁也不能确保他们不会卷土重来。", talk_name = "<nick_name>",},
	[101182227] = { talk_id = 101182227, group_id = 1011822, type = 3, next_id = 101182228, talk_content = "我也不可能总是在那站岗。", talk_name = "<nick_name>",},
	[101182228] = { talk_id = 101182228, group_id = 1011822, type = 3, next_id = 101182229, talk_content = "这事之后再担心就好，我现在只想有人来教训他们。", talk_name = "“学生”",},
	[101182229] = { talk_id = 101182229, group_id = 1011822, type = 3, next_id = 101182231, talk_content = "也行吧，毕竟这事你做主。", talk_name = "<nick_name>",},
	[101182230] = { talk_id = 101182230, group_id = 1011822, type = 3, next_id = 101182232, icon = "interaction_4", talk_content = "对了，聊了半天……我的名字叫于彦，我该怎么称呼你。", talk_name = "于彦",},
	[101182231] = { talk_id = 101182231, group_id = 1011822, type = 2, options = {{"告知",101182232,},}, talk_content = "对了，聊了半天……我的名字叫于彦，我该怎么称呼你。", talk_name = "于彦",},
	[101182232] = { talk_id = 101182232, group_id = 1011822, type = 3, talk_content = "<nick_name>，好，我知道了。", talk_name = "于彦",},
	[101182301] = { talk_id = 101182301, group_id = 1011823, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>弃置场就在南边，直接过去吧。", talk_content = "弃置场就在南边，直接过去吧。", talk_name = "<nick_name>",},
	[101182401] = { talk_id = 101182401, group_id = 1011824, type = 1, next_id = 101182402, talk_content = "有不少人都是靠弃置场过日子的，我家的店也是如此。", talk_name = "于彦",},
	[101182402] = { talk_id = 101182402, group_id = 1011824, type = 1, talk_content = "你可要帮我们出一口气啊，青乌。", talk_name = "于彦",},
	[101182501] = { talk_id = 101182501, group_id = 1011825, type = 4, next_id = 101182502, aside = "<color=#FFEBA2><nick_name>：</color>这地方的确不是普通人能来的，感觉身体变得好重。", talk_content = "这地方的确不是普通人能来的，感觉身体变得好重。", talk_name = "<nick_name>",},
	[101182502] = { talk_id = 101182502, group_id = 1011825, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>在那站着的大概就是霸占了这的流氓了吧。", talk_content = "在那站着的大概就是霸占了这的流氓了吧。", talk_name = "<nick_name>",},
	[101182601] = { talk_id = 101182601, group_id = 1011826, type = 4, aside = "<color=#FFEBA2>混混：</color>怎么会有脉者，喂，有人打过来了！", talk_content = "怎么会有脉者，喂，有人打过来了！", talk_name = "混混",},
	[101182701] = { talk_id = 101182701, group_id = 1011827, type = 4, next_id = 101182702, aside = "<color=#FFEBA2><nick_name>：</color>事情解决了，回去和于彦说一声吧。", talk_content = "事情解决了，回去和于彦说一声吧。", talk_name = "<nick_name>",},
	[101182702] = { talk_id = 101182702, group_id = 1011827, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>话说回来……他好像是在这里找点零件……", talk_content = "话说回来……他好像是在这里找点零件……", talk_name = "<nick_name>",},
	[101182801] = { talk_id = 101182801, group_id = 1011828, type = 1, next_id = 101182802, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "事情都解决了。", talk_name = "<nick_name>",},
	[101182802] = { talk_id = 101182802, group_id = 1011828, type = 1, next_id = 101182803, talk_content = "我在下面看见了，不愧是脉者啊，这下子他们一时半会应该不会回来了。", talk_name = "于彦",},
	[101182803] = { talk_id = 101182803, group_id = 1011828, type = 1, next_id = 101182804, talk_content = "也只有一时半会而已。", talk_name = "<nick_name>",},
	[101182804] = { talk_id = 101182804, group_id = 1011828, type = 2, options = {{"你家是开什么店的。",101182805,},{"那你的问题应该算解决了吧？",101182806,},}, talk_content = "那也足够了，青乌，这样一来店面就暂时就不会缺材料了。", talk_name = "于彦",},
	[101182805] = { talk_id = 101182805, group_id = 1011828, type = 3, next_id = 101182809, talk_content = "呃，我没说过吗，我家里做的是修车生意，青乌你要是车有什么问题之后也可以让我修哦。", talk_name = "于彦",},
	[101182806] = { talk_id = 101182806, group_id = 1011828, type = 3, next_id = 101182807, talk_content = "嗯，这下就有办法解决那堆修车单了，我爸看我不读书了直接把店面一堆单丢给我，可麻烦了。", talk_name = "于彦",},
	[101182807] = { talk_id = 101182807, group_id = 1011828, type = 3, next_id = 101182808, talk_content = "你是个修车师傅？", talk_name = "<nick_name>",},
	[101182808] = { talk_id = 101182808, group_id = 1011828, type = 3, next_id = 101182809, talk_content = "师傅算不上，老爸也说我是个学徒而已。", talk_name = "于彦",},
	[101182809] = { talk_id = 101182809, group_id = 1011828, type = 1, next_id = 101182810, talk_content = "总之下次青乌你有空的话就来我家的店坐坐吧，有关于车的事情都能给你打个折。", talk_name = "于彦",},
	[101182810] = { talk_id = 101182810, group_id = 1011828, type = 1, next_id = 101182811, talk_content = "所以这事就算结了？", talk_name = "<nick_name>",},
	[101182811] = { talk_id = 101182811, group_id = 1011828, type = 1, next_id = 101182812, talk_content = "嗯，结了结了，那神棍虽然乱骗人，但也让你来帮了我个大忙，就一笔勾销了吧。", talk_name = "于彦",},
	[101182812] = { talk_id = 101182812, group_id = 1011828, type = 1, next_id = 101182813, talk_content = "反正我或多或少也觉得自己应该不太适合读书。", talk_name = "于彦",},
	[101182813] = { talk_id = 101182813, group_id = 1011828, type = 1, talk_content = "我接下来会在这找些材料，青乌你有事的话就先走吧。", talk_name = "于彦",},
	[101182901] = { talk_id = 101182901, group_id = 1011829, type = 1, next_id = 101182902, talk_content = "哎，明明说好和林妹子一起读大学的……", talk_name = "于彦",},
	[101182902] = { talk_id = 101182902, group_id = 1011829, type = 1, next_id = 101182903, talk_content = "现在想起来，林妹子可真香啊。", talk_name = "于彦",},
	[101182903] = { talk_id = 101182903, group_id = 1011829, type = 1, talk_content = "……", talk_name = "<nick_name>",},
	[101183001] = { talk_id = 101183001, group_id = 1011830, type = 4, next_id = 101183002, aside = "<color=#FFEBA2><nick_name>：</color>两个人的售后都告一段落。", talk_content = "两个人的售后都告一段落。", talk_name = "<nick_name>",},
	[101183002] = { talk_id = 101183002, group_id = 1011830, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>青乌……只希望那个“神棍”没有骗我。", talk_content = "青乌……只希望那个“神棍”没有骗我。", talk_name = "<nick_name>",},
	[101183101] = { talk_id = 101183101, group_id = 1011831, type = 4, next_id = 101183102, aside = "<color=#FFEBA2><nick_name>：</color>若龙好像是说那些和蜥蜴差不多的东西吧……", talk_content = "若龙好像是说那些和蜥蜴差不多的东西吧……", talk_name = "<nick_name>",},
	[101183102] = { talk_id = 101183102, group_id = 1011831, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>为什么城里会有那样的生物呢……", talk_content = "为什么城里会有那样的生物呢……", talk_name = "<nick_name>",},
	[101183201] = { talk_id = 101183201, group_id = 1011832, type = 4, next_id = 101183202, aside = "<color=#FFEBA2><nick_name>：</color>刚刚好像看见附近有个能钻进去的洞。", talk_content = "刚刚好像看见附近有个能钻进去的洞。", talk_name = "<nick_name>",},
	[101183202] = { talk_id = 101183202, group_id = 1011832, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>要不先试试这个遁地好用不好用吧。", talk_content = "要不先试试这个遁地好用不好用吧。", talk_name = "<nick_name>",},
	[101183301] = { talk_id = 101183301, group_id = 1011833, type = 4, next_id = 101183302, aside = "<color=#FFEBA2>脉灵球球：</color>大家都说阿优是个好玩的人类，嘻嘻，真的好玩。", talk_content = "大家都说阿优是个好玩的人类，嘻嘻，真的好玩。", talk_name = "脉灵球球",},
	[101183302] = { talk_id = 101183302, group_id = 1011833, type = 4, next_id = 101183303, aside = "<color=#FFEBA2><nick_name>：</color>大家……哎，她也太倒霉了。", talk_content = "大家……哎，她也太倒霉了。", talk_name = "<nick_name>",},
	[101183303] = { talk_id = 101183303, group_id = 1011833, type = 4, next_id = 101183304, aside = "<color=#FFEBA2>？？？：</color>嘻哈！", talk_content = "嘻哈！", talk_name = "？？？",},
	[101183304] = { talk_id = 101183304, group_id = 1011833, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>这声音是？", talk_content = "这声音是？", talk_name = "<nick_name>",},
	[101183401] = { talk_id = 101183401, group_id = 1011834, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>果然这里还有一只脉灵。", talk_content = "果然这里还有一只脉灵。", talk_name = "<nick_name>",},
	[101183501] = { talk_id = 101183501, group_id = 1011835, type = 4, next_id = 101183502, aside = "<color=#FFEBA2>坡坡：</color>嘻哈，被捉咯。", talk_content = "嘻哈，被捉咯。", talk_name = "坡坡",},
	[101183502] = { talk_id = 101183502, group_id = 1011835, type = 4, aside = "<color=#FFEBA2>球球：</color>青乌不愧是青乌啦。", talk_content = "青乌不愧是青乌啦。", talk_name = "球球",},
	[101183601] = { talk_id = 101183601, group_id = 1011816, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"要打你一巴掌吗？",101183602,},{"你没有在做梦。",101183603,},}, talk_content = "是……是脉灵，我没有在做梦吧！", talk_name = "沐优",},
	[101183602] = { talk_id = 101183602, group_id = 1011816, type = 3, next_id = 101183604, talk_content = "不了，我有点怕痛……", talk_name = "沐优",},
	[101183603] = { talk_id = 101183603, group_id = 1011816, type = 3, next_id = 101183604, talk_content = "嗯嗯，毕竟要是梦的话那就亏大发了。", talk_name = "沐优",},
	[101183604] = { talk_id = 101183604, group_id = 1011816, type = 1, next_id = 101183605, talk_content = "嘻哈？", talk_name = "脉灵球球",},
	[101183605] = { talk_id = 101183605, group_id = 1011816, type = 1, next_id = 101183606, talk_content = "对了，可不能把脉灵放在一边，你，你们好，我的名字叫沐优，你们叫？", talk_name = "沐优",},
	[101183606] = { talk_id = 101183606, group_id = 1011816, type = 1, next_id = 101183607, talk_content = "嘻哈，球球是球球哦，阿优，你要球球帮忙是吗？", talk_name = "脉灵球球",},
	[101183607] = { talk_id = 101183607, group_id = 1011816, type = 1, next_id = 101183608, talk_content = "坡坡，我喜欢吃东西。", talk_name = "脉灵坡坡",},
	[101183608] = { talk_id = 101183608, group_id = 1011816, type = 1, next_id = 101183609, talk_content = "没人问你要吃什么。", talk_name = "脉灵球球",},
	[101183609] = { talk_id = 101183609, group_id = 1011816, type = 1, next_id = 101183610, talk_content = "是，是的，我正在做一些关于脉灵的研究，但无奈样本不够。", talk_name = "沐优",},
	[101183610] = { talk_id = 101183610, group_id = 1011816, type = 1, next_id = 101183611, talk_content = "对了，只要你们愿意的话那一天五餐，睡觉的地方我也会准备好的，求求帮帮我吧！", talk_name = "沐优",},
	[101183611] = { talk_id = 101183611, group_id = 1011816, type = 1, next_id = 101183612, talk_content = "既然是阿优应该可以吧，但是大家之前约好不能独食，我能把朋友都带过来吗？", talk_name = "脉灵球球",},
	[101183612] = { talk_id = 101183612, group_id = 1011816, type = 1, next_id = 101183613, talk_content = "明明你就没朋友。", talk_name = "脉灵坡坡",},
	[101183613] = { talk_id = 101183613, group_id = 1011816, type = 1, next_id = 101183614, talk_content = "禁言术嘻哈！", talk_name = "脉灵球球",},
	[101183614] = { talk_id = 101183614, group_id = 1011816, type = 1, next_id = 101183615, talk_content = "唔！呜呜！", talk_name = "脉灵坡坡",},
	[101183615] = { talk_id = 101183615, group_id = 1011816, type = 1, next_id = 101183616, talk_content = "当然可以，有多少来多少！", talk_name = "沐优",},
	[101183616] = { talk_id = 101183616, group_id = 1011816, type = 1, next_id = 101183617, talk_content = "嘻哈，那等我一会，你也跟过来。", talk_name = "脉灵球球",},
	[101183617] = { talk_id = 101183617, group_id = 1011816, type = 1, next_id = 101183618, talk_content = "唔！", talk_name = "脉灵坡坡",},
	[101183618] = { talk_id = 101183618, group_id = 1011816, type = 1, next_id = 101183619, talk_content = "走掉了……", talk_name = "<nick_name>",},
	[101183619] = { talk_id = 101183619, group_id = 1011816, type = 1, next_id = 101183620, talk_content = "脉灵都是些很纯真的生物，它肯定会回来的，不过真是感谢你多少次都不够。", talk_name = "沐优",},
	[101183620] = { talk_id = 101183620, group_id = 1011816, type = 1, next_id = 101183621, talk_content = "也不用谢我，我觉得你之前只要表演一下假哭脉灵应该就会聚过来的了。", talk_name = "<nick_name>",},
	[101183621] = { talk_id = 101183621, group_id = 1011816, type = 2, options = {{"（说刚才的经历）",101183622,},}, talk_content = "啊？", talk_name = "沐优",},
	[101183622] = { talk_id = 101183622, group_id = 1011816, type = 3, next_id = 101183623, talk_content = "脉灵们以为我是在跟它们玩，所以才一直躲起来吗？", talk_name = "沐优",},
	[101183623] = { talk_id = 101183623, group_id = 1011816, type = 3, next_id = 101183624, talk_content = "应该是这样，但至少你应该是挺受它们喜欢的就是了，话说回来，脉灵这种生物到底是什么？", talk_name = "<nick_name>",},
	[101183624] = { talk_id = 101183624, group_id = 1011816, type = 2, child_type = 21, options = {{"（回答）",101183625,},{"不知道。",101183627,},}, talk_content = "就是气脉之灵，青乌你应该明白气脉这个概念吧？", talk_name = "沐优",},
	[101183625] = { talk_id = 101183625, group_id = 1011816, type = 3, next_id = 101183626, icon = "interaction_4", talk_content = "就是构成这个世界万物的特殊能量对吧，说是时刻都以“无形”的样貌流动在世界的表面。", talk_name = "<nick_name>",},
	[101183626] = { talk_id = 101183626, group_id = 1011816, type = 3, next_id = 101183630, talk_content = "嗯，大概没错。", talk_name = "沐优",},
	[101183627] = { talk_id = 101183627, group_id = 1011816, type = 3, next_id = 101183628, icon = "interaction_4", talk_content = "那我就说明一下吧，气脉指的是流淌在世界之中的无形的能量脉络。", talk_name = "沐优",},
	[101183628] = { talk_id = 101183628, group_id = 1011816, type = 3, next_id = 101183629, talk_content = "其中流动的能量构成了世间万物，你，我的身体之中都有气脉的存在。", talk_name = "沐优",},
	[101183629] = { talk_id = 101183629, group_id = 1011816, type = 3, next_id = 101183630, talk_content = "而身体能流通的能量越多，生物本身就会越强，你这样的脉者也是因此而出现的。", talk_name = "沐优",},
	[101183630] = { talk_id = 101183630, group_id = 1011816, type = 1, next_id = 101183631, talk_content = "所以搞明白气脉的概念和认知脉灵有什么帮助吗？", talk_name = "<nick_name>",},
	[101183631] = { talk_id = 101183631, group_id = 1011816, type = 1, next_id = 101183632, talk_content = "因为脉灵都是从气脉之中直接诞生的，它们与气脉的联系比其他生物都要深。", talk_name = "沐优",},
	[101183632] = { talk_id = 101183632, group_id = 1011816, type = 1, next_id = 101183633, talk_content = "要详细解释可能有些困难，但简单来说的话我认为你可以把它们当作是气脉意志的延申。", talk_name = "沐优",},
	[101183633] = { talk_id = 101183633, group_id = 1011816, type = 1, next_id = 101183634, talk_content = "自古以来脉灵群聚的地方会风调雨顺，万物平安，而没有脉灵的地方则会逐渐变成荒地，最后甚至无法住人。", talk_name = "<nick_name>",},
	[101183634] = { talk_id = 101183634, group_id = 1011816, type = 1, next_id = 101183635, talk_content = "所以说它们的多少会影响一个地方的环境之类的吗，看着也不像有这么大威力的样子。", talk_name = "<nick_name>",},
	[101183635] = { talk_id = 101183635, group_id = 1011816, type = 1, next_id = 101183636, talk_content = "但这些都是千真万确的事情，之后也有人对这个现象进行了研究。", talk_name = "沐优",},
	[101183636] = { talk_id = 101183636, group_id = 1011816, type = 1, next_id = 101183637, talk_content = "而得出的结果就是地方气脉流动的能量会随着脉灵的多少而变化。", talk_name = "沐优",},
	[101183637] = { talk_id = 101183637, group_id = 1011816, type = 1, next_id = 101183638, talk_content = "它们控制着气脉吗？", talk_name = "<nick_name>",},
	[101183638] = { talk_id = 101183638, group_id = 1011816, type = 1, next_id = 101183639, talk_content = "我想象不出这么可爱的小家伙们故意让一片地方荒废的场面。", talk_name = "沐优",},
	[101183639] = { talk_id = 101183639, group_id = 1011816, type = 1, next_id = 101183640, talk_content = "也是，而且不是还有噬脉生物这种东西吗？", talk_name = "<nick_name>",},
	[101183640] = { talk_id = 101183640, group_id = 1011816, type = 1, next_id = 101183641, talk_content = "嗯，噬脉生物也是会导致气脉衰弱的元凶，所以我才选择了脉灵作为自己的研究方向，为了证明它们不是坏人。", talk_name = "沐优",},
	[101183641] = { talk_id = 101183641, group_id = 1011816, type = 1, next_id = 101183642, talk_content = "说着说着我自己都有点害羞，总之请让我再说一次。", talk_name = "沐优",},
	[101183642] = { talk_id = 101183642, group_id = 1011816, type = 1, talk_content = "感谢你帮我找到脉灵，青乌！", talk_name = "沐优",},
	[101183701] = { talk_id = 101183701, group_id = 1011822, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"请问你找过“咨询师”吗？",101183702,},{"询问伤势",101183703,},}, talk_content = "谢谢你救了我，但我应该不认识你才是。", talk_name = "“学生”",},
	[101183702] = { talk_id = 101183702, group_id = 1011822, type = 3, next_id = 101183706, talk_content = "咨询师？找过是找过，那家伙根本就是个信口开河的神棍。", talk_name = "“学生”",},
	[101183703] = { talk_id = 101183703, group_id = 1011822, type = 3, next_id = 101183704, talk_content = "受了点皮外伤，咳……早知道就把去看神棍的钱留着买药了。", talk_name = "“学生”",},
	[101183704] = { talk_id = 101183704, group_id = 1011822, type = 3, next_id = 101183705, talk_content = "所以你去找过“咨询师”？", talk_name = "<nick_name>",},
	[101183705] = { talk_id = 101183705, group_id = 1011822, type = 3, next_id = 101183706, talk_content = "嗯，找过。", talk_name = "“学生”",},
	[101183706] = { talk_id = 101183706, group_id = 1011822, type = 1, next_id = 101183707, talk_content = "那还有个问题，刚刚的混混好像叫你做学生，你是在哪上学吗？", talk_name = "<nick_name>",},
	[101183707] = { talk_id = 101183707, group_id = 1011822, type = 1, next_id = 101183708, talk_content = "现在没上了，反正怎么学都学不好。", talk_name = "“学生”",},
	[101183708] = { talk_id = 101183708, group_id = 1011822, type = 1, next_id = 101183709, talk_content = "所以你问这些来做啥，虽然我很感谢你帮了我，但我可没什么能给你的。", talk_name = "“学生”",},
	[101183709] = { talk_id = 101183709, group_id = 1011822, type = 1, next_id = 101183710, talk_content = "报酬有人替你付了，对了，我是咨询师派来的售后，是来看看你有什么需要帮忙的。", talk_name = "<nick_name>",},
	[101183710] = { talk_id = 101183710, group_id = 1011822, type = 1, next_id = 101183711, talk_content = "售后？我都退学了还售后干嘛，你知道我去咨询学业的时候那神棍说了啥吗？", talk_name = "“学生”",},
	[101183711] = { talk_id = 101183711, group_id = 1011822, type = 2, options = {{"听上去像是学业一定能成的感觉",101183712,},{"这回复有种模棱两可的感觉",101183713,},}, talk_content = "“至今所学，必不白费。”", talk_name = "“学生”",},
	[101183712] = { talk_id = 101183712, group_id = 1011822, type = 3, next_id = 101183715, talk_content = "对吧，我当时也这么想的，可结果就我成绩和之前一样差，实在没法干脆就退学回家给老爸打工了。", talk_name = "“学生”",},
	[101183713] = { talk_id = 101183713, group_id = 1011822, type = 3, next_id = 101183714, talk_content = "当时我也没多想，因为大家都说那神棍是”金口“，说的都很准。", talk_name = "“学生”",},
	[101183714] = { talk_id = 101183714, group_id = 1011822, type = 3, next_id = 101183715, talk_content = "可实际上他就是个神棍，我也因为成绩差干脆回家给老爸打工了。", talk_name = "“学生”",},
	[101183715] = { talk_id = 101183715, group_id = 1011822, type = 1, next_id = 101183716, talk_content = "你和我说这些我也不知道怎么安慰你好，毕竟那神棍算是我现在的委托人。", talk_name = "<nick_name>",},
	[101183716] = { talk_id = 101183716, group_id = 1011822, type = 2, child_type = 21, options = {{"嗯，账单算神棍头上",101183717,},{"是要我教你读书吗",101183718,},}, talk_content = "我也不用什么安慰，怪就怪自己太笨了……对了，你说你能帮我个忙是吗？", talk_name = "“学生”",},
	[101183717] = { talk_id = 101183717, group_id = 1011822, type = 3, next_id = 101183719, icon = "interaction_4", talk_content = "那能请你到浮空区去吗？", talk_name = "“学生”",},
	[101183718] = { talk_id = 101183718, group_id = 1011822, type = 3, next_id = 101183719, icon = "interaction_4", talk_content = "要读书我找以前同学不就好了，我是想让你帮忙夺回浮空区。", talk_name = "“学生”",},
	[101183719] = { talk_id = 101183719, group_id = 1011822, type = 1, next_id = 101183720, talk_content = "刚才你和那些混混也有提到这个地方，那是混混的地盘吗？", talk_name = "<nick_name>",},
	[101183720] = { talk_id = 101183720, group_id = 1011822, type = 1, next_id = 101183721, talk_content = "那才不是谁的地盘，你有注意到天上浮起来的那些地方吧，那是因为帝江太阳而形成特殊区域，那些区域里面往往有不错的材料。", talk_name = "“学生”",},
	[101183721] = { talk_id = 101183721, group_id = 1011822, type = 1, next_id = 101183722, talk_content = "这附近的人也大多都依靠在浮空区上拾荒来维生，但是那些流氓突然就闯了进来，然后宣布东西都是他们的……", talk_name = "“学生”",},
	[101183722] = { talk_id = 101183722, group_id = 1011822, type = 2, child_type = 21, options = {{"对付混混不是什么事",101183723,},{"赶跑他们只能解决一时问题",101183725,},{"我试试吧。",101183729,},}, talk_content = "怎么样，你能帮帮我吗？", talk_name = "“学生”",},
	[101183723] = { talk_id = 101183723, group_id = 1011822, type = 3, next_id = 101183724, icon = "interaction_4", talk_content = "没什么问题，我一个打他们十个也不是什么事。", talk_name = "<nick_name>",},
	[101183724] = { talk_id = 101183724, group_id = 1011822, type = 3, next_id = 101183729, talk_content = "我也是见你是脉者所以才拜托你这个的，我等你好消息。", talk_name = "“学生”",},
	[101183725] = { talk_id = 101183725, group_id = 1011822, type = 3, next_id = 101183726, icon = "interaction_4", talk_content = "夺回应该不难，但谁也不能确保他们不会卷土重来。", talk_name = "<nick_name>",},
	[101183726] = { talk_id = 101183726, group_id = 1011822, type = 3, next_id = 101183727, talk_content = "我也不可能总是在那站岗。", talk_name = "<nick_name>",},
	[101183727] = { talk_id = 101183727, group_id = 1011822, type = 3, next_id = 101183728, talk_content = "这事之后再担心就好，我现在只想有人来教训他们。", talk_name = "“学生”",},
	[101183728] = { talk_id = 101183728, group_id = 1011822, type = 3, next_id = 101183729, talk_content = "也行吧，毕竟这事你做主。", talk_name = "<nick_name>",},
	[101183729] = { talk_id = 101183729, group_id = 1011822, type = 2, next_id = 101183730, icon = "interaction_4", options = {{"告知",101183730,},}, talk_content = "对了，聊了半天……我的名字叫于彦，我该怎么称呼你。", talk_name = "于彦",},
	[101183730] = { talk_id = 101183730, group_id = 1011822, type = 3, talk_content = "<nick_name>，好，我知道了。", talk_name = "于彦",},
	[101183801] = { talk_id = 101183801, group_id = 1011838, type = 1, next_id = 101183802, talk_content = "事情都解决了，我还帮你带了些东西，里面有你要的零件不？", talk_name = "<nick_name>",},
	[101183802] = { talk_id = 101183802, group_id = 1011838, type = 2, options = {{"算神棍头上。",101183803,},{"帮人帮到底。",101183803,},}, talk_content = "呃……我没想到你竟然会连零件也帮我拿下来，应该不用另外的收费吧？", talk_name = "于彦",},
	[101183803] = { talk_id = 101183803, group_id = 1011838, type = 1, next_id = 101183804, talk_content = "谢谢，浮空区虽说谁都能去，但来往一趟不是脉者的话也需要一笔花费，你能帮我带下来真的太好了。", talk_name = "于彦",},
	[101183804] = { talk_id = 101183804, group_id = 1011838, type = 2, options = {{"那你的问题应该算解决了吧？",101183806,},{"你家是开什么店的。",101183805,},}, talk_content = "……这里也有我要的材料，这下家里的店一时半会也不会缺东西了。", talk_name = "于彦",},
	[101183805] = { talk_id = 101183805, group_id = 1011838, type = 3, next_id = 101183809, talk_content = "呃，我没说过吗，我家里做的是修车生意，青乌你要是车有什么问题之后也可以让我修哦。", talk_name = "于彦",},
	[101183806] = { talk_id = 101183806, group_id = 1011838, type = 3, next_id = 101183807, talk_content = "嗯，这下就有办法解决那堆修车单了，我爸看我不读书了直接把店面一堆单丢给我，可麻烦了。", talk_name = "于彦",},
	[101183807] = { talk_id = 101183807, group_id = 1011838, type = 3, next_id = 101183808, talk_content = "你是个修车师傅？", talk_name = "<nick_name>",},
	[101183808] = { talk_id = 101183808, group_id = 1011838, type = 3, next_id = 101183809, talk_content = "师傅算不上，老爸也说我是个学徒而已。", talk_name = "于彦",},
	[101183809] = { talk_id = 101183809, group_id = 1011838, type = 1, next_id = 101183810, talk_content = "总之下次青乌你有空的话就来我家的店坐坐吧，有关于车的事情都能给你打个折。", talk_name = "于彦",},
	[101183810] = { talk_id = 101183810, group_id = 1011838, type = 1, next_id = 101183811, talk_content = "所以这事就算结了？", talk_name = "<nick_name>",},
	[101183811] = { talk_id = 101183811, group_id = 1011838, type = 1, next_id = 101183812, talk_content = "嗯，结了结了，那神棍虽然乱骗人，但也让你来帮了我个大忙，就一笔勾销了吧。", talk_name = "于彦",},
	[101183812] = { talk_id = 101183812, group_id = 1011838, type = 1, next_id = 101183813, talk_content = "反正我或多或少也觉得自己应该不太适合读书。", talk_name = "于彦",},
	[101183813] = { talk_id = 101183813, group_id = 1011838, type = 2, options = {{"别搞这么大。",101183815,},{"多用力宣传！",101183814,},}, talk_content = "我稍后会告诉街区上其他人青乌你赶跑了流氓的事情，这下你就是这街的救世主了。", talk_name = "于彦",},
	[101183814] = { talk_id = 101183814, group_id = 1011838, type = 3, talk_content = "明白明白，我会和大家说有委托就找你的！", talk_name = "于彦",},
	[101183815] = { talk_id = 101183815, group_id = 1011838, type = 3, talk_content = "客套话嘛，懂得懂得！", talk_name = "于彦",},
	[101183901] = { talk_id = 101183901, group_id = 1011839, type = 2, options = {{"你在准备跑路吗？",101183903,},{"这些箱子是？",101183902,},}, talk_content = "要带的应该就这些了吧。", talk_name = "采桑子",},
	[101183902] = { talk_id = 101183902, group_id = 1011839, type = 3, next_id = 101183904, talk_content = "是些行李和谋生道具，毕竟我准备离开这地方了。", talk_name = "采桑子",},
	[101183903] = { talk_id = 101183903, group_id = 1011839, type = 3, next_id = 101183904, talk_content = "被你一眼看穿了啊，哈哈。", talk_name = "采桑子",},
	[101183904] = { talk_id = 101183904, group_id = 1011839, type = 1, next_id = 101183905, talk_content = "……我先问一下你该不会打算不付报酬吧？", talk_name = "<nick_name>",},
	[101183905] = { talk_id = 101183905, group_id = 1011839, type = 1, next_id = 101183906, talk_content = "要是我不想付的话早就溜了，何必把行李队堆这儿等青乌你呢。", talk_name = "采桑子",},
	[101183906] = { talk_id = 101183906, group_id = 1011839, type = 1, next_id = 101183907, talk_content = "那……", talk_name = "<nick_name>",},
	[101183907] = { talk_id = 101183907, group_id = 1011839, type = 2, options = {{"告知情况",101183908,},}, talk_content = "在谈报酬之前能先和我说说“售后”的情况吗？", talk_name = "采桑子",},
	[101183908] = { talk_id = 101183908, group_id = 1011839, type = 1, next_id = 101183909, talk_content = "看来委托你是正确的，事情都如我的“咨询”所说的那样在发展呢。", talk_name = "采桑子",},
	[101183909] = { talk_id = 101183909, group_id = 1011839, type = 1, next_id = 101183910, talk_content = "也只有沐优有那种迹象，另一位可是完全与学校无缘了，你这“神棍”。", talk_name = "<nick_name>",},
	[101183910] = { talk_id = 101183910, group_id = 1011839, type = 1, next_id = 101183911, talk_content = "说神棍那就多少有点污蔑人，我一直都说自己是咨询师而不是什么算命大师。", talk_name = "采桑子",},
	[101183911] = { talk_id = 101183911, group_id = 1011839, type = 1, next_id = 101183912, talk_content = "就算摊位长这样？", talk_name = "<nick_name>",},
	[101183912] = { talk_id = 101183912, group_id = 1011839, type = 1, next_id = 101183913, talk_content = "就算摊位长这样。", talk_name = "采桑子",},
	[101183913] = { talk_id = 101183913, group_id = 1011839, type = 1, next_id = 101183914, talk_content = "不过多亏了你的额外工作，他们这下离自己该走的路就近一些了。", talk_name = "采桑子",},
	[101183914] = { talk_id = 101183914, group_id = 1011839, type = 1, next_id = 101183915, talk_content = "另外……青乌，其实我之前就见过你。", talk_name = "采桑子",},
	[101183915] = { talk_id = 101183915, group_id = 1011839, type = 1, next_id = 101183916, talk_content = "……", talk_name = "<nick_name>",},
	[101183916] = { talk_id = 101183916, group_id = 1011839, type = 2, options = {{"我当时是什么样的？",101183918,},{"你和悬天是一伙的？",101183917,},}, talk_content = "你会怀疑也是当然的，我当时因为一时鬼迷心窍协助悬天运送了某件货物，而这件货物就是你了。", talk_name = "采桑子",},
	[101183917] = { talk_id = 101183917, group_id = 1011839, type = 3, next_id = 101183919, talk_content = "只是协助他们避开耳目而已，算不上什么合作者，不然我也不会在这里摆摊维生了。", talk_name = "采桑子",},
	[101183918] = { talk_id = 101183918, group_id = 1011839, type = 3, next_id = 101183919, talk_content = "外貌和现在差不多，但是不像是活着的……气脉没有在你体内流通，但看你这样子应该是我当时搞错了。", talk_name = "采桑子",},
	[101183919] = { talk_id = 101183919, group_id = 1011839, type = 3, talk_content = "总之在我被宗门的其他人发现之前我就因为好奇找到了一些关于你的资料……稍后打开短信吧，我把我知道的都说出来。", talk_name = "采桑子",},
	[101190101] = { talk_id = 101190101, group_id = 1011901, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"这些箱子是？",101190102,},{"你在准备跑路吗？",101190103,},}, talk_content = "要带的应该就这些了吧。", talk_name = "采桑子",},
	[101190102] = { talk_id = 101190102, group_id = 1011901, type = 3, next_id = 101190104, talk_content = "是些行李和谋生道具，毕竟我准备离开这地方了。", talk_name = "采桑子",},
	[101190103] = { talk_id = 101190103, group_id = 1011901, type = 3, next_id = 101190104, talk_content = "被你一眼看穿了啊，哈哈。", talk_name = "采桑子",},
