	[101190113] = { talk_id = 101190113, group_id = 1011901, type = 1, next_id = 101190114, talk_content = "总之，那位研究员肯定能继续研究，而退学的学生能发挥所学，这就已经足够了。", talk_name = "采桑子",},
	[101190114] = { talk_id = 101190114, group_id = 1011901, type = 1, next_id = 101190115, talk_content = "话说得够多了，青乌，再不跑路下一位投诉者就要出现了。", talk_name = "采桑子",},
