	[101200402] = { talk_id = 101200402, group_id = 1012004, type = 5, next_id = 101200403, aside = "<color=#FFEBA2>青乌：</color>有什么要在意的东西吗？", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 51, talk_content = "有什么要在意的东西吗？", talk_name = "青乌",},
	[101200403] = { talk_id = 101200403, group_id = 1012004, type = 5, next_id = 101200404, aside = "<color=#FFEBA2>小五：</color>恶索帮的人大概能随时引爆这里的炸弹，我们得做两手准备。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 51, talk_content = "恶索帮的人大概能随时引爆这里的炸弹，我们得做两手准备。", talk_name = "小五",},
