	[101200405] = { talk_id = 101200405, group_id = 1012004, type = 5, next_id = 101200406, aside = "<color=#FFEBA2>小五：</color>只是建议……你也可以直接到外面去，恶索帮的人都在天台。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 51, talk_content = "只是建议……你也可以直接到外面去，恶索帮的人都在天台。", talk_name = "小五",},
