	[101200701] = { talk_id = 101200701, group_id = 1012007, type = 5, next_id = 101200702, aside = "<color=#FFEBA2>帝江新闻：</color>虽然恶索帮的其中一位首脑被捕，但帮派丝毫没有减弱其作恶的力度，组织的崩溃似乎让他们陷入了盲目与疯狂。", fade_in = 1, fade_out = true, hide_player = true, not_skip = true, talk_content = "虽然恶索帮的其中一位首脑被捕，但帮派丝毫没有减弱其作恶的力度，组织的崩溃似乎让他们陷入了盲目与疯狂。", talk_name = "帝江新闻",},
	[101200702] = { talk_id = 101200702, group_id = 1012007, type = 5, next_id = 101200703, aside = "<color=#FFEBA2>帝江新闻：</color>截止至今日，以恶索帮为首的帮派已实施了多宗犯罪，其中引发的伤亡初步估计有百人以上。", talk_content = "截止至今日，以恶索帮为首的帮派已实施了多宗犯罪，其中引发的伤亡初步估计有百人以上。", talk_name = "帝江新闻",},
	[101200703] = { talk_id = 101200703, group_id = 1012007, type = 5, next_id = 101200704, aside = "<color=#FFEBA2>帝江新闻：</color>虽然打着人权自由等称号，但恶索帮的暴力行径已经让多数市民发出怨声，一些非恶索帮成员的禁闭者也因此遭到歧视对待。", talk_content = "虽然打着人权自由等称号，但恶索帮的暴力行径已经让多数市民发出怨声，一些非恶索帮成员的禁闭者也因此遭到歧视对待。", talk_name = "帝江新闻",},
	[101200704] = { talk_id = 101200704, group_id = 1012007, type = 5, aside = "<color=#FFEBA2>帝江新闻：</color>对此，巡卫组织举行了新闻发布会，并表示他们将联合天月城内的有志组织，尽全力解决恶索帮问题，还市民们一个和平的天月城。", talk_content = "对此，巡卫组织举行了新闻发布会，并表示他们将联合天月城内的有志组织，尽全力解决恶索帮问题，还市民们一个和平的天月城。", talk_name = "帝江新闻",},
	[101200801] = { talk_id = 101200801, group_id = 1012008, type = 1, next_id = 101200802, fade_in = 1, fade_out = true, hide_player = true, not_skip = true, talk_content = "使用环保且可循环气脉能源的公司，翡翠能源。", talk_name = "青乌",},
	[101200802] = { talk_id = 101200802, group_id = 1012008, type = 2, options = {{"（思考气脉）",101200803,},{"去看看别的吧。",101200808,},}, talk_content = "这宣传语看着还真官方。", talk_name = "青乌",},
	[101200803] = { talk_id = 101200803, group_id = 1012008, type = 3, next_id = 101200804, talk_content = "这个世界充盈着名为气脉的神秘能量。", talk_name = "青乌",},
	[101200804] = { talk_id = 101200804, group_id = 1012008, type = 3, next_id = 101200805, talk_content = "虽然不知源头，但这个能量的确为世界带来了便利，最简单的体现就是我战斗时使出的火焰。", talk_name = "青乌",},
	[101200805] = { talk_id = 101200805, group_id = 1012008, type = 3, next_id = 101200806, talk_content = "而刚刚的敌人，那些叫噬脉生物的东西就是专门蚕食气脉的怪物。", talk_name = "青乌",},
	[101200806] = { talk_id = 101200806, group_id = 1012008, type = 3, next_id = 101200807, talk_content = "但老实说现代人也会和这些怪物一样抽取气脉\n为自己所用。", talk_name = "青乌",},
	[101200807] = { talk_id = 101200807, group_id = 1012008, type = 3, talk_content = "两者的差异……也就只有是否节制这点了吗。", talk_name = "青乌",},
	[101200808] = { talk_id = 101200808, group_id = 1012008, type = 3, talk_content = "去看看别的吧。", talk_name = "青乌",},
	[101200901] = { talk_id = 101200901, group_id = 1012009, type = 1, next_id = 101200902, fade_in = 1, fade_out = true, hide_player = true, not_skip = true, talk_content = "建筑的内部线路图，这到底有多少层啊。", talk_name = "青乌",},
	[101200902] = { talk_id = 101200902, group_id = 1012009, type = 1, talk_content = "我所在的位置应该就是这个标红的顶楼了吧。", talk_name = "青乌",},
	[101201001] = { talk_id = 101201001, group_id = 1012010, type = 5, next_id = 101201002, aside = "<color=#FFEBA2>女性的声音：</color>恶索帮以夺回自由作为口号劫持了城内多个能源设施，请问对此巡卫们有什么对策呢？", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "恶索帮以夺回自由作为口号劫持了城内多个能源设施，请问对此巡卫们有什么对策呢？", talk_name = "女性的声音",},
	[101201002] = { talk_id = 101201002, group_id = 1012010, type = 5, next_id = 101201003, aside = "<color=#FFEBA2>男性的声音：</color>我们会以最快的速度夺回这些设施，包括一些支援者在内，我们已经出动了20队人马，\n在一个小时之内肯定能解决这个状况。", talk_content = "我们会以最快的速度夺回这些设施，包括一些支援者在内，我们已经出动了20队人马，\n在一个小时之内肯定能解决这个状况。", talk_name = "男性的声音",},
	[101201003] = { talk_id = 101201003, group_id = 1012010, type = 5, next_id = 101201004, aside = "<color=#FFEBA2>男性的声音：</color>恶索帮的禁闭者们你们听见了吧，我们没有那么多功夫陪你们过家家。", talk_content = "恶索帮的禁闭者们你们听见了吧，我们没有那么多功夫陪你们过家家。", talk_name = "男性的声音",},
	[101201004] = { talk_id = 101201004, group_id = 1012010, type = 5, next_id = 101201005, aside = "<color=#FFEBA2>女性的声音：</color>呃，直接用那个称呼应该……", talk_content = "呃，直接用那个称呼应该……", talk_name = "女性的声音",},
	[101201005] = { talk_id = 101201005, group_id = 1012010, type = 5, next_id = 101201006, aside = "<color=#FFEBA2>男性的声音：</color>不守规则的禁闭者没必要给什么面子，你们这些一辈子都不能到地上去的垃圾！", talk_content = "不守规则的禁闭者没必要给什么面子，你们这些一辈子都不能到地上去的垃圾！", talk_name = "男性的声音",},
	[101201006] = { talk_id = 101201006, group_id = 1012010, type = 5, next_id = 101201007, aside = "<color=#FFEBA2>女性的声音：</color>啊，信号似乎出现了问题……！", talk_content = "啊，信号似乎出现了问题……！", talk_name = "女性的声音",},
