	[101201008] = { talk_id = 101201008, group_id = 1012010, type = 5, aside = "<color=#FFEBA2>青乌：</color>这样的法令除了让市民不满以外根本没有任何作用，天月城的上层到底是怎么想的呢。", talk_content = "这样的法令除了让市民不满以外根本没有任何作用，天月城的上层到底是怎么想的呢。", talk_name = "青乌",},
	[101201101] = { talk_id = 101201101, group_id = 1012011, type = 5, next_id = 101201102, hide_player = true,},
	[101201102] = { talk_id = 101201102, group_id = 1012011, type = 5, next_id = 101201103,},
	[101201103] = { talk_id = 101201103, group_id = 1012011, type = 5, next_id = 101201104,},
	[101201104] = { talk_id = 101201104, group_id = 1012011, type = 5, next_id = 101201105,},
	[101201105] = { talk_id = 101201105, group_id = 1012011, type = 5, next_id = 101201106,},
	[101201106] = { talk_id = 101201106, group_id = 1012011, type = 5, next_id = 101201107,},
	[101201107] = { talk_id = 101201107, group_id = 1012011, type = 5,},
	[101201201] = { talk_id = 101201201, group_id = 1012012, type = 4, aside = "<color=#FFEBA2>青乌：</color>地上有个钥匙卡……是谁落在这里的吗？", not_skip = true, talk_content = "地上有个钥匙卡……是谁落在这里的吗？", talk_name = "青乌",},
	[101201301] = { talk_id = 101201301, group_id = 1012013, type = 1, fade_in = 1, fade_out = true, hide_player = true, not_skip = true, talk_content = "这门……开不了啊。", talk_name = "青乌",},
	[101201401] = { talk_id = 101201401, group_id = 1012014, type = 2, fade_in = 1, fade_out = true, hide_player = true, not_skip = true, options = {{"算了",},{"用",},}, talk_content = "刚刚的钥匙卡难不成能用在这……", talk_name = "青乌",},
	[101201501] = { talk_id = 101201501, group_id = 1012015, type = 1, fade_in = 1, fade_out = true, hide_player = true, not_skip = true, talk_content = "幸好有方法开门。", talk_name = "青乌",},
	[101201601] = { talk_id = 101201601, group_id = 1012016, type = 1, next_id = 101201602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "嘻哈，你好？", talk_name = "脉灵",},
	[101201602] = { talk_id = 101201602, group_id = 1012016, type = 1, next_id = 101201603, talk_content = "脉灵……和倒下的人，这怎么回事？", talk_name = "青乌",},
	[101201603] = { talk_id = 101201603, group_id = 1012016, type = 2, options = {{"<color=#FFEBA2>都发生了什么？</color>",101201604,},{"这个人是？",101201606,},{"你是什么？",101201608,},}, talk_content = "说来话长噢，总之这个人现在很虚弱，所以我得在这照顾他。", talk_name = "脉灵",},
	[101201604] = { talk_id = 101201604, group_id = 1012016, type = 3, next_id = 101201605, talk_content = "嘻哈，有可怕的人从电梯里面冲了出来，然后大家都打了起来。", talk_name = "脉灵",},
	[101201605] = { talk_id = 101201605, group_id = 1012016, type = 3, next_id = 101201610, talk_content = "然后，然后，我就和这个人躲进了这个小房间了，幸好这里很暖和呢。", talk_name = "脉灵",},
	[101201606] = { talk_id = 101201606, group_id = 1012016, type = 3, next_id = 101201607, talk_content = "或许是在这里工作的人噢，虽然有点生面孔。", talk_name = "脉灵",},
	[101201607] = { talk_id = 101201607, group_id = 1012016, type = 3, next_id = 101201610, talk_content = "嘻哈，不知道呢。", talk_name = "脉灵",},
	[101201608] = { talk_id = 101201608, group_id = 1012016, type = 3, next_id = 101201609, talk_content = "想问我的名字吗，朋友们都说不要告诉陌生人名字。", talk_name = "脉灵",},
	[101201609] = { talk_id = 101201609, group_id = 1012016, type = 3, next_id = 101201610, talk_content = "不过名字什么的都是没用的东西，大家终究都是会回到气脉的，嘻哈！", talk_name = "脉灵",},
	[101201610] = { talk_id = 101201610, group_id = 1012016, type = 1, next_id = 101201611, talk_content = "脉灵交谈起来大多是这感觉，估计再问也问不出来了……", talk_name = "青乌",},
	[101201611] = { talk_id = 101201611, group_id = 1012016, type = 1, next_id = 101201612, talk_content = "我看出了你渴望的眼神，嘻哈，假如你不伤害这个人的话可以搜他身噢。", talk_name = "脉灵",},
	[101201612] = { talk_id = 101201612, group_id = 1012016, type = 1, talk_content = "那真是多谢了。", talk_name = "青乌",},
	[101201701] = { talk_id = 101201701, group_id = 1012017, type = 1, next_id = 101201702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "嘻哈，有人害怕，有人紧张，有人快乐，有人充满期待。", talk_name = "脉灵",},
	[101201702] = { talk_id = 101201702, group_id = 1012017, type = 1, next_id = 101201703, talk_content = "你在说什么？", talk_name = "青乌",},
	[101201703] = { talk_id = 101201703, group_id = 1012017, type = 1, talk_content = "我在说这里的事情。", talk_name = "脉灵",},
	[101201801] = { talk_id = 101201801, group_id = 1012018, type = 1, next_id = 101201802, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "这个人为什么会倒在这里……", talk_name = "青乌",},
	[101201802] = { talk_id = 101201802, group_id = 1012018, type = 1, talk_content = "就如脉灵说的那样来同调试试吧。", talk_name = "青乌",},
	[101201901] = { talk_id = 101201901, group_id = 1012019, type = 5, next_id = 101201902, aside = "<color=#FFEBA2>？？？：</color>喂，这么长时间没联系你到底哪去了，有拦住恶索帮的人吗？", call_icon = "Textures/Icon/Single/CampIcon/Weizhizhenyin.png", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "喂，这么长时间没联系你到底哪去了，有拦住恶索帮的人吗？", talk_name = "？？？",},
	[101201902] = { talk_id = 101201902, group_id = 1012019, type = 5, next_id = 101201903, aside = "<color=#FFEBA2>青乌：</color>你问的人现在受伤昏迷了。先说好，我也是来处理恶索帮的人的。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "你问的人现在受伤昏迷了。先说好，我也是来处理恶索帮的人的。", talk_name = "青乌",},
	[101201903] = { talk_id = 101201903, group_id = 1012019, type = 5, next_id = 101201904, aside = "<color=#FFEBA2>？？？：</color>……他现在没事吧。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "……他现在没事吧。", talk_name = "？？？",},
	[101201904] = { talk_id = 101201904, group_id = 1012019, type = 5, next_id = 101201905, aside = "<color=#FFEBA2>青乌：</color>看着没大碍，但暂时应该醒不过来了，你们又是谁。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "看着没大碍，但暂时应该醒不过来了，你们又是谁。", talk_name = "青乌",},
	[101201905] = { talk_id = 101201905, group_id = 1012019, type = 5, next_id = 101201906, aside = "<color=#FFEBA2>？？？：</color>一个危机处理小组而已，我想你应该是个挺有能力的人，能答应我一个请求吗？", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "一个危机处理小组而已，我想你应该是个挺有能力的人，能答应我一个请求吗？", talk_name = "？？？",},
	[101201906] = { talk_id = 101201906, group_id = 1012019, type = 5, next_id = 101201907, aside = "<color=#FFEBA2>青乌：</color>说说看？", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "说说看？", talk_name = "青乌",},
	[101201907] = { talk_id = 101201907, group_id = 1012019, type = 5, next_id = 101201908, aside = "<color=#FFEBA2>？？？：</color>楼内的炸弹量稍有些多，要是引爆恐怕周遭都无人能幸免。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "楼内的炸弹量稍有些多，要是引爆恐怕周遭都无人能幸免。", talk_name = "？？？",},
	[101201908] = { talk_id = 101201908, group_id = 1012019, type = 5, next_id = 101201909, aside = "<color=#FFEBA2>？？？：</color>所以我们找来了专业的拆弹专家，但你也知道拆除是需要时间的，用尽一切办法拖住那群疯子，可以吧。", child_type = 51, talk_content = "所以我们找来了专业的拆弹专家，但你也知道拆除是需要时间的，用尽一切办法拖住那群疯子，可以吧。", talk_name = "？？？",},
	[101201909] = { talk_id = 101201909, group_id = 1012019, type = 5, next_id = 101201910, aside = "<color=#FFEBA2>青乌：</color>……如果我说不的话？", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "……如果我说不的话？", talk_name = "青乌",},
	[101201910] = { talk_id = 101201910, group_id = 1012019, type = 5, next_id = 101201911, aside = "<color=#FFEBA2>？？？：</color>因为某些原因我们已经开始了行动，所以我才会说这是个请求而不是要求。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "因为某些原因我们已经开始了行动，所以我才会说这是个请求而不是要求。", talk_name = "？？？",},
	[101201911] = { talk_id = 101201911, group_id = 1012019, type = 5, next_id = 101201912, aside = "<color=#FFEBA2>？？？：</color>请你随身带着这部电话，在成功之后我们会直接拨通它。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "请你随身带着这部电话，在成功之后我们会直接拨通它。", talk_name = "？？？",},
	[101201912] = { talk_id = 101201912, group_id = 1012019, type = 5, aside = "<color=#FFEBA2>青乌：</color>……", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "……", talk_name = "青乌",},
	[101202001] = { talk_id = 101202001, group_id = 1012020, type = 5, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true,},
	[101202101] = { talk_id = 101202101, group_id = 1012021, type = 4, next_id = 101202102, not_skip = true,},
	[101202102] = { talk_id = 101202102, group_id = 1012021, type = 4, next_id = 101202103, aside = "<color=#FFEBA2>青乌：</color>敌人有三个是吧……", talk_content = "敌人有三个是吧……", talk_name = "青乌",},
	[101202103] = { talk_id = 101202103, group_id = 1012021, type = 4, aside = "<color=#FFEBA2>青乌：</color>直接应付应该没问题。", talk_content = "直接应付应该没问题。", talk_name = "青乌",},
	[101202201] = { talk_id = 101202201, group_id = 1012022, type = 4, next_id = 101202202, aside = "<color=#FFEBA2>小五：</color>悬天是这次的幕后黑手，青乌。", call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, not_skip = true, talk_content = "悬天是这次的幕后黑手，青乌。", talk_name = "小五",},
	[101202202] = { talk_id = 101202202, group_id = 1012022, type = 4, next_id = 101202203, aside = "<color=#FFEBA2>小五：</color>他们之前就和恶索帮搭上了线，并且为这次行动提供了不少物资。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "他们之前就和恶索帮搭上了线，并且为这次行动提供了不少物资。", talk_name = "小五",},
	[101202203] = { talk_id = 101202203, group_id = 1012022, type = 4, next_id = 101202204, aside = "<color=#FFEBA2>青乌：</color>又是这群人，他们这次想干什么？", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "又是这群人，他们这次想干什么？", talk_name = "青乌",},
	[101202204] = { talk_id = 101202204, group_id = 1012022, type = 4, next_id = 101202205, aside = "<color=#FFEBA2>小五：</color>他们想要换一个统治天月城的人。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "他们想要换一个统治天月城的人。", talk_name = "小五",},
	[101202205] = { talk_id = 101202205, group_id = 1012022, type = 4, next_id = 101202206, aside = "<color=#FFEBA2>小五：</color>青乌，悬天答应了会协助恶索帮的禁闭者获取自由，送他们离开天月城。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "青乌，悬天答应了会协助恶索帮的禁闭者获取自由，送他们离开天月城。", talk_name = "小五",},
	[101202206] = { talk_id = 101202206, group_id = 1012022, type = 4, next_id = 101202207, aside = "<color=#FFEBA2>青乌：</color>离开天月城……就是要用空港吗？", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "离开天月城……就是要用空港吗？", talk_name = "青乌",},
	[101202207] = { talk_id = 101202207, group_id = 1012022, type = 4, next_id = 101202208, aside = "<color=#FFEBA2>小五：</color>用不了，在能源被截断以后空港就只能放出普通的飞机，但绝对无法安全跨越太阳形成的“境界线”。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "用不了，在能源被截断以后空港就只能放出普通的飞机，但绝对无法安全跨越太阳形成的“境界线”。", talk_name = "小五",},
	[101202208] = { talk_id = 101202208, group_id = 1012022, type = 4, next_id = 101202209, aside = "<color=#FFEBA2>小五：</color>而且在事成以后悬天应该会沿用原本的规则，继续限制禁闭者们的自由。", child_type = 41, talk_content = "而且在事成以后悬天应该会沿用原本的规则，继续限制禁闭者们的自由。", talk_name = "小五",},
	[101202209] = { talk_id = 101202209, group_id = 1012022, type = 4, next_id = 101202210, aside = "<color=#FFEBA2>青乌：</color>我想起了一个词，两面三刀。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "我想起了一个词，两面三刀。", talk_name = "青乌",},
	[101202210] = { talk_id = 101202210, group_id = 1012022, type = 4, aside = "<color=#FFEBA2>小五：</color>现场调查得差不多了就到天台上去吧，不能让悬天得逞。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "现场调查得差不多了就到天台上去吧，不能让悬天得逞。", talk_name = "小五",},
	[101202301] = { talk_id = 101202301, group_id = 1012023, type = 2, fade_in = 1, fade_out = true, hide_player = true, not_skip = true, options = {{"否",101202303,},{"是",101202302,},}, talk_content = "好，内部已经没什么值得注意的东西了，准备一下就出去吧。", talk_name = "青乌",},
	[101202302] = { talk_id = 101202302, group_id = 1012023, type = 3, talk_content = "走吧。", talk_name = "青乌",},
	[101202303] = { talk_id = 101202303, group_id = 1012023, type = 3, talk_content = "再等一下吧。", talk_name = "青乌",},
	[101202401] = { talk_id = 101202401, group_id = 1012024, type = 5, next_id = 101202402, aside = "<color=#FFEBA2>恶索帮成员乙：</color>喂，有人来了！", fade_in = 1, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "喂，有人来了！", talk_name = "恶索帮成员乙",},
	[101202402] = { talk_id = 101202402, group_id = 1012024, type = 5, next_id = 101202403, aside = "<color=#FFEBA2>恶索帮成员甲：</color>别过来，你这家伙是什么人！", talk_content = "别过来，你这家伙是什么人！", talk_name = "恶索帮成员甲",},
	[101202403] = { talk_id = 101202403, group_id = 1012024, type = 5, next_id = 101202404, aside = "<color=#FFEBA2>青乌：</color>你可以叫我青乌，如你所见我是位脉者。", talk_content = "你可以叫我青乌，如你所见我是位脉者。", talk_name = "青乌",},
	[101202404] = { talk_id = 101202404, group_id = 1012024, type = 5, next_id = 101202405,},
	[101202405] = { talk_id = 101202405, group_id = 1012024, type = 2, child_type = 21, options = {{"态度温和",101202406,},{"态度强硬",101202409,},}, talk_content = "（那个恐怕就是炸弹的遥控器……想办法先让他们冷静下来吧。）", talk_name = "青乌",},
	[101202406] = { talk_id = 101202406, group_id = 1012024, type = 3, next_id = 101202407, icon = "interaction_4", talk_content = "我来这里是为了和你们谈谈，你们到底是怎么想的我现在还不清楚。", talk_name = "青乌",},
	[101202407] = { talk_id = 101202407, group_id = 1012024, type = 3, next_id = 101202408, talk_content = "但我认为直接炸了这里并不是什么好方法，对吧。", talk_name = "青乌",},
	[101202408] = { talk_id = 101202408, group_id = 1012024, type = 3, talk_content = "……有什么好谈的，就算再怎么谈你也帮不了我们！", talk_name = "恶索帮成员丙",},
	[101202409] = { talk_id = 101202409, group_id = 1012024, type = 3, next_id = 101202410, icon = "interaction_4", talk_content = "那自然是来阻止你们的了，对此我不介意使用武力。", talk_name = "青乌",},
	[101202410] = { talk_id = 101202410, group_id = 1012024, type = 3, next_id = 101202411, talk_content = "威吓我们也没用，只要我们按下……", talk_name = "恶索帮成员丙",},
	[101202411] = { talk_id = 101202411, group_id = 1012024, type = 3, talk_content = "真引爆了的话那你们就没有回头路可以走了，你们想清楚了吗？", talk_name = "青乌",},
	[101202501] = { talk_id = 101202501, group_id = 1012025, type = 1, next_id = 101202502, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "别以为几句话就能让我们收手，当我们决定要做这件事情的时候就已经知道后果是什么了。", talk_name = "恶索帮成员丙",},
	[101202502] = { talk_id = 101202502, group_id = 1012025, type = 1, next_id = 101202503, talk_content = "是，是啊，我们为了自由可以献出一切！", talk_name = "恶索帮成员乙",},
	[101202503] = { talk_id = 101202503, group_id = 1012025, type = 1, next_id = 101202504, talk_content = "对于你们来说自由到底是什么？", talk_name = "青乌",},
	[101202504] = { talk_id = 101202504, group_id = 1012025, type = 2, child_type = 21, options = {{"呵斥",101202505,},{"现实",101202509,},{"共情",101202512,},}, options_pos = 1, talk_content = "那自然就是离开天月城去真正的地上了，为了打通这条道路我无论如何都要引爆这个地方，让行动成功。", talk_name = "恶索帮成员甲",},
	[101202505] = { talk_id = 101202505, group_id = 1012025, type = 3, next_id = 101202506, icon = "interaction_4", talk_content = "你们这些生养在天月城的人真的有办法在天月城外生活吗，帝江地上遍布危机，不是脉者的你们有办法活下去吗？", talk_name = "青乌",},
	[101202506] = { talk_id = 101202506, group_id = 1012025, type = 3, next_id = 101202507, talk_content = "那就去其他的城市……", talk_name = "恶索帮成员丙",},
	[101202507] = { talk_id = 101202507, group_id = 1012025, type = 3, next_id = 101202508, talk_content = "有城市会接受你们这样的罪人吗！", talk_name = "青乌",},
	[101202508] = { talk_id = 101202508, group_id = 1012025, type = 3, talk_content = "这……", talk_name = "恶索帮成员丙",},
	[101202509] = { talk_id = 101202509, group_id = 1012025, type = 3, next_id = 101202510, icon = "interaction_4", talk_content = "你们获得的不会是自由，只会是无尽的追捕而已。", talk_name = "青乌",},
	[101202510] = { talk_id = 101202510, group_id = 1012025, type = 3, next_id = 101202511, talk_content = "那又如何，我们这一辈子本来就被人监视，多点追兵又怎么样，你觉得我们会在意吗？", talk_name = "恶索帮成员丙",},
	[101202511] = { talk_id = 101202511, group_id = 1012025, type = 3, talk_content = "（冥顽不灵。）", talk_name = "青乌",},
	[101202512] = { talk_id = 101202512, group_id = 1012025, type = 3, next_id = 101202513, icon = "interaction_4", talk_content = "想要行动成功那你们就只能炸了这里，但爆炸之后你们大概也无法脱身。", talk_name = "青乌",},
	[101202513] = { talk_id = 101202513, group_id = 1012025, type = 3, next_id = 101202514, talk_content = "自然，最终去到地上的也不会是你们，这真是你们想要的自由吗。", talk_name = "青乌",},
	[101202514] = { talk_id = 101202514, group_id = 1012025, type = 3, next_id = 101202515, talk_content = "我们说了，这是我们早就知道的结果！", talk_name = "恶索帮成员丙",},
	[101202515] = { talk_id = 101202515, group_id = 1012025, type = 3, next_id = 101202516, talk_content = "那你们真能接受这样的结果吗！", talk_name = "青乌",},
	[101202516] = { talk_id = 101202516, group_id = 1012025, type = 3, talk_content = "这……", talk_name = "恶索帮成员丙",},
	[101202601] = { talk_id = 101202601, group_id = 1012026, type = 1, next_id = 101202602, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "自从太阳帝江带着天月城升起以后，封侯发布的禁闭令已经将好几代人困在这个名为天月城的牢笼之中。", talk_name = "恶索帮成员甲",},
	[101202602] = { talk_id = 101202602, group_id = 1012026, type = 1, next_id = 101202603, talk_content = "最初大家都认为封侯有别的想法，这只是在确认地上平安前做的一个特殊处理。", talk_name = "恶索帮成员甲",},
	[101202603] = { talk_id = 101202603, group_id = 1012026, type = 1, next_id = 101202604, talk_content = "但很快大家就明白封侯只是单纯想将禁闭者们永远关在天月城而已，就连一句解释也没有！", talk_name = "恶索帮成员甲",},
	[101202604] = { talk_id = 101202604, group_id = 1012026, type = 1, next_id = 101202605, talk_content = "离开天月城，到地上去是我们禁闭者一代又一代的愿望。", talk_name = "恶索帮成员甲",},
	[101202605] = { talk_id = 101202605, group_id = 1012026, type = 2, child_type = 21, options = {{"不屑",101202607,},{"劝阻",101202609,},{"“转移注意力”",101202614,},}, options_pos = 1, talk_content = "青乌，无论付出什么代价我们都会完成这个愿望！", talk_name = "恶索帮成员甲",},
	[101202607] = { talk_id = 101202607, group_id = 1012026, type = 3, next_id = 101202608, icon = "d_1001", talk_content = "看来跟你们这群疯子没什么好说的了。", talk_name = "青乌",},
	[101202608] = { talk_id = 101202608, group_id = 1012026, type = 3, talk_content = "哼，说到底还是要打对吧！", talk_name = "恶索帮成员丙",},
	[101202609] = { talk_id = 101202609, group_id = 1012026, type = 3, next_id = 101202610, icon = "interaction_4", talk_content = "暴力并不是解决这个问题的最好办法，你们难道不知道自己的所作所为已经引起了大部分人反感了吗？", talk_name = "青乌",},
	[101202610] = { talk_id = 101202610, group_id = 1012026, type = 3, next_id = 101202611, talk_content = "这不正好，反正我们也没打算让他们喜欢！", talk_name = "恶索帮成员丙",},
	[101202611] = { talk_id = 101202611, group_id = 1012026, type = 3, next_id = 101202612, talk_content = "那在你们之后的禁闭者要怎么办，一个恶索帮走了，剩下的禁闭者要怎么办？", talk_name = "青乌",},
	[101202612] = { talk_id = 101202612, group_id = 1012026, type = 3, next_id = 101202613, talk_content = "你们的自由就是夺走其他人的自由是吗！", talk_name = "青乌",},
	[101202613] = { talk_id = 101202613, group_id = 1012026, type = 3, talk_content = "我们是……", talk_name = "恶索帮成员丙",},
	[101202614] = { talk_id = 101202614, group_id = 1012026, type = 3, next_id = 101202615, icon = "interaction_4", talk_content = "但这个愿望真的能实现吗，帝江的简介册子上写过一段让我很在意的话。", talk_name = "青乌",},
	[101202615] = { talk_id = 101202615, group_id = 1012026, type = 3, next_id = 101202616, talk_content = "禁闭者不可能离开天月城。", talk_name = "青乌",},
	[101202616] = { talk_id = 101202616, group_id = 1012026, type = 3, next_id = 101202617, talk_content = "还以为你要说什么，那种不过是骗小孩的屁话，你该不会是以为我们会怕这个吧？", talk_name = "恶索帮成员丙",},
	[101202617] = { talk_id = 101202617, group_id = 1012026, type = 3, talk_content = "（虽然只是突然想起的一句……但总觉得很让人在意。）", talk_name = "青乌",},
	[101202701] = { talk_id = 101202701, group_id = 1012027, type = 4, next_id = 101202702, aside = "<color=#FFEBA2>恶索帮成员甲：</color>我立刻准备引爆，你们两个护着我！", not_skip = true, talk_content = "我立刻准备引爆，你们两个护着我！", talk_name = "恶索帮成员甲",},
	[101202702] = { talk_id = 101202702, group_id = 1012027, type = 4, next_id = 101202703, aside = "<color=#FFEBA2>青乌：</color>得在爆炸之前阻止他！", talk_content = "得在爆炸之前阻止他！", talk_name = "青乌",},
	[101202703] = { talk_id = 101202703, group_id = 1012027, type = 4, next_id = 101202704, aside = "<color=#FFEBA2>恶索帮成员丙：</color>听见没有，快和我一起顶上去！", talk_content = "听见没有，快和我一起顶上去！", talk_name = "恶索帮成员丙",},
	[101202704] = { talk_id = 101202704, group_id = 1012027, type = 4, aside = "<color=#FFEBA2>恶索帮成员乙：</color>只要撑住就能帮到大家……", talk_content = "只要撑住就能帮到大家……", talk_name = "恶索帮成员乙",},
	[101202801] = { talk_id = 101202801, group_id = 1012028, type = 5, next_id = 101202802, aside = "<color=#FFEBA2>恶索帮成员甲：</color>可恶……就算你击败了我们……", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "可恶……就算你击败了我们……", talk_name = "恶索帮成员甲",},
	[101202802] = { talk_id = 101202802, group_id = 1012028, type = 5, next_id = 101202803, aside = "<color=#FFEBA2>青乌：</color>有话去牢里再说吧。", talk_content = "有话去牢里再说吧。", talk_name = "青乌",},
	[101202803] = { talk_id = 101202803, group_id = 1012028, type = 5, next_id = 101202804, aside = "<color=#FFEBA2>青乌：</color>小五，事情算是解决了，接下来该怎么办。", talk_content = "小五，事情算是解决了，接下来该怎么办。", talk_name = "青乌",},
	[101202804] = { talk_id = 101202804, group_id = 1012028, type = 5, next_id = 101202805, aside = "<color=#FFEBA2>小五：</color>悬天。", talk_content = "悬天。", talk_name = "小五",},
	[101202805] = { talk_id = 101202805, group_id = 1012028, type = 5, next_id = 101202806, aside = "<color=#FFEBA2>青乌：</color>也是呢……", talk_content = "也是呢……", talk_name = "青乌",},
	[101202806] = { talk_id = 101202806, group_id = 1012028, type = 5, aside = "<color=#FFEBA2>小五：</color>那走吧，去继续我们的故事。", talk_content = "那走吧，去继续我们的故事。", talk_name = "小五",},
	[101202901] = { talk_id = 101202901, group_id = 1012029, type = 5, next_id = 101202902, aside = "<color=#FFEBA2>青乌：</color>别挡路！", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "别挡路！", talk_name = "青乌",},
	[101202902] = { talk_id = 101202902, group_id = 1012029, type = 5, next_id = 101202903, aside = "<color=#FFEBA2>恶索帮成员甲：</color>没人可以阻止恶索帮，阻止我们！", talk_content = "没人可以阻止恶索帮，阻止我们！", talk_name = "恶索帮成员甲",},
	[101202903] = { talk_id = 101202903, group_id = 1012029, type = 5, next_id = 101202904, aside = "<color=#FFEBA2>青乌：</color>什么？", talk_content = "什么？", talk_name = "青乌",},
	[101202904] = { talk_id = 101202904, group_id = 1012029, type = 5, next_id = 101202905,},
	[101202905] = { talk_id = 101202905, group_id = 1012029, type = 5, next_id = 101202906,},
	[101202906] = { talk_id = 101202906, group_id = 1012029, type = 5, aside = "<color=#FFEBA2>小五：</color>青乌，青乌！", talk_content = "青乌，青乌！", talk_name = "小五",},
	[101203001] = { talk_id = 101203001, group_id = 1012030, type = 1, next_id = 101203002, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "我们已经聊得够多了，我也知道你应该不是巡卫或者是封侯那边的人。", talk_name = "恶索帮成员甲",},
	[101203002] = { talk_id = 101203002, group_id = 1012030, type = 1, next_id = 101203003, talk_content = "？", talk_name = "青乌",},
	[101203003] = { talk_id = 101203003, group_id = 1012030, type = 1, next_id = 101203004, talk_content = "我是说我不打算和你进行无谓的争执，只要你愿意离开这里的话，那在你离开之前我都不会引爆。", talk_name = "恶索帮成员甲",},
	[101203004] = { talk_id = 101203004, group_id = 1012030, type = 2, child_type = 21, options = {{"劝阻",101203005,},{"共情",101203008,},{"愤怒",101203013,},}, options_pos = 1, talk_content = "（……）", talk_name = "青乌",},
	[101203005] = { talk_id = 101203005, group_id = 1012030, type = 3, next_id = 101203006, icon = "interaction_4", talk_content = "既然有放过别人的想法那你也尝试放过自己如何。", talk_name = "青乌",},
	[101203006] = { talk_id = 101203006, group_id = 1012030, type = 3, next_id = 101203007, talk_content = "除了自爆以外应该还有更好的方法吧！", talk_name = "青乌",},
	[101203007] = { talk_id = 101203007, group_id = 1012030, type = 3, talk_content = "就是没有所以我们才会站在这里！", talk_name = "恶索帮成员甲",},
	[101203008] = { talk_id = 101203008, group_id = 1012030, type = 3, next_id = 101203009, icon = "interaction_4", talk_content = "听我说，我在天月城生活的这段时间也见到了不少像你们这样的禁闭者，也和其中的一些成为了好朋友。", talk_name = "青乌",},
	[101203009] = { talk_id = 101203009, group_id = 1012030, type = 3, next_id = 101203010, talk_content = "我从他们身上能感受到禁闭者对于大地的渴望。", talk_name = "青乌",},
	[101203010] = { talk_id = 101203010, group_id = 1012030, type = 3, next_id = 101203011, talk_content = "你想说什么。", talk_name = "恶索帮成员甲",},
	[101203011] = { talk_id = 101203011, group_id = 1012030, type = 3, next_id = 101203012, talk_content = "我不打算反对你们的思想，但我也不能赞同你们的行动。", talk_name = "青乌",},
	[101203012] = { talk_id = 101203012, group_id = 1012030, type = 3, talk_content = "我希望你们能够投降，这就是我的想法。", talk_name = "青乌",},
	[101203013] = { talk_id = 101203013, group_id = 1012030, type = 3, next_id = 101203014, icon = "interaction_4", talk_content = "你们把我当什么人，引爆后出事的只会是你们，再说你认为放跑我一个人就能抵消你们准备犯下的错误吗。", talk_name = "青乌",},
	[101203014] = { talk_id = 101203014, group_id = 1012030, type = 3, next_id = 101203015, talk_content = "你这脉者，好心放你走，哪那么多废话！", talk_name = "恶索帮成员丙",},
	[101203015] = { talk_id = 101203015, group_id = 1012030, type = 3, next_id = 101203016, talk_content = "我是在问你们到底想做什么，想放我走不就证明你们觉得自己做的事情是错的吗！", talk_name = "青乌",},
	[101203016] = { talk_id = 101203016, group_id = 1012030, type = 3, talk_content = "呃……", talk_name = "恶索帮成员甲",},
	[101203101] = { talk_id = 101203101, group_id = 1012031, type = 5, next_id = 101203102, aside = "<color=#FFEBA2>恶索帮成员甲：</color>我明白你要说的事情，但我们已经没法回头了……像你这样的脉者根本就不懂我们一分一毫的苦。", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "我明白你要说的事情，但我们已经没法回头了……像你这样的脉者根本就不懂我们一分一毫的苦。", talk_name = "恶索帮成员甲",},
	[101203102] = { talk_id = 101203102, group_id = 1012031, type = 5, next_id = 101203103, aside = "<color=#FFEBA2>青乌：</color>住手！", talk_content = "住手！", talk_name = "青乌",},
	[101203103] = { talk_id = 101203103, group_id = 1012031, type = 5, next_id = 101203104, aside = "<color=#FFEBA2>青乌：</color>什么？", talk_content = "什么？", talk_name = "青乌",},
	[101203104] = { talk_id = 101203104, group_id = 1012031, type = 5, next_id = 101203105,},
	[101203105] = { talk_id = 101203105, group_id = 1012031, type = 5, next_id = 101203106,},
	[101203106] = { talk_id = 101203106, group_id = 1012031, type = 5, aside = "<color=#FFEBA2>小五：</color>青乌，青乌！", talk_content = "青乌，青乌！", talk_name = "小五",},
	[101203201] = { talk_id = 101203201, group_id = 1012032, type = 1, next_id = 101203202, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "到约定的引爆时间了……", talk_name = "恶索帮成员乙",},
	[101203202] = { talk_id = 101203202, group_id = 1012032, type = 1, next_id = 101203203, talk_content = "那就没必要再和这脉者讲废话了，喂，立刻引爆吧！", talk_name = "恶索帮成员丙",},
	[101203203] = { talk_id = 101203203, group_id = 1012032, type = 1, next_id = 101203204, talk_content = "嗯，只要我们和其他人都引爆的话就能让城里能源崩溃……", talk_name = "恶索帮成员乙",},
	[101203204] = { talk_id = 101203204, group_id = 1012032, type = 1, next_id = 101203205, talk_content = "这样一来大家就能离开天月城，离开这个地方！", talk_name = "恶索帮成员甲",},
	[101203205] = { talk_id = 101203205, group_id = 1012032, type = 2, child_type = 21, options = {{"喝止",101203206,},{"说出真相",101203207,},{"拖延时间",101203208,},{"欺骗",101203209,},}, options_pos = 1, talk_content = "（……）", talk_name = "青乌",},
	[101203206] = { talk_id = 101203206, group_id = 1012032, type = 3, icon = "interaction_4", talk_content = "应该还有别的方法，你们别按下去！", talk_name = "青乌",},
	[101203207] = { talk_id = 101203207, group_id = 1012032, type = 3, icon = "interaction_4", talk_content = "（就这样做吧。）", talk_name = "青乌",},
	[101203208] = { talk_id = 101203208, group_id = 1012032, type = 3, icon = "interaction_4", talk_content = "（刚刚有个危机小组说他们正在处理炸弹……要想办法拖延时间。）", talk_name = "青乌",},
	[101203209] = { talk_id = 101203209, group_id = 1012032, type = 3, icon = "interaction_4", talk_content = "（只能这样了。）", talk_name = "青乌",},
	[101203301] = { talk_id = 101203301, group_id = 1012033, type = 1, next_id = 101203302, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "但悬天从一开始就没打算让你们离开这！", talk_name = "青乌",},
	[101203302] = { talk_id = 101203302, group_id = 1012033, type = 1, next_id = 101203303, talk_content = "这家伙知道我们背后是悬天吗？", talk_name = "恶索帮成员丙",},
	[101203303] = { talk_id = 101203303, group_id = 1012033, type = 1, next_id = 101203304, talk_content = "……你是说悬天欺骗了我们？", talk_name = "恶索帮成员甲",},
	[101203304] = { talk_id = 101203304, group_id = 1012033, type = 1, next_id = 101203305, talk_content = "悬天的目的是换个顺从的人来统治天月城，但其余的一切他们都没打算改变。", talk_name = "青乌",},
	[101203305] = { talk_id = 101203305, group_id = 1012033, type = 1, next_id = 101203306, talk_content = "口说无凭，你有什么证据。", talk_name = "恶索帮成员甲",},
	[101203306] = { talk_id = 101203306, group_id = 1012033, type = 1, next_id = 101203307, talk_content = "那他们有没有说过打算用什么方法送你们离开天月城。", talk_name = "青乌",},
	[101203307] = { talk_id = 101203307, group_id = 1012033, type = 1, next_id = 101203308, talk_content = "在城市能源崩溃，空港停摆以后，你们到底要怎么离开天月城！", talk_name = "青乌",},
	[101203308] = { talk_id = 101203308, group_id = 1012033, type = 1, next_id = 101203309, talk_content = "这……或许悬天有特别的工具。", talk_name = "恶索帮成员乙",},
	[101203309] = { talk_id = 101203309, group_id = 1012033, type = 1, next_id = 101203310, talk_content = "那你们见过吗。", talk_name = "青乌",},
	[101203310] = { talk_id = 101203310, group_id = 1012033, type = 2, child_type = 21, options = {{"劝阻",101203311,},{"强硬",101203314,},}, options_pos = 1, talk_content = "呃……", talk_name = "恶索帮成员乙",},
	[101203311] = { talk_id = 101203311, group_id = 1012033, type = 3, next_id = 101203312, icon = "interaction_4", talk_content = "就算你们的行动成功，炸毁了这个地方，但你们，禁闭者们的生活都不会发生任何改变。", talk_name = "青乌",},
	[101203312] = { talk_id = 101203312, group_id = 1012033, type = 3, next_id = 101203313, talk_content = "……", talk_name = "恶索帮成员甲",},
	[101203313] = { talk_id = 101203313, group_id = 1012033, type = 3, talk_content = "放弃吧，现在收手还来得及。", talk_name = "青乌",},
	[101203314] = { talk_id = 101203314, group_id = 1012033, type = 3, next_id = 101203315, icon = "interaction_4", talk_content = "我不会劝你们投降，但是我要说你们的行动不会得到任何结果。", talk_name = "青乌",},
	[101203315] = { talk_id = 101203315, group_id = 1012033, type = 3, next_id = 101203316, talk_content = "你们所做的一切最终只会变成悬天掌握城市的垫脚石，而禁闭者们还是会和以前一样永远囚禁在天月城之中。", talk_name = "青乌",},
	[101203316] = { talk_id = 101203316, group_id = 1012033, type = 3, next_id = 101203317, talk_content = "别听这脉者废话，赶紧按下去！", talk_name = "恶索帮成员丙",},
	[101203317] = { talk_id = 101203317, group_id = 1012033, type = 3, next_id = 101203318, talk_content = "有自爆的骨气还不如现在就去找悬天算账，你们好好想想自己到底想要些什么！", talk_name = "青乌",},
	[101203318] = { talk_id = 101203318, group_id = 1012033, type = 3, next_id = 101203319, talk_content = "……", talk_name = "恶索帮成员甲",},
	[101203319] = { talk_id = 101203319, group_id = 1012033, type = 3, next_id = 101203320, talk_content = "你能帮我们吗？", talk_name = "恶索帮成员甲",},
	[101203320] = { talk_id = 101203320, group_id = 1012033, type = 3, talk_content = "我会的。", talk_name = "青乌",},
	[101203401] = { talk_id = 101203401, group_id = 1012034, type = 5, next_id = 101203402, aside = "<color=#FFEBA2>青乌：</color>小五，事情解决了。", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "小五，事情解决了。", talk_name = "青乌",},
	[101203402] = { talk_id = 101203402, group_id = 1012034, type = 5, next_id = 101203403, aside = "<color=#FFEBA2>小五：</color>嗯，得到了个不错的结局。", talk_content = "嗯，得到了个不错的结局。", talk_name = "小五",},
	[101203403] = { talk_id = 101203403, group_id = 1012034, type = 5, next_id = 101203404, aside = "<color=#FFEBA2>青乌：</color>之后该怎么处理，恶索帮的人只是被利用了吧。", talk_content = "之后该怎么处理，恶索帮的人只是被利用了吧。", talk_name = "青乌",},
	[101203404] = { talk_id = 101203404, group_id = 1012034, type = 5, next_id = 101203405, aside = "<color=#FFEBA2>小五：</color>那自然是去对付幕后黑手了。", talk_content = "那自然是去对付幕后黑手了。", talk_name = "小五",},
	[101203405] = { talk_id = 101203405, group_id = 1012034, type = 5, aside = "<color=#FFEBA2>小五：</color>那走吧，去继续我们的故事。", talk_content = "那走吧，去继续我们的故事。", talk_name = "小五",},
	[101203501] = { talk_id = 101203501, group_id = 1012035, type = 1, next_id = 101203502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "（刚刚有个危机小组说他们正在处理炸弹……要办法拖延时间。）", talk_name = "青乌",},
	[101203502] = { talk_id = 101203502, group_id = 1012035, type = 1, next_id = 101203503, talk_content = "我明白了，我也来帮你们吧！", talk_name = "青乌",},
	[101203503] = { talk_id = 101203503, group_id = 1012035, type = 1, next_id = 101203504, talk_content = "啊？你说你要帮我们？", talk_name = "恶索帮成员乙",},
	[101203504] = { talk_id = 101203504, group_id = 1012035, type = 1, next_id = 101203505, talk_content = "是的，我在和你们聊过之后觉得不是非得炸掉这里才能解决问题。", talk_name = "青乌",},
	[101203505] = { talk_id = 101203505, group_id = 1012035, type = 1, next_id = 101203506, talk_content = "说到底还不是要让我们放弃！", talk_name = "恶索帮成员甲",},
	[101203506] = { talk_id = 101203506, group_id = 1012035, type = 1, next_id = 101203507, talk_content = "别冲动，你们的诉求就是离开这里不是吗，我在黑白两道都有人。", talk_name = "青乌",},
	[101203507] = { talk_id = 101203507, group_id = 1012035, type = 1, next_id = 101203508, talk_content = "整个恶索帮是没法，但只带你们三个肯定可以，我肯定能帮你们三个离开这地方！", talk_name = "青乌",},
	[101203508] = { talk_id = 101203508, group_id = 1012035, type = 1, next_id = 101203509, talk_content = "什么……你真能？", talk_name = "恶索帮成员丙",},
	[101203509] = { talk_id = 101203509, group_id = 1012035, type = 1, next_id = 101203510, talk_content = "不然为什么只有我一个人负责这个设施，从你们行动开始到现在也只有我来到这里不是吗？", talk_name = "青乌",},
	[101203510] = { talk_id = 101203510, group_id = 1012035, type = 1, next_id = 101203511, talk_content = "唔……", talk_name = "恶索帮成员甲",},
	[101203511] = { talk_id = 101203511, group_id = 1012035, type = 1, next_id = 101203512, talk_content = "这个交易对你们并无坏处，你们能保全性命，我也能轻松解决这个危机。", talk_name = "青乌",},
	[101203512] = { talk_id = 101203512, group_id = 1012035, type = 1, next_id = 101203513, talk_content = "怎么办，喂，你们怎么想。", talk_name = "恶索帮成员丙",},
	[101203513] = { talk_id = 101203513, group_id = 1012035, type = 1, next_id = 101203514, talk_content = "这……", talk_name = "恶索帮成员乙",},
	[101203514] = { talk_id = 101203514, group_id = 1012035, type = 1, next_id = 101203515, talk_content = "（……）", talk_name = "青乌",},
	[101203515] = { talk_id = 101203515, group_id = 1012035, type = 1, next_id = 101203516, talk_content = "喂，这是什么声音。", talk_name = "恶索帮成员丙",},
	[101203516] = { talk_id = 101203516, group_id = 1012035, type = 1, next_id = 101203517, talk_content = "（那人说过成功就能会拨通电话，也就是说还没拆的炸弹只有他们身后的那些了。）", talk_name = "青乌",},
	[101203517] = { talk_id = 101203517, group_id = 1012035, type = 1, next_id = 101203518, talk_content = "不，我们不能为了个人的利益抛弃其他人，开始引爆！", talk_name = "恶索帮成员甲",},
	[101203518] = { talk_id = 101203518, group_id = 1012035, type = 2, child_type = 21, options = {{"（阻止引爆）",101203701,},{"（任由引爆）",101203601,},}, talk_content = "！", talk_name = "青乌",},
	[101203601] = { talk_id = 101203601, group_id = 1012036, type = 5, next_id = 101203602, aside = "<color=#FFEBA2>恶索帮成员甲：</color>给我好好看着，这就是我们禁闭者的骨气！", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "给我好好看着，这就是我们禁闭者的骨气！", talk_name = "恶索帮成员甲",},
