	[101203701] = { talk_id = 101203701, group_id = 1012037, type = 5, next_id = 101203702, aside = "<color=#FFEBA2>青乌：</color>别按下去，设施内的其他炸弹都已经被人拆走了。", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "别按下去，设施内的其他炸弹都已经被人拆走了。", talk_name = "青乌",},
