	[101203704] = { talk_id = 101203704, group_id = 1012037, type = 5, next_id = 101203705, aside = "<color=#FFEBA2>青乌：</color>假如还要自爆的话就随便你们，但至少请你们在我走了之后再做那种蠢事。", talk_content = "假如还要自爆的话就随便你们，但至少请你们在我走了之后再做那种蠢事。", talk_name = "青乌",},
