	[101203706] = { talk_id = 101203706, group_id = 1012037, type = 5, next_id = 101203707, aside = "<color=#FFEBA2>青乌：</color>……我只是想保全眼前的性命而已。", talk_content = "……我只是想保全眼前的性命而已。", talk_name = "青乌",},
	[101203707] = { talk_id = 101203707, group_id = 1012037, type = 5, next_id = 101203708, aside = "<color=#FFEBA2>小五：</color>走吧，青乌，悬天的人现身了。", talk_content = "走吧，青乌，悬天的人现身了。", talk_name = "小五",},
	[101203708] = { talk_id = 101203708, group_id = 1012037, type = 5, aside = "<color=#FFEBA2>青乌：</color>我明白，故事还要继续，对吧", talk_content = "我明白，故事还要继续，对吧", talk_name = "青乌",},
	[101203801] = { talk_id = 101203801, group_id = 1012038, type = 1, next_id = 101203802, hide_all_entity = true, hide_player = true, not_skip = true, talk_content = "我这里有一个好消息和一个坏消息，你们要听哪个！", talk_name = "青乌",},
	[101203802] = { talk_id = 101203802, group_id = 1012038, type = 1, next_id = 101203803, talk_content = "啊？", talk_name = "恶索帮成员甲",},
	[101203803] = { talk_id = 101203803, group_id = 1012038, type = 1, next_id = 101203804, talk_content = "好消息是我的确拿你们没办法，你们要是按下引爆的话那爆炸将会如期而来。", talk_name = "青乌",},
	[101203804] = { talk_id = 101203804, group_id = 1012038, type = 1, next_id = 101203805, talk_content = "而坏消息是，能按下引爆按钮的就只剩下你们了。", talk_name = "青乌",},
	[101203805] = { talk_id = 101203805, group_id = 1012038, type = 1, next_id = 101203806, talk_content = "你说什么！", talk_name = "恶索帮成员丙",},
	[101203806] = { talk_id = 101203806, group_id = 1012038, type = 2, child_type = 21, options = {{"恐吓",101203807,},{"安抚",101203810,},}, options_pos = 1, talk_content = "你们该不会以为这种与全城为敌的行动能成功吧，告诉你们，还有很多像我这样的脉者也和巡卫们联手了。", talk_name = "青乌",},
	[101203807] = { talk_id = 101203807, group_id = 1012038, type = 3, next_id = 101203808, icon = "interaction_4", talk_content = "你们恶索帮已经毫无胜算了，现在立刻……", talk_name = "青乌",},
	[101203808] = { talk_id = 101203808, group_id = 1012038, type = 3, next_id = 101203809, talk_content = "不，还没有失败，就算只剩下我们也要完成这个行动。", talk_name = "恶索帮成员甲",},
	[101203809] = { talk_id = 101203809, group_id = 1012038, type = 3, talk_content = "什么！？", talk_name = "青乌",},
	[101203810] = { talk_id = 101203810, group_id = 1012038, type = 3, next_id = 101203811, icon = "interaction_4", talk_content = "事已至此，你们的行动已经等于失败，在争执下去也没有意义。", talk_name = "青乌",},
	[101203811] = { talk_id = 101203811, group_id = 1012038, type = 3, talk_content = "现在收手的话还能保全性命，你们再好好想想吧！", talk_name = "青乌",},
	[101203901] = { talk_id = 101203901, group_id = 1012039, type = 5, next_id = 101203902, call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 53, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, not_skip = true,},
	[101203902] = { talk_id = 101203902, group_id = 1012039, type = 5, next_id = 101203903, aside = "<color=#FFEBA2>青乌：</color>没问题，话说占了这里的是哪些人？", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 53, talk_content = "没问题，话说占了这里的是哪些人？", talk_name = "青乌",},
