	[101203904] = { talk_id = 101203904, group_id = 1012039, type = 5, next_id = 101203905, aside = "<color=#FFEBA2>小五：</color>激进的那些，得想办法阻止他们，青乌。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 53, talk_content = "激进的那些，得想办法阻止他们，青乌。", talk_name = "小五",},
