	[101204202] = { talk_id = 101204202, group_id = 1012042, type = 1, next_id = 101204203, talk_content = "我没想到会在这遇到脉灵，你在这里干什么？", talk_name = "青乌",},
	[101204203] = { talk_id = 101204203, group_id = 1012042, type = 2, options = {{"<color=#FFEBA2>都发生了什么？</color>",101204204,},{"这个人是？",101204206,},{"你是什么？",101204207,},}, talk_content = "因为这个人很虚弱，所以我在这照顾他哦。", talk_name = "脉灵",},
	[101204204] = { talk_id = 101204204, group_id = 1012042, type = 3, next_id = 101204205, talk_content = "嘻哈，说不清楚噢。", talk_name = "脉灵",},
	[101204205] = { talk_id = 101204205, group_id = 1012042, type = 3, next_id = 101204209, talk_content = "不过我看到了一些事情，嘻哈，来同调吧，来同调吧！", talk_name = "脉灵",},
	[101204206] = { talk_id = 101204206, group_id = 1012042, type = 3, next_id = 101204203, talk_content = "或许是在这里的工作的人噢，虽然有点面生。", talk_name = "脉灵",},
	[101204207] = { talk_id = 101204207, group_id = 1012042, type = 3, next_id = 101204208, talk_content = "想问我的名字吗，朋友们都说不要告诉陌生人名字。", talk_name = "脉灵",},
