	[101204606] = { talk_id = 101204606, group_id = 1012046, type = 1, next_id = 101204607, talk_content = "整个恶索帮是没办法，但只带你们三个肯定可以，我肯定能帮你们三个离开这里！", talk_name = "青乌",},
	[101204607] = { talk_id = 101204607, group_id = 1012046, type = 1, next_id = 101204608, talk_content = "什么……你真能？", talk_name = "恶索帮成员丙",},
	[101204608] = { talk_id = 101204608, group_id = 1012046, type = 1, next_id = 101204609, talk_content = "你们好好想想，从你们行动开始到现在只有我一个人来这里不是吗？", talk_name = "青乌",},
	[101204609] = { talk_id = 101204609, group_id = 1012046, type = 1, next_id = 101204610, talk_content = "唔……", talk_name = "恶索帮成员甲",},
	[101204610] = { talk_id = 101204610, group_id = 1012046, type = 1, next_id = 101204611, talk_content = "这个交易对你们并无坏处，你们能保全性命，我也能轻松解决这个危机。", talk_name = "青乌",},
	[101204611] = { talk_id = 101204611, group_id = 1012046, type = 1, next_id = 101204612, talk_content = "怎么办，喂，你们怎么想。", talk_name = "恶索帮成员丙",},
	[101204612] = { talk_id = 101204612, group_id = 1012046, type = 1, next_id = 101204613, talk_content = "这……", talk_name = "恶索帮成员乙",},
	[101204613] = { talk_id = 101204613, group_id = 1012046, type = 1, next_id = 101204614, talk_content = "（……）", talk_name = "青乌",},
	[101204614] = { talk_id = 101204614, group_id = 1012046, type = 1, next_id = 101204615, talk_content = "喂，这是什么声音。", talk_name = "恶索帮成员丙",},
	[101204615] = { talk_id = 101204615, group_id = 1012046, type = 1, next_id = 101204616, talk_content = "（那人说过成功就会拨通电话，也就是说还没拆的炸弹只有他们身后的那些了。）", talk_name = "青乌",},
	[101204616] = { talk_id = 101204616, group_id = 1012046, type = 1, next_id = 101204617, talk_content = "不，我们不能为了个人的利益抛弃其他人，开始引爆！", talk_name = "恶索帮成员甲",},
	[101204617] = { talk_id = 101204617, group_id = 1012046, type = 2, child_type = 21, options = {{"（任由引爆）",101204618,},{"（阻止引爆）",101204619,},}, options_pos = 1, talk_content = "！", talk_name = "青乌",},
	[101204618] = { talk_id = 101204618, group_id = 1012046, type = 3, icon = "interaction_4", talk_content = "……", talk_name = "青乌",},
	[101204619] = { talk_id = 101204619, group_id = 1012046, type = 3, icon = "interaction_4", talk_content = "……", talk_name = "青乌",},
	[101204701] = { talk_id = 101204701, group_id = 1012047, type = 5, child_type = 52, hide_npc_list = {8102100,8102101,}, hide_player = true, not_skip = true,},
	[101210101] = { talk_id = 101210101, group_id = 1012101, type = 1, next_id = 101210102, fade_in = 1, fade_out = true, hide_player = true, talk_content = "我从你手机上看过那个江湖郎中的报酬了，结论是可信性相当高，青乌。", talk_name = "离歌",},
	[101210102] = { talk_id = 101210102, group_id = 1012101, type = 1, next_id = 101210103, talk_content = "我在你面前真的有隐私可言吗？", talk_name = "<nick_name>",},
	[101210103] = { talk_id = 101210103, group_id = 1012101, type = 1, next_id = 101210104, talk_content = "……继续说可信性这件事情吧，那位采桑子的确如他所说是青门的门徒。", talk_name = "离歌",},
	[101210104] = { talk_id = 101210104, group_id = 1012101, type = 1, next_id = 101210105, talk_content = "青门是长久以来主持着天月城各种非官方事务的宗门组织，在民间有相当大的影响力。", talk_name = "离歌",},
	[101210105] = { talk_id = 101210105, group_id = 1012101, type = 1, next_id = 101210106, talk_content = "而这样自古存在的宗门内部有史书自然也不出奇，实际上《创生截记》这本书也存在于青门的书库目录之中。", talk_name = "离歌",},
	[101210106] = { talk_id = 101210106, group_id = 1012101, type = 1, next_id = 101210107, talk_content = "你能拿出来吗？", talk_name = "<nick_name>",},
	[101210107] = { talk_id = 101210107, group_id = 1012101, type = 1, next_id = 101210108, talk_content = "硬闯或许有机会，但我现在并不在这里。", talk_name = "离歌",},
	[101210108] = { talk_id = 101210108, group_id = 1012101, type = 1, next_id = 101210109, talk_content = "我还以为你会让我去。", talk_name = "<nick_name>",},
	[101210109] = { talk_id = 101210109, group_id = 1012101, type = 1, next_id = 101210110, talk_content = "青门和街边混混可不是一个等级的东西，之后还是考虑点和平的方式吧。", talk_name = "离歌",},
	[101210110] = { talk_id = 101210110, group_id = 1012101, type = 1, next_id = 101210111, talk_content = "那现在该怎么办，虽然有了线索但却没有接近线索的办法。", talk_name = "<nick_name>",},
	[101210111] = { talk_id = 101210111, group_id = 1012101, type = 1, next_id = 101210112, talk_content = "你到地上去不就得了。", talk_name = "离歌",},
	[101210112] = { talk_id = 101210112, group_id = 1012101, type = 1, next_id = 101210113, talk_content = "我记得之前的委托说过去地上的审查很严格吧。", talk_name = "<nick_name>",},
	[101210113] = { talk_id = 101210113, group_id = 1012101, type = 1, next_id = 101210114, talk_content = "而且你还没有天月城的身份证明，从一开始就不会有人接受你的审查申请。", talk_name = "离歌",},
	[101210114] = { talk_id = 101210114, group_id = 1012101, type = 1, next_id = 101210115, talk_content = "不过。", talk_name = "离歌",},
	[101210115] = { talk_id = 101210115, group_id = 1012101, type = 1, talk_content = "我准备了一个新的委托，顺利的话应该能为你解决到地上去的各种烦恼。", talk_name = "离歌",},
	[101210301] = { talk_id = 101210301, group_id = 1012103, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>这是办事处之类的地方吗。", talk_content = "这是办事处之类的地方吗。", talk_name = "<nick_name>",},
	[101210401] = { talk_id = 101210401, group_id = 1012104, type = 1, next_id = 101210402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，我是9527的担保人。", talk_name = "<nick_name>",},
	[101210402] = { talk_id = 101210402, group_id = 1012104, type = 1, next_id = 101210403, talk_content = "请稍等，的确今日有一则关于伪造师9527的预约，他现在被安排到一号会面室。", talk_name = "前台",},
	[101210403] = { talk_id = 101210403, group_id = 1012104, type = 1, talk_content = "你可以直接上楼。", talk_name = "前台",},
	[101210501] = { talk_id = 101210501, group_id = 1012105, type = 2, options = {{"这是个什么地方。",101210502,},{"我要见的9527不会是犯了事的人吧？",101210504,},}, talk_content = "你好，请问还有什么需要吗？", talk_name = "前台",},
	[101210502] = { talk_id = 101210502, group_id = 1012105, type = 3, next_id = 101210503, talk_content = "这里是设立在旧城区的城务办事处，各种各样的公共事务都能在此办理。", talk_name = "前台",},
	[101210503] = { talk_id = 101210503, group_id = 1012105, type = 3, talk_content = "当然在办理前请先准备好身份证明和基本的文件，以免要来回跑几趟。", talk_name = "前台",},
	[101210504] = { talk_id = 101210504, group_id = 1012105, type = 3, next_id = 101210505, talk_content = "的确是这样的，呃，难道你是什么都不知道就过来当担保的吗？", talk_name = "前台",},
	[101210505] = { talk_id = 101210505, group_id = 1012105, type = 3, talk_content = "……", talk_name = "<nick_name>",},
	[101210601] = { talk_id = 101210601, group_id = 1012106, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"我不是离歌",101210602,},{"自我介绍",101210604,},}, talk_content = "终于有人来了，果然出外就是得靠朋友，从今以后离歌你的事就是我的事了！", talk_name = "“9527”",},
	[101210602] = { talk_id = 101210602, group_id = 1012106, type = 3, next_id = 101210603, talk_content = "啊？那你是谁？", talk_name = "“9527”",},
	[101210603] = { talk_id = 101210603, group_id = 1012106, type = 3, next_id = 101210605, talk_content = "<nick_name>，你叫我的外号青乌就行。", talk_name = "<nick_name>",},
	[101210604] = { talk_id = 101210604, group_id = 1012106, type = 3, next_id = 101210605, talk_content = "青乌，呃，不太认识。", talk_name = "“9527”",},
	[101210605] = { talk_id = 101210605, group_id = 1012106, type = 1, next_id = 101210606, talk_content = "那离歌呢，离歌人在哪里？", talk_name = "“9527”",},
	[101210606] = { talk_id = 101210606, group_id = 1012106, type = 1, next_id = 101210607, talk_content = "她说她现在不在这，但假如你要找离歌的话我就先回去一趟吧。", talk_name = "<nick_name>",},
	[101210607] = { talk_id = 101210607, group_id = 1012106, type = 1, next_id = 101210608, talk_content = "等等，你也可以，我只是在这里呆的神经有些失常了，绝对没有看低你的意思！", talk_name = "“9527”",},
	[101210608] = { talk_id = 101210608, group_id = 1012106, type = 1, next_id = 101210609, talk_content = "那直接说说委托的事情，你想让我做点什么？", talk_name = "<nick_name>",},
	[101210609] = { talk_id = 101210609, group_id = 1012106, type = 1, next_id = 101210610, talk_content = "我想让你替我去取点钱好做担保离开这个地方。", talk_name = "“9527”",},
	[101210610] = { talk_id = 101210610, group_id = 1012106, type = 1, next_id = 101210611, talk_content = "……", talk_name = "<nick_name>",},
	[101210611] = { talk_id = 101210611, group_id = 1012106, type = 1, next_id = 101210612, talk_content = "说来可耻……我是城内一个“假证”团伙的成员，是位造证的师傅。", talk_name = "“9527”",},
	[101210612] = { talk_id = 101210612, group_id = 1012106, type = 1, next_id = 101210613, talk_content = "当然我是被威胁的，所以被捉之后我马上供出了那些人全部的信息，然后巡卫说我只要交笔钱就能离开这了。", talk_name = "“9527”",},
	[101210613] = { talk_id = 101210613, group_id = 1012106, type = 1, next_id = 101210614, talk_content = "所以你是用情报换了自由是吧？", talk_name = "<nick_name>",},
	[101210614] = { talk_id = 101210614, group_id = 1012106, type = 1, next_id = 101210615, talk_content = "是为了正义！", talk_name = "“9527”",},
	[101210615] = { talk_id = 101210615, group_id = 1012106, type = 2, options = {{"那我就跑一趟吧。",101210616,},{"放垃圾桶里没关系吗？",101210617,},{"我听说这次的报酬能让我去“地上”",101210618,},}, talk_content = "我的钱因为某些原因被放在了一个垃圾桶里面，垃圾桶附近有个叉作为标记，你现在就替我去取一下吧。", talk_name = "“9527”",},
	[101210616] = { talk_id = 101210616, group_id = 1012106, type = 3, talk_content = "嗯，你可要快点，要不然等下巡卫反悔就麻烦了！", talk_name = "“9527”",},
	[101210617] = { talk_id = 101210617, group_id = 1012106, type = 3, talk_content = "我有自己的一些门路，那个垃圾桶是不会有人靠近的。", talk_name = "“9527”",},
	[101210618] = { talk_id = 101210618, group_id = 1012106, type = 3, talk_content = "嘘，这个等出去再谈好吗。", talk_name = "“9527”",},
	[101210701] = { talk_id = 101210701, group_id = 1012107, type = 1, talk_content = "钱放在垃圾桶里面，你翻翻就有。", talk_name = "“9527”",},
	[101210801] = { talk_id = 101210801, group_id = 1012108, type = 1, talk_content = "有话快说，不说就赶紧滚出去。", talk_name = "巡卫",},
	[101211001] = { talk_id = 101211001, group_id = 1012110, type = 4, next_id = 101211002, aside = "<color=#FFEBA2><nick_name>：</color>没想到竟然会有翻垃圾桶的一天。", talk_content = "没想到竟然会有翻垃圾桶的一天。", talk_name = "<nick_name>",},
	[101211002] = { talk_id = 101211002, group_id = 1012110, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>去找找叉的记号吧。", talk_content = "去找找叉的记号吧。", talk_name = "<nick_name>",},
	[101211101] = { talk_id = 101211101, group_id = 1012111, type = 4, next_id = 101211102, aside = "<color=#FFEBA2><nick_name>：</color>位置应该没错，但没看到哪里有叉……", talk_content = "位置应该没错，但没看到哪里有叉……", talk_name = "<nick_name>",},
	[101211102] = { talk_id = 101211102, group_id = 1012111, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>找个人问问吧。", talk_content = "找个人问问吧。", talk_name = "<nick_name>",},
	[101211201] = { talk_id = 101211201, group_id = 1012112, type = 1, next_id = 101211202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，请问你有在附近看过叉型的符号吗？", talk_name = "<nick_name>",},
	[101211202] = { talk_id = 101211202, group_id = 1012112, type = 1, next_id = 101211203, talk_content = "涂鸦的话最近都被抹掉了，之前就有人说影响市容。", talk_name = "环卫工",},
	[101211203] = { talk_id = 101211203, group_id = 1012112, type = 1, talk_content = "难不成那是你画的吗，下次可不要干这种事，擦起来可累了。", talk_name = "环卫工",},
	[101211301] = { talk_id = 101211301, group_id = 1012113, type = 4, next_id = 101211302, aside = "<color=#FFEBA2><nick_name>：</color>记号都没有了……", talk_content = "记号都没有了……", talk_name = "<nick_name>",},
	[101211302] = { talk_id = 101211302, group_id = 1012113, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>只能逐个翻了吗。", talk_content = "只能逐个翻了吗。", talk_name = "<nick_name>",},
	[101211401] = { talk_id = 101211401, group_id = 1012114, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>里面除了垃圾以外就没有别的东西了。", talk_content = "里面除了垃圾以外就没有别的东西了。", talk_name = "<nick_name>",},
	[101211501] = { talk_id = 101211501, group_id = 1012115, type = 4, next_id = 101211502, aside = "<color=#FFEBA2><nick_name>：</color>这里面……有了。", talk_content = "这里面……有了。", talk_name = "<nick_name>",},
	[101211502] = { talk_id = 101211502, group_id = 1012115, type = 4, next_id = 101211503, aside = "<color=#FFEBA2><nick_name>：</color>垃圾桶里面塞隔层……", talk_content = "垃圾桶里面塞隔层……", talk_name = "<nick_name>",},
	[101211503] = { talk_id = 101211503, group_id = 1012115, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>就不能好好存银行里吗。", talk_content = "就不能好好存银行里吗。", talk_name = "<nick_name>",},
	[101211601] = { talk_id = 101211601, group_id = 1012116, type = 1, next_id = 101211602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我收到了你们办事处的信息，保金我带来了，其余手续要怎么办？", talk_name = "<nick_name>",},
	[101211602] = { talk_id = 101211602, group_id = 1012116, type = 1, next_id = 101211603, talk_content = "手续方面的事情其实已经办完了，那个只是系统例行发送的信息。", talk_name = "前台",},
	[101211603] = { talk_id = 101211603, group_id = 1012116, type = 1, next_id = 101211604, talk_content = "嗯，保金的数量也正确，在我们最后审批结束以后9527就可以暂时离开这里。", talk_name = "前台",},
	[101211604] = { talk_id = 101211604, group_id = 1012116, type = 1, next_id = 101211605, talk_content = "十分感谢你的合作。", talk_name = "前台",},
	[101211605] = { talk_id = 101211605, group_id = 1012116, type = 1, talk_content = "结果就是交钱就行了啊。", talk_name = "<nick_name>",},
	[101220101] = { talk_id = 101220101, group_id = 1012201, type = 4, next_id = 101220102, aside = "<color=#FFEBA2><nick_name>：</color>刚见到委托人的时候我就大概知道是这回事了。", talk_content = "刚见到委托人的时候我就大概知道是这回事了。", talk_name = "<nick_name>",},
	[101220102] = { talk_id = 101220102, group_id = 1012201, type = 4, next_id = 101220103, aside = "<color=#FFEBA2><nick_name>：</color>办证大师……那种证真能用吗？", talk_content = "办证大师……那种证真能用吗？", talk_name = "<nick_name>",},
	[101220103] = { talk_id = 101220103, group_id = 1012201, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>委托人给了两个位置，按顺序去看看吧。", talk_content = "委托人给了两个位置，按顺序去看看吧。", talk_name = "<nick_name>",},
	[101220201] = { talk_id = 101220201, group_id = 1012202, type = 4, next_id = 101220202, aside = "<color=#FFEBA2><nick_name>：</color>办证大师……貌似有些先到的客人。", talk_content = "办证大师……貌似有些先到的客人。", talk_name = "<nick_name>",},
	[101220202] = { talk_id = 101220202, group_id = 1012202, type = 4, aside = "<color=#FFEBA2>市民：</color>救……救命！", talk_content = "救……救命！", talk_name = "市民",},
	[101220301] = { talk_id = 101220301, group_id = 1012203, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>你们都退开些！", talk_content = "你们都退开些！", talk_name = "<nick_name>",},
	[101220401] = { talk_id = 101220401, group_id = 1012204, type = 4, aside = "<color=#FFEBA2>市民：</color>那位脉者你也应该是来找办证大师的吧？", talk_content = "那位脉者你也应该是来找办证大师的吧？", talk_name = "市民",},
	[101220501] = { talk_id = 101220501, group_id = 1012205, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"投诉什么？",101220502,},{"我是来办证的。",101220507,},}, talk_content = "你也是来找办证大师投诉的对吧。", talk_name = "市民",},
	[101220502] = { talk_id = 101220502, group_id = 1012205, type = 3, next_id = 101220503, talk_content = "那当然是他们那伙人专门做的假证啊。", talk_name = "市民",},
	[101220503] = { talk_id = 101220503, group_id = 1012205, type = 3, next_id = 101220504, talk_content = "本来说好了肯定能瞒天过海，结果这证根本用都不能用，一上机立刻就被人发现了。", talk_name = "市民",},
	[101220504] = { talk_id = 101220504, group_id = 1012205, type = 3, next_id = 101220505, talk_content = "你看到附近那几个人了吧，他们也是因为劣质证来讨说法的。", talk_name = "市民",},
	[101220505] = { talk_id = 101220505, group_id = 1012205, type = 3, next_id = 101220506, talk_content = "可不是说那个办证大师挺厉害的吗？", talk_name = "<nick_name>",},
	[101220506] = { talk_id = 101220506, group_id = 1012205, type = 3, next_id = 101220511, talk_content = "我当时也是听说他厉害才来的，结果不还是这样。", talk_name = "市民",},
	[101220507] = { talk_id = 101220507, group_id = 1012205, type = 3, next_id = 101220508, talk_content = "我劝你还是别了，我和周遭这几个人今天都是来讨说法的。", talk_name = "市民",},
	[101220508] = { talk_id = 101220508, group_id = 1012205, type = 3, next_id = 101220509, talk_content = "……证不能用吗？", talk_name = "<nick_name>",},
	[101220509] = { talk_id = 101220509, group_id = 1012205, type = 3, next_id = 101220510, talk_content = "那必然是不能啊，吹了半天办证大师的风，但结果给我们做证的还是一群学徒。", talk_name = "市民",},
	[101220510] = { talk_id = 101220510, group_id = 1012205, type = 3, next_id = 101220511, talk_content = "结果就出事了，我生意赔了好几十万，哎。", talk_name = "市民",},
	[101220511] = { talk_id = 101220511, group_id = 1012205, type = 1, next_id = 101220512, talk_content = "然后还碰上了噬脉生物，差点把命也赔了，哎。", talk_name = "市民",},
	[101220512] = { talk_id = 101220512, group_id = 1012205, type = 1, next_id = 101220513, talk_content = "所以办证大师现在在这里吗？", talk_name = "<nick_name>",},
	[101220513] = { talk_id = 101220513, group_id = 1012205, type = 1, next_id = 101220514, talk_content = "不在，那些家伙肯定是知道有人会上门投诉所以换地了吧。", talk_name = "市民",},
	[101220514] = { talk_id = 101220514, group_id = 1012205, type = 1, talk_content = "可恶，我一定要让他们赔偿我的损失。", talk_name = "市民",},
	[101220601] = { talk_id = 101220601, group_id = 1012206, type = 4, next_id = 101220602, aside = "<color=#FFEBA2><nick_name>：</color>这里落空的话就只能去另一个地方了。", talk_content = "这里落空的话就只能去另一个地方了。", talk_name = "<nick_name>",},
	[101220602] = { talk_id = 101220602, group_id = 1012206, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>不过我是越来越怀疑这证到底能不能用了。", talk_content = "不过我是越来越怀疑这证到底能不能用了。", talk_name = "<nick_name>",},
	[101220701] = { talk_id = 101220701, group_id = 1012207, type = 4, next_id = 101220702, aside = "<color=#FFEBA2><nick_name>：</color>这里挺安静的，该不会也落空了。", talk_content = "这里挺安静的，该不会也落空了。", talk_name = "<nick_name>",},
	[101220702] = { talk_id = 101220702, group_id = 1012207, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>去问问那边的人吧。", talk_content = "去问问那边的人吧。", talk_name = "<nick_name>",},
	[101220801] = { talk_id = 101220801, group_id = 1012208, type = 4, next_id = 101220802, aside = "<color=#FFEBA2><nick_name>：</color>你好，我想问点事……", talk_content = "你好，我想问点事……", talk_name = "<nick_name>",},
	[101220802] = { talk_id = 101220802, group_id = 1012208, type = 4, next_id = 101220803, aside = "<color=#FFEBA2>混混：</color>被发现了？", talk_content = "被发现了？", talk_name = "混混",},
	[101220803] = { talk_id = 101220803, group_id = 1012208, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>等等！", talk_content = "等等！", talk_name = "<nick_name>",},
	[101220901] = { talk_id = 101220901, group_id = 1012209, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>心虚成这样一定有鬼，这附近应该有车。", talk_content = "心虚成这样一定有鬼，这附近应该有车。", talk_name = "<nick_name>",},
	[101221001] = { talk_id = 101221001, group_id = 1012210, type = 4, next_id = 101221002, aside = "<color=#FFEBA2>混混：</color>你这家伙别欺人太甚！", talk_content = "你这家伙别欺人太甚！", talk_name = "混混",},
	[101221002] = { talk_id = 101221002, group_id = 1012210, type = 4, next_id = 101221003, aside = "<color=#FFEBA2><nick_name>：</color>我只想问点事情而已。", talk_content = "我只想问点事情而已。", talk_name = "<nick_name>",},
	[101221003] = { talk_id = 101221003, group_id = 1012210, type = 4, next_id = 101221004, aside = "<color=#FFEBA2>混混：</color>还问什么，你就是知道我是办证的才追过来的吧。", talk_content = "还问什么，你就是知道我是办证的才追过来的吧。", talk_name = "混混",},
	[101221004] = { talk_id = 101221004, group_id = 1012210, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>……", talk_content = "……", talk_name = "<nick_name>",},
	[101221101] = { talk_id = 101221101, group_id = 1012211, type = 4, aside = "<color=#FFEBA2>混混：</color>我不会跑了，别打了！", talk_content = "我不会跑了，别打了！", talk_name = "混混",},
	[101221201] = { talk_id = 101221201, group_id = 1012212, type = 2, child_type = 21, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"别废话，把你知道的说出来",101221202,},{"我是那边派来检查你忠诚的",101221209,},}, talk_content = "才刚加入没多久就让我一个人善后，这工我不干了！", talk_name = "混混",},
	[101221202] = { talk_id = 101221202, group_id = 1012212, type = 3, next_id = 101221203, icon = "interaction_4", talk_content = "我知道了，我知道了，除了我以外的其他人都在工厂那边。", talk_name = "混混",},
	[101221203] = { talk_id = 101221203, group_id = 1012212, type = 3, next_id = 101221204, talk_content = "那个办证大师也是？", talk_name = "<nick_name>",},
	[101221204] = { talk_id = 101221204, group_id = 1012212, type = 3, next_id = 101221205, talk_content = "大师……不知道，我才刚入伙，人都还没认完。", talk_name = "混混",},
	[101221205] = { talk_id = 101221205, group_id = 1012212, type = 3, next_id = 101221206, talk_content = "你说真的吗？", talk_name = "<nick_name>",},
	[101221206] = { talk_id = 101221206, group_id = 1012212, type = 3, next_id = 101221207, talk_content = "真的，就因为我地位低所以才被派去刚刚那地收拾落下的工具。", talk_name = "混混",},
	[101221207] = { talk_id = 101221207, group_id = 1012212, type = 3, next_id = 101221208, talk_content = "要是敢骗我你知道会怎么样吧？", talk_name = "<nick_name>",},
	[101221208] = { talk_id = 101221208, group_id = 1012212, type = 3, talk_content = "不敢，我真不敢骗你。", talk_name = "混混",},
	[101221209] = { talk_id = 101221209, group_id = 1012212, type = 3, next_id = 101221210, icon = "interaction_4", talk_content = "呃，不，不会吧，对了，我刚刚只是想引开你注意力然后逃跑。", talk_name = "混混",},
	[101221210] = { talk_id = 101221210, group_id = 1012212, type = 3, next_id = 101221211, talk_content = "绝对没有出卖伙伴的意思。", talk_name = "混混",},
	[101221211] = { talk_id = 101221211, group_id = 1012212, type = 3, next_id = 101221212, talk_content = "你有没有这件事由我来决定……现在的据点在哪，我要回去一趟做报告。", talk_name = "<nick_name>",},
	[101221212] = { talk_id = 101221212, group_id = 1012212, type = 3, next_id = 101221213, talk_content = "就工厂那边啊……等等，你不知道吗？", talk_name = "混混",},
	[101221213] = { talk_id = 101221213, group_id = 1012212, type = 3, next_id = 101221214, talk_content = "我之前一直跑外勤少了联络，你可以滚了。", talk_name = "<nick_name>",},
	[101221214] = { talk_id = 101221214, group_id = 1012212, type = 3, next_id = 101221215, talk_content = "滚，呃，滚去哪？", talk_name = "混混",},
	[101221215] = { talk_id = 101221215, group_id = 1012212, type = 3, next_id = 101221216, talk_content = "滚回你家，在我们联络你之前闭嘴，懂了吗。", talk_name = "<nick_name>",},
	[101221216] = { talk_id = 101221216, group_id = 1012212, type = 3, talk_content = "懂，懂！", talk_name = "混混",},
	[101221301] = { talk_id = 101221301, group_id = 1012213, type = 1, next_id = 101221302, talk_content = "不，不要再打我了！", talk_name = "混混",},
	[101221302] = { talk_id = 101221302, group_id = 1012213, type = 1, talk_content = "（也太不适合这工作了。）", talk_name = "<nick_name>",},
	[101230101] = { talk_id = 101230101, group_id = 1012301, type = 5, aside = "<color=#FFEBA2>追识：</color>抱歉，我没想吓到你，看上去你应该不是“办证大师”的人吧？", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "抱歉，我没想吓到你，看上去你应该不是“办证大师”的人吧？", talk_name = "追识",},
	[101230201] = { talk_id = 101230201, group_id = 1012302, type = 1, next_id = 101230202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "先说明我的情况吧，我叫追识，维安司的司长，来这里是因为我掌握到办证大师窝藏在这里的情报。", talk_name = "追识",},
	[101230202] = { talk_id = 101230202, group_id = 1012302, type = 2, options = {{"听上去像是来来的上司。",101230203,},{"青乌，我是来找办证大师的。",101230208,},}, talk_content = "你呢？", talk_name = "追识",},
	[101230203] = { talk_id = 101230203, group_id = 1012302, type = 3, next_id = 101230204, talk_content = "你认识来来？啊，所以你是青乌对吧，我从来来那里听了不少你的事。", talk_name = "追识",},
	[101230204] = { talk_id = 101230204, group_id = 1012302, type = 3, next_id = 101230205, talk_content = "我可以问问都说了什么吗？", talk_name = "<nick_name>",},
	[101230205] = { talk_id = 101230205, group_id = 1012302, type = 3, next_id = 101230206, talk_content = "赞不绝口，来来都和我说想要拉你入伙了。", talk_name = "追识",},
	[101230206] = { talk_id = 101230206, group_id = 1012302, type = 3, next_id = 101230207, talk_content = "话说回来，我认为青乌你的目标应该也是“办证大师”，对吗。", talk_name = "追识",},
	[101230207] = { talk_id = 101230207, group_id = 1012302, type = 3, next_id = 101230208, talk_content = "嗯，我找里面的大师有事，虽然不知道有没有用就是了。", talk_name = "<nick_name>",},
	[101230208] = { talk_id = 101230208, group_id = 1012302, type = 2, options = {{"我是听说有不少假证。",101230209,},{"该不会你和来来一样要捣毁犯罪吧？",101230211,},}, talk_content = "假如你的目标是证件的话我劝青乌你还是别抱什么期待比较好。", talk_name = "追识",},
	[101230209] = { talk_id = 101230209, group_id = 1012302, type = 3, next_id = 101230210, talk_content = "这个团伙自从出现在天月城以后就用办证大师的名号造了不少假证，搞出了很多麻烦。", talk_name = "追识",},
	[101230210] = { talk_id = 101230210, group_id = 1012302, type = 3, next_id = 101230212, talk_content = "作为维安司的一员我可不能放任这种祸根继续存在于天月城。", talk_name = "追识",},
	[101230211] = { talk_id = 101230211, group_id = 1012302, type = 3, next_id = 101230212, talk_content = "在我看来不能放过任何可能危害天月城根本的人物，维安司也是为此而存在的。", talk_name = "追识",},
	[101230212] = { talk_id = 101230212, group_id = 1012302, type = 2, child_type = 21, options = {{"看来我们只能一战了。",101230213,},{"我不打算空手而归。",101230214,},{"要不先让我办个证？",101230215,},}, talk_content = "……", talk_name = "<nick_name>",},
	[101230213] = { talk_id = 101230213, group_id = 1012302, type = 3, next_id = 101230218, icon = "interaction_4", talk_content = "直来直去……真是来来会喜欢的性格，但我并不是那么好战的人物。", talk_name = "追识",},
	[101230214] = { talk_id = 101230214, group_id = 1012302, type = 3, next_id = 101230218, icon = "interaction_4", talk_content = "难得遇上，我当然也希望和有能者交上朋友……", talk_name = "追识",},
	[101230215] = { talk_id = 101230215, group_id = 1012302, type = 3, next_id = 101230216, icon = "interaction_4", talk_content = "抱歉，这件事我可无法答应，该不会青乌你是这方面的惯犯？", talk_name = "追识",},
	[101230216] = { talk_id = 101230216, group_id = 1012302, type = 3, next_id = 101230217, talk_content = "这倒没有。", talk_name = "<nick_name>",},
	[101230217] = { talk_id = 101230217, group_id = 1012302, type = 3, next_id = 101230218, talk_content = "嗯……", talk_name = "追识",},
	[101230218] = { talk_id = 101230218, group_id = 1012302, type = 1, next_id = 101230219, talk_content = "这样吧，我们来做个交易如何？", talk_name = "追识",},
	[101230219] = { talk_id = 101230219, group_id = 1012302, type = 1, next_id = 101230220, talk_content = "交易指的是……？", talk_name = "<nick_name>",},
	[101230220] = { talk_id = 101230220, group_id = 1012302, type = 2, options = {{"成交。",101230222,},{"我还以为维安司不会容许这种事情。",101230221,},}, talk_content = "你来这里大概是为了“证件”吧，我这有快捷且正规的门路。", talk_name = "追识",},
	[101230221] = { talk_id = 101230221, group_id = 1012302, type = 3, next_id = 101230222, talk_content = "我做的只是引荐而已，实际如何还是要看你自己。", talk_name = "追识",},
	[101230222] = { talk_id = 101230222, group_id = 1012302, type = 1, talk_content = "那我们走吧，办证大师的人应该都聚在那里面了。", talk_name = "追识",},
	[101230301] = { talk_id = 101230301, group_id = 1012303, type = 4, next_id = 101230302, aside = "<color=#FFEBA2>追识：</color>不能放跑任何一个人，青乌。", talk_content = "不能放跑任何一个人，青乌。", talk_name = "追识",},
	[101230302] = { talk_id = 101230302, group_id = 1012303, type = 4, aside = "<color=#FFEBA2>混混：</color>有人闯进来了，快把老大们叫起来！", talk_content = "有人闯进来了，快把老大们叫起来！", talk_name = "混混",},
	[101230401] = { talk_id = 101230401, group_id = 1012304, type = 4, next_id = 101230402, aside = "<color=#FFEBA2>悬天成员：</color>我说了把大师找回来之前别吵闹……你们是什么人！", talk_content = "我说了把大师找回来之前别吵闹……你们是什么人！", talk_name = "悬天成员",},
	[101230402] = { talk_id = 101230402, group_id = 1012304, type = 4, next_id = 101230403, aside = "<color=#FFEBA2><nick_name>：</color>悬天的人……？", talk_content = "悬天的人……？", talk_name = "<nick_name>",},
	[101230403] = { talk_id = 101230403, group_id = 1012304, type = 4, aside = "<color=#FFEBA2>追识：</color>我就知道这件事背后有悬天的人，这次一定要捉住你们的把柄！", talk_content = "我就知道这件事背后有悬天的人，这次一定要捉住你们的把柄！", talk_name = "追识",},
	[101230501] = { talk_id = 101230501, group_id = 1012305, type = 4, next_id = 101230502, aside = "<color=#FFEBA2>悬天成员：</color>走！", talk_content = "走！", talk_name = "悬天成员",},
	[101230502] = { talk_id = 101230502, group_id = 1012305, type = 4, next_id = 101230503, aside = "<color=#FFEBA2><nick_name>：</color>要追吗？", talk_content = "要追吗？", talk_name = "<nick_name>",},
	[101230503] = { talk_id = 101230503, group_id = 1012305, type = 4, next_id = 101230504, aside = "<color=#FFEBA2>追识：</color>我让同伴埋伏在逃跑必经的小道上，那边可以交给他们。", talk_content = "我让同伴埋伏在逃跑必经的小道上，那边可以交给他们。", talk_name = "追识",},
	[101230504] = { talk_id = 101230504, group_id = 1012305, type = 4, aside = "<color=#FFEBA2>追识：</color>青乌，能和我一起调查下这里吗？", talk_content = "青乌，能和我一起调查下这里吗？", talk_name = "追识",},
	[101230601] = { talk_id = 101230601, group_id = 1012306, type = 1, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "（放着一些印刷用的工具以及原材料，感觉有点简陋。）", talk_name = "<nick_name>",},
	[101230701] = { talk_id = 101230701, group_id = 1012307, type = 1, next_id = 101230702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "（这上面打开了封邮件。）", talk_name = "<nick_name>",},
	[101230702] = { talk_id = 101230702, group_id = 1012307, type = 1, next_id = 101230703, talk_content = "证件的质量不如预想，虽然也能达成目的，但还是得想办法能假以乱真的东西。", talk_name = "通讯人1",},
	[101230703] = { talk_id = 101230703, group_id = 1012307, type = 1, next_id = 101230704, talk_content = "我们的人要学到大师的功夫还得再练一段时间。", talk_name = "通讯人2",},
	[101230704] = { talk_id = 101230704, group_id = 1012307, type = 1, next_id = 101230705, talk_content = "可最近做得有些过火了，接下来找个地方避避风头吧。", talk_name = "通讯人1",},
	[101230705] = { talk_id = 101230705, group_id = 1012307, type = 1, next_id = 101230706, talk_content = "避去哪避，不知不觉手下人也变这么多了，难不成要去地上吗？", talk_name = "通讯人2",},
	[101230706] = { talk_id = 101230706, group_id = 1012307, type = 1, next_id = 101230707, talk_content = "去那个茶室，在去之前把没用的家伙都处理一下就行了。", talk_name = "通讯人1",},
	[101230707] = { talk_id = 101230707, group_id = 1012307, type = 1, talk_content = "……茶室。", talk_name = "<nick_name>",},
	[101230801] = { talk_id = 101230801, group_id = 1012308, type = 1, next_id = 101230802, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "如何照顾办证大师", talk_name = "文件2",},
	[101230802] = { talk_id = 101230802, group_id = 1012308, type = 1, next_id = 101230803, talk_content = "1.一日六餐，早餐，早茶，午餐，下午茶，晚餐以及宵夜。", talk_name = "文件2",},
	[101230803] = { talk_id = 101230803, group_id = 1012308, type = 1, next_id = 101230804, talk_content = "2.不要让其接触网络", talk_name = "文件2",},
	[101230804] = { talk_id = 101230804, group_id = 1012308, type = 1, next_id = 101230805, talk_content = "3.办证大师性格古怪，尽量让其保持好心情。", talk_name = "文件2",},
	[101230805] = { talk_id = 101230805, group_id = 1012308, type = 1, next_id = 101230806, talk_content = "4.每七日必须让其制作一份证件的基板，并且确认可用。", talk_name = "文件2",},
	[101230806] = { talk_id = 101230806, group_id = 1012308, type = 1, next_id = 101230807, talk_content = "5.办证大师基本会拖延时间，将”通行印鉴“放到最后才做，在此之前不要让其有任何逃脱的机会。", talk_name = "文件2",},
	[101230807] = { talk_id = 101230807, group_id = 1012308, type = 1, talk_content = "办证大师趁机跑掉了……", talk_name = "文件2",},
	[101230901] = { talk_id = 101230901, group_id = 1012309, type = 1, next_id = 101230902, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "（这里是一批做好的证件，有商业许可，卫生许可，还有允许持有武器的证件。）", talk_name = "<nick_name>",},
	[101230902] = { talk_id = 101230902, group_id = 1012309, type = 1, next_id = 101230903, talk_content = "（还有通行印鉴，看上去这个应该就是天月城的出入证件。）", talk_name = "<nick_name>",},
	[101230903] = { talk_id = 101230903, group_id = 1012309, type = 2, options = {{"不拿",101230906,},{"拿",101230904,},}, talk_content = "（要偷偷拿走吗？）", talk_name = "<nick_name>",},
	[101230904] = { talk_id = 101230904, group_id = 1012309, type = 3, next_id = 101230905, talk_content = "……青乌，我看着的哦？", talk_name = "追识",},
	[101230905] = { talk_id = 101230905, group_id = 1012309, type = 3, talk_content = "（看来没办法拿走。）", talk_name = "<nick_name>",},
	[101230906] = { talk_id = 101230906, group_id = 1012309, type = 3, talk_content = "（算了吧，假的东西拿了也没用。）", talk_name = "<nick_name>",},
	[101231001] = { talk_id = 101231001, group_id = 1012310, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（去找追识吧，在意的地方都看过一遍了。）", talk_content = "（去找追识吧，在意的地方都看过一遍了。）", talk_name = "<nick_name>",},
	[101231101] = { talk_id = 101231101, group_id = 1012311, type = 1, talk_content = "青乌，我们一人负责一边，那样效率要快些。", talk_name = "追识",},
	[101231201] = { talk_id = 101231201, group_id = 1012312, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"办证大师不在这里了。",101231202,},{"他们似乎在学大师的技术。",101231203,},}, talk_content = "看上去你那边也结束了呢，有找到什么值得在意的东西吗？", talk_name = "追识",},
	[101231202] = { talk_id = 101231202, group_id = 1012312, type = 3, next_id = 101231204, talk_content = "嗯，我估计也是如此，要不然悬天的人也不会走得如此干脆。", talk_name = "追识",},
	[101231203] = { talk_id = 101231203, group_id = 1012312, type = 3, next_id = 101231204, talk_content = "在各处流通的假证恐怕就是学习的成果了吧，悬天到底想拿这些证件来做些什么……", talk_name = "追识",},
	[101231204] = { talk_id = 101231204, group_id = 1012312, type = 1, next_id = 101231205, talk_content = "你好像很在意悬天，你和他们有什么关系吗？", talk_name = "<nick_name>",},
	[101231205] = { talk_id = 101231205, group_id = 1012312, type = 1, next_id = 101231206, talk_content = "只是单纯的敌人而已，维安司想要让天月城获得真正的和平，而悬天则是破坏这些和平的人物。", talk_name = "追识",},
	[101231206] = { talk_id = 101231206, group_id = 1012312, type = 1, next_id = 101231207, talk_content = "……在我看来巡卫也半斤八两。", talk_name = "追识",},
	[101231207] = { talk_id = 101231207, group_id = 1012312, type = 1, next_id = 101231208, talk_content = "他们不过是和混混相等的投机主义者，但悬天仗着是司世太阳的调整者在城中肆意妄为。", talk_name = "追识",},
	[101231208] = { talk_id = 101231208, group_id = 1012312, type = 1, next_id = 101231209, talk_content = "在依赖太阳生活的世道可没人敢公开反抗他们。", talk_name = "追识",},
	[101231209] = { talk_id = 101231209, group_id = 1012312, type = 1, next_id = 101231210, talk_content = "太阳……那个真的有这么重要吗？", talk_name = "<nick_name>",},
	[101231210] = { talk_id = 101231210, group_id = 1012312, type = 2, options = {{"你也是这么想吗？",101231211,},{"这也太天荒夜谈了吧。",101231212,},}, talk_content = "先有太阳然后才有这座城市是大家的共识，毕竟没有了太阳那天月城可能会直接往地上坠落也说不定。", talk_name = "追识",},
	[101231211] = { talk_id = 101231211, group_id = 1012312, type = 3, next_id = 101231213, talk_content = "从小时候我就这么想了，直到现在也没改变过。", talk_name = "追识",},
	[101231212] = { talk_id = 101231212, group_id = 1012312, type = 3, next_id = 101231213, talk_content = "在天地翻转的这座城市里面可不是什么天荒夜谈，现在支撑着这座城市的毫无疑问是天上的太阳。", talk_name = "追识",},
	[101231213] = { talk_id = 101231213, group_id = 1012312, type = 1, next_id = 101231214, talk_content = "对了，青乌你应该不是天月城本地人对吧，而且我估计你应该连身份证明也没有。", talk_name = "追识",},
	[101231214] = { talk_id = 101231214, group_id = 1012312, type = 1, next_id = 101231215, talk_content = "……因为某些原因，你该不会是要捉我回去吧？", talk_name = "<nick_name>",},
	[101231215] = { talk_id = 101231215, group_id = 1012312, type = 1, next_id = 101231216, talk_content = "不，因为我刚刚发觉你似乎一直盯着印鉴，你想要到”地上“去对不，我帮你安排通行审查吧。", talk_name = "追识",},
	[101231216] = { talk_id = 101231216, group_id = 1012312, type = 1, next_id = 101231217, talk_content = "老实说我就是担心那个审查所以才会来找办证大师的。", talk_name = "<nick_name>",},
	[101231217] = { talk_id = 101231217, group_id = 1012312, type = 1, next_id = 101231218, talk_content = "对于外来者来说审查一直都是走个形式而已，虽然要定期更新，但至今都没有出过问题。", talk_name = "追识",},
	[101231218] = { talk_id = 101231218, group_id = 1012312, type = 1, next_id = 101231219, talk_content = "毕竟审查的严厉一直都是单方面的。", talk_name = "追识",},
	[101231219] = { talk_id = 101231219, group_id = 1012312, type = 1, next_id = 101231220, talk_content = "单独针对天月城人的审查啊，这到底是为了什么？", talk_name = "<nick_name>",},
	[101231220] = { talk_id = 101231220, group_id = 1012312, type = 1, next_id = 101231221, talk_content = "这就不是我能知道的事情，毕竟养父……不，话就说到这里吧。", talk_name = "追识",},
	[101231221] = { talk_id = 101231221, group_id = 1012312, type = 1, next_id = 101231222, talk_content = "青乌，我稍后会把审查相关的东西发到你手机上，你只要按照上面的指示去做就可以。", talk_name = "追识",},
	[101231222] = { talk_id = 101231222, group_id = 1012312, type = 1, next_id = 101231223, talk_content = "那你呢？", talk_name = "<nick_name>",},
	[101231223] = { talk_id = 101231223, group_id = 1012312, type = 1, talk_content = "我还要在这里等其他人带巡卫过来，我们之后再见。", talk_name = "追识",},
	[101231301] = { talk_id = 101231301, group_id = 1012313, type = 1, next_id = 101231302, talk_content = "青乌，你也和悬天有某种纠葛对吧？", talk_name = "追识",},
	[101231302] = { talk_id = 101231302, group_id = 1012313, type = 1, next_id = 101231303, talk_content = "……不好说。", talk_name = "<nick_name>",},
	[101231303] = { talk_id = 101231303, group_id = 1012313, type = 1, next_id = 101231304, talk_content = "就我观察所得你应该也和他们互为敌人，所以我希望能和你保持友好的关系。", talk_name = "追识",},
	[101231304] = { talk_id = 101231304, group_id = 1012313, type = 1, talk_content = "为了维护这座城市……我必须要把能用的全部用上。", talk_name = "追识",},
	[101240301] = { talk_id = 101240301, group_id = 1012403, type = 1, next_id = 101240302, fade_in = 1, fade_out = true, hide_player = true, talk_content = "老实说我本来没打算让你去参加什么审查，毕竟天月城中有着禁闭者的制度。", talk_name = "离歌",},
	[101240302] = { talk_id = 101240302, group_id = 1012403, type = 1, next_id = 101240303, talk_content = "那些假证没法用吧，我可不想在通关的时候被人捉住。", talk_name = "<nick_name>",},
	[101240303] = { talk_id = 101240303, group_id = 1012403, type = 1, next_id = 101240304, talk_content = "毕竟他们并不是出于办证大师之手。", talk_name = "离歌",},
	[101240304] = { talk_id = 101240304, group_id = 1012403, type = 1, next_id = 101240305, talk_content = "真有办证大师这人吗？", talk_name = "<nick_name>",},
	[101240305] = { talk_id = 101240305, group_id = 1012403, type = 1, next_id = 101240306, talk_content = "……现在这个已经不是重点，毕竟每次都要让大师来更新证件这件事的确有些麻烦。", talk_name = "离歌",},
	[101240306] = { talk_id = 101240306, group_id = 1012403, type = 1, next_id = 101240307, talk_content = "青乌，你就去试试追识安排的审查吧。", talk_name = "离歌",},
	[101240307] = { talk_id = 101240307, group_id = 1012403, type = 1, next_id = 101240308, talk_content = "失败了怎么办，我可不想一辈子都出不去天月城。", talk_name = "<nick_name>",},
	[101240308] = { talk_id = 101240308, group_id = 1012403, type = 1, next_id = 101240309, talk_content = "那时候我自有别的办法，我可不打算放弃好不容易才搞到手的东西。", talk_name = "离歌",},
	[101240309] = { talk_id = 101240309, group_id = 1012403, type = 1, next_id = 101240310, talk_content = "……我可没有签过什么卖身契。", talk_name = "<nick_name>",},
	[101240310] = { talk_id = 101240310, group_id = 1012403, type = 1, talk_content = "审查制度本身到底有什么乾坤我也不知道，但我的直觉告诉我你应该是能通过的。", talk_name = "离歌",},
	[101240401] = { talk_id = 101240401, group_id = 1012404, type = 4, next_id = 101240402, aside = "<color=#FFEBA2><nick_name>：</color>只要在公务局完成审查就能到地上去。", talk_content = "只要在公务局完成审查就能到地上去。", talk_name = "<nick_name>",},
	[101240402] = { talk_id = 101240402, group_id = 1012404, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>……先不说能不能找回记忆，我的确对上面的这块大地有些兴趣。", talk_content = "……先不说能不能找回记忆，我的确对上面的这块大地有些兴趣。", talk_name = "<nick_name>",},
	[101240501] = { talk_id = 101240501, group_id = 1012405, type = 1, next_id = 101240502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，我是来参加“通行审查”的，维安司的追识说她帮我打点了下，你应该知道的吧？", talk_name = "<nick_name>",},
	[101240502] = { talk_id = 101240502, group_id = 1012405, type = 1, talk_content = "你好，的确是有这样的预约，要参加通行审查的话就请到那边去吧。", talk_name = "前台",},
	[101240601] = { talk_id = 101240601, group_id = 1012406, type = 5, next_id = 101240602, aside = "<color=#FFEBA2><nick_name>：</color>应该就是这里没错吧。", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "应该就是这里没错吧。", talk_name = "<nick_name>",},
	[101240602] = { talk_id = 101240602, group_id = 1012406, type = 5, next_id = 101240603, aside = "<color=#FFEBA2><nick_name>：</color>有人吗？", talk_content = "有人吗？", talk_name = "<nick_name>",},
	[101240603] = { talk_id = 101240603, group_id = 1012406, type = 5, next_id = 101240604, aside = "<color=#FFEBA2>？？？：</color>呼……嗯。", talk_content = "呼……嗯。", talk_name = "？？？",},
	[101240604] = { talk_id = 101240604, group_id = 1012406, type = 5, next_id = 101240605, aside = "<color=#FFEBA2><nick_name>：</color>……", talk_content = "……", talk_name = "<nick_name>",},
	[101240605] = { talk_id = 101240605, group_id = 1012406, type = 5, next_id = 101240606, aside = "<color=#FFEBA2>？？？：</color>嗯？", talk_content = "嗯？", talk_name = "？？？",},
	[101240606] = { talk_id = 101240606, group_id = 1012406, type = 5, aside = "<color=#FFEBA2>？？？：</color>哇！", talk_content = "哇！", talk_name = "？？？",},
	[101240701] = { talk_id = 101240701, group_id = 1012407, type = 1, next_id = 101240702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你没事吧？", talk_name = "<nick_name>",},
	[101240702] = { talk_id = 101240702, group_id = 1012407, type = 2, options = {{"我想找审查的负责人。",101240703,},{"小朋友你知道审查的人去哪了吗？",101240703,},}, talk_content = "还好……没想到我竟然会睡着，对了，你是来做通行审查的对吧。", talk_name = "？？？",},
	[101240703] = { talk_id = 101240703, group_id = 1012407, type = 2, options = {{"真的吗？",101240704,},{"我信了。",101240706,},{"童工？",101240705,},}, talk_content = "……又来一个，虽然的确是小孩，但我是如假包换的这里的兼职负责人！", talk_name = "？？？",},
	[101240704] = { talk_id = 101240704, group_id = 1012407, type = 3, next_id = 101240706, talk_content = "你这眼神是什么意思，穿着一身这么白的衣服，头上还长了个断角的脸瘫脉者……哎？", talk_name = "？？？",},
	[101240705] = { talk_id = 101240705, group_id = 1012407, type = 3, next_id = 101240706, talk_content = "童什么工，这可是密学院给的学分任务，你这个头上长了个断角的脸瘫脉者……哎？", talk_name = "？？？",},
	[101240706] = { talk_id = 101240706, group_id = 1012407, type = 2, options = {{"我是叫青乌。",101240707,},{"你是？",101240707,},}, talk_content = "……你叫青乌是吗？", talk_name = "？？？",},
	[101240707] = { talk_id = 101240707, group_id = 1012407, type = 3, next_id = 101240708, talk_content = "哼恩……我本以为值得维安司司长特地过来打点的是个什么大人物，也罢。", talk_name = "？？？",},
	[101240708] = { talk_id = 101240708, group_id = 1012407, type = 2, options = {{"审查不是在这里做的吗？",101240709,},{"我们要去哪？",101240710,},}, talk_content = "我叫祁玉，创生密学院最年轻的院士，既然人到了那我们就出发吧。", talk_name = "祁玉",},
	[101240709] = { talk_id = 101240709, group_id = 1012407, type = 3, talk_content = "审查是在这里做，但你额外的“义务”——诏令是得在城里面东奔西走才能完成的事情。", talk_name = "祁玉",},
	[101240710] = { talk_id = 101240710, group_id = 1012407, type = 3, talk_content = "当然是去完成”诏令“呀，司长说你不是连基本的身份证明都没有吗？", talk_name = "祁玉",},
	[101240801] = { talk_id = 101240801, group_id = 1012408, type = 4, aside = "<color=#FFEBA2>祁玉：</color>审查本身只要在前台抽个血就可以，你先去办好这事我们再出门吧。", talk_content = "审查本身只要在前台抽个血就可以，你先去办好这事我们再出门吧。", talk_name = "祁玉",},
	[101240901] = { talk_id = 101240901, group_id = 1012409, type = 1, next_id = 101240902, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "麻烦帮这人抽下血，要加急化验。", talk_name = "祁玉",},
	[101240902] = { talk_id = 101240902, group_id = 1012409, type = 1, next_id = 101240903, talk_content = "没问题，那么请伸出手。", talk_name = "前台",},
	[101240903] = { talk_id = 101240903, group_id = 1012409, type = 2, child_type = 21, options = {{"伸",101240904,},{"不伸",101240906,},}, talk_content = "（……）", talk_name = "<nick_name>",},
	[101240904] = { talk_id = 101240904, group_id = 1012409, type = 3, next_id = 101240905, icon = "interaction_4", talk_content = "刺痛了一下",},
	[101240905] = { talk_id = 101240905, group_id = 1012409, type = 3, next_id = 101240912, talk_content = "可以了，在止血之前建议不要进行剧烈运动哦，化验结束以后我们会发短信通知您的。", talk_name = "前台",},
	[101240906] = { talk_id = 101240906, group_id = 1012409, type = 3, next_id = 101240907, icon = "interaction_4", talk_content = "……", talk_name = "前台",},
	[101240907] = { talk_id = 101240907, group_id = 1012409, type = 3, next_id = 101240908, talk_content = "……", talk_name = "祁玉",},
	[101240908] = { talk_id = 101240908, group_id = 1012409, type = 3, next_id = 101240909, talk_content = "……", talk_name = "<nick_name>",},
	[101240909] = { talk_id = 101240909, group_id = 1012409, type = 3, next_id = 101240910, talk_content = "怎么和我姐似的，多大的人了还怕痛，来人给我按着青乌！", talk_name = "祁玉",},
	[101240910] = { talk_id = 101240910, group_id = 1012409, type = 3, next_id = 101240911, talk_content = "感受到刻骨铭心的痛",},
	[101240911] = { talk_id = 101240911, group_id = 1012409, type = 3, next_id = 101240912, talk_content = "可以了，在止血之前建议不要进行剧烈运动哦，化验结束以后我们会发短信通知您的。", talk_name = "前台",},
	[101240912] = { talk_id = 101240912, group_id = 1012409, type = 1, talk_content = "好，接下来就去履行诏令吧。", talk_name = "祁玉",},
	[101241001] = { talk_id = 101241001, group_id = 1012410, type = 2, fade_in = 1, fade_out = true, hide_player = true, options = {{"我不记得有把手机给过你。",101241002,},{"你是黑客吗！？",101241002,},}, talk_content = "从这里开始就可以了吧，青乌，我已经替你手机安装好”诏令“的应用了。", talk_name = "祁玉",},
	[101241002] = { talk_id = 101241002, group_id = 1012410, type = 3, next_id = 101241003, talk_content = "这年头手机会自动安装些软件不是常识吗，祁朱……我姐姐她手机里面也经常一堆奇怪软件。", talk_name = "祁玉",},
	[101241003] = { talk_id = 101241003, group_id = 1012410, type = 3, next_id = 101241004, talk_content = "每天光替她删那些垃圾你知道就要耗费我多少时间……不，还是别说这个了。", talk_name = "祁玉",},
	[101241004] = { talk_id = 101241004, group_id = 1012410, type = 1, next_id = 101241005, talk_content = "……追识是有告诉我除了审查以外还需要做点事情来证明身份，但这个诏令到底是什么？", talk_name = "<nick_name>",},
	[101241005] = { talk_id = 101241005, group_id = 1012410, type = 1, next_id = 101241006, talk_content = "诏令是封侯直接分发的任务的称呼，诏令的内容有很多，但基本都是以维护城市和平为目的的任务。", talk_name = "祁玉",},
	[101241006] = { talk_id = 101241006, group_id = 1012410, type = 1, next_id = 101241007, talk_content = "而这些任务因为伴随有危险，所以自古以来也会交给脉者处理。", talk_name = "祁玉",},
	[101241007] = { talk_id = 101241007, group_id = 1012410, type = 1, next_id = 101241008, talk_content = "在古时候有不少希望获得居住权或者想要赚钱的脉者都会承接这些任务，城内有好几个组织都是靠着做“诏令”发家的。", talk_name = "祁玉",},
	[101241008] = { talk_id = 101241008, group_id = 1012410, type = 2, options = {{"听上去好高科技。",101241009,},{"所以我只要做诏令的任务就好了？",101241011,},}, talk_content = "另外一提，其实进入现代以后就没有封侯通过诏令来发放任务了，现在管理这个的是个AI。", talk_name = "祁玉",},
	[101241009] = { talk_id = 101241009, group_id = 1012410, type = 3, next_id = 101241010, talk_content = "也没多高级，我听说分类系统中还是有人工筛选环节的。", talk_name = "祁玉",},
	[101241010] = { talk_id = 101241010, group_id = 1012410, type = 3, next_id = 101241012, talk_content = "不说AI了，我们来这里是为了完成诏令的，我替你选了些简单的任务，接下来只要和我一起走就好！", talk_name = "祁玉",},
	[101241011] = { talk_id = 101241011, group_id = 1012410, type = 3, next_id = 101241012, talk_content = "是的，因为你是第一次所以任务就由我这边替你直接选了些简单的，你接下来只要和我一起走就好！", talk_name = "祁玉",},
	[101241012] = { talk_id = 101241012, group_id = 1012410, type = 1, next_id = 101241013, talk_content = "我还以为是我自己一个去解决那些任务。", talk_name = "<nick_name>",},
	[101241013] = { talk_id = 101241013, group_id = 1012410, type = 1, talk_content = "毕竟我答应了那个司长要多关照下你，总之快走，我下班还得回家给姐姐做饭呢！", talk_name = "祁玉",},
	[101241101] = { talk_id = 101241101, group_id = 1012411, type = 4, next_id = 101241102, aside = "<color=#FFEBA2>祁玉：</color>没想到还挺顺利的，趁着势头把第二个区域的事也解决了吧。", talk_content = "没想到还挺顺利的，趁着势头把第二个区域的事也解决了吧。", talk_name = "祁玉",},
	[101241102] = { talk_id = 101241102, group_id = 1012411, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>第二个区域……再打开那个应用看看吧。", talk_content = "第二个区域……再打开那个应用看看吧。", talk_name = "<nick_name>",},
	[101241201] = { talk_id = 101241201, group_id = 1012412, type = 4, next_id = 101241202, aside = "<color=#FFEBA2>祁玉：</color>完成诏令的全部任务以后就能通过应用交付，系统会自动计算你在这中间的努力，并且给予奖励。", talk_content = "完成诏令的全部任务以后就能通过应用交付，系统会自动计算你在这中间的努力，并且给予奖励。", talk_name = "祁玉",},
	[101241202] = { talk_id = 101241202, group_id = 1012412, type = 4, aside = "<color=#FFEBA2>祁玉：</color>当然，首当其冲的就是天月城的临时身份证明了，有了这个那才能谈“通行印鉴”的事情。", talk_content = "当然，首当其冲的就是天月城的临时身份证明了，有了这个那才能谈“通行印鉴”的事情。", talk_name = "祁玉",},
	[101241301] = { talk_id = 101241301, group_id = 1012413, type = 1, next_id = 101241302, fade_in = 1, fade_out = true, hide_player = true, talk_content = "这样事情就算解决了，青乌，你之后有空的话也可以用诏令应用做点任务。", talk_name = "祁玉",},
	[101241302] = { talk_id = 101241302, group_id = 1012413, type = 2, options = {{"天月城的气脉？",101241303,},{"我看情况吧。",101241306,},}, talk_content = "像你这样善于战斗的脉者其实不多见，假如你能多出点力的话应该能很快缓解天月城的气脉。", talk_name = "祁玉",},
	[101241303] = { talk_id = 101241303, group_id = 1012413, type = 3, next_id = 101241304, talk_content = "嗯，天月城是个反过来的城市，所以城市中流动的气脉能量其实一直都不太稳定。", talk_name = "祁玉",},
	[101241304] = { talk_id = 101241304, group_id = 1012413, type = 3, next_id = 101241305, talk_content = "最近城内外也发生了不少事，这更是让城里气脉偶尔出现断开的状况，密学院的教授们为此可是挠破头了。", talk_name = "祁玉",},
	[101241305] = { talk_id = 101241305, group_id = 1012413, type = 3, next_id = 101241308, talk_content = "听上去好像挺麻烦的。", talk_name = "<nick_name>",},
	[101241306] = { talk_id = 101241306, group_id = 1012413, type = 3, next_id = 101241307, talk_content = "我可不是在说笑，你要是有空一定要多做点诏令，毕竟青门的人基本不到最后关头都不出场。", talk_name = "祁玉",},
	[101241307] = { talk_id = 101241307, group_id = 1012413, type = 3, next_id = 101241308, talk_content = "平时能协助维护气脉安稳的人可没几个呢。", talk_name = "祁玉",},
	[101241308] = { talk_id = 101241308, group_id = 1012413, type = 2, options = {{"要不直接叫祁朱吧。",101241309,},{"之前不是说回家做饭吗？",101241310,},}, talk_content = "啊，时间到了，青乌，我得去找祁朱……我姐了。", talk_name = "祁玉",},
	[101241309] = { talk_id = 101241309, group_id = 1012413, type = 3, talk_content = "在外人面前念祁朱的名字总觉得很怪……也罢，毕竟我们也一起战斗过了。", talk_name = "祁玉",},
	[101241310] = { talk_id = 101241310, group_id = 1012413, type = 3, talk_content = "找姐姐跟做饭是两件不冲突的事情……毕竟姐姐好像又跑去玩了。", talk_name = "祁玉",},
	[101241501] = { talk_id = 101241501, group_id = 1012415, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"下次也介绍你姐姐给我认识。",101241502,},{"今天谢谢你了。",101241503,},}, talk_content = "所以公务局那边的审查也结束了啊，那我就不继续闲聊了，毕竟祁朱可能又会跑到奇怪的地方。", talk_name = "祁玉",},
	[101241502] = { talk_id = 101241502, group_id = 1012415, type = 3, talk_content = "总觉得你在盘算什么。", talk_name = "祁玉",},
	[101241503] = { talk_id = 101241503, group_id = 1012415, type = 3, talk_content = "突然这么客气做什么……额，总之再见！", talk_name = "祁玉",},
	[101241601] = { talk_id = 101241601, group_id = 1012416, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>短信说证件会送到离歌那里，回去看看吧。", talk_content = "短信说证件会送到离歌那里，回去看看吧。", talk_name = "<nick_name>",},
	[101250101] = { talk_id = 101250101, group_id = 1012501, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>追识？", talk_content = "追识？", talk_name = "<nick_name>",},
	[101250201] = { talk_id = 101250201, group_id = 1012502, type = 1, next_id = 101250202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你为什么会在这里？", talk_name = "<nick_name>",},
	[101250202] = { talk_id = 101250202, group_id = 1012502, type = 2, options = {{"你见到离歌了？",101250203,},{"你要找我吗？",101250205,},}, talk_content = "我通过一些渠道发现了你住在这里，刚刚拜访的时候也受了这里主人的招待，所以就直接在这等了。", talk_name = "追识",},
	[101250203] = { talk_id = 101250203, group_id = 1012502, type = 3, next_id = 101250204, talk_content = "并没有，只是我在正门观察的时候她给我打了个电话让我进来等而已。", talk_name = "追识",},
	[101250204] = { talk_id = 101250204, group_id = 1012502, type = 3, next_id = 101250206, talk_content = "对了青乌，你还记得我之前说过有事想要拜托你吗？", talk_name = "追识",},
	[101250205] = { talk_id = 101250205, group_id = 1012502, type = 3, next_id = 101250206, talk_content = "嗯，就是之前在”办证大师“那儿说的我想拜托你的事。", talk_name = "追识",},
	[101250206] = { talk_id = 101250206, group_id = 1012502, type = 1, next_id = 101250207, talk_content = "我记得这个拜托的前提是拿到印鉴之后，你是想让我做什么？", talk_name = "<nick_name>",},
	[101250207] = { talk_id = 101250207, group_id = 1012502, type = 2, options = {{"你们不自己去吗？",101250208,},{"什么样的噬脉生物",101250211,},}, talk_content = "我刚刚也和离歌小姐说了我的委托，我希望你能代替维安司到地上去参加剿灭噬脉生物的行动。", talk_name = "追识",},
	[101250208] = { talk_id = 101250208, group_id = 1012502, type = 3, next_id = 101250209, talk_content = "维安司成员并不多，包括我在内都有着些不能离开天月城太久的任务，所以只能像这样寻求外力了。", talk_name = "追识",},
	[101250209] = { talk_id = 101250209, group_id = 1012502, type = 3, next_id = 101250210, talk_content = "只有我一个去的话也谈不上什么剿灭吧。", talk_name = "<nick_name>",},
	[101250210] = { talk_id = 101250210, group_id = 1012502, type = 3, next_id = 101250212, talk_content = "这次是由青门和巡卫共同组织的大行动，人数这点青乌你不用担心。", talk_name = "追识",},
	[101250211] = { talk_id = 101250211, group_id = 1012502, type = 3, next_id = 101250212, talk_content = "具体的情况应该要到地上才知道，这次行动由青门和巡卫共同组织，维安司是刚好被叫上的而已。", talk_name = "追识",},
	[101250212] = { talk_id = 101250212, group_id = 1012502, type = 1, next_id = 101250213, talk_content = "听着我好像只需要去充个场面就行。", talk_name = "<nick_name>",},
	[101250213] = { talk_id = 101250213, group_id = 1012502, type = 1, next_id = 101250214, talk_content = "对方也是这么想的，但我既然来拜托你就代表我希望你能做到更多。", talk_name = "追识",},
	[101250214] = { talk_id = 101250214, group_id = 1012502, type = 1, next_id = 101250215, talk_content = "我的目标是让维安司成为天月城新的秩序，为此必须要取得更多人的认可。", talk_name = "追识",},
	[101250215] = { talk_id = 101250215, group_id = 1012502, type = 2, options = {{"你太看得起我了。",101250216,},{"我尽力而为吧。",101250217,},{"报酬是什么？",101250218,},}, talk_content = "换句话说，我希望青乌你能在这次行动中以维安司的名义立下大功。", talk_name = "追识",},
	[101250216] = { talk_id = 101250216, group_id = 1012502, type = 3, next_id = 101250220, talk_content = "虽然和养父不同，但我在你战斗时看见了相似的决断感，这就是我认为你能办到的理由。", talk_name = "追识",},
	[101250217] = { talk_id = 101250217, group_id = 1012502, type = 3, next_id = 101250220, talk_content = "谢谢，有你这句话就好。", talk_name = "追识",},
	[101250218] = { talk_id = 101250218, group_id = 1012502, type = 3, next_id = 101250219, talk_content = "通行印鉴和身份证是这次的预付款，成功以后的报酬我也会和离歌小姐讨论过了，她说暂时对你保密。", talk_name = "追识",},
	[101250219] = { talk_id = 101250219, group_id = 1012502, type = 3, next_id = 101250220, talk_content = "……", talk_name = "<nick_name>",},
	[101250220] = { talk_id = 101250220, group_id = 1012502, type = 1, next_id = 101250221, talk_content = "另外还有一件事，我认为这次噬脉生物灾害是悬天在背后搞鬼。", talk_name = "追识",},
	[101250221] = { talk_id = 101250221, group_id = 1012502, type = 1, next_id = 101250222, talk_content = "你有证据吗？", talk_name = "<nick_name>",},
	[101250222] = { talk_id = 101250222, group_id = 1012502, type = 1, next_id = 101250223, talk_content = "我这边的线人的确发现了被悬天废弃的据点，据他汇报还有一位骑龙的脉者领导着他们。", talk_name = "追识",},
	[101250223] = { talk_id = 101250223, group_id = 1012502, type = 1, talk_content = "龙……？", talk_name = "<nick_name>",},
	[101250401] = { talk_id = 101250401, group_id = 1012504, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>那边……我记得有个很高的奇怪建筑。", talk_content = "那边……我记得有个很高的奇怪建筑。", talk_name = "<nick_name>",},
	[101250501] = { talk_id = 101250501, group_id = 1012505, type = 1, next_id = 101250502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "在这里停下来，青乌。", talk_name = "巡卫",},
	[101250502] = { talk_id = 101250502, group_id = 1012505, type = 1, next_id = 101250503, talk_content = "……我应该不认识你。", talk_name = "<nick_name>",},
	[101250503] = { talk_id = 101250503, group_id = 1012505, type = 2, options = {{"你是巡卫的人对吧？",101250504,},{"老古董？",101250505,},{"空港是？",101250506,},}, talk_content = "离歌老板向我交代过你会到这边来，好好的人不走空港偏要用这老古董。", talk_name = "巡卫",},
	[101250504] = { talk_id = 101250504, group_id = 1012505, type = 3, next_id = 101250507, talk_content = "光靠巡卫的工资可养不活自己，多几个老板很正常。", talk_name = "巡卫",},
	[101250505] = { talk_id = 101250505, group_id = 1012505, type = 3, next_id = 101250507, talk_content = "这东西的原型是200年前就有的东西，这不叫老古董啥叫老古董。", talk_name = "巡卫",},
	[101250506] = { talk_id = 101250506, group_id = 1012505, type = 3, next_id = 101250507, talk_content = "……就空港啊，你问的什么怪问题啊。", talk_name = "巡卫",},
	[101250507] = { talk_id = 101250507, group_id = 1012505, type = 1, next_id = 101250508, talk_content = "老板让我把你的通行印鉴何一些杂七杂八的交给你，你都拿着吧。", talk_name = "巡卫",},
	[101250508] = { talk_id = 101250508, group_id = 1012505, type = 1, next_id = 101250509, talk_content = "周转到这了啊……", talk_name = "<nick_name>",},
	[101250509] = { talk_id = 101250509, group_id = 1012505, type = 1, next_id = 101250510, talk_content = "这地方必须要有印鉴才能靠近，也算是提前检查了，青乌你等下就直接用电梯往上去吧。", talk_name = "巡卫",},
	[101250510] = { talk_id = 101250510, group_id = 1012505, type = 1, next_id = 101250511, talk_content = "上面有些什么吗？", talk_name = "<nick_name>",},
	[101250511] = { talk_id = 101250511, group_id = 1012505, type = 1, next_id = 101250512, talk_content = "脉者才能用的升降装置，你可以通过那个直达地面，我有按照吩咐让人来做过调整。", talk_name = "巡卫",},
	[101250512] = { talk_id = 101250512, group_id = 1012505, type = 1, next_id = 101250513, talk_content = "应该是不会出意外的了。", talk_name = "巡卫",},
	[101250513] = { talk_id = 101250513, group_id = 1012505, type = 1, talk_content = "……", talk_name = "<nick_name>",},
	[101250601] = { talk_id = 101250601, group_id = 1012506, type = 5, next_id = 101250602, aside = "<color=#FFEBA2>离歌：</color>只要和天钟悬的脉保持同调就能通过它前往地上，你准备好了吗？", call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 51, fade_in = 1, fade_out = true, hide_player = true, talk_content = "只要和天钟悬的脉保持同调就能通过它前往地上，你准备好了吗？", talk_name = "离歌",},
	[101250602] = { talk_id = 101250602, group_id = 1012506, type = 5, next_id = 101250603, aside = "<color=#FFEBA2><nick_name>：</color>离歌，我听下面的人说这是个老古董。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 51, talk_content = "离歌，我听下面的人说这是个老古董。", talk_name = "<nick_name>",},
	[101250603] = { talk_id = 101250603, group_id = 1012506, type = 5, next_id = 101250604, aside = "<color=#FFEBA2>离歌：</color>空港的确便利，但相对限制性有些多，尤其是我想单独和你会面的情况下。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 51, talk_content = "空港的确便利，但相对限制性有些多，尤其是我想单独和你会面的情况下。", talk_name = "离歌",},
	[101250604] = { talk_id = 101250604, group_id = 1012506, type = 5, next_id = 101250605, aside = "<color=#FFEBA2><nick_name>：</color>你在地上吗？", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 51, talk_content = "你在地上吗？", talk_name = "<nick_name>",},
	[101250605] = { talk_id = 101250605, group_id = 1012506, type = 5, next_id = 101250606, aside = "<color=#FFEBA2>离歌：</color>是的，我让人调整过天钟悬和地上的连接点，用尽全力跨越司世太阳设下的境界线。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 51, talk_content = "是的，我让人调整过天钟悬和地上的连接点，用尽全力跨越司世太阳设下的境界线。", talk_name = "离歌",},
	[101250606] = { talk_id = 101250606, group_id = 1012506, type = 5, next_id = 101250607, aside = "<color=#FFEBA2>离歌：</color>到地上来找我吧，青乌。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 51, talk_content = "到地上来找我吧，青乌。", talk_name = "离歌",},
	[101250607] = { talk_id = 101250607, group_id = 1012506, type = 5, aside = "<color=#FFEBA2><nick_name>：</color>……那看来你的谜语人日子就到此为止了。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 51, talk_content = "……那看来你的谜语人日子就到此为止了。", talk_name = "<nick_name>",},
	[101250701] = { talk_id = 101250701, group_id = 1012507, type = 5, next_id = 101250702, aside = "<color=#FFEBA2>离歌：</color>只要和天钟悬的脉保持同调就能通过它前往地上，你准备好了吗？", talk_content = "只要和天钟悬的脉保持同调就能通过它前往地上，你准备好了吗？", talk_name = "离歌",},
	[101250702] = { talk_id = 101250702, group_id = 1012507, type = 5, next_id = 101250703, aside = "<color=#FFEBA2>离歌：</color>离歌，我听下面的人说这是个老古董。", talk_content = "离歌，我听下面的人说这是个老古董。", talk_name = "离歌",},
	[101250703] = { talk_id = 101250703, group_id = 1012507, type = 5, next_id = 101250704, aside = "<color=#FFEBA2>离歌：</color>空港的确便利，但相对限制性有些多，尤其是我想单独和你会面的情况下。", talk_content = "空港的确便利，但相对限制性有些多，尤其是我想单独和你会面的情况下。", talk_name = "离歌",},
	[101250704] = { talk_id = 101250704, group_id = 1012507, type = 5, next_id = 101250705, aside = "<color=#FFEBA2>离歌：</color>你在地上吗？", talk_content = "你在地上吗？", talk_name = "离歌",},
	[101250705] = { talk_id = 101250705, group_id = 1012507, type = 2, child_type = 21, options = {{"你是巡卫的人对吧？",},{"老古董？",},{"空港是？",},}, talk_content = "是的，我让人调整过天钟悬和地上的连接点，用尽全力跨越司世太阳设下的境界线。", talk_name = "离歌",},
	[102010101] = { talk_id = 102010101, group_id = 1020101, type = 1, next_id = 102010102, fade_in = 1, fade_out = true, hide_player = true, talk_content = "我知道你有很多问题，但有话等下再说，\n现在要做的应该是敲响那边的钟悬。", talk_name = "司命",},
	[102010102] = { talk_id = 102010102, group_id = 1020101, type = 1, next_id = 102010103, talk_content = "……假如你说要到那个塔楼去的话，\n我只能和你讲我可没力气走到那边去了。", talk_name = "叙慕",},
	[102010103] = { talk_id = 102010103, group_id = 1020101, type = 1, next_id = 102010104, talk_content = "用飞的就可以，虽然准确点来说叫滑翔。", talk_name = "司命",},
	[102010104] = { talk_id = 102010104, group_id = 1020101, type = 1, next_id = 102010105, talk_content = "听上去挺危险的，不会往下掉吧。", talk_name = "叙慕",},
	[102010105] = { talk_id = 102010105, group_id = 1020101, type = 1, next_id = 102010106, talk_content = "担心什么，你要是不小心掉下去了那我再把你拉回来就是。", talk_name = "司命",},
	[102010106] = { talk_id = 102010106, group_id = 1020101, type = 1, talk_content = "总之把东西拿好，起飞！", talk_name = "司命",},
	[102010201] = { talk_id = 102010201, group_id = 1020102, type = 5, next_id = 102010202, fade_in = 1, fade_out = true,},
	[102010202] = { talk_id = 102010202, group_id = 1020102, type = 5,},
	[102010301] = { talk_id = 102010301, group_id = 1020103, type = 4, next_id = 102010302, aside = "<color=#FFEBA2>叙慕：</color>要平衡还挺难。", talk_content = "要平衡还挺难。", talk_name = "叙慕",},
	[102010302] = { talk_id = 102010302, group_id = 1020103, type = 4, aside = "<color=#FFEBA2>司命：</color>你捉紧就好了，目标是钟悬噢！", talk_content = "你捉紧就好了，目标是钟悬噢！", talk_name = "司命",},
	[102010401] = { talk_id = 102010401, group_id = 1020104, type = 1, next_id = 102010402, fade_in = 1, fade_out = true, hide_player = true, talk_content = "接下来要怎么办，司命。", talk_name = "叙慕",},
	[102010402] = { talk_id = 102010402, group_id = 1020104, type = 1, next_id = 102010403, talk_content = "我说了要敲钟吧，只要敲响了钟就能与附近的气脉连接，\n知道我们到底身在何处了。", talk_name = "司命",},
	[102010403] = { talk_id = 102010403, group_id = 1020104, type = 1, next_id = 102010404, talk_content = "还有这么便利的东西，这个是谁做出来的？", talk_name = "叙慕",},
	[102010404] = { talk_id = 102010404, group_id = 1020104, type = 2, options = {{"你到底活多久了。",102010405,},{"……现在还能用吗？",102010407,},}, talk_content = "是一个很自大的人类，姓甚名谁我不记得了，毕竟是好几百年前的事情。", talk_name = "司命",},
	[102010405] = { talk_id = 102010405, group_id = 1020104, type = 3, next_id = 102010406, talk_content = "过了一定年月以后就没数过了，记这些事情也没什么用。", talk_name = "司命",},
	[102010406] = { talk_id = 102010406, group_id = 1020104, type = 3, talk_content = "倒是快点敲钟吧，不然那个组织的人又要追过来了。", talk_name = "司命",},
	[102010407] = { talk_id = 102010407, group_id = 1020104, type = 3, next_id = 102010408, talk_content = "这个不是复杂机械，只是个向气脉告拜的祭坛而已，只要形式满足，即便再过多少年月也能使用。", talk_name = "司命",},
	[102010408] = { talk_id = 102010408, group_id = 1020104, type = 3, talk_content = "倒是快点敲钟吧，不然那个组织的人又要追过来了。", talk_name = "司命",},
	[102010501] = { talk_id = 102010501, group_id = 1020105, type = 1, next_id = 102010502, fade_in = 1, fade_out = true, hide_player = true, talk_content = "有种豁然开朗的感觉……真奇妙。", talk_name = "叙慕",},
	[102010502] = { talk_id = 102010502, group_id = 1020105, type = 1, next_id = 102010503, talk_content = "好，那接下来就朝着太阳的方向出发吧！", talk_name = "司命",},
	[102010503] = { talk_id = 102010503, group_id = 1020105, type = 1, next_id = 102010504, talk_content = "我没答应过那个。", talk_name = "叙慕",},
	[102010504] = { talk_id = 102010504, group_id = 1020105, type = 1, next_id = 102010505, talk_content = "？", talk_name = "司命",},
	[102010505] = { talk_id = 102010505, group_id = 1020105, type = 1, next_id = 102010506, talk_content = "世界如何，太阳如何另说。", talk_name = "叙慕",},
	[102010506] = { talk_id = 102010506, group_id = 1020105, type = 1, next_id = 102010507, talk_content = "但对于我来说，当务之急并不是什么拯救世界，\n而是搞懂自己是谁。", talk_name = "叙慕",},
	[102010507] = { talk_id = 102010507, group_id = 1020105, type = 1, next_id = 102010508, talk_content = "搞懂自己为什么会被人当成猎物追捕。", talk_name = "叙慕",},
	[102010508] = { talk_id = 102010508, group_id = 1020105, type = 1, next_id = 102010509, talk_content = "……", talk_name = "司命",},
	[102010509] = { talk_id = 102010509, group_id = 1020105, type = 1, next_id = 102010510, talk_content = "你也好，离歌那个组织也好，你们都似乎想利用我做些什么事情。", talk_name = "叙慕",},
	[102010510] = { talk_id = 102010510, group_id = 1020105, type = 1, next_id = 102010511, talk_content = "既然现在有偷闲的时间，那应该能跟我好好说说吧，\n我到底是什么人。", talk_name = "叙慕",},
	[102010511] = { talk_id = 102010511, group_id = 1020105, type = 1, next_id = 102010512, talk_content = "我说了很多遍了呀，你是青乌，是救世之人。", talk_name = "司命",},
	[102010512] = { talk_id = 102010512, group_id = 1020105, type = 1, next_id = 102010513, talk_content = "你这叫糊弄，就算我是救世之人也得有个前因后果吧？", talk_name = "叙慕",},
	[102010513] = { talk_id = 102010513, group_id = 1020105, type = 2, options = {{"你该不会要说你也失忆了吧？",102010514,},{"不知道却把我叫醒吗？",102010514,},}, talk_content = "别这样看着我，我什么都不知道。", talk_name = "司命",},
	[102010514] = { talk_id = 102010514, group_id = 1020105, type = 3, next_id = 102010515, talk_content = "现在的我的确不知道你的“前因后果”，但要是能变回真体的话大概就能记起关于你的一切了。", talk_name = "司命",},
	[102010515] = { talk_id = 102010515, group_id = 1020105, type = 3, next_id = 102010516, talk_content = "……", talk_name = "叙慕",},
	[102010516] = { talk_id = 102010516, group_id = 1020105, type = 3, next_id = 102010517, talk_content = "要怀疑我就尽管怀疑好了，但你可要记得是我把你沉睡中唤醒，\n带你自由的人。", talk_name = "司命",},
	[102010517] = { talk_id = 102010517, group_id = 1020105, type = 3, next_id = 102010518, talk_content = "一路上我也有为你解疑，为你寻回记忆感到喜悦，\n要害你的人可不会这样吧？", talk_name = "司命",},
	[102010518] = { talk_id = 102010518, group_id = 1020105, type = 3, next_id = 102010519, talk_content = "……这点我的确没法反驳。", talk_name = "叙慕",},
	[102010519] = { talk_id = 102010519, group_id = 1020105, type = 3, next_id = 102010520, talk_content = "实际你现在除了依靠我就没有别的好办法了。", talk_name = "司命",},
	[102010520] = { talk_id = 102010520, group_id = 1020105, type = 3, next_id = 102010521, talk_content = "你就不能在我听不见的地方说这句话。", talk_name = "叙慕",},
	[102010521] = { talk_id = 102010521, group_id = 1020105, type = 3, next_id = 102010522, talk_content = "这叫诚实。", talk_name = "司命",},
	[102010522] = { talk_id = 102010522, group_id = 1020105, type = 3, next_id = 102010523, talk_content = "啾！", talk_name = "？？？",},
	[102010523] = { talk_id = 102010523, group_id = 1020105, type = 3, next_id = 102010524, talk_content = "呃，瘟神出现了！", talk_name = "司命",},
	[102010524] = { talk_id = 102010524, group_id = 1020105, type = 3, next_id = 102010525, talk_content = "啾！", talk_name = "脉灵",},
	[102010525] = { talk_id = 102010525, group_id = 1020105, type = 3, next_id = 102010526, talk_content = "这个可比你可爱多了，话说它好像在传达些什么。", talk_name = "叙慕",},
	[102010526] = { talk_id = 102010526, group_id = 1020105, type = 3, next_id = 102010527, talk_content = "啾！！！", talk_name = "脉灵",},
	[102010527] = { talk_id = 102010527, group_id = 1020105, type = 3, next_id = 102010528, talk_content = "好像在往那边指哎，有什么吗？", talk_name = "司命",},
	[102010528] = { talk_id = 102010528, group_id = 1020105, type = 3, next_id = 102010529, talk_content = "那边好像有个小基地，不对……据点？", talk_name = "叙慕",},
	[102010529] = { talk_id = 102010529, group_id = 1020105, type = 3, talk_content = "剩下的话之后再说，我们去看看吧，司命。", talk_name = "叙慕",},
	[102030101] = { talk_id = 102030101, group_id = 1020301, type = 4, next_id = 102030102, aside = "<color=#FFEBA2>司命：</color>我觉得你这人是带点歧视的。", talk_content = "我觉得你这人是带点歧视的。", talk_name = "司命",},
	[102030102] = { talk_id = 102030102, group_id = 1020301, type = 4, next_id = 102030103, aside = "<color=#FFEBA2>叙慕：</color>怎讲。", talk_content = "怎讲。", talk_name = "叙慕",},
	[102030103] = { talk_id = 102030103, group_id = 1020301, type = 4, next_id = 102030104, aside = "<color=#FFEBA2>司命：</color>我拜托你的时候可没见\n你有刚才那么爽快。", talk_content = "我拜托你的时候可没见\n你有刚才那么爽快。", talk_name = "司命",},
	[102030104] = { talk_id = 102030104, group_id = 1020301, type = 4, next_id = 102030105, aside = "<color=#FFEBA2>叙慕：</color>要不你也换个讨喜点的样子。", talk_content = "要不你也换个讨喜点的样子。", talk_name = "叙慕",},
	[102030105] = { talk_id = 102030105, group_id = 1020301, type = 4, aside = "<color=#FFEBA2>司命：</color>别把我尊贵的玉体和那种烂大街的货\n相提并论！", talk_content = "别把我尊贵的玉体和那种烂大街的货\n相提并论！", talk_name = "司命",},
	[102030201] = { talk_id = 102030201, group_id = 1020302, type = 1, next_id = 102030202, hide_player = true, talk_content = "呼，一路这么走到现在，总算是有点“文明”的感觉了。", talk_name = "叙慕",},
	[102030202] = { talk_id = 102030202, group_id = 1020302, type = 1, next_id = 102030203, talk_content = "毕竟我们一直都在走崎岖的山路，碰不见人也是正常。", talk_name = "司命",},
	[102030203] = { talk_id = 102030203, group_id = 1020302, type = 1, next_id = 102030204, talk_content = "不过在这里面的似乎并不是人呢。", talk_name = "叙慕",},
	[102030204] = { talk_id = 102030204, group_id = 1020302, type = 1, next_id = 102030205, talk_content = "奇怪，可我明明闻到了人类的味道。", talk_name = "司命",},
	[102030205] = { talk_id = 102030205, group_id = 1020302, type = 1, next_id = 102030206, talk_content = "我还是第一次知道你有这种特异功能。", talk_name = "叙慕",},
	[102030206] = { talk_id = 102030206, group_id = 1020302, type = 1, next_id = 102030207, talk_content = "以后你还会知道更多的，\n那小瘟神大概就是想让我们解决一下这个状况。", talk_name = "司命",},
	[102030207] = { talk_id = 102030207, group_id = 1020302, type = 2, options = {{"怎么突然变积极了？",102030208,},{"你有什么计划吗？",102030210,},}, talk_content = "好，青乌，接下来就啪啪啪地几下解决敌人吧！", talk_name = "司命",},
	[102030208] = { talk_id = 102030208, group_id = 1020302, type = 3, next_id = 102030209, talk_content = "小善通大善，培养一下你对于拯救世界的使命感也没坏吧。", talk_name = "司命",},
	[102030209] = { talk_id = 102030209, group_id = 1020302, type = 3, next_id = 102030212, talk_content = "这话倒是让我的积极性下降了。", talk_name = "叙慕",},
	[102030210] = { talk_id = 102030210, group_id = 1020302, type = 3, next_id = 102030211, talk_content = "啪啪啪地解决敌人就好了。", talk_name = "司命",},
	[102030211] = { talk_id = 102030211, group_id = 1020302, type = 3, next_id = 102030212, talk_content = "讲废话一流啊，司命。", talk_name = "叙慕",},
	[102030212] = { talk_id = 102030212, group_id = 1020302, type = 1, next_id = 102030213, talk_content = "怪话放一边，贸然冲进去肯定要吃苦头，\n所以现在就是用佩从的好时机了。", talk_name = "司命",},
	[102030213] = { talk_id = 102030213, group_id = 1020302, type = 1, next_id = 102030214, talk_content = "又是我没听过的词语。", talk_name = "叙慕",},
	[102030214] = { talk_id = 102030214, group_id = 1020302, type = 1, next_id = 102030215, talk_content = "就是你之前拾得的玉佩，那个是生命的记录，灵魂的分魂，脉者们可以使用这个丰富自己的作战手段，然后……", talk_name = "司命",},
	[102030215] = { talk_id = 102030215, group_id = 1020302, type = 1, next_id = 102030216, talk_content = "简单点。", talk_name = "叙慕",},
	[102030216] = { talk_id = 102030216, group_id = 1020302, type = 1, next_id = 102030217, talk_content = "你只要把能量灌入其中，就能把那个离歌的幻影\n叫出来让她帮你解决敌人了。", talk_name = "司命",},
	[102030217] = { talk_id = 102030217, group_id = 1020302, type = 1, next_id = 102030218, talk_content = "这样没危险吗？", talk_name = "叙慕",},
	[102030218] = { talk_id = 102030218, group_id = 1020302, type = 1, next_id = 102030219, talk_content = "叫出来的佩从并没有意识，只是能量的瞬间具象而已。", talk_name = "司命",},
	[102030219] = { talk_id = 102030219, group_id = 1020302, type = 1, talk_content = "百闻不如一见，我教你先用一次吧。", talk_name = "司命",},
	[102030301] = { talk_id = 102030301, group_id = 1020303, type = 4, next_id = 102030302, aside = "<color=#FFEBA2>司命：</color>青乌，人都在那里面，\n那里气味最浓了！", talk_content = "青乌，人都在那里面，\n那里气味最浓了！", talk_name = "司命",},
	[102030302] = { talk_id = 102030302, group_id = 1020303, type = 4, next_id = 102030303, aside = "<color=#FFEBA2>叙慕：</color>这门能直接开吗？", talk_content = "这门能直接开吗？", talk_name = "叙慕",},
	[102030303] = { talk_id = 102030303, group_id = 1020303, type = 4, aside = "<color=#FFEBA2>司命：</color>总会有办法的。", talk_content = "总会有办法的。", talk_name = "司命",},
	[102030401] = { talk_id = 102030401, group_id = 1020304, type = 1, next_id = 102030402, fade_in = 1, fade_out = true, hide_player = true, talk_content = "得，得救了……", talk_name = "被救者",},
	[102030402] = { talk_id = 102030402, group_id = 1020304, type = 1, next_id = 102030403, talk_content = "外面真的没有噬脉生物了吗？", talk_name = "被救者",},
	[102030403] = { talk_id = 102030403, group_id = 1020304, type = 1, next_id = 102030404, talk_content = "看来是安全了。", talk_name = "被救者",},
	[102030404] = { talk_id = 102030404, group_id = 1020304, type = 1, next_id = 102030405, talk_content = "打开门以后人一下子就涌出来了呢，看来他们并不是被困，\n而是躲在里面的。", talk_name = "司命",},
	[102030405] = { talk_id = 102030405, group_id = 1020304, type = 2, options = {{"（告知名字）",102030406,},}, talk_content = "感谢两位，我的名字叫温罗，敢问义人的名字是？", talk_name = "温罗",},
	[102030406] = { talk_id = 102030406, group_id = 1020304, type = 3, next_id = 102030407, talk_content = "叫我青乌就好，这只鸟也是这么叫的。", talk_name = "叙慕",},
	[102030407] = { talk_id = 102030407, group_id = 1020304, type = 3, next_id = 102030408, talk_content = "话说回来，这里的状况到底是？", talk_name = "叙慕",},
	[102030408] = { talk_id = 102030408, group_id = 1020304, type = 2, options = {{"脉者……？",102030409,},}, talk_content = "如你所见，我们的据点遭到怪物袭击，由于没有像您这样的脉者在场，我们只好躲藏在仓库之中等候救援。", talk_name = "温罗",},
	[102030409] = { talk_id = 102030409, group_id = 1020304, type = 3, next_id = 102030410, talk_content = "就是能使用特殊力量的人的称呼，当然也只有人类之间会这么叫而已。", talk_name = "司命",},
	[102030410] = { talk_id = 102030410, group_id = 1020304, type = 3, next_id = 102030411, talk_content = "也就是说其他人并不能像我那样让剑上带火吗？", talk_name = "叙慕",},
	[102030411] = { talk_id = 102030411, group_id = 1020304, type = 2, options = {{"听着很玄乎。",102030412,},{"那离歌也是被选上的？",102030414,},}, talk_content = "那当然不行，这可是受到天地眷顾，被气脉选上的人才能拥有的力量。", talk_name = "司命",},
	[102030412] = { talk_id = 102030412, group_id = 1020304, type = 3, next_id = 102030413, talk_content = "也没那么玄乎，你就当个人体质就好，脉者的身体都会有一些特别的特征，常人则没有。", talk_name = "司命",},
	[102030413] = { talk_id = 102030413, group_id = 1020304, type = 3, next_id = 102030416, talk_content = "用这个可以简单区分对面的人类是不是脉者噢。", talk_name = "司命",},
	[102030414] = { talk_id = 102030414, group_id = 1020304, type = 3, next_id = 102030415, talk_content = "嗯，毕竟被气脉选上与个人的道德无关，恶用力量的大有人在。", talk_name = "司命",},
	[102030415] = { talk_id = 102030415, group_id = 1020304, type = 3, next_id = 102030416, talk_content = "力量与道德无关，这个道理我倒是能明白呢。", talk_name = "叙慕",},
	[102030416] = { talk_id = 102030416, group_id = 1020304, type = 1, next_id = 102030417, talk_content = "呃，两位义人……你们聊好了吗？", talk_name = "温罗",},
	[102030417] = { talk_id = 102030417, group_id = 1020304, type = 1, next_id = 102030418, talk_content = "啊……抱歉，刚刚因为听到了不认识的词语所以走了神。", talk_name = "叙慕",},
	[102030418] = { talk_id = 102030418, group_id = 1020304, type = 1, next_id = 102030419, talk_content = "不要紧，倒是义人您竟然会不知道脉者……", talk_name = "温罗",},
	[102030419] = { talk_id = 102030419, group_id = 1020304, type = 1, next_id = 102030420, talk_content = "这家伙是乡下来的，你就不要介意这么多了。", talk_name = "司命",},
	[102030420] = { talk_id = 102030420, group_id = 1020304, type = 1, next_id = 102030421, talk_content = "谁是乡下来的。", talk_name = "叙慕",},
	[102030421] = { talk_id = 102030421, group_id = 1020304, type = 1, next_id = 102030422, talk_content = "这里先闭嘴，别让不认识的人知道太多你的情报。（小声）", talk_name = "司命",},
	[102030422] = { talk_id = 102030422, group_id = 1020304, type = 1, next_id = 102030423, talk_content = "这应该在我把青乌二字报出来之前说。", talk_name = "叙慕",},
	[102030423] = { talk_id = 102030423, group_id = 1020304, type = 1, next_id = 102030424, talk_content = "呃，假若是从外面来的话那义人们的目标肯定到天月城没错吧？", talk_name = "温罗",},
	[102030424] = { talk_id = 102030424, group_id = 1020304, type = 1, next_id = 102030425, talk_content = "你说的天月城是指的天上的那个吗？", talk_name = "温罗",},
	[102030425] = { talk_id = 102030425, group_id = 1020304, type = 1, next_id = 102030426, talk_content = "是的，呃，难道你们只是路过？", talk_name = "司命",},
	[102030426] = { talk_id = 102030426, group_id = 1020304, type = 1, next_id = 102030427, talk_content = "不，我现在的确想去看看……所以该怎么才能到上面去？", talk_name = "叙慕",},
	[102030427] = { talk_id = 102030427, group_id = 1020304, type = 1, next_id = 102030428, talk_content = "需要取个通行许可，这附近有个小镇熙来，在熙来中有一个特别显眼的建筑，名为维安司。", talk_name = "温罗",},
	[102030428] = { talk_id = 102030428, group_id = 1020304, type = 1, next_id = 102030429, talk_content = "我会提前和那边联系说你们两位会到那边去，详细的手续事宜就请你们到那边再确认吧。", talk_name = "温罗",},
	[102030429] = { talk_id = 102030429, group_id = 1020304, type = 1, next_id = 102030430, talk_content = "也就是说你会帮我们打点吗，不会被赶出来吧？", talk_name = "司命",},
	[102030430] = { talk_id = 102030430, group_id = 1020304, type = 1, next_id = 102030431, talk_content = "那当然，毕竟你们可是救命恩人呢。", talk_name = "温罗",},
	[102030431] = { talk_id = 102030431, group_id = 1020304, type = 1, next_id = 102030432, talk_content = "这还差不多，另外再给点钱……", talk_name = "司命",},
	[102030432] = { talk_id = 102030432, group_id = 1020304, type = 1, talk_content = "别贪得无厌。", talk_name = "叙慕",},
	[102030501] = { talk_id = 102030501, group_id = 1020305, type = 1, next_id = 102030502, talk_content = "话说回来，你们之后要怎么回去，路上也有噬脉生物徘徊。", talk_name = "叙慕",},
	[102030502] = { talk_id = 102030502, group_id = 1020305, type = 1, next_id = 102030503, talk_content = "不用担心，我们知道安全回去的道路，就不用劳烦义人你们了。", talk_name = "温罗",},
	[102030503] = { talk_id = 102030503, group_id = 1020305, type = 1, next_id = 102030504, talk_content = "（别没事找事，她很明显就是不想再麻烦我们了你不知道吗？）", talk_name = "司命",},
	[102030504] = { talk_id = 102030504, group_id = 1020305, type = 1, next_id = 102030505, talk_content = "之前不才说要培养我的使命感云云的吗？", talk_name = "叙慕",},
	[102030505] = { talk_id = 102030505, group_id = 1020305, type = 1, talk_content = "呃……", talk_name = "司命",},
	[102030601] = { talk_id = 102030601, group_id = 1020306, type = 4, next_id = 102030602, aside = "<color=#FFEBA2>司命：</color>话说回来，下面有几只小从士，拿那个来练练手吧。", talk_content = "话说回来，下面有几只小从士，拿那个来练练手吧。", talk_name = "司命",},
	[102030602] = { talk_id = 102030602, group_id = 1020306, type = 4, aside = "<color=#FFEBA2>叙慕：</color>练手？", talk_content = "练手？", talk_name = "叙慕",},
	[102030701] = { talk_id = 102030701, group_id = 1020307, type = 1, next_id = 102030702, fade_in = 1, fade_out = true, hide_player = true, talk_content = "这里应该就不会引起警觉了，青乌，接下来我就教你使用佩从的方法吧。", talk_name = "司命",},
	[102030702] = { talk_id = 102030702, group_id = 1020307, type = 1, next_id = 102030703, talk_content = "又是我没听过的词语。", talk_name = "叙慕",},
	[102030703] = { talk_id = 102030703, group_id = 1020307, type = 1, next_id = 102030704, talk_content = "就是你之前拾得的玉佩，那个是生命的记录，灵魂的分魂，脉者们可以使用这个丰富自己的作战手段，然后……", talk_name = "司命",},
	[102030704] = { talk_id = 102030704, group_id = 1020307, type = 1, next_id = 102030705, talk_content = "简单点。", talk_name = "叙慕",},
	[102030705] = { talk_id = 102030705, group_id = 1020307, type = 1, next_id = 102030706, talk_content = "你只要把能量灌入其中，就能把那个离歌的幻影\n叫出来让她帮你解决敌人了。", talk_name = "司命",},
	[102030706] = { talk_id = 102030706, group_id = 1020307, type = 1, next_id = 102030707, talk_content = "这样没危险吗？", talk_name = "叙慕",},
	[102030707] = { talk_id = 102030707, group_id = 1020307, type = 1, next_id = 102030708, talk_content = "叫出来的佩从并没有意识，只是能量的瞬间具象而已。", talk_name = "司命",},
	[102030708] = { talk_id = 102030708, group_id = 1020307, type = 1, talk_content = "百闻不如一见，你先按照我说的去做吧。", talk_name = "司命",},
	[102030801] = { talk_id = 102030801, group_id = 1020308, type = 1, next_id = 102030802, fade_in = 1, fade_out = true, hide_player = true, talk_content = "呼，一路这么走到现在，总算是有点“文明”的感觉了。", talk_name = "叙慕",},
	[102030802] = { talk_id = 102030802, group_id = 1020308, type = 1, next_id = 102030803, talk_content = "毕竟我们一直都在走崎岖的山路，碰不见人也是正常。", talk_name = "司命",},
	[102030803] = { talk_id = 102030803, group_id = 1020308, type = 1, next_id = 102030804, talk_content = "不过在这里面的似乎并不是人呢。", talk_name = "叙慕",},
	[102030804] = { talk_id = 102030804, group_id = 1020308, type = 1, next_id = 102030805, talk_content = "奇怪，可我明明闻到了人类的味道。", talk_name = "司命",},
	[102030805] = { talk_id = 102030805, group_id = 1020308, type = 1, next_id = 102030806, talk_content = "我还是第一次知道你有这种特异功能。", talk_name = "叙慕",},
	[102030806] = { talk_id = 102030806, group_id = 1020308, type = 1, next_id = 102030807, talk_content = "以后你还会知道更多的，\n那小瘟神大概就是想让我们解决一下这个状况。", talk_name = "司命",},
	[102030807] = { talk_id = 102030807, group_id = 1020308, type = 2, options = {{"怎么突然变积极了？",102030808,},{"你有什么计划吗？",102030810,},}, talk_content = "好，青乌，接下来就啪啪啪地几下解决敌人吧！", talk_name = "司命",},
	[102030808] = { talk_id = 102030808, group_id = 1020308, type = 3, next_id = 102030809, talk_content = "小善通大善，培养一下你对于拯救世界的使命感也没坏吧。", talk_name = "司命",},
	[102030809] = { talk_id = 102030809, group_id = 1020308, type = 3, talk_content = "这话倒是让我的积极性下降了。", talk_name = "叙慕",},
	[102030810] = { talk_id = 102030810, group_id = 1020308, type = 3, next_id = 102030811, talk_content = "啪啪啪地解决敌人就好了。", talk_name = "司命",},
	[102030811] = { talk_id = 102030811, group_id = 1020308, type = 3, talk_content = "讲废话一流啊，司命。", talk_name = "叙慕",},
	[102030901] = { talk_id = 102030901, group_id = 1020309, type = 4, next_id = 102030902, aside = "<color=#FFEBA2>叙慕：</color>没想到那个玉佩还能干这种事。", talk_content = "没想到那个玉佩还能干这种事。", talk_name = "叙慕",},
	[102030902] = { talk_id = 102030902, group_id = 1020309, type = 4, next_id = 102030903, aside = "<color=#FFEBA2>司命：</color>佩从能干的事还有很多，要是有那个意思多收集些也不是坏事。", talk_content = "佩从能干的事还有很多，要是有那个意思多收集些也不是坏事。", talk_name = "司命",},
	[102030903] = { talk_id = 102030903, group_id = 1020309, type = 4, aside = "<color=#FFEBA2>司命：</color>倒是脉灵刚刚指的地方的确有什么奇怪的东西，我们去看看吧，青乌。", talk_content = "倒是脉灵刚刚指的地方的确有什么奇怪的东西，我们去看看吧，青乌。", talk_name = "司命",},
	[102040101] = { talk_id = 102040101, group_id = 1020401, type = 4, next_id = 102040102, aside = "<color=#FFEBA2>司命：</color>青乌，你有什么心事吗？", talk_content = "青乌，你有什么心事吗？", talk_name = "司命",},
	[102040102] = { talk_id = 102040102, group_id = 1020401, type = 4, next_id = 102040103, aside = "<color=#FFEBA2>叙慕：</color>一切都是如此熟悉又陌生。", talk_content = "一切都是如此熟悉又陌生。", talk_name = "叙慕",},
	[102040103] = { talk_id = 102040103, group_id = 1020401, type = 4, next_id = 102040104, aside = "<color=#FFEBA2>司命：</color>？", talk_content = "？", talk_name = "司命",},
	[102040104] = { talk_id = 102040104, group_id = 1020401, type = 4, next_id = 102040105, aside = "<color=#FFEBA2>叙慕：</color>我是说现在的世界，我能够明白很多\n概念，但又有很多概念是我不认得的。", talk_content = "我是说现在的世界，我能够明白很多\n概念，但又有很多概念是我不认得的。", talk_name = "叙慕",},
	[102040105] = { talk_id = 102040105, group_id = 1020401, type = 4, next_id = 102040106, aside = "<color=#FFEBA2>叙慕：</color>（就彷佛……我来到了另一个世界一般。）", talk_content = "（就彷佛……我来到了另一个世界一般。）", talk_name = "叙慕",},
	[102040106] = { talk_id = 102040106, group_id = 1020401, type = 4, next_id = 102040107, aside = "<color=#FFEBA2>叙慕：</color>无论如何，得先想办法回忆起一切。", talk_content = "无论如何，得先想办法回忆起一切。", talk_name = "叙慕",},
	[102040107] = { talk_id = 102040107, group_id = 1020401, type = 4, aside = "<color=#FFEBA2>司命：</color>可别忘了还有拯救世界！", talk_content = "可别忘了还有拯救世界！", talk_name = "司命",},
	[102040201] = { talk_id = 102040201, group_id = 1020402, type = 1, next_id = 102040202, fade_in = 1, fade_out = true, hide_player = true, talk_content = "到处都是人类的气味呢。", talk_name = "司命",},
	[102040202] = { talk_id = 102040202, group_id = 1020402, type = 1, next_id = 102040203, talk_content = "怎样，青乌你对这个有什么感触吗？", talk_name = "司命",},
	[102040203] = { talk_id = 102040203, group_id = 1020402, type = 1, next_id = 102040204, talk_content = "这和我认识中的城市没什么差异，除了街上有些奇怪的装置。", talk_name = "叙慕",},
	[102040204] = { talk_id = 102040204, group_id = 1020402, type = 1, next_id = 102040205, talk_content = "那些是气脉的传输管道，现在的人类都是靠这个来维持生活的。", talk_name = "司命",},
	[102040205] = { talk_id = 102040205, group_id = 1020402, type = 1, next_id = 102040206, talk_content = "气脉……也就是说那些怪物需要这个，然后人类也需要这个。", talk_name = "叙慕",},
	[102040206] = { talk_id = 102040206, group_id = 1020402, type = 1, next_id = 102040207, talk_content = "是全世界都需要这个，虽然没有什么感知，\n但你我的身体里面都存在气脉哦。", talk_name = "司命",},
	[102040207] = { talk_id = 102040207, group_id = 1020402, type = 1, next_id = 102040208, talk_content = "话说回来，你一个动物为什么会对人类社会的事情这么清楚？", talk_name = "叙慕",},
	[102040208] = { talk_id = 102040208, group_id = 1020402, type = 1, next_id = 102040209, talk_content = "这就是活得久的好处了。", talk_name = "司命",},
	[102040209] = { talk_id = 102040209, group_id = 1020402, type = 1, next_id = 102040210, talk_content = "青乌，在城市里可以调查到很多信息，\n但是在那之前先到那个温罗说的维安司去吧。", talk_name = "司命",},
	[102040210] = { talk_id = 102040210, group_id = 1020402, type = 1, next_id = 102040211, talk_content = "不搞点钱可没法在城里自由行动呢。", talk_name = "司命",},
	[102040211] = { talk_id = 102040211, group_id = 1020402, type = 1, talk_content = "这一点我只能同意了。", talk_name = "叙慕",},
	[102040301] = { talk_id = 102040301, group_id = 1020403, type = 4, next_id = 102040302, aside = "<color=#FFEBA2>叙慕：</color>城里挺和平的。", talk_content = "城里挺和平的。", talk_name = "叙慕",},
	[102040302] = { talk_id = 102040302, group_id = 1020403, type = 4, next_id = 102040303, aside = "<color=#FFEBA2>司命：</color>的确是这样。", talk_content = "的确是这样。", talk_name = "司命",},
	[102040303] = { talk_id = 102040303, group_id = 1020403, type = 4, aside = "<color=#FFEBA2>叙慕：</color>（所以真的有必要拯救世界吗？）", talk_content = "（所以真的有必要拯救世界吗？）", talk_name = "叙慕",},
	[102040401] = { talk_id = 102040401, group_id = 1020404, type = 5, next_id = 102040402, aside = "<color=#FFEBA2>司命：</color>是佩从玉……从人身上落下来还真是少见。", talk_content = "是佩从玉……从人身上落下来还真是少见。", talk_name = "司命",},
	[102040402] = { talk_id = 102040402, group_id = 1020404, type = 5, aside = "<color=#FFEBA2>司命：</color>喂，青乌，去把那个捡起来吧。", talk_content = "喂，青乌，去把那个捡起来吧。", talk_name = "司命",},
	[102040501] = { talk_id = 102040501, group_id = 1020405, type = 1, next_id = 102040502, fade_in = 1, fade_out = true, hide_npc_list = {8010207,}, hide_player = true, talk_content = "两位义人，我还在想你们会不会迷路了呢。", talk_name = "温罗",},
	[102040502] = { talk_id = 102040502, group_id = 1020405, type = 2, options = {{"你竟然会在意这个吗？",102040503,},{"（赞同司命的顾虑）",102040504,},}, talk_content = "唔，你刚刚好像在聊什么重要的事情呐，我们不会妨碍到你了吧。", talk_name = "司命",},
	[102040503] = { talk_id = 102040503, group_id = 1020405, type = 3, next_id = 102040505, talk_content = "我可是知书达理的神鸟，别把我当野蛮人。", talk_name = "司命",},
	[102040504] = { talk_id = 102040504, group_id = 1020405, type = 3, next_id = 102040505, talk_content = "在你们到的时候刚好聊完，所以没什么妨碍不妨碍的。", talk_name = "温罗",},
	[102040505] = { talk_id = 102040505, group_id = 1020405, type = 1, next_id = 102040506, talk_content = "倒是我没想到你小子竟然是个大人物呐，看着像是个官对吧！", talk_name = "司命",},
	[102040506] = { talk_id = 102040506, group_id = 1020405, type = 1, next_id = 102040507, talk_content = "只是个负责日常治安的小司长而已，算不上什么大人物。", talk_name = "温罗",},
	[102040507] = { talk_id = 102040507, group_id = 1020405, type = 2, options = {{"挺不错的。",102040508,},{"暂时没什么想法。",102040509,},}, talk_content = "对了，说到这里，义人们认为这座城市如何？", talk_name = "温罗",},
	[102040508] = { talk_id = 102040508, group_id = 1020405, type = 3, next_id = 102040510, talk_content = "那是再好不过了，街上还有很多商铺，你们之后大可到处逛逛。", talk_name = "温罗",},
	[102040509] = { talk_id = 102040509, group_id = 1020405, type = 3, next_id = 102040510, talk_content = "的确，一路上都是走马观花，谈不上什么感受呢。", talk_name = "司命",},
	[102040510] = { talk_id = 102040510, group_id = 1020405, type = 1, next_id = 102040511, talk_content = "……", talk_name = "温罗",},
	[102040511] = { talk_id = 102040511, group_id = 1020405, type = 1, next_id = 102040512, talk_content = "闻到了麻烦的气味。", talk_name = "司命",},
	[102040512] = { talk_id = 102040512, group_id = 1020405, type = 2, options = {{"清剿噬脉生物？",102040513,},{"我可能有别的事情要处理。",102040514,},}, talk_content = "我就开门见山吧，义人，我希望你们能再帮我一个忙。", talk_name = "温罗",},
	[102040513] = { talk_id = 102040513, group_id = 1020405, type = 3, next_id = 102040515, talk_content = "不愧是义人，明察秋毫啊。", talk_name = "温罗",},
	[102040514] = { talk_id = 102040514, group_id = 1020405, type = 3, next_id = 102040515, talk_content = "这是自然，我没有让义人强行接受的意思，\n但只是听听应该可以吧？", talk_name = "温罗",},
	[102040515] = { talk_id = 102040515, group_id = 1020405, type = 1, next_id = 102040516, talk_content = "就如义人进门前听见的那样，\n我们有准备组织清剿噬脉生物的行动。", talk_name = "温罗",},
	[102040516] = { talk_id = 102040516, group_id = 1020405, type = 1, next_id = 102040517, talk_content = "而之所以会组织这个行动，是因为最近噬脉生物有向城里靠近的迹象。", talk_name = "温罗",},
	[102040517] = { talk_id = 102040517, group_id = 1020405, type = 1, next_id = 102040518, talk_content = "很出奇吗，噬脉生物本来就是会往其他活物所在的地方聚集的吧？", talk_name = "司命",},
	[102040518] = { talk_id = 102040518, group_id = 1020405, type = 1, next_id = 102040519, talk_content = "但不同的点在于他们基本都大群行动，彷佛要攻下这个地区一样。", talk_name = "温罗",},
	[102040519] = { talk_id = 102040519, group_id = 1020405, type = 1, next_id = 102040520, talk_content = "而实际上，我们在外部建设的据点已经基本沦陷了。", talk_name = "温罗",},
	[102040520] = { talk_id = 102040520, group_id = 1020405, type = 1, next_id = 102040521, talk_content = "城里面倒是看不见这股紧张劲呢。", talk_name = "司命",},
	[102040521] = { talk_id = 102040521, group_id = 1020405, type = 1, next_id = 102040522, talk_content = "我们不希望市民过度恐慌，\n噬脉生物的袭击大家也都是习以为常了。", talk_name = "温罗",},
	[102040522] = { talk_id = 102040522, group_id = 1020405, type = 2, options = {{"你们防不住吗？",102040523,},{"那该怎么办？",102040524,},}, talk_content = "但假若再这样下去，\n噬脉生物们的下一个目标可能就是这座熙来城了。", talk_name = "温罗",},
	[102040523] = { talk_id = 102040523, group_id = 1020405, type = 3, next_id = 102040525, talk_content = "防卫的力量我们当然是有，但真在城内发生那样的战斗会很麻烦。", talk_name = "温罗",},
	[102040524] = { talk_id = 102040524, group_id = 1020405, type = 3, next_id = 102040525, talk_content = "在发生状况之前由我们主动出击，这是现在的普遍想法。", talk_name = "温罗",},
	[102040525] = { talk_id = 102040525, group_id = 1020405, type = 1, next_id = 102040526, talk_content = "唔……所以呢，你是想让我们干什么？", talk_name = "司命",},
	[102040526] = { talk_id = 102040526, group_id = 1020405, type = 1, next_id = 102040527, talk_content = "我希望拥有出色作战能力的义人能作为我们的协助者，\n为我们夺回“悬天”的据点。", talk_name = "温罗",},
	[102040527] = { talk_id = 102040527, group_id = 1020405, type = 1, next_id = 102040528, talk_content = "等等，悬天……你刚刚是说了悬天这两个字吗？", talk_name = "叙慕",},
	[102040528] = { talk_id = 102040528, group_id = 1020405, type = 1, next_id = 102040529, talk_content = "是，是的，难不成义人与那个大企业有什么关系吗？", talk_name = "温罗",},
	[102040529] = { talk_id = 102040529, group_id = 1020405, type = 1, next_id = 102040530, talk_content = "……", talk_name = "司命",},
	[102040530] = { talk_id = 102040530, group_id = 1020405, type = 1, next_id = 102040531, talk_content = "司命，这是怎么回事，他说那个是企业？", talk_name = "叙慕",},
	[102040531] = { talk_id = 102040531, group_id = 1020405, type = 1, next_id = 102040532, talk_content = "这种事等下再说。", talk_name = "司命",},
	[102040532] = { talk_id = 102040532, group_id = 1020405, type = 1, next_id = 102040533, talk_content = "……", talk_name = "叙慕",},
	[102040533] = { talk_id = 102040533, group_id = 1020405, type = 1, next_id = 102040534, talk_content = "……谈话似乎有些长了。", talk_name = "温罗",},
	[102040534] = { talk_id = 102040534, group_id = 1020405, type = 1, next_id = 102040535, talk_content = "两位义人，据点的位置我会为你们标记起来，\n假若有意的话，请务必协助我们。", talk_name = "温罗",},
	[102040535] = { talk_id = 102040535, group_id = 1020405, type = 1, talk_content = "另外先前说好的酬谢我也准备好了，请在离开时一并带走吧。", talk_name = "温罗",},
	[102050101] = { talk_id = 102050101, group_id = 1020501, type = 4, next_id = 102050102, aside = "<color=#FFEBA2>叙慕：</color>司命，你能说明一下刚刚那个吧，\n关于悬天的。", talk_content = "司命，你能说明一下刚刚那个吧，\n关于悬天的。", talk_name = "叙慕",},
	[102050102] = { talk_id = 102050102, group_id = 1020501, type = 4, aside = "<color=#FFEBA2>司命：</color>找个人少些的地方吧，我看城外就不错。", talk_content = "找个人少些的地方吧，我看城外就不错。", talk_name = "司命",},
	[102050201] = { talk_id = 102050201, group_id = 1020502, type = 1, next_id = 102050202, talk_content = "咳咳。", talk_name = "司命",},
	[102050202] = { talk_id = 102050202, group_id = 1020502, type = 2, options = {{"更详细的呢？",102050203,},{"他们不应该是什么邪恶组织才对吗？",102050204,},}, talk_content = "是的，追缉你的悬天组织是当今世界首屈一指的大企业。", talk_name = "司命",},
	[102050203] = { talk_id = 102050203, group_id = 1020502, type = 3, next_id = 102050205, talk_content = "喜欢刨根问底的小朋友真让人烦心呐。", talk_name = "司命",},
	[102050204] = { talk_id = 102050204, group_id = 1020502, type = 3, next_id = 102050205, talk_content = "邪恶组织和企业并不是互斥的东西呐。", talk_name = "司命",},
	[102050205] = { talk_id = 102050205, group_id = 1020502, type = 1, next_id = 102050206, talk_content = "悬天是一个大型能源企业，大概是在50年前吧，他们表示自己能采集气脉能量，并且将其转化为人们可用的能源。", talk_name = "司命",},
	[102050206] = { talk_id = 102050206, group_id = 1020502, type = 1, next_id = 102050207, talk_content = "自此世界各处就遍布悬天的能源设施，分属机构，甚至出现了贴着他们铭牌的武装队伍。", talk_name = "司命",},
	[102050207] = { talk_id = 102050207, group_id = 1020502, type = 1, next_id = 102050208, talk_content = "现在想起我也因为那个受了不少罪呐。", talk_name = "司命",},
	[102050208] = { talk_id = 102050208, group_id = 1020502, type = 1, next_id = 102050209, talk_content = "别岔开话题。", talk_name = "叙慕",},
	[102050209] = { talk_id = 102050209, group_id = 1020502, type = 2, options = {{"那不是你想让我消灭的东西吗？",102050210,},{"事情变麻烦起来了。",102050210,},}, talk_content = "我不是人类，所以更多的详细不太清楚，但最终的结果就是他们在大地上升起了九个说是能更好提取与分配能量的太阳。", talk_name = "司命",},
	[102050210] = { talk_id = 102050210, group_id = 1020502, type = 3, next_id = 102050211, talk_content = "总之，世界在那九个太阳升起之后就发生了诡异的变化，在人类所感知不到的地方各种东西开始了蠢动。", talk_name = "司命",},
	[102050211] = { talk_id = 102050211, group_id = 1020502, type = 3, next_id = 102050212, talk_content = "比如呢？", talk_name = "叙慕",},
	[102050212] = { talk_id = 102050212, group_id = 1020502, type = 3, next_id = 102050213, talk_content = "就比如噬脉生物们开始大批量向人类所在地攻击这件事。", talk_name = "司命",},
	[102050213] = { talk_id = 102050213, group_id = 1020502, type = 3, next_id = 102050214, talk_content = "还有，路上不是能看见枯萎的，被破坏的气脉吗，那些就是太阳过度割取能量的结果。", talk_name = "司命",},
	[102050214] = { talk_id = 102050214, group_id = 1020502, type = 2, options = {{"你没有和其他人说这件事吗？",102050215,},{"悬天是知道这个后果的吗？",102050216,},}, talk_content = "要是继续放任那九个太阳世界就会迎来终结，\n毕竟作为生命之源的气脉都要没有了呐。", talk_name = "司命",},
	[102050215] = { talk_id = 102050215, group_id = 1020502, type = 3, next_id = 102050217, talk_content = "说也没用吧，就如现在，你对我的话也半信半疑不是吗？", talk_name = "司命",},
	[102050216] = { talk_id = 102050216, group_id = 1020502, type = 3, next_id = 102050217, talk_content = "这我就不晓得了，但我估计是知道的吧。", talk_name = "司命",},
	[102050217] = { talk_id = 102050217, group_id = 1020502, type = 1, next_id = 102050218, talk_content = "……我们去温罗说的悬天据点看看。", talk_name = "叙慕",},
	[102050218] = { talk_id = 102050218, group_id = 1020502, type = 1, next_id = 102050219, talk_content = "怎么，有心拯救世界了吗？", talk_name = "司命",},
	[102050219] = { talk_id = 102050219, group_id = 1020502, type = 1, next_id = 102050220, talk_content = "我的记忆，破坏世界的太阳……假若一切的源头都是那个悬天的话，那走一趟也没坏。", talk_name = "叙慕",},
	[102050220] = { talk_id = 102050220, group_id = 1020502, type = 1, next_id = 102050221, talk_content = "话说回来。", talk_name = "叙慕",},
	[102050221] = { talk_id = 102050221, group_id = 1020502, type = 1, next_id = 102050222, talk_content = "？", talk_name = "司命",},
	[102050222] = { talk_id = 102050222, group_id = 1020502, type = 1, next_id = 102050223, talk_content = "这个到底是什么？", talk_name = "叙慕",},
	[102050223] = { talk_id = 102050223, group_id = 1020502, type = 1, next_id = 102050224, talk_content = "哦，这个是空洞，名字我也不知道为啥叫这个，但既然碰见了的话就进去看看如何？", talk_name = "司命",},
	[102050224] = { talk_id = 102050224, group_id = 1020502, type = 1, next_id = 102050225, talk_content = "进去也没什么吧，这不就是个树洞吗？", talk_name = "叙慕",},
	[102050225] = { talk_id = 102050225, group_id = 1020502, type = 1, talk_content = "嘿嘿，你先进去就明白了。", talk_name = "司命",},
	[102050301] = { talk_id = 102050301, group_id = 1020503, type = 1, next_id = 102050302, talk_content = "这个地方是……司命，这是哪里？", talk_name = "叙慕",},
	[102050302] = { talk_id = 102050302, group_id = 1020503, type = 1, next_id = 102050303, talk_content = "你就当成是个异空间而已，顺带一提就算是相同的“空洞”，不同的人进入也会看见不同的景色哦。", talk_name = "司命",},
	[102050303] = { talk_id = 102050303, group_id = 1020503, type = 1, next_id = 102050304, talk_content = "也就是说这是只有我进入才能看见的风景吗……或者和我的记忆。", talk_name = "叙慕",},
	[102050304] = { talk_id = 102050304, group_id = 1020503, type = 1, talk_content = "你可以先把思考环节放后一些，有怪物来了。", talk_name = "司命",},
	[102050401] = { talk_id = 102050401, group_id = 1020504, type = 4, next_id = 102050402, aside = "<color=#FFEBA2>叙慕：</color>异空间里面也有噬脉生物吗？", talk_content = "异空间里面也有噬脉生物吗？", talk_name = "叙慕",},
	[102050402] = { talk_id = 102050402, group_id = 1020504, type = 4, next_id = 102050403, aside = "<color=#FFEBA2>司命：</color>这大概是这个空洞的规则吧，青乌，注意规则，不然一辈子都没法打败这东西哦。", talk_content = "这大概是这个空洞的规则吧，青乌，注意规则，不然一辈子都没法打败这东西哦。", talk_name = "司命",},
	[102050403] = { talk_id = 102050403, group_id = 1020504, type = 4, aside = "<color=#FFEBA2>叙慕：</color>早知道这么麻烦我就不进来了！", talk_content = "早知道这么麻烦我就不进来了！", talk_name = "叙慕",},
	[102050501] = { talk_id = 102050501, group_id = 1020505, type = 4, next_id = 102050502, aside = "<color=#FFEBA2>叙慕：</color>应该没敌人了吧。", talk_content = "应该没敌人了吧。", talk_name = "叙慕",},
	[102050502] = { talk_id = 102050502, group_id = 1020505, type = 4, next_id = 102050503, aside = "<color=#FFEBA2>司命：</color>嗯，感觉这地方变回“空洞”了。", talk_content = "嗯，感觉这地方变回“空洞”了。", talk_name = "司命",},
	[102050503] = { talk_id = 102050503, group_id = 1020505, type = 4, next_id = 102050504, aside = "<color=#FFEBA2>司命：</color>青乌，虽然这地方很麻烦，但一般来说都能得到些好东西，之后路过空洞也进来看看吧。", talk_content = "青乌，虽然这地方很麻烦，但一般来说都能得到些好东西，之后路过空洞也进来看看吧。", talk_name = "司命",},
	[102050504] = { talk_id = 102050504, group_id = 1020505, type = 4, aside = "<color=#FFEBA2>叙慕：</color>空洞和洞内景色……", talk_content = "空洞和洞内景色……", talk_name = "叙慕",},
	[102050601] = { talk_id = 102050601, group_id = 1020506, type = 1, next_id = 102050602, talk_content = "你好你好，你们刚刚从空洞回来是吧？", talk_name = "？？？",},
	[102050602] = { talk_id = 102050602, group_id = 1020506, type = 1, next_id = 102050603, talk_content = "你是……还有你是怎么在我耳边说话的？", talk_name = "叙慕",},
	[102050603] = { talk_id = 102050603, group_id = 1020506, type = 1, next_id = 102050604, talk_content = "只是有点小手段而已，对了，你叫我阿一就好。", talk_name = "阿一",},
	[102050604] = { talk_id = 102050604, group_id = 1020506, type = 1, next_id = 102050605, talk_content = "这名字和阿猫阿狗听上去差不多呢。", talk_name = "司命",},
	[102050605] = { talk_id = 102050605, group_id = 1020506, type = 1, next_id = 102050606, talk_content = "哈哈哈，我这名字可是由尊贵的……咳咳，这个先不提了。", talk_name = "阿一",},
	[102050606] = { talk_id = 102050606, group_id = 1020506, type = 1, talk_content = "总之你们先回城里一趟吧，我手上有能帮到你们的东西噢。", talk_name = "阿一",},
	[102050701] = { talk_id = 102050701, group_id = 1020507, type = 1, next_id = 102050702, hide_all_entity = true, hide_player = true, talk_content = "两位好，我就是阿一，真的十分感谢两位能移驾来找我呢。", talk_name = "阿一",},
	[102050702] = { talk_id = 102050702, group_id = 1020507, type = 1, next_id = 102050703, talk_content = "因为你说了能帮到我们，所以你是知道我们的事情吗？", talk_name = "司命",},
	[102050703] = { talk_id = 102050703, group_id = 1020507, type = 1, next_id = 102050704, talk_content = "些许，不过这些其实都不是重点，重要的是你们是能从空洞中平安回归的人物。", talk_name = "阿一",},
	[102050704] = { talk_id = 102050704, group_id = 1020507, type = 1, next_id = 102050705, talk_content = "虽然我知道里面很危险，但能回来很奇怪吗？", talk_name = "叙慕",},
	[102050705] = { talk_id = 102050705, group_id = 1020507, type = 1, next_id = 102050706, talk_content = "我刚刚也说了，重要的是“平安”。", talk_name = "阿一",},
	[102050706] = { talk_id = 102050706, group_id = 1020507, type = 1, next_id = 102050707, talk_content = "闲话就不说太多了，我阿一是一位空洞研究学家，现在正寻找一些能帮忙调查空洞的人。", talk_name = "阿一",},
	[102050707] = { talk_id = 102050707, group_id = 1020507, type = 1, next_id = 102050708, talk_content = "原来是要我们帮忙打工，那报酬到底是什么？", talk_name = "司命",},
	[102050708] = { talk_id = 102050708, group_id = 1020507, type = 1, next_id = 102050709, talk_content = "是力量。", talk_name = "阿一",},
	[102050709] = { talk_id = 102050709, group_id = 1020507, type = 1, next_id = 102050710, talk_content = "啊？", talk_name = "叙慕",},
	[102050710] = { talk_id = 102050710, group_id = 1020507, type = 1, next_id = 102050711, talk_content = "可能说的有些抽象，不过我因为工作关系能得到不少能提升脉者“战斗技能”的珍宝，应该会对你有用的。", talk_name = "阿一",},
	[102050711] = { talk_id = 102050711, group_id = 1020507, type = 1, next_id = 102050712, talk_content = "真有用吗？", talk_name = "叙慕",},
	[102050712] = { talk_id = 102050712, group_id = 1020507, type = 1, talk_content = "你先试试就知道了。", talk_name = "阿一",},
	[102050801] = { talk_id = 102050801, group_id = 1020508, type = 1, next_id = 102050802, talk_content = "等我下次准备好报酬会再联络你们的~", talk_name = "阿一",},
	[102050802] = { talk_id = 102050802, group_id = 1020508, type = 1, talk_content = "只希望你下次别突然在我耳边说话。", talk_name = "叙慕",},
	[102050901] = { talk_id = 102050901, group_id = 1020509, type = 4, next_id = 102050902, aside = "<color=#FFEBA2>司命：</color>青乌，在这里的话或许能使用更多的力量也说不定。", talk_content = "青乌，在这里的话或许能使用更多的力量也说不定。", talk_name = "司命",},
	[102050902] = { talk_id = 102050902, group_id = 1020509, type = 4, next_id = 102050903, aside = "<color=#FFEBA2>叙慕：</color>更多的？", talk_content = "更多的？", talk_name = "叙慕",},
	[102050903] = { talk_id = 102050903, group_id = 1020509, type = 4, aside = "<color=#FFEBA2>司命：</color>嗯，因为这个空间让我们的联系更紧密了。", talk_content = "嗯，因为这个空间让我们的联系更紧密了。", talk_name = "司命",},
	[102051001] = { talk_id = 102051001, group_id = 1020510, type = 4, aside = "<color=#FFEBA2>叙慕：</color>总觉得身体中的能量有些奇怪的涌动……信念，是吗……", talk_content = "总觉得身体中的能量有些奇怪的涌动……信念，是吗……", talk_name = "叙慕",},
	[102060101] = { talk_id = 102060101, group_id = 1020601, type = 4, next_id = 102060102, aside = "<color=#FFEBA2>司命：</color>好，下一步就往据点那边去吧。", talk_content = "好，下一步就往据点那边去吧。", talk_name = "司命",},
	[102060102] = { talk_id = 102060102, group_id = 1020601, type = 4, next_id = 102060103, aside = "<color=#FFEBA2>叙慕：</color>假若悬天真如你口中那样，\n那我现在是不是不太安全？", talk_content = "假若悬天真如你口中那样，\n那我现在是不是不太安全？", talk_name = "叙慕",},
	[102060103] = { talk_id = 102060103, group_id = 1020601, type = 4, next_id = 102060104, aside = "<color=#FFEBA2>司命：</color>关于青乌的事应该也是机密中的机密，那个离歌看着也不像是普通人吧。", talk_content = "关于青乌的事应该也是机密中的机密，那个离歌看着也不像是普通人吧。", talk_name = "司命",},
	[102060104] = { talk_id = 102060104, group_id = 1020601, type = 4, next_id = 102060105, aside = "<color=#FFEBA2>叙慕：</color>……就是他们不打算告知\n下层知道关于我的一切吗？", talk_content = "……就是他们不打算告知\n下层知道关于我的一切吗？", talk_name = "叙慕",},
	[102060105] = { talk_id = 102060105, group_id = 1020601, type = 4, aside = "<color=#FFEBA2>司命：</color>应该是那样。", talk_content = "应该是那样。", talk_name = "司命",},
	[102060201] = { talk_id = 102060201, group_id = 1020602, type = 1, next_id = 102060202, talk_content = "地图上指的就是这里没错吧？", talk_name = "司命",},
	[102060202] = { talk_id = 102060202, group_id = 1020602, type = 1, next_id = 102060203, talk_content = "嗯，这里就是悬天的据点。", talk_name = "叙慕",},
	[102060203] = { talk_id = 102060203, group_id = 1020602, type = 1, next_id = 102060204, talk_content = "可是看不清楚里面到底什么状况。", talk_name = "叙慕",},
	[102060204] = { talk_id = 102060204, group_id = 1020602, type = 1, next_id = 102060205, talk_content = "要不靠近看看？", talk_name = "司命",},
	[102060205] = { talk_id = 102060205, group_id = 1020602, type = 1, talk_content = "等等，两位就是青乌和司命对吧？", talk_name = "？？？",},
	[102060301] = { talk_id = 102060301, group_id = 1020603, type = 1, next_id = 102060302, talk_content = "你是？", talk_name = "叙慕",},
	[102060302] = { talk_id = 102060302, group_id = 1020603, type = 1, next_id = 102060303, talk_content = "我叫来来，是维安司的成员，没想到竟然是真人，\n司长和我提增援的时候我还以为只是开个玩笑呢。", talk_name = "来来",},
	[102060303] = { talk_id = 102060303, group_id = 1020603, type = 1, next_id = 102060304, talk_content = "温罗那家伙，说是让我们考虑，但实际早就猜到我们会帮忙了呢。", talk_name = "司命",},
	[102060304] = { talk_id = 102060304, group_id = 1020603, type = 2, options = {{"我们是来帮忙的。",102060305,},{"是的，我只是来看看。",102060306,},}, talk_content = "怎么听着感觉怪怪的，难不成你们只是来看看的？", talk_name = "来来",},
	[102060305] = { talk_id = 102060305, group_id = 1020603, type = 3, next_id = 102060308, talk_content = "那太好了，悬天的据点规格要大上不少，只靠我一个人可抢不回来呢。", talk_name = "来来",},
	[102060306] = { talk_id = 102060306, group_id = 1020603, type = 3, next_id = 102060307, talk_content = "什，什么……果然增援什么的只是个骗局，我又一次被骗了吗！", talk_name = "来来",},
	[102060307] = { talk_id = 102060307, group_id = 1020603, type = 3, next_id = 102060308, talk_content = "那只是青乌的玩笑而已，我们是来帮忙的。", talk_name = "司命",},
	[102060308] = { talk_id = 102060308, group_id = 1020603, type = 1, next_id = 102060309, talk_content = "所以里面的状况怎么样，你有侦察过吧？", talk_name = "叙慕",},
	[102060309] = { talk_id = 102060309, group_id = 1020603, type = 1, next_id = 102060310, talk_content = "都看过了，普通的怪物若干，理论上不成问题。", talk_name = "来来",},
	[102060310] = { talk_id = 102060310, group_id = 1020603, type = 1, next_id = 102060311, talk_content = "但精英级的有两三只，可能还有些藏在暗处没发现。", talk_name = "来来",},
	[102060311] = { talk_id = 102060311, group_id = 1020603, type = 1, next_id = 102060312, talk_content = "只是这样的话没什么问题吧，听着挺好对付的。", talk_name = "司命",},
	[102060312] = { talk_id = 102060312, group_id = 1020603, type = 1, next_id = 102060313, talk_content = "但难点其实是在据点上面，悬天的据点中装了不少警报器。", talk_name = "来来",},
	[102060313] = { talk_id = 102060313, group_id = 1020603, type = 1, next_id = 102060314, talk_content = "虽然那里面现在被怪物们侵占了，但机械还有在运作，一旦被发现的话那个就会发出巨响，那个声音可能会引来其他怪物也说不定。", talk_name = "来来",},
	[102060314] = { talk_id = 102060314, group_id = 1020603, type = 1, next_id = 102060315, talk_content = "唔，那噬脉生物就不会引发警报吗，\n比起我们那些家伙不才是要警报的东西？", talk_name = "司命",},
	[102060315] = { talk_id = 102060315, group_id = 1020603, type = 1, next_id = 102060316, talk_content = "那个警报似乎平时并不会启用，噬脉生物在占领这个地方之后扰乱了气脉，导致那个警报器被误启动了。", talk_name = "来来",},
	[102060316] = { talk_id = 102060316, group_id = 1020603, type = 1, next_id = 102060317, talk_content = "原本就在里面的不算威胁，是这个说法噢。", talk_name = "司命",},
	[102060317] = { talk_id = 102060317, group_id = 1020603, type = 1, next_id = 102060318, talk_content = "嗯。", talk_name = "来来",},
	[102060318] = { talk_id = 102060318, group_id = 1020603, type = 1, next_id = 102060319, talk_content = "那是挺麻烦的……你有什么解决办法吗？", talk_name = "叙慕",},
	[102060319] = { talk_id = 102060319, group_id = 1020603, type = 1, talk_content = "哼哼，那当然有！", talk_name = "来来",},
	[102060401] = { talk_id = 102060401, group_id = 1020604, type = 4, next_id = 102060402, aside = "<color=#FFEBA2>来来：</color>据点内的噬脉生物似乎都解决干净了。", talk_content = "据点内的噬脉生物似乎都解决干净了。", talk_name = "来来",},
	[102060402] = { talk_id = 102060402, group_id = 1020604, type = 4, next_id = 102060403, aside = "<color=#FFEBA2>司命：</color>那接下来就该干我们的事了，青乌，找个可以用的终端吧，或者能找到些有用的档案。", talk_content = "那接下来就该干我们的事了，青乌，找个可以用的终端吧，或者能找到些有用的档案。", talk_name = "司命",},
	[102060403] = { talk_id = 102060403, group_id = 1020604, type = 4, aside = "<color=#FFEBA2>叙慕：</color>你这鸟到底从那里学来这些的。", talk_content = "你这鸟到底从那里学来这些的。", talk_name = "叙慕",},
	[102060501] = { talk_id = 102060501, group_id = 1020605, type = 2, options = {{"追猎者。",102060502,},{"没有。",102060503,},}, talk_content = "怎么样，有没有什么值得注意的点。", talk_name = "司命",},
	[102060502] = { talk_id = 102060502, group_id = 1020605, type = 3, next_id = 102060504, talk_content = "嗯，你也瞧见那个单词了吧，感觉不是什么好东西呢。", talk_name = "司命",},
	[102060503] = { talk_id = 102060503, group_id = 1020605, type = 3, next_id = 102060504, talk_content = "怎么可能没有，不是有个档案上面写着奇怪的名字的吗，就追猎者那个！", talk_name = "司命",},
	[102060504] = { talk_id = 102060504, group_id = 1020605, type = 1, next_id = 102060505, talk_content = "虽然档案写得不多，但里面有提到让各个据点给追猎者放行的这件事。", talk_name = "司命",},
	[102060505] = { talk_id = 102060505, group_id = 1020605, type = 1, next_id = 102060506, talk_content = "还有谨慎留意最近突然出现的人物，并且上报到系统。", talk_name = "叙慕",},
	[102060506] = { talk_id = 102060506, group_id = 1020605, type = 1, next_id = 102060507, talk_content = "很明显，那个追猎者就是瞄着我们派来的刺客，就像离歌那样呢。", talk_name = "司命",},
	[102060507] = { talk_id = 102060507, group_id = 1020605, type = 2, options = {{"反抗份子指的是？",102060508,},{"我不记得。",102060509,},}, talk_content = "呃，你们是和悬天有什么过节吗，该不会是反抗份子吧？", talk_name = "来来",},
	[102060508] = { talk_id = 102060508, group_id = 1020605, type = 3, next_id = 102060512, talk_content = "不，当我没说过吧。", talk_name = "来来",},
	[102060509] = { talk_id = 102060509, group_id = 1020605, type = 3, next_id = 102060510, talk_content = "也是，司长那边怎么也不可能让那些人帮忙。", talk_name = "来来",},
	[102060510] = { talk_id = 102060510, group_id = 1020605, type = 3, next_id = 102060511, talk_content = "不记得？", talk_name = "来来",},
	[102060511] = { talk_id = 102060511, group_id = 1020605, type = 3, next_id = 102060512, talk_content = "这是我们自己的事情，你还是别问那么多比较好哦。", talk_name = "司命",},
	[102060512] = { talk_id = 102060512, group_id = 1020605, type = 1, next_id = 102060513, talk_content = "……那你们接下来打算怎么办？", talk_name = "来来",},
	[102060513] = { talk_id = 102060513, group_id = 1020605, type = 1, next_id = 102060514, talk_content = "青乌，怎么办。", talk_name = "司命",},
	[102060514] = { talk_id = 102060514, group_id = 1020605, type = 1, next_id = 102060515, talk_content = "主动出击，刚好档案上面有写追猎者暂留的地点。", talk_name = "叙慕",},
	[102060515] = { talk_id = 102060515, group_id = 1020605, type = 1, next_id = 102060516, talk_content = "呵？", talk_name = "司命",},
	[102060516] = { talk_id = 102060516, group_id = 1020605, type = 1, next_id = 102060517, talk_content = "我们的存在并没有被明确指出，就代表追捕我们的行动\n应该只会是离歌那样的少数精英。", talk_name = "叙慕",},
	[102060517] = { talk_id = 102060517, group_id = 1020605, type = 1, next_id = 102060518, talk_content = "那就代表我们每次都只需要应付一个，再说……\n我还没有得到关于自己的情报。", talk_name = "叙慕",},
	[102060518] = { talk_id = 102060518, group_id = 1020605, type = 1, next_id = 102060519, talk_content = "既然悬天因为什么而追捕我，那么我只要慢慢解决他们，\n再把那个什么找出来就是。", talk_name = "叙慕",},
	[102060519] = { talk_id = 102060519, group_id = 1020605, type = 1, next_id = 102060520, talk_content = "这种做法我并不讨厌，准了！", talk_name = "司命",},
	[102060520] = { talk_id = 102060520, group_id = 1020605, type = 1, next_id = 102060521, talk_content = "既然如此……接下来也能让我一起吗？", talk_name = "来来",},
	[102060521] = { talk_id = 102060521, group_id = 1020605, type = 1, next_id = 102060522, talk_content = "为啥，你的工作应该只是夺回这个据点而已吧？", talk_name = "叙慕",},
	[102060522] = { talk_id = 102060522, group_id = 1020605, type = 1, next_id = 102060523, talk_content = "刚刚找到的档案里面也有让我感到在意的事情，拜托了，\n假若要面对强敌的话我应该能帮上什么才是。", talk_name = "来来",},
	[102060523] = { talk_id = 102060523, group_id = 1020605, type = 1, next_id = 102060524, talk_content = "时不待人，再聊下去追猎者可能就不在那里了，\n这人我看着也没什么城府，就带着她吧。", talk_name = "司命",},
	[102060524] = { talk_id = 102060524, group_id = 1020605, type = 1, next_id = 102060525, talk_content = "必要时还能当个肉盾呢。", talk_name = "司命",},
	[102060525] = { talk_id = 102060525, group_id = 1020605, type = 1, talk_content = "主张拯救世界的家伙说要把人当肉盾……这也太猎奇了。", talk_name = "叙慕",},
	[102070101] = { talk_id = 102070101, group_id = 1020701, type = 5, next_id = 102070102, aside = "<color=#FFEBA2>来来：</color>……这里是平时不会派人巡逻的区域呢。", talk_content = "……这里是平时不会派人巡逻的区域呢。", talk_name = "来来",},
	[102070102] = { talk_id = 102070102, group_id = 1020701, type = 5, next_id = 102070103, aside = "<color=#FFEBA2>司命：</color>就因为这样追猎者才会在这里待机吧。", talk_content = "就因为这样追猎者才会在这里待机吧。", talk_name = "司命",},
	[102070103] = { talk_id = 102070103, group_id = 1020701, type = 5, next_id = 102070104, aside = "<color=#FFEBA2>叙慕：</color>可关键的追猎者在哪？", talk_content = "可关键的追猎者在哪？", talk_name = "叙慕",},
	[102070104] = { talk_id = 102070104, group_id = 1020701, type = 5, next_id = 102070105, aside = "<color=#FFEBA2>来来：</color>说是待机，但也没看见附近有什么生活痕迹……", talk_content = "说是待机，但也没看见附近有什么生活痕迹……", talk_name = "来来",},
	[102070105] = { talk_id = 102070105, group_id = 1020701, type = 5, next_id = 102070106, aside = "<color=#FFEBA2>叙慕：</color>什么！？", talk_content = "什么！？", talk_name = "叙慕",},
	[102070106] = { talk_id = 102070106, group_id = 1020701, type = 5, aside = "<color=#FFEBA2>神荼：</color>猎物……开始战斗。", talk_content = "猎物……开始战斗。", talk_name = "神荼",},
	[102070201] = { talk_id = 102070201, group_id = 1020702, type = 1, next_id = 102070202, talk_content = "咳……", talk_name = "神荼",},
	[102070202] = { talk_id = 102070202, group_id = 1020702, type = 1, next_id = 102070203, talk_content = "你是噬脉生物吧，为什么会给悬天做事？", talk_name = "来来",},
	[102070203] = { talk_id = 102070203, group_id = 1020702, type = 1, next_id = 102070204, talk_content = "我等……追随太阳。", talk_name = "神荼",},
	[102070204] = { talk_id = 102070204, group_id = 1020702, type = 1, next_id = 102070205, talk_content = "……", talk_name = "来来",},
	[102070205] = { talk_id = 102070205, group_id = 1020702, type = 1, next_id = 102070206, talk_content = "这里也提到太阳了呢，噬脉生物，悬天，然后你。", talk_name = "叙慕",},
	[102070206] = { talk_id = 102070206, group_id = 1020702, type = 1, next_id = 102070207, talk_content = "感觉每个人都在绕着那太阳转。", talk_name = "叙慕",},
	[102070207] = { talk_id = 102070207, group_id = 1020702, type = 1, next_id = 102070208, talk_content = "青乌，这里有个东西哎。", talk_name = "司命",},
	[102070208] = { talk_id = 102070208, group_id = 1020702, type = 1, next_id = 102070209, talk_content = "……是从那个怪物身上掉下来的吗？", talk_name = "叙慕",},
	[102070209] = { talk_id = 102070209, group_id = 1020702, type = 2, options = {{"（捡起来。）",102070210,},}, talk_content = "先捡起来吧，总感觉有些什么特别的呢。", talk_name = "司命",},
	[102070210] = { talk_id = 102070210, group_id = 1020702, type = 3, next_id = 102070211, talk_content = "望月。", talk_name = "叙慕",},
	[102070211] = { talk_id = 102070211, group_id = 1020702, type = 3, next_id = 102070212, talk_content = "——。", talk_name = "某个声音",},
	[102070212] = { talk_id = 102070212, group_id = 1020702, type = 3, next_id = 102070213, talk_content = "毁灭这一切吧。", talk_name = "叙慕",},
	[102070213] = { talk_id = 102070213, group_id = 1020702, type = 3, next_id = 102070214, talk_content = "青乌！", talk_name = "司命",},
	[102070214] = { talk_id = 102070214, group_id = 1020702, type = 3, next_id = 102070215, talk_content = "！", talk_name = "叙慕",},
	[102070215] = { talk_id = 102070215, group_id = 1020702, type = 3, next_id = 102070216, talk_content = "又发呆了，你就不能换个地方……", talk_name = "司命",},
	[102070216] = { talk_id = 102070216, group_id = 1020702, type = 3, next_id = 102070217, talk_content = "我，不……喂，地上的这个到底是什么东西？", talk_name = "叙慕",},
	[102070217] = { talk_id = 102070217, group_id = 1020702, type = 3, next_id = 102070218, talk_content = "呃，你突然怎么了？", talk_name = "司命",},
	[102070218] = { talk_id = 102070218, group_id = 1020702, type = 3, next_id = 102070219, talk_content = "我记起了一些事情……快说，这个到底是什么！", talk_name = "叙慕",},
	[102070219] = { talk_id = 102070219, group_id = 1020702, type = 3, next_id = 102070220, talk_content = "……太阳的碎片。", talk_name = "来来",},
	[102070220] = { talk_id = 102070220, group_id = 1020702, type = 3, next_id = 102070221, talk_content = "以前在城里的博物馆见过，那上面的花纹和这个一模一样。", talk_name = "来来",},
	[102070221] = { talk_id = 102070221, group_id = 1020702, type = 3, next_id = 102070222, talk_content = "噢……看来你也要绕着太阳转了呢。", talk_name = "司命",},
	[102070222] = { talk_id = 102070222, group_id = 1020702, type = 3, next_id = 102070223, talk_content = "来来，那个还放在博物馆吗？", talk_name = "叙慕",},
	[102070223] = { talk_id = 102070223, group_id = 1020702, type = 3, talk_content = "……或许。", talk_name = "来来",},
	[102080101] = { talk_id = 102080101, group_id = 1020801, type = 4, next_id = 102080102, aside = "<color=#FFEBA2>来来：</color>接下来先回熙来，向司长报告吧。", talk_content = "接下来先回熙来，向司长报告吧。", talk_name = "来来",},
	[102080102] = { talk_id = 102080102, group_id = 1020801, type = 4, next_id = 102080103, aside = "<color=#FFEBA2>司命：</color>虽然我也知道悬天是不会放弃追击，但没想到人都已经准备好了。", talk_content = "虽然我也知道悬天是不会放弃追击，但没想到人都已经准备好了。", talk_name = "司命",},
	[102080103] = { talk_id = 102080103, group_id = 1020801, type = 4, next_id = 102080104, aside = "<color=#FFEBA2>司命：</color>说起来你刚刚是记起了些什么。", talk_content = "说起来你刚刚是记起了些什么。", talk_name = "司命",},
	[102080104] = { talk_id = 102080104, group_id = 1020801, type = 4, next_id = 102080105, aside = "<color=#FFEBA2>叙慕：</color>……我需要整理一下再说出来。", talk_content = "……我需要整理一下再说出来。", talk_name = "叙慕",},
	[102080105] = { talk_id = 102080105, group_id = 1020801, type = 4, next_id = 102080106, aside = "<color=#FFEBA2>司命：</color>呵，还会跟我故弄玄虚了？", talk_content = "呵，还会跟我故弄玄虚了？", talk_name = "司命",},
	[102080106] = { talk_id = 102080106, group_id = 1020801, type = 4, next_id = 102080107, aside = "<color=#FFEBA2>来来：</color>呃，所以记起这个是什么意思？", talk_content = "呃，所以记起这个是什么意思？", talk_name = "来来",},
	[102080107] = { talk_id = 102080107, group_id = 1020801, type = 4, aside = "<color=#FFEBA2>司命：</color>只是这家伙有点健忘而已。", talk_content = "只是这家伙有点健忘而已。", talk_name = "司命",},
	[102080201] = { talk_id = 102080201, group_id = 1020802, type = 4, aside = "<color=#FFEBA2>来来：</color>司长应该还在办公室内，我们直接进去吧。", talk_content = "司长应该还在办公室内，我们直接进去吧。", talk_name = "来来",},
	[102080301] = { talk_id = 102080301, group_id = 1020803, type = 1, next_id = 102080302, talk_content = "这不是来来和义人吗，我收到斥候的报告了，你们收回据点了对吧？", talk_name = "温罗",},
	[102080302] = { talk_id = 102080302, group_id = 1020803, type = 1, next_id = 102080303, talk_content = "是的，多亏有青乌的协助才能如此顺利，这下据点能重新投入生产了，但……", talk_name = "来来",},
	[102080303] = { talk_id = 102080303, group_id = 1020803, type = 1, next_id = 102080304, talk_content = "但？", talk_name = "温罗",},
	[102080304] = { talk_id = 102080304, group_id = 1020803, type = 1, next_id = 102080305, talk_content = "司长，除了收复据点以外，我们还遇见了一只强大的噬脉生物，而那只噬脉生物似乎与悬天有什么关系。", talk_name = "来来",},
	[102080305] = { talk_id = 102080305, group_id = 1020803, type = 1, next_id = 102080306, talk_content = "噬脉生物与悬天有关联……你为什么会有这个结论？", talk_name = "温罗",},
	[102080306] = { talk_id = 102080306, group_id = 1020803, type = 1, next_id = 102080307, talk_content = "因为我从被打败的噬脉生物身上得到了太阳的碎片。", talk_name = "来来",},
	[102080307] = { talk_id = 102080307, group_id = 1020803, type = 1, next_id = 102080308, talk_content = "太阳的碎片，就是之前也在局里展出过的东西吧……", talk_name = "温罗",},
	[102080308] = { talk_id = 102080308, group_id = 1020803, type = 1, next_id = 102080309, talk_content = "是的，而且在战斗的途中那个噬脉生物似乎还表达出了对太阳的热枕。", talk_name = "来来",},
	[102080309] = { talk_id = 102080309, group_id = 1020803, type = 1, next_id = 102080310, talk_content = "恍若那个太阳就是他们的神明一样，之前的噬脉生物们也会这样吗？", talk_name = "叙慕",},
	[102080310] = { talk_id = 102080310, group_id = 1020803, type = 1, next_id = 102080311, talk_content = "不，我可没遇过这样的状况，说到底，太阳的碎片一直都是悬天用来作展出的宣传品。", talk_name = "温罗",},
	[102080311] = { talk_id = 102080311, group_id = 1020803, type = 1, next_id = 102080312, talk_content = "一般是不应该落到噬脉生物手上才对……", talk_name = "温罗",},
	[102080312] = { talk_id = 102080312, group_id = 1020803, type = 1, next_id = 102080313, talk_content = "所以说他们是一伙的话就说得通了吧，悬天因为什么原因把碎片交给了噬脉生物。", talk_name = "司命",},
	[102080313] = { talk_id = 102080313, group_id = 1020803, type = 1, next_id = 102080314, talk_content = "……不，请义人们停止这种猜测，我们不能往那个方向去思考。", talk_name = "温罗",},
	[102080314] = { talk_id = 102080314, group_id = 1020803, type = 1, next_id = 102080315, talk_content = "司长，你是不相信我们吗，虽然这个只是间接证据，但现在区域内的噬脉生物暴动已经……", talk_name = "来来",},
	[102080315] = { talk_id = 102080315, group_id = 1020803, type = 1, next_id = 102080316, talk_content = "悬天是当今的能源龙头，他们就如同社会的第二位管理者。", talk_name = "温罗",},
	[102080316] = { talk_id = 102080316, group_id = 1020803, type = 1, next_id = 102080317, talk_content = "现在就先当作什么都没发生吧，来来。", talk_name = "温罗",},
	[102080317] = { talk_id = 102080317, group_id = 1020803, type = 1, next_id = 102080318, talk_content = "……", talk_name = "来来",},
	[102080318] = { talk_id = 102080318, group_id = 1020803, type = 2, options = {{"悬天的权力有这么大吗？",102080319,},{"这样真的好吗？",102080319,},}, talk_content = "噢，不愧是当了官的，你这家伙还挺机灵嘛？", talk_name = "司命",},
	[102080319] = { talk_id = 102080319, group_id = 1020803, type = 3, next_id = 102080320, talk_content = "就如我说的，悬天掌控着城市中的能源命脉，我并不是不相信你们。", talk_name = "温罗",},
	[102080320] = { talk_id = 102080320, group_id = 1020803, type = 3, next_id = 102080321, talk_content = "只是仅凭一块无法证明来历的太阳的碎片，恐怕不足以撼动悬天这颗大树。", talk_name = "温罗",},
	[102080321] = { talk_id = 102080321, group_id = 1020803, type = 3, next_id = 102080322, talk_content = "毕竟那个神荼在被打败以后就自行消灭变成粒子了呢。", talk_name = "司命",},
	[102080322] = { talk_id = 102080322, group_id = 1020803, type = 3, next_id = 102080323, talk_content = "总之这件事先放一边，义人，十分感谢你能协助我们收回据点。", talk_name = "温罗",},
	[102080323] = { talk_id = 102080323, group_id = 1020803, type = 3, next_id = 102080324, talk_content = "作为报酬我约好了城里的房间和饭店，你之后……", talk_name = "温罗",},
	[102080324] = { talk_id = 102080324, group_id = 1020803, type = 3, next_id = 102080325, talk_content = "相比起这些我想要看看之前展出过的太阳的碎片。", talk_name = "叙慕",},
	[102080325] = { talk_id = 102080325, group_id = 1020803, type = 3, next_id = 102080326, talk_content = "呃，义人为何想要看那个，再说你们手上现在不是有一块了吗？", talk_name = "温罗",},
	[102080326] = { talk_id = 102080326, group_id = 1020803, type = 3, next_id = 102080327, talk_content = "我有些私事需要用到，虽然是刚刚才知道，所以说能让我看看吗？", talk_name = "叙慕",},
	[102080327] = { talk_id = 102080327, group_id = 1020803, type = 3, next_id = 102080328, talk_content = "很可惜，假若是一周前的话我肯定会帮义人这个忙，但现在碎片已经不在熙来城里面了。", talk_name = "温罗",},
	[102080328] = { talk_id = 102080328, group_id = 1020803, type = 3, next_id = 102080329, talk_content = "该不会说被某某人带走了吧？", talk_name = "司命",},
	[102080329] = { talk_id = 102080329, group_id = 1020803, type = 3, next_id = 102080330, talk_content = "一周前，该不会是创生主义之前的行动……", talk_name = "来来",},
	[102080330] = { talk_id = 102080330, group_id = 1020803, type = 3, next_id = 102080331, talk_content = "嗯，悬天因此提前结束了展出，把碎片带回到他们管理的仓库了。", talk_name = "温罗",},
	[102080331] = { talk_id = 102080331, group_id = 1020803, type = 3, next_id = 102080332, talk_content = "那仓库在哪里？", talk_name = "叙慕",},
	[102080332] = { talk_id = 102080332, group_id = 1020803, type = 3, next_id = 102080333, talk_content = "就在城的西北方，在某个地下隧道之中，不知道哪里有没有被噬脉生物占领呢……", talk_name = "温罗",},
	[102080333] = { talk_id = 102080333, group_id = 1020803, type = 3, next_id = 102080334, talk_content = "无论有没有被占领，对我来说下一步也只能往哪里去了。", talk_name = "叙慕",},
	[102080334] = { talk_id = 102080334, group_id = 1020803, type = 3, next_id = 102080335, talk_content = "虽然和拯救世界没什么关系就是了。", talk_name = "司命",},
	[102080335] = { talk_id = 102080335, group_id = 1020803, type = 2, options = {{"嗯，我有想要确认的事。",102080336,},{"这次你也要跟着来吗？",102080337,},}, talk_content = "等等，你们要走了吗？", talk_name = "来来",},
	[102080336] = { talk_id = 102080336, group_id = 1020803, type = 3, next_id = 102080338, talk_content = "是这样呐……我还以为能再和你们聊聊呢，比如悬天的事。", talk_name = "来来",},
	[102080337] = { talk_id = 102080337, group_id = 1020803, type = 3, next_id = 102080338, talk_content = "对不起，我还有维安警的任务，不能擅离职守。", talk_name = "来来",},
	[102080338] = { talk_id = 102080338, group_id = 1020803, type = 1, next_id = 102080339, talk_content = "既然义人如此匆忙的话那我不碍着义人，我让人准备一些报酬，请你有闲的话就去一楼取吧。", talk_name = "温罗",},
	[102080339] = { talk_id = 102080339, group_id = 1020803, type = 1, next_id = 102080340, talk_content = "嗯，谢谢。", talk_name = "叙慕",},
	[102080340] = { talk_id = 102080340, group_id = 1020803, type = 1, talk_content = "那下次再见了，十分感谢你的协助，青乌。", talk_name = "来来",},
	[102090101] = { talk_id = 102090101, group_id = 1020901, type = 4, next_id = 102090102, aside = "<color=#FFEBA2>司命：</color>轻轻松松就有下一步的线索了呢。", talk_content = "轻轻松松就有下一步的线索了呢。", talk_name = "司命",},
	[102090102] = { talk_id = 102090102, group_id = 1020901, type = 4, next_id = 102090103, aside = "<color=#FFEBA2>叙慕：</color>真正轻松是我立刻就能得到那个太阳的碎片。", talk_content = "真正轻松是我立刻就能得到那个太阳的碎片。", talk_name = "叙慕",},
	[102090103] = { talk_id = 102090103, group_id = 1020901, type = 4, next_id = 102090104, aside = "<color=#FFEBA2>司命：</color>总之先去一楼把温罗说的报酬拿到手吧。", talk_content = "总之先去一楼把温罗说的报酬拿到手吧。", talk_name = "司命",},
	[102090104] = { talk_id = 102090104, group_id = 1020901, type = 4, aside = "<color=#FFEBA2>司命：</color>之后再去找你的记忆，再然后就是拯救世界。", talk_content = "之后再去找你的记忆，再然后就是拯救世界。", talk_name = "司命",},
	[102090201] = { talk_id = 102090201, group_id = 1020902, type = 1, next_id = 102090202, talk_content = "你好，想必你就是司长提到过的青乌了，我是维安司的接待员。", talk_name = "接待员",},
	[102090202] = { talk_id = 102090202, group_id = 1020902, type = 1, next_id = 102090203, talk_content = "接待呐，总之听说过我们的事情就好办了，温罗说你这边有给我们准备的报酬对吧。", talk_name = "司命",},
	[102090203] = { talk_id = 102090203, group_id = 1020902, type = 1, next_id = 102090204, talk_content = "是的，不过在交付之前能请你们听听这边的请求吗？", talk_name = "接待员",},
	[102090204] = { talk_id = 102090204, group_id = 1020902, type = 1, next_id = 102090205, talk_content = "……该不会又想让我帮点什么吧？", talk_name = "叙慕",},
	[102090205] = { talk_id = 102090205, group_id = 1020902, type = 1, next_id = 102090206, talk_content = "只是想向你说明一下我们运营方针而已。", talk_name = "接待员",},
	[102090206] = { talk_id = 102090206, group_id = 1020902, type = 1, next_id = 102090207, talk_content = "就如你所知的那样，世上大部分人都不是脉者，而世界上却有很多问题需要脉者才能解决。", talk_name = "接待员",},
	[102090207] = { talk_id = 102090207, group_id = 1020902, type = 1, next_id = 102090208, talk_content = "好，我知道了，你是想让我们帮忙解决那些奇怪的问题是吧？", talk_name = "司命",},
	[102090208] = { talk_id = 102090208, group_id = 1020902, type = 1, next_id = 102090209, talk_content = "当然是有偿的委托，假若两位之后有空或者需要赚外快的话，请务必到这里来看看。", talk_name = "接待员",},
	[102090209] = { talk_id = 102090209, group_id = 1020902, type = 1, next_id = 102090210, talk_content = "唔，听着是不错，青乌，之后有空就来这里接几个任务吧。", talk_name = "司命",},
	[102090210] = { talk_id = 102090210, group_id = 1020902, type = 1, next_id = 102090211, talk_content = "你不是只对拯救世界有兴趣吗？", talk_name = "叙慕",},
	[102090211] = { talk_id = 102090211, group_id = 1020902, type = 1, talk_content = "筹集拯救世界用的资金也是很重要的。", talk_name = "司命",},
	[102090301] = { talk_id = 102090301, group_id = 1020903, type = 4, next_id = 102090302, aside = "<color=#FFEBA2>司命：</color>记得温罗说的是城的西北方，在隧道里面的来着。", talk_content = "记得温罗说的是城的西北方，在隧道里面的来着。", talk_name = "司命",},
	[102090302] = { talk_id = 102090302, group_id = 1020903, type = 4, aside = "<color=#FFEBA2>司命：</color>到处看看有没有能钻下去的地方吧。", talk_content = "到处看看有没有能钻下去的地方吧。", talk_name = "司命",},
	[102090401] = { talk_id = 102090401, group_id = 1020904, type = 4, aside = "<color=#FFEBA2>司命：</color>青乌，那个是隧道的入口没错吧？", talk_content = "青乌，那个是隧道的入口没错吧？", talk_name = "司命",},
	[102100101] = { talk_id = 102100101, group_id = 1021001, type = 1, next_id = 102100102, talk_content = "这里大概就是温罗说的仓库了吧。", talk_name = "司命",},
	[102100102] = { talk_id = 102100102, group_id = 1021001, type = 1, next_id = 102100103, talk_content = "看标记应该没错，但门从里面被反锁了。", talk_name = "叙慕",},
	[102100103] = { talk_id = 102100103, group_id = 1021001, type = 1, next_id = 102100104, talk_content = "这时候用佩从不就好了嘛。", talk_name = "司命",},
	[102100104] = { talk_id = 102100104, group_id = 1021001, type = 1, next_id = 102100105, talk_content = "就路上那些小木人，我记得他们就能干这事的来着。", talk_name = "司命",},
	[102100105] = { talk_id = 102100105, group_id = 1021001, type = 1, next_id = 102100106, talk_content = "你这一路上有没有搞到那个的佩从？", talk_name = "司命",},
	[102100106] = { talk_id = 102100106, group_id = 1021001, type = 1, next_id = 102100107, talk_content = "一时半会我也不记得了。", talk_name = "叙慕",},
	[102100107] = { talk_id = 102100107, group_id = 1021001, type = 1, talk_content = "那你先找找看吧，没有的话我们到附近搞一个便是，毕竟那些小木人跟蚂蚁一样满地都是捏。", talk_name = "司命",},
	[102100201] = { talk_id = 102100201, group_id = 1021002, type = 4, next_id = 102100202, aside = "<color=#FFEBA2>叙慕：</color>回头一想，我似乎是没有那个的佩从。", talk_content = "回头一想，我似乎是没有那个的佩从。", talk_name = "叙慕",},
	[102100202] = { talk_id = 102100202, group_id = 1021002, type = 4, aside = "<color=#FFEBA2>司命：</color>那我们在路边随便搞一只吧。", talk_content = "那我们在路边随便搞一只吧。", talk_name = "司命",},
	[102100301] = { talk_id = 102100301, group_id = 1021003, type = 1, next_id = 102100302, talk_content = "司命，是这个没错吧。", talk_name = "叙慕",},
	[102100302] = { talk_id = 102100302, group_id = 1021003, type = 1, next_id = 102100303, talk_content = "是它没错了，倒是你之后得在路上多多收集一些佩魔。", talk_name = "司命",},
	[102100303] = { talk_id = 102100303, group_id = 1021003, type = 1, next_id = 102100304, talk_content = "等到要用的时候才会去搞效率太低了。", talk_name = "司命",},
	[102100304] = { talk_id = 102100304, group_id = 1021003, type = 1, talk_content = "我会记下来的。", talk_name = "叙慕",},
	[102100401] = { talk_id = 102100401, group_id = 1021004, type = 1, next_id = 102100402, talk_content = "轻轻松松。", talk_name = "司命",},
	[102100402] = { talk_id = 102100402, group_id = 1021004, type = 1, next_id = 102100403, talk_content = "实际操作的人是我吧？", talk_name = "叙慕",},
	[102100403] = { talk_id = 102100403, group_id = 1021004, type = 1, next_id = 102100404, talk_content = "可提醒你的人是我，倒是快点进去吧，要是宝物被人拿走就不好了。", talk_name = "司命",},
	[102100404] = { talk_id = 102100404, group_id = 1021004, type = 1, next_id = 102100405, talk_content = "除了我以外大概也没人特地来这里找太阳的碎片了吧。", talk_name = "叙慕",},
	[102100405] = { talk_id = 102100405, group_id = 1021004, type = 1, next_id = 102100406, talk_content = "这可不好说，毕竟太阳的碎片这个名号听着就觉得挺容易被强盗盯上的。", talk_name = "司命",},
	[102100406] = { talk_id = 102100406, group_id = 1021004, type = 1, next_id = 102100407, talk_content = "等等，擅自开锁还要拿走别人仓库的东西，我们现在干的和强盗差不……", talk_name = "司命",},
	[102100407] = { talk_id = 102100407, group_id = 1021004, type = 1, next_id = 102100408, talk_content = "闭嘴，虽然性质很像，但这里是悬天的仓库，换言之是恶党的仓库。", talk_name = "叙慕",},
	[102100408] = { talk_id = 102100408, group_id = 1021004, type = 1, next_id = 102100409, talk_content = "我只是来打击罪恶而已。", talk_name = "叙慕",},
	[102100409] = { talk_id = 102100409, group_id = 1021004, type = 1, next_id = 102100410, talk_content = "……", talk_name = "司命",},
	[102100410] = { talk_id = 102100410, group_id = 1021004, type = 1, next_id = 102100411, talk_content = "……别这样看着我，赶紧进去吧。", talk_name = "叙慕",},
	[102100411] = { talk_id = 102100411, group_id = 1021004, type = 1, talk_content = "嘿嘿，急了。", talk_name = "司命",},
	[102110101] = { talk_id = 102110101, group_id = 1021101, type = 1, next_id = 102110102, talk_content = "喂，青乌，刚才走过的是悬天的士兵。", talk_name = "司命",},
	[102110102] = { talk_id = 102110102, group_id = 1021101, type = 1, next_id = 102110103, talk_content = "看不太清楚，你确定吗？", talk_name = "叙慕",},
	[102110103] = { talk_id = 102110103, group_id = 1021101, type = 1, next_id = 102110104, talk_content = "当然，我这双火眼金睛可不会看错。", talk_name = "司命",},
	[102110104] = { talk_id = 102110104, group_id = 1021101, type = 1, next_id = 102110105, talk_content = "是不是金睛另说，但我可没想到这里竟然还在正常运作，其他地方明明都是噬脉生物。", talk_name = "叙慕",},
	[102110105] = { talk_id = 102110105, group_id = 1021101, type = 1, next_id = 102110106, talk_content = "毕竟噬脉生物也不会特意打破锁钻到这种地方来。", talk_name = "司命",},
	[102110106] = { talk_id = 102110106, group_id = 1021101, type = 1, next_id = 102110107, talk_content = "也就是说接下来的对手是那些家伙。", talk_name = "叙慕",},
	[102110107] = { talk_id = 102110107, group_id = 1021101, type = 1, next_id = 102110108, talk_content = "应该也有不战斗的做法，总之一边应付悬天，一边找运到这里来的碎片吧。", talk_name = "司命",},
	[102110108] = { talk_id = 102110108, group_id = 1021101, type = 1, talk_content = "就这么办。", talk_name = "叙慕",},
	[102110201] = { talk_id = 102110201, group_id = 1021102, type = 4, next_id = 102110202, aside = "<color=#FFEBA2>司命：</color>青乌，这么大的仓库应该不只是用存放碎片的才对。", talk_content = "青乌，这么大的仓库应该不只是用存放碎片的才对。", talk_name = "司命",},
	[102110202] = { talk_id = 102110202, group_id = 1021102, type = 4, next_id = 102110203, aside = "<color=#FFEBA2>叙慕：</color>的确，你想到什么了吗？", talk_content = "的确，你想到什么了吗？", talk_name = "叙慕",},
	[102110203] = { talk_id = 102110203, group_id = 1021102, type = 4, next_id = 102110204, aside = "<color=#FFEBA2>司命：</color>我的意思是让你可以顺路拿点好东西。", talk_content = "我的意思是让你可以顺路拿点好东西。", talk_name = "司命",},
	[102110204] = { talk_id = 102110204, group_id = 1021102, type = 4, next_id = 102110205, aside = "<color=#FFEBA2>叙慕：</color>那不是真成那个了？", talk_content = "那不是真成那个了？", talk_name = "叙慕",},
	[102110205] = { talk_id = 102110205, group_id = 1021102, type = 4, next_id = 102110206, aside = "<color=#FFEBA2>司命：</color>你都到这里来了，说啥呢这天真孩子。", talk_content = "你都到这里来了，说啥呢这天真孩子。", talk_name = "司命",},
	[102110206] = { talk_id = 102110206, group_id = 1021102, type = 4, aside = "<color=#FFEBA2>叙慕：</color>……", talk_content = "……", talk_name = "叙慕",},
	[102110301] = { talk_id = 102110301, group_id = 1021103, type = 1, next_id = 102110302, talk_content = "离歌……", talk_name = "叙慕",},
	[102110302] = { talk_id = 102110302, group_id = 1021103, type = 1, next_id = 102110303, talk_content = "怎么突然说起那个人的名字？", talk_name = "司命",},
	[102110303] = { talk_id = 102110303, group_id = 1021103, type = 1, next_id = 102110304, talk_content = "毕竟现在身处悬天的地盘，那个人现在会在哪里呢。", talk_name = "叙慕",},
	[102110304] = { talk_id = 102110304, group_id = 1021103, type = 1, next_id = 102110305, talk_content = "你该不会是打算跟她走，与她为伍吧？", talk_name = "司命",},
	[102110305] = { talk_id = 102110305, group_id = 1021103, type = 1, next_id = 102110306, talk_content = "我说过了，她和我记忆中的人很像。", talk_name = "叙慕",},
	[102110306] = { talk_id = 102110306, group_id = 1021103, type = 1, next_id = 102110307, talk_content = "但她不记得你。", talk_name = "司命",},
	[102110307] = { talk_id = 102110307, group_id = 1021103, type = 1, next_id = 102110308, talk_content = "我也隐约觉得她并不是“她”。", talk_name = "叙慕",},
	[102110308] = { talk_id = 102110308, group_id = 1021103, type = 1, next_id = 102110309, talk_content = "假如能再见的话，我一定要好好确认是怎么回事。", talk_name = "叙慕",},
	[102110309] = { talk_id = 102110309, group_id = 1021103, type = 1, talk_content = "不，我可不想再和那人见面了。", talk_name = "司命",},
	[102110401] = { talk_id = 102110401, group_id = 1021104, type = 5, next_id = 102110402, aside = "<color=#FFEBA2>司命：</color>这里应该就是仓库的尽头了吧，太阳的碎片……", talk_content = "这里应该就是仓库的尽头了吧，太阳的碎片……", talk_name = "司命",},
	[102110402] = { talk_id = 102110402, group_id = 1021104, type = 5, next_id = 102110403, aside = "<color=#FFEBA2>司命：</color>等等，那边好像有个人？", talk_content = "等等，那边好像有个人？", talk_name = "司命",},
	[102110403] = { talk_id = 102110403, group_id = 1021104, type = 5, next_id = 102110404, aside = "<color=#FFEBA2>？？？：</color>本以为都已经解决干净了，没想到竟然还有增援。", talk_content = "本以为都已经解决干净了，没想到竟然还有增援。", talk_name = "？？？",},
	[102110404] = { talk_id = 102110404, group_id = 1021104, type = 5, next_id = 102110405, aside = "<color=#FFEBA2>叙慕：</color>你是……？", talk_content = "你是……？", talk_name = "叙慕",},
	[102110405] = { talk_id = 102110405, group_id = 1021104, type = 5, next_id = 102110406, aside = "<color=#FFEBA2>？？？：</color>老大说过可不能随便把名字告诉别人，反正你也不需要知道。", talk_content = "老大说过可不能随便把名字告诉别人，反正你也不需要知道。", talk_name = "？？？",},
	[102110406] = { talk_id = 102110406, group_id = 1021104, type = 5, aside = "<color=#FFEBA2>？？？：</color>我马上就送你归西。", talk_content = "我马上就送你归西。", talk_name = "？？？",},
	[102110501] = { talk_id = 102110501, group_id = 1021105, type = 4, next_id = 102110502, aside = "<color=#FFEBA2>司命：</color>这家伙……也是悬天的吗！", talk_content = "这家伙……也是悬天的吗！", talk_name = "司命",},
	[102110502] = { talk_id = 102110502, group_id = 1021105, type = 4, next_id = 102110503, aside = "<color=#FFEBA2>叙慕：</color>总觉得不是那回事，喂，你……", talk_content = "总觉得不是那回事，喂，你……", talk_name = "叙慕",},
	[102110503] = { talk_id = 102110503, group_id = 1021105, type = 4, aside = "<color=#FFEBA2>？？？：</color>别东张西望！", talk_content = "别东张西望！", talk_name = "？？？",},
	[102110601] = { talk_id = 102110601, group_id = 1021106, type = 4, next_id = 102110602, aside = "<color=#FFEBA2>？？？：</color>挺能打得嘛，不过……还不如我！", talk_content = "挺能打得嘛，不过……还不如我！", talk_name = "？？？",},
	[102110602] = { talk_id = 102110602, group_id = 1021106, type = 4, next_id = 102110603, aside = "<color=#FFEBA2>司命：</color>那家伙要动真格了。", talk_content = "那家伙要动真格了。", talk_name = "司命",},
	[102110603] = { talk_id = 102110603, group_id = 1021106, type = 4, aside = "<color=#FFEBA2>叙慕：</color>真希望这家伙能静下来听我说几句！", talk_content = "真希望这家伙能静下来听我说几句！", talk_name = "叙慕",},
	[102110701] = { talk_id = 102110701, group_id = 1021107, type = 1, next_id = 102110702, talk_content = "等等，我们别打了好不好！", talk_name = "？？？",},
	[102110702] = { talk_id = 102110702, group_id = 1021107, type = 1, next_id = 102110703, talk_content = "我从头到尾都没说我们要打吧。", talk_name = "叙慕",},
	[102110703] = { talk_id = 102110703, group_id = 1021107, type = 1, next_id = 102110704, talk_content = "哎，是这样吗？", talk_name = "？？？",},
	[102110704] = { talk_id = 102110704, group_id = 1021107, type = 1, next_id = 102110705, talk_content = "的确，是你这死猴子自说自话打过来了。", talk_name = "司命",},
	[102110705] = { talk_id = 102110705, group_id = 1021107, type = 1, next_id = 102110706, talk_content = "哈哈……是这样吗，对了，我的名字叫义向。", talk_name = "义向",},
	[102110706] = { talk_id = 102110706, group_id = 1021107, type = 1, next_id = 102110707, talk_content = "1.不是说不随便说名字的吗？", talk_name = "叙慕",},
	[102110707] = { talk_id = 102110707, group_id = 1021107, type = 1, next_id = 102110708, talk_content = "只是不能和为恶者说名字而已，从刚才的“交心”之中我已经明白你是个值得信赖的家伙了。", talk_name = "义向",},
	[102110708] = { talk_id = 102110708, group_id = 1021107, type = 1, next_id = 102110709, talk_content = "这家伙变脸变得还真快。", talk_name = "司命",},
	[102110709] = { talk_id = 102110709, group_id = 1021107, type = 1, next_id = 102110710, talk_content = "毕竟有好几次青乌都没有下死手，就算笨蛋如我大概也明白你们不是什么坏人了。", talk_name = "义向",},
	[102110710] = { talk_id = 102110710, group_id = 1021107, type = 1, next_id = 102110711, talk_content = "明白就好，倒是你为什么会在这里，你也不是悬天的人不是吗？", talk_name = "司命",},
	[102110711] = { talk_id = 102110711, group_id = 1021107, type = 1, next_id = 102110712, talk_content = "这个要不要出去再说，我们站在这里谈话也不安全。", talk_name = "义向",},
	[102110712] = { talk_id = 102110712, group_id = 1021107, type = 1, next_id = 102110713, talk_content = "当然，太阳的碎片我已经拿好了，走吧，这边有个通风口。", talk_name = "义向",},
	[102110713] = { talk_id = 102110713, group_id = 1021107, type = 1, talk_content = "就一定要钻通风口吗……", talk_name = "叙慕",},
	[102120101] = { talk_id = 102120101, group_id = 1021201, type = 1, next_id = 102120102, talk_content = "前面就是出口了吧。", talk_name = "叙慕",},
	[102120102] = { talk_id = 102120102, group_id = 1021201, type = 1, next_id = 102120103, talk_content = "呸呸，这通风口脏死了，我羽毛都被搞得黑漆漆的！", talk_name = "司命",},
	[102120103] = { talk_id = 102120103, group_id = 1021201, type = 1, next_id = 102120104, talk_content = "你不本来就是黑的。", talk_name = "叙慕",},
	[102120104] = { talk_id = 102120104, group_id = 1021201, type = 1, next_id = 102120105, talk_content = "话说回来，那猴子呢？", talk_name = "司命",},
	[102120105] = { talk_id = 102120105, group_id = 1021201, type = 1, next_id = 102120106, talk_content = "人不见了。", talk_name = "叙慕",},
	[102120106] = { talk_id = 102120106, group_id = 1021201, type = 1, next_id = 102120107, talk_content = "喂，地上有个纸条！", talk_name = "司命",},
	[102120107] = { talk_id = 102120107, group_id = 1021201, type = 1, next_id = 102120108, talk_content = "内容是……“先行一步”？", talk_name = "叙慕",},
	[102120108] = { talk_id = 102120108, group_id = 1021201, type = 1, next_id = 102120109, talk_content = "“碎片我就先拿走了，请你们之后直接来天月城来找我。”", talk_name = "叙慕",},
	[102120109] = { talk_id = 102120109, group_id = 1021201, type = 1, next_id = 102120110, talk_content = "“从这一路向北就能看见天月城，进城以后找找尾巴的标记。”", talk_name = "叙慕",},
	[102120110] = { talk_id = 102120110, group_id = 1021201, type = 1, next_id = 102120111, talk_content = "“我就在最后一个尾巴所指的地方等你们。”", talk_name = "叙慕",},
	[102120111] = { talk_id = 102120111, group_id = 1021201, type = 1, next_id = 102120112, talk_content = "这是什么……解谜吗？", talk_name = "司命",},
	[102120112] = { talk_id = 102120112, group_id = 1021201, type = 1, next_id = 102120113, talk_content = "算不上，只是个留言而已，早知道刚刚应该直接把他打趴下的……", talk_name = "叙慕",},
	[102120113] = { talk_id = 102120113, group_id = 1021201, type = 1, talk_content = "……你这家伙有时候还挺危险的。", talk_name = "司命",},
	[102120201] = { talk_id = 102120201, group_id = 1021202, type = 4, next_id = 102120202, aside = "<color=#FFEBA2>司命：</color>青乌，这附近有钟悬的气息，进城之前先去那边敲个钟吧。", talk_content = "青乌，这附近有钟悬的气息，进城之前先去那边敲个钟吧。", talk_name = "司命",},
	[102120202] = { talk_id = 102120202, group_id = 1021202, type = 4, next_id = 102120203, aside = "<color=#FFEBA2>叙慕：</color>为啥？", talk_content = "为啥？", talk_name = "叙慕",},
	[102120203] = { talk_id = 102120203, group_id = 1021202, type = 4, aside = "<color=#FFEBA2>司命：</color>我有用就是了，反正敲响了钟悬以后能拓宽视野，对你规划道路也有帮助不是吗？", talk_content = "我有用就是了，反正敲响了钟悬以后能拓宽视野，对你规划道路也有帮助不是吗？", talk_name = "司命",},
	[102120301] = { talk_id = 102120301, group_id = 1021203, type = 1, next_id = 102120302, talk_content = "要说敲……这里也没有敲钟的东西。", talk_name = "叙慕",},
	[102120302] = { talk_id = 102120302, group_id = 1021203, type = 1, next_id = 102120303, talk_content = "奇了个怪了，钟杵是被谁拿走了吗？", talk_name = "司命",},
	[102120303] = { talk_id = 102120303, group_id = 1021203, type = 1, next_id = 102120304, talk_content = "那怎么办，直接用剑打上去能行吗？", talk_name = "叙慕",},
	[102120304] = { talk_id = 102120304, group_id = 1021203, type = 1, next_id = 102120305, talk_content = "钟和杵是一体的东西，用代替物就算能敲也没法发挥功效。", talk_name = "司命",},
	[102120305] = { talk_id = 102120305, group_id = 1021203, type = 1, next_id = 102120306, talk_content = "唔，看样子总觉得是有某人把杵给带走了，青乌，试着在周围找找看吧。", talk_name = "司命",},
	[102120306] = { talk_id = 102120306, group_id = 1021203, type = 1, next_id = 102120307, talk_content = "线索都没有是要怎么找？", talk_name = "叙慕",},
	[102120307] = { talk_id = 102120307, group_id = 1021203, type = 1, talk_content = "足迹，气息，或者其他异常的东西，总会有办法的。", talk_name = "司命",},
	[102120401] = { talk_id = 102120401, group_id = 1021204, type = 4, next_id = 102120402, aside = "<color=#FFEBA2>叙慕：</color>要说像是痕迹的只有这个了吧，很明显有什么压过的痕迹。", talk_content = "要说像是痕迹的只有这个了吧，很明显有什么压过的痕迹。", talk_name = "叙慕",},
	[102120402] = { talk_id = 102120402, group_id = 1021204, type = 4, next_id = 102120403, aside = "<color=#FFEBA2>司命：</color>看着是一路延申到树林里面呢。", talk_content = "看着是一路延申到树林里面呢。", talk_name = "司命",},
	[102120403] = { talk_id = 102120403, group_id = 1021204, type = 4, aside = "<color=#FFEBA2>叙慕：</color>去看看吧。", talk_content = "去看看吧。", talk_name = "叙慕",},
	[102120501] = { talk_id = 102120501, group_id = 1021205, type = 4, next_id = 102120502, aside = "<color=#FFEBA2>叙慕：</color>果然这个地方自然生态很丰富……一路走来都看见好几种感觉了。", talk_content = "果然这个地方自然生态很丰富……一路走来都看见好几种感觉了。", talk_name = "叙慕",},
	[102120502] = { talk_id = 102120502, group_id = 1021205, type = 4, aside = "<color=#FFEBA2>司命：</color>毕竟这里可是被人类称创生地的地方呢。", talk_content = "毕竟这里可是被人类称创生地的地方呢。", talk_name = "司命",},
	[102120601] = { talk_id = 102120601, group_id = 1021206, type = 1, next_id = 102120602, talk_content = "感觉我们越来越靠近太阳了。", talk_name = "叙慕",},
	[102120602] = { talk_id = 102120602, group_id = 1021206, type = 1, next_id = 102120603, talk_content = "！", talk_name = "？？？",},
	[102120603] = { talk_id = 102120603, group_id = 1021206, type = 1, next_id = 102120604, talk_content = "那些是普通的生物吗？", talk_name = "叙慕",},
	[102120604] = { talk_id = 102120604, group_id = 1021206, type = 1, next_id = 102120605, talk_content = "中间那只明显很不正常，有噬脉的气息在它身上。", talk_name = "司命",},
	[102120605] = { talk_id = 102120605, group_id = 1021206, type = 1, next_id = 102120606, talk_content = "所以是噬脉生物？", talk_name = "叙慕",},
	[102120606] = { talk_id = 102120606, group_id = 1021206, type = 1, next_id = 102120607, talk_content = "也不像是，奇怪，这到底是什么……", talk_name = "司命",},
	[102120607] = { talk_id = 102120607, group_id = 1021206, type = 1, next_id = 102120608, talk_content = "！！！", talk_name = "怪物",},
	[102120608] = { talk_id = 102120608, group_id = 1021206, type = 1, talk_content = "也罢，反正都被盯上了。", talk_name = "叙慕",},
	[102120701] = { talk_id = 102120701, group_id = 1021207, type = 4, next_id = 102120702, aside = "<color=#FFEBA2>叙慕：</color>怎么有种一直都在和强敌战斗的感觉，这个世界到处都这么危险的吗！", talk_content = "怎么有种一直都在和强敌战斗的感觉，这个世界到处都这么危险的吗！", talk_name = "叙慕",},
	[102120702] = { talk_id = 102120702, group_id = 1021207, type = 4, next_id = 102120703, aside = "<color=#FFEBA2>司命：</color>或许这就是拯救世界的旅途了吧。", talk_content = "或许这就是拯救世界的旅途了吧。", talk_name = "司命",},
	[102120703] = { talk_id = 102120703, group_id = 1021207, type = 4, aside = "<color=#FFEBA2>叙慕：</color>明明我都还没准备干那事！", talk_content = "明明我都还没准备干那事！", talk_name = "叙慕",},
	[102120801] = { talk_id = 102120801, group_id = 1021208, type = 4, next_id = 102120802, aside = "<color=#FFEBA2>叙慕：</color>简直就和发狂了没两样。", talk_content = "简直就和发狂了没两样。", talk_name = "叙慕",},
	[102120802] = { talk_id = 102120802, group_id = 1021208, type = 4, aside = "<color=#FFEBA2>司命：</color>它身体里面的噬脉之气到底是被谁弄进去的……", talk_content = "它身体里面的噬脉之气到底是被谁弄进去的……", talk_name = "司命",},
	[102120901] = { talk_id = 102120901, group_id = 1021209, type = 4, next_id = 102120902, aside = "<color=#FFEBA2>司命：</color>喂，青乌，杵在那边。", talk_content = "喂，青乌，杵在那边。", talk_name = "司命",},
	[102120902] = { talk_id = 102120902, group_id = 1021209, type = 4, next_id = 102120903, aside = "<color=#FFEBA2>司命：</color>看来犯人就是刚刚的生物了呢，难怪会有那种痕迹。", talk_content = "看来犯人就是刚刚的生物了呢，难怪会有那种痕迹。", talk_name = "司命",},
	[102120903] = { talk_id = 102120903, group_id = 1021209, type = 4, next_id = 102120904, aside = "<color=#FFEBA2>叙慕：</color>所以刚刚的到底怎么回事，那个是本来就那么危险的怪物吗？", talk_content = "所以刚刚的到底怎么回事，那个是本来就那么危险的怪物吗？", talk_name = "叙慕",},
	[102120904] = { talk_id = 102120904, group_id = 1021209, type = 4, aside = "<color=#FFEBA2>司命：</color>凶暴的家伙自然是有，但那个应该不是危险的品种才对。", talk_content = "凶暴的家伙自然是有，但那个应该不是危险的品种才对。", talk_name = "司命",},
	[102121001] = { talk_id = 102121001, group_id = 1021210, type = 4, aside = "<color=#FFEBA2>司命：</color>可以，这样就能敲钟了，我们赶紧回去吧。", talk_content = "可以，这样就能敲钟了，我们赶紧回去吧。", talk_name = "司命",},
	[102121101] = { talk_id = 102121101, group_id = 1021211, type = 1, next_id = 102121102, talk_content = "很好，这样创生地就又有一片地方可以为我所用了。", talk_name = "司命",},
	[102121102] = { talk_id = 102121102, group_id = 1021211, type = 1, next_id = 102121103, talk_content = "什么叫为你所用。", talk_name = "叙慕",},
	[102121103] = { talk_id = 102121103, group_id = 1021211, type = 2, options = {{"为什么？",102121104,},}, talk_content = "就是字面上的意思，你敲响越多的钟悬，我能够运用的力量也就越多。", talk_name = "司命",},
	[102121104] = { talk_id = 102121104, group_id = 1021211, type = 3, next_id = 102121105, talk_content = "说来好像没和你说过呐，关于我真体之间的联系。", talk_name = "司命",},
	[102121105] = { talk_id = 102121105, group_id = 1021211, type = 3, next_id = 102121106, talk_content = "给我听好了，这个世界起源于一片混沌，随着天地开辟，气脉因此出现在了大地之上。", talk_name = "司命",},
	[102121106] = { talk_id = 102121106, group_id = 1021211, type = 3, next_id = 102121107, talk_content = "其后海洋，大陆分化演变，最终起源的生命于星球之上诞生。", talk_name = "司命",},
	[102121107] = { talk_id = 102121107, group_id = 1021211, type = 3, next_id = 102121108, talk_content = "是说细胞分子之类的？", talk_name = "叙慕",},
	[102121108] = { talk_id = 102121108, group_id = 1021211, type = 3, next_id = 102121109, talk_content = "这都什么东西，说到起源自然是起源十二兽了！", talk_name = "司命",},
	[102121109] = { talk_id = 102121109, group_id = 1021211, type = 3, next_id = 102121110, talk_content = "……没听说过。", talk_name = "叙慕",},
	[102121110] = { talk_id = 102121110, group_id = 1021211, type = 3, next_id = 102121111, talk_content = "顺带一提，创生地这里之所以会有这个名字是因为十二兽中的几只都诞生于此。", talk_name = "司命",},
	[102121111] = { talk_id = 102121111, group_id = 1021211, type = 3, next_id = 102121112, talk_content = "总之，十二兽在诞生之后就朝着不同的方向移动，他们的身体与气脉紧密联系", talk_name = "司命",},
	[102121112] = { talk_id = 102121112, group_id = 1021211, type = 3, next_id = 102121113, talk_content = "走到哪里，生命的种子就会散播到哪里，最终，当这些散播种子的使者沉睡的时候，世界已遍布生命。", talk_name = "司命",},
	[102121113] = { talk_id = 102121113, group_id = 1021211, type = 3, next_id = 102121114, talk_content = "大概听懂了，就是创世神话一类的东西嘛，所以这跟你有什么关系？", talk_name = "叙慕",},
	[102121114] = { talk_id = 102121114, group_id = 1021211, type = 3, next_id = 102121115, talk_content = "你是真不懂还是装不懂，我都说到这份上了，哎。", talk_name = "司命",},
	[102121115] = { talk_id = 102121115, group_id = 1021211, type = 3, next_id = 102121116, talk_content = "所以说，我就是始源十二兽之一，名为酉的始源兽的……", talk_name = "司命",},
	[102121116] = { talk_id = 102121116, group_id = 1021211, type = 3, next_id = 102121117, talk_content = "的？", talk_name = "叙慕",},
	[102121117] = { talk_id = 102121117, group_id = 1021211, type = 3, next_id = 102121118, talk_content = "……残骸。", talk_name = "司命",},
	[102121118] = { talk_id = 102121118, group_id = 1021211, type = 3, next_id = 102121119, talk_content = "……这是怎么回事？", talk_name = "叙慕",},
	[102121119] = { talk_id = 102121119, group_id = 1021211, type = 3, next_id = 102121120, talk_content = "起源并不是永远，生命终有凋零之日，十二兽也是一样。", talk_name = "司命",},
	[102121120] = { talk_id = 102121120, group_id = 1021211, type = 3, next_id = 102121121, talk_content = "在酉顺应天命之后，我，司命就从那个残骸之中出生了。", talk_name = "司命",},
	[102121121] = { talk_id = 102121121, group_id = 1021211, type = 3, next_id = 102121122, talk_content = "作为残骸的我就只记得一件事，青乌能够拯救这个世界。", talk_name = "司命",},
	[102121122] = { talk_id = 102121122, group_id = 1021211, type = 3, next_id = 102121123, talk_content = "所以之前你才会说你不知道。", talk_name = "叙慕",},
	[102121123] = { talk_id = 102121123, group_id = 1021211, type = 3, next_id = 102121124, talk_content = "嗯，不是失忆，只是因为我大概和酉已经不是一样的东西了，但是只要持续与气脉联系的话……", talk_name = "司命",},
	[102121124] = { talk_id = 102121124, group_id = 1021211, type = 3, next_id = 102121125, talk_content = "就能变回酉吗？", talk_name = "叙慕",},
	[102121125] = { talk_id = 102121125, group_id = 1021211, type = 2, options = {{"我没想到你竟然是个好人。",102121126,},{"你应该早点和我说这些事。",102121127,},}, talk_content = "是的，只要变回真体，那么拯救世界之旅应该也会更轻松，甚至那几个太阳应该也可以轻松碾压过去才是。", talk_name = "司命",},
	[102121126] = { talk_id = 102121126, group_id = 1021211, type = 3, next_id = 102121128, talk_content = "什么叫没想到，你就这么想我是什么邪恶生物吗！", talk_name = "司命",},
	[102121127] = { talk_id = 102121127, group_id = 1021211, type = 3, next_id = 102121128, talk_content = "没机会捏，何况说出来也对情况没什么帮助吧。", talk_name = "司命",},
	[102121128] = { talk_id = 102121128, group_id = 1021211, type = 1, next_id = 102121129, talk_content = "唉，我只是说我两的关系或许能再好点而已，总之已经没有需要干的事情了吧。", talk_name = "叙慕",},
	[102121129] = { talk_id = 102121129, group_id = 1021211, type = 1, next_id = 102121130, talk_content = "嗯，钟也敲了，我们接下来就去城里面吧。", talk_name = "司命",},
	[102121130] = { talk_id = 102121130, group_id = 1021211, type = 1, next_id = 102121131, talk_content = "天月城……太阳，我的记忆。", talk_name = "叙慕",},
	[102121131] = { talk_id = 102121131, group_id = 1021211, type = 1, next_id = 102121132, talk_content = "当然还有能预想到无数战斗。", talk_name = "司命",},
	[102121132] = { talk_id = 102121132, group_id = 1021211, type = 1, next_id = 102121133, talk_content = "城里面打起来没问题吗？", talk_name = "叙慕",},
	[102121133] = { talk_id = 102121133, group_id = 1021211, type = 1, next_id = 102121134, talk_content = "悬天的人打过来你也没办法吧。", talk_name = "司命",},
	[102121134] = { talk_id = 102121134, group_id = 1021211, type = 1, next_id = 102121135, talk_content = "拯救世界还挺辛苦的。", talk_name = "叙慕",},
	[102121135] = { talk_id = 102121135, group_id = 1021211, type = 1, next_id = 102121136, talk_content = "唉，等等，你刚刚是不是说拯救世界这几个字了。", talk_name = "司命",},
	[102121136] = { talk_id = 102121136, group_id = 1021211, type = 1, talk_content = "不，我没说过。", talk_name = "叙慕",},
	[102130101] = { talk_id = 102130101, group_id = 1021301, type = 4, next_id = 102130102, aside = "<color=#FFEBA2>叙慕：</color>我没想到那上面真的是个城市，该不会这里的人全是这样生活的吧？", talk_content = "我没想到那上面真的是个城市，该不会这里的人全是这样生活的吧？", talk_name = "叙慕",},
	[102130102] = { talk_id = 102130102, group_id = 1021301, type = 4, next_id = 102130103, aside = "<color=#FFEBA2>司命：</color>怎么可能，这是个特例而已。", talk_content = "怎么可能，这是个特例而已。", talk_name = "司命",},
	[102130103] = { talk_id = 102130103, group_id = 1021301, type = 4, next_id = 102130104, aside = "<color=#FFEBA2>叙慕：</color>世界的一切都是如此熟悉但又感到陌生……", talk_content = "世界的一切都是如此熟悉但又感到陌生……", talk_name = "叙慕",},
	[102130104] = { talk_id = 102130104, group_id = 1021301, type = 4, next_id = 102130105, aside = "<color=#FFEBA2>司命：</color>？", talk_content = "？", talk_name = "司命",},
	[102130105] = { talk_id = 102130105, group_id = 1021301, type = 4, next_id = 102130106, aside = "<color=#FFEBA2>叙慕：</color>无论如何，得先想办法回忆起一切。", talk_content = "无论如何，得先想办法回忆起一切。", talk_name = "叙慕",},
	[102130106] = { talk_id = 102130106, group_id = 1021301, type = 4, aside = "<color=#FFEBA2>司命：</color>可别忘了还有拯救世界！", talk_content = "可别忘了还有拯救世界！", talk_name = "司命",},
	[102130201] = { talk_id = 102130201, group_id = 1021302, type = 5, next_id = 102130202, aside = "<color=#FFEBA2>叙慕：</color>司命，我应该没走错地方吧？", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "司命，我应该没走错地方吧？", talk_name = "叙慕",},
	[102130202] = { talk_id = 102130202, group_id = 1021302, type = 5, next_id = 102130203, aside = "<color=#FFEBA2>司命：</color>城里最大的建筑就是这个没跑了，倒是找人问问怎么到天上……", talk_content = "城里最大的建筑就是这个没跑了，倒是找人问问怎么到天上……", talk_name = "司命",},
	[102130203] = { talk_id = 102130203, group_id = 1021302, type = 5, next_id = 102130204, aside = "<color=#FFEBA2>？？？：</color>把我这个脉者放着不用反而让一群普通人去办事你到底是怎么想的！", talk_content = "把我这个脉者放着不用反而让一群普通人去办事你到底是怎么想的！", talk_name = "？？？",},
	[102130204] = { talk_id = 102130204, group_id = 1021302, type = 5, next_id = 102130205, aside = "<color=#FFEBA2>？？？：</color>刚加入的新人是不能参与危险任务的，这我应该强调过很多次了吧？", talk_content = "刚加入的新人是不能参与危险任务的，这我应该强调过很多次了吧？", talk_name = "？？？",},
	[102130205] = { talk_id = 102130205, group_id = 1021302, type = 5, next_id = 102130206, aside = "<color=#FFEBA2>？？？：</color>调查队的名单里面不是有20几个人，加我一个有什么关系，我已经证明过自己实力了吧！", talk_content = "调查队的名单里面不是有20几个人，加我一个有什么关系，我已经证明过自己实力了吧！", talk_name = "？？？",},
	[102130206] = { talk_id = 102130206, group_id = 1021302, type = 5, next_id = 102130207, aside = "<color=#FFEBA2>？？？：</color>这不是实力问题，是组织纪律的问题，回到你的岗位上去！", talk_content = "这不是实力问题，是组织纪律的问题，回到你的岗位上去！", talk_name = "？？？",},
	[102130207] = { talk_id = 102130207, group_id = 1021302, type = 5, next_id = 102130208, aside = "<color=#FFEBA2>？？？：</color>跟你聊不下去了！", talk_content = "跟你聊不下去了！", talk_name = "？？？",},
	[102130208] = { talk_id = 102130208, group_id = 1021302, type = 5, aside = "<color=#FFEBA2>？？？：</color>喂，等等！", talk_content = "喂，等等！", talk_name = "？？？",},
	[102130301] = { talk_id = 102130301, group_id = 1021303, type = 4, aside = "<color=#FFEBA2>司命：</color>那家伙从衣服上看应该是个大官，去跟他问问上天的事情吧。", talk_content = "那家伙从衣服上看应该是个大官，去跟他问问上天的事情吧。", talk_name = "司命",},
	[102130401] = { talk_id = 102130401, group_id = 1021304, type = 1, next_id = 102130402, hide_all_entity = true, hide_player = true, talk_content = "你好，方便问些问题吗？", talk_name = "叙慕",},
	[102130402] = { talk_id = 102130402, group_id = 1021304, type = 1, next_id = 102130403, talk_content = "当然可以……啊，一人一鸟。", talk_name = "？？？",},
	[102130403] = { talk_id = 102130403, group_id = 1021304, type = 1, next_id = 102130404, talk_content = "你知道我们？", talk_name = "司命",},
	[102130404] = { talk_id = 102130404, group_id = 1021304, type = 1, next_id = 102130405, talk_content = "刚刚温罗发来了一条信息，说是稍后会有一人一鸟的组合来拜访我。", talk_name = "？？？",},
	[102130405] = { talk_id = 102130405, group_id = 1021304, type = 2, options = {{"（自我介绍）",102130406,},}, talk_content = "先自我介绍一下吧，我的名字叫朱嫦，是这个维安司的司长，也就是总负责人。", talk_name = "朱嫦",},
	[102130406] = { talk_id = 102130406, group_id = 1021304, type = 3, next_id = 102130407, talk_content = "和温罗说的一样，感谢你们救下了帝江之地的居民，青乌以及司命。", talk_name = "朱嫦",},
	[102130407] = { talk_id = 102130407, group_id = 1021304, type = 1, next_id = 102130408, talk_content = "要感谢的话就帮我们到天上去吧，那个温罗说是要来你这里办手续对吗？", talk_name = "司命",},
	[102130408] = { talk_id = 102130408, group_id = 1021304, type = 2, options = {{"有什么问题？",102130409,},{"司长应该不会受限制吧？",102130410,},}, talk_content = "手续的话在柜台附近就能办，但现在地上有些问题，相关的出入手续都被暂停了。", talk_name = "朱嫦",},
	[102130409] = { talk_id = 102130409, group_id = 1021304, type = 3, next_id = 102130411, talk_content = "是异常。", talk_name = "朱嫦",},
	[102130410] = { talk_id = 102130410, group_id = 1021304, type = 3, next_id = 102130411, talk_content = "抱歉，我这个司长还没有这么大的权利，在“异常”被解决之前就是我也不能到天月城去。", talk_name = "朱嫦",},
	[102130411] = { talk_id = 102130411, group_id = 1021304, type = 1, next_id = 102130412, talk_content = "异常是说什么？", talk_name = "司命",},
	[102130412] = { talk_id = 102130412, group_id = 1021304, type = 1, next_id = 102130413, talk_content = "简单来说就是土地上出现了反重力的区域。", talk_name = "朱嫦",},
	[102130413] = { talk_id = 102130413, group_id = 1021304, type = 1, next_id = 102130414, talk_content = "怎么个反重力法？", talk_name = "叙慕",},
	[102130414] = { talk_id = 102130414, group_id = 1021304, type = 1, next_id = 102130415, talk_content = "就是东西都以倒立的形式浮起来了，对，就和天月城一样。", talk_name = "朱嫦",},
	[102130415] = { talk_id = 102130415, group_id = 1021304, type = 1, next_id = 102130416, talk_content = "就只是这样应该没什么问题吧，那个城市不也这么浮着吗？", talk_name = "司命",},
	[102130416] = { talk_id = 102130416, group_id = 1021304, type = 1, next_id = 102130417, talk_content = "但在异常出现之前只有天月城是这样的状态，而且那些反重力区还是噬脉生物的聚集地。", talk_name = "朱嫦",},
	[102130417] = { talk_id = 102130417, group_id = 1021304, type = 1, next_id = 102130418, talk_content = "就是说被怪物当作大本营了啊。", talk_name = "司命",},
	[102130418] = { talk_id = 102130418, group_id = 1021304, type = 1, next_id = 102130419, talk_content = "嗯，这个状况是百年未闻，所以在我们调查有所进展之前天月城下令暂时封锁通行。", talk_name = "朱嫦",},
	[102130419] = { talk_id = 102130419, group_id = 1021304, type = 1, next_id = 102130420, talk_content = "所以说真正管着帝江之地的是上面的家伙呐。", talk_name = "司命",},
	[102130420] = { talk_id = 102130420, group_id = 1021304, type = 1, next_id = 102130421, talk_content = "虽然有些岔开话题，但天月到底是怎么回事，你们真的住在那里面吗？", talk_name = "叙慕",},
	[102130421] = { talk_id = 102130421, group_id = 1021304, type = 1, talk_content = "这个算是外来人都会问的一个问题了，答案自然是是的，我们都住在那里面。", talk_name = "朱嫦",},
	[102130501] = { talk_id = 102130501, group_id = 1021305, type = 1, next_id = 102130502, hide_all_entity = true, hide_player = true, talk_content = "而这个帝江之地，原名为创生，天月城，也并不是一开始就在空中的。", talk_name = "朱嫦",},
	[102130502] = { talk_id = 102130502, group_id = 1021305, type = 1, next_id = 102130503, talk_content = "自然，我们的祖先也从没有想过要在空中生活，日复一日和平的生活着。", talk_name = "朱嫦",},
	[102130503] = { talk_id = 102130503, group_id = 1021305, type = 1, next_id = 102130504, talk_content = "但在数百年前的某一天，一场灾难改变了一切。", talk_name = "朱嫦",},
	[102130504] = { talk_id = 102130504, group_id = 1021305, type = 1, next_id = 102130505, talk_content = "关于灾难的记载就只有只言片语，但可以知晓的是地上突然就不适合生存了。", talk_name = "朱嫦",},
	[102130505] = { talk_id = 102130505, group_id = 1021305, type = 1, next_id = 102130506, talk_content = "在刹那间，城内外出现了无数因不明原因倒下的居民，圈养的家畜也无一幸免，唯一能在这场灾难中活下来的，就只有名为噬脉的可怖存在。", talk_name = "朱嫦",},
	[102130506] = { talk_id = 102130506, group_id = 1021305, type = 1, next_id = 102130507, talk_content = "这些噬脉生物和灾难有何关系我们已不得而知，但无疑它们借着这场灾难大举向人类发起攻击，并将我们的祖先逼入了绝路。", talk_name = "朱嫦",},
	[102130507] = { talk_id = 102130507, group_id = 1021305, type = 1, next_id = 102130508, talk_content = "在这个关头，远方的世界之宗主，王土送来了制世太阳——帝江。", talk_name = "朱嫦",},
	[102130508] = { talk_id = 102130508, group_id = 1021305, type = 1, next_id = 102130509, talk_content = "以帝江之力，天月城和周遭的一部份区域以倒立的形式被移到了天上，居民也因此脱离危难，保存了性命。", talk_name = "朱嫦",},
	[102130509] = { talk_id = 102130509, group_id = 1021305, type = 1, next_id = 102130510, talk_content = "之后，帝江用其光芒持续照耀大地，最终就连地上的灾难也完全驱散，让天月城的人们能重新回到大地之上。", talk_name = "朱嫦",},
	[102130510] = { talk_id = 102130510, group_id = 1021305, type = 1, next_id = 102130511, talk_content = "于是，保持着尊敬和感激，人们将这片土地冠上了太阳的尊名。", talk_name = "朱嫦",},
	[102130511] = { talk_id = 102130511, group_id = 1021305, type = 1, next_id = 102130512, talk_content = "而关于这段历史，后人有诗曰……", talk_name = "朱嫦",},
	[102130512] = { talk_id = 102130512, group_id = 1021305, type = 1, talk_content = "日落帝江土，天月倒空中。噬脉灾肆虐，帝光护生灵。", talk_name = "朱嫦",},
	[102130601] = { talk_id = 102130601, group_id = 1021306, type = 1, next_id = 102130602, hide_all_entity = true, hide_player = true, talk_content = "以上，就是关于这片土地和天月城的历史，每当外人问起我们就会搬出这个，算是标准答案了。", talk_name = "朱嫦",},
	[102130602] = { talk_id = 102130602, group_id = 1021306, type = 1, next_id = 102130603, talk_content = "差点就睡着了。", talk_name = "司命",},
	[102130603] = { talk_id = 102130603, group_id = 1021306, type = 1, next_id = 102130604, talk_content = "呃，果然是有点长了吗？", talk_name = "朱嫦",},
	[102130604] = { talk_id = 102130604, group_id = 1021306, type = 1, next_id = 102130605, talk_content = "司命，我有些在意的地方。", talk_name = "叙慕",},
	[102130605] = { talk_id = 102130605, group_id = 1021306, type = 1, next_id = 102130606, talk_content = "我知道，你要说太阳的事情对吧，这个等我们把进城的手续搞妥再谈如何？", talk_name = "司命",},
	[102130606] = { talk_id = 102130606, group_id = 1021306, type = 1, next_id = 102130607, talk_content = "就如我之前所说的，手续一时半会恐怕办不下来，但在道路重新开通以后我能够帮两位首先安排。", talk_name = "朱嫦",},
	[102130607] = { talk_id = 102130607, group_id = 1021306, type = 1, next_id = 102130608, talk_content = "作为交换，我想拜托两位一件小事。", talk_name = "朱嫦",},
	[102130608] = { talk_id = 102130608, group_id = 1021306, type = 1, next_id = 102130609, talk_content = "啥？太累得我可不干噢。", talk_name = "司命",},
	[102130609] = { talk_id = 102130609, group_id = 1021306, type = 1, next_id = 102130610, talk_content = "帮我追回来来，将她带到这里来。", talk_name = "朱嫦",},
	[102130610] = { talk_id = 102130610, group_id = 1021306, type = 2, options = {{"来来是谁？",102130612,},{"为什么要拜托我？",102130611,},}, talk_content = "就是刚刚与我争吵并跑出去的那位。", talk_name = "朱嫦",},
	[102130611] = { talk_id = 102130611, group_id = 1021306, type = 3, next_id = 102130613, talk_content = "因为你是脉者，来来是最近派到这里来的新人，她本质不坏，但立功心切，放任不管的话我怕会闯出祸来。", talk_name = "朱嫦",},
	[102130612] = { talk_id = 102130612, group_id = 1021306, type = 3, next_id = 102130613, talk_content = "来来是最近派到这里来的新人，她本质不坏，但立功心切，放任不管的话我怕会闯出祸来。", talk_name = "朱嫦",},
	[102130613] = { talk_id = 102130613, group_id = 1021306, type = 1, next_id = 102130614, talk_content = "平时的话我差遣别人便是，但不巧的是现在司内大部分人都被我派到外面执行任务。", talk_name = "朱嫦",},
	[102130614] = { talk_id = 102130614, group_id = 1021306, type = 1, next_id = 102130615, talk_content = "而我也不能随意离开维安司，拜托了，青乌。", talk_name = "朱嫦",},
	[102130615] = { talk_id = 102130615, group_id = 1021306, type = 2, options = {{"嗯，就接下来吧。",102130616,},{"就试试吧。",102130616,},}, talk_content = "去追任性小孩呐……听着也不是难事，而且之后也能插队，就帮他一下吧，青乌。", talk_name = "司命",},
	[102130616] = { talk_id = 102130616, group_id = 1021306, type = 3, next_id = 102130617, talk_content = "就这么定了。", talk_name = "司命",},
	[102130617] = { talk_id = 102130617, group_id = 1021306, type = 3, next_id = 102130618, talk_content = "对了，虽然说通往天月城的技能被关停了，但是城内的大钟悬本身还保留着原有的功能，初来这里的话你们还是去敲敲求个平安比较好。", talk_name = "朱嫦",},
	[102130618] = { talk_id = 102130618, group_id = 1021306, type = 3, talk_content = "原来敲钟这件事是普遍都会做的吗？", talk_name = "叙慕",},
	[102130619] = { talk_id = 102130619, group_id = 1021306, type = 3, talk_content = "要考虑下吗，也没关系，但干等也挺无聊的。", talk_name = "司命",},
	[102130701] = { talk_id = 102130701, group_id = 1021307, type = 1, next_id = 102130702, talk_content = "来来是最近派到这里来的新人，她本质不坏，但立功心切，放任不管的话我怕会闯出祸来。", talk_name = "朱嫦",},
	[102130702] = { talk_id = 102130702, group_id = 1021307, type = 1, next_id = 102130703, talk_content = "平时的话我差遣别人便是，但不巧的是现在司内大部分人都被我派到外面执行任务。", talk_name = "朱嫦",},
	[102130703] = { talk_id = 102130703, group_id = 1021307, type = 1, next_id = 102130704, talk_content = "而我也不能随意离开维安司，拜托了，青乌。", talk_name = "朱嫦",},
	[102130704] = { talk_id = 102130704, group_id = 1021307, type = 2, options = {{"嗯，就接下来吧。",102130705,},{"我思考一下。",102130708,},}, talk_content = "去追任性小孩呐……听着也不是难事，而且之后也能插队，就帮他一下吧，青乌。", talk_name = "司命",},
	[102130705] = { talk_id = 102130705, group_id = 1021307, type = 3, next_id = 102130706, talk_content = "就这么定了。", talk_name = "司命",},
	[102130706] = { talk_id = 102130706, group_id = 1021307, type = 3, next_id = 102130707, talk_content = "对了，虽然说通往天月城的技能被关停了，但是城内的大钟悬本身还保留着原有的功能，初来这里的话你们还是去敲敲求个平安比较好。", talk_name = "朱嫦",},
	[102130707] = { talk_id = 102130707, group_id = 1021307, type = 3, talk_content = "原来敲钟这件事是普遍都会做的吗？", talk_name = "叙慕",},
	[102130708] = { talk_id = 102130708, group_id = 1021307, type = 3, talk_content = "要考虑下吗，也没关系，但干等也挺无聊的。", talk_name = "司命",},
	[102140101] = { talk_id = 102140101, group_id = 1021401, type = 4, next_id = 102140102, aside = "<color=#FFEBA2>司命：</color>要找人就先找几个路人问问如何，那些在卖东西的就不错。", talk_content = "要找人就先找几个路人问问如何，那些在卖东西的就不错。", talk_name = "司命",},
	[102140102] = { talk_id = 102140102, group_id = 1021401, type = 4, next_id = 102140103, aside = "<color=#FFEBA2>叙慕：</color>比起问人我更想你和我说一下太阳的事情。", talk_content = "比起问人我更想你和我说一下太阳的事情。", talk_name = "叙慕",},
	[102140103] = { talk_id = 102140103, group_id = 1021401, type = 4, next_id = 102140104, aside = "<color=#FFEBA2>司命：</color>这些事情得讲气氛，气氛到了，我自然就会讲了。", talk_content = "这些事情得讲气氛，气氛到了，我自然就会讲了。", talk_name = "司命",},
	[102140104] = { talk_id = 102140104, group_id = 1021401, type = 4, aside = "<color=#FFEBA2>叙慕：</color>……", talk_content = "……", talk_name = "叙慕",},
	[102140201] = { talk_id = 102140201, group_id = 1021402, type = 1, next_id = 102140202, hide_all_entity = true, hide_player = true, talk_content = "噢，是生面孔呢，要买点什么吗？", talk_name = "安琪",},
	[102140202] = { talk_id = 102140202, group_id = 1021402, type = 1, next_id = 102140203, talk_content = "你这里有啥卖的？", talk_name = "司命",},
	[102140203] = { talk_id = 102140203, group_id = 1021402, type = 1, next_id = 102140204, talk_content = "日用杂货，药品装备，我们百玲珑是专门贩售各类常品的店铺。", talk_name = "安琪",},
	[102140204] = { talk_id = 102140204, group_id = 1021402, type = 1, next_id = 102140205, talk_content = "就是百货一类的？", talk_name = "叙慕",},
	[102140205] = { talk_id = 102140205, group_id = 1021402, type = 1, next_id = 102140206, talk_content = "是的，我们这里基本什么都有卖，但假如需要某些特定用途的工具抑或药品的话建议客人还是去专门店的好。", talk_name = "安琪",},
	[102140206] = { talk_id = 102140206, group_id = 1021402, type = 1, next_id = 102140207, talk_content = "反之，一般的素材我们这里都有常备，客人之后可以多来看看哦。", talk_name = "安琪",},
	[102140207] = { talk_id = 102140207, group_id = 1021402, type = 1, next_id = 102140208, talk_content = "之后会看看的，其实我想问些事情。", talk_name = "叙慕",},
	[102140208] = { talk_id = 102140208, group_id = 1021402, type = 1, next_id = 102140209, talk_content = "是什么呢？", talk_name = "安琪",},
	[102140209] = { talk_id = 102140209, group_id = 1021402, type = 1, next_id = 102140210, talk_content = "我们正在找一个叫来来的女孩，她穿紫色的衣服，是那边维安司的新人，你有见过她吗？", talk_name = "叙慕",},
	[102140210] = { talk_id = 102140210, group_id = 1021402, type = 1, next_id = 102140211, talk_content = "没什么印象，可能没往这边走吧。", talk_name = "安琪",},
	[102140211] = { talk_id = 102140211, group_id = 1021402, type = 1, next_id = 102140212, talk_content = "谢谢，打扰你了。", talk_name = "叙慕",},
	[102140212] = { talk_id = 102140212, group_id = 1021402, type = 1, talk_content = "没关系没关系，下次来当客人就好。", talk_name = "安琪",},
	[102140301] = { talk_id = 102140301, group_id = 1021403, type = 1, next_id = 102140302, hide_all_entity = true, hide_player = true, talk_content = "这边有股好香的气味哦。", talk_name = "司命",},
	[102140302] = { talk_id = 102140302, group_id = 1021403, type = 1, next_id = 102140303, talk_content = "是食店之类的吧。", talk_name = "叙慕",},
	[102140303] = { talk_id = 102140303, group_id = 1021403, type = 1, next_id = 102140304, talk_content = "两位客人要不要来尝点什么，我们这里也支持外带哦。", talk_name = "纪晴",},
	[102140304] = { talk_id = 102140304, group_id = 1021403, type = 1, next_id = 102140305, talk_content = "有什么吃的？", talk_name = "司命",},
	[102140305] = { talk_id = 102140305, group_id = 1021403, type = 1, next_id = 102140306, talk_content = "虽然老爸说这季度主打清水百面，但我还是会推荐各位点一份烧艳鸟。", talk_name = "纪晴",},
	[102140306] = { talk_id = 102140306, group_id = 1021403, type = 1, next_id = 102140307, talk_content = "清水白面也能用来当招牌吗？", talk_name = "司命",},
	[102140307] = { talk_id = 102140307, group_id = 1021403, type = 1, next_id = 102140308, talk_content = "听着就不好吃对吧，真搞不懂老爸的想法。", talk_name = "纪晴",},
	[102140308] = { talk_id = 102140308, group_id = 1021403, type = 1, next_id = 102140309, talk_content = "不过那个烧什么鸟给我来一份。", talk_name = "司命",},
	[102140309] = { talk_id = 102140309, group_id = 1021403, type = 1, next_id = 102140310, talk_content = "你吃烧鸟的情景我想想都觉得惊悚，倒是我们是有正事的吧。", talk_name = "叙慕",},
	[102140310] = { talk_id = 102140310, group_id = 1021403, type = 1, next_id = 102140311, talk_content = "对哦，我们在找一个紫色的叫来来的小女孩，你有见过吗？", talk_name = "司命",},
	[102140311] = { talk_id = 102140311, group_id = 1021403, type = 1, next_id = 102140312, talk_content = "她可不是什么“小女孩”。", talk_name = "司命",},
	[102140312] = { talk_id = 102140312, group_id = 1021403, type = 1, next_id = 102140313, talk_content = "你们是说来来是吧，她刚才气冲冲地往那边的出口跑过去了。", talk_name = "纪晴",},
	[102140313] = { talk_id = 102140313, group_id = 1021403, type = 1, next_id = 102140314, talk_content = "所以说不在熙来里面了？", talk_name = "司命",},
	[102140314] = { talk_id = 102140314, group_id = 1021403, type = 1, talk_content = "应该是。", talk_name = "叙慕",},
	[102140401] = { talk_id = 102140401, group_id = 1021404, type = 4, aside = "<color=#FFEBA2>司命：</color>出口有守卫在站岗，问问他来来跑那里去了吧。", talk_content = "出口有守卫在站岗，问问他来来跑那里去了吧。", talk_name = "司命",},
	[102140501] = { talk_id = 102140501, group_id = 1021405, type = 1, next_id = 102140502, hide_all_entity = true, hide_player = true, talk_content = "你好，我想向你打听一个叫来来的女孩，她穿着紫色的衣服……", talk_name = "叙慕",},
	[102140502] = { talk_id = 102140502, group_id = 1021405, type = 2, options = {{"候补队员？",102140503,},{"我有不好的预感。",102140505,},}, talk_content = "怎么，你也是没有跟上调查队的候补队员吗？", talk_name = "守卫",},
	[102140503] = { talk_id = 102140503, group_id = 1021405, type = 3, next_id = 102140504, talk_content = "嗯，刚刚来来突然跑过来问我派出去调查的队伍到哪里去了……所以你并不是候补队员吗？", talk_name = "守卫",},
	[102140504] = { talk_id = 102140504, group_id = 1021405, type = 3, next_id = 102140506, talk_content = "应该说那个来来也不是呢。", talk_name = "司命",},
	[102140505] = { talk_id = 102140505, group_id = 1021405, type = 3, next_id = 102140506, talk_content = "嗯，大概那个来来是想要追上调查队吧。", talk_name = "司命",},
	[102140506] = { talk_id = 102140506, group_id = 1021405, type = 1, next_id = 102140507, talk_content = "话说回来，这个调查队是调查什么的？", talk_name = "司命",},
	[102140507] = { talk_id = 102140507, group_id = 1021405, type = 1, next_id = 102140508, talk_content = "是调查”异常“的队伍，听他们说是想要搞明白反重力区域和那些噬脉生物的关系。", talk_name = "守卫",},
	[102140508] = { talk_id = 102140508, group_id = 1021405, type = 1, next_id = 102140509, talk_content = "好像那个司长说过反重力区是噬脉生物的巢穴不是吗？", talk_name = "司命",},
	[102140509] = { talk_id = 102140509, group_id = 1021405, type = 1, next_id = 102140510, talk_content = "嗯，所以要调查为什么反重力区会变成它们巢穴的这件事。", talk_name = "守卫",},
	[102140510] = { talk_id = 102140510, group_id = 1021405, type = 1, next_id = 102140511, talk_content = "噢，是想要调查原因然后好把噬脉生物赶出去是吧？", talk_name = "司命",},
	[102140511] = { talk_id = 102140511, group_id = 1021405, type = 1, next_id = 102140512, talk_content = "是的，毕竟噬脉生物聚起来就会袭击城镇，实在是太麻烦了。", talk_name = "守卫",},
	[102140512] = { talk_id = 102140512, group_id = 1021405, type = 1, next_id = 102140513, talk_content = "闲话就谈到这里吧，我们得赶紧追上来来。", talk_name = "叙慕",},
	[102140513] = { talk_id = 102140513, group_id = 1021405, type = 1, next_id = 102140514, talk_content = "也是，所以调查队是往哪里去了？", talk_name = "司命",},
	[102140514] = { talk_id = 102140514, group_id = 1021405, type = 1, next_id = 102140515, talk_content = "往山地的方向，来来她也朝着那边去了。", talk_name = "守卫",},
	[102140515] = { talk_id = 102140515, group_id = 1021405, type = 1, talk_content = "走吧。", talk_name = "叙慕",},
	[102140601] = { talk_id = 102140601, group_id = 1021406, type = 4, next_id = 102140602, aside = "<color=#FFEBA2>司命：</color>既然这个人不知道那就问下一个吧，我看那边的就不错。", talk_content = "既然这个人不知道那就问下一个吧，我看那边的就不错。", talk_name = "司命",},
	[102140602] = { talk_id = 102140602, group_id = 1021406, type = 4, aside = "<color=#FFEBA2>叙慕：</color>你只是单纯觉得那边店面比较大而已吧。", talk_content = "你只是单纯觉得那边店面比较大而已吧。", talk_name = "叙慕",},
	[102150101] = { talk_id = 102150101, group_id = 1021501, type = 4, next_id = 102150102, aside = "<color=#FFEBA2>司命：</color>除了那个熙来以外其他地方都是自然环境呢，看来这里的人真不住在地上。", talk_content = "除了那个熙来以外其他地方都是自然环境呢，看来这里的人真不住在地上。", talk_name = "司命",},
	[102150102] = { talk_id = 102150102, group_id = 1021501, type = 4, next_id = 102150103, aside = "<color=#FFEBA2>叙慕：</color>但是倒过来生活真的可以吗？", talk_content = "但是倒过来生活真的可以吗？", talk_name = "叙慕",},
	[102150103] = { talk_id = 102150103, group_id = 1021501, type = 4, next_id = 102150104, aside = "<color=#FFEBA2>司命：</color>应该没什么问题吧，他们不也活几百年了？", talk_content = "应该没什么问题吧，他们不也活几百年了？", talk_name = "司命",},
	[102150104] = { talk_id = 102150104, group_id = 1021501, type = 4, aside = "<color=#FFEBA2>叙慕：</color>不敢想象。", talk_content = "不敢想象。", talk_name = "叙慕",},
	[102150201] = { talk_id = 102150201, group_id = 1021502, type = 4, next_id = 102150202, aside = "<color=#FFEBA2>叙慕：</color>话说回来，离歌……悬天是什么样的组织？", talk_content = "话说回来，离歌……悬天是什么样的组织？", talk_name = "叙慕",},
	[102150202] = { talk_id = 102150202, group_id = 1021502, type = 4, next_id = 102150203, aside = "<color=#FFEBA2>司命：</color>这时候问起这个吗？", talk_content = "这时候问起这个吗？", talk_name = "司命",},
	[102150203] = { talk_id = 102150203, group_id = 1021502, type = 4, next_id = 102150204, aside = "<color=#FFEBA2>叙慕：</color>我有一堆问题，只是都被你用”时机“躲过去了。", talk_content = "我有一堆问题，只是都被你用”时机“躲过去了。", talk_name = "叙慕",},
	[102150204] = { talk_id = 102150204, group_id = 1021502, type = 4, next_id = 102150205, aside = "<color=#FFEBA2>司命：</color>那这次也让我这么躲过去吧。", talk_content = "那这次也让我这么躲过去吧。", talk_name = "司命",},
	[102150205] = { talk_id = 102150205, group_id = 1021502, type = 4, next_id = 102150206, aside = "<color=#FFEBA2>叙慕：</color>喂。", talk_content = "喂。", talk_name = "叙慕",},
	[102150206] = { talk_id = 102150206, group_id = 1021502, type = 4, aside = "<color=#FFEBA2>司命：</color>只要进了城，你不想知道也不行。", talk_content = "只要进了城，你不想知道也不行。", talk_name = "司命",},
	[102150301] = { talk_id = 102150301, group_id = 1021503, type = 1, next_id = 102150302, hide_player = true, talk_content = "走到这里也没看见人，是我中途走错了吗？", talk_name = "叙慕",},
	[102150302] = { talk_id = 102150302, group_id = 1021503, type = 1, next_id = 102150303, talk_content = "或者说守卫给我们指了错路。", talk_name = "司命",},
	[102150303] = { talk_id = 102150303, group_id = 1021503, type = 1, next_id = 102150304, talk_content = "他没有骗我们的理由，往别的方向走吧，司命。", talk_name = "叙慕",},
	[102150304] = { talk_id = 102150304, group_id = 1021503, type = 2, options = {{"的确",102150305,},{"我不觉得",102150306,},}, talk_content = "等等，你就没有看见眼前那个如此吸人眼球的空洞吗？", talk_name = "司命",},
	[102150305] = { talk_id = 102150305, group_id = 1021503, type = 3, next_id = 102150307, talk_content = "对吧对吧，在荒郊野岭突然有扇像是经人修饰，诡异无比大门，很勾人心弦对吧。", talk_name = "司命",},
	[102150306] = { talk_id = 102150306, group_id = 1021503, type = 3, next_id = 102150307, talk_content = "你这个没情趣的家伙，就不明白这其中到底蕴含着什么浪漫吗？", talk_name = "司命",},
	[102150307] = { talk_id = 102150307, group_id = 1021503, type = 1, next_id = 102150308, talk_content = "所以这东西到底是什么，感觉靠近这玩意以后你连说话的语气都恶心了好多倍。", talk_name = "叙慕",},
	[102150308] = { talk_id = 102150308, group_id = 1021503, type = 1, next_id = 102150309, talk_content = "哼哼，这个叫作空洞，是世界的缺陷。", talk_name = "司命",},
	[102150309] = { talk_id = 102150309, group_id = 1021503, type = 1, next_id = 102150310, talk_content = "何为缺憾这点我稍后再作说明，但一般来说在空洞里面可以搞到不少好东西。", talk_name = "司命",},
	[102150310] = { talk_id = 102150310, group_id = 1021503, type = 1, next_id = 102150311, talk_content = "所以你的目的是里面的宝物吗？", talk_name = "叙慕",},
	[102150311] = { talk_id = 102150311, group_id = 1021503, type = 1, next_id = 102150312, talk_content = "有时候是宝物，有时候是其他东西，不过那里面肯定有不少好玩意。", talk_name = "司命",},
	[102150312] = { talk_id = 102150312, group_id = 1021503, type = 1, next_id = 102150313, talk_content = "我觉得你应该改名叫守财奴之类的比较好……", talk_name = "叙慕",},
	[102150313] = { talk_id = 102150313, group_id = 1021503, type = 2, options = {{"……就上一次你的当吧。",102150314,},{"先等一下。",102150315,},}, talk_content = "反正人大概我们也找不到了，要不要进去看看？", talk_name = "司命",},
	[102150314] = { talk_id = 102150314, group_id = 1021503, type = 3, talk_content = "嘻嘻，向空洞出发！", talk_name = "司命",},
	[102150315] = { talk_id = 102150315, group_id = 1021503, type = 3, talk_content = "要继续找人是可以还是想先做点准备，对我来说也没差就是了。", talk_name = "司命",},
	[102150401] = { talk_id = 102150401, group_id = 1021504, type = 2, options = {{"……就上一次你的当吧。",102150402,},{"我拒绝。",102150403,},}, talk_content = "要进空洞对吧，要进空洞对吧！", talk_name = "司命",},
	[102150402] = { talk_id = 102150402, group_id = 1021504, type = 3, talk_content = "嘻嘻，向空洞出发！", talk_name = "司命",},
	[102150403] = { talk_id = 102150403, group_id = 1021504, type = 3, talk_content = "要继续找人是可以还是想先做点准备，对我来说也没差就是了。", talk_name = "司命",},
	[102160101] = { talk_id = 102160101, group_id = 1021601, type = 1, next_id = 102160102, hide_all_entity = true, hide_player = true, talk_content = "怎样，我没骗你吧，不过我也没想到有人会在空洞里面学会新技能呢。", talk_name = "司命",},
	[102160102] = { talk_id = 102160102, group_id = 1021601, type = 1, next_id = 102160103, talk_content = "不是学会，而是回忆了……那应该是我以前用过的招式。", talk_name = "叙慕",},
	[102160103] = { talk_id = 102160103, group_id = 1021601, type = 1, next_id = 102160104, talk_content = "司命，你说过这个地方是世界的缺陷是吧。", talk_name = "叙慕",},
	[102160104] = { talk_id = 102160104, group_id = 1021601, type = 1, next_id = 102160105, talk_content = "嗯，这个地方的构成，景色，对于现金世界来说都是被废弃的东西。", talk_name = "司命",},
	[102160105] = { talk_id = 102160105, group_id = 1021601, type = 1, next_id = 102160106, talk_content = "是谁把东西丢进了空洞的意思吗？", talk_name = "叙慕",},
	[102160106] = { talk_id = 102160106, group_id = 1021601, type = 1, next_id = 102160107, talk_content = "是谁我就不知道了，怎么一脸凝重，你是又想起什么了吗？", talk_name = "司命",},
	[102160107] = { talk_id = 102160107, group_id = 1021601, type = 1, talk_content = "不，只是我觉得自己应该认识这个地方。", talk_name = "叙慕",},
	[102170101] = { talk_id = 102170101, group_id = 1021701, type = 1, next_id = 102170102, hide_all_entity = true, hide_player = true, talk_content = "本以为这个地方会暗藏危机，但实际上要比想象温和很多。", talk_name = "叙慕",},
	[102170102] = { talk_id = 102170102, group_id = 1021701, type = 1, next_id = 102170103, talk_content = "空洞是会随着各种因素变化的，刚好这个是比较温和那类而已。", talk_name = "司命",},
	[102170103] = { talk_id = 102170103, group_id = 1021701, type = 1, next_id = 102170104, talk_content = "看着也没有敌人了，我们离开这里吧。", talk_name = "司命",},
	[102170104] = { talk_id = 102170104, group_id = 1021701, type = 1, next_id = 102170105, talk_content = "等等。", talk_name = "叙慕",},
	[102170105] = { talk_id = 102170105, group_id = 1021701, type = 1, next_id = 102170106, talk_content = "？", talk_name = "司命",},
	[102170106] = { talk_id = 102170106, group_id = 1021701, type = 1, next_id = 102170107, talk_content = "我认为是合适的地点和时机，司命。", talk_name = "叙慕",},
	[102170107] = { talk_id = 102170107, group_id = 1021701, type = 1, next_id = 102170108, talk_content = "是要说太阳的事情对吧，明明对拯救世界没什么兴趣。", talk_name = "司命",},
	[102170108] = { talk_id = 102170108, group_id = 1021701, type = 1, next_id = 102170109, talk_content = "无论要不要拯救世界，那些人口中和你描述的就不一样。", talk_name = "叙慕",},
	[102170109] = { talk_id = 102170109, group_id = 1021701, type = 1, next_id = 102170110, talk_content = "你说太阳是祸害世界之物，我在看见那副景象的时候也是这么想的。", talk_name = "叙慕",},
	[102170110] = { talk_id = 102170110, group_id = 1021701, type = 1, next_id = 102170111, talk_content = "可实际上，这里的人却崇敬太阳，而太阳也拯救过这些人，而他们的神情也并不是说谎者该有的样貌。", talk_name = "叙慕",},
	[102170111] = { talk_id = 102170111, group_id = 1021701, type = 1, next_id = 102170112, talk_content = "哦呵，所以你在怀疑我罗？", talk_name = "司命",},
	[102170112] = { talk_id = 102170112, group_id = 1021701, type = 1, next_id = 102170113, talk_content = "怀疑这个把一半生命分给了你的我？", talk_name = "司命",},
	[102170113] = { talk_id = 102170113, group_id = 1021701, type = 1, next_id = 102170114, talk_content = "所以是如何，我说过我虽失去了记忆，但绝对不会被人摆布，假若你真是骗子，那这条命在这里还给你也没所谓。", talk_name = "叙慕",},
	[102170114] = { talk_id = 102170114, group_id = 1021701, type = 1, next_id = 102170115, talk_content = "……", talk_name = "叙慕",},
	[102170115] = { talk_id = 102170115, group_id = 1021701, type = 1, next_id = 102170116, talk_content = "别用那种眼神看着我，这不是搞得我跟个反派似的。", talk_name = "司命",},
	[102170116] = { talk_id = 102170116, group_id = 1021701, type = 1, next_id = 102170117, talk_content = "……", talk_name = "叙慕",},
	[102170117] = { talk_id = 102170117, group_id = 1021701, type = 1, next_id = 102170118, talk_content = "哎，我知道了。", talk_name = "司命",},
	[102170118] = { talk_id = 102170118, group_id = 1021701, type = 1, next_id = 102170119, talk_content = "我不了解人类历史中太阳到底都做了些什么，但是我可没有说谎。", talk_name = "司命",},
	[102170119] = { talk_id = 102170119, group_id = 1021701, type = 1, next_id = 102170120, talk_content = "我从诞生之初就知道太阳是为恶之物，恐怕那个司长说过的反重力云云的东西也是那个太阳造成的影响。", talk_name = "司命",},
	[102170120] = { talk_id = 102170120, group_id = 1021701, type = 1, next_id = 102170121, talk_content = "就不能是自然灾害吗，那个太阳升起已经数百年了吧。", talk_name = "叙慕",},
	[102170121] = { talk_id = 102170121, group_id = 1021701, type = 1, next_id = 102170122, talk_content = "你要这么说那我也没办法反驳，但就算如此我的天命都不会改变。", talk_name = "司命",},
	[102170122] = { talk_id = 102170122, group_id = 1021701, type = 1, next_id = 102170123, talk_content = "在这个世界之中，每个人都有每个人的天命，青乌二字是你的天命，所以你在失去记忆以后仍然记得。", talk_name = "司命",},
	[102170123] = { talk_id = 102170123, group_id = 1021701, type = 1, next_id = 102170124, talk_content = "而我的天命，就是不顾一切去协助你拯救世界。", talk_name = "司命",},
	[102170124] = { talk_id = 102170124, group_id = 1021701, type = 1, next_id = 102170125, talk_content = "虽然至今为止的表现可能不值得你信任，但假若真到那个时候的话，我把剩下的半条命也给你都可以。", talk_name = "司命",},
	[102170125] = { talk_id = 102170125, group_id = 1021701, type = 1, next_id = 102170126, talk_content = "我没有让你把剩下的半条命给我，而是想要知道你所说的是否真实。", talk_name = "叙慕",},
	[102170126] = { talk_id = 102170126, group_id = 1021701, type = 1, next_id = 102170127, talk_content = "是真的，我刚刚这么说就代表我愿意用性命去担保。", talk_name = "司命",},
	[102170127] = { talk_id = 102170127, group_id = 1021701, type = 2, options = {{"（相信他。）",102170128,},{"（保留意见。）",102170135,},}, talk_content = "太阳，以及太阳带来的恩惠……绝对是危害世界之物。", talk_name = "司命",},
	[102170128] = { talk_id = 102170128, group_id = 1021701, type = 3, next_id = 102170129, talk_content = "……一开始这么说不就好了，诚实才是最大的武器，没人跟你说过这件事吗？", talk_name = "叙慕",},
	[102170129] = { talk_id = 102170129, group_id = 1021701, type = 3, next_id = 102170130, talk_content = "这不符合我的性格，再者一上来大条道理也没用，你要寻找记忆，而我能给你自由活动的生命。", talk_name = "司命",},
	[102170130] = { talk_id = 102170130, group_id = 1021701, type = 3, next_id = 102170131, talk_content = "我认为最初只要维持这样的利益关系就足够了。", talk_name = "司命",},
	[102170131] = { talk_id = 102170131, group_id = 1021701, type = 3, next_id = 102170132, talk_content = "你这个别扭精。", talk_name = "叙慕",},
	[102170132] = { talk_id = 102170132, group_id = 1021701, type = 3, next_id = 102170133, talk_content = "话都谈完了就走吧，不然等下空洞门关上就没法跑了。", talk_name = "司命",},
	[102170133] = { talk_id = 102170133, group_id = 1021701, type = 3, next_id = 102170134, talk_content = "真会关上吗？", talk_name = "叙慕",},
	[102170134] = { talk_id = 102170134, group_id = 1021701, type = 3, talk_content = "不知道捏。", talk_name = "司命",},
	[102170135] = { talk_id = 102170135, group_id = 1021701, type = 3, next_id = 102170136, talk_content = "虽然你这个告白的确有些出乎意料，但口说无凭……", talk_name = "叙慕",},
	[102170136] = { talk_id = 102170136, group_id = 1021701, type = 3, next_id = 102170137, talk_content = "你先听着就好，到时候你自然会明白我才是正确的。", talk_name = "司命",},
	[102170137] = { talk_id = 102170137, group_id = 1021701, type = 3, next_id = 102170138, talk_content = "话都谈完了就走吧，不然等下空洞门关上就没法跑了。", talk_name = "司命",},
	[102170138] = { talk_id = 102170138, group_id = 1021701, type = 3, next_id = 102170139, talk_content = "真会关上吗？", talk_name = "叙慕",},
	[102170139] = { talk_id = 102170139, group_id = 1021701, type = 3, talk_content = "不知道捏。", talk_name = "司命",},
	[102180101] = { talk_id = 102180101, group_id = 1021801, type = 1, next_id = 102180102, talk_content = "你好你好，你们刚刚从空洞回来是吧？", talk_name = "？？？",},
	[102180102] = { talk_id = 102180102, group_id = 1021801, type = 1, next_id = 102180103, talk_content = "你是……还有你是怎么在我耳边说话的？", talk_name = "叙慕",},
	[102180103] = { talk_id = 102180103, group_id = 1021801, type = 1, next_id = 102180104, talk_content = "只是有点小手段而已，对了，你叫我阿一就好。", talk_name = "阿一",},
	[102180104] = { talk_id = 102180104, group_id = 1021801, type = 1, next_id = 102180105, talk_content = "这名字和阿猫阿狗听上去差不多呢。", talk_name = "司命",},
	[102180105] = { talk_id = 102180105, group_id = 1021801, type = 1, next_id = 102180106, talk_content = "哈哈哈，我这名字可是由尊贵的……咳咳，这个先不提了。", talk_name = "阿一",},
	[102180106] = { talk_id = 102180106, group_id = 1021801, type = 1, next_id = 102180107, talk_content = "总之你们先回城里一趟吧，我手上有能帮到你们的东西噢。", talk_name = "阿一",},
	[102180107] = { talk_id = 102180107, group_id = 1021801, type = 1, next_id = 102180108, talk_content = "要去吗？", talk_name = "司命",},
	[102180108] = { talk_id = 102180108, group_id = 1021801, type = 1, talk_content = "至少现在找人这个委托大概是落空了，顺道回去做个报告吧。", talk_name = "叙慕",},
	[102180201] = { talk_id = 102180201, group_id = 1021802, type = 5, next_id = 102180202, aside = "<color=#FFEBA2>？？？：</color>我刚见到你们的时候就知道你们并不简单。", hide_all_entity = true, hide_player = true, talk_content = "我刚见到你们的时候就知道你们并不简单。", talk_name = "？？？",},
	[102180202] = { talk_id = 102180202, group_id = 1021802, type = 5, next_id = 102180203, aside = "<color=#FFEBA2>？？？：</color>只是没想到竟然能只靠一人就从空洞中全身而退。", talk_content = "只是没想到竟然能只靠一人就从空洞中全身而退。", talk_name = "？？？",},
	[102180203] = { talk_id = 102180203, group_id = 1021802, type = 5, next_id = 102180204, aside = "<color=#FFEBA2>司命：</color>青乌，那个女的不就是！", talk_content = "青乌，那个女的不就是！", talk_name = "司命",},
	[102180204] = { talk_id = 102180204, group_id = 1021802, type = 5, next_id = 102180205, aside = "<color=#FFEBA2>叙慕：</color>嗯。", talk_content = "嗯。", talk_name = "叙慕",},
	[102180205] = { talk_id = 102180205, group_id = 1021802, type = 5, aside = "<color=#FFEBA2>来来：</color>我的名字叫来来，时时来来的来来，我听说你们在找我对吧。", talk_content = "我的名字叫来来，时时来来的来来，我听说你们在找我对吧。", talk_name = "来来",},
	[102180301] = { talk_id = 102180301, group_id = 1021803, type = 1, next_id = 102180302, hide_all_entity = true, hide_player = true, talk_content = "我们的确是在找你，但是你为什么会在城里面，不是追着调查队去了吗？", talk_name = "司命",},
	[102180302] = { talk_id = 102180302, group_id = 1021803, type = 1, next_id = 102180303, talk_content = "因为发现追不上所以就放弃了，相反地我找到了另外的能用来立功的东西。", talk_name = "来来",},
	[102180303] = { talk_id = 102180303, group_id = 1021803, type = 1, next_id = 102180304, talk_content = "立功的东西？", talk_name = "司命",},
	[102180304] = { talk_id = 102180304, group_id = 1021803, type = 1, next_id = 102180305, talk_content = "新的反重力区，里面似乎已已经有噬脉生物盘踞了。", talk_name = "来来",},
	[102180305] = { talk_id = 102180305, group_id = 1021803, type = 1, next_id = 102180306, talk_content = "说到底反重力区也不是滋生噬脉生物的地方，只是容易被当成巢穴而已，无论有没有那个，噬脉生物聚起来以后还是会袭击我们居住的地方。", talk_name = "来来",},
	[102180306] = { talk_id = 102180306, group_id = 1021803, type = 2, options = {{"的确",102180307,},{"有点治标不治本",102180308,},}, talk_content = "那么与其费尽心思查明真相，还不如直接让我们这样的脉者扫荡反重力区要轻松一些吧。", talk_name = "来来",},
	[102180307] = { talk_id = 102180307, group_id = 1021803, type = 3, next_id = 102180309, talk_content = "我就知道你会同意的，那走吧。", talk_name = "来来",},
	[102180308] = { talk_id = 102180308, group_id = 1021803, type = 3, next_id = 102180309, talk_content = "能治不就行了，我们走吧！", talk_name = "来来",},
	[102180309] = { talk_id = 102180309, group_id = 1021803, type = 1, next_id = 102180310, talk_content = "等等，走去哪？", talk_name = "司命",},
	[102180310] = { talk_id = 102180310, group_id = 1021803, type = 1, next_id = 102180311, talk_content = "那当然是那个反重力区了，你们想要去天月城对吧。", talk_name = "来来",},
	[102180311] = { talk_id = 102180311, group_id = 1021803, type = 1, next_id = 102180312, talk_content = "我们是想去，但这有什么联系？", talk_name = "司命",},
	[102180312] = { talk_id = 102180312, group_id = 1021803, type = 1, next_id = 102180313, talk_content = "上面的人说是解除危机后就会重新开通天月城的出入境，与其等那些调查队寻找不知道是啥的东西，你不觉得自己动手来的更快吗？", talk_name = "来来",},
	[102180313] = { talk_id = 102180313, group_id = 1021803, type = 1, next_id = 102180314, talk_content = "……但是我接到的委托是带你回去。", talk_name = "叙慕",},
	[102180314] = { talk_id = 102180314, group_id = 1021803, type = 1, next_id = 102180315, talk_content = "我可不会跟你回去就是了，我需要尽快立功升职好回天月城，而你也想要到天月城去。", talk_name = "来来",},
	[102180315] = { talk_id = 102180315, group_id = 1021803, type = 1, next_id = 102180316, talk_content = "我认为我们应该可以合作才是。", talk_name = "来来",},
	[102180316] = { talk_id = 102180316, group_id = 1021803, type = 1, next_id = 102180317, talk_content = "要合作的话我们还不如找司长，他还能帮忙插队呢。", talk_name = "司命",},
	[102180317] = { talk_id = 102180317, group_id = 1021803, type = 1, next_id = 102180318, talk_content = "……呃，你这么说也是。", talk_name = "来来",},
	[102180318] = { talk_id = 102180318, group_id = 1021803, type = 1, next_id = 102180319, talk_content = "真被司命驳倒了？", talk_name = "叙慕",},
	[102180319] = { talk_id = 102180319, group_id = 1021803, type = 1, next_id = 102180320, talk_content = "没办法，那在这之上我就再加点筹码……和你们签个”相契“如何？", talk_name = "来来",},
	[102180320] = { talk_id = 102180320, group_id = 1021803, type = 1, next_id = 102180321, talk_content = "相契？", talk_name = "叙慕",},
	[102180321] = { talk_id = 102180321, group_id = 1021803, type = 1, next_id = 102180322, talk_content = "是一种以气脉为媒介的特殊契约，在一些重要的事件上都会用这个来做公证，我也见过人类之间签订这种契约的情景。", talk_name = "司命",},
	[102180322] = { talk_id = 102180322, group_id = 1021803, type = 1, next_id = 102180323, talk_content = "所以是合约一类的东西……那合约的内容是什么？", talk_name = "叙慕",},
	[102180323] = { talk_id = 102180323, group_id = 1021803, type = 1, next_id = 102180324, talk_content = "自然是互相帮助了，我看你们就一副会惹事上身的感觉，应该有不少时候都需要别人帮忙吧。", talk_name = "来来",},
	[102180324] = { talk_id = 102180324, group_id = 1021803, type = 1, next_id = 102180325, talk_content = "的确……在拯救世界的路上多几个伙伴也不是坏事。", talk_name = "司命",},
	[102180325] = { talk_id = 102180325, group_id = 1021803, type = 1, next_id = 102180326, talk_content = "我还没有说自己要拯救世界。", talk_name = "叙慕",},
	[102180326] = { talk_id = 102180326, group_id = 1021803, type = 1, next_id = 102180327, talk_content = "所以怎么说，要立契吗？", talk_name = "来来",},
	[102180327] = { talk_id = 102180327, group_id = 1021803, type = 1, next_id = 102180328, talk_content = "不过的确，考虑到我的状况，有时候多一个帮手并不是坏事。", talk_name = "叙慕",},
	[102180328] = { talk_id = 102180328, group_id = 1021803, type = 1, talk_content = "很好。", talk_name = "来来",},
	[102190101] = { talk_id = 102190101, group_id = 1021901, type = 4, next_id = 102190102, aside = "<color=#FFEBA2>来来：</color>对了，说了这么久都忘记问了。", talk_content = "对了，说了这么久都忘记问了。", talk_name = "来来",},
