	[102200305] = { talk_id = 102200305, group_id = 1022003, type = 5, aside = "<color=#FFEBA2>青乌：</color>这个世界到底发生了什么……", talk_content = "这个世界到底发生了什么……", talk_name = "青乌",},
	[102200401] = { talk_id = 102200401, group_id = 1022004, type = 1, next_id = 102200402, fade_out = true, hide_player = true, talk_content = "太阳升到了天上……我想要阻止的应该是这件事来着。", talk_name = "青乌",},
	[102200402] = { talk_id = 102200402, group_id = 1022004, type = 1, next_id = 102200403, talk_content = "是我失败了吗，还是……", talk_name = "青乌",},
	[102200403] = { talk_id = 102200403, group_id = 1022004, type = 1, next_id = 102200404, talk_content = "敲响钟悬……有人这么和我说过。", talk_name = "青乌",},
	[102200404] = { talk_id = 102200404, group_id = 1022004, type = 1, next_id = 102200405, talk_content = "但地上也有怪物……", talk_name = "青乌",},
	[102200405] = { talk_id = 102200405, group_id = 1022004, type = 1, talk_content = "试试用从离歌那里拿到的“佩玉”吧。", talk_name = "青乌",},
	[102200501] = { talk_id = 102200501, group_id = 1022005, type = 4, next_id = 102200502, aside = "<color=#FFEBA2>青乌：</color>佩从的用法的确是这样来着。", talk_content = "佩从的用法的确是这样来着。", talk_name = "青乌",},
	[102200502] = { talk_id = 102200502, group_id = 1022005, type = 4, aside = "<color=#FFEBA2>青乌：</color>不过……总觉得哪里有些奇怪。", talk_content = "不过……总觉得哪里有些奇怪。", talk_name = "青乌",},
	[102200601] = { talk_id = 102200601, group_id = 1022006, type = 2, fade_out = true, hide_player = true, options = {{"深思气脉",102200602,},{"深思钟悬",102200607,},}, talk_content = "钟悬是向气脉告拜的装置……", talk_name = "青乌",},
	[102200602] = { talk_id = 102200602, group_id = 1022006, type = 3, next_id = 102200603, talk_content = "气脉是指流淌于整个世界的无形能量，它也会流经生物体内，对流过的生物，土地产生各种影响。", talk_name = "青乌",},
	[102200603] = { talk_id = 102200603, group_id = 1022006, type = 2, options = {{"深思钟悬",102200604,},}, talk_content = "也就是说气脉会确切影响到某地的人和物。", talk_name = "青乌",},
	[102200604] = { talk_id = 102200604, group_id = 1022006, type = 3, next_id = 102200605, talk_content = "有某个已经记不起来的人曾说过要在一个陌生地方活动的话最好就敲响其中全部的钟悬。", talk_name = "青乌",},
	[102200605] = { talk_id = 102200605, group_id = 1022006, type = 3, next_id = 102200606, talk_content = "因为这能够让气脉记得你这个人。", talk_name = "青乌",},
	[102200606] = { talk_id = 102200606, group_id = 1022006, type = 3, next_id = 102200612, talk_content = "虽然无法确认这是否真实，但敲响钟悬以后的确减缓了头昏的状况。", talk_name = "青乌",},
