	[102210501] = { talk_id = 102210501, group_id = 1022105, type = 4, next_id = 102210502, aside = "<color=#FFEBA2>来来：</color>你也是脉者对吧，别光看着，帮我解决一些麻烦！", talk_content = "你也是脉者对吧，别光看着，帮我解决一些麻烦！", talk_name = "来来",},
