	[102210601] = { talk_id = 102210601, group_id = 1022106, type = 1, next_id = 102210602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你还挺有两下子的嘛，我的名字叫来来，时时来来的来来，你是？", talk_name = "来来",},
	[102210602] = { talk_id = 102210602, group_id = 1022106, type = 1, next_id = 102210603, talk_content = "我是……我是……青乌，应该是这么称呼的。", talk_name = "青乌",},
	[102210603] = { talk_id = 102210603, group_id = 1022106, type = 2, options = {{"去哪里？",102210604,},}, talk_content = "应该？不论如何，这下我们就算认识了，然后你能和我一起来对吧？", talk_name = "来来",},
	[102210604] = { talk_id = 102210604, group_id = 1022106, type = 3, next_id = 102210605, talk_content = "去前面的据点恢复熙来的供能，现在能在野外行动自由行动的也就只有我了。", talk_name = "来来",},
	[102210605] = { talk_id = 102210605, group_id = 1022106, type = 3, next_id = 102210606, talk_content = "我看你的脸也知道你应该有很多问题，来做个交易吧，你先帮我解决那个据点，然后我回答你的问题。", talk_name = "来来",},
	[102210606] = { talk_id = 102210606, group_id = 1022106, type = 3, next_id = 102210607, talk_content = "唉，噬脉生物突然对这周围发起了攻击，没防备的地方在一瞬间被占领了，所以我才说天月城应该多派点人下来。", talk_name = "来来",},
	[102210607] = { talk_id = 102210607, group_id = 1022106, type = 3, next_id = 102210608, talk_content = "总之我们出发吧，青乌。", talk_name = "来来",},
	[102210608] = { talk_id = 102210608, group_id = 1022106, type = 3, talk_content = "至少算找到个人了。", talk_name = "青乌",},
	[102210701] = { talk_id = 102210701, group_id = 1022107, type = 5, next_id = 102210702, aside = "<color=#FFEBA2>来来：</color>对了，在一起行动之前应该要互相熟悉作战方式。", fade_in = 1, fade_out = true, talk_content = "对了，在一起行动之前应该要互相熟悉作战方式。", talk_name = "来来",},
	[102210702] = { talk_id = 102210702, group_id = 1022107, type = 5, next_id = 102210703, aside = "<color=#FFEBA2>青乌：</color>如你所见，我用的是剑，擅长近身作战。", talk_content = "如你所见，我用的是剑，擅长近身作战。", talk_name = "青乌",},
	[102210703] = { talk_id = 102210703, group_id = 1022107, type = 5, aside = "<color=#FFEBA2>来来：</color>和我相反呢……刚好有只漏网之鱼。", talk_content = "和我相反呢……刚好有只漏网之鱼。", talk_name = "来来",},
	[102210801] = { talk_id = 102210801, group_id = 1022108, type = 1, next_id = 102210802, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "很好，一击解决。", talk_name = "来来",},
	[102210802] = { talk_id = 102210802, group_id = 1022108, type = 1, next_id = 102210803, talk_content = "能够瞄准并且攻击到天上的敌人就是你的作战方式对吧。", talk_name = "青乌",},
	[102210803] = { talk_id = 102210803, group_id = 1022108, type = 1, next_id = 102210804, talk_content = "还有其他就是了。", talk_name = "来来",},
	[102210804] = { talk_id = 102210804, group_id = 1022108, type = 1, next_id = 102210805, talk_content = "不过司警到底是什么……", talk_name = "青乌",},
	[102210805] = { talk_id = 102210805, group_id = 1022108, type = 1, talk_content = "就是这片土地人民的守护者。", talk_name = "来来",},
	[102210901] = { talk_id = 102210901, group_id = 1022109, type = 4, next_id = 102210902, aside = "<color=#FFEBA2>来来：</color>据点就在右上，不走正门，我们爬到看守亭去吧。", talk_content = "据点就在右上，不走正门，我们爬到看守亭去吧。", talk_name = "来来",},
