	[102211001] = { talk_id = 102211001, group_id = 1022110, type = 1, next_id = 102211002, fade_in = 1, fade_out = true, hide_player = true, talk_content = "这个就是你说的据点是吧？", talk_name = "青乌",},
	[102211002] = { talk_id = 102211002, group_id = 1022110, type = 1, next_id = 102211003, talk_content = "嗯，只要解决里面的噬脉生物就可以恢复熙来的供能了。", talk_name = "来来",},
	[102211003] = { talk_id = 102211003, group_id = 1022110, type = 1, next_id = 102211004, talk_content = "只要消灭怪物就行了？不用做别的吗？", talk_name = "青乌",},
	[102211004] = { talk_id = 102211004, group_id = 1022110, type = 1, talk_content = "据点内的器械应该没有被损坏，只是气脉本身被噬脉生物压制了而已。", talk_name = "来来",},
	[102211101] = { talk_id = 102211101, group_id = 1022111, type = 4, aside = "<color=#FFEBA2>来来：</color>青乌，你在前，我在后，尽快解决敌人吧！", talk_content = "青乌，你在前，我在后，尽快解决敌人吧！", talk_name = "来来",},
