	[102220102] = { talk_id = 102220102, group_id = 1022201, type = 4, aside = "<color=#FFEBA2>来来：</color>青乌，下面！", talk_content = "青乌，下面！", talk_name = "来来",},
	[102220201] = { talk_id = 102220201, group_id = 1022202, type = 5, next_id = 102220202, aside = "<color=#FFEBA2>？？？：</color>我明明说过无数次自己想要轻松愉快的工作……", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我明明说过无数次自己想要轻松愉快的工作……", talk_name = "？？？",},
	[102220202] = { talk_id = 102220202, group_id = 1022202, type = 5, aside = "<color=#FFEBA2>神荼：</color>你也复活了……", talk_content = "你也复活了……", talk_name = "神荼",},
	[102220301] = { talk_id = 102220301, group_id = 1022203, type = 4, next_id = 102220302, aside = "<color=#FFEBA2>青乌：</color>做得好，青乌！", talk_content = "做得好，青乌！", talk_name = "青乌",},
	[102220302] = { talk_id = 102220302, group_id = 1022203, type = 4, next_id = 102220303, aside = "<color=#FFEBA2>来来：</color>虽然不知道那个敌人为啥会退去，但现在只要消灭剩下的喽啰就可以解决问题了！", talk_content = "虽然不知道那个敌人为啥会退去，但现在只要消灭剩下的喽啰就可以解决问题了！", talk_name = "来来",},
	[102220303] = { talk_id = 102220303, group_id = 1022203, type = 4, next_id = 102220304, aside = "<color=#FFEBA2>青乌：</color>还有我的问题没有解决。", talk_content = "还有我的问题没有解决。", talk_name = "青乌",},
	[102220304] = { talk_id = 102220304, group_id = 1022203, type = 4, aside = "<color=#FFEBA2>来来：</color>我可没有忘记！", talk_content = "我可没有忘记！", talk_name = "来来",},
	[102220401] = { talk_id = 102220401, group_id = 1022204, type = 4, aside = "<color=#FFEBA2>来来：</color>大获全胜，这下可是立大功了，青乌，我们进熙来吧。", talk_content = "大获全胜，这下可是立大功了，青乌，我们进熙来吧。", talk_name = "来来",},
	[102220501] = { talk_id = 102220501, group_id = 1022205, type = 1, next_id = 102220502, fade_in = 1, fade_out = true, hide_player = true, talk_content = "这就是我所工作的熙来，如你所见只是个小地方。", talk_name = "来来",},
	[102220502] = { talk_id = 102220502, group_id = 1022205, type = 1, next_id = 102220503, talk_content = "商铺不多，常住人口也不过几百，先说明一下，这个地方只是我暂时的驻留地，我的理想……", talk_name = "来来",},
	[102220503] = { talk_id = 102220503, group_id = 1022205, type = 2, options = {{"不知道。",102220504,},{"我是哪里人……",102220504,},}, talk_content = "啊对了，青乌你是哪里人？", talk_name = "来来",},
	[102220504] = { talk_id = 102220504, group_id = 1022205, type = 3, next_id = 102220505, talk_content = "怎么一脸迷茫的样子？", talk_name = "来来",},
	[102220505] = { talk_id = 102220505, group_id = 1022205, type = 3, talk_content = "也罢，先到维安司去吧，站在路中间聊也不太好呢。", talk_name = "来来",},
	[102220601] = { talk_id = 102220601, group_id = 1022206, type = 1, next_id = 102220602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "司长，我回来了！", talk_name = "来来",},
	[102220602] = { talk_id = 102220602, group_id = 1022206, type = 1, next_id = 102220603, talk_content = "来来，多亏你及时赶回来了，要是再晚一步的话真不知道会发生什么事。", talk_name = "？？？",},
	[102220603] = { talk_id = 102220603, group_id = 1022206, type = 1, next_id = 102220604, talk_content = "也多亏了青乌就是了。", talk_name = "来来",},
	[102220604] = { talk_id = 102220604, group_id = 1022206, type = 1, next_id = 102220605, talk_content = "青乌是你旁边的这位吗，的确，刚刚在外面战斗的时候也有看见。", talk_name = "朱嫦",},
	[102220605] = { talk_id = 102220605, group_id = 1022206, type = 2, options = {{"青乌。",102220606,},}, talk_content = "这位义人，我的名字叫朱嫦，是维安司的司长，也是这个熙来的总负责人，敢问你是？", talk_name = "朱嫦",},
	[102220606] = { talk_id = 102220606, group_id = 1022206, type = 3, next_id = 102220607, talk_content = "虽然冒昧，但我其实想要问你来自哪里，为何来这，以及你有何目的。", talk_name = "朱嫦",},
	[102220607] = { talk_id = 102220607, group_id = 1022206, type = 3, next_id = 102220608, talk_content = "司长，你这是要盘问我的帮手吗？", talk_name = "来来",},
	[102220608] = { talk_id = 102220608, group_id = 1022206, type = 3, next_id = 102220609, talk_content = "我们维安司有义务登记每一位出入这片地区的人，这是手续，所以……", talk_name = "朱嫦",},
	[102220609] = { talk_id = 102220609, group_id = 1022206, type = 3, next_id = 102220610, talk_content = "我不知道我来自哪里，但是后面两者我或许能回答上来。", talk_name = "青乌",},
	[102220610] = { talk_id = 102220610, group_id = 1022206, type = 3, next_id = 102220611, talk_content = "这是怎么回事？", talk_name = "朱嫦",},
	[102220611] = { talk_id = 102220611, group_id = 1022206, type = 3, next_id = 102220612, talk_content = "我也不太清楚，但是我好像除了自己是悬天的一员以及目的是要阻止太阳升起以外就什么都不记得了。", talk_name = "青乌",},
	[102220612] = { talk_id = 102220612, group_id = 1022206, type = 3, next_id = 102220613, talk_content = "先不说悬天……太阳是指在这片土地上漂浮的大型球体没错吧，那个可是维系土地的守护神……", talk_name = "来来",},
	[102220613] = { talk_id = 102220613, group_id = 1022206, type = 3, next_id = 102220614, talk_content = "你阻止那个是要做什么？", talk_name = "来来",},
	[102220614] = { talk_id = 102220614, group_id = 1022206, type = 3, next_id = 102220615, talk_content = "守护神？但我和同伴们为了阻止那个升起付出了许多牺牲，要是太阳升起的话不是会发生很糟糕的事情吗？", talk_name = "青乌",},
	[102220615] = { talk_id = 102220615, group_id = 1022206, type = 3, next_id = 102220616, talk_content = "不，在那之前有个前提……", talk_name = "朱嫦",},
	[102220616] = { talk_id = 102220616, group_id = 1022206, type = 3, next_id = 102220617, talk_content = "前提？", talk_name = "青乌",},
	[102220617] = { talk_id = 102220617, group_id = 1022206, type = 3, next_id = 102220618, talk_content = "太阳是在数百年前升起的伟物，就我看来，你的年龄不可能“阻止”它升起。", talk_name = "朱嫦",},
	[102220618] = { talk_id = 102220618, group_id = 1022206, type = 3, next_id = 102220619, talk_content = "数百年前？", talk_name = "青乌",},
	[102220619] = { talk_id = 102220619, group_id = 1022206, type = 3, next_id = 102220620, talk_content = "（通讯音）",},
	[102220620] = { talk_id = 102220620, group_id = 1022206, type = 3, next_id = 102220621, talk_content = "抱歉，剩下的我们之后再聊，来来你安排一下青乌的落脚地，我要去处理伤员的一些事情。", talk_name = "朱嫦",},
	[102220621] = { talk_id = 102220621, group_id = 1022206, type = 3, next_id = 102220622, talk_content = "是青门隐吗？", talk_name = "来来",},
	[102220622] = { talk_id = 102220622, group_id = 1022206, type = 3, next_id = 102220623, talk_content = "嗯，刚才只有她一个守住正门，现在情况有点危急。", talk_name = "朱嫦",},
	[102220623] = { talk_id = 102220623, group_id = 1022206, type = 3, talk_content = "义人，我们稍后再见。", talk_name = "朱嫦",},
	[102220701] = { talk_id = 102220701, group_id = 1022207, type = 4, aside = "<color=#FFEBA2>来来：</color>闷着也不是事，既然你不记得自己有没有到过这里的话就先去敲一下城里的钟悬如何。", talk_content = "闷着也不是事，既然你不记得自己有没有到过这里的话就先去敲一下城里的钟悬如何。", talk_name = "来来",},
	[102230101] = { talk_id = 102230101, group_id = 1022301, type = 4, next_id = 102230102, aside = "<color=#FFEBA2>来来：</color>青乌，我们接下来去采药吧，得帮伤员们准备点应急品。", talk_content = "青乌，我们接下来去采药吧，得帮伤员们准备点应急品。", talk_name = "来来",},
	[102230102] = { talk_id = 102230102, group_id = 1022301, type = 4, next_id = 102230103, aside = "<color=#FFEBA2>青乌：</color>等等，我现在思绪还……", talk_content = "等等，我现在思绪还……", talk_name = "青乌",},
	[102230103] = { talk_id = 102230103, group_id = 1022301, type = 4, next_id = 102230104, aside = "<color=#FFEBA2>来来：</color>大不了你不就是个失去记忆的几百年前的人，虽然我觉得这不太可能就是了。", talk_content = "大不了你不就是个失去记忆的几百年前的人，虽然我觉得这不太可能就是了。", talk_name = "来来",},
	[102230104] = { talk_id = 102230104, group_id = 1022301, type = 4, next_id = 102230105, aside = "<color=#FFEBA2>青乌：</color>……", talk_content = "……", talk_name = "青乌",},
	[102230105] = { talk_id = 102230105, group_id = 1022301, type = 4, next_id = 102230106, aside = "<color=#FFEBA2>来来：</color>走吧，与其在意几百年前的事情不如我在路上和你说说现在的事情吧。", talk_content = "走吧，与其在意几百年前的事情不如我在路上和你说说现在的事情吧。", talk_name = "来来",},
	[102230106] = { talk_id = 102230106, group_id = 1022301, type = 4, aside = "<color=#FFEBA2>青乌：</color>现在……", talk_content = "现在……", talk_name = "青乌",},
	[102230201] = { talk_id = 102230201, group_id = 1022302, type = 1, next_id = 102230202, fade_in = 1, fade_out = true, hide_player = true, talk_content = "难怪刚见你的时候你就有点不知所措的样子，原来是失去记忆了啊。", talk_name = "来来",},
	[102230202] = { talk_id = 102230202, group_id = 1022302, type = 1, next_id = 102230203, talk_content = "不过在那之前我想确认下，你能记得些什么", talk_name = "来来",},
	[102230203] = { talk_id = 102230203, group_id = 1022302, type = 1, next_id = 102230204, talk_content = "我认为自己应该是记得一些常识的……气脉，钟悬，这些东西我都记得。", talk_name = "青乌",},
	[102230204] = { talk_id = 102230204, group_id = 1022302, type = 1, next_id = 102230205, talk_content = "但是熙来，世界大体的样貌和构成我都不记得了。", talk_name = "青乌",},
	[102230205] = { talk_id = 102230205, group_id = 1022302, type = 1, next_id = 102230206, talk_content = "然后记得的还有自己是悬天的一员，目的是阻止太阳升起。", talk_name = "青乌",},
	[102230206] = { talk_id = 102230206, group_id = 1022302, type = 1, next_id = 102230207, talk_content = "阻止太阳升起你会获得什么好处吗？", talk_name = "来来",},
	[102230207] = { talk_id = 102230207, group_id = 1022302, type = 1, next_id = 102230208, talk_content = "不，我好像是为了保护世界之类的才要阻止太阳的……", talk_name = "青乌",},
	[102230208] = { talk_id = 102230208, group_id = 1022302, type = 1, next_id = 102230209, talk_content = "所以你忘记了最不能忘记的东西啊。", talk_name = "来来",},
	[102230209] = { talk_id = 102230209, group_id = 1022302, type = 1, next_id = 102230210, talk_content = "最不能忘记的？", talk_name = "青乌",},
	[102230210] = { talk_id = 102230210, group_id = 1022302, type = 1, talk_content = "人生的目的。", talk_name = "来来",},
	[102230301] = { talk_id = 102230301, group_id = 1022303, type = 1, next_id = 102230302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "在这一带应该能找到挺不错的药材。", talk_name = "来来",},
	[102230302] = { talk_id = 102230302, group_id = 1022303, type = 1, next_id = 102230303, talk_content = "青乌，这里是个清单，大概每种收集几个吧。", talk_name = "来来",},
	[102230303] = { talk_id = 102230303, group_id = 1022303, type = 1, next_id = 102230304, talk_content = "我认为我分不出哪个是哪个。", talk_name = "青乌",},
	[102230304] = { talk_id = 102230304, group_id = 1022303, type = 1, next_id = 102230305, talk_content = "那觉得有用的都拿上了不就行了。", talk_name = "来来",},
	[102230305] = { talk_id = 102230305, group_id = 1022303, type = 1, next_id = 102230306, talk_content = "不过在那之前……", talk_name = "来来",},
	[102230306] = { talk_id = 102230306, group_id = 1022303, type = 1, talk_content = "得先解决一些麻烦呢。", talk_name = "来来",},
	[102230401] = { talk_id = 102230401, group_id = 1022304, type = 4, aside = "<color=#FFEBA2>来来：</color>接下来就是按照清单来收集药材了。", talk_content = "接下来就是按照清单来收集药材了。", talk_name = "来来",},
	[102230501] = { talk_id = 102230501, group_id = 1022305, type = 1, next_id = 102230502, fade_in = 1, fade_out = true, hide_player = true, talk_content = "几百年前，阻止太阳，以及悬天。", talk_name = "来来",},
	[102230502] = { talk_id = 102230502, group_id = 1022305, type = 1, next_id = 102230503, talk_content = "？", talk_name = "青乌",},
	[102230503] = { talk_id = 102230503, group_id = 1022305, type = 2, options = {{"离歌",102230504,},{"那你呢。",102230505,},}, talk_content = "我在列举关于你这个人的关键词而已，你有什么补充吗？", talk_name = "来来",},
	[102230504] = { talk_id = 102230504, group_id = 1022305, type = 3, next_id = 102230506, talk_content = "听上去是个人名……我大概没听过。", talk_name = "来来",},
	[102230505] = { talk_id = 102230505, group_id = 1022305, type = 3, next_id = 102230506, talk_content = "人上之人，不过现在还是关注到你身上吧。", talk_name = "来来",},
	[102230506] = { talk_id = 102230506, group_id = 1022305, type = 1, next_id = 102230507, talk_content = "虽然你说你自己失忆，但能够记得这么多的话我想大概只是那种轻微失忆而已。", talk_name = "来来",},
	[102230507] = { talk_id = 102230507, group_id = 1022305, type = 1, next_id = 102230508, talk_content = "可老实说我对这个世界没什么印象，一些概念也只是知道而已，完全没有接触过的感觉。", talk_name = "青乌",},
	[102230508] = { talk_id = 102230508, group_id = 1022305, type = 1, next_id = 102230509, talk_content = "那今后开始熟悉就是了，再说你的关键词里面藏着一个重大的线索。", talk_name = "来来",},
	[102230509] = { talk_id = 102230509, group_id = 1022305, type = 1, next_id = 102230510, talk_content = "是什么？", talk_name = "青乌",},
	[102230510] = { talk_id = 102230510, group_id = 1022305, type = 1, next_id = 102230511, talk_content = "悬天，这可是个很有名的组织。", talk_name = "来来",},
	[102230511] = { talk_id = 102230511, group_id = 1022305, type = 1, next_id = 102230512, talk_content = "等等，你是说你知道悬天在哪吗？", talk_name = "青乌",},
	[102230512] = { talk_id = 102230512, group_id = 1022305, type = 1, next_id = 102230513, talk_content = "天月城……啊，这么说你也不清楚吧，我说的是天上面的那个城市。", talk_name = "来来",},
	[102230513] = { talk_id = 102230513, group_id = 1022305, type = 1, next_id = 102230514, talk_content = "我之前就想问这个了，那个倒过来的城市是真的吗？", talk_name = "青乌",},
	[102230514] = { talk_id = 102230514, group_id = 1022305, type = 1, next_id = 102230515, talk_content = "那当然，我就是在那里出生而且长大的，可惜的是在地上就业。", talk_name = "来来",},
	[102230515] = { talk_id = 102230515, group_id = 1022305, type = 1, next_id = 102230516, talk_content = "说回悬天，他们是世界首屈一指的企业组织，其中一个分部就建在天月城之中。", talk_name = "来来",},
	[102230516] = { talk_id = 102230516, group_id = 1022305, type = 1, next_id = 102230517, talk_content = "假若你真是他们的一员的话，那只要到那边去问问大概就能查到关于你的一切了吧。", talk_name = "来来",},
	[102230517] = { talk_id = 102230517, group_id = 1022305, type = 1, next_id = 102230518, talk_content = "悬天是首屈一指的组织……", talk_name = "青乌",},
	[102230518] = { talk_id = 102230518, group_id = 1022305, type = 2, options = {{"我真是几百年前的人吗？",102230519,},{"得去看看。",102230519,},}, talk_content = "虽然不知道他们的档案馆有没有保存几百年前的信息就是了。", talk_name = "来来",},
	[102230519] = { talk_id = 102230519, group_id = 1022305, type = 3, next_id = 102230520, talk_content = "反正对于你来说这就是能触碰的线索，把这里的工作解决了，好卖个人情给司长还有那个青门小鬼。", talk_name = "来来",},
	[102230520] = { talk_id = 102230520, group_id = 1022305, type = 3, talk_content = "之后办手续去天月城也比较容易哦。", talk_name = "来来",},
	[102230601] = { talk_id = 102230601, group_id = 1022306, type = 4, next_id = 102230602, aside = "<color=#FFEBA2>青乌：</color>所以你让我和你一起采药就是为了我能够到天上去吗？", talk_content = "所以你让我和你一起采药就是为了我能够到天上去吗？", talk_name = "青乌",},
	[102230602] = { talk_id = 102230602, group_id = 1022306, type = 4, next_id = 102230603, aside = "<color=#FFEBA2>来来：</color>哼哼，这就是人上之人的计谋，反正外来者肯定会对天月城感兴趣就是了。", talk_content = "哼哼，这就是人上之人的计谋，反正外来者肯定会对天月城感兴趣就是了。", talk_name = "来来",},
	[102230603] = { talk_id = 102230603, group_id = 1022306, type = 4, next_id = 102230604, aside = "<color=#FFEBA2>青乌：</color>我不否认……但人上之人这个称呼是？", talk_content = "我不否认……但人上之人这个称呼是？", talk_name = "青乌",},
	[102230604] = { talk_id = 102230604, group_id = 1022306, type = 4, aside = "<color=#FFEBA2>来来：</color>我的人生目的。", talk_content = "我的人生目的。", talk_name = "来来",},
	[102230701] = { talk_id = 102230701, group_id = 1022307, type = 4, next_id = 102230702, aside = "<color=#FFEBA2>来来：</color>量应该这些就够了，我们打道回府，青乌。", talk_content = "量应该这些就够了，我们打道回府，青乌。", talk_name = "来来",},
	[102230702] = { talk_id = 102230702, group_id = 1022307, type = 4, next_id = 102230703, aside = "<color=#FFEBA2>来来：</color>你不帮忙拿点吗？", talk_content = "你不帮忙拿点吗？", talk_name = "来来",},
	[102230703] = { talk_id = 102230703, group_id = 1022307, type = 4, aside = "<color=#FFEBA2>青乌：</color>一个负责护卫警戒，一个负责看守货物，这样不是很合理吗？", talk_content = "一个负责护卫警戒，一个负责看守货物，这样不是很合理吗？", talk_name = "青乌",},
	[102230801] = { talk_id = 102230801, group_id = 1022308, type = 4, next_id = 102230802, aside = "<color=#FFEBA2>青乌：</color>接下来到维安司去？", talk_content = "接下来到维安司去？", talk_name = "青乌",},
	[102230802] = { talk_id = 102230802, group_id = 1022308, type = 4, next_id = 102230803, aside = "<color=#FFEBA2>来来：</color>不，接下来要去找个和窑。", talk_content = "不，接下来要去找个和窑。", talk_name = "来来",},
	[102230803] = { talk_id = 102230803, group_id = 1022308, type = 4, next_id = 102230804, aside = "<color=#FFEBA2>青乌：</color>和窑是？", talk_content = "和窑是？", talk_name = "青乌",},
	[102230804] = { talk_id = 102230804, group_id = 1022308, type = 4, aside = "<color=#FFEBA2>来来：</color>去到就知道了，往这边来。", talk_content = "去到就知道了，往这边来。", talk_name = "来来",},
	[102240101] = { talk_id = 102240101, group_id = 1022401, type = 1, next_id = 102240102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "喂，行士甲，我们能借用一下和窑吧。", talk_name = "来来",},
	[102240102] = { talk_id = 102240102, group_id = 1022401, type = 1, next_id = 102240103, talk_content = "我记得我的名字并不是什么行士甲就是了。", talk_name = "行士",},
	[102240103] = { talk_id = 102240103, group_id = 1022401, type = 1, next_id = 102240104, talk_content = "你们这些人全部都把自己叫行士也太难分辨了，加个甲乙丙丁什么的比较好。", talk_name = "来来",},
	[102240104] = { talk_id = 102240104, group_id = 1022401, type = 1, next_id = 102240105, talk_content = "只是对你而言而已……窑本身已经开火，你们想借用的话随意就好。", talk_name = "行士",},
	[102240105] = { talk_id = 102240105, group_id = 1022401, type = 1, next_id = 102240106, talk_content = "所以这是个什么……是要烧制东西吗？", talk_name = "青乌",},
	[102240106] = { talk_id = 102240106, group_id = 1022401, type = 1, next_id = 102240107, talk_content = "知道窑但是不记得”和窑“，不过和窑也是这阵子才开始流行的东西……", talk_name = "来来",},
	[102240107] = { talk_id = 102240107, group_id = 1022401, type = 2, options = {{"试什么？",102240108,},{"你还没跟我解释这个是啥呢。",102240109,},}, talk_content = "也罢，既然是第一次接触的话青乌你就亲自试试如何？", talk_name = "来来",},
	[102240108] = { talk_id = 102240108, group_id = 1022401, type = 3, talk_content = "那当然是试着用和窑啊。", talk_name = "来来",},
	[102240109] = { talk_id = 102240109, group_id = 1022401, type = 3, talk_content = "等你下手你就知道了。", talk_name = "来来",},
	[102240201] = { talk_id = 102240201, group_id = 1022402, type = 4, aside = "<color=#FFEBA2>来来：</color>这次的目标是健体药，就由我手把手教你吧。", talk_content = "这次的目标是健体药，就由我手把手教你吧。", talk_name = "来来",},
	[102240301] = { talk_id = 102240301, group_id = 1022403, type = 4, next_id = 102240302, aside = "<color=#FFEBA2>青乌：</color>应该没有下一步了吧。", talk_content = "应该没有下一步了吧。", talk_name = "青乌",},
	[102240302] = { talk_id = 102240302, group_id = 1022403, type = 4, aside = "<color=#FFEBA2>来来：</color>嗯，赶紧把药给过去顺带和司长谈条件吧。", talk_content = "嗯，赶紧把药给过去顺带和司长谈条件吧。", talk_name = "来来",},
	[102240401] = { talk_id = 102240401, group_id = 1022404, type = 1, next_id = 102240402, fade_in = 1, fade_out = true, hide_player = true, talk_content = "司长，我们……", talk_name = "来来",},
	[102240402] = { talk_id = 102240402, group_id = 1022404, type = 1, next_id = 102240403, talk_content = "唉，刚到外面来就听见那个让人烦躁的声音。", talk_name = "？？？",},
	[102240403] = { talk_id = 102240403, group_id = 1022404, type = 1, next_id = 102240404, talk_content = "呃……青门小鬼你什么时候醒的。", talk_name = "来来",},
	[102240404] = { talk_id = 102240404, group_id = 1022404, type = 1, next_id = 102240405, talk_content = "那必然是刚刚了。", talk_name = "？？？",},
	[102240405] = { talk_id = 102240405, group_id = 1022404, type = 2, options = {{"之前的小聪明是？",102240406,},}, talk_content = "我闻到你身上有些药味……所以你又想搞之前那种小聪明是不是。", talk_name = "？？？",},
	[102240406] = { talk_id = 102240406, group_id = 1022404, type = 3, next_id = 102240407, talk_content = "嗯？这位……我记得了，你是之前替我挡了敌人一招的脉者对吧。", talk_name = "？？？",},
	[102240407] = { talk_id = 102240407, group_id = 1022404, type = 2, options = {{"是什么？",102240408,},}, talk_content = "你好，我的名字是青门隐，青门“除祸”的一员，虽然可能有些刺耳，但我希望您能记住这句忠告。", talk_name = "青门隐",},
	[102240408] = { talk_id = 102240408, group_id = 1022404, type = 3, next_id = 102240409, talk_content = "谨慎交友。", talk_name = "青门隐",},
	[102240409] = { talk_id = 102240409, group_id = 1022404, type = 3, next_id = 102240410, talk_content = "喂……懒得跟你闹了，小鬼，司长和其他人呢，我得把应急的药品拿过去。", talk_name = "来来",},
	[102240410] = { talk_id = 102240410, group_id = 1022404, type = 3, next_id = 102240411, talk_content = "她们现在大概在靠正门的营地，在商量之后的对策，毕竟这次的袭击中有持名者的存在。", talk_name = "青门隐",},
	[102240411] = { talk_id = 102240411, group_id = 1022404, type = 3, next_id = 102240412, talk_content = "……天上为什么还不派人下来。", talk_name = "来来",},
	[102240412] = { talk_id = 102240412, group_id = 1022404, type = 3, next_id = 102240413, talk_content = "你把我放哪里了？", talk_name = "青门隐",},
	[102240413] = { talk_id = 102240413, group_id = 1022404, type = 3, next_id = 102240414, talk_content = "一个差点被人打爆的小鬼可称不上战斗力，不过……", talk_name = "来来",},
	[102240414] = { talk_id = 102240414, group_id = 1022404, type = 3, next_id = 102240415, talk_content = "隐，这家伙现在失了忆，唯一的线索就是要到天月城去，你有办法搞到通行证吗？", talk_name = "来来",},
	[102240415] = { talk_id = 102240415, group_id = 1022404, type = 3, next_id = 102240416, talk_content = "得人恩果千年记，假若这位有需求的话我定会帮忙，但……", talk_name = "青门隐",},
	[102240416] = { talk_id = 102240416, group_id = 1022404, type = 3, next_id = 102240417, talk_content = "你会这么说就代表这位恐怕没有什么能证明身份的东西，常规手段肯定是不行了。", talk_name = "青门隐",},
	[102240417] = { talk_id = 102240417, group_id = 1022404, type = 3, next_id = 102240418, talk_content = "果然还是得拜托司长吗。", talk_name = "来来",},
	[102240418] = { talk_id = 102240418, group_id = 1022404, type = 2, options = {{"搞不到吗？",102240419,},{"就没有其他方法吗？",102240419,},}, talk_content = "即便拜托了最后也应该只会得到一个结果。", talk_name = "青门隐",},
	[102240419] = { talk_id = 102240419, group_id = 1022404, type = 3, next_id = 102240420, talk_content = "方法自然是有的，那就是你能够解决持名者。", talk_name = "青门隐",},
	[102240420] = { talk_id = 102240420, group_id = 1022404, type = 2, options = {{"持名者到底是什么？",102240421,},}, talk_content = "能够解决持名者的流浪脉者的确能用那个事迹来当身份证明。", talk_name = "来来",},
	[102240421] = { talk_id = 102240421, group_id = 1022404, type = 3, next_id = 102240422, talk_content = "就是你在熙来门前碰见的那个拿锤子的家伙，我们会把能说话的噬脉生物叫作持名者。", talk_name = "来来",},
	[102240422] = { talk_id = 102240422, group_id = 1022404, type = 3, next_id = 102240423, talk_content = "那来来你把药拿过去给司长和其他人，我和这位去追查持名者的下落。", talk_name = "青门隐",},
	[102240423] = { talk_id = 102240423, group_id = 1022404, type = 3, next_id = 102240424, talk_content = "喂，小鬼，可别太勉强了。", talk_name = "来来",},
	[102240424] = { talk_id = 102240424, group_id = 1022404, type = 3, talk_content = "……在对谁说话呢。", talk_name = "青门隐",},
	[102240501] = { talk_id = 102240501, group_id = 1022405, type = 4, next_id = 102240502, aside = "<color=#FFEBA2>青门隐：</color>这么说来刚刚忘记问你的称呼了，我听来来她把你叫作青乌是吗？", talk_content = "这么说来刚刚忘记问你的称呼了，我听来来她把你叫作青乌是吗？", talk_name = "青门隐",},
	[102240502] = { talk_id = 102240502, group_id = 1022405, type = 4, next_id = 102240503, aside = "<color=#FFEBA2>青乌：</color>嗯，你也可以这么叫。", talk_content = "嗯，你也可以这么叫。", talk_name = "青乌",},
	[102240503] = { talk_id = 102240503, group_id = 1022405, type = 4, next_id = 102240504, aside = "<color=#FFEBA2>青门隐：</color>等等，青乌……", talk_content = "等等，青乌……", talk_name = "青门隐",},
	[102240504] = { talk_id = 102240504, group_id = 1022405, type = 4, next_id = 102240505, aside = "<color=#FFEBA2>青乌：</color>你认识我吗？", talk_content = "你认识我吗？", talk_name = "青乌",},
	[102240505] = { talk_id = 102240505, group_id = 1022405, type = 4, next_id = 102240506, aside = "<color=#FFEBA2>青门隐：</color>……不认识，但是我总觉得在哪里听说过这个名字。", talk_content = "……不认识，但是我总觉得在哪里听说过这个名字。", talk_name = "青门隐",},
	[102240506] = { talk_id = 102240506, group_id = 1022405, type = 4, aside = "<color=#FFEBA2>青门隐：</color>是哪里呢……", talk_content = "是哪里呢……", talk_name = "青门隐",},
	[102250101] = { talk_id = 102250101, group_id = 1022501, type = 1, next_id = 102250102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "司长，我和青乌把药搞好了。", talk_name = "来来",},
	[102250102] = { talk_id = 102250102, group_id = 1022501, type = 1, next_id = 102250103, talk_content = "刚才一直找不到你，原来去做药了吗……对了，也谢谢你呢，青乌。", talk_name = "朱嫦",},
	[102250103] = { talk_id = 102250103, group_id = 1022501, type = 1, next_id = 102250104, talk_content = "所以青门的小鬼现在怎么样了？", talk_name = "来来",},
	[102250104] = { talk_id = 102250104, group_id = 1022501, type = 1, next_id = 102250105, talk_content = "力量用过度了……再说你不能称呼主祝大人为小鬼，懂吗。", talk_name = "朱嫦",},
	[102250105] = { talk_id = 102250105, group_id = 1022501, type = 2, options = {{"你之前也提到过青门……",102250106,},{"受伤的只有一个人吗？",102250107,},}, talk_content = "哼，我看是运动过度才对吧，那人刚来的第一天我就觉得她弱不禁风的。", talk_name = "来来",},
	[102250106] = { talk_id = 102250106, group_id = 1022501, type = 3, next_id = 102250108, talk_content = "这么说来我都忘了……你在熙来门前保护的那个脉者名叫青门隐，是天月城来的祝士。", talk_name = "来来",},
	[102250107] = { talk_id = 102250107, group_id = 1022501, type = 3, next_id = 102250108, talk_content = "较严重的就只有主祝大人，其他人都受了些皮外伤，我本来也没料想到竟然能靠这点人就守住熙来……", talk_name = "朱嫦",},
	[102250108] = { talk_id = 102250108, group_id = 1022501, type = 1, next_id = 102250109, talk_content = "话说回来，司长，能不能帮青乌办个上天用的身份证明？", talk_name = "来来",},
	[102250109] = { talk_id = 102250109, group_id = 1022501, type = 1, next_id = 102250110, talk_content = "就因为青乌帮我们筹办的医疗药物？", talk_name = "朱嫦",},
	[102250110] = { talk_id = 102250110, group_id = 1022501, type = 1, next_id = 102250111, talk_content = "就因为青乌帮我们筹办的医疗药物。", talk_name = "来来",},
	[102250111] = { talk_id = 102250111, group_id = 1022501, type = 2, options = {{"不能给我办吗？",102250112,},{"难道要钱吗？",102250113,},}, talk_content = "唉，来这里的人十有八九肯定都是要借道去天月城的，我也很乐意为青乌办个身份证明，但……", talk_name = "朱嫦",},
	[102250112] = { talk_id = 102250112, group_id = 1022501, type = 3, next_id = 102250114, talk_content = "抱歉，现在上面下了命令，在地上的噬脉灾害解决之前不能发新的身份证明。", talk_name = "朱嫦",},
	[102250113] = { talk_id = 102250113, group_id = 1022501, type = 3, next_id = 102250114, talk_content = "脉者的身份办理是免费的，现在的问题其实是在噬脉灾害解决之前不能发新的身份证明。", talk_name = "朱嫦",},
	[102250114] = { talk_id = 102250114, group_id = 1022501, type = 1, next_id = 102250115, talk_content = "噬脉灾害……听上去像是跟噬脉生物有什么关系。", talk_name = "青乌",},
	[102250115] = { talk_id = 102250115, group_id = 1022501, type = 2, options = {{"持名者？",102250116,},}, talk_content = "嗯，每次噬脉灾害都有可能影响环境和滋生噬脉生物，这次甚至还出现了持名者。", talk_name = "来来",},
	[102250116] = { talk_id = 102250116, group_id = 1022501, type = 3, next_id = 102250117, talk_content = "就是你在熙来门前碰见的那个拿锤子的家伙，我们会把能说话的噬脉生物叫作持名者。", talk_name = "来来",},
	[102250117] = { talk_id = 102250117, group_id = 1022501, type = 1, next_id = 102250118, talk_content = "对了，司长，让青乌也来帮忙怎么样？", talk_name = "来来",},
	[102250118] = { talk_id = 102250118, group_id = 1022501, type = 1, next_id = 102250119, talk_content = "帮你们解决那个持名者吗？", talk_name = "青乌",},
	[102250119] = { talk_id = 102250119, group_id = 1022501, type = 1, next_id = 102250120, talk_content = "嗯，反正在这次事件解决之前你都没法到天月城去，而且一个人行走江湖也需要点“盘缠”对吧。", talk_name = "来来",},
	[102250120] = { talk_id = 102250120, group_id = 1022501, type = 1, next_id = 102250121, talk_content = "也就是说帮助你们的话我就能更快到天上去，你们也能给我报酬，是这样吗？", talk_name = "青乌",},
	[102250121] = { talk_id = 102250121, group_id = 1022501, type = 1, next_id = 102250122, talk_content = "来来，我才是司长。", talk_name = "朱嫦",},
	[102250122] = { talk_id = 102250122, group_id = 1022501, type = 1, next_id = 102250123, talk_content = "可司长应该也认为这样比较好对吧。", talk_name = "来来",},
	[102250123] = { talk_id = 102250123, group_id = 1022501, type = 1, next_id = 102250124, talk_content = "也是，天上迟迟不派增援，我们的战力的确有些不足，青乌，能接下我们的委托吗？", talk_name = "朱嫦",},
	[102250124] = { talk_id = 102250124, group_id = 1022501, type = 1, next_id = 102250125, talk_content = "帮人帮己，我没什么好拒绝的。", talk_name = "青乌",},
	[102250125] = { talk_id = 102250125, group_id = 1022501, type = 1, talk_content = "谢谢，今天时间也不早了，详细任务明天再说，你今天就在熙来的旅馆下榻吧，我会让人打点好的。", talk_name = "朱嫦",},
	[102250201] = { talk_id = 102250201, group_id = 1022502, type = 4, next_id = 102250202, aside = "<color=#FFEBA2>来来：</color>旅馆的方向我给你标记出来了，接下来你就自己一个走吧。", talk_content = "旅馆的方向我给你标记出来了，接下来你就自己一个走吧。", talk_name = "来来",},
	[102250202] = { talk_id = 102250202, group_id = 1022502, type = 4, next_id = 102250203, aside = "<color=#FFEBA2>青乌：</color>你不来吗？", talk_content = "你不来吗？", talk_name = "青乌",},
	[102250203] = { talk_id = 102250203, group_id = 1022502, type = 4, aside = "<color=#FFEBA2>来来：</color>司警是有员工宿舍的。", talk_content = "司警是有员工宿舍的。", talk_name = "来来",},
	[102250301] = { talk_id = 102250301, group_id = 1022503, type = 1, next_id = 102250302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，你就是青乌对吧。", talk_name = "？？？",},
	[102250302] = { talk_id = 102250302, group_id = 1022503, type = 1, next_id = 102250303, talk_content = "你是？", talk_name = "青乌",},
	[102250303] = { talk_id = 102250303, group_id = 1022503, type = 1, next_id = 102250304, talk_content = "我是司长安排在这里等候你的接待人，叫宁静。", talk_name = "宁静",},
	[102250304] = { talk_id = 102250304, group_id = 1022503, type = 1, next_id = 102250305, talk_content = "话说回来，你是第一次来这里吧，没想到一下子就碰上这么多事。", talk_name = "宁静",},
	[102250305] = { talk_id = 102250305, group_id = 1022503, type = 1, next_id = 102250306, talk_content = "嗯，是真碰了很多。", talk_name = "青乌",},
	[102250306] = { talk_id = 102250306, group_id = 1022503, type = 2, options = {{"继续聆听",102250309,},{"脉者到底是……",102250307,},}, talk_content = "但要不是你的话或者噬脉生物现在就冲进了熙来了，要是我也是脉者那该多好啊……", talk_name = "宁静",},
	[102250307] = { talk_id = 102250307, group_id = 1022503, type = 3, next_id = 102250308, talk_content = "脉者就是你这样能够使用气脉之力的呀，其中一个特征就是身体的显化。", talk_name = "宁静",},
	[102250308] = { talk_id = 102250308, group_id = 1022503, type = 3, next_id = 102250309, talk_content = "比如你头上的角，不过也有显化不明显的人就是了。", talk_name = "宁静",},
	[102250309] = { talk_id = 102250309, group_id = 1022503, type = 1, next_id = 102250310, talk_content = "对了，你明天还要出任务对吧。", talk_name = "宁静",},
	[102250310] = { talk_id = 102250310, group_id = 1022503, type = 1, talk_content = "在睡觉之前去熙来各处的商店看看如何？", talk_name = "宁静",},
	[102250401] = { talk_id = 102250401, group_id = 1022504, type = 1, next_id = 102250402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好你好，要买点什么吗？", talk_name = "多闻",},
	[102250402] = { talk_id = 102250402, group_id = 1022504, type = 1, next_id = 102250403, talk_content = "只是有人提议我到处看看……", talk_name = "青乌",},
	[102250403] = { talk_id = 102250403, group_id = 1022504, type = 1, next_id = 102250404, talk_content = "这里是卖什么的？", talk_name = "青乌",},
	[102250404] = { talk_id = 102250404, group_id = 1022504, type = 1, next_id = 102250405, talk_content = "我们百玲珑什么都有卖，所以简单来说就是卖杂货。", talk_name = "多闻",},
	[102250405] = { talk_id = 102250405, group_id = 1022504, type = 1, next_id = 102250406, talk_content = "总觉得这不像是什么吸引人的介绍。", talk_name = "青乌",},
	[102250406] = { talk_id = 102250406, group_id = 1022504, type = 1, next_id = 102250407, talk_content = "茶香不怕巷子深，再说我们百玲珑是世界各处都有分店的大商行……", talk_name = "多闻",},
	[102250407] = { talk_id = 102250407, group_id = 1022504, type = 2, options = {{"可以这么说",102250408,},{"我觉得是你们名气还不够响。",102250410,},}, talk_content = "会这么问难道客人你是从没有我们分店的远方来的吗？", talk_name = "多闻",},
	[102250408] = { talk_id = 102250408, group_id = 1022504, type = 3, next_id = 102250409, talk_content = "没想到世界竟然还有那样未开化的蛮荒之地……", talk_name = "多闻",},
	[102250409] = { talk_id = 102250409, group_id = 1022504, type = 3, talk_content = "（总觉得被当成野人了……）", talk_name = "青乌",},
	[102250410] = { talk_id = 102250410, group_id = 1022504, type = 3, next_id = 102250411, talk_content = "呃……我们百玲珑……名气还不够响。", talk_name = "多闻",},
	[102250411] = { talk_id = 102250411, group_id = 1022504, type = 3, talk_content = "（貌似石化了。）", talk_name = "青乌",},
	[102250501] = { talk_id = 102250501, group_id = 1022505, type = 1, next_id = 102250502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "今天的材料没剩多少了，只有炒粉可以吗？", talk_name = "纪火望",},
	[102250502] = { talk_id = 102250502, group_id = 1022505, type = 1, next_id = 102250503, talk_content = "所以这里是饮食店？", talk_name = "青乌",},
	[102250503] = { talk_id = 102250503, group_id = 1022505, type = 2, options = {{"你有生计给我？",102250504,},{"……我只是到处看看。",102250509,},}, talk_content = "是饭馆，唔……你是生面孔呢，今后要在这里谋生吗？", talk_name = "纪火望",},
	[102250504] = { talk_id = 102250504, group_id = 1022505, type = 3, next_id = 102250505, talk_content = "有是有，厨房里面还缺一位学徒，会用菜刀就能做，如何。", talk_name = "纪火望",},
	[102250505] = { talk_id = 102250505, group_id = 1022505, type = 3, next_id = 102250506, talk_content = "菜刀会不会用我不知道，但剑我挺会的。", talk_name = "青乌",},
	[102250506] = { talk_id = 102250506, group_id = 1022505, type = 3, next_id = 102250507, talk_content = "哼，原来又是侠客之流……你去维安司找工作吧。", talk_name = "纪火望",},
	[102250507] = { talk_id = 102250507, group_id = 1022505, type = 3, next_id = 102250508, talk_content = "我这里不用你们这些打打杀杀的人，身上的味道会让菜变难吃的。", talk_name = "纪火望",},
	[102250508] = { talk_id = 102250508, group_id = 1022505, type = 3, talk_content = "（好明显的歧视。）", talk_name = "青乌",},
	[102250509] = { talk_id = 102250509, group_id = 1022505, type = 3, next_id = 102250510, talk_content = "这样啊，观光客是吧，没想到这种时候都会有人特地跑这来。", talk_name = "纪火望",},
	[102250510] = { talk_id = 102250510, group_id = 1022505, type = 3, next_id = 102250511, talk_content = "这种时候？", talk_name = "青乌",},
	[102250511] = { talk_id = 102250511, group_id = 1022505, type = 3, next_id = 102250512, talk_content = "先不提这些天噬脉生物袭击熙来……天月城最近也在收紧出入的策略。", talk_name = "纪火望",},
	[102250512] = { talk_id = 102250512, group_id = 1022505, type = 3, next_id = 102250513, talk_content = "总觉得有什么大事要发生了。", talk_name = "纪火望",},
	[102250513] = { talk_id = 102250513, group_id = 1022505, type = 3, next_id = 102250514, talk_content = "唉，不过无论如何我也只能和祖辈一样在这里炒菜过日就是了。", talk_name = "纪火望",},
	[102250514] = { talk_id = 102250514, group_id = 1022505, type = 3, talk_content = "观光客，等下次我材料充足你再来吧，我给你整顿好的。", talk_name = "纪火望",},
	[102250601] = { talk_id = 102250601, group_id = 1022506, type = 1, next_id = 102250602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "哦，你是脉者对吧。", talk_name = "张嘉媛",},
	[102250602] = { talk_id = 102250602, group_id = 1022506, type = 1, next_id = 102250603, talk_content = "是这样没错，你这里也是买卖东西的吗？", talk_name = "青乌",},
	[102250603] = { talk_id = 102250603, group_id = 1022506, type = 1, next_id = 102250604, talk_content = "嗯，我们辉光蜻蜓专门售卖居家旅行必备的商品——武器。", talk_name = "张嘉媛",},
	[102250604] = { talk_id = 102250604, group_id = 1022506, type = 1, next_id = 102250605, talk_content = "都有些什么卖，有剑吗？", talk_name = "青乌",},
	[102250605] = { talk_id = 102250605, group_id = 1022506, type = 1, next_id = 102250606, talk_content = "我就知道客人你会感兴趣的，不过在我看我手上没有匹敌你武器的商品。", talk_name = "张嘉媛",},
	[102250606] = { talk_id = 102250606, group_id = 1022506, type = 1, next_id = 102250607, talk_content = "可这把只是地上捡来的东西……", talk_name = "青乌",},
	[102250607] = { talk_id = 102250607, group_id = 1022506, type = 1, next_id = 102250608, talk_content = "即便如此他也是一件不错的逸品。", talk_name = "张嘉媛",},
	[102250608] = { talk_id = 102250608, group_id = 1022506, type = 1, next_id = 102250609, talk_content = "是这样吗，还以为能更新一下装备，毕竟这把剑我觉得快撑不住了。", talk_name = "青乌",},
	[102250609] = { talk_id = 102250609, group_id = 1022506, type = 1, next_id = 102250610, talk_content = "哈哈，那回头等你有素材了就拿来订做吧，订做出来的武器肯定比我普通卖的要好上100倍。", talk_name = "张嘉媛",},
	[102250610] = { talk_id = 102250610, group_id = 1022506, type = 1, talk_content = "（也太夸张了。）", talk_name = "青乌",},
	[102250701] = { talk_id = 102250701, group_id = 1022507, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"每个人都很有性格",102250702,},{"感觉很悠闲。",102250703,},}, talk_content = "逛完回来了呢，觉得这个熙来如何？", talk_name = "宁静",},
	[102250702] = { talk_id = 102250702, group_id = 1022507, type = 3, next_id = 102250704, talk_content = "嗯，正因为太有性格他们经常被人投诉呢。", talk_name = "宁静",},
	[102250703] = { talk_id = 102250703, group_id = 1022507, type = 3, next_id = 102250704, talk_content = "熙来这个地方就是这样，毕竟只是从出入口慢慢扩大的小聚集地。", talk_name = "宁静",},
	[102250704] = { talk_id = 102250704, group_id = 1022507, type = 1, next_id = 102250705, talk_content = "啊，时间也不早了吧，抱歉，捉住你搞了一堆有的没的，我就不打扰你休息了。", talk_name = "宁静",},
	[102250705] = { talk_id = 102250705, group_id = 1022507, type = 1, talk_content = "明天醒来以后就到维安司去，请记得噢。", talk_name = "宁静",},
	[102260101] = { talk_id = 102260101, group_id = 1022601, type = 1, next_id = 102260102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "主祝大人，你真的不再休息一下吗？", talk_name = "朱嫦",},
	[102260102] = { talk_id = 102260102, group_id = 1022601, type = 2, options = {{"你们好。",102260103,},}, talk_content = "不，我也想尽快解决这个问题。", talk_name = "？？？",},
	[102260103] = { talk_id = 102260103, group_id = 1022601, type = 3, next_id = 102260104, talk_content = "青乌，你来了吗，抱歉刚刚在聊些事情。", talk_name = "朱嫦",},
	[102260104] = { talk_id = 102260104, group_id = 1022601, type = 3, next_id = 102260105, talk_content = "这位……是昨天为我挡下了持名者一击的人。", talk_name = "？？？",},
	[102260105] = { talk_id = 102260105, group_id = 1022601, type = 3, next_id = 102260106, talk_content = "这么说来也忘记和主祝大人你介绍了呢，这位的名字是青乌，和来来一起采了你昨天需要的药。", talk_name = "朱嫦",},
	[102260106] = { talk_id = 102260106, group_id = 1022601, type = 3, next_id = 102260107, talk_content = "没想到一下子受了这么多恩惠，作为回礼就让我亲自来做个自我介绍吧。", talk_name = "？？？",},
	[102260107] = { talk_id = 102260107, group_id = 1022601, type = 3, next_id = 102260108, talk_content = "我的名字叫青门隐，青门的现任主祝，虽然不知道你是否认得青门是什么就是了。", talk_name = "青门隐",},
	[102260108] = { talk_id = 102260108, group_id = 1022601, type = 3, next_id = 102260109, talk_content = "我不认得……因为我失去了记忆。", talk_name = "青乌",},
	[102260109] = { talk_id = 102260109, group_id = 1022601, type = 2, options = {{"是的。",102260110,},{"不是。",102260113,},}, talk_content = "难道是昨天为我挡下攻击造成的么？", talk_name = "青门隐",},
	[102260110] = { talk_id = 102260110, group_id = 1022601, type = 3, next_id = 102260111, talk_content = "青乌你在说些什么呢，我听来来说不是你再遇见她之前就已经失忆了吗？", talk_name = "朱嫦",},
	[102260111] = { talk_id = 102260111, group_id = 1022601, type = 3, next_id = 102260112, talk_content = "呃……", talk_name = "青乌",},
	[102260112] = { talk_id = 102260112, group_id = 1022601, type = 3, next_id = 102260114, talk_content = "（感受到了青门隐有些冷漠的视线。）",},
	[102260113] = { talk_id = 102260113, group_id = 1022601, type = 3, next_id = 102260114, talk_content = "幸好，因为青门里面也没有治疗失忆的术法呢。", talk_name = "青门隐",},
	[102260114] = { talk_id = 102260114, group_id = 1022601, type = 1, next_id = 102260115, talk_content = "说回正题，朱司长你说的帮手应该就是青乌没错吧。", talk_name = "青门隐",},
	[102260115] = { talk_id = 102260115, group_id = 1022601, type = 2, options = {{"组队？",102260116,},}, talk_content = "其他人已经出发，眼下要不要让我和青乌组成一队呢？", talk_name = "青门隐",},
	[102260116] = { talk_id = 102260116, group_id = 1022601, type = 3, next_id = 102260117, talk_content = "嗯，因为考虑到我们不能一味被动，所以我派了些人出去找寻那个持名者的行踪。", talk_name = "朱嫦",},
	[102260117] = { talk_id = 102260117, group_id = 1022601, type = 2, options = {{"可以。",102260118,},{"来来呢？",102260119,},}, talk_content = "要是顺利找到的话接下来就是全力围剿了，所以青乌你愿意和我一起行动吗？", talk_name = "青门隐",},
	[102260118] = { talk_id = 102260118, group_id = 1022601, type = 3, next_id = 102260121, talk_content = "谢谢，司长，这样没问题吧。", talk_name = "青门隐",},
	[102260119] = { talk_id = 102260119, group_id = 1022601, type = 3, next_id = 102260120, talk_content = "来来她从早上就不知道去哪了，大概又在打什么歪主意吧。", talk_name = "朱嫦",},
	[102260120] = { talk_id = 102260120, group_id = 1022601, type = 3, next_id = 102260121, talk_content = "那朱司长，我和青乌去出任务没问题吧？", talk_name = "青门隐",},
	[102260121] = { talk_id = 102260121, group_id = 1022601, type = 1, talk_content = "主祝大人是心意已决了吧，从立场上我也没法阻拦……就拜托你们了。", talk_name = "朱嫦",},
	[102260201] = { talk_id = 102260201, group_id = 1022602, type = 4, next_id = 102260202, aside = "<color=#FFEBA2>青门隐：</color>青乌，刚刚朱司长把需要探查的地点发给了我，但在那之前先熟悉一下我的战斗方式如何。", talk_content = "青乌，刚刚朱司长把需要探查的地点发给了我，但在那之前先熟悉一下我的战斗方式如何。", talk_name = "青门隐",},
	[102260202] = { talk_id = 102260202, group_id = 1022602, type = 4, aside = "<color=#FFEBA2>青门隐：</color>在熙来的附近应该有一处常常滋生噬脉生物的土地，我们先到那边去吧。", talk_content = "在熙来的附近应该有一处常常滋生噬脉生物的土地，我们先到那边去吧。", talk_name = "青门隐",},
	[102260301] = { talk_id = 102260301, group_id = 1022603, type = 4, aside = "<color=#FFEBA2>青门隐：</color>我的战斗能力肯定不如青乌你优秀，但相对的，我可以用青门的技法来做些你做不到的事。", talk_content = "我的战斗能力肯定不如青乌你优秀，但相对的，我可以用青门的技法来做些你做不到的事。", talk_name = "青门隐",},
	[102260401] = { talk_id = 102260401, group_id = 1022604, type = 1, next_id = 102260402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "治愈的确是我所做不到的事情……", talk_name = "青乌",},
	[102260402] = { talk_id = 102260402, group_id = 1022604, type = 2, options = {{"（问问主祝的事情）",102260403,},}, talk_content = "脉者的能力千差百异，而且我还是青门的主祝，这不过一种技法的应用而已。", talk_name = "青门隐",},
	[102260403] = { talk_id = 102260403, group_id = 1022604, type = 3, next_id = 102260404, talk_content = "果然会在意吗，毕竟朱司长也称呼我为大人。", talk_name = "青门隐",},
	[102260404] = { talk_id = 102260404, group_id = 1022604, type = 3, next_id = 102260405, talk_content = "不过我并没有这么伟大，只是刚好有合适的血脉，生在合适的时间而已。", talk_name = "青门隐",},
	[102260405] = { talk_id = 102260405, group_id = 1022604, type = 3, next_id = 102260406, talk_content = "而且你看，就算贵为主祝我也站在了战斗的第一线不是吗？", talk_name = "青门隐",},
	[102260406] = { talk_id = 102260406, group_id = 1022604, type = 3, next_id = 102260407, talk_content = "的确如此。", talk_name = "青乌",},
	[102260407] = { talk_id = 102260407, group_id = 1022604, type = 1, next_id = 102260408, talk_content = "不过……你说你失去记忆了对吧，朱司长也说你是为了去天月城而协助这次行动的。", talk_name = "青门隐",},
	[102260408] = { talk_id = 102260408, group_id = 1022604, type = 1, next_id = 102260409, talk_content = "怎么突然问起这个，难道你有什么线索吗？", talk_name = "青乌",},
	[102260409] = { talk_id = 102260409, group_id = 1022604, type = 1, next_id = 102260410, talk_content = "能不能算上线索我不知道，但我总觉得自己在哪里认识过青乌这个名号。", talk_name = "青门隐",},
	[102260410] = { talk_id = 102260410, group_id = 1022604, type = 1, next_id = 102260411, talk_content = "在哪，还是说某人曾对你说过？", talk_name = "青乌",},
	[102260411] = { talk_id = 102260411, group_id = 1022604, type = 1, talk_content = "不，应该不是的，但我一时半会有些想不起来到底是哪里了，抱歉。", talk_name = "青门隐",},
	[102260501] = { talk_id = 102260501, group_id = 1022605, type = 4, next_id = 102260502, aside = "<color=#FFEBA2>青门隐：</color>青乌，时间稍稍有些多耽误了，我们往第一个调查点去。", talk_content = "青乌，时间稍稍有些多耽误了，我们往第一个调查点去。", talk_name = "青门隐",},
	[102260502] = { talk_id = 102260502, group_id = 1022605, type = 4, aside = "<color=#FFEBA2>青门隐：</color>在这期间我也会尽量思考的，一旦想到就和你说我在哪里见过你吧，青乌。", talk_content = "在这期间我也会尽量思考的，一旦想到就和你说我在哪里见过你吧，青乌。", talk_name = "青门隐",},
	[102260601] = { talk_id = 102260601, group_id = 1022606, type = 4, aside = "<color=#FFEBA2>青门隐：</color>话说回来……青乌你身体里面似乎还有些没被引出的力量。", talk_content = "话说回来……青乌你身体里面似乎还有些没被引出的力量。", talk_name = "青门隐",},
	[102260602] = { talk_id = 102260602, group_id = 1022606, type = 4, aside = "<color=#FFEBA2>青乌：</color>我倒不觉得就是了。", talk_content = "我倒不觉得就是了。", talk_name = "青乌",},
	[102260603] = { talk_id = 102260603, group_id = 1022606, type = 4, aside = "<color=#FFEBA2>青门隐：</color>这附近有个挺适合引导力量的地方，跟我来吧，青乌。", talk_content = "这附近有个挺适合引导力量的地方，跟我来吧，青乌。", talk_name = "青门隐",},
	[102260701] = { talk_id = 102260701, group_id = 1022607, type = 1, next_id = 102260702, fade_in = 1, fade_out = true, hide_player = true, talk_content = "这个就是空洞吗？", talk_name = "青乌",},
	[102260702] = { talk_id = 102260702, group_id = 1022607, type = 1, next_id = 102260703, talk_content = "是的，所以青乌你连这个也不记得了嘛？", talk_name = "青门隐",},
	[102260703] = { talk_id = 102260703, group_id = 1022607, type = 1, next_id = 102260704, talk_content = "应该吧，至少没有多少印象，能解释一下吗？", talk_name = "青乌",},
	[102260704] = { talk_id = 102260704, group_id = 1022607, type = 1, next_id = 102260705, talk_content = "嗯，空洞是气脉的网络中间偶发的空隙，那里面存在有各样的景观，并且会因应进入者而产生不同的反应。", talk_name = "青门隐",},
	[102260705] = { talk_id = 102260705, group_id = 1022607, type = 1, next_id = 102260706, talk_content = "这个能进去？", talk_name = "青乌",},
	[102260706] = { talk_id = 102260706, group_id = 1022607, type = 1, next_id = 102260707, talk_content = "是的，而且还可能会遭遇怪物。", talk_name = "青门隐",},
	[102260707] = { talk_id = 102260707, group_id = 1022607, type = 1, next_id = 102260708, talk_content = "听着挺危险的。", talk_name = "青乌",},
	[102260708] = { talk_id = 102260708, group_id = 1022607, type = 1, next_id = 102260709, talk_content = "伴随危险的还有收益，不少人都以在空洞中“狩猎”为生，实际上也有人靠这个发了大财。", talk_name = "青门隐",},
	[102260709] = { talk_id = 102260709, group_id = 1022607, type = 1, next_id = 102260710, talk_content = "当然也有人并没有发财，但获得了“天赋”。", talk_name = "青门隐",},
	[102260710] = { talk_id = 102260710, group_id = 1022607, type = 1, next_id = 102260711, talk_content = "你是说我也能通过空洞获得“天赋”吗？", talk_name = "青乌",},
	[102260711] = { talk_id = 102260711, group_id = 1022607, type = 1, next_id = 102260712, talk_content = "是的，这个空洞看来入口有些小，只能供一人进入，但青乌你肯定没问题的。", talk_name = "青门隐",},
	[102260712] = { talk_id = 102260712, group_id = 1022607, type = 1, next_id = 102260713, talk_content = "要是感到危险的话应该能退出来吧。", talk_name = "青乌",},
	[102260713] = { talk_id = 102260713, group_id = 1022607, type = 1, next_id = 102260714, talk_content = "是的，我也会在外面接应你的，而且我认为青乌你应该很需要力量才是。", talk_name = "青门隐",},
	[102260714] = { talk_id = 102260714, group_id = 1022607, type = 1, talk_content = "的确如此，我去去就回。", talk_name = "青乌",},
	[102260801] = { talk_id = 102260801, group_id = 1022608, type = 4, next_id = 102260802, aside = "<color=#FFEBA2>青乌：</color>没想到竟然会是“试炼”，还有这个空间总感觉莫名的熟悉。", talk_content = "没想到竟然会是“试炼”，还有这个空间总感觉莫名的熟悉。", talk_name = "青乌",},
	[102260802] = { talk_id = 102260802, group_id = 1022608, type = 4, next_id = 102260803, aside = "<color=#FFEBA2>青乌：</color>而且这个能力，我总感觉我很久之前就会了。", talk_content = "而且这个能力，我总感觉我很久之前就会了。", talk_name = "青乌",},
	[102260803] = { talk_id = 102260803, group_id = 1022608, type = 4, aside = "<color=#FFEBA2>青乌：</color>空洞会根据进入者产生反应吗……", talk_content = "空洞会根据进入者产生反应吗……", talk_name = "青乌",},
	[102260901] = { talk_id = 102260901, group_id = 1022609, type = 1, next_id = 102260902, fade_in = 1, fade_out = true, hide_player = true, talk_content = "果然，感觉完全不一样了呢。", talk_name = "青门隐",},
	[102260902] = { talk_id = 102260902, group_id = 1022609, type = 1, next_id = 102260903, talk_content = "嗯，在空洞中回忆了过去的技能，但是……", talk_name = "青乌",},
	[102260903] = { talk_id = 102260903, group_id = 1022609, type = 1, next_id = 102260904, talk_content = "假若真是现在才回忆起来的话……那之前的我……", talk_name = "青乌",},
	[102260904] = { talk_id = 102260904, group_id = 1022609, type = 1, next_id = 102260905, talk_content = "不对，还是说那份记忆之中有什么遗漏的东西……", talk_name = "青乌",},
	[102260905] = { talk_id = 102260905, group_id = 1022609, type = 1, talk_content = "虽然我不能直接为你分担困惑，但假若空洞的确有效的话，之后多试试进入空洞如何。", talk_name = "青门隐",},
	[102270101] = { talk_id = 102270101, group_id = 1022701, type = 1, next_id = 102270102, fade_in = 1, fade_out = true, hide_player = true, talk_content = "这里的风景是……", talk_name = "青乌",},
	[102270102] = { talk_id = 102270102, group_id = 1022701, type = 1, next_id = 102270103, talk_content = "逆浮区，我们都是这么称呼这样的区域的。", talk_name = "青门隐",},
	[102270103] = { talk_id = 102270103, group_id = 1022701, type = 1, next_id = 102270104, talk_content = "的确……这里的东西都是逆与浮的。", talk_name = "青乌",},
	[102270104] = { talk_id = 102270104, group_id = 1022701, type = 1, next_id = 102270105, talk_content = "等等，要说逆浮的话。", talk_name = "青乌",},
	[102270105] = { talk_id = 102270105, group_id = 1022701, type = 1, next_id = 102270106, talk_content = "天月城，你想这么说对吧，这片区域的确有这个意思。", talk_name = "青门隐",},
	[102270106] = { talk_id = 102270106, group_id = 1022701, type = 1, next_id = 102270107, talk_content = "逆浮区是在太阳升起之后出现的区域，内部的景物也会缓慢呈现“逆浮”的状态。", talk_name = "青门隐",},
	[102270107] = { talk_id = 102270107, group_id = 1022701, type = 1, next_id = 102270108, talk_content = "太阳造出了逆浮区吗？", talk_name = "青乌",},
	[102270108] = { talk_id = 102270108, group_id = 1022701, type = 1, next_id = 102270109, talk_content = "应该是的，但逆浮区本身不是什么问题，有问题的是因为内部地形复杂，往往会被噬脉生物当作聚集地。", talk_name = "青门隐",},
	[102270109] = { talk_id = 102270109, group_id = 1022701, type = 1, next_id = 102270110, talk_content = "所以才要搜集这个地方吗？", talk_name = "青乌",},
	[102270110] = { talk_id = 102270110, group_id = 1022701, type = 1, next_id = 102270111, talk_content = "是的，而且光是站在这里我就明白里面肯定有不少危险。", talk_name = "青门隐",},
	[102270111] = { talk_id = 102270111, group_id = 1022701, type = 2, options = {{"你应该也要战斗吧？",102270112,},{"那回复就交给你了。",102270113,},}, talk_content = "青乌，我作战能力不高，所以能请你好好保护我吗？", talk_name = "青门隐",},
	[102270112] = { talk_id = 102270112, group_id = 1022701, type = 3, talk_content = "当然我会参加战斗，但我可不想再和上次一样面对大怪物了……", talk_name = "青门隐",},
	[102270113] = { talk_id = 102270113, group_id = 1022701, type = 3, talk_content = "是的，就让我们各自做各自擅长的事情吧。", talk_name = "青门隐",},
	[102270201] = { talk_id = 102270201, group_id = 1022702, type = 4, next_id = 102270202, aside = "<color=#FFEBA2>青乌：</color>这里有很多残缺的建筑。", talk_content = "这里有很多残缺的建筑。", talk_name = "青乌",},
	[102270202] = { talk_id = 102270202, group_id = 1022702, type = 4, next_id = 102270203, aside = "<color=#FFEBA2>青门隐：</color>我估计是当年太阳之战的遗迹吧。", talk_content = "我估计是当年太阳之战的遗迹吧。", talk_name = "青门隐",},
	[102270203] = { talk_id = 102270203, group_id = 1022702, type = 4, next_id = 102270204, aside = "<color=#FFEBA2>青乌：</color>太阳之战是？", talk_content = "太阳之战是？", talk_name = "青乌",},
	[102270204] = { talk_id = 102270204, group_id = 1022702, type = 4, next_id = 102270205, aside = "<color=#FFEBA2>青门隐：</color>在太阳升起之前人们曾和噬脉生物发生过战斗，详细持续了多久不清楚。", talk_content = "在太阳升起之前人们曾和噬脉生物发生过战斗，详细持续了多久不清楚。", talk_name = "青门隐",},
	[102270205] = { talk_id = 102270205, group_id = 1022702, type = 4, aside = "<color=#FFEBA2>青门隐：</color>但在那场大战之中逝去了无数的生命，无论是人还是噬脉生物。", talk_content = "但在那场大战之中逝去了无数的生命，无论是人还是噬脉生物。", talk_name = "青门隐",},
	[102270301] = { talk_id = 102270301, group_id = 1022703, type = 1, next_id = 102270302, fade_in = 1, fade_out = true, hide_player = true, talk_content = "这边算是走过一圈了吧，主祝……呃。", talk_name = "青乌",},
	[102270302] = { talk_id = 102270302, group_id = 1022703, type = 1, next_id = 102270303, talk_content = "叫我隐就可以，毕竟让救命恩人也叫我大人也太奇怪了。", talk_name = "青门隐",},
	[102270303] = { talk_id = 102270303, group_id = 1022703, type = 1, next_id = 102270304, talk_content = "不过就如你说的那样，这片区域走了一圈也没什么收获。", talk_name = "青门隐",},
	[102270304] = { talk_id = 102270304, group_id = 1022703, type = 2, options = {{"什么时间到了？",102270305,},}, talk_content = "但噬脉生物的数量不容小瞧……果然是时间到了吗？", talk_name = "青门隐",},
	[102270305] = { talk_id = 102270305, group_id = 1022703, type = 3, next_id = 102270306, talk_content = "我只是说再这样下去任由滋生就会很麻烦而已，必须要想办法消灭持名种。", talk_name = "青门隐",},
	[102270306] = { talk_id = 102270306, group_id = 1022703, type = 1, next_id = 102270307, talk_content = "（通信音）",},
	[102270307] = { talk_id = 102270307, group_id = 1022703, type = 2, options = {{"（接通信）",102270308,},}, talk_content = "通信？", talk_name = "青门隐",},
	[102270308] = { talk_id = 102270308, group_id = 1022703, type = 3, next_id = 102270309, talk_content = "喂，青乌，是青乌对吧，我另外派出去的人刚刚受到了袭击。", talk_name = "朱嫦",},
	[102270309] = { talk_id = 102270309, group_id = 1022703, type = 3, next_id = 102270310, talk_content = "难道是被持名者……", talk_name = "青门隐",},
	[102270310] = { talk_id = 102270310, group_id = 1022703, type = 3, next_id = 102270311, talk_content = "是的，情况不容乐观。", talk_name = "朱嫦",},
	[102270311] = { talk_id = 102270311, group_id = 1022703, type = 3, talk_content = "我刚刚联系上来来让她先过去了，也拜托你们赶去救援。", talk_name = "朱嫦",},
	[102280101] = { talk_id = 102280101, group_id = 1022801, type = 4, aside = "<color=#FFEBA2>青门隐：</color>位置就在不远的地方，青乌，我们赶紧走吧。", talk_content = "位置就在不远的地方，青乌，我们赶紧走吧。", talk_name = "青门隐",},
	[102280201] = { talk_id = 102280201, group_id = 1022802, type = 1, next_id = 102280202, fade_in = 1, fade_out = true, hide_player = true, talk_content = "哦，你们来了啊？", talk_name = "来来",},
	[102280202] = { talk_id = 102280202, group_id = 1022802, type = 1, next_id = 102280203, talk_content = "这状况是？", talk_name = "青乌",},
	[102280203] = { talk_id = 102280203, group_id = 1022802, type = 1, talk_content = "先解决怪物再说吧。", talk_name = "来来",},
	[102280301] = { talk_id = 102280301, group_id = 1022803, type = 1, next_id = 102280302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "来来，现在的情况如何，我听说调查的人被袭击了。", talk_name = "青乌",},
	[102280302] = { talk_id = 102280302, group_id = 1022803, type = 1, next_id = 102280303, talk_content = "嗯，有几个人已经趁着我拦住怪物的时间点撤退了，但是深处应该还有人。", talk_name = "来来",},
	[102280303] = { talk_id = 102280303, group_id = 1022803, type = 1, next_id = 102280304, talk_content = "搜查队是分散搜查的吗？", talk_name = "青门隐",},
	[102280304] = { talk_id = 102280304, group_id = 1022803, type = 1, next_id = 102280305, talk_content = "只是没有应对袭击的经验，不慎跑散了而已，丫头你刚刚是不是在怀疑我们司警的权威。", talk_name = "来来",},
	[102280305] = { talk_id = 102280305, group_id = 1022803, type = 1, next_id = 102280306, talk_content = "要我说是你在怀疑我的权……咳咳，让你见笑了，青乌。", talk_name = "青门隐",},
	[102280306] = { talk_id = 102280306, group_id = 1022803, type = 2, options = {{"嗯，救人要紧。",},{"你们很熟吗？",102280307,},}, talk_content = "哼，要装乖宝宝随你，反正事不宜迟，我们行动吧，青乌。", talk_name = "来来",},
	[102280307] = { talk_id = 102280307, group_id = 1022803, type = 3, talk_content = "才没有！", talk_name = "两人",},
	[102280401] = { talk_id = 102280401, group_id = 1022804, type = 4, next_id = 102280402, aside = "<color=#FFEBA2>来来：</color>走散的还有三个人，大海、吉禄以及宁静。", talk_content = "走散的还有三个人，大海、吉禄以及宁静。", talk_name = "来来",},
	[102280402] = { talk_id = 102280402, group_id = 1022804, type = 4, next_id = 102280403, aside = "<color=#FFEBA2>青乌：</color>宁静……", talk_content = "宁静……", talk_name = "青乌",},
	[102280403] = { talk_id = 102280403, group_id = 1022804, type = 4, next_id = 102280404, aside = "<color=#FFEBA2>来来：</color>怎么，你认识她吗？", talk_content = "怎么，你认识她吗？", talk_name = "来来",},
	[102280404] = { talk_id = 102280404, group_id = 1022804, type = 4, next_id = 102280405, aside = "<color=#FFEBA2>青乌：</color>昨晚是她来接待我的。", talk_content = "昨晚是她来接待我的。", talk_name = "青乌",},
	[102280405] = { talk_id = 102280405, group_id = 1022804, type = 4, aside = "<color=#FFEBA2>来来：</color>那就更得把她救下来了。", talk_content = "那就更得把她救下来了。", talk_name = "来来",},
	[102280501] = { talk_id = 102280501, group_id = 1022805, type = 4, next_id = 102280502, aside = "<color=#FFEBA2>青门隐：</color>前面有人被包围了。", talk_content = "前面有人被包围了。", talk_name = "青门隐",},
	[102280502] = { talk_id = 102280502, group_id = 1022805, type = 4, aside = "<color=#FFEBA2>来来：</color>那个是吉禄，救他出来吧。", talk_content = "那个是吉禄，救他出来吧。", talk_name = "来来",},
	[102280601] = { talk_id = 102280601, group_id = 1022806, type = 1, next_id = 102280602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "关键时刻还是得靠脉者……", talk_name = "吉禄",},
	[102280602] = { talk_id = 102280602, group_id = 1022806, type = 1, next_id = 102280603, talk_content = "那就记得在下次选副司的时候投我一票。", talk_name = "来来",},
	[102280603] = { talk_id = 102280603, group_id = 1022806, type = 1, next_id = 102280604, talk_content = "对了，剩下两人在什么方向？", talk_name = "来来",},
	[102280604] = { talk_id = 102280604, group_id = 1022806, type = 1, next_id = 102280605, talk_content = "在前面，我没受什么伤，我也和你们一起去吧。", talk_name = "吉禄",},
	[102280605] = { talk_id = 102280605, group_id = 1022806, type = 1, next_id = 102280606, talk_content = "不，这里有我们的就足够了，现在你的任务是回去与朱司长报平安。", talk_name = "青门隐",},
	[102280606] = { talk_id = 102280606, group_id = 1022806, type = 1, next_id = 102280607, talk_content = "也是，是我鲁莽了，刚刚也如此所以才和大部队走散。", talk_name = "吉禄",},
	[102280607] = { talk_id = 102280607, group_id = 1022806, type = 1, talk_content = "拜托你们，一定要救下宁静和大海……", talk_name = "吉禄",},
	[102280701] = { talk_id = 102280701, group_id = 1022807, type = 4, aside = "<color=#FFEBA2>来来：</color>宁静和大海按他说就在这前面，我们走吧。", talk_content = "宁静和大海按他说就在这前面，我们走吧。", talk_name = "来来",},
	[102280801] = { talk_id = 102280801, group_id = 1022808, type = 1, next_id = 102280802, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "可恶，我才不会输！", talk_name = "大海",},
	[102280802] = { talk_id = 102280802, group_id = 1022808, type = 1, talk_content = "赶上了！", talk_name = "来来",},
	[102280901] = { talk_id = 102280901, group_id = 1022809, type = 1, next_id = 102280902, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "大海，只有你一个吗？", talk_name = "来来",},
	[102280902] = { talk_id = 102280902, group_id = 1022809, type = 2, options = {{"神荼？",102280903,},}, talk_content = "宁静被神荼带走了……", talk_name = "大海",},
	[102280903] = { talk_id = 102280903, group_id = 1022809, type = 3, next_id = 102280904, talk_content = "袭击熙来的持名者，他刚刚说他的名叫神荼。", talk_name = "大海",},
	[102280904] = { talk_id = 102280904, group_id = 1022809, type = 1, next_id = 102280905, talk_content = "手握磊锤，衣饰似人，被大锏所灭……", talk_name = "青门隐",},
	[102280905] = { talk_id = 102280905, group_id = 1022809, type = 1, next_id = 102280906, talk_content = "丫头你突然念什么诗？", talk_name = "来来",},
	[102280906] = { talk_id = 102280906, group_id = 1022809, type = 1, next_id = 102280907, talk_content = "是《帝江事记》。", talk_name = "青门隐",},
	[102280907] = { talk_id = 102280907, group_id = 1022809, type = 1, next_id = 102280908, talk_content = "就是那本厚的不行的历史书吗……等等你说历史书上面记了那个持名者的事情？", talk_name = "来来",},
	[102280908] = { talk_id = 102280908, group_id = 1022809, type = 1, next_id = 102280909, talk_content = "最初见到他的时候我就该认出来的，可是为什么，他明明已经被灭了才对。", talk_name = "青门隐",},
	[102280909] = { talk_id = 102280909, group_id = 1022809, type = 1, next_id = 102280910, talk_content = "在第一次见面的时候，他曾经向我说过一句话。", talk_name = "青乌",},
	[102280910] = { talk_id = 102280910, group_id = 1022809, type = 1, next_id = 102280911, talk_content = "“你也复活了。”", talk_name = "青乌",},
	[102280911] = { talk_id = 102280911, group_id = 1022809, type = 1, next_id = 102280912, talk_content = "复活……", talk_name = "青门隐",},
	[102280912] = { talk_id = 102280912, group_id = 1022809, type = 1, next_id = 102280913, talk_content = "不论他是复活还是伪装，现在要做的应该是去救人才对吧。", talk_name = "来来",},
	[102280913] = { talk_id = 102280913, group_id = 1022809, type = 1, next_id = 102280914, talk_content = "可是说要救人……神荼他到底在哪？", talk_name = "青门隐",},
	[102280914] = { talk_id = 102280914, group_id = 1022809, type = 2, options = {{"……要地毯式搜查吗？",102280915,},{"有什么可用来追踪的线索吗？",102280916,},}, talk_content = "对不起，我只知道他离开的方向……", talk_name = "大海",},
	[102280915] = { talk_id = 102280915, group_id = 1022809, type = 3, next_id = 102280918, talk_content = "不可能，只靠我们做不到。", talk_name = "青门隐",},
	[102280916] = { talk_id = 102280916, group_id = 1022809, type = 3, next_id = 102280917, talk_content = "我刚刚也试过了，但是宁静身上的设备似乎受到了什么干扰。", talk_name = "大海",},
	[102280917] = { talk_id = 102280917, group_id = 1022809, type = 3, next_id = 102280918, talk_content = "是太靠近持名者了吧。", talk_name = "青门隐",},
	[102280918] = { talk_id = 102280918, group_id = 1022809, type = 1, next_id = 102280919, talk_content = "那剩下的办法是……", talk_name = "青门隐",},
	[102280919] = { talk_id = 102280919, group_id = 1022809, type = 1, next_id = 102280920, talk_content = "直接去找他不就好了。", talk_name = "来来",},
	[102280920] = { talk_id = 102280920, group_id = 1022809, type = 1, next_id = 102280921, talk_content = "？", talk_name = "青乌",},
	[102280921] = { talk_id = 102280921, group_id = 1022809, type = 1, next_id = 102280922, talk_content = "我早上单独行动的时候就已经找到他的老巢了。", talk_name = "来来",},
	[102280922] = { talk_id = 102280922, group_id = 1022809, type = 1, talk_content = "啊？", talk_name = "青门隐",},
	[102281001] = { talk_id = 102281001, group_id = 1022810, type = 4, next_id = 102281002, aside = "<color=#FFEBA2>青门隐：</color>我刚刚应该没听错吧，你说你找到了？", talk_content = "我刚刚应该没听错吧，你说你找到了？", talk_name = "青门隐",},
	[102281002] = { talk_id = 102281002, group_id = 1022810, type = 4, next_id = 102281003, aside = "<color=#FFEBA2>来来：</color>嗯，一堆人在一起的话功劳都要被瓜分，但只有我一个的话……", talk_content = "嗯，一堆人在一起的话功劳都要被瓜分，但只有我一个的话……", talk_name = "来来",},
	[102281003] = { talk_id = 102281003, group_id = 1022810, type = 4, next_id = 102281004, aside = "<color=#FFEBA2>青门隐：</color>所以你就在大家出发之前一个人跑不知道哪去了吗。", talk_content = "所以你就在大家出发之前一个人跑不知道哪去了吗。", talk_name = "青门隐",},
	[102281004] = { talk_id = 102281004, group_id = 1022810, type = 4, next_id = 102281005, aside = "<color=#FFEBA2>青门隐：</color>朱司长找你可久了。", talk_content = "朱司长找你可久了。", talk_name = "青门隐",},
	[102281005] = { talk_id = 102281005, group_id = 1022810, type = 4, aside = "<color=#FFEBA2>来来：</color>要成为人上之人就要不拘小节，对吧。", talk_content = "要成为人上之人就要不拘小节，对吧。", talk_name = "来来",},
	[102290101] = { talk_id = 102290101, group_id = 1022901, type = 1, next_id = 102290102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "前面……", talk_name = "青门隐",},
	[102290102] = { talk_id = 102290102, group_id = 1022901, type = 1, next_id = 102290103, talk_content = "竟然大摇大摆的站着，喂，持名的，你把宁静弄哪里去了？", talk_name = "来来",},
	[102290103] = { talk_id = 102290103, group_id = 1022901, type = 1, next_id = 102290104, talk_content = "人躺在一边，我打算在解决你们之后再处置她……然后。", talk_name = "神荼",},
	[102290104] = { talk_id = 102290104, group_id = 1022901, type = 2, options = {{"等我？",102290105,},{"你认识我吗？",102290106,},}, talk_content = "等你很久了，青乌。", talk_name = "神荼",},
	[102290105] = { talk_id = 102290105, group_id = 1022901, type = 3, next_id = 102290107, talk_content = "嗯，因为我知道你会因为人质而来，过去的敌人。", talk_name = "神荼",},
	[102290106] = { talk_id = 102290106, group_id = 1022901, type = 3, next_id = 102290107, talk_content = "当然认识，我绝不会忘记毁灭我的人们。", talk_name = "神荼",},
	[102290107] = { talk_id = 102290107, group_id = 1022901, type = 1, next_id = 102290108, talk_content = "难道说青乌真是几百年前的人……", talk_name = "来来",},
	[102290108] = { talk_id = 102290108, group_id = 1022901, type = 1, next_id = 102290109, talk_content = "既然你认识我的话，能不能和我说说以前的我是什么人，我想要做什么？", talk_name = "青乌",},
	[102290109] = { talk_id = 102290109, group_id = 1022901, type = 1, next_id = 102290110, talk_content = "不知，我就连你们当时想要做什么都不知道，你与你的同伴只是不断攻击我们……", talk_name = "神荼",},
	[102290110] = { talk_id = 102290110, group_id = 1022901, type = 1, next_id = 102290111, talk_content = "我还记得我与右门之将两者被你们攻击，使双锏的少年瞬间就命中了我们的要害。", talk_name = "神荼",},
	[102290111] = { talk_id = 102290111, group_id = 1022901, type = 1, next_id = 102290112, talk_content = "可是……正因如此，我才能看见太阳挂于天空的世界。", talk_name = "神荼",},
	[102290112] = { talk_id = 102290112, group_id = 1022901, type = 1, next_id = 102290113, talk_content = "那份光辉，那份伟大，那就是此世的未来。", talk_name = "神荼",},
	[102290113] = { talk_id = 102290113, group_id = 1022901, type = 1, next_id = 102290114, talk_content = "青乌，无论过去还是现在，你与你的同伴们都只会是我等的敌人。", talk_name = "神荼",},
	[102290114] = { talk_id = 102290114, group_id = 1022901, type = 1, talk_content = "为了报复，为了太阳，我要在这里消灭你。", talk_name = "神荼",},
	[102290201] = { talk_id = 102290201, group_id = 1022902, type = 4, next_id = 102290202, aside = "<color=#FFEBA2>青门隐：</color>青乌……复活，我想起来了，青乌是……", talk_content = "青乌……复活，我想起来了，青乌是……", talk_name = "青门隐",},
	[102290202] = { talk_id = 102290202, group_id = 1022902, type = 4, next_id = 102290203, aside = "<color=#FFEBA2>来来：</color>这种事情之后再说！", talk_content = "这种事情之后再说！", talk_name = "来来",},
	[102290203] = { talk_id = 102290203, group_id = 1022902, type = 4, aside = "<color=#FFEBA2>青乌：</color>总觉得别人都比我要了解我自己。", talk_content = "总觉得别人都比我要了解我自己。", talk_name = "青乌",},
	[102290301] = { talk_id = 102290301, group_id = 1022903, type = 4, next_id = 102290302, aside = "<color=#FFEBA2>神荼：</color>我之所以复活那必然是因为太阳需要谁来统领群体。", talk_content = "我之所以复活那必然是因为太阳需要谁来统领群体。", talk_name = "神荼",},
	[102290302] = { talk_id = 102290302, group_id = 1022903, type = 4, next_id = 102290303, aside = "<color=#FFEBA2>神荼：</color>而我，就会是统领群体的那个存在。", talk_content = "而我，就会是统领群体的那个存在。", talk_name = "神荼",},
	[102290303] = { talk_id = 102290303, group_id = 1022903, type = 4, next_id = 102290304, aside = "<color=#FFEBA2>神荼：</color>消灭你们，消灭那个人类的聚集地。", talk_content = "消灭你们，消灭那个人类的聚集地。", talk_name = "神荼",},
	[102290304] = { talk_id = 102290304, group_id = 1022903, type = 4, next_id = 102290305, aside = "<color=#FFEBA2>神荼：</color>那就是我在现世复活的理由。", talk_content = "那就是我在现世复活的理由。", talk_name = "神荼",},
	[102290305] = { talk_id = 102290305, group_id = 1022903, type = 4, aside = "<color=#FFEBA2>神荼：</color>你又如何，青乌！", talk_content = "你又如何，青乌！", talk_name = "神荼",},
	[102290401] = { talk_id = 102290401, group_id = 1022904, type = 1, next_id = 102290402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "最后有一件事，你为什么要说我也复活了，我难道……", talk_name = "青乌",},
	[102290402] = { talk_id = 102290402, group_id = 1022904, type = 1, next_id = 102290403, talk_content = "悬天的家伙是这么说的。", talk_name = "神荼",},
	[102290403] = { talk_id = 102290403, group_id = 1022904, type = 1, next_id = 102290404, talk_content = "悬天？", talk_name = "青乌",},
	[102290404] = { talk_id = 102290404, group_id = 1022904, type = 1, next_id = 102290405, talk_content = "呃……我还会……复活的。", talk_name = "神荼",},
	[102290405] = { talk_id = 102290405, group_id = 1022904, type = 1, talk_content = "喂，等等！", talk_name = "青乌",},
	[102290501] = { talk_id = 102290501, group_id = 1022905, type = 1, next_id = 102290502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "对了，我去救宁静！", talk_name = "来来",},
	[102290502] = { talk_id = 102290502, group_id = 1022905, type = 1, next_id = 102290503, talk_content = "我也……", talk_name = "青乌",},
	[102290503] = { talk_id = 102290503, group_id = 1022905, type = 2, options = {{"（捡起来）",102290504,},}, talk_content = "地上的是……", talk_name = "青乌",},
	[102290504] = { talk_id = 102290504, group_id = 1022905, type = 3, next_id = 102290505, talk_content = "等等青乌，那个是！", talk_name = "青门隐",},
	[102290505] = { talk_id = 102290505, group_id = 1022905, type = 3, next_id = 102290506, back_text = " ", talk_content = "玩过头了，就算是噬脉生物也……", talk_name = "严厉的女性",},
	[102290506] = { talk_id = 102290506, group_id = 1022905, type = 3, next_id = 102290507, back_text = " ", talk_content = "没关系吧，他们不是这个世界的害虫吗？", talk_name = "刻薄的男性",},
	[102290507] = { talk_id = 102290507, group_id = 1022905, type = 3, next_id = 102290508, back_text = " ", talk_content = "要说的话我觉得你也是。", talk_name = "严厉的女性",},
	[102290508] = { talk_id = 102290508, group_id = 1022905, type = 3, next_id = 102290509, back_text = " ", talk_content = "什么！", talk_name = "刻薄的男性",},
	[102290509] = { talk_id = 102290509, group_id = 1022905, type = 3, next_id = 102290510, back_text = " ", talk_content = "好了，别吵架，青乌，你也来说说吧。", talk_name = "离歌",},
	[102290510] = { talk_id = 102290510, group_id = 1022905, type = 3, next_id = 102290511, back_text = " ", talk_content = "青乌？", talk_name = "离歌",},
	[102290511] = { talk_id = 102290511, group_id = 1022905, type = 3, next_id = 102290512, back_text = " ", talk_content = "抱歉，我刚刚在考虑月的事情。", talk_name = "青乌",},
	[102290512] = { talk_id = 102290512, group_id = 1022905, type = 1, next_id = 102290513, back_text = " ", talk_content = "青乌……", talk_name = "来来",},
	[102290513] = { talk_id = 102290513, group_id = 1022905, type = 1, next_id = 102290514, talk_content = "青乌！", talk_name = "来来",},
	[102290514] = { talk_id = 102290514, group_id = 1022905, type = 1, next_id = 102290515, talk_content = "啊……", talk_name = "青乌",},
	[102290515] = { talk_id = 102290515, group_id = 1022905, type = 2, options = {{"我或许看见了过去的记忆。",102290516,},{"我感觉有什么人在和我说话。",102290516,},}, talk_content = "你刚刚怎么了，刚去看完宁静的情况回来就发现你呆在这不动。", talk_name = "来来",},
	[102290516] = { talk_id = 102290516, group_id = 1022905, type = 3, next_id = 102290517, talk_content = "刚刚青乌用手去触碰神荼身上掉落的太阳碎片，然后就陷入呆住不动的状态了。", talk_name = "青门隐",},
	[102290517] = { talk_id = 102290517, group_id = 1022905, type = 3, next_id = 102290518, talk_content = "太阳的碎片……我看青乌手上也没有这东西吧？", talk_name = "来来",},
	[102290518] = { talk_id = 102290518, group_id = 1022905, type = 3, next_id = 102290519, talk_content = "被吸收了。", talk_name = "青门隐",},
	[102290519] = { talk_id = 102290519, group_id = 1022905, type = 3, next_id = 102290520, talk_content = "你是说太阳的碎片被我吸收了？", talk_name = "青乌",},
	[102290520] = { talk_id = 102290520, group_id = 1022905, type = 3, next_id = 102290521, talk_content = "听上去就感觉挺危险的，话说丫头你怎么知道那个就是太阳的碎片，太阳会掉碎片吗？", talk_name = "来来",},
	[102290521] = { talk_id = 102290521, group_id = 1022905, type = 3, next_id = 102290522, talk_content = "青门自古就是侍候封侯的一族，我自然知道太阳的事情。", talk_name = "青门隐",},
	[102290522] = { talk_id = 102290522, group_id = 1022905, type = 3, next_id = 102290523, talk_content = "太阳……这么说来青乌曾说过失忆之前的自己是以打倒太阳作为目标的吧。", talk_name = "来来",},
	[102290523] = { talk_id = 102290523, group_id = 1022905, type = 2, options = {{"青乌是？",102290524,},}, talk_content = "等等，我还是第一次听见这件事，打倒太阳，不对吧，青乌可是……", talk_name = "青门隐",},
	[102290524] = { talk_id = 102290524, group_id = 1022905, type = 3, next_id = 102290525, talk_content = "不，现在在这里谈话也不合适，毕竟是敌人的巢穴。", talk_name = "青门隐",},
	[102290525] = { talk_id = 102290525, group_id = 1022905, type = 3, talk_content = "我们先回去吧，之后我会把我知道的事情告诉你的，青乌。", talk_name = "青门隐",},
	[102300101] = { talk_id = 102300101, group_id = 1023001, type = 1, next_id = 102300102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我收到来来提前发的报告了，我没想到青乌你们能在救人的同时解决持名者。", talk_name = "朱嫦",},
	[102300102] = { talk_id = 102300102, group_id = 1023001, type = 2, options = {{"来来呢？",102300103,},{"青门隐呢？",102300111,},}, talk_content = "真的，于公于私我都应该感谢你，青乌。", talk_name = "朱嫦",},
	[102300103] = { talk_id = 102300103, group_id = 1023001, type = 3, next_id = 102300104, talk_content = "现在能做的工作也没几件重要的了，恐怕来来现在在宿舍附近偷懒吧。", talk_name = "朱嫦",},
	[102300104] = { talk_id = 102300104, group_id = 1023001, type = 3, next_id = 102300105, talk_content = "我还以为她是个很勤奋的人。", talk_name = "青乌",},
	[102300105] = { talk_id = 102300105, group_id = 1023001, type = 3, next_id = 102300106, talk_content = "用勤奋来形容她有些不对就是了，哈哈。", talk_name = "朱嫦",},
	[102300106] = { talk_id = 102300106, group_id = 1023001, type = 3, next_id = 102300107, talk_content = "你是说她老是在自由行动这件事？", talk_name = "青乌",},
	[102300107] = { talk_id = 102300107, group_id = 1023001, type = 3, next_id = 102300108, talk_content = "虽然这次是因为她自由行动我们才能顺利度过难关。", talk_name = "朱嫦",},
	[102300108] = { talk_id = 102300108, group_id = 1023001, type = 3, next_id = 102300109, talk_content = "但是她的目标太过远大了，总觉得走错一步就……", talk_name = "朱嫦",},
	[102300109] = { talk_id = 102300109, group_id = 1023001, type = 3, next_id = 102300110, talk_content = "她说过她的目标是人上之人，我听着也觉得不太正经。", talk_name = "朱嫦",},
	[102300110] = { talk_id = 102300110, group_id = 1023001, type = 3, next_id = 102300116, talk_content = "具体为何是这样的目标我就不知道了，或许她有她的考虑吧。", talk_name = "朱嫦",},
	[102300111] = { talk_id = 102300111, group_id = 1023001, type = 3, next_id = 102300112, talk_content = "主祝大人在广场附近，大概在接受居民的问询吧。", talk_name = "朱嫦",},
	[102300112] = { talk_id = 102300112, group_id = 1023001, type = 3, next_id = 102300113, talk_content = "我可以理解成在做问答吗？", talk_name = "青乌",},
	[102300113] = { talk_id = 102300113, group_id = 1023001, type = 3, next_id = 102300114, talk_content = "具体是开解和断定之类的工作，青门是帝江之地历史悠久的一族，深受大家仰仗。", talk_name = "朱嫦",},
	[102300114] = { talk_id = 102300114, group_id = 1023001, type = 3, next_id = 102300115, talk_content = "不过这次的行动我本以为主祝大人会带着其他门人，可实际上来到这里的只有她一个。", talk_name = "朱嫦",},
	[102300115] = { talk_id = 102300115, group_id = 1023001, type = 3, next_id = 102300116, talk_content = "而且在那之前我也没听说过青门会派人来支援……", talk_name = "朱嫦",},
	[102300116] = { talk_id = 102300116, group_id = 1023001, type = 1, next_id = 102300117, talk_content = "事情就先聊到这里，青乌你来这里应该是为了领赏的对吧。", talk_name = "朱嫦",},
	[102300117] = { talk_id = 102300117, group_id = 1023001, type = 1, next_id = 102300118, talk_content = "你的身份证明我已经为你办妥，因为持名者也被解决了，天月城也在有序地陆续开放了。", talk_name = "朱嫦",},
	[102300118] = { talk_id = 102300118, group_id = 1023001, type = 1, next_id = 102300119, talk_content = "也就是说我可以去上面了对吧？", talk_name = "青乌",},
	[102300119] = { talk_id = 102300119, group_id = 1023001, type = 1, next_id = 102300120, talk_content = "是的，不过在那之前主祝大人说让你去见她一面。", talk_name = "朱嫦",},
	[102300120] = { talk_id = 102300120, group_id = 1023001, type = 1, talk_content = "我知道了。", talk_name = "青乌",},
	[102300201] = { talk_id = 102300201, group_id = 1023002, type = 1, next_id = 102300202, fade_in = 1, fade_out = true, hide_player = true, talk_content = "青乌，你来了呀。", talk_name = "青门隐",},
	[102300202] = { talk_id = 102300202, group_id = 1023002, type = 1, next_id = 102300203, talk_content = "你要和我说我的事情，对吧。", talk_name = "青乌",},
	[102300203] = { talk_id = 102300203, group_id = 1023002, type = 1, next_id = 102300204, talk_content = "嗯，因为答应过你想起来就要和你说的。", talk_name = "青门隐",},
	[102300204] = { talk_id = 102300204, group_id = 1023002, type = 1, next_id = 102300205, talk_content = "所以你是在哪里认识我的名号的？", talk_name = "青乌",},
	[102300205] = { talk_id = 102300205, group_id = 1023002, type = 1, next_id = 102300206, talk_content = "在书上，和神荼一样，虽然并不是《帝江事记》，但我的确想起了关于“青乌”的事情。", talk_name = "青门隐",},
	[102300206] = { talk_id = 102300206, group_id = 1023002, type = 2, options = {{"……什么样的英雄？",102300207,},}, talk_content = "直接从结论上说……青乌是太阳大战的英雄。", talk_name = "青门隐",},
	[102300207] = { talk_id = 102300207, group_id = 1023002, type = 3, next_id = 102300208, talk_content = "你在太阳大战中为了让太阳升起而与噬脉生物战斗，最终在筋疲力尽的状态下牺牲了。", talk_name = "青门隐",},
	[102300208] = { talk_id = 102300208, group_id = 1023002, type = 3, next_id = 102300209, talk_content = "这不可能。", talk_name = "青乌",},
	[102300209] = { talk_id = 102300209, group_id = 1023002, type = 3, next_id = 102300210, talk_content = "姑且不论“太阳大战”到底如何，我记得我当初是为了阻止太阳升起才来到这个地方的。", talk_name = "青乌",},
	[102300210] = { talk_id = 102300210, group_id = 1023002, type = 3, next_id = 102300211, talk_content = "嗯，这就是我在那时候感到震惊的一点，但当然还有一个可能性。", talk_name = "青门隐",},
	[102300211] = { talk_id = 102300211, group_id = 1023002, type = 3, next_id = 102300212, talk_content = "是什么？", talk_name = "青乌",},
	[102300212] = { talk_id = 102300212, group_id = 1023002, type = 3, next_id = 102300213, talk_content = "你并不是书中的那个青乌。", talk_name = "青门隐",},
	[102300213] = { talk_id = 102300213, group_id = 1023002, type = 3, next_id = 102300214, talk_content = "先不说你是否复活而来，但自称英雄之名的人并不少见……", talk_name = "青门隐",},
	[102300214] = { talk_id = 102300214, group_id = 1023002, type = 3, next_id = 102300215, talk_content = "当然我并不是说你是仿冒名号的三流，但不这样就无法解释你跟记载之中的矛盾。", talk_name = "青门隐",},
	[102300215] = { talk_id = 102300215, group_id = 1023002, type = 3, next_id = 102300216, talk_content = "这个就只能问书的作者了吧……对了，那本书的名字叫什么？", talk_name = "青乌",},
	[102300216] = { talk_id = 102300216, group_id = 1023002, type = 3, next_id = 102300217, talk_content = "就算描述中的青乌并不是我，但也应该能找到什么相关的信息才对。", talk_name = "青乌",},
	[102300217] = { talk_id = 102300217, group_id = 1023002, type = 3, next_id = 102300218, talk_content = "详细的书籍我可能指不出来，因为英雄本身就记载的比较复杂，实际上在我印象中有好几本书都记录了青乌你的名字。", talk_name = "青门隐",},
	[102300218] = { talk_id = 102300218, group_id = 1023002, type = 3, next_id = 102300219, talk_content = "可是要说最初的出处的话那肯定只有“悬天”那个组织……", talk_name = "青门隐",},
	[102300219] = { talk_id = 102300219, group_id = 1023002, type = 3, next_id = 102300220, talk_content = "到处都是这个名字……", talk_name = "青乌",},
	[102300220] = { talk_id = 102300220, group_id = 1023002, type = 3, next_id = 102300221, talk_content = "你是打算到天上去接触悬天吧，虽然我不建议你这么做，总之你一定要小心。", talk_name = "青门隐",},
	[102300221] = { talk_id = 102300221, group_id = 1023002, type = 3, next_id = 102300222, talk_content = "因为悬天是现在世界对太阳最忠诚的追随者。", talk_name = "青门隐",},
	[102300222] = { talk_id = 102300222, group_id = 1023002, type = 3, talk_content = "我要说的就是这么多，青乌，现在你到大钟悬那边去吧，在顶层有通往天月城的秘法。", talk_name = "青门隐",},
	[102300301] = { talk_id = 102300301, group_id = 1023003, type = 4, next_id = 102300302, aside = "<color=#FFEBA2>青乌：</color>悬天……先去大钟悬吧。", talk_content = "悬天……先去大钟悬吧。", talk_name = "青乌",},
	[102300302] = { talk_id = 102300302, group_id = 1023003, type = 4, next_id = 102300303, aside = "<color=#FFEBA2>青乌：</color>悬天，太阳，英雄……", talk_content = "悬天，太阳，英雄……", talk_name = "青乌",},
	[102300303] = { talk_id = 102300303, group_id = 1023003, type = 4, aside = "<color=#FFEBA2>青乌：</color>到底是我错了，还是这个世界本身……", talk_content = "到底是我错了，还是这个世界本身……", talk_name = "青乌",},
	[102300401] = { talk_id = 102300401, group_id = 1023004, type = 1, next_id = 102300402, fade_in = 1, fade_out = true, hide_npc_list = {8010706,}, hide_player = true, talk_content = "你好，青乌，我是负责引导你使用钟悬移动的人，我叫武安虢。", talk_name = "武安虢",},
	[102300402] = { talk_id = 102300402, group_id = 1023004, type = 1, next_id = 102300403, talk_content = "所以我该怎么使用这个，话说我真能用这个上天吗？", talk_name = "青乌",},
	[102300403] = { talk_id = 102300403, group_id = 1023004, type = 1, next_id = 102300404, talk_content = "帝江的大钟悬经过调整，虽然我说不出来是什么技术……但反正你放心就是。", talk_name = "武安虢",},
	[102300404] = { talk_id = 102300404, group_id = 1023004, type = 1, next_id = 102300405, talk_content = "我们从父辈开始就在使用这样的方式来往天月城和地面了呢。", talk_name = "武安虢",},
	[102300405] = { talk_id = 102300405, group_id = 1023004, type = 1, next_id = 102300406, talk_content = "总之因为持名者被消灭，我们已经重启了移动相关的机构。", talk_name = "武安虢",},
	[102300406] = { talk_id = 102300406, group_id = 1023004, type = 1, next_id = 102300407, talk_content = "青乌，你直接到顶层去吧，在那里向钟悬祷告，然后能直达天月城了。", talk_name = "武安虢",},
	[102300407] = { talk_id = 102300407, group_id = 1023004, type = 1, next_id = 102300408, talk_content = "真的不会有危险吧？", talk_name = "青乌",},
	[102300408] = { talk_id = 102300408, group_id = 1023004, type = 1, talk_content = "没有没有，我敢担保你会喜欢上这种感觉噢。", talk_name = "武安虢",},
	[102300501] = { talk_id = 102300501, group_id = 1023005, type = 1, next_id = 102300502, fade_in = 1, fade_out = true, hide_player = true, talk_content = "下面的人说只要祷告就可以……", talk_name = "青乌",},
	[102300502] = { talk_id = 102300502, group_id = 1023005, type = 1, next_id = 102300503, talk_content = "……", talk_name = "青乌",},
	[102300503] = { talk_id = 102300503, group_id = 1023005, type = 1, next_id = 102300504, talk_content = "我记得自己曾作为悬天的一份子战斗，但最后被离歌背叛。", talk_name = "青乌",},
	[102300504] = { talk_id = 102300504, group_id = 1023005, type = 1, next_id = 102300505, talk_content = "然后再次醒来的时候本想阻止升起的太阳却挂在了天上。", talk_name = "青乌",},
	[102300505] = { talk_id = 102300505, group_id = 1023005, type = 1, next_id = 102300506, talk_content = "在那上面还有一座巨大的城市，然后，青乌这个名字是数百年前被记载的英雄。", talk_name = "青乌",},
	[102300506] = { talk_id = 102300506, group_id = 1023005, type = 1, next_id = 102300507, talk_content = "但“青乌”之所以会成为英雄，是因为保护了太阳。", talk_name = "青乌",},
	[102300507] = { talk_id = 102300507, group_id = 1023005, type = 1, next_id = 102300508, talk_content = "而且在这次的战斗之中所遇见的神荼……他说他见过我。", talk_name = "青乌",},
	[102300508] = { talk_id = 102300508, group_id = 1023005, type = 1, next_id = 102300509, talk_content = "触碰“碎片”以后……我也看见了可能是记忆的画面。", talk_name = "青乌",},
	[102300509] = { talk_id = 102300509, group_id = 1023005, type = 1, next_id = 102300510, talk_content = "同时，悬天现在也是太阳最忠诚的追随者。", talk_name = "青乌",},
	[102300510] = { talk_id = 102300510, group_id = 1023005, type = 1, next_id = 102300511, talk_content = "全部的关键都集中在“悬天”之中。", talk_name = "青乌",},
	[102300511] = { talk_id = 102300511, group_id = 1023005, type = 1, next_id = 102300512, talk_content = "而悬天现在就在天月城中活动。", talk_name = "青乌",},
	[102300512] = { talk_id = 102300512, group_id = 1023005, type = 1, talk_content = "无论如何都要去见一面。", talk_name = "青乌",},
	[102310101] = { talk_id = 102310101, group_id = 1023101, type = 1, next_id = 102310102, fade_in = 1, fade_out = true, hide_player = true, talk_content = "呃……虽说落下来的时候就觉得不太对劲。", talk_name = "青乌",},
	[102310102] = { talk_id = 102310102, group_id = 1023101, type = 1, next_id = 102310103, talk_content = "但这到底是什么。", talk_name = "青乌",},
	[102310103] = { talk_id = 102310103, group_id = 1023101, type = 1, next_id = 102310104, talk_content = "怎么和地上完全不同……", talk_name = "青乌",},
	[102310104] = { talk_id = 102310104, group_id = 1023101, type = 1, next_id = 102310105, talk_content = "而且……真的反过来了，这个世界到底是怎么回事……", talk_name = "青乌",},
	[102310105] = { talk_id = 102310105, group_id = 1023101, type = 1, next_id = 102310106, talk_content = "第一次来天月城的人都会有所困惑，这是正常的，毕竟这是真正反过来的世界。", talk_name = "？？？",},
	[102310106] = { talk_id = 102310106, group_id = 1023101, type = 1, next_id = 102310107, talk_content = "嗯？", talk_name = "青乌",},
	[102310107] = { talk_id = 102310107, group_id = 1023101, type = 1, next_id = 102310108, talk_content = "我听说有一位脉者要在完全开通道路之前插队……所以就是你吗？", talk_name = "？？？",},
	[102310108] = { talk_id = 102310108, group_id = 1023101, type = 2, options = {{"听着好像是大人物。",102310109,},{"（自我介绍）",102310110,},}, talk_content = "我的名字叫追识，是统管帝江维安司的总司长。", talk_name = "追识",},
	[102310109] = { talk_id = 102310109, group_id = 1023101, type = 3, next_id = 102310111, talk_content = "不用太过拘谨，我记得你的登记名是青乌对吧？", talk_name = "追识",},
	[102310110] = { talk_id = 102310110, group_id = 1023101, type = 3, next_id = 102310111, talk_content = "青乌，我会记住的。", talk_name = "追识",},
	[102310111] = { talk_id = 102310111, group_id = 1023101, type = 2, options = {{"下面人手不足。",102310112,},{"要是我不在就危险了。",102310112,},}, talk_content = "你在下面……应该说是上面的行动我都有所耳闻，多亏了你才能迅速解决问题，谢谢。", talk_name = "追识",},
	[102310112] = { talk_id = 102310112, group_id = 1023101, type = 3, next_id = 102310113, talk_content = "你说的问题我们当然明白，但是城内最近有些不平安……不，这不是能对外人说的事情。", talk_name = "追识",},
	[102310113] = { talk_id = 102310113, group_id = 1023101, type = 3, next_id = 102310114, talk_content = "总之我十分感谢你为地上维安司的付出，所以我才会亲自来到这里。", talk_name = "追识",},
	[102310114] = { talk_id = 102310114, group_id = 1023101, type = 2, options = {{"明明有身份证明？",102310115,},{"你想要听些什么？",102310116,},}, talk_content = "然后恕我冒昧，我可以再确认一下你的来历吗？", talk_name = "追识",},
	[102310115] = { talk_id = 102310115, group_id = 1023101, type = 3, next_id = 102310117, talk_content = "是的，这对现在的天月城来说很重要。", talk_name = "追识",},
	[102310116] = { talk_id = 102310116, group_id = 1023101, type = 3, next_id = 102310117, talk_content = "谢谢你的配合。", talk_name = "追识",},
	[102310117] = { talk_id = 102310117, group_id = 1023101, type = 1, next_id = 102310118, talk_content = "首先……不，直接问吧，青乌，你来天月城的目的是什么？", talk_name = "追识",},
	[102310118] = { talk_id = 102310118, group_id = 1023101, type = 1, next_id = 102310119, talk_content = "找回记忆，以及我不知道。", talk_name = "青乌",},
	[102310119] = { talk_id = 102310119, group_id = 1023101, type = 1, next_id = 102310120, talk_content = "找回记忆和不知道吗？", talk_name = "追识",},
	[102310120] = { talk_id = 102310120, group_id = 1023101, type = 1, next_id = 102310121, talk_content = "我失去了关于以前的记忆，当务之急是要找回它们。", talk_name = "青乌",},
	[102310121] = { talk_id = 102310121, group_id = 1023101, type = 1, next_id = 102310122, talk_content = "然后……我得通过找回的记忆来确定自己接下来该怎么办。", talk_name = "青乌",},
	[102310122] = { talk_id = 102310122, group_id = 1023101, type = 1, next_id = 102310123, talk_content = "竟然失去记忆……假若是这样的话或许司警这边能为你提供些帮助。", talk_name = "追识",},
	[102310123] = { talk_id = 102310123, group_id = 1023101, type = 2, options = {{"悬天。",102310124,},{"没有。",102310124,},}, talk_content = "你有什么线索吗？", talk_name = "追识",},
	[102310124] = { talk_id = 102310124, group_id = 1023101, type = 3, next_id = 102310125, talk_content = "（通信声）",},
	[102310125] = { talk_id = 102310125, group_id = 1023101, type = 3, next_id = 102310126, talk_content = "抱歉……有事件发生了，关于你的事情假如哪天遇见我们继续聊吧。", talk_name = "追识",},
	[102310126] = { talk_id = 102310126, group_id = 1023101, type = 3, next_id = 102310127, talk_content = "青乌，在天月城大部分区域都可以自由行动，但你千万记得不要惹事生非。", talk_name = "追识",},
	[102310127] = { talk_id = 102310127, group_id = 1023101, type = 3, talk_content = "不然和维安司合作的巡卫们可就要出手了。", talk_name = "追识",},
	[102310201] = { talk_id = 102310201, group_id = 1023102, type = 4, next_id = 102310202, aside = "<color=#FFEBA2>青乌：</color>没想到一上来就碰见了大人物……", talk_content = "没想到一上来就碰见了大人物……", talk_name = "青乌",},
	[102310202] = { talk_id = 102310202, group_id = 1023102, type = 4, aside = "<color=#FFEBA2>青乌：</color>无论如何，去到处找人问问吧，从来来她们那里听来应该很多人都知道悬天这个组织才对。", talk_content = "无论如何，去到处找人问问吧，从来来她们那里听来应该很多人都知道悬天这个组织才对。", talk_name = "青乌",},
	[102310301] = { talk_id = 102310301, group_id = 1023103, type = 1, next_id = 102310302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，我想问问，你知道悬天吗？", talk_name = "青乌",},
	[102310302] = { talk_id = 102310302, group_id = 1023103, type = 1, next_id = 102310303, talk_content = "最近来找人的人真多哇……", talk_name = "绝音",},
	[102310303] = { talk_id = 102310303, group_id = 1023103, type = 1, next_id = 102310304, talk_content = "你要找什么人来着，悬天是吧，悬天的人我没见过呐。", talk_name = "绝音",},
	[102310304] = { talk_id = 102310304, group_id = 1023103, type = 1, next_id = 102310305, talk_content = "唔……", talk_name = "绝音",},
	[102310305] = { talk_id = 102310305, group_id = 1023103, type = 1, next_id = 102310306, talk_content = "怎么了？", talk_name = "青乌",},
	[102310306] = { talk_id = 102310306, group_id = 1023103, type = 1, next_id = 102310307, talk_content = "白发，有角……之前有个客人似乎在找像你这样的人呢。", talk_name = "绝音",},
	[102310307] = { talk_id = 102310307, group_id = 1023103, type = 1, next_id = 102310308, talk_content = "有人在找我？", talk_name = "青乌",},
	[102310308] = { talk_id = 102310308, group_id = 1023103, type = 1, talk_content = "应该只是认错了而已吧。", talk_name = "绝音",},
	[102310401] = { talk_id = 102310401, group_id = 1023104, type = 1, next_id = 102310402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，我想问些事情。", talk_name = "青乌",},
	[102310402] = { talk_id = 102310402, group_id = 1023104, type = 1, next_id = 102310403, talk_content = "我刚刚看见了，你是用那个复古方法跳过来的对吧？", talk_name = "念溪",},
	[102310403] = { talk_id = 102310403, group_id = 1023104, type = 1, next_id = 102310404, talk_content = "复古？", talk_name = "青乌",},
	[102310404] = { talk_id = 102310404, group_id = 1023104, type = 1, next_id = 102310405, talk_content = "听说最初将天月城和地上的大钟悬连接以后人们都是这样通行的。", talk_name = "念溪",},
	[102310405] = { talk_id = 102310405, group_id = 1023104, type = 1, next_id = 102310406, talk_content = "但后来因为意外频发，终于是改成用有安全措施的座仓，然后逐渐变成汽车之类的。", talk_name = "念溪",},
	[102310406] = { talk_id = 102310406, group_id = 1023104, type = 2, options = {{"我想问点事情。",102310407,},{"反倒悬协会是？",102310408,},}, talk_content = "总之本人真是太感动了，作为反倒悬协会的一员能亲眼看见如此美妙的自由落体。", talk_name = "念溪",},
	[102310407] = { talk_id = 102310407, group_id = 1023104, type = 3, next_id = 102310410, talk_content = "啊，这么说来你好像刚开始也说了这句话。", talk_name = "念溪",},
	[102310408] = { talk_id = 102310408, group_id = 1023104, type = 3, next_id = 102310409, talk_content = "我们反倒悬协会是对一切反转都感兴趣的协会，假如想入会的话欢迎来商业区总部看看哦？", talk_name = "念溪",},
	[102310409] = { talk_id = 102310409, group_id = 1023104, type = 3, next_id = 102310410, talk_content = "我考虑一下吧，倒是我其实想问你些事情。", talk_name = "青乌",},
	[102310410] = { talk_id = 102310410, group_id = 1023104, type = 1, next_id = 102310411, talk_content = "悬天这个组织你有印象吗？", talk_name = "青乌",},
	[102310411] = { talk_id = 102310411, group_id = 1023104, type = 1, next_id = 102310412, talk_content = "悬天我当然知道，要形容那个组织根本就只能用横行霸道来形容，先前才把我们的支部强行封禁。", talk_name = "念溪",},
	[102310412] = { talk_id = 102310412, group_id = 1023104, type = 1, next_id = 102310413, talk_content = "呃……那你知道在哪能找到他们吗？", talk_name = "青乌",},
	[102310413] = { talk_id = 102310413, group_id = 1023104, type = 1, next_id = 102310414, talk_content = "刚刚的话是知道的，因为他们偶尔会聚起来在城里游荡，在几分钟前我在马路附近见到了他们。", talk_name = "念溪",},
	[102310414] = { talk_id = 102310414, group_id = 1023104, type = 1, talk_content = "谢谢。", talk_name = "青乌",},
	[102310501] = { talk_id = 102310501, group_id = 1023105, type = 4, next_id = 102310502, aside = "<color=#FFEBA2>青乌：</color>那个人指的应该是这边，可看着也不像有悬天的人……", talk_content = "那个人指的应该是这边，可看着也不像有悬天的人……", talk_name = "青乌",},
	[102310502] = { talk_id = 102310502, group_id = 1023105, type = 4, aside = "<color=#FFEBA2>青乌：</color>再找人问问吧。", talk_content = "再找人问问吧。", talk_name = "青乌",},
	[102310601] = { talk_id = 102310601, group_id = 1023106, type = 1, next_id = 102310602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，我听说前不久有悬天的人路过，你知道他们现在在哪吗？", talk_name = "青乌",},
	[102310602] = { talk_id = 102310602, group_id = 1023106, type = 1, next_id = 102310603, talk_content = "悬天的话应该是往楼房那边去了。", talk_name = "空青",},
	[102310603] = { talk_id = 102310603, group_id = 1023106, type = 1, talk_content = "谢谢。", talk_name = "青乌",},
	[102310701] = { talk_id = 102310701, group_id = 1023107, type = 4, aside = "<color=#FFEBA2>青乌：</color>走来走去的，得赶紧过去，不然回头又要继续问了。", talk_content = "走来走去的，得赶紧过去，不然回头又要继续问了。", talk_name = "青乌",},
	[102310801] = { talk_id = 102310801, group_id = 1023108, type = 1, next_id = 102310802, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "总感觉又走空了，可是这附近……", talk_name = "青乌",},
	[102310802] = { talk_id = 102310802, group_id = 1023108, type = 1, next_id = 102310803, talk_content = "不，看来没走空呢。", talk_name = "青乌",},
	[102310803] = { talk_id = 102310803, group_id = 1023108, type = 1, next_id = 102310804, talk_content = "青乌。", talk_name = "悬天成员甲",},
	[102310804] = { talk_id = 102310804, group_id = 1023108, type = 1, next_id = 102310805, talk_content = "隔着面具都能感受到你们的想法……我不是来打架的。", talk_name = "青乌",},
	[102310805] = { talk_id = 102310805, group_id = 1023108, type = 1, next_id = 102310806, talk_content = "我想要知道自己到底是谁，你们，悬天就是我唯一的线索。", talk_name = "青乌",},
	[102310806] = { talk_id = 102310806, group_id = 1023108, type = 1, next_id = 102310807, talk_content = "我们收到的命令是找到并且将你暗中处理，没想到你竟然会亲自送上门来。", talk_name = "悬天成员甲",},
	[102310807] = { talk_id = 102310807, group_id = 1023108, type = 1, next_id = 102310808, talk_content = "虽然谈话不怎么顺利，但总归是确认到你们的确知道我是谁了。", talk_name = "青乌",},
	[102310808] = { talk_id = 102310808, group_id = 1023108, type = 1, talk_content = "倒在这里吧，青乌。", talk_name = "悬天成员甲",},
	[102310901] = { talk_id = 102310901, group_id = 1023109, type = 1, next_id = 102310902, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "还有吗……", talk_name = "青乌",},
	[102310902] = { talk_id = 102310902, group_id = 1023109, type = 2, options = {{"那你是？",102310903,},{"我也想好好谈话就是了。",102310904,},}, talk_content = "等等，我可不是你的敌人！", talk_name = "义向",},
	[102310903] = { talk_id = 102310903, group_id = 1023109, type = 3, next_id = 102310905, talk_content = "幸好，还以为会像以往那样打起来。", talk_name = "义向",},
	[102310904] = { talk_id = 102310904, group_id = 1023109, type = 3, next_id = 102310905, talk_content = "对吧对吧。", talk_name = "义向",},
	[102310905] = { talk_id = 102310905, group_id = 1023109, type = 1, next_id = 102310906, talk_content = "自我介绍一下吧，我的名字叫义向，向义而生的义向。", talk_name = "义向",},
	[102310906] = { talk_id = 102310906, group_id = 1023109, type = 1, next_id = 102310907, talk_content = "我想要知道的是你为什么要接近我。", talk_name = "青乌",},
	[102310907] = { talk_id = 102310907, group_id = 1023109, type = 1, next_id = 102310908, talk_content = "那当然是来救你的咯，虽说你貌似把人都打翻了。", talk_name = "义向",},
	[102310908] = { talk_id = 102310908, group_id = 1023109, type = 1, next_id = 102310909, talk_content = "救我……你认识我吗？", talk_name = "青乌",},
	[102310909] = { talk_id = 102310909, group_id = 1023109, type = 1, next_id = 102310910, talk_content = "我只是在调查悬天的过程中瞟见了关于你的信息，你不是正在被他们通缉吗？", talk_name = "义向",},
	[102310910] = { talk_id = 102310910, group_id = 1023109, type = 1, next_id = 102310911, talk_content = "通缉……等等，你说你在调查悬天吗？", talk_name = "青乌",},
	[102310911] = { talk_id = 102310911, group_id = 1023109, type = 1, next_id = 102310912, talk_content = "你问题好多啊，要聊的话找个空阔的地方再聊吧，这里也不好谈话。", talk_name = "义向",},
	[102310912] = { talk_id = 102310912, group_id = 1023109, type = 1, talk_content = "好吧。", talk_name = "青乌",},
	[102320101] = { talk_id = 102320101, group_id = 1023201, type = 2, fade_in = 1, fade_out = true, hide_player = true, options = {{"继续刚才的话题？",102320103,},{"我觉得这里也不安全就是了。",102320102,},}, talk_content = "嗯嗯，藏木于林，这里的话应该就不用害怕被攻击了。", talk_name = "义向",},
	[102320102] = { talk_id = 102320102, group_id = 1023201, type = 3, next_id = 102320103, talk_content = "虽然悬天不是什么好人，但他们也不会大庭广众展开追捕行动。", talk_name = "义向",},
	[102320103] = { talk_id = 102320103, group_id = 1023201, type = 1, next_id = 102320104, talk_content = "那继续说刚才的事情吧，我义向跟随大哥创建了一个叫创生主义的组织。", talk_name = "义向",},
	[102320104] = { talk_id = 102320104, group_id = 1023201, type = 1, next_id = 102320105, talk_content = "组织的目的……呃，其实我也不太理解，不过大哥说那能帮助到别人就是了。", talk_name = "义向",},
	[102320105] = { talk_id = 102320105, group_id = 1023201, type = 1, next_id = 102320106, talk_content = "然后分给我的任务就是调查悬天，于是在调查悬天的过程中就发现了青乌你这个被他们通缉的人物。", talk_name = "义向",},
	[102320106] = { talk_id = 102320106, group_id = 1023201, type = 1, next_id = 102320107, talk_content = "除了通缉以外还有什么关于我的发现吗？", talk_name = "青乌",},
	[102320107] = { talk_id = 102320107, group_id = 1023201, type = 1, next_id = 102320108, talk_content = "没有哦，也就只有特征描述而已。", talk_name = "义向",},
	[102320108] = { talk_id = 102320108, group_id = 1023201, type = 1, next_id = 102320109, talk_content = "但师傅说能救便救，所以我就在城中四处寻找，想要在悬天发现你之前将你保护起来。", talk_name = "义向",},
	[102320109] = { talk_id = 102320109, group_id = 1023201, type = 1, next_id = 102320110, talk_content = "于是乎就出现了刚才那一幕……", talk_name = "青乌",},
	[102320110] = { talk_id = 102320110, group_id = 1023201, type = 2, options = {{"什么问题？",102320111,},{"太隐私的问题我可不回答。",102320111,},}, talk_content = "反正你没事是再好不过了，接下来能请你回答我的问题吗？", talk_name = "义向",},
	[102320111] = { talk_id = 102320111, group_id = 1023201, type = 3, next_id = 102320112, talk_content = "我只是想问问你为什么会被他们通缉而已，难不成你也在调查悬天？", talk_name = "义向",},
	[102320112] = { talk_id = 102320112, group_id = 1023201, type = 3, next_id = 102320113, talk_content = "我的确是在调查悬天，但我应该不是因为这个而被通缉的。", talk_name = "青乌",},
	[102320113] = { talk_id = 102320113, group_id = 1023201, type = 3, next_id = 102320114, talk_content = "那是什么？", talk_name = "义向",},
	[102320114] = { talk_id = 102320114, group_id = 1023201, type = 3, next_id = 102320115, talk_content = "不知道……我失去了记忆，而悬天是我找回记忆的唯一线索。", talk_name = "青乌",},
	[102320115] = { talk_id = 102320115, group_id = 1023201, type = 3, next_id = 102320116, talk_content = "呃，这下子我不知道该说什么好了，本以为你掌握了什么核心秘密。", talk_name = "义向",},
	[102320116] = { talk_id = 102320116, group_id = 1023201, type = 3, next_id = 102320117, talk_content = "也罢，既然你我都要调查悬天那来组个队如何。", talk_name = "义向",},
	[102320117] = { talk_id = 102320117, group_id = 1023201, type = 3, next_id = 102320118, talk_content = "那要看是为什么组队了。", talk_name = "青乌",},
	[102320118] = { talk_id = 102320118, group_id = 1023201, type = 2, options = {{"要把东西抢过来吗？",102320119,},{"听着觉得有点超出“调查”了。",102320119,},}, talk_content = "我之前就盯上了一个悬天聚集的地方，那边今天好像要交接点什么东西。", talk_name = "义向",},
	[102320119] = { talk_id = 102320119, group_id = 1023201, type = 3, next_id = 102320120, talk_content = "师傅说过，邪不胜正，因为我们是正，所以这么做肯定是没问题的。", talk_name = "义向",},
	[102320120] = { talk_id = 102320120, group_id = 1023201, type = 3, talk_content = "听着你师傅还挺自由的……", talk_name = "青乌",},
	[102320201] = { talk_id = 102320201, group_id = 1023202, type = 4, next_id = 102320202, aside = "<color=#FFEBA2>义向：</color>青乌，聚集点我给你标好了！", talk_content = "青乌，聚集点我给你标好了！", talk_name = "义向",},
	[102320202] = { talk_id = 102320202, group_id = 1023202, type = 4, next_id = 102320203, aside = "<color=#FFEBA2>义向：</color>嘿嘿，果然就像师傅说的那样，助人就等于助己，有青乌你在这下调查也能更顺利了。", talk_content = "嘿嘿，果然就像师傅说的那样，助人就等于助己，有青乌你在这下调查也能更顺利了。", talk_name = "义向",},
	[102320203] = { talk_id = 102320203, group_id = 1023202, type = 4, aside = "<color=#FFEBA2>青乌：</color>说得我都有些不好意思了。", talk_content = "说得我都有些不好意思了。", talk_name = "青乌",},
	[102320301] = { talk_id = 102320301, group_id = 1023203, type = 2, options = {{"有噬脉生物……",102320302,},{"要直接冲进去吗？",102320305,},}, talk_content = "青乌，那边。", talk_name = "义向",},
	[102320302] = { talk_id = 102320302, group_id = 1023203, type = 3, next_id = 102320303, talk_content = "嗯，那个看着也不像是佩从……", talk_name = "义向",},
	[102320303] = { talk_id = 102320303, group_id = 1023203, type = 3, next_id = 102320304, talk_content = "果然就像大哥之前说的那样，悬天似乎有某种控制噬脉生物的方法。", talk_name = "义向",},
	[102320304] = { talk_id = 102320304, group_id = 1023203, type = 3, talk_content = "不过畏首畏尾是不义，就算敌人有多强，一旦决定要上那就只有上！", talk_name = "义向",},
	[102320305] = { talk_id = 102320305, group_id = 1023203, type = 3, next_id = 102320306, talk_content = "唉？", talk_name = "义向",},
	[102320306] = { talk_id = 102320306, group_id = 1023203, type = 3, next_id = 102320307, talk_content = "怎么了，不上吗？", talk_name = "青乌",},
	[102320307] = { talk_id = 102320307, group_id = 1023203, type = 3, talk_content = "我还以为青乌你是那种比较谨慎的人，难怪我觉得我两肯定合得来。", talk_name = "义向",},
	[102320401] = { talk_id = 102320401, group_id = 1023204, type = 4, next_id = 102320402, aside = "<color=#FFEBA2>悬天成员甲：</color>果然被盯上了吗！", talk_content = "果然被盯上了吗！", talk_name = "悬天成员甲",},
	[102320402] = { talk_id = 102320402, group_id = 1023204, type = 4, next_id = 102320403, aside = "<color=#FFEBA2>悬天成员乙：</color>袭击者里面……那个不是青乌吗！", talk_content = "袭击者里面……那个不是青乌吗！", talk_name = "悬天成员乙",},
	[102320403] = { talk_id = 102320403, group_id = 1023204, type = 4, aside = "<color=#FFEBA2>青乌：</color>这么说来今天一天我都在自投罗网呢。", talk_content = "这么说来今天一天我都在自投罗网呢。", talk_name = "青乌",},
	[102320501] = { talk_id = 102320501, group_id = 1023205, type = 4, aside = "<color=#FFEBA2>义向：</color>青乌，那个应该就是他们准备交接的东西，去看看吧。", talk_content = "青乌，那个应该就是他们准备交接的东西，去看看吧。", talk_name = "义向",},
	[102320601] = { talk_id = 102320601, group_id = 1023206, type = 2, fade_in = 1, fade_out = true, hide_player = true, options = {{"打开看看？",102320602,},{"武器都有些什么？",102320603,},}, talk_content = "里面的是武器……还有些记录的档案。", talk_name = "义向",},
	[102320602] = { talk_id = 102320602, group_id = 1023206, type = 3, next_id = 102320604, talk_content = "唉，我可不想看字……但是不读是不义，唉。", talk_name = "义向",},
	[102320603] = { talk_id = 102320603, group_id = 1023206, type = 3, next_id = 102320604, talk_content = "看着是些标准的枪械，不过数量有些多，至于档案……", talk_name = "义向",},
	[102320604] = { talk_id = 102320604, group_id = 1023206, type = 1, next_id = 102320605, talk_content = "档案里面写了什么吗。", talk_name = "青乌",},
	[102320605] = { talk_id = 102320605, group_id = 1023206, type = 1, next_id = 102320606, talk_content = "和之前一样，里面夹杂着你的通缉信息。", talk_name = "义向",},
	[102320606] = { talk_id = 102320606, group_id = 1023206, type = 1, next_id = 102320607, talk_content = "难不成这东西被分发到每一个悬天成员手下了吧。", talk_name = "青乌",},
	[102320607] = { talk_id = 102320607, group_id = 1023206, type = 1, next_id = 102320608, talk_content = "或许是这样也说不定，然后另外的就是……", talk_name = "义向",},
	[102320608] = { talk_id = 102320608, group_id = 1023206, type = 2, options = {{"多少钱？",102320609,},{"要卖给谁吗？",102320610,},}, talk_content = "这些武器的交易价格。", talk_name = "义向",},
	[102320609] = { talk_id = 102320609, group_id = 1023206, type = 3, next_id = 102320611, talk_content = "每把2000约币……不对，这里应该要注意的应该是买方吧！", talk_name = "义向",},
	[102320610] = { talk_id = 102320610, group_id = 1023206, type = 3, next_id = 102320611, talk_content = "这目录还挺长的，我翻翻看。", talk_name = "义向",},
	[102320611] = { talk_id = 102320611, group_id = 1023206, type = 1, next_id = 102320612, talk_content = "卖方的名字被擦除了……然后买方是“恶索帮”。", talk_name = "义向",},
	[102320612] = { talk_id = 102320612, group_id = 1023206, type = 1, next_id = 102320613, talk_content = "听着像是一个组织。", talk_name = "青乌",},
	[102320613] = { talk_id = 102320613, group_id = 1023206, type = 1, talk_content = "嗯，是天月城道上的一个帮派，我们创生主义也和他们打过交道。", talk_name = "义向",},
	[102320701] = { talk_id = 102320701, group_id = 1023207, type = 1, next_id = 102320702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "接到通报所以才来看看，没想到竟然人赃并获。", talk_name = "众一桃夭",},
	[102320702] = { talk_id = 102320702, group_id = 1023207, type = 1, next_id = 102320703, talk_content = "你们就是最近准备袭击这座城市的人，对吧。", talk_name = "众一桃夭",},
	[102320703] = { talk_id = 102320703, group_id = 1023207, type = 1, talk_content = "啊？", talk_name = "两人",},
	[102320801] = { talk_id = 102320801, group_id = 1023208, type = 2, fade_in = 1, fade_out = true, hide_player = true, options = {{"有噬脉生物……",102320802,},{"要直接冲进去吗？",102320805,},}, talk_content = "青乌，那边。", talk_name = "青乌",},
	[102320802] = { talk_id = 102320802, group_id = 1023208, type = 3, next_id = 102320803, talk_content = "嗯，那个看着也不像是佩从……", talk_name = "义向",},
	[102320803] = { talk_id = 102320803, group_id = 1023208, type = 3, next_id = 102320804, talk_content = "果然就像大哥之前说的那样，悬天似乎有某种控制噬脉生物的方法。", talk_name = "义向",},
	[102320804] = { talk_id = 102320804, group_id = 1023208, type = 3, next_id = 102320808, talk_content = "不过畏首畏尾是不义，就算敌人有多强，一旦决定要上那就只有上！", talk_name = "义向",},
	[102320805] = { talk_id = 102320805, group_id = 1023208, type = 3, next_id = 102320806, talk_content = "唉？", talk_name = "义向",},
	[102320806] = { talk_id = 102320806, group_id = 1023208, type = 3, next_id = 102320807, talk_content = "怎么了，不上吗？", talk_name = "青乌",},
	[102320807] = { talk_id = 102320807, group_id = 1023208, type = 3, next_id = 102320808, talk_content = "我还以为青乌你是那种比较谨慎的人，难怪我觉得我两肯定合得来。", talk_name = "义向",},
	[102320808] = { talk_id = 102320808, group_id = 1023208, type = 2, options = {{"有的。",102320809,},{"我得看看。",102320810,},}, talk_content = "不过直接冲过去可能会吓走他们，青乌，你身上肯定带着能穿越的佩从对不。", talk_name = "义向",},
	[102320809] = { talk_id = 102320809, group_id = 1023208, type = 3, talk_content = "那就好办了。", talk_name = "义向",},
	[102320810] = { talk_id = 102320810, group_id = 1023208, type = 3, talk_content = "嗯，先看看吧。", talk_name = "义向",},
	[102320901] = { talk_id = 102320901, group_id = 1023209, type = 5, aside = "<color=#FFEBA2>义向：</color>那里有可以通到上面的管道，去打他们个措手不及吧！", fade_in = 1, fade_out = true, hide_player = true, talk_content = "那里有可以通到上面的管道，去打他们个措手不及吧！", talk_name = "义向",},
	[102321001] = { talk_id = 102321001, group_id = 1023210, type = 5, next_id = 102321002, aside = "<color=#FFEBA2>义向：</color>对了，青乌，刚刚的噬脉生物的残留似乎能凝聚成玉。", fade_in = 1, fade_out = true, hide_player = true, talk_content = "对了，青乌，刚刚的噬脉生物的残留似乎能凝聚成玉。", talk_name = "义向",},
	[102321002] = { talk_id = 102321002, group_id = 1023210, type = 5, aside = "<color=#FFEBA2>青乌：</color>东西越多越好，我来吧。", talk_content = "东西越多越好，我来吧。", talk_name = "青乌",},
	[102321101] = { talk_id = 102321101, group_id = 1023211, type = 4, next_id = 102321102, aside = "<color=#FFEBA2>义向：</color>“连接”啊，我有个熟人也经常使用这种能力。", talk_content = "“连接”啊，我有个熟人也经常使用这种能力。", talk_name = "义向",},
	[102321102] = { talk_id = 102321102, group_id = 1023211, type = 4, aside = "<color=#FFEBA2>义向：</color>青乌，试试用这个能力来控制路上的监视器吧。", talk_content = "青乌，试试用这个能力来控制路上的监视器吧。", talk_name = "义向",},
	[102321201] = { talk_id = 102321201, group_id = 1023212, type = 4, next_id = 102321202, aside = "<color=#FFEBA2>义向：</color>成功了！我那个熟人之前跟我说过可以通过连接来进入各种各样的设备和仪器。", talk_content = "成功了！我那个熟人之前跟我说过可以通过连接来进入各种各样的设备和仪器。", talk_name = "义向",},
	[102321202] = { talk_id = 102321202, group_id = 1023212, type = 4, aside = "<color=#FFEBA2>义向：</color>虽然我觉得这种类似偷窥的行为多少有些不义就是了。", talk_content = "虽然我觉得这种类似偷窥的行为多少有些不义就是了。", talk_name = "义向",},
	[102321301] = { talk_id = 102321301, group_id = 1023213, type = 4, next_id = 102321302, aside = "<color=#FFEBA2>义向：</color>青乌，去下个地方吧。", talk_content = "青乌，去下个地方吧。", talk_name = "义向",},
	[102321302] = { talk_id = 102321302, group_id = 1023213, type = 4, next_id = 102321303, aside = "<color=#FFEBA2>青乌：</color>我还以为这样就算结束了。", talk_content = "我还以为这样就算结束了。", talk_name = "青乌",},
	[102321303] = { talk_id = 102321303, group_id = 1023213, type = 4, aside = "<color=#FFEBA2>义向：</color>但是你还没有得到让你满意的情报，我也是不能就这样回去交差。", talk_content = "但是你还没有得到让你满意的情报，我也是不能就这样回去交差。", talk_name = "义向",},
	[102321401] = { talk_id = 102321401, group_id = 1023214, type = 1, next_id = 102321402, fade_in = 1, fade_out = true, hide_player = true, talk_content = "这里的人是不是都特别喜欢楼顶？", talk_name = "青乌",},
	[102321402] = { talk_id = 102321402, group_id = 1023214, type = 1, next_id = 102321403, talk_content = "我听说的确有些人认为靠近大地才是我们人类应有的生活方式。", talk_name = "义向",},
	[102321403] = { talk_id = 102321403, group_id = 1023214, type = 1, next_id = 102321404, talk_content = "但我觉得悬天的人应该不是那种浪漫主义者就是了。", talk_name = "义向",},
	[102321404] = { talk_id = 102321404, group_id = 1023214, type = 1, next_id = 102321405, talk_content = "对了，青乌，接下来要不要试试飞行？", talk_name = "义向",},
	[102321405] = { talk_id = 102321405, group_id = 1023214, type = 1, next_id = 102321406, talk_content = "滑翔的话我已经会了。", talk_name = "青乌",},
	[102321406] = { talk_id = 102321406, group_id = 1023214, type = 1, talk_content = "是飞行，青乌你就先试试看吧。", talk_name = "义向",},
	[102321501] = { talk_id = 102321501, group_id = 1023215, type = 4, next_id = 102321502, aside = "<color=#FFEBA2>青乌：</color>世界之大，无奇不有。", talk_content = "世界之大，无奇不有。", talk_name = "青乌",},
	[102321502] = { talk_id = 102321502, group_id = 1023215, type = 4, aside = "<color=#FFEBA2>义向：</color>老实说我可喜欢这个了。", talk_content = "老实说我可喜欢这个了。", talk_name = "义向",},
	[102321601] = { talk_id = 102321601, group_id = 1023216, type = 1, next_id = 102321602, fade_in = 1, fade_out = true, hide_player = true, talk_content = "义向，所以这里有什么东西吗？", talk_name = "青乌",},
	[102321602] = { talk_id = 102321602, group_id = 1023216, type = 1, next_id = 102321603, talk_content = "不……感觉走空了。", talk_name = "义向",},
	[102321603] = { talk_id = 102321603, group_id = 1023216, type = 1, next_id = 102321604, talk_content = "虽然因为我人生地不熟，而且与悬天接触也合我意，所以我不介意与你再走几趟。", talk_name = "青乌",},
	[102321604] = { talk_id = 102321604, group_id = 1023216, type = 1, next_id = 102321605, talk_content = "但是能和我说说你现在到底想找什么吗？", talk_name = "青乌",},
	[102321605] = { talk_id = 102321605, group_id = 1023216, type = 1, next_id = 102321606, talk_content = "嗯……我在找天月城的秘密。", talk_name = "义向",},
	[102321606] = { talk_id = 102321606, group_id = 1023216, type = 1, next_id = 102321607, talk_content = "秘密……？", talk_name = "青乌",},
	[102321607] = { talk_id = 102321607, group_id = 1023216, type = 1, next_id = 102321608, talk_content = "按大哥的说法就是调查都市中的各种传说和流言，大哥似乎认为这中间藏着天月城的秘密。", talk_name = "义向",},
	[102321608] = { talk_id = 102321608, group_id = 1023216, type = 1, next_id = 102321609, talk_content = "然后因为悬天是这个城市中最显眼，并且拥有最大自由的权的团体，所以大哥才让我以悬天为中心调查的。", talk_name = "义向",},
	[102321609] = { talk_id = 102321609, group_id = 1023216, type = 1, next_id = 102321610, talk_content = "你们找到秘密以后想做什么？", talk_name = "青乌",},
	[102321610] = { talk_id = 102321610, group_id = 1023216, type = 1, next_id = 102321611, talk_content = "大哥说……想扫清这座天月城的黑暗。", talk_name = "义向",},
	[102321611] = { talk_id = 102321611, group_id = 1023216, type = 1, next_id = 102321612, talk_content = "（通信音）",},
	[102321612] = { talk_id = 102321612, group_id = 1023216, type = 1, next_id = 102321613, talk_content = "嗯，大哥……明白了，我马上去！", talk_name = "义向",},
	[102321613] = { talk_id = 102321613, group_id = 1023216, type = 2, options = {{"你的大哥吗？",102321614,},{"有什么事情发生了吗？",102321614,},}, talk_content = "对了，等下和你介绍个人。", talk_name = "义向",},
	[102321614] = { talk_id = 102321614, group_id = 1023216, type = 3, next_id = 102321615, talk_content = "嗯，大哥说他发现了一批从地上来天月城的人，里面有个叫离歌的肯定会知道些什么。", talk_name = "义向",},
	[102321615] = { talk_id = 102321615, group_id = 1023216, type = 3, talk_content = "离歌！？", talk_name = "青乌",},
	[102321701] = { talk_id = 102321701, group_id = 1023217, type = 4, next_id = 102321702, aside = "<color=#FFEBA2>青乌：</color>义向，人在哪里？", talk_content = "义向，人在哪里？", talk_name = "青乌",},
	[102321702] = { talk_id = 102321702, group_id = 1023217, type = 4, next_id = 102321703, aside = "<color=#FFEBA2>义向：</color>在广场附近，沿着路一路过去吧。", talk_content = "在广场附近，沿着路一路过去吧。", talk_name = "义向",},
	[102321703] = { talk_id = 102321703, group_id = 1023217, type = 4, aside = "<color=#FFEBA2>义向：</color>青乌你好像挺急的？", talk_content = "青乌你好像挺急的？", talk_name = "义向",},
	[102321801] = { talk_id = 102321801, group_id = 1023218, type = 4, next_id = 102321802, aside = "<color=#FFEBA2>悬天成员甲：</color>果然，青乌已经进城了！", talk_content = "果然，青乌已经进城了！", talk_name = "悬天成员甲",},
	[102321802] = { talk_id = 102321802, group_id = 1023218, type = 4, next_id = 102321803, aside = "<color=#FFEBA2>悬天成员甲：</color>拦住青乌！", talk_content = "拦住青乌！", talk_name = "悬天成员甲",},
	[102321803] = { talk_id = 102321803, group_id = 1023218, type = 4, aside = "<color=#FFEBA2>青乌：</color>别挡路！", talk_content = "别挡路！", talk_name = "青乌",},
	[102321901] = { talk_id = 102321901, group_id = 1023219, type = 4, next_id = 102321902, aside = "<color=#FFEBA2>青乌：</color>喂，你们知道离歌在哪对吧！", talk_content = "喂，你们知道离歌在哪对吧！", talk_name = "青乌",},
	[102321902] = { talk_id = 102321902, group_id = 1023219, type = 4, next_id = 102321903, aside = "<color=#FFEBA2>悬天成员甲：</color>区区青乌也想要靠近七政……", talk_content = "区区青乌也想要靠近七政……", talk_name = "悬天成员甲",},
	[102321903] = { talk_id = 102321903, group_id = 1023219, type = 4, aside = "<color=#FFEBA2>青乌：</color>果然知道！", talk_content = "果然知道！", talk_name = "青乌",},
	[102322001] = { talk_id = 102322001, group_id = 1023220, type = 1, next_id = 102322002, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "这里是？", talk_name = "青乌",},
	[102322002] = { talk_id = 102322002, group_id = 1023220, type = 1, next_id = 102322003, talk_content = "星落广场，大哥说的就是这里。", talk_name = "义向",},
	[102322003] = { talk_id = 102322003, group_id = 1023220, type = 1, next_id = 102322004, talk_content = "哪里也没看见离歌。", talk_name = "青乌",},
	[102322004] = { talk_id = 102322004, group_id = 1023220, type = 1, next_id = 102322005, talk_content = "大哥说他现在有别的事情要办，但是在地上留下了印记。", talk_name = "义向",},
	[102322005] = { talk_id = 102322005, group_id = 1023220, type = 1, talk_content = "或许我们能通过印记找到你想要找的那个人也说不定。", talk_name = "义向",},
	[102322101] = { talk_id = 102322101, group_id = 1023221, type = 4, aside = "<color=#FFEBA2>义向：</color>印记……啊，在这边，青乌。", talk_content = "印记……啊，在这边，青乌。", talk_name = "义向",},
	[102322201] = { talk_id = 102322201, group_id = 1023222, type = 4, aside = "<color=#FFEBA2>义向：</color>留言里面的内容似乎指向了那个商人，要不要聊聊看？", talk_content = "留言里面的内容似乎指向了那个商人，要不要聊聊看？", talk_name = "义向",},
	[102322301] = { talk_id = 102322301, group_id = 1023223, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"暂时不用。",102322302,},{"离歌在哪？",102322303,},}, talk_content = "客人你好，请问需要什么东西吗？", talk_name = "商人",},
	[102322302] = { talk_id = 102322302, group_id = 1023223, type = 3, talk_content = "哦，欢迎下次光临。", talk_name = "商人",},
	[102322303] = { talk_id = 102322303, group_id = 1023223, type = 3, next_id = 102322304, talk_content = "有你这么直接问的吗？", talk_name = "义向",},
	[102322304] = { talk_id = 102322304, group_id = 1023223, type = 3, next_id = 102322305, talk_content = "虽然也没打算隐藏，不过没想到青乌如此直白。", talk_name = "商人",},
	[102322305] = { talk_id = 102322305, group_id = 1023223, type = 3, next_id = 102322306, talk_content = "本打算等其他成员赶来一起围捕，但看来计划赶不上变化。", talk_name = "商人",},
	[102322306] = { talk_id = 102322306, group_id = 1023223, type = 3, talk_content = "接招吧，青乌。", talk_name = "商人",},
	[102322401] = { talk_id = 102322401, group_id = 1023224, type = 1, next_id = 102322402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "青乌，刚刚听见那个叫离歌的你突然变了脸，那个人是谁？", talk_name = "义向",},
	[102322402] = { talk_id = 102322402, group_id = 1023224, type = 1, next_id = 102322403, talk_content = "或许是我以前的朋友。", talk_name = "青乌",},
	[102322403] = { talk_id = 102322403, group_id = 1023224, type = 1, next_id = 102322404, talk_content = "或许？", talk_name = "义向",},
	[102322404] = { talk_id = 102322404, group_id = 1023224, type = 1, next_id = 102322405, talk_content = "是我自己的问题，义向，我想要找到那个叫离歌的人。", talk_name = "青乌",},
	[102322405] = { talk_id = 102322405, group_id = 1023224, type = 1, next_id = 102322406, talk_content = "嗯，大哥也让我去找她。", talk_name = "义向",},
	[102322406] = { talk_id = 102322406, group_id = 1023224, type = 1, talk_content = "那接下来再看看哪里有线索吧。", talk_name = "义向",},
	[102330101] = { talk_id = 102330101, group_id = 1023301, type = 5, next_id = 102330102, aside = "<color=#FFEBA2>喜大：</color>嘻嘻嘻，离歌你投资又失败啦！", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "嘻嘻嘻，离歌你投资又失败啦！", talk_name = "喜大",},
	[102330102] = { talk_id = 102330102, group_id = 1023301, type = 5, next_id = 102330103, aside = "<color=#FFEBA2>喜大：</color>这次亏多少了，应该不是一千这种寒酸数吧，一万，该不会说是百……哇。", talk_content = "这次亏多少了，应该不是一千这种寒酸数吧，一万，该不会说是百……哇。", talk_name = "喜大",},
	[102330103] = { talk_id = 102330103, group_id = 1023301, type = 5, next_id = 102330104, aside = "<color=#FFEBA2>离歌：</color>就没办法调整一下这只小鬼吗，小五。", talk_content = "就没办法调整一下这只小鬼吗，小五。", talk_name = "离歌",},
	[102330104] = { talk_id = 102330104, group_id = 1023301, type = 5, next_id = 102330105, aside = "<color=#FFEBA2>喜大：</color>嘿嘿，毕竟小哥我的帅是天生的。", talk_content = "嘿嘿，毕竟小哥我的帅是天生的。", talk_name = "喜大",},
	[102330105] = { talk_id = 102330105, group_id = 1023301, type = 5, aside = "<color=#FFEBA2>离歌：</color>总之……欢迎回来，青乌。", talk_content = "总之……欢迎回来，青乌。", talk_name = "离歌",},
	[102330201] = { talk_id = 102330201, group_id = 1023302, type = 4, next_id = 102330202, aside = "<color=#FFEBA2>离歌：</color>青乌，这次的车可不会坏了。", call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "青乌，这次的车可不会坏了。", talk_name = "离歌",},
	[102330202] = { talk_id = 102330202, group_id = 1023302, type = 4, aside = "<color=#FFEBA2>青乌：</color>你特意强调这个让我有些担心。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "你特意强调这个让我有些担心。", talk_name = "青乌",},
	[102330301] = { talk_id = 102330301, group_id = 1023303, type = 1, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我的东西被抢了，有人吗！", talk_name = "市民",},
	[102330401] = { talk_id = 102330401, group_id = 1023304, type = 4, next_id = 102330402, aside = "<color=#FFEBA2>离歌：</color>伸张正义也是做大事者的必修课。", talk_content = "伸张正义也是做大事者的必修课。", talk_name = "离歌",},
	[102330402] = { talk_id = 102330402, group_id = 1023304, type = 4, next_id = 102330403, aside = "<color=#FFEBA2>离歌：</color>青乌，帮一下这人吧。", talk_content = "青乌，帮一下这人吧。", talk_name = "离歌",},
	[102330403] = { talk_id = 102330403, group_id = 1023304, type = 4, aside = "<color=#FFEBA2>青乌：</color>你到底都是怎么看见这些的……", talk_content = "你到底都是怎么看见这些的……", talk_name = "青乌",},
	[102330501] = { talk_id = 102330501, group_id = 1023305, type = 1, next_id = 102330502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我把东西拿回来了，是这个没错吧？", talk_name = "青乌",},
	[102330502] = { talk_id = 102330502, group_id = 1023305, type = 1, next_id = 102330503, talk_content = "……啊，哎？真有人帮我拿回来啊？", talk_name = "市民",},
	[102330503] = { talk_id = 102330503, group_id = 1023305, type = 1, next_id = 102330504, talk_content = "你也不用这么震惊吧。", talk_name = "青乌",},
	[102330504] = { talk_id = 102330504, group_id = 1023305, type = 1, talk_content = "因为最近……不，真是谢谢你了！", talk_name = "市民",},
	[102330601] = { talk_id = 102330601, group_id = 1023306, type = 5, next_id = 102330602, aside = "<color=#FFEBA2>青乌：</color>这地方还真破烂。", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "这地方还真破烂。", talk_name = "青乌",},
	[102330602] = { talk_id = 102330602, group_id = 1023306, type = 5, next_id = 102330603, aside = "<color=#FFEBA2>青乌：</color>啊，那几个人应该就是……", talk_content = "啊，那几个人应该就是……", talk_name = "青乌",},
	[102330603] = { talk_id = 102330603, group_id = 1023306, type = 5, next_id = 102330604, aside = "<color=#FFEBA2>？？？：</color>抱歉，我来晚了吗？", talk_content = "抱歉，我来晚了吗？", talk_name = "？？？",},
	[102330604] = { talk_id = 102330604, group_id = 1023306, type = 5, next_id = 102330605, aside = "<color=#FFEBA2>青乌：</color>呃，你也是委托人？", talk_content = "呃，你也是委托人？", talk_name = "青乌",},
	[102330605] = { talk_id = 102330605, group_id = 1023306, type = 5, aside = "<color=#FFEBA2>追识：</color>算是其中一位吧，我叫追识，天月城维安司的司长。", talk_content = "算是其中一位吧，我叫追识，天月城维安司的司长。", talk_name = "追识",},
	[102330701] = { talk_id = 102330701, group_id = 1023307, type = 2, child_type = 21, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"我没听说有两个委托人，得加钱。",102330702,},{"王土是什么？",102330705,},}, talk_content = "我是郭中郎，王土的特派员，也是这次任务的委托人。", talk_name = "郭中郎",},
	[102330702] = { talk_id = 102330702, group_id = 1023307, type = 3, next_id = 102330703, icon = "interaction_4", talk_content = "维安司和郭先生只是一定程度上的合作，我们并没有给你派两个任务的意思。", talk_name = "追识",},
	[102330703] = { talk_id = 102330703, group_id = 1023307, type = 3, next_id = 102330704, talk_content = "你这脉者特意说这话是什么意思，你是觉得我们会占你便宜吗！", talk_name = "郭中郎",},
	[102330704] = { talk_id = 102330704, group_id = 1023307, type = 3, next_id = 102330712, talk_content = "小心使得万年船，最开始说清楚对谁都好吧。", talk_name = "青乌",},
	[102330705] = { talk_id = 102330705, group_id = 1023307, type = 3, next_id = 102330706, icon = "interaction_4", talk_content = "你哪个乡下出来的，连王土也不知道吗？", talk_name = "郭中郎",},
	[102330706] = { talk_id = 102330706, group_id = 1023307, type = 3, next_id = 102330707, talk_content = "我失了忆，你就和我说说呗。", talk_name = "青乌",},
	[102330707] = { talk_id = 102330707, group_id = 1023307, type = 3, next_id = 102330708, talk_content = "哼……也罢，我们王土是曾经的世界之王，拥有世界上最肥沃土地和最丰富的资源。", talk_name = "郭中郎",},
	[102330708] = { talk_id = 102330708, group_id = 1023307, type = 3, next_id = 102330709, talk_content = "正因如此，王土的国力也相当强大，当众生都因为噬脉灾害而苦恼的时候，王土也是第一个组建脉者军队与噬脉对抗的势力。", talk_name = "郭中郎",},
	[102330709] = { talk_id = 102330709, group_id = 1023307, type = 3, next_id = 102330710, talk_content = "通过一场又一场捍卫人类的战斗，王土得到了世界的保护者这一大众公认的形象，成为了第一个也是唯一一个统一了世界的……", talk_name = "郭中郎",},
	[102330710] = { talk_id = 102330710, group_id = 1023307, type = 3, next_id = 102330711, talk_content = "郭先生，再这么说下去恐怕有些长了……历史就先说到这里吧？", talk_name = "追识",},
	[102330711] = { talk_id = 102330711, group_id = 1023307, type = 3, next_id = 102330712, talk_content = "……总之你只要记得我们王土是迟早会重新建立世界秩序，为世界带来安宁和平的势力就可以了。", talk_name = "郭中郎",},
	[102330712] = { talk_id = 102330712, group_id = 1023307, type = 1, next_id = 102330713, talk_content = "那接下来就说点委托方面的事情怎样，郭先生。", talk_name = "追识",},
	[102330713] = { talk_id = 102330713, group_id = 1023307, type = 1, next_id = 102330714, talk_content = "哼，青乌，你应该知道悬天是什么样的组织吧。", talk_name = "郭中郎",},
	[102330714] = { talk_id = 102330714, group_id = 1023307, type = 1, next_id = 102330715, talk_content = "上次的委托就碰过，离歌说他们是个巨大的企业，还能只手遮天对吧。", talk_name = "青乌",},
	[102330715] = { talk_id = 102330715, group_id = 1023307, type = 1, next_id = 102330716, talk_content = "这些人不过是趁着我们王土虚弱偷偷兴风作浪的鼠辈而已。", talk_name = "郭中郎",},
	[102330716] = { talk_id = 102330716, group_id = 1023307, type = 2, child_type = 21, options = {{"要我去对付他们？",102330717,},{"对付一个大企业真的没问题吗？",102330722,},{"那就想办法挖他们的黑料吧。",102330725,},}, talk_content = "听着，青乌，我们想要把悬天彻底从这座城市中赶出去。", talk_name = "郭中郎",},
	[102330717] = { talk_id = 102330717, group_id = 1023307, type = 3, next_id = 102330718, icon = "interaction_4", talk_content = "要我去对付他们吗，我之前跟他们的人打过，只能说不弱，但也不强。", talk_name = "青乌",},
	[102330718] = { talk_id = 102330718, group_id = 1023307, type = 3, next_id = 102330719, talk_content = "你碰上的都是些菜鸟而已，悬天有专门的战士训练机构，从那里面出来的每个都不是常人能应付的。", talk_name = "郭中郎",},
	[102330719] = { talk_id = 102330719, group_id = 1023307, type = 3, next_id = 102330720, talk_content = "不过你要是愿意试试的话……", talk_name = "郭中郎",},
	[102330720] = { talk_id = 102330720, group_id = 1023307, type = 3, next_id = 102330721, talk_content = "动武是最后的手段，郭先生，我们维安司也不希望城内发生什么骚动，这也是双方合作的基础。", talk_name = "追识",},
	[102330721] = { talk_id = 102330721, group_id = 1023307, type = 3, next_id = 102330729, talk_content = "呃……我也就是说说而已。", talk_name = "郭中郎",},
	[102330722] = { talk_id = 102330722, group_id = 1023307, type = 3, next_id = 102330723, icon = "interaction_4", talk_content = "悬天可是个跨势力的大企业，何况现在都什么时代了……真的有办法把一个企业彻底赶出去吗？", talk_name = "青乌",},
	[102330723] = { talk_id = 102330723, group_id = 1023307, type = 3, next_id = 102330724, talk_content = "政治，经济，文化，手段多的是，这点轮不到你来担心。", talk_name = "郭中郎",},
	[102330724] = { talk_id = 102330724, group_id = 1023307, type = 3, next_id = 102330729, talk_content = "我们希望采取的是尽量温和的手段，与其说是赶出去，不如说是希望找到能约束悬天的办法。", talk_name = "追识",},
	[102330725] = { talk_id = 102330725, group_id = 1023307, type = 3, next_id = 102330726, icon = "interaction_4", talk_content = "噢？你还挺上道的嘛。", talk_name = "郭中郎",},
	[102330726] = { talk_id = 102330726, group_id = 1023307, type = 3, next_id = 102330727, talk_content = "可是太嫩了，要是一点黑料就能搞垮那今天要你来干嘛，你当我后面的人都是吃干饭的是吧！", talk_name = "郭中郎",},
	[102330727] = { talk_id = 102330727, group_id = 1023307, type = 3, next_id = 102330728, talk_content = "哎……所以我才让离歌给我派点更厉害的来。", talk_name = "郭中郎",},
	[102330728] = { talk_id = 102330728, group_id = 1023307, type = 3, next_id = 102330729, talk_content = "青乌只是提些想法，没必要把场面搞不愉快，郭先生。", talk_name = "追识",},
	[102330729] = { talk_id = 102330729, group_id = 1023307, type = 1, next_id = 102330730, talk_content = "悬天正慢慢蚕食着天月城，他们除了垄断产业以外还资助地下帮派来打击自己的对手，就现状来看，他们已经威胁到这个城市的安宁。", talk_name = "追识",},
	[102330730] = { talk_id = 102330730, group_id = 1023307, type = 1, next_id = 102330731, talk_content = "这群人无恶不作，天月城这里已经算是风平浪静的了，在某些地方悬天甚至直接成为了城市的主宰。", talk_name = "郭中郎",},
	[102330731] = { talk_id = 102330731, group_id = 1023307, type = 1, next_id = 102330732, talk_content = "那些地方可真是……唉。", talk_name = "郭中郎",},
	[102330732] = { talk_id = 102330732, group_id = 1023307, type = 1, next_id = 102330733, talk_content = "……所以我才会和郭先生合作，希望找办法遏制这股势头。", talk_name = "追识",},
	[102330733] = { talk_id = 102330733, group_id = 1023307, type = 2, options = {{"掌握来做啥？",102330734,},{"我该怎么做？",102330736,},}, talk_content = "那就按原计划进行吧，青乌，我现在要你加入到调查悬天的行动之中，要达成的目标很简单，那就是掌握悬天在天月城中的配置。", talk_name = "郭中郎",},
	[102330734] = { talk_id = 102330734, group_id = 1023307, type = 3, next_id = 102330735, talk_content = "那肯定就是评估该怎么打击他们了，悬天在天月城立足的时间尚浅，只要方法用对的话那接下来的事情就由我们王土负责就好。", talk_name = "郭中郎",},
	[102330735] = { talk_id = 102330735, group_id = 1023307, type = 3, next_id = 102330736, talk_content = "追识司长，我记得维安司有些人也参加了这次调查对吧？", talk_name = "郭中郎",},
	[102330736] = { talk_id = 102330736, group_id = 1023307, type = 1, talk_content = "青乌，维安司的一位司务已经先你一步开始调查了，她现在似乎在公园附近，你去了解状况确认下一步该怎么做吧。", talk_name = "追识",},
	[102330801] = { talk_id = 102330801, group_id = 1023308, type = 4, aside = "<color=#FFEBA2>青乌：</color>茶室“望月”，这就是我现在住的地方。", talk_content = "茶室“望月”，这就是我现在住的地方。", talk_name = "青乌",},
	[102330901] = { talk_id = 102330901, group_id = 1023309, type = 1, next_id = 102330902, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "怎么呆看着我？", talk_name = "离歌",},
	[102330902] = { talk_id = 102330902, group_id = 1023309, type = 1, next_id = 102330903, talk_content = "该不会又想不起我是谁了吧，忘记了这个望月的头领，同时也是你好友的离歌。", talk_name = "离歌",},
	[102330903] = { talk_id = 102330903, group_id = 1023309, type = 1, next_id = 102330904, talk_content = "我只是不知道该怎么回复你，就算你说欢迎回来……我也没有这里是家的感觉。", talk_name = "青乌",},
	[102330904] = { talk_id = 102330904, group_id = 1023309, type = 2, options = {{"<color=#FFEBA2>我把任务的东西带回来了。</color>",102330916,},{"再和你说一下你吧。",102330910,},{"我为什么会失去记忆。",102330905,},}, talk_content = "毕竟你现在失去了记忆，做大事者不拘小节，我劝你还是别太在意的好。", talk_name = "离歌",},
	[102330905] = { talk_id = 102330905, group_id = 1023309, type = 3, next_id = 102330906, talk_content = "因为我们上次讨伐任务失败了，敌人异常强大，期间你也受了重伤，然后就变成这样。", talk_name = "离歌",},
	[102330906] = { talk_id = 102330906, group_id = 1023309, type = 3, next_id = 102330907, talk_content = "我很抱歉，青乌。", talk_name = "离歌",},
	[102330907] = { talk_id = 102330907, group_id = 1023309, type = 3, next_id = 102330908, talk_content = "具体是什么敌人？", talk_name = "青乌",},
	[102330908] = { talk_id = 102330908, group_id = 1023309, type = 3, next_id = 102330909, talk_content = "一只通体纯白的生物，我之后也查了很多文献，估计我们是第一个遇到那家伙的。", talk_name = "离歌",},
	[102330909] = { talk_id = 102330909, group_id = 1023309, type = 3, next_id = 102330904, talk_content = "无论如何，能活下来是不幸中的万幸。", talk_name = "离歌",},
	[102330910] = { talk_id = 102330910, group_id = 1023309, type = 3, next_id = 102330911, talk_content = "刚才也说了，我的名字是离歌，精英云集的独立组织“望月“的头领。", talk_name = "离歌",},
	[102330911] = { talk_id = 102330911, group_id = 1023309, type = 3, next_id = 102330912, talk_content = "顺带一提，望月的总部并不在这里，天月城这个铺面只是个分店而已。", talk_name = "离歌",},
	[102330912] = { talk_id = 102330912, group_id = 1023309, type = 3, next_id = 102330913, talk_content = "你刚刚说我们两是好友对吗？", talk_name = "青乌",},
	[102330913] = { talk_id = 102330913, group_id = 1023309, type = 3, next_id = 102330914, talk_content = "我们至今为止都在并肩作战，所以就算你失了忆，变得很弱我也不会抛弃你。", talk_name = "离歌",},
	[102330914] = { talk_id = 102330914, group_id = 1023309, type = 3, next_id = 102330915, talk_content = "我应该不算很弱。", talk_name = "青乌",},
	[102330915] = { talk_id = 102330915, group_id = 1023309, type = 3, next_id = 102330904, talk_content = "比起以前弱多了。", talk_name = "离歌",},
	[102330916] = { talk_id = 102330916, group_id = 1023309, type = 3, next_id = 102330917, talk_content = "做的很好，这样一来我们就得到“做大事”的敲门砖了。", talk_name = "离歌",},
	[102330917] = { talk_id = 102330917, group_id = 1023309, type = 1, next_id = 102330918, talk_content = "我发觉你好像很常用大事这个词。", talk_name = "青乌",},
	[102330918] = { talk_id = 102330918, group_id = 1023309, type = 1, next_id = 102330919, talk_content = "这可是我们起行之前的誓言，人在世上就是为了做一件大事，青乌你现在有什么想干的事吗？", talk_name = "离歌",},
	[102330919] = { talk_id = 102330919, group_id = 1023309, type = 1, next_id = 102330920, talk_content = "没有……你还不如跟我说说失忆前的我有什么想干的。", talk_name = "青乌",},
	[102330920] = { talk_id = 102330920, group_id = 1023309, type = 1, next_id = 102330921, talk_content = "我希望你能自己想起来……说回正题，在一些人的帮助下我们联系上了某个强大的势力。", talk_name = "离歌",},
	[102330921] = { talk_id = 102330921, group_id = 1023309, type = 1, next_id = 102330922, talk_content = "而你刚刚的任务是我们通往成功之路的敲门砖，那个势力在评估我们的力量以后决定委托我们下一个任务。", talk_name = "离歌",},
	[102330922] = { talk_id = 102330922, group_id = 1023309, type = 2, options = {{"名字变响亮了之后要怎么办？",102330923,},{"（继续听下去）",102330924,},}, talk_content = "只要这次也能好好完成的话，我估计这下望月，我们的名字就会更加响亮了。", talk_name = "离歌",},
	[102330923] = { talk_id = 102330923, group_id = 1023309, type = 3, next_id = 102330924, talk_content = "……你忘记了啊，那就等你想起来再说吧，现在提你也没什么感觉。", talk_name = "离歌",},
	[102330924] = { talk_id = 102330924, group_id = 1023309, type = 1, next_id = 102330925, talk_content = "综上所述，青乌你就再跑一趟去见见我们接下来的大客户。", talk_name = "离歌",},
	[102330925] = { talk_id = 102330925, group_id = 1023309, type = 1, talk_content = "我给你在手机里面添加了送车公司的电话，你直接让他们送辆车开过去吧。", talk_name = "离歌",},
	[102331001] = { talk_id = 102331001, group_id = 1023310, type = 2, options = {{"没事了。",},{"能说一下客户的事情吗？",102331002,},{"进门的时候应该还有只奇怪的东西。",102331003,},}, talk_content = "还有什么问题吗，还是想要先休息一下，客户那边并不着急。", talk_name = "离歌",},
	[102331002] = { talk_id = 102331002, group_id = 1023310, type = 3, next_id = 102331005, talk_content = "你见到委托人以后委托人自然会跟你报家门的。", talk_name = "离歌",},
	[102331003] = { talk_id = 102331003, group_id = 1023310, type = 3, next_id = 102331004, talk_content = "喜大啊，那是吕……不，她不喜欢人这么叫。", talk_name = "离歌",},
	[102331004] = { talk_id = 102331004, group_id = 1023310, type = 3, next_id = 102331005, talk_content = "你只要把那个当成啰嗦小精灵之类的东西就好。", talk_name = "离歌",},
	[102331005] = { talk_id = 102331005, group_id = 1023310, type = 1, talk_content = "那下次再说把。", talk_name = "离歌",},
	[102331101] = { talk_id = 102331101, group_id = 1023311, type = 4, aside = "<color=#FFEBA2>青乌：</color>要叫车是吧。", talk_content = "要叫车是吧。", talk_name = "青乌",},
	[102331201] = { talk_id = 102331201, group_id = 1023312, type = 4, aside = "<color=#FFEBA2>青乌：</color>上车吧。", talk_content = "上车吧。", talk_name = "青乌",},
	[102331301] = { talk_id = 102331301, group_id = 1023313, type = 4, aside = "<color=#FFEBA2>混混：</color>不管你你还真追一路了是吧！", talk_content = "不管你你还真追一路了是吧！", talk_name = "混混",},
	[102331401] = { talk_id = 102331401, group_id = 1023314, type = 4, aside = "<color=#FFEBA2>青乌：</color>这个是……拿回去给失主吧。", talk_content = "这个是……拿回去给失主吧。", talk_name = "青乌",},
	[102331601] = { talk_id = 102331601, group_id = 1023316, type = 4, next_id = 102331602, aside = "<color=#FFEBA2>青乌：</color>被硬塞了个奇怪玩意啊……", talk_content = "被硬塞了个奇怪玩意啊……", talk_name = "青乌",},
	[102331602] = { talk_id = 102331602, group_id = 1023316, type = 4, aside = "<color=#FFEBA2>青乌：</color>也罢，先继续往委托人那边去吧。", talk_content = "也罢，先继续往委托人那边去吧。", talk_name = "青乌",},
	[102331701] = { talk_id = 102331701, group_id = 1023317, type = 4, aside = "<color=#FFEBA2>青乌：</color>见面的地方就是这里……委托人真在这吗？", talk_content = "见面的地方就是这里……委托人真在这吗？", talk_name = "青乌",},
	[102331801] = { talk_id = 102331801, group_id = 1023318, type = 2, options = {{"没事了。",102331005,},{"进门的时候应该还有只奇怪的东西。",102331003,},{"能说一下客户的事情吗？",102331002,},}, talk_content = "青乌，你还有什么问题吗？", talk_name = "追识",},
	[102331802] = { talk_id = 102331802, group_id = 1023318, type = 3, next_id = 102331803, talk_content = "维安司在曾经的天月城中是正义的代名词，而我借用这个名字创立了现在的这个组织。", talk_name = "追识",},
	[102331803] = { talk_id = 102331803, group_id = 1023318, type = 3, next_id = 102331804, talk_content = "至于说维安司的目的的话，那就是保护这个天月城，仅此而已。", talk_name = "追识",},
	[102331804] = { talk_id = 102331804, group_id = 1023318, type = 3, next_id = 102331805, talk_content = "聚集在维安司中的大家虽然性格各异，但都是因为赞同了我的这个想法才在维安司中工作的。", talk_name = "追识",},
	[102331805] = { talk_id = 102331805, group_id = 1023318, type = 3, talk_content = "青乌，假若你也有意想要维护这座天月城的话我也十分欢迎你的加入。", talk_name = "追识",},
	[102331806] = { talk_id = 102331806, group_id = 1023318, type = 3, talk_content = "我吗……等有机会再说吧，现在你还有任务在身，对吧。", talk_name = "追识",},
	[102331807] = { talk_id = 102331807, group_id = 1023318, type = 3, next_id = 102331808, talk_content = "我和王土的郭先生希望你能够协助我们调查悬天在天月城中的力量配置。", talk_name = "追识",},
	[102331808] = { talk_id = 102331808, group_id = 1023318, type = 3, talk_content = "这能让我们评估如何应付这个想要在天月城中为恶的组织。", talk_name = "追识",},
	[102331901] = { talk_id = 102331901, group_id = 1023319, type = 1, next_id = 102331902, talk_content = "本来听说望月是个还不错的独立组织才下委托的。", talk_name = "郭中郎",},
	[102331902] = { talk_id = 102331902, group_id = 1023319, type = 1, next_id = 102331903, talk_content = "结果派过来的人看着呆头呆脑的，唉。", talk_name = "郭中郎",},
	[102331903] = { talk_id = 102331903, group_id = 1023319, type = 1, talk_content = "……", talk_name = "青乌",},
	[102332001] = { talk_id = 102332001, group_id = 1023320, type = 4, aside = "<color=#FFEBA2>青乌：</color>要去公园是吧，来的路上应该见过。", talk_content = "要去公园是吧，来的路上应该见过。", talk_name = "青乌",},
	[102340101] = { talk_id = 102340101, group_id = 1023401, type = 5, next_id = 102340102, aside = "<color=#FFEBA2>？？？：</color>有够缠人的！", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "有够缠人的！", talk_name = "？？？",},
	[102340102] = { talk_id = 102340102, group_id = 1023401, type = 5, aside = "<color=#FFEBA2>？？？：</color>那边的，别一直看着，帮帮忙如何？", talk_content = "那边的，别一直看着，帮帮忙如何？", talk_name = "？？？",},
	[102340201] = { talk_id = 102340201, group_id = 1023402, type = 4, next_id = 102340202, aside = "<color=#FFEBA2>来来：</color>最近城里面噬脉乱流的次数明显增多了，光是这次跟踪就碰上四五次噬脉生物了。", talk_content = "最近城里面噬脉乱流的次数明显增多了，光是这次跟踪就碰上四五次噬脉生物了。", talk_name = "来来",},
	[102340202] = { talk_id = 102340202, group_id = 1023402, type = 4, next_id = 102340203, aside = "<color=#FFEBA2>青乌：</color>……抱歉，我不知道这算多还是少。", talk_content = "……抱歉，我不知道这算多还是少。", talk_name = "青乌",},
	[102340203] = { talk_id = 102340203, group_id = 1023402, type = 4, aside = "<color=#FFEBA2>来来：</color>当然是多啊！", talk_content = "当然是多啊！", talk_name = "来来",},
	[102340301] = { talk_id = 102340301, group_id = 1023403, type = 4, next_id = 102340302, aside = "<color=#FFEBA2>来来：</color>一下就中了，这可是好兆头！", talk_content = "一下就中了，这可是好兆头！", talk_name = "来来",},
	[102340302] = { talk_id = 102340302, group_id = 1023403, type = 4, aside = "<color=#FFEBA2>青乌：</color>好像还有……", talk_content = "好像还有……", talk_name = "青乌",},
	[102340401] = { talk_id = 102340401, group_id = 1023404, type = 1, next_id = 102340402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "这边应该就只有这些了吧。", talk_name = "来来",},
	[102340402] = { talk_id = 102340402, group_id = 1023404, type = 2, child_type = 21, options = {{"我觉得你一个人就能完事。",102340403,},{"你可以再感谢我一些。",102340404,},}, talk_content = "幸好把你带上了，刚刚那几只从士我应付起来还挺麻烦的。", talk_name = "来来",},
	[102340403] = { talk_id = 102340403, group_id = 1023404, type = 3, next_id = 102340405, talk_content = "完事是能完事，但就是没那么顺利而已。", talk_name = "来来",},
	[102340404] = { talk_id = 102340404, group_id = 1023404, type = 3, next_id = 102340405, icon = "interaction_4", talk_content = "哈哈，你真有趣啊，青乌，嗯嗯，我会再多感谢你些的。", talk_name = "来来",},
	[102340405] = { talk_id = 102340405, group_id = 1023404, type = 1, next_id = 102340406, talk_content = "可是这事还没有结，刚刚噬脉生物们出来的时候公园里面的警报没响起来，接下来我们得去把警报修好才行。", talk_name = "来来",},
	[102340406] = { talk_id = 102340406, group_id = 1023404, type = 1, next_id = 102340407, talk_content = "我可不会修东西。", talk_name = "青乌",},
	[102340407] = { talk_id = 102340407, group_id = 1023404, type = 1, talk_content = "既然是我提出的事情自然是我来做了，这可是人上之人的必须修养。", talk_name = "来来",},
	[102340501] = { talk_id = 102340501, group_id = 1023405, type = 4, next_id = 102340502, aside = "<color=#FFEBA2>青乌：</color>所以我们是要怎么修这东西？", talk_content = "所以我们是要怎么修这东西？", talk_name = "青乌",},
	[102340502] = { talk_id = 102340502, group_id = 1023405, type = 4, aside = "<color=#FFEBA2>来来：</color>接下来就是金系脉者的魅力时刻了。", talk_content = "接下来就是金系脉者的魅力时刻了。", talk_name = "来来",},
	[102340601] = { talk_id = 102340601, group_id = 1023406, type = 1, next_id = 102340602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "好，公园里面的麻烦都解决了，青乌你是来了解状况的对吧。", talk_name = "来来",},
	[102340602] = { talk_id = 102340602, group_id = 1023406, type = 1, next_id = 102340603, talk_content = "嗯，委托说是要我调查悬天城内的力量，然后说是你这边其实调查一阵子了是吗？", talk_name = "来来",},
	[102340603] = { talk_id = 102340603, group_id = 1023406, type = 2, child_type = 21, options = {{"有得到什么结论吗？",102340604,},{"怎么算比较可疑？",102340605,},{"有很多普通人也在悬天上班。",102340608,},}, talk_content = "大概有一周了吧，这一周内我都在跟踪行踪比较可疑的几个在悬天上班的人。", talk_name = "来来",},
	[102340604] = { talk_id = 102340604, group_id = 1023406, type = 3, next_id = 102340603, talk_content = "一周还到不了结论的阶段，我只能说自己有些收获。", talk_name = "来来",},
	[102340605] = { talk_id = 102340605, group_id = 1023406, type = 3, next_id = 102340606, talk_content = "你是想偷学我们维安司秘传的侦探技巧对不对。", talk_name = "来来",},
	[102340606] = { talk_id = 102340606, group_id = 1023406, type = 3, next_id = 102340607, talk_content = "虽然我不是想得到这个回答，但你会告诉我吗？", talk_name = "青乌",},
	[102340607] = { talk_id = 102340607, group_id = 1023406, type = 3, next_id = 102340603, talk_content = "直觉哦，青乌。", talk_name = "来来",},
	[102340608] = { talk_id = 102340608, group_id = 1023406, type = 3, next_id = 102340609, icon = "interaction_4", talk_content = "悬天里面也有不少普通人在上班，光凭可疑……你真确定自己跟对人了吗？", talk_name = "青乌",},
	[102340609] = { talk_id = 102340609, group_id = 1023406, type = 3, next_id = 102340610, talk_content = "这……呃……", talk_name = "来来",},
	[102340610] = { talk_id = 102340610, group_id = 1023406, type = 3, next_id = 102340611, talk_content = "应该没问题，毕竟我在上次被发现的时候揍的人可是战斗部门的人。", talk_name = "来来",},
	[102340611] = { talk_id = 102340611, group_id = 1023406, type = 3, next_id = 102340612, talk_content = "要说这个战斗部门啊，那可是悬天中的小核心部门……", talk_name = "来来",},
	[102340612] = { talk_id = 102340612, group_id = 1023406, type = 3, next_id = 102340613, talk_content = "还给发现了啊？", talk_name = "青乌",},
	[102340613] = { talk_id = 102340613, group_id = 1023406, type = 2, options = {{"（答应）",102340614,},{"（不答应）",102340616,},}, talk_content = "没，没问题的，不要和司长说可以吗！", talk_name = "来来",},
	[102340614] = { talk_id = 102340614, group_id = 1023406, type = 3, next_id = 102340615, talk_content = "真的吗，你应该没在说谎吧，司长生气的时候特可怕……我可不想再挨骂了。", talk_name = "来来",},
	[102340615] = { talk_id = 102340615, group_id = 1023406, type = 3, next_id = 102340618, talk_content = "嗯，所以继续说状况吧……", talk_name = "青乌",},
	[102340616] = { talk_id = 102340616, group_id = 1023406, type = 3, next_id = 102340617, talk_content = "这……这，青乌，你怎么能这样！", talk_name = "来来",},
	[102340617] = { talk_id = 102340617, group_id = 1023406, type = 3, next_id = 102340618, talk_content = "还是继续说状况吧……", talk_name = "青乌",},
	[102340618] = { talk_id = 102340618, group_id = 1023406, type = 1, next_id = 102340619, talk_content = "对了，状况……办正事办正事。", talk_name = "来来",},
	[102340619] = { talk_id = 102340619, group_id = 1023406, type = 1, next_id = 102340620, talk_content = "刚刚不是说我跟踪悬天的人跟了一周嘛，虽然还没搞清楚他们到底有啥人员配置。", talk_name = "来来",},
	[102340620] = { talk_id = 102340620, group_id = 1023406, type = 1, next_id = 102340621, talk_content = "但是我知道了两件事，一是有几栋建筑频繁都有悬天的成员出入。", talk_name = "来来",},
	[102340621] = { talk_id = 102340621, group_id = 1023406, type = 1, next_id = 102340622, talk_content = "二则是城内悬天雇了天月城的本地人来替他们办某件事。", talk_name = "来来",},
	[102340622] = { talk_id = 102340622, group_id = 1023406, type = 1, next_id = 102340623, talk_content = "频繁出入的建筑还有被雇佣的本地人……", talk_name = "青乌",},
	[102340623] = { talk_id = 102340623, group_id = 1023406, type = 1, next_id = 102340624, talk_content = "第一个这点继续由我来负责就好，至于第二个点就交给你了，没问题吧青乌？", talk_name = "来来",},
	[102340624] = { talk_id = 102340624, group_id = 1023406, type = 1, next_id = 102340625, talk_content = "既然是委托我自然是接下，但是眼下也没有更多线索。", talk_name = "青乌",},
	[102340625] = { talk_id = 102340625, group_id = 1023406, type = 1, talk_content = "这就只能靠自己了，或者你那边，那个望月里面会有人能有这方面的门路？", talk_name = "来来",},
	[102340701] = { talk_id = 102340701, group_id = 1023407, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"（自我介绍）",102340702,},{"刚还挺好看的。",102340703,},}, talk_content = "白衣辰属……等等，你该不会是司长请来的人吧，叫什么来着……", talk_name = "？？？",},
	[102340702] = { talk_id = 102340702, group_id = 1023407, type = 3, next_id = 102340705, talk_content = "对了对了，是叫这个名字，我想起来了。", talk_name = "？？？",},
	[102340703] = { talk_id = 102340703, group_id = 1023407, type = 3, next_id = 102340704, talk_content = "是吧是吧，这可是我以前拜师的时候特地练过的，尤其刚刚那几下踢击……", talk_name = "？？？",},
	[102340704] = { talk_id = 102340704, group_id = 1023407, type = 3, next_id = 102340705, talk_content = "不对，你就是青乌是吧！", talk_name = "？？？",},
	[102340705] = { talk_id = 102340705, group_id = 1023407, type = 2, options = {{"你们司长说让我来了解状况。",102340706,},{"刚才的啥属啥啥属的是？",102340707,},}, talk_content = "我是维安司的来来，是个寅属的脉者。", talk_name = "来来",},
	[102340706] = { talk_id = 102340706, group_id = 1023407, type = 3, next_id = 102340712, talk_content = "状况就是……对了，说起这个，青乌你来帮帮我！", talk_name = "来来",},
	[102340707] = { talk_id = 102340707, group_id = 1023407, type = 3, next_id = 102340708, talk_content = "你说属系吗，也是，很多脉者其实不太注意属系这个东西。", talk_name = "来来",},
	[102340708] = { talk_id = 102340708, group_id = 1023407, type = 3, next_id = 102340709, talk_content = "简单来说就是气脉在我们脉者身体上留下的印记，你的是龙，所以是辰。", talk_name = "来来",},
	[102340709] = { talk_id = 102340709, group_id = 1023407, type = 3, next_id = 102340710, talk_content = "而我是猫科，所以是寅。", talk_name = "来来",},
	[102340710] = { talk_id = 102340710, group_id = 1023407, type = 3, next_id = 102340711, talk_content = "还是不太懂，为什么会有专门的名字，这到底是按什么分类的？", talk_name = "青乌",},
	[102340711] = { talk_id = 102340711, group_id = 1023407, type = 3, next_id = 102340712, talk_content = "那自然是十二始兽了……对了，比起这个有更重要的事情，青乌你来帮帮我！", talk_name = "来来",},
	[102340712] = { talk_id = 102340712, group_id = 1023407, type = 1, next_id = 102340713, talk_content = "帮你啥？", talk_name = "青乌",},
	[102340713] = { talk_id = 102340713, group_id = 1023407, type = 1, next_id = 102340714, talk_content = "解决这里的噬脉生物，我本是照命令跟踪着悬天其中一个人的，但跟到这里的时候噬脉生物们突然就冒了出来。", talk_name = "来来",},
	[102340714] = { talk_id = 102340714, group_id = 1023407, type = 1, talk_content = "虽然好歹是把人都救下了，但剩下的噬脉生物只靠我一个人有点太累了。", talk_name = "来来",},
	[102340801] = { talk_id = 102340801, group_id = 1023408, type = 4, aside = "<color=#FFEBA2>青乌：</color>前面的是……！", talk_content = "前面的是……！", talk_name = "青乌",},
	[102340901] = { talk_id = 102340901, group_id = 1023409, type = 4, aside = "<color=#FFEBA2>来来：</color>青乌，应该就是它们把警报器搞坏了。", talk_content = "青乌，应该就是它们把警报器搞坏了。", talk_name = "来来",},
	[102341001] = { talk_id = 102341001, group_id = 1023410, type = 4, next_id = 102341002, aside = "<color=#FFEBA2>青乌：</color>电眼逼人啊，来来。", talk_content = "电眼逼人啊，来来。", talk_name = "青乌",},
	[102341002] = { talk_id = 102341002, group_id = 1023410, type = 4, aside = "<color=#FFEBA2>来来：</color>……", talk_content = "……", talk_name = "来来",},
	[102341101] = { talk_id = 102341101, group_id = 1023411, type = 4, aside = "<color=#FFEBA2>青乌：</color>望月……那先回去看看吧。", talk_content = "望月……那先回去看看吧。", talk_name = "青乌",},
	[102341201] = { talk_id = 102341201, group_id = 1023412, type = 2, options = {{"维安司有多少人？",102341202,},{"没事。",},}, talk_content = "得考虑下之后的行动，青乌你还有什么事吗？", talk_name = "来来",},
	[102341202] = { talk_id = 102341202, group_id = 1023412, type = 3, next_id = 102341203, talk_content = "维安司是个不大不小的组织，大概有三十个左右吧。", talk_name = "来来",},
	[102341203] = { talk_id = 102341203, group_id = 1023412, type = 3, talk_content = "其中像我一样专门跑外勤的没几个就是了。", talk_name = "来来",},
	[102350101] = { talk_id = 102350101, group_id = 1023501, type = 4, aside = "<color=#FFEBA2>旁白：</color>与离歌通话要求回茶室休整", child_type = 41, talk_content = "与离歌通话要求回茶室休整", talk_name = "旁白",},
	[102350201] = { talk_id = 102350201, group_id = 1023502, type = 1, next_id = 102350202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "辛苦了，青乌。", talk_name = "离歌",},
	[102350202] = { talk_id = 102350202, group_id = 1023502, type = 1, next_id = 102350203, talk_content = "只是路上一直都在打架而已，还好吧。", talk_name = "青乌",},
	[102350203] = { talk_id = 102350203, group_id = 1023502, type = 2, options = {{"但我不知道从哪开始找起。",102350204,},{"真的要帮王土对付悬天吗？",102350205,},}, talk_content = "我都听说了，接下来是要找接过悬天委托的人是吧？", talk_name = "离歌",},
	[102350204] = { talk_id = 102350204, group_id = 1023502, type = 3, next_id = 102350210, talk_content = "我自然有方法。", talk_name = "离歌",},
	[102350205] = { talk_id = 102350205, group_id = 1023502, type = 3, next_id = 102350206, talk_content = "是的，虽然也有帮悬天对付王土这条路，但于正义还有于交易的安全这个方面而言我还是更倾向王土。", talk_name = "离歌",},
	[102350206] = { talk_id = 102350206, group_id = 1023502, type = 3, next_id = 102350207, talk_content = "我是说我们真的要让自己参加到这种听着就很危险的事情里面去吗？", talk_name = "青乌",},
	[102350207] = { talk_id = 102350207, group_id = 1023502, type = 3, next_id = 102350208, talk_content = "这次的报酬是个好东西……再者我说过要做大事吧，青乌。", talk_name = "离歌",},
	[102350208] = { talk_id = 102350208, group_id = 1023502, type = 3, next_id = 102350209, talk_content = "唉……", talk_name = "青乌",},
	[102350209] = { talk_id = 102350209, group_id = 1023502, type = 3, next_id = 102350210, talk_content = "要是出了什么事我会兜底的，你就放心好了。", talk_name = "离歌",},
	[102350210] = { talk_id = 102350210, group_id = 1023502, type = 2, child_type = 21, options = {{"摇摇街是什么地方？",102350211,},{"那里的人不太友善",102350213,},}, talk_content = "然后说到会接悬天的委托的人……你可以去遥遥街找找看，青乌。", talk_name = "离歌",},
	[102350211] = { talk_id = 102350211, group_id = 1023502, type = 3, next_id = 102350212, talk_content = "在地下社会生存的人的其中一个聚集地，虽然出入那里的未必都是坏人，但大部分脏活累活都是在那儿交易的。", talk_name = "离歌",},
	[102350212] = { talk_id = 102350212, group_id = 1023502, type = 3, next_id = 102350218, talk_content = "虽然不知道悬天要干啥事，但那事绝对不干净，自然也就只能找不干净的人来做了。", talk_name = "离歌",},
	[102350213] = { talk_id = 102350213, group_id = 1023502, type = 3, next_id = 102350214, icon = "interaction_4", talk_content = "我知道那地方，之前光是路过就被人瞪了几眼，之后还被堵了，要不是拳头硬我估计得要掉层皮。", talk_name = "青乌",},
	[102350214] = { talk_id = 102350214, group_id = 1023502, type = 3, next_id = 102350215, talk_content = "虽然没有记忆，但你还是和之前一样喜欢到处乱逛啊……有好几次出发之前都找不到你。", talk_name = "离歌",},
	[102350215] = { talk_id = 102350215, group_id = 1023502, type = 3, next_id = 102350216, talk_content = "之前的我是什么人，我感觉你没怎么谈论过这个。", talk_name = "青乌",},
	[102350216] = { talk_id = 102350216, group_id = 1023502, type = 3, next_id = 102350217, talk_content = "老实说我是有在刻意回避这个问题，因为你就是你，青乌。", talk_name = "离歌",},
	[102350217] = { talk_id = 102350217, group_id = 1023502, type = 3, next_id = 102350218, talk_content = "不说这个了，还是说回遥遥街吧，正所谓狼狈为奸，我认为悬天大概也会从那里面挑人。", talk_name = "离歌",},
	[102350218] = { talk_id = 102350218, group_id = 1023502, type = 1, next_id = 102350219, talk_content = "也就是接下来要往那个遥遥街跑一趟是吧。", talk_name = "青乌",},
	[102350219] = { talk_id = 102350219, group_id = 1023502, type = 1, talk_content = "嗯，我会在这里等你好消息的。", talk_name = "离歌",},
	[102350301] = { talk_id = 102350301, group_id = 1023503, type = 4, aside = "<color=#FFEBA2>青乌：</color>遥遥街……要开车还是走路过去呢。", talk_content = "遥遥街……要开车还是走路过去呢。", talk_name = "青乌",},
	[102350401] = { talk_id = 102350401, group_id = 1023504, type = 2, options = {{"没有。",},{"遥遥街为啥叫遥遥街？",102350402,},{"这地一个客人都没有。",102350405,},}, talk_content = "看你一脸烦恼，有什么要问的吗？", talk_name = "离歌",},
	[102350402] = { talk_id = 102350402, group_id = 1023504, type = 3, next_id = 102350403, talk_content = "是长路遥遥的意思，在以前貌似那里是天月城的其中一个出入口，远行的人都会从那出发。", talk_name = "离歌",},
	[102350403] = { talk_id = 102350403, group_id = 1023504, type = 3, next_id = 102350404, talk_content = "……还挺有寓意的。", talk_name = "青乌",},
	[102350404] = { talk_id = 102350404, group_id = 1023504, type = 3, talk_content = "虽然现在就是个混混窝。", talk_name = "离歌",},
	[102350405] = { talk_id = 102350405, group_id = 1023504, type = 3, next_id = 102350406, talk_content = "不是每间店铺都需要客人的，青乌。", talk_name = "离歌",},
	[102350406] = { talk_id = 102350406, group_id = 1023504, type = 3, talk_content = "再说我们有额外的业务，比如你现在身上的委托。", talk_name = "离歌",},
	[102360101] = { talk_id = 102360101, group_id = 1023601, type = 1, next_id = 102360102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，我想找你问点事。", talk_name = "青乌",},
	[102360102] = { talk_id = 102360102, group_id = 1023601, type = 1, talk_content = "……", talk_name = "混混",},
	[102360201] = { talk_id = 102360201, group_id = 1023602, type = 1, next_id = 102360202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我想找人，你有什么门路吗？", talk_name = "青乌",},
	[102360202] = { talk_id = 102360202, group_id = 1023602, type = 1, talk_content = "……没有，再见。", talk_name = "混混",},
	[102360301] = { talk_id = 102360301, group_id = 1023603, type = 1, next_id = 102360302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我想要打听点事情，你们这里……", talk_name = "青乌",},
	[102360302] = { talk_id = 102360302, group_id = 1023603, type = 2, child_type = 21, options = {{"我看你是想出事了。",102360303,},{"你是看哨的吧，能做主的人在哪？",102360313,},{"我有个大生意想找人聊聊。",102360319,},}, talk_content = "没看见我有事吗，一边去！", talk_name = "混混",},
	[102360303] = { talk_id = 102360303, group_id = 1023603, type = 3, next_id = 102360304, icon = "interaction_4", talk_content = "我是没看见你有什么事，但要是你愿意的话我可以真让你点事。", talk_name = "青乌",},
	[102360304] = { talk_id = 102360304, group_id = 1023603, type = 3, next_id = 102360305, talk_content = "噫……你，你想干什么！", talk_name = "混混",},
	[102360305] = { talk_id = 102360305, group_id = 1023603, type = 3, next_id = 102360306, talk_content = "那就得看你了，我在找最近帮悬天干过活的人。", talk_name = "青乌",},
	[102360306] = { talk_id = 102360306, group_id = 1023603, type = 3, next_id = 102360307, talk_content = "你知道不？", talk_name = "青乌",},
	[102360307] = { talk_id = 102360307, group_id = 1023603, type = 3, next_id = 102360308, talk_content = "给悬天干活的……呃……", talk_name = "混混",},
	[102360308] = { talk_id = 102360308, group_id = 1023603, type = 3, next_id = 102360309, talk_content = "所以你想有事是吧。", talk_name = "青乌",},
	[102360309] = { talk_id = 102360309, group_id = 1023603, type = 3, next_id = 102360310, talk_content = "我知道了，我知道，我刚不是在思考嘛！", talk_name = "混混",},
	[102360310] = { talk_id = 102360310, group_id = 1023603, type = 3, next_id = 102360311, talk_content = "我之前是有看见过悬天的人来这找人，还带了个装满钱的箱子，就那边，收了钱的人平常都呆那。", talk_name = "混混",},
	[102360311] = { talk_id = 102360311, group_id = 1023603, type = 3, next_id = 102360312, talk_content = "真的？", talk_name = "青乌",},
	[102360312] = { talk_id = 102360312, group_id = 1023603, type = 3, talk_content = "真的，我骗你干嘛！", talk_name = "混混",},
	[102360313] = { talk_id = 102360313, group_id = 1023603, type = 3, next_id = 102360314, icon = "interaction_4", talk_content = "我看你站在这像个看哨的，这街这么大应该有个能做代表的龙头吧，我有点事情想打听。", talk_name = "青乌",},
	[102360314] = { talk_id = 102360314, group_id = 1023603, type = 3, next_id = 102360315, talk_content = "……看你也是个懂规矩的，但老板不是说见就见的，等等，你是脉者吗？", talk_name = "混混",},
	[102360315] = { talk_id = 102360315, group_id = 1023603, type = 3, next_id = 102360316, talk_content = "我是个脉者，怎么了，你们那个老板该不会有不见脉者的规矩吧？", talk_name = "青乌",},
	[102360316] = { talk_id = 102360316, group_id = 1023603, type = 3, next_id = 102360317, talk_content = "没有这样的规矩，行吧，你去那边等着，我们老板稍后就来。", talk_name = "混混",},
	[102360317] = { talk_id = 102360317, group_id = 1023603, type = 3, next_id = 102360318, talk_content = "不能在这等吗？", talk_name = "青乌",},
	[102360318] = { talk_id = 102360318, group_id = 1023603, type = 3, talk_content = "让你去你就去，不想见拉倒！", talk_name = "混混",},
	[102360319] = { talk_id = 102360319, group_id = 1023603, type = 3, next_id = 102360320, icon = "interaction_4", talk_content = "你们都是这样待客的吗？", talk_name = "青乌",},
	[102360320] = { talk_id = 102360320, group_id = 1023603, type = 3, next_id = 102360321, talk_content = "客？", talk_name = "混混",},
	[102360321] = { talk_id = 102360321, group_id = 1023603, type = 3, next_id = 102360322, talk_content = "我最近有些麻烦，想让街上的人帮我解决一下。", talk_name = "青乌",},
	[102360322] = { talk_id = 102360322, group_id = 1023603, type = 3, next_id = 102360323, talk_content = "……那你要什么人，要几个？", talk_name = "混混",},
	[102360323] = { talk_id = 102360323, group_id = 1023603, type = 3, next_id = 102360324, talk_content = "越多越好，最好是那悬天也能看上的人才，钱我多给你一份怎么样。", talk_name = "青乌",},
	[102360324] = { talk_id = 102360324, group_id = 1023603, type = 3, next_id = 102360325, talk_content = "行，你去那边，我等下带几个熟手来。", talk_name = "混混",},
	[102360325] = { talk_id = 102360325, group_id = 1023603, type = 3, talk_content = "好，谢了。", talk_name = "青乌",},
	[102360401] = { talk_id = 102360401, group_id = 1023604, type = 1, next_id = 102360402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "客人你好，要买点什么吗？", talk_name = "回收店主",},
	[102360402] = { talk_id = 102360402, group_id = 1023604, type = 1, next_id = 102360403, talk_content = "你就是“遥遥街老板”对吧？", talk_name = "青乌",},
	[102360403] = { talk_id = 102360403, group_id = 1023604, type = 1, next_id = 102360404, talk_content = "呃，客人你说的是什么意思，我不太懂……", talk_name = "回收店主",},
	[102360404] = { talk_id = 102360404, group_id = 1023604, type = 1, next_id = 102360405, talk_content = "你头像和你本人一摸一样。", talk_name = "青乌",},
	[102360405] = { talk_id = 102360405, group_id = 1023604, type = 1, next_id = 102360406, talk_content = "……哈哈，忘记换头像了。", talk_name = "回收店主",},
	[102360406] = { talk_id = 102360406, group_id = 1023604, type = 2, options = {{"（自我介绍）",102360407,},}, talk_content = "是的，我就是遥遥街的老板，名字就不提了，你能和我说说你自己吗？", talk_name = "回收店主",},
	[102360407] = { talk_id = 102360407, group_id = 1023604, type = 3, next_id = 102360408, talk_content = "我叫<nick_name>，你叫我青乌就行。", talk_name = "青乌",},
	[102360408] = { talk_id = 102360408, group_id = 1023604, type = 2, options = {{"具体是什么合作？",102360409,},{"我不是来谈合作的。",102360410,},}, talk_content = "那么我们就算认识了，刚看你打起来也挺有一手的，接下来我想我们会有不少合作。", talk_name = "回收店主",},
	[102360409] = { talk_id = 102360409, group_id = 1023604, type = 3, next_id = 102360411, talk_content = "各种各样，没法摆明面上来的工作都会流到这遥遥街来。", talk_name = "回收店主",},
	[102360410] = { talk_id = 102360410, group_id = 1023604, type = 3, next_id = 102360411, talk_content = "多点机会也不是什么坏事。", talk_name = "回收店主",},
	[102360411] = { talk_id = 102360411, group_id = 1023604, type = 1, next_id = 102360412, talk_content = "这么说来你是来这街上是要干什么，发委托吗，还是要找人？", talk_name = "回收店主",},
	[102360412] = { talk_id = 102360412, group_id = 1023604, type = 1, next_id = 102360413, talk_content = "找人，我听说悬天曾经找人给他们干了些事，我想知道是谁。", talk_name = "青乌",},
	[102360413] = { talk_id = 102360413, group_id = 1023604, type = 1, next_id = 102360414, talk_content = "竟然盯上了悬天，所以你的老板……不不不，我就不惹火上身了。", talk_name = "回收店主",},
	[102360414] = { talk_id = 102360414, group_id = 1023604, type = 1, next_id = 102360415, talk_content = "你要找的人是在这街上没错，但是我们这边也讲究保密……", talk_name = "回收店主",},
	[102360415] = { talk_id = 102360415, group_id = 1023604, type = 1, next_id = 102360416, talk_content = "那我该怎么办？", talk_name = "青乌",},
	[102360416] = { talk_id = 102360416, group_id = 1023604, type = 2, options = {{"能便宜点吗？",8,},}, talk_content = "买个东西就好，我这的东西你挑一个，买了我就告诉你。", talk_name = "回收店主",},
	[102360417] = { talk_id = 102360417, group_id = 1023604, type = 3, talk_content = "就是要钱对吧……", talk_name = "青乌",},
	[102360501] = { talk_id = 102360501, group_id = 1023605, type = 1, next_id = 102360502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我感觉我是放水了。", talk_name = "回收店主",},
	[102360502] = { talk_id = 102360502, group_id = 1023605, type = 1, next_id = 102360503, talk_content = "有没有别的办法，这钱……", talk_name = "青乌",},
	[102360503] = { talk_id = 102360503, group_id = 1023605, type = 1, next_id = 102360504, talk_content = "办法自然有，这样，这附近有群赊账不还的小子，你帮我教训一下他们吧。", talk_name = "回收店主",},
	[102360504] = { talk_id = 102360504, group_id = 1023605, type = 1, next_id = 102360505, talk_content = "这样就可以了？", talk_name = "青乌",},
	[102360505] = { talk_id = 102360505, group_id = 1023605, type = 1, talk_content = "嗯，就这样吧，赶紧去，不然等下我可就反悔了。", talk_name = "回收店主",},
	[102360601] = { talk_id = 102360601, group_id = 1023606, type = 1, next_id = 102360602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你这家伙是打哪里冒出来的，你知道我是谁吗！", talk_name = "混混",},
	[102360602] = { talk_id = 102360602, group_id = 1023606, type = 1, next_id = 102360603, talk_content = "有人说你们赊账不还，有人让我教训你们。", talk_name = "青乌",},
	[102360603] = { talk_id = 102360603, group_id = 1023606, type = 1, talk_content = "我才没有赊账！", talk_name = "混混",},
	[102360701] = { talk_id = 102360701, group_id = 1023607, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"赊账的是你孙子？",102360702,},{"应该没有。",102360705,},}, talk_content = "真快啊，怎么样，那小子长记性了吗？", talk_name = "回收店主",},
	[102360702] = { talk_id = 102360702, group_id = 1023607, type = 3, next_id = 102360703, talk_content = "除了我孙子谁还敢在我面前说赊账这两字，唉，那孩子净是不学好。", talk_name = "回收店主",},
	[102360703] = { talk_id = 102360703, group_id = 1023607, type = 3, next_id = 102360704, talk_content = "……我觉得是家庭原因。", talk_name = "青乌",},
	[102360704] = { talk_id = 102360704, group_id = 1023607, type = 3, next_id = 102360706, talk_content = "我就当没听见吧。", talk_name = "回收店主",},
	[102360705] = { talk_id = 102360705, group_id = 1023607, type = 3, next_id = 102360706, talk_content = "真顽固，真不知道像谁……", talk_name = "回收店主",},
	[102360706] = { talk_id = 102360706, group_id = 1023607, type = 1, next_id = 102360707, talk_content = "然后你想要找的人他们是个叫创生主义的小团体，在我看来就是凑着玩的组织。", talk_name = "回收店主",},
	[102360707] = { talk_id = 102360707, group_id = 1023607, type = 1, next_id = 102360708, talk_content = "其中一个成员今天才在这逛悠来着，是个白发有点矮的小女生，你要不现在就找一下吧？", talk_name = "回收店主",},
	[102360708] = { talk_id = 102360708, group_id = 1023607, type = 1, next_id = 102360709, talk_content = "好，谢了。", talk_name = "青乌",},
	[102360709] = { talk_id = 102360709, group_id = 1023607, type = 1, talk_content = "下次再来啊，青乌。", talk_name = "回收店主",},
	[102360801] = { talk_id = 102360801, group_id = 1023608, type = 4, aside = "<color=#FFEBA2>青乌：</color>到是到了……随便找几个人问问吧。", talk_content = "到是到了……随便找几个人问问吧。", talk_name = "青乌",},
	[102360901] = { talk_id = 102360901, group_id = 1023609, type = 1, next_id = 102360902, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "挺不错的啊你，好，今天就把这个当作见面礼送给你好了。", talk_name = "回收店主",},
	[102360902] = { talk_id = 102360902, group_id = 1023609, type = 1, next_id = 102360903, talk_content = "不要钱了？", talk_name = "青乌",},
	[102360903] = { talk_id = 102360903, group_id = 1023609, type = 1, next_id = 102360904, talk_content = "不要了不要了，本来就是些废品。", talk_name = "回收店主",},
	[102360904] = { talk_id = 102360904, group_id = 1023609, type = 1, next_id = 102360905, talk_content = "然后你想要找的人他们是个叫创生主义的小团体，在我看来就是凑着玩的组织。", talk_name = "回收店主",},
	[102360905] = { talk_id = 102360905, group_id = 1023609, type = 1, next_id = 102360906, talk_content = "其中一个成员今天才在这逛悠来着，是个白发有点矮的小女生，你要不现在就找一下吧？", talk_name = "回收店主",},
	[102360906] = { talk_id = 102360906, group_id = 1023609, type = 1, next_id = 102360907, talk_content = "好，谢了。", talk_name = "青乌",},
	[102360907] = { talk_id = 102360907, group_id = 1023609, type = 1, talk_content = "下次再来啊，青乌。", talk_name = "回收店主",},
	[102361001] = { talk_id = 102361001, group_id = 1023610, type = 1, next_id = 102361002, talk_content = "要买这个吗？", talk_name = "回收店主",},
	[102361002] = { talk_id = 102361002, group_id = 1023610, type = 1, next_id = 102361003, talk_content = "钱是能省则省……老板，能便宜点吗？", talk_name = "青乌",},
	[102361003] = { talk_id = 102361003, group_id = 1023610, type = 1, talk_content = "那就得看你的功夫了。", talk_name = "回收店主",},
	[102361101] = { talk_id = 102361101, group_id = 1023611, type = 4, next_id = 102361102, aside = "<color=#FFEBA2>混混：</color>呃……爷爷那混帐，给我等着！", talk_content = "呃……爷爷那混帐，给我等着！", talk_name = "混混",},
	[102361102] = { talk_id = 102361102, group_id = 1023611, type = 4, aside = "<color=#FFEBA2>青乌：</color>……", talk_content = "……", talk_name = "青乌",},
	[102361201] = { talk_id = 102361201, group_id = 1023612, type = 4, aside = "<color=#FFEBA2>青乌：</color>白发的小女生，这样的外貌应该是挺显眼的，在这周围找找吧。", talk_content = "白发的小女生，这样的外貌应该是挺显眼的，在这周围找找吧。", talk_name = "青乌",},
	[102361301] = { talk_id = 102361301, group_id = 1023613, type = 4, next_id = 102361302, aside = "<color=#FFEBA2>青乌：</color>那人说的就是这里了吧。", talk_content = "那人说的就是这里了吧。", talk_name = "青乌",},
	[102361302] = { talk_id = 102361302, group_id = 1023613, type = 4, next_id = 102361303, aside = "<color=#FFEBA2>刚才的混混：</color>兄弟们，给我用力揍！", talk_content = "兄弟们，给我用力揍！", talk_name = "刚才的混混",},
	[102361303] = { talk_id = 102361303, group_id = 1023613, type = 4, aside = "<color=#FFEBA2>青乌：</color>……我就该想到会是这结果的。", talk_content = "……我就该想到会是这结果的。", talk_name = "青乌",},
	[102361401] = { talk_id = 102361401, group_id = 1023614, type = 4, aside = "<color=#FFEBA2>刚才的混混：</color>撤，撤退！", talk_content = "撤，撤退！", talk_name = "刚才的混混",},
	[102361501] = { talk_id = 102361501, group_id = 1023615, type = 4, aside = "<color=#FFEBA2>青乌：</color>这样一样线索可全断了，刚刚该追过去吗……", talk_content = "这样一样线索可全断了，刚刚该追过去吗……", talk_name = "青乌",},
	[102370101] = { talk_id = 102370101, group_id = 1023701, type = 1, next_id = 102370102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "只有一个人果然还是有些勉强……", talk_name = "？？？",},
	[102370102] = { talk_id = 102370102, group_id = 1023701, type = 1, next_id = 102370103, talk_content = "你好？", talk_name = "青乌",},
	[102370103] = { talk_id = 102370103, group_id = 1023701, type = 1, next_id = 102370104, talk_content = "哇，啊！", talk_name = "？？？",},
	[102370104] = { talk_id = 102370104, group_id = 1023701, type = 1, next_id = 102370105, talk_content = "痛痛痛痛……", talk_name = "？？？",},
	[102370105] = { talk_id = 102370105, group_id = 1023701, type = 1, next_id = 102370106, talk_content = "你没事吧。", talk_name = "青乌",},
	[102370106] = { talk_id = 102370106, group_id = 1023701, type = 1, talk_content = "你说呢！", talk_name = "？？？",},
	[102370201] = { talk_id = 102370201, group_id = 1023702, type = 1, next_id = 102370202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我来报刚才的仇了，青乌！", talk_name = "混混",},
	[102370202] = { talk_id = 102370202, group_id = 1023702, type = 1, next_id = 102370203, talk_content = "他们是什么人？", talk_name = "祁玉",},
	[102370203] = { talk_id = 102370203, group_id = 1023702, type = 1, talk_content = "刚刚在遥遥街里面揍过的混混。", talk_name = "青乌",},
	[102370301] = { talk_id = 102370301, group_id = 1023703, type = 4, aside = "<color=#FFEBA2>祁玉：</color>往这边来，青乌！", talk_content = "往这边来，青乌！", talk_name = "祁玉",},
	[102370401] = { talk_id = 102370401, group_id = 1023704, type = 1, next_id = 102370402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "找到悬天的人了，现在要咋办？", talk_name = "青乌",},
	[102370402] = { talk_id = 102370402, group_id = 1023704, type = 1, next_id = 102370403, talk_content = "那自然是去他们说的那个空地了，现在想来抗议也不见得有什么效果。", talk_name = "祁玉",},
	[102370403] = { talk_id = 102370403, group_id = 1023704, type = 2, child_type = 21, options = {{"那就去大闹一场吧。",102370404,},{"冲进敌人阵地真的没问题吗？",102370407,},{"我想叫点人。",102370411,},}, talk_content = "青乌，我们去他刚刚提到的空地来个人赃并获吧。", talk_name = "祁玉",},
	[102370404] = { talk_id = 102370404, group_id = 1023704, type = 3, next_id = 102370405, icon = "interaction_4", talk_content = "大闹一场我是没什么问题，就是不知道他们这次的货跟你们运的是不是同一个。", talk_name = "青乌",},
	[102370405] = { talk_id = 102370405, group_id = 1023704, type = 3, next_id = 102370406, talk_content = "你接到的委托是调查悬天，所以只要有收获不就得了？", talk_name = "祁玉",},
	[102370406] = { talk_id = 102370406, group_id = 1023704, type = 3, talk_content = "也是。", talk_name = "青乌",},
	[102370407] = { talk_id = 102370407, group_id = 1023704, type = 3, next_id = 102370408, icon = "interaction_4", talk_content = "可那空地附近不都是他们的店吗，我只怕靠近的时候就会有一群人冲出来。", talk_name = "青乌",},
	[102370408] = { talk_id = 102370408, group_id = 1023704, type = 3, next_id = 102370409, talk_content = "这部分就随机应变吧，要是人数在可控范围就出手，感觉不对路就跑路，情况交给你来判断如何？", talk_name = "祁玉",},
	[102370409] = { talk_id = 102370409, group_id = 1023704, type = 3, next_id = 102370410, talk_content = "先采取行动是吧，我还以为你是更冷静的类型。", talk_name = "青乌",},
	[102370410] = { talk_id = 102370410, group_id = 1023704, type = 3, talk_content = "只是着急回家做饭而已，要不家里那三个……唉，不提了。", talk_name = "祁玉",},
	[102370411] = { talk_id = 102370411, group_id = 1023704, type = 3, next_id = 102370412, icon = "interaction_4", talk_content = "既然是这样的话我叫点人过来如何，只靠我们两个人我觉得不太稳妥。", talk_name = "青乌",},
	[102370412] = { talk_id = 102370412, group_id = 1023704, type = 3, next_id = 102370413, talk_content = "等确认那边真有东西再叫也不迟，之前给悬天运货的时候货也是刚到点几分钟就会被人车带走。", talk_name = "祁玉",},
	[102370413] = { talk_id = 102370413, group_id = 1023704, type = 3, talk_content = "我们要是去晚了那我估计那边就真成“空地”了。", talk_name = "祁玉",},
	[102370501] = { talk_id = 102370501, group_id = 1023705, type = 1, next_id = 102370502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "这没东西，青乌！", talk_name = "祁玉",},
	[102370502] = { talk_id = 102370502, group_id = 1023705, type = 1, next_id = 102370503, talk_content = "也不是没有。", talk_name = "青乌",},
	[102370503] = { talk_id = 102370503, group_id = 1023705, type = 1, next_id = 102370504, talk_content = "真用一个信息就钓上来了，跟之前一样蠢啊，创生主义！", talk_name = "悬天斩客",},
	[102370504] = { talk_id = 102370504, group_id = 1023705, type = 1, talk_content = "说谁笨呢！", talk_name = "祁玉",},
	[102370601] = { talk_id = 102370601, group_id = 1023706, type = 1, next_id = 102370602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "唔……刚刚悬天好像说过创生主义这几个字对吗？", talk_name = "祁玉",},
	[102370602] = { talk_id = 102370602, group_id = 1023706, type = 1, next_id = 102370603, talk_content = "是有这么说，应该是认出来了吧。", talk_name = "青乌",},
	[102370603] = { talk_id = 102370603, group_id = 1023706, type = 2, options = {{"等等，那我的事情呢？",102370604,},{"要我和你一起走吗？",102370605,},}, talk_content = "这地方也是个陷阱，该不会是我们反过来被盯上了……青乌，我得回去通知创生主义的大家。", talk_name = "祁玉",},
	[102370604] = { talk_id = 102370604, group_id = 1023706, type = 3, next_id = 102370608, talk_content = "你不是知道帮过悬天的本地人就是我们了吗，我们替悬天运过货，至于货是什么只能靠你自己去调查了。", talk_name = "祁玉",},
	[102370605] = { talk_id = 102370605, group_id = 1023706, type = 3, next_id = 102370606, talk_content = "谢谢，但请你别把我当小孩。", talk_name = "祁玉",},
	[102370606] = { talk_id = 102370606, group_id = 1023706, type = 3, next_id = 102370607, talk_content = "我只是担心悬天还会来袭击你。", talk_name = "青乌",},
	[102370607] = { talk_id = 102370607, group_id = 1023706, type = 3, next_id = 102370608, talk_content = "不，不用担心啦。", talk_name = "祁玉",},
	[102370608] = { talk_id = 102370608, group_id = 1023706, type = 1, talk_content = "总之我回去以后会和其他人都说说这次的事，假如之后知道了运的货是什么的话就再联络吧。", talk_name = "祁玉",},
	[102370701] = { talk_id = 102370701, group_id = 1023707, type = 4, aside = "<color=#FFEBA2>青乌：</color>找到了，她在这干嘛呢。", talk_content = "找到了，她在这干嘛呢。", talk_name = "青乌",},
	[102370801] = { talk_id = 102370801, group_id = 1023708, type = 2, child_type = 21, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"抱歉。",102370802,},{"我觉得我挺光明正大的。",102370803,},{"你在做亏心事吧？",102370804,},}, talk_content = "就没人和你说过不要突然出现在别人身后的吗！", talk_name = "？？？",},
	[102370802] = { talk_id = 102370802, group_id = 1023708, type = 3, next_id = 102370809, icon = "interaction_4", talk_content = "呃，嗯……下次注意点就好！", talk_name = "？？？",},
	[102370803] = { talk_id = 102370803, group_id = 1023708, type = 3, next_id = 102370809, icon = "interaction_4", talk_content = "哪有，脚步声都不带一个的，你是气球吗！", talk_name = "？？？",},
	[102370804] = { talk_id = 102370804, group_id = 1023708, type = 3, next_id = 102370805, icon = "interaction_4", talk_content = "没没没，没有啊，你哪只眼睛看见了，我才没有在跟踪人。", talk_name = "？？？",},
	[102370805] = { talk_id = 102370805, group_id = 1023708, type = 3, next_id = 102370806, talk_content = "……", talk_name = "青乌",},
	[102370806] = { talk_id = 102370806, group_id = 1023708, type = 3, next_id = 102370807, talk_content = "……我没有做亏心事哦。", talk_name = "？？？",},
	[102370807] = { talk_id = 102370807, group_id = 1023708, type = 3, next_id = 102370808, talk_content = "那我能在这里大喊大叫吗？", talk_name = "青乌",},
	[102370808] = { talk_id = 102370808, group_id = 1023708, type = 3, next_id = 102370809, talk_content = "不要，求你了！", talk_name = "？？？",},
	[102370809] = { talk_id = 102370809, group_id = 1023708, type = 2, options = {{"（说明来意）",102370810,},}, talk_content = "好啦，所以你是谁，会拍我肩膀就代表有事找我是吧！", talk_name = "？？？",},
	[102370810] = { talk_id = 102370810, group_id = 1023708, type = 3, next_id = 102370811, talk_content = "这样……终究还是暴露了啊，是的，我们创生主义是接过悬天的委托。", talk_name = "？？？",},
	[102370811] = { talk_id = 102370811, group_id = 1023708, type = 1, next_id = 102370812, talk_content = "但那是我们不知道委托人是悬天的情况下，要是知道的话绝对不会接那个委托的。", talk_name = "？？？",},
	[102370812] = { talk_id = 102370812, group_id = 1023708, type = 2, options = {{"研究院是什么地方？",102370813,},{"你们创生主义是什么团体？",102370817,},{"所以你们和悬天不是一伙的？",102370820,},{"<color=#FFEBA2>能和我说说委托的内容吗？",102370821,},}, talk_content = "对了，我叫祁玉，创生主义的成员，还是创生研究院创立以来最年轻的院士。", talk_name = "祁玉",},
	[102370813] = { talk_id = 102370813, group_id = 1023708, type = 3, next_id = 102370814, talk_content = "你是外地人吧，竟然不知道研究院，研究院全名是创生密学院，是这个天月城的学术首府。", talk_name = "祁玉",},
	[102370814] = { talk_id = 102370814, group_id = 1023708, type = 3, next_id = 102370815, talk_content = "除了我所在的生物科室以外，密学院里面还有各种各样的科室，还设有教授普通的小中大学的学业课程。", talk_name = "祁玉",},
	[102370815] = { talk_id = 102370815, group_id = 1023708, type = 3, next_id = 102370816, talk_content = "所以是个大学城？", talk_name = "青乌",},
	[102370816] = { talk_id = 102370816, group_id = 1023708, type = 3, next_id = 102370812, talk_content = "嗯，差不多吧。", talk_name = "祁玉",},
	[102370817] = { talk_id = 102370817, group_id = 1023708, type = 3, next_id = 102370818, talk_content = "创生主义是引用地上，也就是我们头上的土地创生地名字来命名的团体，包括我在内合计有四个人。", talk_name = "祁玉",},
	[102370818] = { talk_id = 102370818, group_id = 1023708, type = 3, next_id = 102370819, talk_content = "只有四个人吗？", talk_name = "青乌",},
	[102370819] = { talk_id = 102370819, group_id = 1023708, type = 3, next_id = 102370812, talk_content = "是，因为我们的目标也只对我们自己有益。", talk_name = "祁玉",},
	[102370820] = { talk_id = 102370820, group_id = 1023708, type = 3, next_id = 102370812, talk_content = "当然不是了，先不说我，创生主义有个猴子可讨厌坏人了。", talk_name = "祁玉",},
	[102370821] = { talk_id = 102370821, group_id = 1023708, type = 1, next_id = 102370822, talk_content = "就是个运货任务而已，数量挺多，还要分好几批次运。", talk_name = "祁玉",},
	[102370822] = { talk_id = 102370822, group_id = 1023708, type = 1, next_id = 102370823, talk_content = "那运的是……？", talk_name = "青乌",},
	[102370823] = { talk_id = 102370823, group_id = 1023708, type = 1, next_id = 102370824, talk_content = "我也不太清楚运的是什么东西，不过基本都挺轻的……对了，还有个重玩意，不过中途就不让我们搬了。", talk_name = "祁玉",},
	[102370824] = { talk_id = 102370824, group_id = 1023708, type = 1, next_id = 102370825, talk_content = "只知道重量吗，还有没有什么线索。", talk_name = "青乌",},
	[102370825] = { talk_id = 102370825, group_id = 1023708, type = 1, next_id = 102370826, talk_content = "送货的时候都是一个个密封起来的箱，对了，要想知道内容物的话要不要跟我一起找悬天的人理论？", talk_name = "祁玉",},
	[102370826] = { talk_id = 102370826, group_id = 1023708, type = 1, next_id = 102370827, talk_content = "所以你是在找悬天的人吗？", talk_name = "青乌",},
	[102370827] = { talk_id = 102370827, group_id = 1023708, type = 1, talk_content = "嗯，不害也说被骗了就要大声抗议，你也跟过来吧，青乌。", talk_name = "祁玉",},
	[102370901] = { talk_id = 102370901, group_id = 1023709, type = 2, child_type = 21, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"抱歉。",102370902,},{"这也不是我能预见的。",102370903,},{"当作是悬天前的热身得了。",102370904,},}, talk_content = "我可没想到刚和你组队就要和混混打架。", talk_name = "祁玉",},
	[102370902] = { talk_id = 102370902, group_id = 1023709, type = 3, next_id = 102370907, icon = "interaction_4", talk_content = "知道就好，下次你得多注意点，不然煮熟的鸭子可都要飞走了。", talk_name = "祁玉",},
	[102370903] = { talk_id = 102370903, group_id = 1023709, type = 3, next_id = 102370907, icon = "interaction_4", talk_content = "未雨绸缪才是一个成年人应该做的东西，青乌你还是太不谨慎了。", talk_name = "祁玉",},
	[102370904] = { talk_id = 102370904, group_id = 1023709, type = 3, next_id = 102370905, icon = "interaction_4", talk_content = "这话说得跟我们团长似的……该不会青乌你也是那种超级武斗派吧。", talk_name = "祁玉",},
	[102370905] = { talk_id = 102370905, group_id = 1023709, type = 3, next_id = 102370906, talk_content = "我只是不太喜欢拐弯抹角的处事方式，反正我们等下是要逮个悬天的人来问话不是吗？", talk_name = "青乌",},
	[102370906] = { talk_id = 102370906, group_id = 1023709, type = 3, next_id = 102370907, talk_content = "说是这么说。", talk_name = "祁玉",},
	[102370907] = { talk_id = 102370907, group_id = 1023709, type = 1, next_id = 102370908, talk_content = "那我们现在是要怎么找悬天的人，随便找也不能确保他会知道当时运的什么货吧？", talk_name = "青乌",},
	[102370908] = { talk_id = 102370908, group_id = 1023709, type = 1, next_id = 102370909, talk_content = "只要找当时给我们创生主义下委托的人就好，那人老是变着样子混在人群之中，之前我们也是因为看不出来才被他骗了。", talk_name = "祁玉",},
	[102370909] = { talk_id = 102370909, group_id = 1023709, type = 1, next_id = 102370910, talk_content = "那我觉得现在也未必能看出来。", talk_name = "青乌",},
	[102370910] = { talk_id = 102370910, group_id = 1023709, type = 1, next_id = 102370911, talk_content = "用”操控“就好，你也应该试过用那个来调查别人吧。", talk_name = "祁玉",},
	[102370911] = { talk_id = 102370911, group_id = 1023709, type = 1, talk_content = "那个啊……", talk_name = "青乌",},
	[102371001] = { talk_id = 102371001, group_id = 1023710, type = 4, aside = "<color=#FFEBA2>青乌：</color>打怪旁白", talk_content = "打怪旁白", talk_name = "青乌",},
	[102371101] = { talk_id = 102371101, group_id = 1023711, type = 4, aside = "<color=#FFEBA2>祁玉：</color>青乌，你应该带着箴石之劣吧，用那个把附近的人都确认下吧。", talk_content = "青乌，你应该带着箴石之劣吧，用那个把附近的人都确认下吧。", talk_name = "祁玉",},
	[102371301] = { talk_id = 102371301, group_id = 1023713, type = 4, aside = "<color=#FFEBA2>祁玉：</color>一路上都没有人……青乌。", talk_content = "一路上都没有人……青乌。", talk_name = "祁玉",},
	[102371302] = { talk_id = 102371302, group_id = 1023713, type = 4, aside = "<color=#FFEBA2>青乌：</color>不太妙。", talk_content = "不太妙。", talk_name = "青乌",},
	[102380101] = { talk_id = 102380101, group_id = 1023801, type = 4, next_id = 102380102, aside = "<color=#FFEBA2>青乌：</color>离歌，给悬天干活的是叫创生主义的组织，他们似乎帮悬天运了些货到天月城来。", call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "离歌，给悬天干活的是叫创生主义的组织，他们似乎帮悬天运了些货到天月城来。", talk_name = "青乌",},
	[102380102] = { talk_id = 102380102, group_id = 1023801, type = 4, next_id = 102380103, aside = "<color=#FFEBA2>离歌：</color>运货啊……找本地人来运大概是为了避开一些审查，那些货都是啥你有查到吗？", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "运货啊……找本地人来运大概是为了避开一些审查，那些货都是啥你有查到吗？", talk_name = "离歌",},
	[102380103] = { talk_id = 102380103, group_id = 1023801, type = 4, next_id = 102380104, aside = "<color=#FFEBA2>青乌：</color>没有，而且货的位置也没确认到，半路还给人偷袭了。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "没有，而且货的位置也没确认到，半路还给人偷袭了。", talk_name = "青乌",},
	[102380104] = { talk_id = 102380104, group_id = 1023801, type = 4, next_id = 102380105, aside = "<color=#FFEBA2>离歌：</color>……没受伤就好，青乌，你接下来去找维安司的那位来来吧，我会转告你的收获给她。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "……没受伤就好，青乌，你接下来去找维安司的那位来来吧，我会转告你的收获给她。", talk_name = "离歌",},
	[102380105] = { talk_id = 102380105, group_id = 1023801, type = 4, next_id = 102380106, aside = "<color=#FFEBA2>青乌：</color>找她做什么？", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "找她做什么？", talk_name = "青乌",},
	[102380106] = { talk_id = 102380106, group_id = 1023801, type = 4, aside = "<color=#FFEBA2>离歌：</color>但她知道悬天成员会频繁进出的地方，或许货物就放在其中一个地也说不定。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "但她知道悬天成员会频繁进出的地方，或许货物就放在其中一个地也说不定。", talk_name = "离歌",},
	[102380201] = { talk_id = 102380201, group_id = 1023802, type = 2, child_type = 21, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"维安司的待遇如何？",102380202,},{"好啊，我想和你当同事。",102380208,},{"还是谈正事吧。",102380214,},}, talk_content = "没想到我们这么快又见面了，这么有缘，我看你干脆来我们维安司如何？", talk_name = "来来",},
	[102380202] = { talk_id = 102380202, group_id = 1023802, type = 3, next_id = 102380203, talk_content = "说其维安司的待遇怎么样？", talk_name = "青乌",},
	[102380203] = { talk_id = 102380203, group_id = 1023802, type = 3, next_id = 102380204, talk_content = "在天月城里面算是中上的水平哦，只要在维安司好好干的话在天月城买房买车都不是什么问题。", talk_name = "来来",},
	[102380204] = { talk_id = 102380204, group_id = 1023802, type = 3, next_id = 102380205, talk_content = "真的有这么好赚吗……维安司的钱都是哪里来的？", talk_name = "青乌",},
	[102380205] = { talk_id = 102380205, group_id = 1023802, type = 3, next_id = 102380206, talk_content = "钱……啊，这个可不能随便乱说。", talk_name = "来来",},
	[102380206] = { talk_id = 102380206, group_id = 1023802, type = 3, next_id = 102380207, talk_content = "不能乱说的金钱来源，你们该不会……", talk_name = "青乌",},
	[102380207] = { talk_id = 102380207, group_id = 1023802, type = 3, next_id = 102380215, talk_content = "你想的事情我们一件都没碰，我们靠的是社会捐赠，现在能告诉给你的就这么多了。", talk_name = "来来",},
	[102380208] = { talk_id = 102380208, group_id = 1023802, type = 3, next_id = 102380209, talk_content = "好啊，我刚好也想和你当同事。", talk_name = "青乌",},
	[102380209] = { talk_id = 102380209, group_id = 1023802, type = 3, next_id = 102380210, talk_content = "所以我要怎么样才能成为维安司的成员，是要通过什么考试吗？", talk_name = "青乌",},
	[102380210] = { talk_id = 102380210, group_id = 1023802, type = 3, next_id = 102380211, talk_content = "加入维安司要通过一次笔试和三次实战测试，而且还要通过一些严格的背调。", talk_name = "来来",},
	[102380211] = { talk_id = 102380211, group_id = 1023802, type = 3, next_id = 102380212, talk_content = "话说你应该没在开玩笑吧？", talk_name = "来来",},
	[102380212] = { talk_id = 102380212, group_id = 1023802, type = 3, next_id = 102380213, talk_content = "你认为呢？", talk_name = "青乌",},
	[102380213] = { talk_id = 102380213, group_id = 1023802, type = 3, next_id = 102380215, talk_content = "哈哈，要是你之后还有这个意思的话我就带你参加下考试吧。", talk_name = "来来",},
	[102380214] = { talk_id = 102380214, group_id = 1023802, type = 3, next_id = 102380215, talk_content = "也对，毕竟现在是工作时间。", talk_name = "来来",},
	[102380215] = { talk_id = 102380215, group_id = 1023802, type = 1, next_id = 102380216, talk_content = "说回正题，青乌你那边是确认到悬天找本地人给他们运了些货是吧？", talk_name = "来来",},
	[102380216] = { talk_id = 102380216, group_id = 1023802, type = 1, next_id = 102380217, talk_content = "就是不知道货是什么，也不知道货在哪里。", talk_name = "青乌",},
	[102380217] = { talk_id = 102380217, group_id = 1023802, type = 1, next_id = 102380218, talk_content = "嗯，到底送了啥货这事我也没线索，不过我的确看见过悬天把外来的货送到某两个建筑里面。", talk_name = "来来",},
	[102380218] = { talk_id = 102380218, group_id = 1023802, type = 1, next_id = 102380219, talk_content = "分别是哪两个？", talk_name = "青乌",},
	[102380219] = { talk_id = 102380219, group_id = 1023802, type = 2, options = {{"那我们该怎么确认？",102380220,},{"那要不收手？",102380221,},}, talk_content = "一个在东，一个在西，不过我估计其中一个是诱饵……因为悬天肯定注意到了城里有人想要对付他们。", talk_name = "来来",},
	[102380220] = { talk_id = 102380220, group_id = 1023802, type = 3, talk_content = "那自然是收集线索了，青乌，你应该会玩摄影吧？", talk_name = "来来",},
	[102380221] = { talk_id = 102380221, group_id = 1023802, type = 3, talk_content = "为了天月城的和平可没有收手的道理，青乌，现在是大搞摄影的时间了！", talk_name = "来来",},
	[102380301] = { talk_id = 102380301, group_id = 1023803, type = 4, aside = "<color=#FFEBA2>青乌：</color>要爬上去啊……", talk_content = "要爬上去啊……", talk_name = "青乌",},
	[102380401] = { talk_id = 102380401, group_id = 1023804, type = 1, next_id = 102380402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我还以为这里也会有群混混。", talk_name = "青乌",},
	[102380402] = { talk_id = 102380402, group_id = 1023804, type = 1, next_id = 102380403, talk_content = "要真是这样那就该出现个叫楼顶帮的帮派了。", talk_name = "来来",},
	[102380403] = { talk_id = 102380403, group_id = 1023804, type = 1, next_id = 102380404, talk_content = "青乌，那栋很显眼的楼就是我们的目标。", talk_name = "来来",},
	[102380404] = { talk_id = 102380404, group_id = 1023804, type = 1, talk_content = "用相机把觉得可疑的地方都拍一遍吧。", talk_name = "来来",},
	[102380501] = { talk_id = 102380501, group_id = 1023805, type = 1, next_id = 102380502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "喂，你们两个鬼鬼祟祟的在这干嘛！", talk_name = "悬天斩客",},
	[102380502] = { talk_id = 102380502, group_id = 1023805, type = 1, talk_content = "是悬天的人……干趴他们！", talk_name = "来来",},
	[102380601] = { talk_id = 102380601, group_id = 1023806, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"可是被对面发现了。",102380602,},{"我还以为会更紧张刺激。",102380605,},}, talk_content = "有惊无险呢，青乌。", talk_name = "来来",},
	[102380602] = { talk_id = 102380602, group_id = 1023806, type = 3, next_id = 102380603, talk_content = "被发现倒不要紧，毕竟他们平常就不干好事，看不惯他们的人可多着呢。", talk_name = "来来",},
	[102380603] = { talk_id = 102380603, group_id = 1023806, type = 3, next_id = 102380604, talk_content = "就是他们未必知道我们是哪来的吗？", talk_name = "青乌",},
	[102380604] = { talk_id = 102380604, group_id = 1023806, type = 3, next_id = 102380609, talk_content = "嗯，很安全的啦。", talk_name = "来来",},
	[102380605] = { talk_id = 102380605, group_id = 1023806, type = 3, next_id = 102380606, talk_content = "青乌你有时候还挺危险的，不过我本来也以为会有一群人跟过来。", talk_name = "来来",},
	[102380606] = { talk_id = 102380606, group_id = 1023806, type = 3, next_id = 102380607, talk_content = "估计是悬天也不想把事情闹大吧。", talk_name = "来来",},
	[102380607] = { talk_id = 102380607, group_id = 1023806, type = 3, next_id = 102380608, talk_content = "为什么？", talk_name = "青乌",},
	[102380608] = { talk_id = 102380608, group_id = 1023806, type = 3, next_id = 102380609, talk_content = "他们在天月城根基尚浅，盯着他们的人可多了。", talk_name = "来来",},
	[102380609] = { talk_id = 102380609, group_id = 1023806, type = 1, next_id = 102380610, talk_content = "虽然出了点意外，但照片算是都拍下来了，青乌，我接下来要把照片拿回维安司。", talk_name = "来来",},
	[102380610] = { talk_id = 102380610, group_id = 1023806, type = 1, next_id = 102380611, talk_content = "那我……？", talk_name = "青乌",},
	[102380611] = { talk_id = 102380611, group_id = 1023806, type = 1, next_id = 102380612, talk_content = "在我们分析完成之前先休息一下如何，我看你这阵子也跑不少地方了吧。", talk_name = "来来",},
	[102380612] = { talk_id = 102380612, group_id = 1023806, type = 1, talk_content = "那就先回望月一趟吧……", talk_name = "青乌",},
	[102380701] = { talk_id = 102380701, group_id = 1023807, type = 4, next_id = 102380702, aside = "<color=#FFEBA2>混混：</color>喂，这里可是私人领地！", talk_content = "喂，这里可是私人领地！", talk_name = "混混",},
	[102380702] = { talk_id = 102380702, group_id = 1023807, type = 4, aside = "<color=#FFEBA2>来来：</color>现在不是了。", talk_content = "现在不是了。", talk_name = "来来",},
	[102380801] = { talk_id = 102380801, group_id = 1023808, type = 1, next_id = 102380802, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "这里风景就不错，有看到那边的大楼吧。", talk_name = "来来",},
	[102380802] = { talk_id = 102380802, group_id = 1023808, type = 2, options = {{"这栋楼是悬天的吗？",102380803,},{"那接下来要干嘛？",102380807,},}, talk_content = "那栋就是我们此行的其中一个目标。", talk_name = "来来",},
	[102380803] = { talk_id = 102380803, group_id = 1023808, type = 3, next_id = 102380804, talk_content = "嗯，悬天刚来天月城的时候一口气买了好几栋高楼，这就是其中一栋。", talk_name = "来来",},
	[102380804] = { talk_id = 102380804, group_id = 1023808, type = 3, next_id = 102380805, talk_content = "能买几栋……他们也太有钱了吧？", talk_name = "青乌",},
	[102380805] = { talk_id = 102380805, group_id = 1023808, type = 3, next_id = 102380806, talk_content = "毕竟是那个悬天。", talk_name = "来来",},
	[102380806] = { talk_id = 102380806, group_id = 1023808, type = 3, next_id = 102380808, talk_content = "不说这些了，拍照要紧。", talk_name = "来来",},
	[102380807] = { talk_id = 102380807, group_id = 1023808, type = 3, next_id = 102380808, talk_content = "我刚刚不也说了嘛，摄影。", talk_name = "来来",},
	[102380808] = { talk_id = 102380808, group_id = 1023808, type = 1, next_id = 102380809, talk_content = "该不会是要拍那栋楼吧？", talk_name = "青乌",},
	[102380809] = { talk_id = 102380809, group_id = 1023808, type = 2, options = {{"真的靠谱吗？",102380810,},{"要怎么拍？",102380811,},}, talk_content = "是哦，维安司里面有专门分析照片的小组，我打算两栋楼都各自拍些照片回去让他们检查下。", talk_name = "来来",},
	[102380810] = { talk_id = 102380810, group_id = 1023808, type = 3, next_id = 102380812, talk_content = "要是不靠谱我就不会拉着你过来了。", talk_name = "来来",},
	[102380811] = { talk_id = 102380811, group_id = 1023808, type = 3, next_id = 102380812, talk_content = "凭感觉？", talk_name = "来来",},
	[102380812] = { talk_id = 102380812, group_id = 1023808, type = 1, talk_content = "青乌，总之你也把相机拿上吧，我教你怎么拍，我们多搞些照片回去。", talk_name = "来来",},
	[102380901] = { talk_id = 102380901, group_id = 1023809, type = 1, next_id = 102380902, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "青乌，再对着你认为可疑的地方多拍几张吧。", talk_name = "来来",},
	[102380902] = { talk_id = 102380902, group_id = 1023809, type = 1, talk_content = "我倒是看不出哪里可疑就是了……", talk_name = "青乌",},
	[102381001] = { talk_id = 102381001, group_id = 1023810, type = 1, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "这里拍得差不多了，我们下一个地方吧。", talk_name = "来来",},
	[102381101] = { talk_id = 102381101, group_id = 1023811, type = 4, next_id = 102381102, aside = "<color=#FFEBA2>悬天斩客：</color>来……人……", talk_content = "来……人……", talk_name = "悬天斩客",},
	[102381102] = { talk_id = 102381102, group_id = 1023811, type = 4, next_id = 102381103, aside = "<color=#FFEBA2>青乌：</color>他刚刚是不是叫人了？", talk_content = "他刚刚是不是叫人了？", talk_name = "青乌",},
	[102381103] = { talk_id = 102381103, group_id = 1023811, type = 4, aside = "<color=#FFEBA2>来来：</color>那肯定是啊，我们得赶紧离开这里，不然等下就要被围起来了！", talk_content = "那肯定是啊，我们得赶紧离开这里，不然等下就要被围起来了！", talk_name = "来来",},
	[102381201] = { talk_id = 102381201, group_id = 1023812, type = 4, aside = "<color=#FFEBA2>青乌：</color>话说离歌有来来的联络方式吗……", talk_content = "话说离歌有来来的联络方式吗……", talk_name = "青乌",},
	[102381301] = { talk_id = 102381301, group_id = 1023813, type = 4, aside = "<color=#FFEBA2>来来：</color>青乌，这边，青乌！", talk_content = "青乌，这边，青乌！", talk_name = "来来",},
	[102381401] = { talk_id = 102381401, group_id = 1023814, type = 4, next_id = 102381402, aside = "<color=#FFEBA2>青乌：</color>摄影跟我们要找货在哪里有什么关系？", talk_content = "摄影跟我们要找货在哪里有什么关系？", talk_name = "青乌",},
	[102381402] = { talk_id = 102381402, group_id = 1023814, type = 4, aside = "<color=#FFEBA2>来来：</color>待会你就知道了，先找个合适的地方吧。", talk_content = "待会你就知道了，先找个合适的地方吧。", talk_name = "来来",},
	[102390101] = { talk_id = 102390101, group_id = 1023901, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"离歌在哪里？",102390102,},{"你是什么？",102390106,},}, talk_content = "嘿嘿，青乌回来了，鼓呢，铜锣呢，都给哥敲起来！", talk_name = "喜大",},
	[102390102] = { talk_id = 102390102, group_id = 1023901, type = 3, next_id = 102390103, talk_content = "离歌大概是去处理生意了吧，这天月城寸土寸金，不多搞点钱等下可就付不起这的租金了。", talk_name = "喜大",},
	[102390103] = { talk_id = 102390103, group_id = 1023901, type = 3, next_id = 102390104, talk_content = "原来这是租的吗？", talk_name = "青乌",},
	[102390104] = { talk_id = 102390104, group_id = 1023901, type = 3, next_id = 102390105, talk_content = "离歌没和你说过呀，嘿嘿，也是，钱这事你知道了也没什么帮助，开心点就好。", talk_name = "喜大",},
	[102390105] = { talk_id = 102390105, group_id = 1023901, type = 3, next_id = 102390101, talk_content = "总觉得有点牛头不搭马嘴。", talk_name = "青乌",},
	[102390106] = { talk_id = 102390106, group_id = 1023901, type = 3, next_id = 102390107, talk_content = "哥是我，不对，应该说喜大是五大的超级杰作，天下第一的开心果，看我这个美丽的身体。", talk_name = "喜大",},
	[102390107] = { talk_id = 102390107, group_id = 1023901, type = 3, next_id = 102390108, talk_content = "闭嘴哈哈哈！", talk_name = "喜大",},
	[102390108] = { talk_id = 102390108, group_id = 1023901, type = 3, next_id = 102390109, talk_content = "这是怎么回事……", talk_name = "青乌",},
	[102390109] = { talk_id = 102390109, group_id = 1023901, type = 3, talk_content = "青乌，看短信吧，哥用短信跟你聊！", talk_name = "喜大",},
	[102390201] = { talk_id = 102390201, group_id = 1023902, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"小五是什么人？",102390202,},}, talk_content = "我听说你帮小五找了些素材是吧，谢谢。", talk_name = "离歌",},
	[102390202] = { talk_id = 102390202, group_id = 1023902, type = 3, next_id = 102390203, talk_content = "从以前就很粘你的望月的联络员，明明是我抢回来的人，但她更亲近的却是你。", talk_name = "离歌",},
	[102390203] = { talk_id = 102390203, group_id = 1023902, type = 1, next_id = 102390204, talk_content = "你刚刚是不是说抢？", talk_name = "青乌",},
	[102390204] = { talk_id = 102390204, group_id = 1023902, type = 2, options = {{"还有其他人？",102390205,},{"望月到底要做什么？",102390206,},}, talk_content = "说了，小五的能力是制作替身，多亏了这些替身我才能把望月的成员散开到世界各地。", talk_name = "离歌",},
	[102390205] = { talk_id = 102390205, group_id = 1023902, type = 3, next_id = 102390209, talk_content = "还有五个吧，一个独行侠，剩下四个两两一队，为了凑到像青乌你这么强的人我可是费尽了心思。", talk_name = "离歌",},
	[102390206] = { talk_id = 102390206, group_id = 1023902, type = 3, next_id = 102390207, talk_content = "做大事，仅此而已。", talk_name = "离歌",},
	[102390207] = { talk_id = 102390207, group_id = 1023902, type = 3, next_id = 102390208, talk_content = "说了等于没说。", talk_name = "青乌",},
	[102390208] = { talk_id = 102390208, group_id = 1023902, type = 3, next_id = 102390209, talk_content = "等你想起来以前的事了再谈吧。", talk_name = "离歌",},
	[102390209] = { talk_id = 102390209, group_id = 1023902, type = 1, next_id = 102390210, talk_content = "另外上次你和那位来来拍的照片出结果了，维安司说他们确定到了哪栋大楼里面放着偷运的货。", talk_name = "离歌",},
	[102390210] = { talk_id = 102390210, group_id = 1023902, type = 1, next_id = 102390211, talk_content = "然后，王土的委托人希望我们能进去看看悬天费心思运进来的到底是些什么东西。", talk_name = "离歌",},
	[102390211] = { talk_id = 102390211, group_id = 1023902, type = 1, next_id = 102390212, talk_content = "怎么进去，那不是悬天的楼吗？", talk_name = "青乌",},
	[102390212] = { talk_id = 102390212, group_id = 1023902, type = 2, child_type = 21, options = {{"要不直接闯进去吧？",102390213,},{"找找不危险的方法如何？",102390217,},{"详细说说那个佩从。",102390220,},}, talk_content = "自然是偷摸进去了，有个被称呼为”石龙“的佩从能帮我们实现这件事。", talk_name = "离歌",},
	[102390213] = { talk_id = 102390213, group_id = 1023902, type = 3, next_id = 102390214, icon = "interaction_4", talk_content = "要不直接从大门闯进去吧，至今为止碰见的悬天小兵也没什么了不起的。", talk_name = "青乌",},
	[102390214] = { talk_id = 102390214, group_id = 1023902, type = 3, next_id = 102390215, talk_content = "还是免了吧，悬天负责战斗的可不只有小兵，要是刚好碰见干部的话估计会变得很麻烦。", talk_name = "离歌",},
	[102390215] = { talk_id = 102390215, group_id = 1023902, type = 3, next_id = 102390216, talk_content = "干部有多强？", talk_name = "青乌",},
	[102390216] = { talk_id = 102390216, group_id = 1023902, type = 3, next_id = 102390225, talk_content = "按你现在的强度来说至少是你五六倍的水平吧，现在直接面对他们还是有些冒险了。", talk_name = "离歌",},
	[102390217] = { talk_id = 102390217, group_id = 1023902, type = 3, next_id = 102390219, icon = "interaction_4", talk_content = "潜入听着就很危险，有没有另外的办法？", talk_name = "青乌",},
	[102390218] = { talk_id = 102390218, group_id = 1023902, type = 3, next_id = 102390220, talk_content = "潜入是眼下最好的办法，一旦出现什么危险的话我们也能立刻逃跑。", talk_name = "离歌",},
	[102390219] = { talk_id = 102390219, group_id = 1023902, type = 3, next_id = 102390225, talk_content = "至少比直接冲进去被人包围要强。", talk_name = "离歌",},
	[102390220] = { talk_id = 102390220, group_id = 1023902, type = 3, next_id = 102390221, icon = "interaction_4", talk_content = "再跟我详细说说那个石龙如何，我得评估一下这个计划。", talk_name = "青乌",},
	[102390221] = { talk_id = 102390221, group_id = 1023902, type = 3, next_id = 102390222, talk_content = "”石龙“是种变异的生物，杂食，卵生，而且能够潜入到气脉中进行移动。", talk_name = "离歌",},
	[102390222] = { talk_id = 102390222, group_id = 1023902, type = 3, next_id = 102390223, talk_content = "怎么潜……？", talk_name = "青乌",},
	[102390223] = { talk_id = 102390223, group_id = 1023902, type = 3, next_id = 102390224, talk_content = "和脉者一样使用能力，虽然看上去就是普通的钻地和挖地，但石龙并不会破坏它经过的土地。", talk_name = "离歌",},
	[102390224] = { talk_id = 102390224, group_id = 1023902, type = 3, next_id = 102390225, talk_content = "因为它能力就是如此，它的身体在土地中能够变得无形。", talk_name = "离歌",},
	[102390225] = { talk_id = 102390225, group_id = 1023902, type = 1, next_id = 102390226, talk_content = "天月城的城区经常会有石龙的目击情报，我也找到了会出现石龙的地方。，去搞一只石龙的佩从玉回来吧。", talk_name = "离歌",},
	[102390226] = { talk_id = 102390226, group_id = 1023902, type = 1, talk_content = "青乌你去搞一只石龙的佩从玉回来吧，然后我们再说怎么进悬天的大楼。", talk_name = "离歌",},
	[102390301] = { talk_id = 102390301, group_id = 1023903, type = 1, next_id = 102390302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你是谁，为什么会带着一群噬脉生物跳下去？", talk_name = "青乌",},
	[102390302] = { talk_id = 102390302, group_id = 1023903, type = 1, next_id = 102390303, talk_content = "不是我带着，而是他们追着才对。", talk_name = "？？？",},
	[102390303] = { talk_id = 102390303, group_id = 1023903, type = 1, talk_content = "先解决他们吧。", talk_name = "青乌",},
	[102390401] = { talk_id = 102390401, group_id = 1023904, type = 1, next_id = 102390402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "青乌，刚刚除了石龙以外还有噬脉生物对不？", talk_name = "天狐恋",},
	[102390402] = { talk_id = 102390402, group_id = 1023904, type = 1, next_id = 102390403, talk_content = "嗯，往不同的方向走了。", talk_name = "青乌",},
	[102390403] = { talk_id = 102390403, group_id = 1023904, type = 1, next_id = 102390404, talk_content = "石龙是在这边转角位钻到了地下，而噬脉生物则是往另一边去搞破坏了。", talk_name = "天狐恋",},
	[102390404] = { talk_id = 102390404, group_id = 1023904, type = 1, next_id = 102390405, talk_content = "我估计那只石龙也是因为不想跟噬脉生物碰面才躲了起来。", talk_name = "天狐恋",},
	[102390405] = { talk_id = 102390405, group_id = 1023904, type = 1, next_id = 102390406, talk_content = "嘻哈，要是我也能喷火的话就能把坏家伙都打倒了。", talk_name = "脉灵",},
	[102390406] = { talk_id = 102390406, group_id = 1023904, type = 1, next_id = 102390407, talk_content = "（脉灵看着这边）",},
	[102390407] = { talk_id = 102390407, group_id = 1023904, type = 1, next_id = 102390408, talk_content = "……", talk_name = "青乌",},
	[102390408] = { talk_id = 102390408, group_id = 1023904, type = 1, talk_content = "青乌，要是你有时间的话我们就帮一下这只脉灵吧？", talk_name = "天狐恋",},
	[102390501] = { talk_id = 102390501, group_id = 1023905, type = 5, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true,},
	[102390601] = { talk_id = 102390601, group_id = 1023906, type = 1, talk_content = "脉灵请求帮帮助对话", talk_name = "脉灵",},
	[102390701] = { talk_id = 102390701, group_id = 1023907, type = 1, next_id = 102390702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "嘻哈，你们是好人呢。", talk_name = "脉灵",},
	[102390702] = { talk_id = 102390702, group_id = 1023907, type = 1, next_id = 102390703, talk_content = "一下子就变精神了。", talk_name = "青乌",},
	[102390703] = { talk_id = 102390703, group_id = 1023907, type = 1, next_id = 102390704, talk_content = "毕竟它们就是气脉的分身，呆在噬脉生物身边不舒服也是正常的。", talk_name = "天狐恋",},
	[102390704] = { talk_id = 102390704, group_id = 1023907, type = 1, next_id = 102390705, talk_content = "现在都好了，谢谢你们！", talk_name = "脉灵",},
	[102390705] = { talk_id = 102390705, group_id = 1023907, type = 1, next_id = 102390706, talk_content = "也谢谢你让我们同调哦？", talk_name = "天狐恋",},
	[102390706] = { talk_id = 102390706, group_id = 1023907, type = 1, next_id = 102390707, talk_content = "毕竟是青乌，要帮青乌才行。", talk_name = "脉灵",},
	[102390707] = { talk_id = 102390707, group_id = 1023907, type = 1, next_id = 102390708, talk_content = "……要帮我？", talk_name = "青乌",},
	[102390708] = { talk_id = 102390708, group_id = 1023907, type = 1, next_id = 102390709, talk_content = "嘻哈！", talk_name = "脉灵",},
	[102390709] = { talk_id = 102390709, group_id = 1023907, type = 1, next_id = 102390710, talk_content = "走了……还想多摸摸它来着。", talk_name = "天狐恋",},
	[102390710] = { talk_id = 102390710, group_id = 1023907, type = 1, talk_content = "那我们再回去找石龙吧。", talk_name = "天狐恋",},
	[102390801] = { talk_id = 102390801, group_id = 1023908, type = 1, next_id = 102390802, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "哩！", talk_name = "石龙",},
	[102390802] = { talk_id = 102390802, group_id = 1023908, type = 1, next_id = 102390803, talk_content = "就是它，可不能放它跑了！", talk_name = "天狐恋",},
	[102390803] = { talk_id = 102390803, group_id = 1023908, type = 1, talk_content = "交给我吧。", talk_name = "青乌",},
	[102390901] = { talk_id = 102390901, group_id = 1023909, type = 1, next_id = 102390902, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你拿到那些文件了是吧，谢谢。", talk_name = "天狐恋",},
	[102390902] = { talk_id = 102390902, group_id = 1023909, type = 2, child_type = 21, options = {{"我没想到有报酬。",102390903,},{"那些文档看标题就很危险.",102390906,},{"（敲击其与悬天的关系）",102390911,},}, talk_content = "对了，文件以外的东西你都可以留着，通宝，约币啥的就当作是你的辛苦费了。", talk_name = "天狐恋",},
	[102390903] = { talk_id = 102390903, group_id = 1023909, type = 3, next_id = 102390904, icon = "interaction_4", talk_content = "哈哈，难道你以为人家会让你打白工吗。", talk_name = "天狐恋",},
	[102390904] = { talk_id = 102390904, group_id = 1023909, type = 3, next_id = 102390905, talk_content = "嗯，原本是这么想的。", talk_name = "青乌",},
	[102390905] = { talk_id = 102390905, group_id = 1023909, type = 3, next_id = 102390918, talk_content = "看来人家在青乌眼里是个坏坏的女人，真让人伤心。", talk_name = "天狐恋",},
	[102390906] = { talk_id = 102390906, group_id = 1023909, type = 3, next_id = 102390907, icon = "interaction_4", talk_content = "把文档拿到手的时候我瞟了几眼，它们似乎都是有天月城的一些调查报告。", talk_name = "青乌",},
	[102390907] = { talk_id = 102390907, group_id = 1023909, type = 3, next_id = 102390908, talk_content = "其中还有一份叫宁王卫线路的……这些文档都是哪里来，要做什么的？", talk_name = "青乌",},
	[102390908] = { talk_id = 102390908, group_id = 1023909, type = 3, next_id = 102390909, talk_content = "嘿嘿，我看青乌和天月城的公家也没什么关系的样子，为什么会在意这个。", talk_name = "天狐恋",},
	[102390909] = { talk_id = 102390909, group_id = 1023909, type = 3, next_id = 102390910, talk_content = "只是看见了所以问问而已，而且我也不想帮错人。", talk_name = "青乌",},
	[102390910] = { talk_id = 102390910, group_id = 1023909, type = 3, next_id = 102390918, talk_content = "那你尽管放心好了，我不打算拿这些情报为恶，这些情报也只是我们到新地方就必须要做的一些功课而已。", talk_name = "天狐恋",},
	[102390911] = { talk_id = 102390911, group_id = 1023909, type = 3, next_id = 102390912, icon = "interaction_4", talk_content = "姑且问一句，你这个“货物”应该不是偷运到城里来的吧？", talk_name = "青乌",},
	[102390912] = { talk_id = 102390912, group_id = 1023909, type = 3, next_id = 102390913, talk_content = "箱子里面除了文档以外都是些钱财，文档本身也是在天月城里制作的，可没有值得偷运的东西哦，为什么要问这个？", talk_name = "天狐恋",},
	[102390913] = { talk_id = 102390913, group_id = 1023909, type = 3, next_id = 102390914, talk_content = "只是有点在意而已，我看天狐恋你也是脉者，能问问你在哪里工作么？", talk_name = "青乌",},
	[102390914] = { talk_id = 102390914, group_id = 1023909, type = 3, next_id = 102390915, talk_content = "叫我恋就可以啦，然后……我大概知道青乌你想套些什么话，但我可以保证我不会是你的敌人哦，现在。", talk_name = "天狐恋",},
	[102390915] = { talk_id = 102390915, group_id = 1023909, type = 3, next_id = 102390916, talk_content = "现在？", talk_name = "青乌",},
	[102390916] = { talk_id = 102390916, group_id = 1023909, type = 3, next_id = 102390917, talk_content = "嗯，现在。", talk_name = "天狐恋",},
	[102390917] = { talk_id = 102390917, group_id = 1023909, type = 3, next_id = 102390918, talk_content = "毕竟人家才刚到这天月城两三天，现在还在调休呢。", talk_name = "天狐恋",},
	[102390918] = { talk_id = 102390918, group_id = 1023909, type = 2, child_type = 21, options = {{"那我真约了啊？",102390919,},{"嗯，下次见。",102390921,},}, talk_content = "那你我的事情都解决了，今天就在此散会吧，回头我们要再约哦，青乌。", talk_name = "天狐恋",},
	[102390919] = { talk_id = 102390919, group_id = 1023909, type = 3, next_id = 102390920, icon = "interaction_4", talk_content = "哈哈，青乌你真主动呀，可以哦。", talk_name = "天狐恋",},
	[102390920] = { talk_id = 102390920, group_id = 1023909, type = 3, talk_content = "要是还想见我的话就下次电话约吧，我也想和青乌你多说些话呢。", talk_name = "天狐恋",},
	[102390921] = { talk_id = 102390921, group_id = 1023909, type = 3, icon = "interaction_4", talk_content = "我认为我们很快就会再见的。", talk_name = "天狐恋",},
	[102391001] = { talk_id = 102391001, group_id = 1023910, type = 1, talk_content = "帮助打开文件箱后天狐恋对话", talk_name = "天狐恋",},
	[102391101] = { talk_id = 102391101, group_id = 1023911, type = 4, aside = "<color=#FFEBA2>供应商：</color>这边，这边，是望月的车对吧？", talk_content = "这边，这边，是望月的车对吧？", talk_name = "供应商",},
	[102391201] = { talk_id = 102391201, group_id = 1023912, type = 1, next_id = 102391202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "！", talk_name = "噬脉生物",},
	[102391202] = { talk_id = 102391202, group_id = 1023912, type = 1, talk_content = "那人说的应该就是这些了吧。", talk_name = "青乌",},
	[102391301] = { talk_id = 102391301, group_id = 1023913, type = 2, child_type = 21, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"这些材料到底能用来干嘛？",102391302,},{"你刚刚剥下来的可不止这点吧",102391307,},}, talk_content = "老板，材料我都给你打包好了。", talk_name = "供应商",},
	[102391302] = { talk_id = 102391302, group_id = 1023913, type = 3, next_id = 102391303, talk_content = "人偶的肉身，你们店里面那个喜大的身体就是用这些做的。", talk_name = "供应商",},
	[102391303] = { talk_id = 102391303, group_id = 1023913, type = 3, next_id = 102391304, talk_content = "用噬脉生物吗？", talk_name = "青乌",},
	[102391304] = { talk_id = 102391304, group_id = 1023913, type = 3, next_id = 102391305, talk_content = "嗯，噬脉生物的身体里面往往会有专门用来保存气脉能量的器官，那个就是制作人偶的关键。", talk_name = "供应商",},
	[102391305] = { talk_id = 102391305, group_id = 1023913, type = 3, next_id = 102391306, talk_content = "听着挺危险的。", talk_name = "青乌",},
	[102391306] = { talk_id = 102391306, group_id = 1023913, type = 3, next_id = 102391315, talk_content = "只要控制好的都没问题了，毕竟你看我都是靠这个吃饭。", talk_name = "供应商",},
	[102391307] = { talk_id = 102391307, group_id = 1023913, type = 3, next_id = 102391308, icon = "interaction_4", talk_content = "那些是我的工钱啊老板，各拿各的，不然为啥我一分钱也没问你要。", talk_name = "供应商",},
	[102391308] = { talk_id = 102391308, group_id = 1023913, type = 3, next_id = 102391309, talk_content = "但也拿太多了吧，我总觉得有点三七开的意思，给我检查检查你都拿了啥。", talk_name = "青乌",},
	[102391309] = { talk_id = 102391309, group_id = 1023913, type = 3, next_id = 102391310, talk_content = "呃……要不然这样，下次我给你们打折好不好，我最近接了些单需要用到那些材料……", talk_name = "供应商",},
	[102391310] = { talk_id = 102391310, group_id = 1023913, type = 3, next_id = 102391311, talk_content = "……", talk_name = "青乌",},
	[102391311] = { talk_id = 102391311, group_id = 1023913, type = 3, next_id = 102391312, talk_content = "当然给你的那些肯定都是足量的，我可是讲诚信经营的人，我可没偷偷黑一点哦。", talk_name = "供应商",},
	[102391312] = { talk_id = 102391312, group_id = 1023913, type = 3, next_id = 102391313, talk_content = "……", talk_name = "青乌",},
	[102391313] = { talk_id = 102391313, group_id = 1023913, type = 3, next_id = 102391314, talk_content = "真的没有，老板你一定要相信我啊！", talk_name = "供应商",},
	[102391314] = { talk_id = 102391314, group_id = 1023913, type = 3, next_id = 102391315, talk_content = "……好吧。", talk_name = "青乌",},
	[102391315] = { talk_id = 102391315, group_id = 1023913, type = 1, talk_content = "那老板，下次还有事的话就再来找我吧！", talk_name = "供应商",},
	[102391401] = { talk_id = 102391401, group_id = 1023914, type = 4, next_id = 102391402, aside = "<color=#FFEBA2>小五：</color>青乌，你好。", call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "青乌，你好。", talk_name = "小五",},
	[102391402] = { talk_id = 102391402, group_id = 1023914, type = 4, next_id = 102391403, aside = "<color=#FFEBA2>青乌：</color>小五，材料拿到了，我没想到你要的竟然是噬脉生物的素材。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "小五，材料拿到了，我没想到你要的竟然是噬脉生物的素材。", talk_name = "青乌",},
	[102391403] = { talk_id = 102391403, group_id = 1023914, type = 4, next_id = 102391404, aside = "<color=#FFEBA2>小五：</color>因为那个做出来的东西更好，青乌，能再麻烦你把素材带回茶室吗。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "因为那个做出来的东西更好，青乌，能再麻烦你把素材带回茶室吗。", talk_name = "小五",},
	[102391404] = { talk_id = 102391404, group_id = 1023914, type = 4, next_id = 102391405, aside = "<color=#FFEBA2>青乌：</color>这个倒没关系。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "这个倒没关系。", talk_name = "青乌",},
	[102391405] = { talk_id = 102391405, group_id = 1023914, type = 4, next_id = 102391406, aside = "<color=#FFEBA2>小五：</color>对了，得给你报酬……我那辆车就送给你吧。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "对了，得给你报酬……我那辆车就送给你吧。", talk_name = "小五",},
	[102391406] = { talk_id = 102391406, group_id = 1023914, type = 4, next_id = 102391407, aside = "<color=#FFEBA2>青乌：</color>啊？这车应该挺贵的吧？", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "啊？这车应该挺贵的吧？", talk_name = "青乌",},
	[102391407] = { talk_id = 102391407, group_id = 1023914, type = 4, aside = "<color=#FFEBA2>小五：</color>不要紧，因为你以前也帮了我很多，我们在茶室再见，青乌。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "不要紧，因为你以前也帮了我很多，我们在茶室再见，青乌。", talk_name = "小五",},
	[102391501] = { talk_id = 102391501, group_id = 1023915, type = 4, next_id = 102391502, aside = "<color=#FFEBA2>供应商：</color>没想到在天月城里面竟然能接到这样的工作，老板，接下来往那边开吧。", talk_content = "没想到在天月城里面竟然能接到这样的工作，老板，接下来往那边开吧。", talk_name = "供应商",},
	[102391502] = { talk_id = 102391502, group_id = 1023915, type = 4, next_id = 102391503, aside = "<color=#FFEBA2>青乌：</color>你就是供应商是吧，我听说我们要去搞些新鲜的材料，那究竟是什么？", talk_content = "你就是供应商是吧，我听说我们要去搞些新鲜的材料，那究竟是什么？", talk_name = "青乌",},
	[102391503] = { talk_id = 102391503, group_id = 1023915, type = 4, aside = "<color=#FFEBA2>供应商：</color>呃，就是噬脉生物啊。", talk_content = "呃，就是噬脉生物啊。", talk_name = "供应商",},
	[102391601] = { talk_id = 102391601, group_id = 1023916, type = 5, next_id = 102391602, aside = "<color=#FFEBA2>青乌：</color>石龙应该就在这附近……", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "石龙应该就在这附近……", talk_name = "青乌",},
	[102391602] = { talk_id = 102391602, group_id = 1023916, type = 5, next_id = 102391603, aside = "<color=#FFEBA2>？？？：</color>麻烦能接我一下吗？", talk_content = "麻烦能接我一下吗？", talk_name = "？？？",},
	[102391603] = { talk_id = 102391603, group_id = 1023916, type = 5, next_id = 102391604, aside = "<color=#FFEBA2>青乌：</color>啊？", talk_content = "啊？", talk_name = "青乌",},
	[102391604] = { talk_id = 102391604, group_id = 1023916, type = 5, next_id = 102391605, aside = "<color=#FFEBA2>？？？：</color>抱歉，然后……还能请你再帮我个忙吗？", talk_content = "抱歉，然后……还能请你再帮我个忙吗？", talk_name = "？？？",},
	[102391605] = { talk_id = 102391605, group_id = 1023916, type = 5, aside = "<color=#FFEBA2>噬脉生物：</color>！", talk_content = "！", talk_name = "噬脉生物",},
	[102391701] = { talk_id = 102391701, group_id = 1023917, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"（自我介绍）",102391702,},}, talk_content = "人家叫天狐恋，谢谢你的搭救……你是？", talk_name = "天狐恋",},
	[102391702] = { talk_id = 102391702, group_id = 1023917, type = 3, next_id = 102391703, talk_content = "嗯嗯，我会记得的，谢谢你哦，青乌。", talk_name = "天狐恋",},
	[102391703] = { talk_id = 102391703, group_id = 1023917, type = 1, next_id = 102391704, talk_content = "话说回来，你刚刚在接住我之前好像提到”石龙“之类的吧。", talk_name = "天狐恋",},
	[102391704] = { talk_id = 102391704, group_id = 1023917, type = 2, child_type = 21, options = {{"我在找石龙。",102391705,},{"我已经拥有石龙了",102391709,},}, talk_content = "难道你也在这附近找石龙吗？", talk_name = "天狐恋",},
	[102391705] = { talk_id = 102391705, group_id = 1023917, type = 3, next_id = 102391706, talk_content = "我是在找石龙，因为刚好有用到的地方，那你呢。", talk_name = "青乌",},
	[102391706] = { talk_id = 102391706, group_id = 1023917, type = 3, next_id = 102391707, talk_content = "那自然也是来找石龙的呢，人家的朋友把要交付的东西放到了很怪的地方，不用石龙可取不出来呢。", talk_name = "天狐恋",},
	[102391707] = { talk_id = 102391707, group_id = 1023917, type = 3, next_id = 102391708, talk_content = "怪……是怎么个怪？", talk_name = "青乌",},
	[102391708] = { talk_id = 102391708, group_id = 1023917, type = 3, next_id = 102391719, talk_content = "只有老鼠才能去到的地方，他也太看得起我了。", talk_name = "天狐恋",},
	[102391709] = { talk_id = 102391709, group_id = 1023917, type = 3, next_id = 102391710, icon = "interaction_4", talk_content = "不，我已经有石龙了。", talk_name = "青乌",},
	[102391710] = { talk_id = 102391710, group_id = 1023917, type = 3, next_id = 102391711, talk_content = "真的吗，那可以请你再帮我个忙不，我有个朋友把东西放进了老鼠洞里面，我自己可没法把东西取出来。", talk_name = "天狐恋",},
	[102391711] = { talk_id = 102391711, group_id = 1023917, type = 3, next_id = 102391712, talk_content = "你是想让我去取？", talk_name = "青乌",},
	[102391712] = { talk_id = 102391712, group_id = 1023917, type = 3, next_id = 102391713, talk_content = "嗯嗯，用石龙的佩从能力就能滑溜过去了，人家会给你报酬的可以吗？", talk_name = "天狐恋",},
	[102391713] = { talk_id = 102391713, group_id = 1023917, type = 3, next_id = 102391714, talk_content = "那这报酬是？", talk_name = "青乌",},
	[102391714] = { talk_id = 102391714, group_id = 1023917, type = 3, next_id = 102391715, talk_content = "直接给你些通宝如何，这东西对人家也挺重要的，通宝你应该认得吧，这可是比黄金还厉害的硬通货哦。", talk_name = "天狐恋",},
	[102391715] = { talk_id = 102391715, group_id = 1023917, type = 3, next_id = 102391716, talk_content = "……的确很有魅力，那我得赶紧找到石龙才行。", talk_name = "青乌",},
	[102391716] = { talk_id = 102391716, group_id = 1023917, type = 3, next_id = 102391717, talk_content = "所以你没有？", talk_name = "天狐恋",},
	[102391717] = { talk_id = 102391717, group_id = 1023917, type = 3, next_id = 102391718, talk_content = "没有，抱歉。", talk_name = "青乌",},
	[102391718] = { talk_id = 102391718, group_id = 1023917, type = 3, next_id = 102391719, talk_content = "……哎。", talk_name = "天狐恋",},
	[102391719] = { talk_id = 102391719, group_id = 1023917, type = 1, next_id = 102391720, talk_content = "总之我知道青乌你也是来找石龙的了，那要不我们两合作吧？", talk_name = "天狐恋",},
	[102391720] = { talk_id = 102391720, group_id = 1023917, type = 1, next_id = 102391721, talk_content = "为什么要合作？", talk_name = "青乌",},
	[102391721] = { talk_id = 102391721, group_id = 1023917, type = 1, next_id = 102391722, talk_content = "因为这边的石龙大概只有一只，而我们有两个人，佩从玉可不能掰两半对吧？", talk_name = "天狐恋",},
	[102391722] = { talk_id = 102391722, group_id = 1023917, type = 1, next_id = 102391723, talk_content = "所以我就想到了个方法，在拿到石龙以后先帮我取回我要的东西，然后这佩从玉你就直接带走好了，如何？", talk_name = "天狐恋",},
	[102391723] = { talk_id = 102391723, group_id = 1023917, type = 1, next_id = 102391724, talk_content = "合作是没问题，但我们要从哪里开始找？", talk_name = "青乌",},
	[102391724] = { talk_id = 102391724, group_id = 1023917, type = 1, next_id = 102391725, talk_content = "正常找大概是找不着的，石龙是种很孤僻的生物，不怎么喜欢在人前露面。", talk_name = "天狐恋",},
	[102391725] = { talk_id = 102391725, group_id = 1023917, type = 1, next_id = 102391726, talk_content = "听着好像有不正常找的办法。", talk_name = "青乌",},
	[102391726] = { talk_id = 102391726, group_id = 1023917, type = 2, options = {{"你是说脉灵或者看见过石龙吗？",102391727,},{"<color=#FFEBA2>同调是什么？",102391728,},}, talk_content = "我刚刚被追着跑的时候见到了只呆呆的脉灵，我猜你应该也带着能同调的佩从对吧？", talk_name = "天狐恋",},
	[102391727] = { talk_id = 102391727, group_id = 1023917, type = 3, next_id = 102391726, talk_content = "满分。", talk_name = "天狐恋",},
	[102391728] = { talk_id = 102391728, group_id = 1023917, type = 3, next_id = 102391729, talk_content = "的确有些脉者不太清楚这个，那天狐恋老师就给你上一课吧。", talk_name = "天狐恋",},
	[102391729] = { talk_id = 102391729, group_id = 1023917, type = 3, next_id = 102391730, talk_content = "我是不是该鼓个掌？", talk_name = "青乌",},
	[102391730] = { talk_id = 102391730, group_id = 1023917, type = 3, next_id = 102391731, talk_content = "在说同调之前，你得先知道脉灵其实是一种有意识的气脉，它们跟普通生物不一样，是纯能量体。", talk_name = "天狐恋",},
	[102391731] = { talk_id = 102391731, group_id = 1023917, type = 3, next_id = 102391732, talk_content = "我之前见过别的脉灵，感觉不像是”纯能量“的样子。", talk_name = "青乌",},
	[102391732] = { talk_id = 102391732, group_id = 1023917, type = 3, next_id = 102391733, talk_content = "只是看上去不像而已，脉灵们能够随时消散然后重新在别的地方形成，虽然不知道形成的是不是同一只。", talk_name = "天狐恋",},
	[102391733] = { talk_id = 102391733, group_id = 1023917, type = 3, next_id = 102391734, talk_content = "那么脉灵的讲解就到此为止吧，接下来就同调的讲解，既然脉灵是气脉的一部分，就代表脉者们其实是能使用脉灵的。", talk_name = "天狐恋",},
	[102391734] = { talk_id = 102391734, group_id = 1023917, type = 3, next_id = 102391735, talk_content = "那个使用就是同调对吧？", talk_name = "青乌",},
	[102391735] = { talk_id = 102391735, group_id = 1023917, type = 3, next_id = 102391736, talk_content = "嗯嗯，只要脉灵同意的话脉者就可以通过特定佩从玉和脉灵进行同调，看见脉灵曾见过的东西。", talk_name = "天狐恋",},
	[102391736] = { talk_id = 102391736, group_id = 1023917, type = 3, talk_content = "也就是说同调是读取脉灵记忆的方法对吧。", talk_name = "青乌",},
	[102391801] = { talk_id = 102391801, group_id = 1023918, type = 4, next_id = 102391802, aside = "<color=#FFEBA2>天狐恋：</color>你好呀，小脉灵？", talk_content = "你好呀，小脉灵？", talk_name = "天狐恋",},
