	[102391805] = { talk_id = 102391805, group_id = 1023918, type = 4, next_id = 102391806, aside = "<color=#FFEBA2>脉灵：</color>你们想要同调是吧，我听见了，来同调吧，嘻哈……", talk_content = "你们想要同调是吧，我听见了，来同调吧，嘻哈……", talk_name = "脉灵",},
	[102391806] = { talk_id = 102391806, group_id = 1023918, type = 4, next_id = 102391807, aside = "<color=#FFEBA2>天狐恋：</color>的确没什么精神的样子，要是今天状态不好的话那就算了吧，我们不会勉强你的。", talk_content = "的确没什么精神的样子，要是今天状态不好的话那就算了吧，我们不会勉强你的。", talk_name = "天狐恋",},
