	[102391809] = { talk_id = 102391809, group_id = 1023918, type = 4, aside = "<color=#FFEBA2>天狐恋：</color>既然这样我们就同调试试吧，或者还能知道它这么失落的原因呢。", talk_content = "既然这样我们就同调试试吧，或者还能知道它这么失落的原因呢。", talk_name = "天狐恋",},
