	[102392001] = { talk_id = 102392001, group_id = 1023920, type = 1, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "青乌，把那个佩从玉拿上吧。", talk_name = "天狐恋",},
	[102392201] = { talk_id = 102392201, group_id = 1023922, type = 4, aside = "<color=#FFEBA2>青乌：</color>这就是小五的车了吧。", talk_content = "这就是小五的车了吧。", talk_name = "青乌",},
	[102392301] = { talk_id = 102392301, group_id = 1023923, type = 41, next_id = 102392302, call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", talk_content = "青乌，为我提供材料的供应商是为手艺人，因为我需要的材料有些特殊……", talk_name = "小五",},
	[102392302] = { talk_id = 102392302, group_id = 1023923, type = 41, next_id = 102392303, call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", talk_content = "所以你得带上他，然后趁素材还新鲜的时候让他来剥取素材。", talk_name = "小五",},
	[102392303] = { talk_id = 102392303, group_id = 1023923, type = 41, next_id = 102392304, call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", talk_content = "听上去跟处理什么海鲜似的。", talk_name = "青乌",},
	[102392304] = { talk_id = 102392304, group_id = 1023923, type = 41, call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", talk_content = "……我要的材料不是海鲜。", talk_name = "小五",},
	[102392401] = { talk_id = 102392401, group_id = 1023824, type = 4, aside = "<color=#FFEBA2>青乌：</color>离歌不在吗……话说那只飘着的是？", talk_content = "离歌不在吗……话说那只飘着的是？", talk_name = "青乌",},
	[102392501] = { talk_id = 102392501, group_id = 1023925, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"你有自己的人格吗？",102392502,},{"（交给喜大）",102392504,},}, talk_content = "青乌，把材料交给本大爷就行了！", talk_name = "喜大",},
	[102392502] = { talk_id = 102392502, group_id = 1023925, type = 3, next_id = 102392503, talk_content = "没有，你觉得老子这种连脑子都没有的东西能有什么人格，哈哈哈。", talk_name = "喜大",},
	[102392503] = { talk_id = 102392503, group_id = 1023925, type = 3, next_id = 102392504, talk_content = "总之把材料给你就好了吧？", talk_name = "青乌",},
	[102392504] = { talk_id = 102392504, group_id = 1023925, type = 3, talk_content = "嗯嗯，丢地上就好，老子得慢慢搬呢。", talk_name = "喜大",},
	[102392601] = { talk_id = 102392601, group_id = 1023926, type = 1, next_id = 102392602, talk_content = "虽然说这里是茶室，但其实我并不怎么泡茶。", talk_name = "离歌",},
	[102392602] = { talk_id = 102392602, group_id = 1023926, type = 1, next_id = 102392603, talk_content = "为什么？", talk_name = "青乌",},
	[102392603] = { talk_id = 102392603, group_id = 1023926, type = 1, next_id = 102392604, talk_content = "因为工序太麻烦了。", talk_name = "离歌",},
	[102392604] = { talk_id = 102392604, group_id = 1023926, type = 1, talk_content = "等这次的工作结束以后就换个经营方向吧。", talk_name = "离歌",},
	[102392701] = { talk_id = 102392701, group_id = 1023927, type = 1, next_id = 102392702, talk_content = "……", talk_name = "喜大",},
	[102392702] = { talk_id = 102392702, group_id = 1023927, type = 1, talk_content = "（喜大只顾着材料没注意到这边。）", talk_name = "青乌",},
	[102392801] = { talk_id = 102392801, group_id = 1023928, type = 4, aside = "<color=#FFEBA2>天狐恋：</color>脉灵就在那边，跟过来吧。", talk_content = "脉灵就在那边，跟过来吧。", talk_name = "天狐恋",},
	[102392901] = { talk_id = 102392901, group_id = 1023929, type = 4, next_id = 102392902, aside = "<color=#FFEBA2>青乌：</color>虽然知道它就在这，但要怎么让它从地里出来呢？", talk_content = "虽然知道它就在这，但要怎么让它从地里出来呢？", talk_name = "青乌",},
	[102392902] = { talk_id = 102392902, group_id = 1023929, type = 4, aside = "<color=#FFEBA2>天狐恋：</color>有看见那个露出来的地方吗，向他攻击试试？", talk_content = "有看见那个露出来的地方吗，向他攻击试试？", talk_name = "天狐恋",},
	[102393001] = { talk_id = 102393001, group_id = 1023930, type = 4, next_id = 102393002, aside = "<color=#FFEBA2>天狐恋：</color>这样一来就能和石龙一样钻到地里面了，接下来就请你帮我取我的东西啦。", talk_content = "这样一来就能和石龙一样钻到地里面了，接下来就请你帮我取我的东西啦。", talk_name = "天狐恋",},
	[102393002] = { talk_id = 102393002, group_id = 1023930, type = 4, next_id = 102393003, aside = "<color=#FFEBA2>青乌：</color>为什么会露出来一个小尖刺呢？", talk_content = "为什么会露出来一个小尖刺呢？", talk_name = "青乌",},
	[102393003] = { talk_id = 102393003, group_id = 1023930, type = 4, aside = "<color=#FFEBA2>天狐恋：</color>因为石龙比较调皮？", talk_content = "因为石龙比较调皮？", talk_name = "天狐恋",},
	[102393101] = { talk_id = 102393101, group_id = 1023931, type = 4, next_id = 102393102, aside = "<color=#FFEBA2>天狐恋：</color>就在这洞里面，进去以后应该很快就能看见一个保险箱才是。", talk_content = "就在这洞里面，进去以后应该很快就能看见一个保险箱才是。", talk_name = "天狐恋",},
	[102393102] = { talk_id = 102393102, group_id = 1023931, type = 4, aside = "<color=#FFEBA2>青乌：</color>为什么要把东西放这种地……", talk_content = "为什么要把东西放这种地……", talk_name = "青乌",},
	[102393201] = { talk_id = 102393201, group_id = 1023932, type = 4, aside = "<color=#FFEBA2>青乌：</color>东西拿到就出去吧。", talk_content = "东西拿到就出去吧。", talk_name = "青乌",},
	[102400201] = { talk_id = 102400201, group_id = 1024002, type = 4, aside = "<color=#FFEBA2>青乌：</color>有看守也是意料之中的事情，绝对不能被发现……", talk_content = "有看守也是意料之中的事情，绝对不能被发现……", talk_name = "青乌",},
	[102400301] = { talk_id = 102400301, group_id = 1024003, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"你怎么了？",102400302,},{"这些是什么？",102400305,},}, talk_content = "你终于来了啊，青乌，这就是悬天运进来的货。", talk_name = "离歌",},
	[102400302] = { talk_id = 102400302, group_id = 1024003, type = 3, next_id = 102400303, talk_content = "只是有些头昏脑胀而已，这些瓶子里面装的是叫悬金溶液的化学品，我刚不小心弄翻了些。", talk_name = "离歌",},
	[102400303] = { talk_id = 102400303, group_id = 1024003, type = 3, next_id = 102400304, talk_content = "听着就不太妙，要我扶你先离开这吗？", talk_name = "青乌",},
	[102400304] = { talk_id = 102400304, group_id = 1024003, type = 3, next_id = 102400308, talk_content = "我休息一下就好，你继续到里面去……我想”货“应该不止这些瓶瓶罐罐才对。", talk_name = "离歌",},
	[102400305] = { talk_id = 102400305, group_id = 1024003, type = 3, next_id = 102400306, talk_content = "悬金溶液，一种我没见过的化学品，青乌你可别乱碰，不然也会和我一样头昏脑胀。", talk_name = "离歌",},
	[102400306] = { talk_id = 102400306, group_id = 1024003, type = 3, next_id = 102400307, talk_content = "你没事吧？", talk_name = "青乌",},
	[102400307] = { talk_id = 102400307, group_id = 1024003, type = 3, next_id = 102400308, talk_content = "并无大碍，倒是这层里面还有别的东西，你去看看吧。", talk_name = "离歌",},
	[102400308] = { talk_id = 102400308, group_id = 1024003, type = 1, next_id = 102400309, talk_content = "离歌，老实说我觉得这次有些太冒险了，虽然潜入到现在还算顺利，但我们要是因此被悬天这种大组织盯上……", talk_name = "青乌",},
	[102400309] = { talk_id = 102400309, group_id = 1024003, type = 1, next_id = 102400310, talk_content = "能从你口中听见与我不同的意见这点我很开心，但我不喜欢半途而废。", talk_name = "离歌",},
	[102400310] = { talk_id = 102400310, group_id = 1024003, type = 1, talk_content = "总之你再到处看看有没有遗漏的东西吧，等我好些我就过来，然后一起离开这里。", talk_name = "离歌",},
	[102400401] = { talk_id = 102400401, group_id = 1024004, type = 4, next_id = 102400402, aside = "本次搬入的的物资清单", talk_content = "本次搬入的的物资清单",},
	[102400402] = { talk_id = 102400402, group_id = 1024004, type = 4, aside = "悬金溶液计50。", talk_content = "悬金溶液计50。",},
	[102400501] = { talk_id = 102400501, group_id = 1024005, type = 4, next_id = 102400502, aside = "（已经停止运作的自动炮台。）", talk_content = "（已经停止运作的自动炮台。）",},
	[102400502] = { talk_id = 102400502, group_id = 1024005, type = 4, next_id = 102400503, aside = "<color=#FFEBA2>青乌：</color>我是怎么想都想不到会有人在公司里面放这东西。", talk_content = "我是怎么想都想不到会有人在公司里面放这东西。", talk_name = "青乌",},
	[102400503] = { talk_id = 102400503, group_id = 1024005, type = 4, aside = "<color=#FFEBA2>青乌：</color>是这里放的东西很重要……还是悬天的大楼每天都有人光顾呢。", talk_content = "是这里放的东西很重要……还是悬天的大楼每天都有人光顾呢。", talk_name = "青乌",},
	[102400601] = { talk_id = 102400601, group_id = 1024006, type = 4, aside = "<color=#FFEBA2>旁白：</color>室内搜索旁白3", talk_content = "室内搜索旁白3", talk_name = "旁白",},
	[102400701] = { talk_id = 102400701, group_id = 1024007, type = 1, next_id = 102400702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "这里面也是放货的地方吗，只希望不是……什么？", talk_name = "青乌",},
	[102400702] = { talk_id = 102400702, group_id = 1024007, type = 1, talk_content = "咳……！", talk_name = "青乌",},
	[102400801] = { talk_id = 102400801, group_id = 1024008, type = 4, aside = "<color=#FFEBA2>青乌：</color>离歌说要找后门对吧……那个平台不知道有没有能进去的地方呢。", talk_content = "离歌说要找后门对吧……那个平台不知道有没有能进去的地方呢。", talk_name = "青乌",},
	[102400901] = { talk_id = 102400901, group_id = 1024009, type = 4, aside = "<color=#FFEBA2>青乌：</color>我还以为这佩从没地能用呢。", talk_content = "我还以为这佩从没地能用呢。", talk_name = "青乌",},
	[102401001] = { talk_id = 102401001, group_id = 1024010, type = 4, next_id = 102401002, aside = "<color=#FFEBA2>离歌：</color>青乌，我找到了悬天用来放货的地方。", talk_content = "青乌，我找到了悬天用来放货的地方。", talk_name = "离歌",},
	[102401002] = { talk_id = 102401002, group_id = 1024010, type = 4, aside = "<color=#FFEBA2>离歌：</color>你想办法到27楼来吧。", talk_content = "你想办法到27楼来吧。", talk_name = "离歌",},
	[102401101] = { talk_id = 102401101, group_id = 1024011, type = 4, aside = "<color=#FFEBA2>青乌：</color>楼梯无法直达27楼……只能通过走道找能上去的路了。", talk_content = "楼梯无法直达27楼……只能通过走道找能上去的路了。", talk_name = "青乌",},
	[102401201] = { talk_id = 102401201, group_id = 1024012, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"你真没事？",102401202,},{"这次我们到底能拿到什么报酬？",102401207,},{"没事，你小心些。",102401209,},}, talk_content = "怎么了，青乌？", talk_name = "离歌",},
	[102401202] = { talk_id = 102401202, group_id = 1024012, type = 3, next_id = 102401203, talk_content = "真相就是我有些喘不过气了，我没想到这些液体会有这么大的副作用。", talk_name = "离歌",},
	[102401203] = { talk_id = 102401203, group_id = 1024012, type = 3, next_id = 102401204, talk_content = "但也仅仅是副作用而已，我之前与噬脉生物战斗的时候也有过几次这样的体验。", talk_name = "离歌",},
	[102401204] = { talk_id = 102401204, group_id = 1024012, type = 3, next_id = 102401205, talk_content = "彷佛是身体里面的脉都被什么堵住了一般……", talk_name = "离歌",},
	[102401205] = { talk_id = 102401205, group_id = 1024012, type = 3, next_id = 102401206, talk_content = "……我不太懂这感觉，但总之你没大问题，对吧？", talk_name = "青乌",},
	[102401206] = { talk_id = 102401206, group_id = 1024012, type = 3, talk_content = "嗯，再等一会应该就好了。", talk_name = "离歌",},
	[102401207] = { talk_id = 102401207, group_id = 1024012, type = 3, next_id = 102401208, talk_content = "我是一个喜欢把惊喜留到最后的人。", talk_name = "离歌",},
	[102401208] = { talk_id = 102401208, group_id = 1024012, type = 3, talk_content = "总之报酬本身对你我都有益，所以我也躬身入局了。", talk_name = "离歌",},
	[102401209] = { talk_id = 102401209, group_id = 1024012, type = 3, talk_content = "谢谢你关心我，青乌。", talk_name = "离歌",},
	[102410101] = { talk_id = 102410101, group_id = 1024101, type = 1, next_id = 102410102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "（眼睛反应不过来，但身体却知道怎么对应怪物的攻击。）", talk_name = "青乌",},
	[102410102] = { talk_id = 102410102, group_id = 1024101, type = 1, next_id = 102410103, talk_content = "（难不成我以前经常和这样的怪物战斗吗？）", talk_name = "青乌",},
	[102410103] = { talk_id = 102410103, group_id = 1024101, type = 1, talk_content = "先活下去再想别的吧！", talk_name = "青乌",},
	[102410201] = { talk_id = 102410201, group_id = 1024102, type = 1, next_id = 102410202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "哇，青乌在打架，快过来这边！", talk_name = "脉灵",},
	[102410202] = { talk_id = 102410202, group_id = 1024102, type = 1, next_id = 102410203, talk_content = "嘻哈！", talk_name = "脉灵",},
	[102410203] = { talk_id = 102410203, group_id = 1024102, type = 1, next_id = 102410204, talk_content = "（脉灵的声音……在哪？）", talk_name = "青乌",},
	[102410204] = { talk_id = 102410204, group_id = 1024102, type = 1, next_id = 102410205, talk_content = "要帮青乌，要帮青乌！", talk_name = "脉灵",},
	[102410205] = { talk_id = 102410205, group_id = 1024102, type = 1, talk_content = "这样一来……能赢！", talk_name = "青乌",},
	[102410301] = { talk_id = 102410301, group_id = 1024103, type = 1, next_id = 102410302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "青乌，上车，就黄色那辆。", talk_name = "离歌",},
	[102410302] = { talk_id = 102410302, group_id = 1024103, type = 1, next_id = 102410303, talk_content = "离歌你没事了吗？", talk_name = "青乌",},
	[102410303] = { talk_id = 102410303, group_id = 1024103, type = 1, talk_content = "……现在应该要问你有没有事才对，总之快上来，得赶紧离开这。", talk_name = "离歌",},
	[102410401] = { talk_id = 102410401, group_id = 1024104, type = 4, next_id = 102410402, aside = "<color=#FFEBA2>离歌：</color>青乌，我听到了声巨响，你还好吗？", call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "青乌，我听到了声巨响，你还好吗？", talk_name = "离歌",},
	[102410402] = { talk_id = 102410402, group_id = 1024104, type = 4, next_id = 102410403, aside = "<color=#FFEBA2>青乌：</color>不怎么样，然后那声巨响是我跟怪物一起摔到地上的声音。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "不怎么样，然后那声巨响是我跟怪物一起摔到地上的声音。", talk_name = "青乌",},
	[102410403] = { talk_id = 102410403, group_id = 1024104, type = 4, next_id = 102410404, aside = "<color=#FFEBA2>离歌：</color>我现在下来……咳……", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "我现在下来……咳……", talk_name = "离歌",},
	[102410404] = { talk_id = 102410404, group_id = 1024104, type = 4, next_id = 102410405, aside = "<color=#FFEBA2>青乌：</color>我就知道那不只是头昏脑胀。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "我就知道那不只是头昏脑胀。", talk_name = "青乌",},
	[102410405] = { talk_id = 102410405, group_id = 1024104, type = 4, aside = "<color=#FFEBA2>青乌：</color>这下只能靠自己了。", call_man_icon = "离歌;Textures/Icon/Single/HeadIcon/SquMBosszhuishi.png", child_type = 41, talk_content = "这下只能靠自己了。", talk_name = "青乌",},
	[102410501] = { talk_id = 102410501, group_id = 1024105, type = 5, next_id = 102410502, aside = "<color=#FFEBA2>青乌：</color>先是失忆，然后又给人做打手，最后还得跟怪物交手。", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "先是失忆，然后又给人做打手，最后还得跟怪物交手。", talk_name = "青乌",},
	[102410502] = { talk_id = 102410502, group_id = 1024105, type = 5, next_id = 102410503, aside = "<color=#FFEBA2>青乌：</color>……也罢，这样也挺有趣的。", talk_content = "……也罢，这样也挺有趣的。", talk_name = "青乌",},
	[102410503] = { talk_id = 102410503, group_id = 1024105, type = 5, aside = "<color=#FFEBA2>青乌：</color>要做大事对吧，离歌。", talk_content = "要做大事对吧，离歌。", talk_name = "青乌",},
	[103010101] = { talk_id = 103010101, group_id = 1030101, type = 5, next_id = 103010102, aside = "<color=#FFEBA2>陌生声音：</color>终于找到你了，青乌。", call_icon = "Textures/Icon/Single/CampIcon/Xuantian.png", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "终于找到你了，青乌。", talk_name = "陌生声音",},
	[103010102] = { talk_id = 103010102, group_id = 1030101, type = 5, next_id = 103010103, aside = "<color=#FFEBA2>青乌：</color>我好像并不认识你。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "我好像并不认识你。", talk_name = "青乌",},
	[103010103] = { talk_id = 103010103, group_id = 1030101, type = 5, next_id = 103010104, aside = "<color=#FFEBA2>陌生声音：</color>你以为你们的行动可以瞒过我们？", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "你以为你们的行动可以瞒过我们？", talk_name = "陌生声音",},
	[103010104] = { talk_id = 103010104, group_id = 1030101, type = 5, next_id = 103010105, aside = "<color=#FFEBA2>陌生声音：</color>不要小瞧悬天的情报，你做过什么我们可是清清楚楚。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "不要小瞧悬天的情报，你做过什么我们可是清清楚楚。", talk_name = "陌生声音",},
	[103010105] = { talk_id = 103010105, group_id = 1030101, type = 5, next_id = 103010106, aside = "<color=#FFEBA2>青乌：</color>不必绕圈子，直说想怎样？", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "不必绕圈子，直说想怎样？", talk_name = "青乌",},
	[103010106] = { talk_id = 103010106, group_id = 1030101, type = 5, next_id = 103010107, aside = "<color=#FFEBA2>陌生声音：</color>悬天和人打交道一向先礼后兵，不会过份为难。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "悬天和人打交道一向先礼后兵，不会过份为难。", talk_name = "陌生声音",},
	[103010107] = { talk_id = 103010107, group_id = 1030101, type = 5, next_id = 103010108, aside = "<color=#FFEBA2>陌生声音：</color>上次没能好好招待二位，这次想请你们来附近的店铺一聚。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "上次没能好好招待二位，这次想请你们来附近的店铺一聚。", talk_name = "陌生声音",},
	[103010108] = { talk_id = 103010108, group_id = 1030101, type = 5, next_id = 103010109, aside = "<color=#FFEBA2>陌生声音：</color>就在附近的桃园食肆，我们恭迎大驾。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "就在附近的桃园食肆，我们恭迎大驾。", talk_name = "陌生声音",},
	[103010109] = { talk_id = 103010109, group_id = 1030101, type = 5, next_id = 103010110, aside = "<color=#FFEBA2>青乌：</color>还没答应你来不来呢……", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "还没答应你来不来呢……", talk_name = "青乌",},
	[103010110] = { talk_id = 103010110, group_id = 1030101, type = 5, aside = "…………", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "…………",},
	[103010201] = { talk_id = 103010201, group_id = 1030102, type = 1, next_id = 103010202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "悬天打来的电话？", talk_name = "离歌",},
	[103010202] = { talk_id = 103010202, group_id = 1030102, type = 1, next_id = 103010203, talk_content = "没礼貌的家伙，说是想邀请我们面谈。", talk_name = "青乌",},
	[103010203] = { talk_id = 103010203, group_id = 1030102, type = 1, next_id = 103010204, talk_content = "不出所料，看来悬天已经坐不住了。", talk_name = "离歌",},
	[103010204] = { talk_id = 103010204, group_id = 1030102, type = 1, next_id = 103010205, talk_content = "怎么样，我们要赴约吗？", talk_name = "青乌",},
	[103010205] = { talk_id = 103010205, group_id = 1030102, type = 1, next_id = 103010206, talk_content = "谈谈无妨，正好试探下悬天的态度。", talk_name = "离歌",},
	[103010206] = { talk_id = 103010206, group_id = 1030102, type = 1, talk_content = "走吧青乌，去会会他们。", talk_name = "离歌",},
	[103010301] = { talk_id = 103010301, group_id = 1030103, type = 5, aside = "到达店铺外自动，被悬天斩客埋伏", fade_in = 1, fade_out = true, hide_player = true, talk_content = "到达店铺外自动，被悬天斩客埋伏",},
	[103010401] = { talk_id = 103010401, group_id = 1030104, type = 4, aside = "<color=#FFEBA2>离歌：</color>又是埋伏，真符合悬天一贯的风格呢。", talk_content = "又是埋伏，真符合悬天一贯的风格呢。", talk_name = "离歌",},
	[103010501] = { talk_id = 103010501, group_id = 1030105, type = 1, next_id = 103010502, fade_in = 1, fade_out = true, hide_player = true, talk_content = "整个邀约，看起来都是一场陷阱。", talk_name = "离歌",},
	[103010502] = { talk_id = 103010502, group_id = 1030105, type = 1, next_id = 103010503, talk_content = "说是请客，但是悬天并不把我们放在眼里。", talk_name = "离歌",},
	[103010503] = { talk_id = 103010503, group_id = 1030105, type = 1, next_id = 103010504, talk_content = "那我们还有必要进去吗？", talk_name = "青乌",},
	[103010504] = { talk_id = 103010504, group_id = 1030105, type = 1, next_id = 103010505, talk_content = "既然来了那就没有后退的理由。", talk_name = "离歌",},
	[103010505] = { talk_id = 103010505, group_id = 1030105, type = 1, talk_content = "我们进去，看看悬天想演一出什么戏。", talk_name = "离歌",},
	[103010601] = { talk_id = 103010601, group_id = 1030106, type = 5, next_id = 103010602, aside = "<color=#FFEBA2>离歌：</color>竟然摆出这么大的阵仗。", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "竟然摆出这么大的阵仗。", talk_name = "离歌",},
	[103010602] = { talk_id = 103010602, group_id = 1030106, type = 5, next_id = 103010603, aside = "<color=#FFEBA2>？？？：</color>我还以为，你们不敢来了呢。", talk_content = "我还以为，你们不敢来了呢。", talk_name = "？？？",},
	[103010603] = { talk_id = 103010603, group_id = 1030106, type = 5, next_id = 103010604, aside = "<color=#FFEBA2>青乌：</color>我们可不像你想象得那么胆小。", talk_content = "我们可不像你想象得那么胆小。", talk_name = "青乌",},
	[103010604] = { talk_id = 103010604, group_id = 1030106, type = 5, next_id = 103010605, aside = "<color=#FFEBA2>？？？：</color>悬天，余辜，恭迎两位。", talk_content = "悬天，余辜，恭迎两位。", talk_name = "？？？",},
	[103010605] = { talk_id = 103010605, group_id = 1030106, type = 5, aside = "<color=#FFEBA2>余辜：</color>是时候谈谈正事了。", talk_content = "是时候谈谈正事了。", talk_name = "余辜",},
	[103010701] = { talk_id = 103010701, group_id = 1030107, type = 1, next_id = 103010702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "两位既然还敢进来，我也不拐弯抹角。", talk_name = "余辜",},
	[103010702] = { talk_id = 103010702, group_id = 1030107, type = 1, next_id = 103010704, talk_content = "你们背后藏着什么目的，悬天并不关心。我们没有功夫天天应付无名之辈。", talk_name = "余辜",},
	[103010704] = { talk_id = 103010704, group_id = 1030107, type = 1, next_id = 103010705, talk_content = "那你找我们是准备谈什么？", talk_name = "青乌",},
	[103010705] = { talk_id = 103010705, group_id = 1030107, type = 1, next_id = 103010706, talk_content = "我说过，悬天待人一向先礼后兵，能够和平解决绝不轻易翻脸。", talk_name = "余辜",},
	[103010706] = { talk_id = 103010706, group_id = 1030107, type = 1, next_id = 103010707, talk_content = "这是给你们准备的一点诚意，只要愿意收下，之后再也别来找事，我们就还能共处。", talk_name = "余辜",},
	[103010707] = { talk_id = 103010707, group_id = 1030107, type = 1, next_id = 103010708, talk_content = "你不会以为，这么老套的收买方式管用吧？", talk_name = "离歌",},
	[103010708] = { talk_id = 103010708, group_id = 1030107, type = 1, next_id = 103010709, talk_content = "这是给你们面子而已，不要太把自己当回事。", talk_name = "余辜",},
	[103010709] = { talk_id = 103010709, group_id = 1030107, type = 1, next_id = 103010710, talk_content = "若是执意作对，那你们的下场我就不好说咯。", talk_name = "余辜",},
	[103010710] = { talk_id = 103010710, group_id = 1030107, type = 1, next_id = 103010711, talk_content = "你是在威胁我们吗？", talk_name = "青乌",},
	[103010711] = { talk_id = 103010711, group_id = 1030107, type = 1, next_id = 103010712, talk_content = "我只是陈明利害而已，具体如何选择还要看二位。", talk_name = "余辜",},
	[103010712] = { talk_id = 103010712, group_id = 1030107, type = 1, next_id = 103010713, talk_content = "不过提醒一句，和悬天作对的人，都没有好下场。", talk_name = "余辜",},
	[103010713] = { talk_id = 103010713, group_id = 1030107, type = 2, child_type = 21, options = {{"你们的诚意我就收下了，合作愉快",103010714,},{"我们不会接受你的条件。",103010719,},{"要打便打，不必废话。",103010726,},}, talk_content = "这是最后考虑的机会。", talk_name = "余辜",},
	[103010714] = { talk_id = 103010714, group_id = 1030107, type = 3, next_id = 103010715, icon = "interaction_4", talk_content = "你们的诚意我就收下了，合作愉快。", talk_name = "青乌",},
	[103010715] = { talk_id = 103010715, group_id = 1030107, type = 3, next_id = 103010716, talk_content = "哼，倒是答应得蛮爽快。", talk_name = "余辜",},
	[103010716] = { talk_id = 103010716, group_id = 1030107, type = 3, next_id = 103010717, talk_content = "我喜欢识相的人，拿着奖励快滚吧。", talk_name = "余辜",},
	[103010717] = { talk_id = 103010717, group_id = 1030107, type = 3, next_id = 103010718, talk_content = "真粗鲁啊。", talk_name = "青乌",},
	[103010718] = { talk_id = 103010718, group_id = 1030107, type = 3, talk_content = "该走了青乌，不用和他们纠缠。", talk_name = "离歌",},
	[103010719] = { talk_id = 103010719, group_id = 1030107, type = 3, next_id = 103010720, talk_content = "我们不会接受你的条件。", talk_name = "青乌",},
	[103010720] = { talk_id = 103010720, group_id = 1030107, type = 3, next_id = 103010721, talk_content = "哼，自己放弃机会就别怪我。", talk_name = "余辜",},
	[103010721] = { talk_id = 103010721, group_id = 1030107, type = 3, next_id = 103010722, talk_content = "只是不愿意和悬天同流合污罢了。", talk_name = "离歌",},
	[103010722] = { talk_id = 103010722, group_id = 1030107, type = 3, next_id = 103010723, talk_content = "喂，说什么呢？放尊重点！", talk_name = "悬天成员",},
	[103010723] = { talk_id = 103010723, group_id = 1030107, type = 3, next_id = 103010724, talk_content = "注意你的用词，还是说你想现在就吃点苦头？", talk_name = "余辜",},
	[103010724] = { talk_id = 103010724, group_id = 1030107, type = 3, next_id = 103010725, talk_content = "要打的话我们可不怕。", talk_name = "青乌",},
	[103010725] = { talk_id = 103010725, group_id = 1030107, type = 3, talk_content = "兄弟们，给他们一点教训！", talk_name = "余辜",},
	[103010726] = { talk_id = 103010726, group_id = 1030107, type = 3, next_id = 103010727, icon = "interaction_4", talk_content = "要打便打，不必在这里废话！", talk_name = "青乌",},
	[103010727] = { talk_id = 103010727, group_id = 1030107, type = 3, next_id = 103010728, talk_content = "哼，好意叫你们来商谈，结果就是这个态度？", talk_name = "余辜",},
	[103010728] = { talk_id = 103010728, group_id = 1030107, type = 3, next_id = 103010729, talk_content = "假如你们真是好意，又怎么会在外面安排埋伏？", talk_name = "离歌",},
	[103010729] = { talk_id = 103010729, group_id = 1030107, type = 3, next_id = 103010730, talk_content = "……", talk_name = "余辜",},
	[103010730] = { talk_id = 103010730, group_id = 1030107, type = 3, next_id = 103010731, talk_content = "怎么，被戳穿之后无言以对了？", talk_name = "青乌",},
	[103010731] = { talk_id = 103010731, group_id = 1030107, type = 3, next_id = 103010732, talk_content = "既然一点诚意都没有，那也就没有商谈的必要。", talk_name = "离歌",},
	[103010732] = { talk_id = 103010732, group_id = 1030107, type = 3, next_id = 103010733, talk_content = "话已至此，就如你们所愿。", talk_name = "余辜",},
	[103010733] = { talk_id = 103010733, group_id = 1030107, type = 3, talk_content = "兄弟们，动手！", talk_name = "余辜",},
	[103010801] = { talk_id = 103010801, group_id = 1030108, type = 4, aside = "<color=#FFEBA2>余辜：</color>够了，给我退下！", talk_content = "够了，给我退下！", talk_name = "余辜",},
	[103010901] = { talk_id = 103010901, group_id = 1030109, type = 1, next_id = 103010902, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "行了，我明白二位的态度了。", talk_name = "余辜",},
	[103010902] = { talk_id = 103010902, group_id = 1030109, type = 1, next_id = 103010903, talk_content = "不愿意和平解决，那就走着瞧吧，到时候可别后悔！", talk_name = "余辜",},
	[103010903] = { talk_id = 103010903, group_id = 1030109, type = 1, next_id = 103010904, talk_content = "谁后悔还不一定呢。", talk_name = "青乌",},
	[103010904] = { talk_id = 103010904, group_id = 1030109, type = 1, next_id = 103010905, talk_content = "大家的立场都摆明了，就不必再浪费口舌。", talk_name = "离歌",},
	[103010905] = { talk_id = 103010905, group_id = 1030109, type = 1, next_id = 103010906, talk_content = "不过，这么远也不能白来一趟。", talk_name = "离歌",},
	[103010906] = { talk_id = 103010906, group_id = 1030109, type = 1, next_id = 103010907, talk_content = "就当是主人的礼数，你们的东西我收下喽。", talk_name = "离歌",},
	[103010907] = { talk_id = 103010907, group_id = 1030109, type = 1, next_id = 103010908, talk_content = "你……你考虑清楚后果别太放肆，悬天可不是什么慈善机构。", talk_name = "余辜",},
	[103010908] = { talk_id = 103010908, group_id = 1030109, type = 1, next_id = 103010909, talk_content = "付出的东西，我们都会千百倍讨回来！", talk_name = "余辜",},
	[103010909] = { talk_id = 103010909, group_id = 1030109, type = 1, next_id = 103010910, talk_content = "不用你提醒，我早就已经考虑过后果了。", talk_name = "离歌",},
	[103010910] = { talk_id = 103010910, group_id = 1030109, type = 1, talk_content = "青乌，我们走。", talk_name = "离歌",},
	[103011001] = { talk_id = 103011001, group_id = 1030110, type = 1, next_id = 103011002, talk_content = "谈完了还不肯走？", talk_name = "余辜",},
	[103011002] = { talk_id = 103011002, group_id = 1030110, type = 1, talk_content = "不用你催。", talk_name = "青乌",},
	[103011101] = { talk_id = 103011101, group_id = 1030111, type = 1, next_id = 103011102, talk_content = "怎么还不滚，难道需要我请你吗？", talk_name = "余辜",},
	[103011102] = { talk_id = 103011102, group_id = 1030111, type = 1, talk_content = "不客气，我们也不愿在这多待。", talk_name = "青乌",},
	[103011201] = { talk_id = 103011201, group_id = 1030112, type = 1, next_id = 103011202, fade_in = 1, fade_out = true, hide_player = true, talk_content = "不敢相信你这么干脆就答应了。", talk_name = "离歌",},
	[103011202] = { talk_id = 103011202, group_id = 1030112, type = 1, next_id = 103011203, talk_content = "有钱收又不起冲突，为什么不答应呢？", talk_name = "青乌",},
	[103011203] = { talk_id = 103011203, group_id = 1030112, type = 1, next_id = 103011204, talk_content = "矛盾不可能这么简单就化解，我不相信悬天会善罢甘休。不要傻傻地把余辜的许诺当真。", talk_name = "离歌",},
	[103011204] = { talk_id = 103011204, group_id = 1030112, type = 1, next_id = 103011205, talk_content = "但是暂且答应，倒是可以用作缓兵之计。", talk_name = "离歌",},
	[103011205] = { talk_id = 103011205, group_id = 1030112, type = 1, next_id = 103011206, talk_content = "嗯，先装作我们同意悬天的条件。再悄悄行动，这样也不错。", talk_name = "离歌",},
	[103011206] = { talk_id = 103011206, group_id = 1030112, type = 1, next_id = 103011207, talk_content = "你又在打算什么……", talk_name = "青乌",},
	[103011207] = { talk_id = 103011207, group_id = 1030112, type = 1, next_id = 103011208, talk_content = "等时机一到，找回失去的记忆，你就能明白一切了。", talk_name = "离歌",},
	[103011208] = { talk_id = 103011208, group_id = 1030112, type = 1, next_id = 103011209, talk_content = "我们去哪？", talk_name = "青乌",},
	[103011209] = { talk_id = 103011209, group_id = 1030112, type = 1, next_id = 103011210, talk_content = "当然是回茶室，还有重要的事情等着我们呢。", talk_name = "离歌",},
	[103011210] = { talk_id = 103011210, group_id = 1030112, talk_content = "装什么神秘……", talk_name = "青乌",},
	[103011301] = { talk_id = 103011301, group_id = 1030113, type = 1, next_id = 103011302, fade_in = 1, fade_out = true, hide_player = true, talk_content = "行了，我明白二位的态度了。", talk_name = "余辜",},
	[103011302] = { talk_id = 103011302, group_id = 1030113, type = 1, next_id = 103011303, talk_content = "不愿意和平解决，那就走着瞧吧，到时候可别后悔！", talk_name = "余辜",},
	[103011303] = { talk_id = 103011303, group_id = 1030113, type = 1, next_id = 103011304, talk_content = "谁后悔还不一定呢。", talk_name = "青乌",},
	[103011304] = { talk_id = 103011304, group_id = 1030113, type = 1, next_id = 103011305, talk_content = "大家的立场都摆明了，就不必再浪费口舌。", talk_name = "离歌",},
	[103011305] = { talk_id = 103011305, group_id = 1030113, type = 1, next_id = 103011306, talk_content = "不过，这么远也不能白来一趟。", talk_name = "离歌",},
	[103011306] = { talk_id = 103011306, group_id = 1030113, type = 1, next_id = 103011307, talk_content = "就当是主人的礼数，你们的东西我收下喽。", talk_name = "离歌",},
	[103011307] = { talk_id = 103011307, group_id = 1030113, type = 1, next_id = 103011308, talk_content = "你……你考虑清楚后果别太放肆，悬天可不是什么慈善机构。", talk_name = "余辜",},
	[103011308] = { talk_id = 103011308, group_id = 1030113, type = 1, next_id = 103011309, talk_content = "付出的东西，我们都会千百倍讨回来！", talk_name = "余辜",},
	[103011309] = { talk_id = 103011309, group_id = 1030113, type = 1, next_id = 103011310, talk_content = "不用你提醒，我早就已经考虑过后果了。", talk_name = "离歌",},
	[103011310] = { talk_id = 103011310, group_id = 1030113, type = 1, talk_content = "青乌，我们走。", talk_name = "离歌",},
	[103011401] = { talk_id = 103011401, group_id = 1020114, type = 1, next_id = 103011402, talk_content = "你们……今天就先放过你们。", talk_name = "余辜",},
	[103011402] = { talk_id = 103011402, group_id = 1020114, type = 1, next_id = 103011403, talk_content = "那我们可不能白来。", talk_name = "离歌",},
	[103011403] = { talk_id = 103011403, group_id = 1020114, type = 1, next_id = 103011404, talk_content = "是你邀请我们，主人给客人准备点礼物可是待客之道。", talk_name = "离歌",},
	[103011404] = { talk_id = 103011404, group_id = 1020114, type = 1, next_id = 103011405, talk_content = "这点东西，就当作是你们准备的礼物吧。", talk_name = "离歌",},
	[103011405] = { talk_id = 103011405, group_id = 1020114, type = 1, next_id = 103011406, talk_content = "你……你们别太放肆！", talk_name = "余辜",},
	[103011406] = { talk_id = 103011406, group_id = 1020114, type = 1, next_id = 103011407, talk_content = "给我等着，下次要让你们晓得悬天不是吃素的！", talk_name = "余辜",},
	[103011407] = { talk_id = 103011407, group_id = 1020114, type = 1, next_id = 103011408, talk_content = "早知如此，也不用假惺惺装客套。", talk_name = "离歌",},
	[103011408] = { talk_id = 103011408, group_id = 1020114, type = 1, talk_content = "我们走吧，青乌。", talk_name = "离歌",},
	[103020101] = { talk_id = 103020101, group_id = 1030201, type = 5, aside = "回到茶室后接委托人电话，要求青乌送溶液", call_icon = "Textures/Icon/Single/CampIcon/Weizhizhenyin.png", call_man_icon = "委托人;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "回到茶室后接委托人电话，要求青乌送溶液",},
	[103020201] = { talk_id = 103020201, group_id = 1030202, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"说吧，要我帮什么忙。",103020202,},{"感觉一直在忙都没有闲着……",103020204,},}, talk_content = "青乌，还有件事需要托付给你。", talk_name = "离歌",},
	[103020202] = { talk_id = 103020202, group_id = 1030202, type = 3, next_id = 103020203, talk_content = "你居然答应得这么利落，我还以为你会吐槽呢。", talk_name = "离歌",},
	[103020203] = { talk_id = 103020203, group_id = 1030202, type = 3, next_id = 103020206, talk_content = "其实我已经习惯了……", talk_name = "青乌",},
	[103020204] = { talk_id = 103020204, group_id = 1030202, type = 3, next_id = 103020205, talk_content = "这些都是做大事之前的铺垫，不会让你的辛苦白费。", talk_name = "离歌",},
	[103020205] = { talk_id = 103020205, group_id = 1030202, type = 3, next_id = 103020206, talk_content = "我们离既定的目标已经越来越近了。", talk_name = "离歌",},
	[103020206] = { talk_id = 103020206, group_id = 1030202, type = 1, next_id = 103020207, talk_content = "王土评估之后认可了我们的力量，下一步只需找到溶液交给他们就好了。", talk_name = "离歌",},
	[103020207] = { talk_id = 103020207, group_id = 1030202, type = 1, next_id = 103020208, talk_content = "郭中郎许诺找到溶液就能给到我们想要的报酬，可惜悬天大楼的溶液样本浪费了。", talk_name = "离歌",},
	[103020208] = { talk_id = 103020208, group_id = 1030202, type = 1, next_id = 103020209, talk_content = "王土委托的事情，我本应和你一同行动，但是没想到溶液遗留的症状比想象中更重。", talk_name = "离歌",},
	[103020209] = { talk_id = 103020209, group_id = 1030202, type = 1, next_id = 103020210, talk_content = "所以，只能拜托你去见一面创生主义的成员，找到悬天委托他们运走的其他溶液。", talk_name = "离歌",},
	[103020210] = { talk_id = 103020210, group_id = 1030202, type = 1, next_id = 103020211, talk_content = "离歌，溶液造成的伤，真的没问题吗？", talk_name = "青乌",},
	[103020211] = { talk_id = 103020211, group_id = 1030202, type = 1, next_id = 103020212, talk_content = "不必太过担心我，更严重的伤也不是没经历过。", talk_name = "离歌",},
	[103020212] = { talk_id = 103020212, group_id = 1030202, type = 1, next_id = 103020213, talk_content = "你先专注于眼前的任务，我在茶室歇歇就好。", talk_name = "离歌",},
	[103020213] = { talk_id = 103020213, group_id = 1030202, type = 1, next_id = 103020214, talk_content = "小五已经联络到创生主义的成员，他们不甘心之前被悬天骗去做事，所以愿意提供帮助。", talk_name = "离歌",},
	[103020214] = { talk_id = 103020214, group_id = 1030202, type = 1, talk_content = "稍后小五会短信告诉你在哪里可以找到他们，只需与他们合作找出剩下的溶液就行。", talk_name = "离歌",},
	[103020301] = { talk_id = 103020301, group_id = 1030203, type = 2, hide_all_entity = true, hide_player = true, options = {{"你了解创生主义吗？",103020302,},{"没事了。",},}, talk_content = "其他事都安排好了，还有什么问题吗？", talk_name = "离歌",},
	[103020302] = { talk_id = 103020302, group_id = 1030203, type = 3, next_id = 103020303, talk_content = "据我所知是天月城新出现的一个小团体。", talk_name = "离歌",},
	[103020303] = { talk_id = 103020303, group_id = 1030203, type = 3, next_id = 103020304, talk_content = "虽然规模不大，但是成员据说都来头不小。", talk_name = "离歌",},
	[103020304] = { talk_id = 103020304, group_id = 1030203, type = 3, talk_content = "不过我们接下来应该会经常和他们打交道就是了。", talk_name = "离歌",},
	[103020501] = { talk_id = 103020501, group_id = 1030205, type = 4, aside = "<color=#FFEBA2>青乌：</color>有空再慢慢逛吧，先找到联络人要紧。", talk_content = "有空再慢慢逛吧，先找到联络人要紧。", talk_name = "青乌",},
	[103020601] = { talk_id = 103020601, group_id = 1030206, type = 5, next_id = 103020602, aside = "<color=#FFEBA2>义向：</color>嗨，看你那双角，你就是望月茶室的那名脉者吧？", hide_all_entity = true, hide_player = true, talk_content = "嗨，看你那双角，你就是望月茶室的那名脉者吧？", talk_name = "义向",},
	[103020602] = { talk_id = 103020602, group_id = 1030206, type = 5, next_id = 103020603, aside = "<color=#FFEBA2>青乌：</color>你就是接头人？", talk_content = "你就是接头人？", talk_name = "青乌",},
	[103020603] = { talk_id = 103020603, group_id = 1030206, type = 5, next_id = 103020604, aside = "<color=#FFEBA2>义向：</color>已经等候你多时了。", talk_content = "已经等候你多时了。", talk_name = "义向",},
	[103020604] = { talk_id = 103020604, group_id = 1030206, type = 5, aside = "<color=#FFEBA2>义向：</color>初次见面，我是申属的脉者义向。", talk_content = "初次见面，我是申属的脉者义向。", talk_name = "义向",},
	[103020701] = { talk_id = 103020701, group_id = 1030207, type = 1, next_id = 103020702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "我是青乌，小五说过让我来和你碰头。", talk_name = "青乌",},
	[103020702] = { talk_id = 103020702, group_id = 1030207, type = 2, child_type = 21, options = {{"有兴趣联手一起变得有名吗？",103020703,},{"创生主义也在关注望月的动向吗？",103020706,},{"你们貌似也名头不小啊。",103020710,},}, talk_content = "原来你就是青乌，最近在天月城，你可是当红人物啊。", talk_name = "义向",},
	[103020703] = { talk_id = 103020703, group_id = 1030207, type = 3, next_id = 103020704, icon = "interaction_4", talk_content = "要是不介意，望月和创生主义可以联手做事，你们也可以变得有名。", talk_name = "青乌",},
	[103020704] = { talk_id = 103020704, group_id = 1030207, type = 3, next_id = 103020705, talk_content = "真的吗？听起来好像挺不错的啊。", talk_name = "义向",},
	[103020705] = { talk_id = 103020705, group_id = 1030207, type = 3, next_id = 103020713, talk_content = "我回去和不害说一声，他也一定乐于和你们合作的。", talk_name = "义向",},
	[103020706] = { talk_id = 103020706, group_id = 1030207, type = 3, next_id = 103020707, icon = "interaction_4", talk_content = "我倒是没想到会这么出名，创生主义也在关注望月茶室的动向吗？", talk_name = "青乌",},
	[103020707] = { talk_id = 103020707, group_id = 1030207, type = 3, next_id = 103020708, talk_content = "我们虽然成员不多，但是对天月城发生的大小事还是略知一二。", talk_name = "义向",},
	[103020708] = { talk_id = 103020708, group_id = 1030207, type = 3, next_id = 103020709, talk_content = "青乌你的事迹，大家都有所了解。", talk_name = "义向",},
	[103020709] = { talk_id = 103020709, group_id = 1030207, type = 3, next_id = 103020713, talk_content = "我还是挺佩服你们和悬天正面交锋的勇气。", talk_name = "义向",},
	[103020710] = { talk_id = 103020710, group_id = 1030207, type = 3, next_id = 103020711, icon = "interaction_4", talk_content = "创生主义貌似也名头不小啊，最近时常有听到你们活跃的消息。", talk_name = "青乌",},
	[103020711] = { talk_id = 103020711, group_id = 1030207, type = 3, next_id = 103020712, talk_content = "哪里哪里，我们才四个人而已，也不怎么出名。", talk_name = "义向",},
	[103020712] = { talk_id = 103020712, group_id = 1030207, type = 3, next_id = 103020713, talk_content = "不过是一个为自己目标奋斗的小团体罢了。", talk_name = "义向",},
	[103020713] = { talk_id = 103020713, group_id = 1030207, type = 1, next_id = 103020714, talk_content = "话说回来，之前在遥遥街，我也遇见过一名你们的同伴。", talk_name = "青乌",},
	[103020714] = { talk_id = 103020714, group_id = 1030207, type = 1, next_id = 103020715, talk_content = "你是说祁玉吗？那小丫头没给你添麻烦吧？", talk_name = "义向",},
	[103020715] = { talk_id = 103020715, group_id = 1030207, type = 1, next_id = 103020716, talk_content = "有惊无险，倒是不算什么麻烦……", talk_name = "青乌",},
	[103020716] = { talk_id = 103020716, group_id = 1030207, type = 1, next_id = 103020717, talk_content = "我是没想到她会着急到孤身一人去遥遥街，幸好她遇见了你。", talk_name = "义向",},
	[103020717] = { talk_id = 103020717, group_id = 1030207, type = 1, next_id = 103020718, talk_content = "早知道就一起去了。", talk_name = "义向",},
	[103020718] = { talk_id = 103020718, group_id = 1030207, type = 1, next_id = 103020719, talk_content = "可惜在遥遥街扑了个空，没找到联络人。", talk_name = "青乌",},
	[103020719] = { talk_id = 103020719, group_id = 1030207, type = 1, next_id = 103020720, talk_content = "这回我们居然被悬天的人蒙了，实在是忍不下这口恶气。", talk_name = "义向",},
	[103020720] = { talk_id = 103020720, group_id = 1030207, type = 1, next_id = 103020721, talk_content = "所以听小五说你们在找悬天悄悄运送的东西，就想着多少可以给你们提供帮助。", talk_name = "义向",},
	[103020721] = { talk_id = 103020721, group_id = 1030207, type = 1, next_id = 103020722, talk_content = "我们在找一种特殊的溶液……之前帮悬天委托运送的货物，其中就应该有这玩意。", talk_name = "青乌",},
	[103020722] = { talk_id = 103020722, group_id = 1030207, type = 1, next_id = 103020723, talk_content = "其实我也不清楚委托运送的货物到底是什么，不过里面确实有几件看起来很神秘的东西。", talk_name = "义向",},
	[103020723] = { talk_id = 103020723, group_id = 1030207, type = 1, next_id = 103020724, talk_content = "记得是一批奇怪的器皿，搬起来还蛮重的。", talk_name = "义向",},
	[103020724] = { talk_id = 103020724, group_id = 1030207, type = 1, next_id = 103020725, talk_content = "先前的要求只是运送到公园，交付他们之后又被搬到哪里去就不得而知了。", talk_name = "义向",},
	[103020725] = { talk_id = 103020725, group_id = 1030207, type = 1, next_id = 103020726, talk_content = "公园这么大，总要有个线索才方便找到。", talk_name = "青乌",},
	[103020726] = { talk_id = 103020726, group_id = 1030207, type = 1, next_id = 103020727, talk_content = "要说线索的话，在移交货物的时候我倒是有听到几句他们的闲聊。", talk_name = "义向",},
	[103020727] = { talk_id = 103020727, group_id = 1030207, type = 2, options = {{"难道说，是被运到了公园的某个高处？",103020728,},}, talk_content = "我记得，他们有抱怨“为什么要费劲运去那么高的地方。”", talk_name = "义向",},
	[103020728] = { talk_id = 103020728, group_id = 1030207, type = 3, next_id = 103020729, talk_content = "要说公园区域最高的地方，那就非浮空岛莫属了。", talk_name = "义向",},
	[103020729] = { talk_id = 103020729, group_id = 1030207, type = 1, next_id = 103020730, talk_content = "总归是有线索了，帮人帮到底，我陪你走一趟吧，这里我最熟悉。", talk_name = "义向",},
	[103020730] = { talk_id = 103020730, group_id = 1030207, type = 1, talk_content = "也好，那就麻烦你带路了。", talk_name = "青乌",},
	[103030101] = { talk_id = 103030101, group_id = 1030301, type = 4, next_id = 103030102, aside = "<color=#FFEBA2>青乌：</color>得先确认悬天是否真的来过这附近。", talk_content = "得先确认悬天是否真的来过这附近。", talk_name = "青乌",},
	[103030102] = { talk_id = 103030102, group_id = 1030301, type = 4, next_id = 103030103, aside = "<color=#FFEBA2>义向：</color>要不试试和脉灵同调?说不定能找到有用的线索。", talk_content = "要不试试和脉灵同调?说不定能找到有用的线索。", talk_name = "义向",},
	[103030103] = { talk_id = 103030103, group_id = 1030301, type = 4, aside = "<color=#FFEBA2>青乌：</color>让我来吧。", talk_content = "让我来吧。", talk_name = "青乌",},
	[103030201] = { talk_id = 103030201, group_id = 1030302, type = 1, next_id = 103030202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "……", talk_name = "脉灵墩墩",},
	[103030202] = { talk_id = 103030202, group_id = 1030302, type = 1, next_id = 103030203, talk_content = "你好。", talk_name = "青乌",},
	[103030203] = { talk_id = 103030203, group_id = 1030302, type = 1, next_id = 103030204, talk_content = "…………", talk_name = "脉灵墩墩",},
	[103030204] = { talk_id = 103030204, group_id = 1030302, type = 1, next_id = 103030205, talk_content = "你好呀。", talk_name = "青乌",},
	[103030205] = { talk_id = 103030205, group_id = 1030302, type = 1, next_id = 103030206, talk_content = "………………", talk_name = "脉灵墩墩",},
	[103030206] = { talk_id = 103030206, group_id = 1030302, type = 1, next_id = 103030207, talk_content = "你好！！", talk_name = "义向",},
	[103030207] = { talk_id = 103030207, group_id = 1030302, type = 1, next_id = 103030208, talk_content = "啊？你在叫我？", talk_name = "脉灵墩墩",},
	[103030208] = { talk_id = 103030208, group_id = 1030302, type = 1, next_id = 103030209, talk_content = "这里还有其他人吗？", talk_name = "义向",},
	[103030209] = { talk_id = 103030209, group_id = 1030302, type = 1, next_id = 103030210, talk_content = "嘻哈，你说的也是噢，有点走神了。", talk_name = "脉灵墩墩",},
	[103030210] = { talk_id = 103030210, group_id = 1030302, type = 1, next_id = 103030211, talk_content = "有点？", talk_name = "青乌",},
	[103030211] = { talk_id = 103030211, group_id = 1030302, type = 1, next_id = 103030212, talk_content = "第一次见这么懒懒散散的脉灵。", talk_name = "义向",},
	[103030212] = { talk_id = 103030212, group_id = 1030302, type = 1, next_id = 103030213, talk_content = "唉，没必要很卖力啊……", talk_name = "脉灵墩墩",},
	[103030213] = { talk_id = 103030213, group_id = 1030302, type = 1, next_id = 103030214, talk_content = "我们在找东西，所以想看看这里之前发生过什么，可以和你同调看下吗？", talk_name = "青乌",},
	[103030214] = { talk_id = 103030214, group_id = 1030302, type = 1, talk_content = "可以呀，那你过来吧，我不太想动嘻哈。", talk_name = "脉灵墩墩",},
	[103030301] = { talk_id = 103030301, group_id = 1030303, type = 1, next_id = 103030302, talk_content = "嘻哈，你们还站在这里干什么呀？", talk_name = "脉灵墩墩",},
	[103030302] = { talk_id = 103030302, group_id = 1030303, type = 1, talk_content = "我要继续发呆了，你们不要太吵哦。", talk_name = "脉灵墩墩",},
	[103030401] = { talk_id = 103030401, group_id = 1030304, type = 1, next_id = 103030402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "悬天的人果然把货物送上去了。", talk_name = "青乌",},
	[103030402] = { talk_id = 103030402, group_id = 1030304, type = 1, next_id = 103030403, talk_content = "怎么样？我说得不假吧？", talk_name = "义向",},
	[103030403] = { talk_id = 103030403, group_id = 1030304, type = 1, talk_content = "一鼓作气，我陪你到浮空岛，把藏起来的东西找到吧。", talk_name = "义向",},
	[103030501] = { talk_id = 103030501, group_id = 1030305, type = 4, next_id = 103030502, aside = "<color=#FFEBA2>青乌：</color>为什么公园中央会留下这样规模的浮空岛呢？", talk_content = "为什么公园中央会留下这样规模的浮空岛呢？", talk_name = "青乌",},
	[103030502] = { talk_id = 103030502, group_id = 1030305, type = 4, next_id = 103030503, aside = "<color=#FFEBA2>义向：</color>算是一种纪念吧，天月城的人们大部分很难去到地面了。", talk_content = "算是一种纪念吧，天月城的人们大部分很难去到地面了。", talk_name = "义向",},
	[103030503] = { talk_id = 103030503, group_id = 1030305, type = 4, aside = "<color=#FFEBA2>义向：</color>所以留下了浮空岛屿的景观，是为了提醒我们脚下的土地并非大地，而是天空中的岛屿。", talk_content = "所以留下了浮空岛屿的景观，是为了提醒我们脚下的土地并非大地，而是天空中的岛屿。", talk_name = "义向",},
	[103030601] = { talk_id = 103030601, group_id = 1030306, type = 1, next_id = 103030602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "哦对了，想上浮空岛得有个先决条件。", talk_name = "义向",},
	[103030602] = { talk_id = 103030602, group_id = 1030306, type = 2, options = {{"不太会。",103030603,},{"我可以学。",103030604,},}, talk_content = "你会用佩从操控物体吗？", talk_name = "义向",},
	[103030603] = { talk_id = 103030603, group_id = 1030306, type = 3, next_id = 103030605, talk_content = "不会可不行，得想个法子快速教会你。", talk_name = "义向",},
	[103030604] = { talk_id = 103030604, group_id = 1030306, type = 3, next_id = 103030605, talk_content = "不算简单，不过青乌你一定能很快掌握。", talk_name = "义向",},
	[103030605] = { talk_id = 103030605, group_id = 1030306, type = 1, next_id = 103030606, talk_content = "师傅说过，操纵能力最关键是找到诀窍。", talk_name = "义向",},
	[103030606] = { talk_id = 103030606, group_id = 1030306, type = 1, next_id = 103030607, talk_content = "“心念同用，神意一体”，这是师傅交给我的口诀。", talk_name = "义向",},
	[103030607] = { talk_id = 103030607, group_id = 1030306, type = 1, talk_content = "初学的话，就先从移动简单的物体开始吧。", talk_name = "义向",},
	[103030701] = { talk_id = 103030701, group_id = 1030307, type = 4, next_id = 103030702, aside = "<color=#FFEBA2>义向：</color>行啊，最为基础的隔空移物已经学得差不多了。", talk_content = "行啊，最为基础的隔空移物已经学得差不多了。", talk_name = "义向",},
	[103030702] = { talk_id = 103030702, group_id = 1030307, type = 4, next_id = 103030703, aside = "<color=#FFEBA2>义向：</color>但仅仅学会隔空移物是不够的。", talk_content = "但仅仅学会隔空移物是不够的。", talk_name = "义向",},
	[103030703] = { talk_id = 103030703, group_id = 1030307, type = 4, next_id = 103030704, aside = "<color=#FFEBA2>义向：</color>想将操纵物体的能力应用得心应手，至少还要学会物体拼接。", talk_content = "想将操纵物体的能力应用得心应手，至少还要学会物体拼接。", talk_name = "义向",},
	[103030704] = { talk_id = 103030704, group_id = 1030307, type = 4, next_id = 103030705, aside = "<color=#FFEBA2>义向：</color>看到那根景观柱了吗？这是第二关考验。", talk_content = "看到那根景观柱了吗？这是第二关考验。", talk_name = "义向",},
	[103030705] = { talk_id = 103030705, group_id = 1030307, talk_content = "试试利用物体拼接爬到景观柱的顶点，把上面的东西取下来。", talk_name = "义向",},
	[103040101] = { talk_id = 103040101, group_id = 1030401, type = 2, child_type = 21, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"没什么难的。",103040102,},{"主要是义向师傅教的好。",103040103,},}, talk_content = "很好，两样最基础的操作你都已经学会了。", talk_name = "义向",},
	[103040102] = { talk_id = 103040102, group_id = 1030401, type = 3, next_id = 103040104, icon = "interaction_4", talk_content = "悟性很高嘛，看来你是学这个的料。", talk_name = "义向",},
	[103040103] = { talk_id = 103040103, group_id = 1030401, type = 3, next_id = 103040104, icon = "interaction_4", talk_content = "别这么说，我和师傅还差得远呐。", talk_name = "义向",},
	[103040104] = { talk_id = 103040104, group_id = 1030401, type = 1, next_id = 103040105, talk_content = "接下来，就进入实操的阶段。", talk_name = "义向",},
	[103040105] = { talk_id = 103040105, group_id = 1030401, type = 1, next_id = 103040106, talk_content = "用学到的操作，造出飞行工具前往浮空区。", talk_name = "义向",},
	[103040106] = { talk_id = 103040106, group_id = 1030401, type = 1, next_id = 103040107, talk_content = "不过，现在距离浮空岛的直线距离太过遥远，飞行工具的能源似乎坚持不了那么久。", talk_name = "义向",},
	[103040107] = { talk_id = 103040107, group_id = 1030401, type = 1, next_id = 103040108, talk_content = "那悬天的人是怎么登上浮空岛的呢？", talk_name = "青乌",},
	[103040108] = { talk_id = 103040108, group_id = 1030401, type = 1, next_id = 103040109, talk_content = "想要缩短飞行的距离，从浮空岛的正下方起飞是最好的。", talk_name = "义向",},
	[103040109] = { talk_id = 103040109, group_id = 1030401, type = 1, next_id = 103040110, talk_content = "你是指，那边的湖心岛？", talk_name = "青乌",},
	[103040110] = { talk_id = 103040110, group_id = 1030401, type = 1, next_id = 103040111, talk_content = "大概悬天的人也是先到湖中心的小岛，再登上空中的岛屿。", talk_name = "义向",},
	[103040111] = { talk_id = 103040111, group_id = 1030401, type = 1, talk_content = "我陪你去湖边搜索一下吧，或许能有些发现。", talk_name = "义向",},
	[103040201] = { talk_id = 103040201, group_id = 1030402, type = 1, next_id = 103040202, fade_in = 1, fade_out = true, hide_player = true, talk_content = "看样子没错，要先抵达湖心岛，从正下方才容易飞上去。", talk_name = "义向",},
	[103040202] = { talk_id = 103040202, group_id = 1030402, type = 1, talk_content = "先用现场的材料造艘船过去吧，也算是提前演练建造。", talk_name = "义向",},
	[103040301] = { talk_id = 103040301, group_id = 1030403, type = 4, next_id = 103040302, aside = "<color=#FFEBA2>义向：</color>好嘞，青乌的第一个作品完工了。", talk_content = "好嘞，青乌的第一个作品完工了。", talk_name = "义向",},
	[103040302] = { talk_id = 103040302, group_id = 1030403, type = 4, next_id = 103040303, aside = "<color=#FFEBA2>义向：</color>咱们走吧，准备启航！", talk_content = "咱们走吧，准备启航！", talk_name = "义向",},
	[103040303] = { talk_id = 103040303, group_id = 1030403, type = 4, aside = "<color=#FFEBA2>青乌：</color>你比我还有激情啊……", talk_content = "你比我还有激情啊……", talk_name = "青乌",},
	[103050101] = { talk_id = 103050101, group_id = 1030501, type = 1, next_id = 103050102, fade_in = 1, fade_out = true, hide_player = true, talk_content = "嗯，和我想的八九不离十。", talk_name = "义向",},
	[103050102] = { talk_id = 103050102, group_id = 1030501, type = 1, next_id = 103050103, talk_content = "这里的高度目测下来，正好可以够飞行工具上去。", talk_name = "义向",},
	[103050103] = { talk_id = 103050103, group_id = 1030501, type = 1, next_id = 103050104, talk_content = "要造飞行工具了吗？", talk_name = "青乌",},
	[103050104] = { talk_id = 103050104, group_id = 1030501, type = 1, next_id = 103050105, talk_content = "对于初学者来说，不必追求复杂的结构。", talk_name = "义向",},
	[103050105] = { talk_id = 103050105, group_id = 1030501, type = 1, talk_content = "简单做出一个飞行踏板就好，对于你来说肯定是小菜一碟。", talk_name = "义向",},
	[103060101] = { talk_id = 103060101, group_id = 1030601, type = 4, next_id = 103060102, aside = "<color=#FFEBA2>青乌：</color>已经有怪物捷足先登了。", talk_content = "已经有怪物捷足先登了。", talk_name = "青乌",},
	[103060102] = { talk_id = 103060102, group_id = 1030601, type = 4, next_id = 103060103, aside = "<color=#FFEBA2>义向：</color>浮空岛这种地方，发生噬脉乱流的几率比看起来比地面上高不少呢？", talk_content = "浮空岛这种地方，发生噬脉乱流的几率比看起来比地面上高不少呢？", talk_name = "义向",},
	[103060103] = { talk_id = 103060103, group_id = 1030601, type = 4, next_id = 103060104, aside = "<color=#FFEBA2>义向：</color>怎么说？要悄悄避开他们吗？", talk_content = "怎么说？要悄悄避开他们吗？", talk_name = "义向",},
	[103060104] = { talk_id = 103060104, group_id = 1030601, type = 4, next_id = 103060105, aside = "<color=#FFEBA2>青乌：</color>当然不用，噬脉生物就把他们统统消灭就好了。", talk_content = "当然不用，噬脉生物就把他们统统消灭就好了。", talk_name = "青乌",},
	[103060105] = { talk_id = 103060105, group_id = 1030601, type = 4, aside = "<color=#FFEBA2>义向：</color>哈哈正合我意，咱们上吧！", talk_content = "哈哈正合我意，咱们上吧！", talk_name = "义向",},
	[103060201] = { talk_id = 103060201, group_id = 1030602, type = 1, next_id = 103060202, fade_in = 1, fade_out = true, hide_player = true, talk_content = "看起来不是这里，找了一圈什么都没有。", talk_name = "青乌",},
	[103060202] = { talk_id = 103060202, group_id = 1030602, type = 1, next_id = 103060203, talk_content = "好几个浮空岛呢，不着急一个个搜查，不怕找不出来。", talk_name = "义向",},
	[103060203] = { talk_id = 103060203, group_id = 1030602, type = 1, talk_content = "先去下一个区域找找吧。", talk_name = "义向",},
	[103070101] = { talk_id = 103070101, group_id = 1030701, type = 4, next_id = 103070102, aside = "<color=#FFEBA2>青乌：</color>我有一种预感，这里怕也找不到我们想要的东西。", talk_content = "我有一种预感，这里怕也找不到我们想要的东西。", talk_name = "青乌",},
	[103070102] = { talk_id = 103070102, group_id = 1030701, type = 4, aside = "<color=#FFEBA2>义向：</color>先找找再说。", talk_content = "先找找再说。", talk_name = "义向",},
	[103070201] = { talk_id = 103070201, group_id = 1030702, type = 4, next_id = 103070202, aside = "<color=#FFEBA2>青乌：</color>所有浮空岛上都挤满了怪物吗？天月城怎么就没有一处安全的地方？", talk_content = "所有浮空岛上都挤满了怪物吗？天月城怎么就没有一处安全的地方？", talk_name = "青乌",},
	[103070202] = { talk_id = 103070202, group_id = 1030702, type = 4, aside = "<color=#FFEBA2>义向：</color>这个问题，我也思考了很久。", talk_content = "这个问题，我也思考了很久。", talk_name = "义向",},
	[103070301] = { talk_id = 103070301, group_id = 1030703, type = 1, next_id = 103070302, fade_in = 1, fade_out = true, hide_player = true, talk_content = "一无所获，只能把希望寄托在最后一座浮空岛了。", talk_name = "青乌",},
	[103070302] = { talk_id = 103070302, group_id = 1030703, type = 1, next_id = 103070303, talk_content = "别泄气，这不是还有希望的嘛。", talk_name = "义向",},
	[103070303] = { talk_id = 103070303, group_id = 1030703, type = 1, next_id = 103070304, talk_content = "悬天既然来过，好好搜寻一番总能有收获的。", talk_name = "义向",},
	[103070304] = { talk_id = 103070304, group_id = 1030703, type = 1, next_id = 103070305, talk_content = "那我们继续吧。", talk_name = "青乌",},
	[103070305] = { talk_id = 103070305, group_id = 1030703, type = 1, next_id = 103070306, talk_content = "不过，我的纵横基体用完了，眼下拼不了飞行踏板。", talk_name = "义向",},
	[103070306] = { talk_id = 103070306, group_id = 1030703, type = 1, next_id = 103070307, talk_content = "估计要你载我一程喽。", talk_name = "义向",},
	[103070307] = { talk_id = 103070307, group_id = 1030703, type = 1, talk_content = "没问题。", talk_name = "青乌",},
	[103070401] = { talk_id = 103070401, group_id = 1030704, type = 1, next_id = 103070402, talk_content = "准备好了吗？准备好就出发咯。", talk_name = "青乌",},
	[103070402] = { talk_id = 103070402, group_id = 1030704, type = 1, talk_content = "当然，我们走吧。", talk_name = "义向",},
	[103080101] = { talk_id = 103080101, group_id = 1030801, type = 4, next_id = 103080102, aside = "<color=#FFEBA2>青乌：</color>只能寄托希望在最后一个岛上了。", talk_content = "只能寄托希望在最后一个岛上了。", talk_name = "青乌",},
	[103080102] = { talk_id = 103080102, group_id = 1030801, type = 4, aside = "<color=#FFEBA2>义向：</color>放心，食言是不义，说过帮你找到，我就一定会帮到底。", talk_content = "放心，食言是不义，说过帮你找到，我就一定会帮到底。", talk_name = "义向",},
	[103080201] = { talk_id = 103080201, group_id = 1030802, type = 1, next_id = 103080202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "这是你要找的溶液吗？", talk_name = "义向",},
	[103080202] = { talk_id = 103080202, group_id = 1030802, type = 1, next_id = 103080203, talk_content = "没错，这下终于……", talk_name = "青乌",},
	[103080203] = { talk_id = 103080203, group_id = 1030802, type = 1, next_id = 103080204, talk_content = "小心！", talk_name = "义向",},
	[103080204] = { talk_id = 103080204, group_id = 1030802, type = 1, next_id = 103080205, talk_content = "差一点就被偷袭到了。", talk_name = "青乌",},
	[103080205] = { talk_id = 103080205, group_id = 1030802, type = 1, next_id = 103080206, talk_content = "这家伙看起来不好对付……不过我也很久没有打个痛快了。", talk_name = "义向",},
	[103080206] = { talk_id = 103080206, group_id = 1030802, type = 1, talk_content = "先打倒它再说！", talk_name = "青乌",},
	[103080301] = { talk_id = 103080301, group_id = 1030803, type = 4, aside = "<color=#FFEBA2>义向：</color>让你见识一下，创生主义的实力！", talk_content = "让你见识一下，创生主义的实力！", talk_name = "义向",},
	[103080401] = { talk_id = 103080401, group_id = 1030804, type = 1, next_id = 103080402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "呼——这家伙还蛮难对付的。", talk_name = "义向",},
	[103080402] = { talk_id = 103080402, group_id = 1030804, type = 1, next_id = 103080403, talk_content = "青乌，打得不错嘛，干净利落。", talk_name = "义向",},
	[103080403] = { talk_id = 103080403, group_id = 1030804, type = 1, next_id = 103080404, talk_content = "多谢你在一旁助阵。", talk_name = "青乌",},
	[103080404] = { talk_id = 103080404, group_id = 1030804, type = 1, next_id = 103080405, talk_content = "不幸中的万幸，战斗中打碎的溶液不多，还能带很多回去。", talk_name = "青乌",},
	[103080405] = { talk_id = 103080405, group_id = 1030804, type = 1, next_id = 103080406, talk_content = "那我们抓紧时间吧，万一悬天留有什么后手，那就超过我们的处理范围了。", talk_name = "义向",},
	[103080406] = { talk_id = 103080406, group_id = 1030804, type = 1, next_id = 103080407, talk_content = "看不出来你还挺小心谨慎的。", talk_name = "青乌",},
	[103080407] = { talk_id = 103080407, group_id = 1030804, type = 1, next_id = 103080408, talk_content = "光有一腔热血可实现不了道义，懂得周旋也是修行的一部分嘛。", talk_name = "义向",},
	[103080408] = { talk_id = 103080408, group_id = 1030804, type = 1, talk_content = "有道理，那我们走吧。", talk_name = "青乌",},
	[103080501] = { talk_id = 103080501, group_id = 1030805, type = 1, next_id = 103080502, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "呼——这家伙真难对付。", talk_name = "义向",},
	[103080502] = { talk_id = 103080502, group_id = 1030805, type = 1, next_id = 103080503, talk_content = "青乌，这一战打得真是惊心动魄啊。", talk_name = "义向",},
	[103080503] = { talk_id = 103080503, group_id = 1030805, type = 1, next_id = 103080504, talk_content = "溶液也打碎了不少……", talk_name = "青乌",},
	[103080504] = { talk_id = 103080504, group_id = 1030805, type = 1, next_id = 103080505, talk_content = "嗨，没事没事，这不还有一罐嘛。", talk_name = "义向",},
	[103080505] = { talk_id = 103080505, group_id = 1030805, type = 1, next_id = 103080506, talk_content = "带回去也能交差了，没有受重伤更重要。", talk_name = "义向",},
	[103080506] = { talk_id = 103080506, group_id = 1030805, type = 1, talk_content = "你还真是乐天派。", talk_name = "青乌",},
	[103080601] = { talk_id = 103080601, group_id = 1030806, type = 5, next_id = 103080602, aside = "<color=#FFEBA2>青乌：</color>小五？我已经拿到溶液了……", call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "小五？我已经拿到溶液了……", talk_name = "青乌",},
	[103080602] = { talk_id = 103080602, group_id = 1030806, type = 5, next_id = 103080603, aside = "<color=#FFEBA2>小五：</color>青乌……茶室遇上大麻烦了……", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "青乌……茶室遇上大麻烦了……", talk_name = "小五",},
	[103080603] = { talk_id = 103080603, group_id = 1030806, type = 5, next_id = 103080604, aside = "<color=#FFEBA2>青乌：</color>什么？你仔细说，茶室怎么了？", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "什么？你仔细说，茶室怎么了？", talk_name = "青乌",},
	[103080604] = { talk_id = 103080604, group_id = 1030806, type = 5, next_id = 103080605, aside = "<color=#FFEBA2>小五：</color>你刚走没多久，悬天的人就突袭了茶室……", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "你刚走没多久，悬天的人就突袭了茶室……", talk_name = "小五",},
	[103080605] = { talk_id = 103080605, group_id = 1030806, type = 5, next_id = 103080606, aside = "<color=#FFEBA2>青乌：</color>……！", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "……！", talk_name = "青乌",},
	[103080606] = { talk_id = 103080606, group_id = 1030806, type = 5, next_id = 103080607, aside = "<color=#FFEBA2>小五：</color>离歌她也被悬天的人带走了……", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "离歌她也被悬天的人带走了……", talk_name = "小五",},
	[103080607] = { talk_id = 103080607, group_id = 1030806, type = 5, next_id = 103080608, aside = "<color=#FFEBA2>青乌：</color>那你知不知道她被带去哪里了？", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "那你知不知道她被带去哪里了？", talk_name = "青乌",},
	[103080608] = { talk_id = 103080608, group_id = 1030806, type = 5, next_id = 103080609, aside = "<color=#FFEBA2>小五：</color>离歌临走时没有留下信息……总之你先回来再说。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "离歌临走时没有留下信息……总之你先回来再说。", talk_name = "小五",},
	[103080609] = { talk_id = 103080609, group_id = 1030806, type = 5, next_id = 103080610, aside = "<color=#FFEBA2>青乌：</color>那我们茶室碰头，我很快就赶回来。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "那我们茶室碰头，我很快就赶回来。", talk_name = "青乌",},
	[103080610] = { talk_id = 103080610, group_id = 1030806, type = 5, aside = "<color=#FFEBA2>小五：</color>嗯，我等你。", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "嗯，我等你。", talk_name = "小五",},
	[103080701] = { talk_id = 103080701, group_id = 1030807, type = 1, next_id = 103080702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "怎么了青乌，又遇到什么麻烦了吗？", talk_name = "义向",},
	[103080702] = { talk_id = 103080702, group_id = 1030807, type = 1, next_id = 103080703, talk_content = "茶室发生了意外，离歌她被悬天带走了。", talk_name = "青乌",},
	[103080703] = { talk_id = 103080703, group_id = 1030807, type = 1, next_id = 103080704, talk_content = "什么？悬天竟敢干出这种事？", talk_name = "义向",},
	[103080704] = { talk_id = 103080704, group_id = 1030807, type = 1, next_id = 103080705, talk_content = "趁着茶室空虚离歌有伤乘虚而入……悬天已经谋划很久了吧。", talk_name = "青乌",},
	[103080705] = { talk_id = 103080705, group_id = 1030807, type = 2, child_type = 21, options = {{"先不要轻举妄动。",103080706,},{"现在最重要的是离歌的安危。",103080709,},}, talk_content = "那你下一步打算怎么办？", talk_name = "义向",},
	[103080706] = { talk_id = 103080706, group_id = 1030807, type = 3, next_id = 103080707, icon = "interaction_4", talk_content = "离歌被他们挟持，我们暂时处在被动。", talk_name = "青乌",},
	[103080707] = { talk_id = 103080707, group_id = 1030807, type = 3, next_id = 103080708, talk_content = "现在还不具备和他们直接交锋的条件。", talk_name = "青乌",},
	[103080708] = { talk_id = 103080708, group_id = 1030807, type = 3, next_id = 103080711, talk_content = "先查清来龙去脉再做打算。", talk_name = "青乌",},
	[103080709] = { talk_id = 103080709, group_id = 1030807, type = 3, next_id = 103080710, icon = "interaction_4", talk_content = "不先把离歌解救出来，其他也无从说起。", talk_name = "青乌",},
	[103080710] = { talk_id = 103080710, group_id = 1030807, type = 3, next_id = 103080711, talk_content = "至于悬天怎么应对，这个先不考虑。", talk_name = "青乌",},
	[103080711] = { talk_id = 103080711, group_id = 1030807, type = 1, next_id = 103080712, talk_content = "虽然有些唐突，但是我必须得立刻赶回去处理茶室的情况。", talk_name = "青乌",},
	[103080712] = { talk_id = 103080712, group_id = 1030807, type = 1, next_id = 103080713, talk_content = "嗨，这当然是要紧事了，不用客套。", talk_name = "义向",},
	[103080713] = { talk_id = 103080713, group_id = 1030807, type = 1, next_id = 103080714, talk_content = "需要帮忙吗？我可以和你一道去。", talk_name = "义向",},
	[103080714] = { talk_id = 103080714, group_id = 1030807, type = 1, next_id = 103080715, talk_content = "毕竟我们已经是并肩战斗过的朋友了，不救友人困厄之间可是不义。", talk_name = "义向",},
	[103080715] = { talk_id = 103080715, group_id = 1030807, type = 1, next_id = 103080716, talk_content = "还无法断定背后发生了什么，我要去确认一下。", talk_name = "青乌",},
	[103080716] = { talk_id = 103080716, group_id = 1030807, type = 1, next_id = 103080717, talk_content = "谢谢你的好意，有需要的话一定联系你。", talk_name = "青乌",},
	[103080717] = { talk_id = 103080717, group_id = 1030807, type = 1, next_id = 103080718, talk_content = "那我等你的消息，有难处创生主义一定帮忙。", talk_name = "义向",},
	[103080718] = { talk_id = 103080718, group_id = 1030807, type = 1, next_id = 103080719, talk_content = "来日再会，义向。", talk_name = "青乌",},
	[103080719] = { talk_id = 103080719, group_id = 1030807, type = 1, talk_content = "再会了，青乌。", talk_name = "义向",},
	[103080801] = { talk_id = 103080801, group_id = 1030808, type = 1, next_id = 103080802, talk_content = "我再找找有没有悬天遗留下来的重要线索。", talk_name = "义向",},
	[103080802] = { talk_id = 103080802, group_id = 1030808, type = 1, next_id = 103080803, talk_content = "还不出发吗？茶室的事情不要紧吗？", talk_name = "义向",},
	[103080803] = { talk_id = 103080803, group_id = 1030808, type = 1, talk_content = "也是，得赶快走了。", talk_name = "青乌",},
	[103090101] = { talk_id = 103090101, group_id = 1030901, type = 1, next_id = 103090102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "小五？", talk_name = "青乌",},
	[103090102] = { talk_id = 103090102, group_id = 1030901, type = 1, next_id = 103090103, talk_content = "青乌，你回来了。", talk_name = "小五",},
	[103090103] = { talk_id = 103090103, group_id = 1030901, type = 1, next_id = 103090104, talk_content = "小五，你没事吧？", talk_name = "青乌",},
	[103090104] = { talk_id = 103090104, group_id = 1030901, type = 1, next_id = 103090105, talk_content = "因为我通常都待在藏身的地方所以没事……", talk_name = "小五",},
	[103090105] = { talk_id = 103090105, group_id = 1030901, type = 1, next_id = 103090106, talk_content = "你没事就好，离歌她……", talk_name = "青乌",},
	[103090106] = { talk_id = 103090106, group_id = 1030901, type = 1, next_id = 103090107, talk_content = "悬天袭击茶室非常突然，我们没有提前察觉到。", talk_name = "小五",},
	[103090107] = { talk_id = 103090107, group_id = 1030901, type = 1, next_id = 103090108, talk_content = "离歌身上溶液造成的伤比想象严重，几乎无法战斗。", talk_name = "小五",},
	[103090108] = { talk_id = 103090108, group_id = 1030901, type = 1, next_id = 103090109, talk_content = "所以，离歌为了避免不必要的冲突，主动和悬天的人走了。", talk_name = "小五",},
	[103090109] = { talk_id = 103090109, group_id = 1030901, type = 1, next_id = 103090110, talk_content = "邀请我们见面，然后突然发动袭击劫走离歌，悬天这一套可真是行云流水。", talk_name = "青乌",},
	[103090110] = { talk_id = 103090110, group_id = 1030901, type = 1, next_id = 103090111, talk_content = "离歌说得没错，悬天不是善类。", talk_name = "青乌",},
	[103090111] = { talk_id = 103090111, group_id = 1030901, type = 1, next_id = 103090112, talk_content = "小五，我们要想办法尽快查出她被带到了哪里，我得去会会悬天。", talk_name = "青乌",},
	[103090112] = { talk_id = 103090112, group_id = 1030901, type = 1, next_id = 103090113, talk_content = "青乌，你先别主动在悬天眼前露面，他们的目标肯定不止离歌她一个人。", talk_name = "小五",},
	[103090113] = { talk_id = 103090113, group_id = 1030901, type = 1, next_id = 103090114, talk_content = "你不能也落入悬天的陷阱。", talk_name = "小五",},
	[103090114] = { talk_id = 103090114, group_id = 1030901, type = 1, next_id = 103090115, talk_content = "我明白，可是离歌现在身处危险之中，我们必须要赶紧想办法解救她。", talk_name = "青乌",},
	[103090115] = { talk_id = 103090115, group_id = 1030901, type = 1, next_id = 103090116, talk_content = "这个你先放心，我们都想快些救离歌回来。", talk_name = "小五",},
	[103090116] = { talk_id = 103090116, group_id = 1030901, type = 1, next_id = 103090117, talk_content = "我已经联络另外的人去调查离歌的下落了。", talk_name = "小五",},
	[103090117] = { talk_id = 103090117, group_id = 1030901, type = 1, next_id = 103090118, talk_content = "你先暗中活动，不要暴露自己的行踪。", talk_name = "小五",},
	[103090118] = { talk_id = 103090118, group_id = 1030901, type = 2, options = {{"那就交给你了。",103090119,},{"我总不能什么都不做吧。",103090119,},}, talk_content = "等有关于离歌的确切消息，我们再商量怎么救她回来。", talk_name = "小五",},
	[103090119] = { talk_id = 103090119, group_id = 1030901, type = 3, next_id = 103090120, talk_content = "离歌不在，茶室有许多业务都不能歇着，也不能让外人到处传茶室无人管事。", talk_name = "小五",},
	[103090120] = { talk_id = 103090120, group_id = 1030901, type = 1, next_id = 103090121, talk_content = "青乌，离歌没回来之前，就你来代她处理这些事情吧。", talk_name = "小五",},
	[103090121] = { talk_id = 103090121, group_id = 1030901, type = 1, next_id = 103090122, talk_content = "要做离歌的代理么……好像也没什么问题。", talk_name = "青乌",},
	[103090122] = { talk_id = 103090122, group_id = 1030901, type = 1, next_id = 103090123, talk_content = "正好，有家和我们望月茶室有合作关系的公司最近遇上麻烦求助离歌，离歌不在的话，你去帮忙看看吧。", talk_name = "小五",},
	[103090123] = { talk_id = 103090123, group_id = 1030901, type = 1, talk_content = "这段时间，拜托你多操心了。", talk_name = "小五",},
	[103090201] = { talk_id = 103090201, group_id = 1030902, type = 1, next_id = 103090202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "有人吗？", talk_name = "悬天成员",},
	[103090202] = { talk_id = 103090202, group_id = 1030902, type = 1, next_id = 103090203, talk_content = "你好，这里是望月茶……", talk_name = "小五",},
	[103090203] = { talk_id = 103090203, group_id = 1030902, type = 1, next_id = 103090204, talk_content = "居然还敢来？", talk_name = "青乌",},
	[103090204] = { talk_id = 103090204, group_id = 1030902, type = 1, next_id = 103090205, talk_content = "等等！我不是来找麻烦的先别动手！", talk_name = "悬天成员",},
	[103090205] = { talk_id = 103090205, group_id = 1030902, type = 1, next_id = 103090206, talk_content = "那你是来干嘛的？难不成是来帮忙？", talk_name = "青乌",},
	[103090206] = { talk_id = 103090206, group_id = 1030902, type = 1, next_id = 103090207, talk_content = "你还别说，我还真是想来帮忙的。", talk_name = "悬天成员",},
	[103090207] = { talk_id = 103090207, group_id = 1030902, type = 1, next_id = 103090208, talk_content = "青乌大人，先前你和余辜见面时我也在场，你当时的胆色我可是见识到了。", talk_name = "悬天成员",},
	[103090208] = { talk_id = 103090208, group_id = 1030902, type = 1, next_id = 103090209, talk_content = "悬天这帮人，当面一套背后一套，我已经忍他们很久了。", talk_name = "悬天成员",},
	[103090209] = { talk_id = 103090209, group_id = 1030902, type = 1, next_id = 103090210, talk_content = "跟着他们混，天天做些见不得人的勾当，感觉也没什么前途。还不如和青乌大人混呢。", talk_name = "悬天成员",},
	[103090210] = { talk_id = 103090210, group_id = 1030902, type = 1, next_id = 103090211, talk_content = "我们可不会轻易相信你。", talk_name = "青乌",},
	[103090211] = { talk_id = 103090211, group_id = 1030902, type = 1, next_id = 103090212, talk_content = "我知道你们怀疑我的身份，要一下子就相信我也不太可能。", talk_name = "悬天成员",},
	[103090212] = { talk_id = 103090212, group_id = 1030902, type = 1, next_id = 103090213, talk_content = "这样吧，我暗中帮你们盯着茶室这条线，要是悬天那边又有什么针对茶室的行动，我暗中和你们通风怎样？", talk_name = "悬天成员",},
	[103090213] = { talk_id = 103090213, group_id = 1030902, type = 1, next_id = 103090214, talk_content = "至少，不会让这个小家伙被抓住。", talk_name = "悬天成员",},
	[103090214] = { talk_id = 103090214, group_id = 1030902, type = 1, next_id = 103090215, talk_content = "我不是什么小家伙……", talk_name = "小五",},
	[103090215] = { talk_id = 103090215, group_id = 1030902, type = 1, next_id = 103090216, talk_content = "怎么样青乌，要信他吗？", talk_name = "小五",},
	[103090216] = { talk_id = 103090216, group_id = 1030902, type = 1, talk_content = "悬天也没必要单独派一个人来骗我们，姑且就相信他吧。", talk_name = "青乌",},
	[103090301] = { talk_id = 103090301, group_id = 1030903, type = 1, next_id = 103090302, talk_content = "青乌，离歌不在，你就是茶室的话事人。", talk_name = "小五",},
	[103090302] = { talk_id = 103090302, group_id = 1030903, type = 1, next_id = 103090303, talk_content = "你可要担当起重任。", talk_name = "小五",},
	[103090303] = { talk_id = 103090303, group_id = 1030903, type = 1, next_id = 103090304, talk_content = "当代理话事人有什么实际的好处吗？", talk_name = "青乌",},
	[103090304] = { talk_id = 103090304, group_id = 1030903, type = 1, next_id = 103090305, talk_content = "没有，只不过让你体验一下离歌有多忙罢了。", talk_name = "小五",},
	[103090305] = { talk_id = 103090305, group_id = 1030903, type = 1, talk_content = "……", talk_name = "青乌",},
	[103090401] = { talk_id = 103090401, group_id = 1030904, type = 1, next_id = 103090402, talk_content = "青乌，你放心去吧。", talk_name = "小五",},
	[103090402] = { talk_id = 103090402, group_id = 1030904, type = 1, next_id = 103090403, talk_content = "记得机灵点，要是那个人有传来消息就及时找我。", talk_name = "青乌",},
	[103090403] = { talk_id = 103090403, group_id = 1030904, type = 1, talk_content = "说过我不是小孩子……", talk_name = "小五",},
	[103090501] = { talk_id = 103090501, group_id = 1030905, type = 4, next_id = 103090502, aside = "<color=#FFEBA2>店员：</color>救命啊，怎么出来这么多怪物！", talk_content = "救命啊，怎么出来这么多怪物！", talk_name = "店员",},
	[103090502] = { talk_id = 103090502, group_id = 1030905, type = 4, next_id = 103090503, aside = "<color=#FFEBA2>青乌：</color>街市上的噬脉生物越来越多了……", talk_content = "街市上的噬脉生物越来越多了……", talk_name = "青乌",},
	[103090503] = { talk_id = 103090503, group_id = 1030905, type = 4, aside = "<color=#FFEBA2>青乌：</color>到底是因为什么原因呢？", talk_content = "到底是因为什么原因呢？", talk_name = "青乌",},
	[103090601] = { talk_id = 103090601, group_id = 1030906, type = 1, next_id = 103090602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "店长，我真的不能再干下去了。", talk_name = "员工大壮",},
	[103090602] = { talk_id = 103090602, group_id = 1030906, type = 1, next_id = 103090603, talk_content = "大壮，悬天只是吓唬我们而已，你别这么轻易就怕了阿？", talk_name = "店长阿龚",},
	[103090603] = { talk_id = 103090603, group_id = 1030906, type = 1, next_id = 103090604, talk_content = "我只是来打工的，可没想到会惹到悬天的人！", talk_name = "员工大壮",},
	[103090604] = { talk_id = 103090604, group_id = 1030906, type = 1, next_id = 103090605, talk_content = "冒着风险挣这么点钱，也太不值了！", talk_name = "员工大壮",},
	[103090605] = { talk_id = 103090605, group_id = 1030906, type = 1, next_id = 103090606, talk_content = "小花，我们都走吧！", talk_name = "员工大壮",},
	[103090606] = { talk_id = 103090606, group_id = 1030906, type = 1, next_id = 103090607, talk_content = "可是……明明不是我们的错，而是悬天太霸道了……", talk_name = "员工小花",},
	[103090607] = { talk_id = 103090607, group_id = 1030906, type = 1, next_id = 103090608, talk_content = "对啊，为什么要我们退让，明明是他们不讲理。", talk_name = "店长阿龚",},
	[103090608] = { talk_id = 103090608, group_id = 1030906, type = 1, next_id = 103090609, talk_content = "你们慢慢和悬天讲理去吧，我不管了。", talk_name = "员工大壮",},
	[103090609] = { talk_id = 103090609, group_id = 1030906, type = 1, next_id = 103090610, talk_content = "唉你怎么就跑了，还没给我申请呢……", talk_name = "店长阿龚",},
	[103090610] = { talk_id = 103090610, group_id = 1030906, type = 1, next_id = 103090611, talk_content = "店长……", talk_name = "员工小花",},
	[103090611] = { talk_id = 103090611, group_id = 1030906, type = 1, next_id = 103090612, talk_content = "请问这里是顺通货运吗？", talk_name = "青乌",},
	[103090612] = { talk_id = 103090612, group_id = 1030906, type = 1, next_id = 103090613, talk_content = "是，请问你是？", talk_name = "店长阿龚",},
	[103090613] = { talk_id = 103090613, group_id = 1030906, type = 1, next_id = 103090614, talk_content = "离歌托我来，说是这里需要帮忙？", talk_name = "青乌",},
	[103090614] = { talk_id = 103090614, group_id = 1030906, type = 1, next_id = 103090615, talk_content = "等下，你是望月茶室的人？", talk_name = "店长阿龚",},
	[103090615] = { talk_id = 103090615, group_id = 1030906, type = 1, next_id = 103090616, talk_content = "太好了……终于有希望了……", talk_name = "店长阿龚",},
	[103090616] = { talk_id = 103090616, group_id = 1030906, type = 1, next_id = 103090617, talk_content = "所以你们店员很多都离职了？", talk_name = "青乌",},
	[103090617] = { talk_id = 103090617, group_id = 1030906, type = 1, next_id = 103090618, talk_content = "唉——没办法，因为和望月茶室有业务往来，悬天找到我们在职的员工，威胁他们不要替我们工作。", talk_name = "店长阿龚",},
	[103090618] = { talk_id = 103090618, group_id = 1030906, type = 1, next_id = 103090619, talk_content = "很多人害怕悬天真做出什么，这两天纷纷提离职走人，已经不剩几个员工了……", talk_name = "店长阿龚",},
	[103090619] = { talk_id = 103090619, group_id = 1030906, type = 1, next_id = 103090620, talk_content = "小花，你不会走吧……", talk_name = "店长阿龚",},
	[103090620] = { talk_id = 103090620, group_id = 1030906, type = 1, next_id = 103090621, talk_content = "我……我再考虑下吧……", talk_name = "员工小花",},
	[103090621] = { talk_id = 103090621, group_id = 1030906, type = 1, next_id = 103090622, talk_content = "所以怎么才可以帮到你？", talk_name = "青乌",},
	[103090622] = { talk_id = 103090622, group_id = 1030906, type = 1, next_id = 103090623, talk_content = "无论如何，不能对悬天服软！生意我们要继续做下去！", talk_name = "店长阿龚",},
	[103090623] = { talk_id = 103090623, group_id = 1030906, type = 1, next_id = 103090624, talk_content = "脉者大人，可以帮公司招募一点新员工来吗？", talk_name = "店长阿龚",},
	[103090624] = { talk_id = 103090624, group_id = 1030906, type = 1, next_id = 103090625, talk_content = "就这么简单？", talk_name = "青乌",},
	[103090625] = { talk_id = 103090625, group_id = 1030906, type = 1, next_id = 103090626, talk_content = "员工安全的事情，我去想办法，你只要帮我拉来合适的人就可以了。", talk_name = "店长阿龚",},
	[103090626] = { talk_id = 103090626, group_id = 1030906, type = 1, next_id = 103090627, talk_content = "只要新员工到位，公司就能继续运转。", talk_name = "店长阿龚",},
	[103090627] = { talk_id = 103090627, group_id = 1030906, type = 1, talk_content = "那你就等我的消息吧。", talk_name = "青乌",},
	[103090701] = { talk_id = 103090701, group_id = 1030907, type = 1, next_id = 103090702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "店长，我真的不能再干下去了。", talk_name = "员工小花",},
	[103090702] = { talk_id = 103090702, group_id = 1030907, type = 1, next_id = 103090703, talk_content = "小花，你可不能走阿！你走了公司就彻底没人了……", talk_name = "店长阿龚",},
	[103090703] = { talk_id = 103090703, group_id = 1030907, type = 1, next_id = 103090704, talk_content = "店长，没有抱怨工作不好的意思。", talk_name = "员工小花",},
	[103090704] = { talk_id = 103090704, group_id = 1030907, type = 1, next_id = 103090705, talk_content = "我们只是打工人，可没本事惹上悬天这种组织。", talk_name = "员工小花",},
	[103090705] = { talk_id = 103090705, group_id = 1030907, type = 1, next_id = 103090706, talk_content = "而且仅仅惹上悬天了就罢了，现在街区上噬脉生物天天大摇大摆出没，随时都会有人身危险。", talk_name = "员工小花",},
	[103090706] = { talk_id = 103090706, group_id = 1030907, type = 1, next_id = 103090707, talk_content = "刚才我在店门外都差点被噬脉生物围住了。", talk_name = "员工小花",},
	[103090707] = { talk_id = 103090707, group_id = 1030907, type = 1, next_id = 103090708, talk_content = "虽然赚钱也重要，可还是小命要紧。", talk_name = "员工小花",},
	[103090708] = { talk_id = 103090708, group_id = 1030907, type = 1, next_id = 103090709, talk_content = "店长，这是我的离职申请，我先走了！", talk_name = "员工小花",},
	[103090709] = { talk_id = 103090709, group_id = 1030907, type = 1, next_id = 103090710, talk_content = "等等，我还没同意呢！", talk_name = "店长阿龚",},
	[103090710] = { talk_id = 103090710, group_id = 1030907, type = 1, next_id = 103090711, talk_content = "唉……", talk_name = "店长阿龚",},
	[103090711] = { talk_id = 103090711, group_id = 1030907, type = 1, next_id = 103090712, talk_content = "请问这里是顺通货运吗？", talk_name = "青乌",},
	[103090712] = { talk_id = 103090712, group_id = 1030907, type = 1, next_id = 103090713, talk_content = "今天不接生意，要关门大吉了，请回吧。", talk_name = "店长阿龚",},
