	[103090718] = { talk_id = 103090718, group_id = 1030907, type = 1, next_id = 103090719, talk_content = "再加上最近街上的噬脉物越来越多，大家人心惶惶，几天之内都跑光了。", talk_name = "店长阿龚",},
	[103090719] = { talk_id = 103090719, group_id = 1030907, type = 1, next_id = 103090720, talk_content = "要是太长时间不营业，地租成本什么的会把公司拖垮，啊……不敢想了。", talk_name = "店长阿龚",},
