	[103090901] = { talk_id = 103090901, group_id = 1030909, type = 4, aside = "<color=#FFEBA2>青乌：</color>这样人就找齐了，回去告诉店长吧。", talk_content = "这样人就找齐了，回去告诉店长吧。", talk_name = "青乌",},
	[103091001] = { talk_id = 103091001, group_id = 1030910, type = 1, next_id = 103091002, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "店长，人都替你找齐了。", talk_name = "青乌",},
	[103091002] = { talk_id = 103091002, group_id = 1030910, type = 1, next_id = 103091003, talk_content = "感恩，感恩，望月茶室真是雪中送炭啊。", talk_name = "店长阿龚",},
	[103091003] = { talk_id = 103091003, group_id = 1030910, type = 1, next_id = 103091004, talk_content = "这下业务可以正常运转了！", talk_name = "店长阿龚",},
	[103091004] = { talk_id = 103091004, group_id = 1030910, type = 1, next_id = 103091005, talk_content = "如果悬天真的来骚扰倒是可以再找茶室帮忙。", talk_name = "青乌",},
	[103091005] = { talk_id = 103091005, group_id = 1030910, type = 1, next_id = 103091006, talk_content = "没事，悬天他们还不敢明目张胆干很过分的事的，我们能处理。", talk_name = "店长阿龚",},
	[103091006] = { talk_id = 103091006, group_id = 1030910, type = 1, talk_content = "脉者大人，谢谢你的帮助。", talk_name = "店长阿龚",},
	[103091101] = { talk_id = 103091101, group_id = 1030911, type = 1, next_id = 103091102, talk_content = "还有，记得向离歌转达我的感谢哦！", talk_name = "店长阿龚",},
