	[103100103] = { talk_id = 103100103, group_id = 1031001, type = 4, next_id = 103100104, aside = "<color=#FFEBA2>？？？：</color>不记得了？我是之前的那个供应商啊。", call_man_icon = "商人;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "不记得了？我是之前的那个供应商啊。", talk_name = "？？？",},
	[103100104] = { talk_id = 103100104, group_id = 1031001, type = 4, next_id = 103100105, aside = "<color=#FFEBA2>青乌：</color>想起来了，就是那个收集稀奇古怪材料的人对吧？", call_man_icon = "商人;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "想起来了，就是那个收集稀奇古怪材料的人对吧？", talk_name = "青乌",},
	[103100105] = { talk_id = 103100105, group_id = 1031001, type = 4, next_id = 103100106, aside = "<color=#FFEBA2>供应商：</color>老板还是好记性，上次得谢谢你帮忙。", call_man_icon = "商人;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "老板还是好记性，上次得谢谢你帮忙。", talk_name = "供应商",},
	[103100106] = { talk_id = 103100106, group_id = 1031001, type = 4, next_id = 103100107, aside = "<color=#FFEBA2>青乌：</color>所以，这次是又有什么业务要求助吗？", call_man_icon = "商人;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "所以，这次是又有什么业务要求助吗？", talk_name = "青乌",},
	[103100107] = { talk_id = 103100107, group_id = 1031001, type = 4, next_id = 103100108, aside = "<color=#FFEBA2>供应商：</color>姑且算是业务吧，求助谈不上，不过是有场比试想请老板来出面参加。", call_man_icon = "商人;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "姑且算是业务吧，求助谈不上，不过是有场比试想请老板来出面参加。", talk_name = "供应商",},
	[103100108] = { talk_id = 103100108, group_id = 1031001, type = 4, next_id = 103100109, aside = "<color=#FFEBA2>青乌：</color>比试？", call_man_icon = "商人;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "比试？", talk_name = "青乌",},
	[103100109] = { talk_id = 103100109, group_id = 1031001, type = 4, next_id = 103100110, aside = "<color=#FFEBA2>供应商：</color>电话里不方便说，你来百工巷附近就知道了。", call_man_icon = "商人;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "电话里不方便说，你来百工巷附近就知道了。", talk_name = "供应商",},
	[103100110] = { talk_id = 103100110, group_id = 1031001, type = 4, aside = "<color=#FFEBA2>青乌：</color>事情还真不少。", call_man_icon = "商人;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "事情还真不少。", talk_name = "青乌",},
	[103100201] = { talk_id = 103100201, group_id = 1031002, type = 1, next_id = 103100202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "老板你来了。", talk_name = "供应商",},
	[103100202] = { talk_id = 103100202, group_id = 1031002, type = 1, next_id = 103100203, talk_content = "不是第一次见面，以后叫我老金就好。", talk_name = "供应商",},
	[103100203] = { talk_id = 103100203, group_id = 1031002, type = 1, next_id = 103100204, talk_content = "老金，所谓的比试，到底是什么意思？", talk_name = "青乌",},
	[103100204] = { talk_id = 103100204, group_id = 1031002, type = 2, options = {{"完全没听过。",103100205,},{"听过也记不得了。",103100205,},}, talk_content = "老板听说过“选候终战”吗？", talk_name = "老金",},
	[103100205] = { talk_id = 103100205, group_id = 1031002, type = 3, next_id = 103100206, talk_content = "那就由我慢慢给老板讲解。", talk_name = "老金",},
	[103100206] = { talk_id = 103100206, group_id = 1031002, type = 1, next_id = 103100207, talk_content = "选候终战是天月城民间组织发起的一种斗技比试，只有脉者才可以参与。", talk_name = "老金",},
	[103100207] = { talk_id = 103100207, group_id = 1031002, type = 1, next_id = 103100208, talk_content = "参与者与噬脉生物战斗，获取相应的积分。", talk_name = "老金",},
	[103100208] = { talk_id = 103100208, group_id = 1031002, type = 2, options = {{"这个排行有什么用？",103100209,},{"为什么叫选候？",103100211,},{"你要我出面参加选候终战？",103100213,},}, talk_content = "根据参加者的积分，大家会列出一个选候榜，排列脉者之间的位次。", talk_name = "老金",},
	[103100209] = { talk_id = 103100209, group_id = 1031002, type = 3, next_id = 103100210, talk_content = "排行么，自然是前列者有奖啦。", talk_name = "老金",},
	[103100210] = { talk_id = 103100210, group_id = 1031002, type = 3, next_id = 103100208, talk_content = "为了鼓励脉者比试争锋，选候榜的奖励可是异常丰厚的哦。", talk_name = "老金",},
	[103100211] = { talk_id = 103100211, group_id = 1031002, type = 3, next_id = 103100212, talk_content = "封侯是天月的最高统治者，只不过民间有些人暗地里并不买他的帐。", talk_name = "老金",},
	[103100212] = { talk_id = 103100212, group_id = 1031002, type = 3, next_id = 103100208, talk_content = "所谓选候，就是民间自己根据实力选出来的封侯，有些调侃的意味在里面。", talk_name = "老金",},
	[103100213] = { talk_id = 103100213, group_id = 1031002, type = 3, next_id = 103100214, talk_content = "嘿嘿，这次的选候榜上有些奖励，我确实是非常想要拿到。", talk_name = "老金",},
	[103100214] = { talk_id = 103100214, group_id = 1031002, type = 3, next_id = 103100215, talk_content = "该不是又要拿去做什么奇怪的东西吧……", talk_name = "青乌",},
	[103100215] = { talk_id = 103100215, group_id = 1031002, type = 3, next_id = 103100216, talk_content = "这也是和离歌商量好的一份委托，老板就帮帮手吧。", talk_name = "老金",},
	[103100216] = { talk_id = 103100216, group_id = 1031002, type = 1, talk_content = "那也只有参加了。", talk_name = "青乌",},
	[103100301] = { talk_id = 103100301, group_id = 1031003, type = 1, next_id = 103100302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "这次真得多谢老板，想要的东西都拿到了。", talk_name = "老金",},
	[103100302] = { talk_id = 103100302, group_id = 1031003, type = 2, options = {{"平平无奇。",103100303,},{"还蛮有意思。",103100303,},}, talk_content = "感觉这比试怎么样？", talk_name = "老金",},
	[103100303] = { talk_id = 103100303, group_id = 1031003, type = 3, next_id = 103100304, talk_content = "选候终战会一直周期举行，老板要是想试试身手，或是想挑战选候榜上的其他脉者，之后再报名参加就可以了。", talk_name = "老金",},
	[103100304] = { talk_id = 103100304, group_id = 1031003, type = 1, next_id = 103100305, talk_content = "可惜我不是脉者，没实力去争，要不然我也想尝试下。", talk_name = "老金",},
	[103100305] = { talk_id = 103100305, group_id = 1031003, type = 1, next_id = 103100306, talk_content = "老板这么强，可不要浪费机会啊。", talk_name = "老金",},
	[103100306] = { talk_id = 103100306, group_id = 1031003, type = 1, next_id = 103100307, talk_content = "我会考虑的。", talk_name = "青乌",},
	[103100307] = { talk_id = 103100307, group_id = 1031003, type = 1, next_id = 103100308, talk_content = "那老板再见，下次想要做人偶就找来老金吧。", talk_name = "老金",},
	[103100308] = { talk_id = 103100308, group_id = 1031003, type = 1, talk_content = "应该不会有这种情况……", talk_name = "青乌",},
	[103100401] = { talk_id = 103100401, group_id = 1031004, type = 1, next_id = 103100402, talk_content = "这下材料就更齐了。", talk_name = "老金",},
	[103100402] = { talk_id = 103100402, group_id = 1031004, type = 1, next_id = 103100403, talk_content = "做出的肉身，究竟能达到什么程度呢？心痒难耐啊。", talk_name = "老金",},
	[103100403] = { talk_id = 103100403, group_id = 1031004, type = 1, talk_content = "……", talk_name = "青乌",},
	[104010101] = { talk_id = 104010101, group_id = 1040101, type = 4, next_id = 104010102, aside = "<color=#FFEBA2>郭中郎：</color>青乌，自从上次联系已经过去一段时间了，我们要的溶液找到没有？", call_icon = "Textures/Icon/Single/CampIcon/Wangtujian.png", call_man_icon = "郭中郎;Textures/Icon/Single/HeadIcon/SquNJuminnan01.png", child_type = 41, talk_content = "青乌，自从上次联系已经过去一段时间了，我们要的溶液找到没有？", talk_name = "郭中郎",},
	[104010102] = { talk_id = 104010102, group_id = 1040101, type = 4, next_id = 104010103, aside = "<color=#FFEBA2>郭中郎：</color>别忘了，你们跟王土可是有契约在先，我不想在这件事上费心。", call_man_icon = "郭中郎;Textures/Icon/Single/HeadIcon/SquNJuminnan01.png", child_type = 41, talk_content = "别忘了，你们跟王土可是有契约在先，我不想在这件事上费心。", talk_name = "郭中郎",},
	[104010103] = { talk_id = 104010103, group_id = 1040101, type = 4, next_id = 104010104, aside = "<color=#FFEBA2><nick_name>：</color>溶液已经拿到了。我们什么时候见面？我想和你谈谈关于酬劳的事。", call_man_icon = "郭中郎;Textures/Icon/Single/HeadIcon/SquNJuminnan01.png", child_type = 41, talk_content = "溶液已经拿到了。我们什么时候见面？我想和你谈谈关于酬劳的事。", talk_name = "<nick_name>",},
	[104010104] = { talk_id = 104010104, group_id = 1040101, type = 4, next_id = 104010105, aside = "<color=#FFEBA2>郭中郎：</color>哦？倒是比我想象的利落一点，不会是用什么假货哄我吧？", call_man_icon = "郭中郎;Textures/Icon/Single/HeadIcon/SquNJuminnan01.png", child_type = 41, talk_content = "哦？倒是比我想象的利落一点，不会是用什么假货哄我吧？", talk_name = "郭中郎",},
	[104010105] = { talk_id = 104010105, group_id = 1040101, type = 4, next_id = 104010106, aside = "<color=#FFEBA2><nick_name>：</color>离歌应该跟你介绍过我们“望月”的信誉，做不到的事我们从来不会随意答应。", call_man_icon = "郭中郎;Textures/Icon/Single/HeadIcon/SquNJuminnan01.png", child_type = 41, talk_content = "离歌应该跟你介绍过我们“望月”的信誉，做不到的事我们从来不会随意答应。", talk_name = "<nick_name>",},
	[104010106] = { talk_id = 104010106, group_id = 1040101, type = 4, next_id = 104010107, aside = "<color=#FFEBA2>郭中郎：</color>一个收钱办事的口气倒是不小，那就按你的意思见面交易吧。", call_man_icon = "郭中郎;Textures/Icon/Single/HeadIcon/SquNJuminnan01.png", child_type = 41, talk_content = "一个收钱办事的口气倒是不小，那就按你的意思见面交易吧。", talk_name = "郭中郎",},
	[104010107] = { talk_id = 104010107, group_id = 1040101, type = 4, aside = "<color=#FFEBA2>郭中郎：</color>见面地址我已经发给你了，准备好了就尽快过来找我。千万别想耍什么花招，青乌。", call_man_icon = "郭中郎;Textures/Icon/Single/HeadIcon/SquNJuminnan01.png", child_type = 41, talk_content = "见面地址我已经发给你了，准备好了就尽快过来找我。千万别想耍什么花招，青乌。", talk_name = "郭中郎",},
	[104010201] = { talk_id = 104010201, group_id = 1040102, type = 1, next_id = 104010202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "青乌，你总算来了。溶液带来了吗？", talk_name = "郭中郎",},
	[104010202] = { talk_id = 104010202, group_id = 1040102, type = 1, next_id = 104010203, talk_content = "带来了，东西就在我的身上。", talk_name = "<nick_name>",},
	[104010203] = { talk_id = 104010203, group_id = 1040102, type = 2, options = {{"还记得之前说过的酬劳吗？",104010204,},}, talk_content = "好啊，那就赶紧交出来吧。在付报酬之前，我得先确认一下货品。", talk_name = "郭中郎",},
	[104010204] = { talk_id = 104010204, group_id = 1040102, type = 3, next_id = 104010205, talk_content = "很抱歉，但我必须先确认酬劳再交出货品。郭先生，我记得你之前说过“事情办成一定有重谢”是吧？", talk_name = "<nick_name>",},
	[104010205] = { talk_id = 104010205, group_id = 1040102, type = 1, next_id = 104010206, talk_content = "这话是我说的，你有什么想法？", talk_name = "郭中郎",},
	[104010206] = { talk_id = 104010206, group_id = 1040102, type = 1, next_id = 104010207, talk_content = "郭先生承认就好。悬天是个危险的对手，为了拿到这份样品我们花了不少功夫。", talk_name = "<nick_name>",},
	[104010207] = { talk_id = 104010207, group_id = 1040102, type = 1, next_id = 104010208, talk_content = "现在既然有郭先生的承诺在先，我们也想得到和付出等值的报酬。", talk_name = "<nick_name>",},
	[104010208] = { talk_id = 104010208, group_id = 1040102, type = 1, next_id = 104010209, talk_content = "原来是想拿钱啊。既然这样那就打开天窗说亮话，你们“望月”想要什么？", talk_name = "郭中郎",},
	[104010209] = { talk_id = 104010209, group_id = 1040102, type = 2, child_type = 21, options = {{"一批人手。",104010210,},{"一份情报。",104010214,},{"一份抗衡的力量。",104010218,},}, talk_content = "我们想要……", talk_name = "<nick_name>",},
	[104010210] = { talk_id = 104010210, group_id = 1040102, type = 3, next_id = 104010211, icon = "interaction_4", talk_content = "既然要谈，我就把实情告诉郭先生。闯入悬天拿溶液的过程中出了意外，离歌现在已经被悬天带走了。", talk_name = "<nick_name>",},
	[104010211] = { talk_id = 104010211, group_id = 1040102, type = 3, next_id = 104010212, talk_content = "离歌的失踪和王土的委托脱不开关系，所以想要的报酬也和离歌有关。", talk_name = "<nick_name>",},
	[104010212] = { talk_id = 104010212, group_id = 1040102, type = 3, next_id = 104010213, talk_content = "我希望你们可以支援一批人手，和我一起寻找离歌。如果拿到悬天非法囚禁的证据，你们也可以借此瓦解他们。", talk_name = "<nick_name>",},
	[104010213] = { talk_id = 104010213, group_id = 1040102, type = 3, next_id = 104010223, talk_content = "相信这种程度的支援对于有着最崇高地位和发达人脉的王土来说应该不算什么，对吧？", talk_name = "<nick_name>",},
	[104010214] = { talk_id = 104010214, group_id = 1040102, type = 3, next_id = 104010215, icon = "interaction_4", talk_content = "既然要谈，我就把实情告诉郭先生。闯入悬天拿溶液的过程中出了意外，离歌现在已经被悬天带走了。", talk_name = "<nick_name>",},
	[104010215] = { talk_id = 104010215, group_id = 1040102, type = 3, next_id = 104010216, talk_content = "离歌的失踪和王土的委托脱不开关系，所以想要的报酬也和离歌有关。", talk_name = "<nick_name>",},
	[104010216] = { talk_id = 104010216, group_id = 1040102, type = 3, next_id = 104010217, talk_content = "离歌的失踪很可能和悬天一位名叫余辜的高层有关，我希望王土可以帮我调查余辜的资料，掌握她的动线。", talk_name = "<nick_name>",},
	[104010217] = { talk_id = 104010217, group_id = 1040102, type = 3, next_id = 104010223, talk_content = "余辜背后有不少秘密，如果可以借这次机会扳倒她，悬天就会损耗不少元气，对于王土来说也是好事不是吗？", talk_name = "<nick_name>",},
	[104010218] = { talk_id = 104010218, group_id = 1040102, type = 3, next_id = 104010219, icon = "interaction_4", talk_content = "既然要谈，我就把实情告诉郭先生。闯入悬天拿溶液的过程中出了意外，离歌现在已经被悬天带走了。", talk_name = "<nick_name>",},
	[104010219] = { talk_id = 104010219, group_id = 1040102, type = 3, next_id = 104010220, talk_content = "离歌的失踪和王土的委托脱不开关系，所以想要的报酬也和离歌有关。", talk_name = "<nick_name>",},
	[104010220] = { talk_id = 104010220, group_id = 1040102, type = 3, next_id = 104010221, talk_content = "悬天之所以随意带走离歌，一定是认准了我们没有力量和他们抗衡。这种力量不是拳头的力量，而是权力和威严。", talk_name = "<nick_name>",},
	[104010221] = { talk_id = 104010221, group_id = 1040102, type = 3, next_id = 104010222, talk_content = "我想从王土借用的也是这份力量。如果能让悬天知道王土站在我们这边，我们就会在接下来的较量中多一个砝码。", talk_name = "<nick_name>",},
	[104010222] = { talk_id = 104010222, group_id = 1040102, type = 3, next_id = 104010223, talk_content = "作为王土的一份子，这也是你们彰显地位、夺回权力的最好机会，或许可以借此扳倒悬天也说不定，对吗？", talk_name = "<nick_name>",},
	[104010223] = { talk_id = 104010223, group_id = 1040102, type = 1, next_id = 104010224, talk_content = "为了表示诚意，这次的委托我们可以分文不取，只要王土愿意出力协助，我就马上交出溶液。郭先生认为如何？", talk_name = "<nick_name>",},
	[104010224] = { talk_id = 104010224, group_id = 1040102, type = 1, next_id = 104010225, talk_content = "好啊，一个要求我们帮下属治疗失忆，一个主动放弃报酬拯救上司，你们还真是上下同心啊。", talk_name = "郭中郎",},
	[104010225] = { talk_id = 104010225, group_id = 1040102, type = 1, next_id = 104010226, talk_content = "等等，你说……治疗失忆？", talk_name = "<nick_name>",},
	[104010226] = { talk_id = 104010226, group_id = 1040102, type = 1, next_id = 104010227, talk_content = "是啊，你该不会还不知道吧？离歌接下我们的委托，报酬就是让我们帮青乌你治疗好失忆。", talk_name = "郭中郎",},
	[104010227] = { talk_id = 104010227, group_id = 1040102, type = 1, next_id = 104010228, talk_content = "只可惜我没什么兴趣陪你们两个玩这种你来我往的游戏。我承认，我之前的确说过溶液到手就会有重谢。", talk_name = "郭中郎",},
	[104010228] = { talk_id = 104010228, group_id = 1040102, type = 1, next_id = 104010229, talk_content = "但所谓重谢对于王土来说只是随手的施舍罢了。不管是约币还是武器，又或者是“记忆”，都不过是些小玩意。", talk_name = "郭中郎",},
	[104010229] = { talk_id = 104010229, group_id = 1040102, type = 1, next_id = 104010230, talk_content = "不过要是想让我们为了一个离歌搭上人手、资源和尊严……就凭你们“望月”也配吗？", talk_name = "郭中郎",},
	[104010230] = { talk_id = 104010230, group_id = 1040102, type = 1, next_id = 104010231, talk_content = "我明白了……原来这就是你们王土的“承诺”。", talk_name = "<nick_name>",},
	[104010231] = { talk_id = 104010231, group_id = 1040102, type = 1, next_id = 104010232, talk_content = "承诺是小孩子的东西，大人眼里只有价值。", talk_name = "郭中郎",},
	[104010232] = { talk_id = 104010232, group_id = 1040102, type = 1, next_id = 104010233, talk_content = "不知所谓的废话就到此为止。青乌，你应该知道“望月”的力量和王土相比是什么水准吧？", talk_name = "郭中郎",},
	[104010233] = { talk_id = 104010233, group_id = 1040102, type = 2, options = {{"（站着不动）",104010234,},{"别以为这件事只有你们说了算。",104010234,},}, talk_content = "把溶液交出来吧。", talk_name = "郭中郎",},
	[104010234] = { talk_id = 104010234, group_id = 1040102, type = 3, talk_content = "看来你是不想交了？那就不必多说了，大伙一起上，把东西从青乌手上抢回来！", talk_name = "郭中郎",},
	[104010301] = { talk_id = 104010301, group_id = 1040103, type = 4, next_id = 104010302, aside = "<color=#FFEBA2>郭中郎：</color>行啊青乌，你居然连我的人都敢打……", talk_content = "行啊青乌，你居然连我的人都敢打……", talk_name = "郭中郎",},
	[104010302] = { talk_id = 104010302, group_id = 1040103, type = 4, aside = "<color=#FFEBA2>郭中郎：</color>等着吧，惹急了王土的人，今后绝对有你好受的！", talk_content = "等着吧，惹急了王土的人，今后绝对有你好受的！", talk_name = "郭中郎",},
	[104010501] = { talk_id = 104010501, group_id = 1040105, type = 4, next_id = 104010502, aside = "<color=#FFEBA2><nick_name>：</color>接下委托的原因，居然是帮我治疗失忆……", talk_content = "接下委托的原因，居然是帮我治疗失忆……", talk_name = "<nick_name>",},
	[104010502] = { talk_id = 104010502, group_id = 1040105, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>离歌，为什么要赌上性命做这样的事？", talk_content = "离歌，为什么要赌上性命做这样的事？", talk_name = "<nick_name>",},
	[104020101] = { talk_id = 104020101, group_id = 1040201, type = 4, next_id = 104020102, aside = "<color=#FFEBA2>小五：</color><nick_name>，和王土交涉的结果如何？", call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "<nick_name>，和王土交涉的结果如何？", talk_name = "小五",},
	[104020102] = { talk_id = 104020102, group_id = 1040201, type = 4, next_id = 104020103, aside = "<color=#FFEBA2><nick_name>：</color>不必盼望他们了，王土的人根本不在意离歌的死活。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "不必盼望他们了，王土的人根本不在意离歌的死活。", talk_name = "<nick_name>",},
	[104020103] = { talk_id = 104020103, group_id = 1040201, type = 4, next_id = 104020104, aside = "<color=#FFEBA2>小五：</color>这样吗，也是呢。以他们的姿态很难好心地帮助别人吧。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "这样吗，也是呢。以他们的姿态很难好心地帮助别人吧。", talk_name = "小五",},
	[104020104] = { talk_id = 104020104, group_id = 1040201, type = 4, next_id = 104020105, aside = "<color=#FFEBA2>小五：</color>不用太在意他们的态度，<nick_name>，只靠我们自己也一定能找到离歌。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "不用太在意他们的态度，<nick_name>，只靠我们自己也一定能找到离歌。", talk_name = "小五",},
	[104020105] = { talk_id = 104020105, group_id = 1040201, type = 4, next_id = 104020106, aside = "<color=#FFEBA2><nick_name>：</color>不，我刚刚已经知道了离歌人在哪里。余辜把她带到了地上。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "不，我刚刚已经知道了离歌人在哪里。余辜把她带到了地上。", talk_name = "<nick_name>",},
	[104020106] = { talk_id = 104020106, group_id = 1040201, type = 4, next_id = 104020107, aside = "<color=#FFEBA2>小五：</color>居然去了那么远的地方……看来余辜并不打算太早把离歌还给我们。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "居然去了那么远的地方……看来余辜并不打算太早把离歌还给我们。", talk_name = "小五",},
	[104020107] = { talk_id = 104020107, group_id = 1040201, type = 4, next_id = 104020108, aside = "<color=#FFEBA2><nick_name>：</color>小五，你知道到地上去的方法吗？", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "小五，你知道到地上去的方法吗？", talk_name = "<nick_name>",},
	[104020108] = { talk_id = 104020108, group_id = 1040201, type = 4, next_id = 104020109, aside = "<color=#FFEBA2>小五：</color>嗯，需要到事务局去接受身份审查，还要抽血检验资质，环节相当复杂。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "嗯，需要到事务局去接受身份审查，还要抽血检验资质，环节相当复杂。", talk_name = "小五",},
	[104020109] = { talk_id = 104020109, group_id = 1040201, type = 4, next_id = 104020110, aside = "<color=#FFEBA2>小五：</color>另外还有一个问题，<nick_name>你的证件只有离歌知道放在哪里，现在这样应该没法通过审查呢。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "另外还有一个问题，<nick_name>你的证件只有离歌知道放在哪里，现在这样应该没法通过审查呢。", talk_name = "小五",},
	[104020110] = { talk_id = 104020110, group_id = 1040201, type = 4, next_id = 104020111, aside = "<color=#FFEBA2><nick_name>：</color>我需要知道快速到地上去的方法。小五，能帮我想想办法吗？", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "我需要知道快速到地上去的方法。小五，能帮我想想办法吗？", talk_name = "<nick_name>",},
	[104020111] = { talk_id = 104020111, group_id = 1040201, type = 4, next_id = 104020112, aside = "<color=#FFEBA2>小五：</color>最快速度的方法……对了，<nick_name>，你认识那位维安司长追识对吧？", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "最快速度的方法……对了，<nick_name>，你认识那位维安司长追识对吧？", talk_name = "小五",},
	[104020112] = { talk_id = 104020112, group_id = 1040201, type = 4, next_id = 104020113, aside = "<color=#FFEBA2>小五：</color>如果可以拿到她的引荐，应该可以免去一些身份审核上的周折。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "如果可以拿到她的引荐，应该可以免去一些身份审核上的周折。", talk_name = "小五",},
	[104020113] = { talk_id = 104020113, group_id = 1040201, type = 4, next_id = 104020114, aside = "<color=#FFEBA2>小五：</color>要找那位维安司长大概要到事务局去看看，听说她平时经常在那里活动。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "要找那位维安司长大概要到事务局去看看，听说她平时经常在那里活动。", talk_name = "小五",},
	[104020114] = { talk_id = 104020114, group_id = 1040201, type = 4, next_id = 104020115, aside = "<color=#FFEBA2><nick_name>：</color>谢了，小五，我去去就回。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "谢了，小五，我去去就回。", talk_name = "<nick_name>",},
	[104020115] = { talk_id = 104020115, group_id = 1040201, type = 4, aside = "<color=#FFEBA2>小五：</color>祝你好运，<nick_name>。虽然可能没有什么意义，但我还是要提醒你一句……不要做出冲动的事。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "祝你好运，<nick_name>。虽然可能没有什么意义，但我还是要提醒你一句……不要做出冲动的事。", talk_name = "小五",},
	[104020201] = { talk_id = 104020201, group_id = 1040202, type = 1, next_id = 104020202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "就凭你这种杂碎也敢随便挑衅天月巡卫？我看你是嫌日子过得太舒服是吧！", talk_name = "彭义",},
	[104020202] = { talk_id = 104020202, group_id = 1040202, type = 1, next_id = 104020203, talk_content = "就你们这种巡卫跟我们恶来也差不了多少，不就是仗着自己手里有把枪吗？有本事来啊！", talk_name = "牧江",},
	[104020203] = { talk_id = 104020203, group_id = 1040202, type = 1, next_id = 104020204, talk_content = "（前面好像有两个人在吵架，是巡卫和附近的居民吗？）", talk_name = "<nick_name>",},
	[104020204] = { talk_id = 104020204, group_id = 1040202, type = 1, next_id = 104020205, talk_content = "来就来，今天我就要看看你们这群禁闭者到底有什么能耐！", talk_name = "彭义",},
	[104020205] = { talk_id = 104020205, group_id = 1040202, type = 1, talk_content = "（不好，他们要打起来了。不能放任这两个家伙把这里弄得一团糟。）", talk_name = "<nick_name>",},
	[104020301] = { talk_id = 104020301, group_id = 1040203, type = 4, next_id = 104020302, aside = "<color=#FFEBA2>？？？：</color>不管是谁在那里，请立即停止争斗，否则维安司不介意介入此事。", talk_content = "不管是谁在那里，请立即停止争斗，否则维安司不介意介入此事。", talk_name = "？？？",},
	[104020302] = { talk_id = 104020302, group_id = 1040203, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>这个声音是……", talk_content = "这个声音是……", talk_name = "<nick_name>",},
	[104020401] = { talk_id = 104020401, group_id = 1040204, type = 4, next_id = 104020402, aside = "<color=#FFEBA2>牧江：</color>喂，你这家伙是从哪冒出来的！别拦着我揍他！", talk_content = "喂，你这家伙是从哪冒出来的！别拦着我揍他！", talk_name = "牧江",},
	[104020402] = { talk_id = 104020402, group_id = 1040204, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>虽然是想让你们冷静一下，但看来问题只能用动手解决呢。", talk_content = "虽然是想让你们冷静一下，但看来问题只能用动手解决呢。", talk_name = "<nick_name>",},
	[104030101] = { talk_id = 104030101, group_id = 1040301, type = 1, next_id = 104030102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "别打了别打了，我答应你好好说，好好说行吧？", talk_name = "牧江",},
	[104030102] = { talk_id = 104030102, group_id = 1040301, type = 2, options = {{"又见面了，追识。",104030103,},{"只是不希望他们生事而已。",104030104,},}, talk_content = "知错就好。幸好有脉者及时制止，不然你们……咦？青乌，想不到居然是你。", talk_name = "追识",},
	[104030103] = { talk_id = 104030103, group_id = 1040301, type = 3, next_id = 104030105, talk_content = "你的行事风格还是跟之前一样呢，青乌。这次真是多谢你出手了。", talk_name = "追识",},
	[104030104] = { talk_id = 104030104, group_id = 1040301, type = 3, next_id = 104030105, talk_content = "幸好没有惹出更大的麻烦。这次真是多谢你出手了，青乌。", talk_name = "追识",},
	[104030105] = { talk_id = 104030105, group_id = 1040301, type = 2, options = {{"恶索帮是什么？",104030106,},}, talk_content = "总之又是巡卫和恶索帮之间的矛盾，对吗？最近已经听下属们报告过很多次了。", talk_name = "追识",},
	[104030106] = { talk_id = 104030106, group_id = 1040301, type = 3, next_id = 104030107, talk_content = "是城内的一个街头帮派，成员以流浪者和失业者居多。由于一些原因，他们中的许多人都无法正常出入城市。", talk_name = "追识",},
	[104030107] = { talk_id = 104030107, group_id = 1040301, type = 3, next_id = 104030108, talk_content = "因为有诸多不稳定因素存在，所以他们和巡卫经常起冲突，维安司也一直在寻找调停的方法。", talk_name = "追识",},
	[104030108] = { talk_id = 104030108, group_id = 1040301, type = 1, next_id = 104030109, talk_content = "那么就来说说吧，这次的情况又是怎么回事？", talk_name = "追识",},
	[104030109] = { talk_id = 104030109, group_id = 1040301, type = 1, next_id = 104030110, talk_content = "嘁……刚才我在大街上执行公务，这小子拒不配合，还屡次三番挑衅我。", talk_name = "彭义",},
	[104030110] = { talk_id = 104030110, group_id = 1040301, type = 1, next_id = 104030111, talk_content = "我就是想趁机好好管管这群混混，虽然方式有点鲁莽，但也是一片好心。这种心情您肯定能理解吧，追识司长？", talk_name = "彭义",},
	[104030111] = { talk_id = 104030111, group_id = 1040301, type = 1, next_id = 104030112, talk_content = "追识司长，您别听他的！刚刚他根本不是在执行公务，就是想找个由头挑事，捞点油水！", talk_name = "牧江",},
	[104030112] = { talk_id = 104030112, group_id = 1040301, type = 1, next_id = 104030113, talk_content = "他们这些巡卫每天仗着有权有势在街上横行霸道，我早就看不惯他们了！", talk_name = "牧江",},
	[104030113] = { talk_id = 104030113, group_id = 1040301, type = 1, next_id = 104030114, talk_content = "呸，我们可是封侯大人亲自授任的巡查人员，就凭你也配指责我们？再说刚才也是你动手在先，我说得没错吧？", talk_name = "彭义",},
	[104030114] = { talk_id = 104030114, group_id = 1040301, type = 1, next_id = 104030115, talk_content = "那也是因为你们挑事在先！", talk_name = "牧江",},
	[104030115] = { talk_id = 104030115, group_id = 1040301, type = 1, next_id = 104030116, talk_content = "擅自攻击巡卫在天月城是什么罪行，你这家伙不会不知道吧？", talk_name = "彭义",},
	[104030116] = { talk_id = 104030116, group_id = 1040301, type = 1, next_id = 104030117, talk_content = "我，你……", talk_name = "牧江",},
	[104030117] = { talk_id = 104030117, group_id = 1040301, type = 2, options = {{"找其他人问问吧。",104030118,},{"找找摄像头怎么样？",104030119,},}, talk_content = "都冷静一下，继续听你们这样吵下去恐怕一整天也不会有眉目，还是想想其他办法吧。", talk_name = "追识",},
	[104030118] = { talk_id = 104030118, group_id = 1040301, type = 3, next_id = 104030121, talk_content = "嗯，有路人的证词的确会好一些，但这附近好像没什么全程都在现场的人呢。", talk_name = "追识",},
	[104030119] = { talk_id = 104030119, group_id = 1040301, type = 3, next_id = 104030120, talk_content = "这一点我之前已经注意过了，很不巧，这段路上正好没有摄像头呢。", talk_name = "追识",},
	[104030120] = { talk_id = 104030120, group_id = 1040301, type = 3, next_id = 104030121, talk_content = "那就有点麻烦了，除非有全程在场的路人……", talk_name = "<nick_name>",},
	[104030121] = { talk_id = 104030121, group_id = 1040301, type = 1, next_id = 104030122, talk_content = "对了，说到全程在场……有一种小动物或许比路人更能提供线索。", talk_name = "追识",},
	[104030122] = { talk_id = 104030122, group_id = 1040301, type = 1, next_id = 104030123, talk_content = "你是说……脉灵？", talk_name = "<nick_name>",},
	[104030123] = { talk_id = 104030123, group_id = 1040301, type = 1, talk_content = "没错，事务局附近经常有脉灵活动，只要和脉灵同调，应该就能拿到证据了吧。", talk_name = "追识",},
	[104030201] = { talk_id = 104030201, group_id = 1040302, type = 1, next_id = 104030202, talk_content = "你小子别嚣张，追识司长是我们巡卫这边的人，今天你就等着挨罚吧！", talk_name = "彭义",},
	[104030202] = { talk_id = 104030202, group_id = 1040302, type = 1, next_id = 104030203, talk_content = "我们恶索帮身正不怕影斜，就算你是巡卫我们也不会怕你！", talk_name = "牧江",},
	[104030203] = { talk_id = 104030203, group_id = 1040302, type = 1, talk_content = "（他们还在气头上，还是先不要插话了。）", talk_name = "<nick_name>",},
	[104030301] = { talk_id = 104030301, group_id = 1040303, type = 1, next_id = 104030302, talk_content = "事务局附近的确经常有脉灵活动，我之前就看到过几次。", talk_name = "追识",},
	[104030302] = { talk_id = 104030302, group_id = 1040303, type = 1, talk_content = "麻烦你帮忙找一下吧，青乌，我留在这里看着他们两个。", talk_name = "追识",},
	[104030401] = { talk_id = 104030401, group_id = 1040304, type = 1, next_id = 104030402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "呼……呼……", talk_name = "脉灵杳杳",},
	[104030402] = { talk_id = 104030402, group_id = 1040304, type = 1, next_id = 104030403, talk_content = "你好？", talk_name = "<nick_name>",},
	[104030403] = { talk_id = 104030403, group_id = 1040304, type = 1, next_id = 104030404, talk_content = "呼……呼……", talk_name = "脉灵杳杳",},
	[104030404] = { talk_id = 104030404, group_id = 1040304, type = 1, next_id = 104030405, talk_content = "能听到吗？", talk_name = "<nick_name>",},
	[104030405] = { talk_id = 104030405, group_id = 1040304, type = 2, options = {{"有人要玩捉迷藏吗？",104030408,},{"噬脉生物来了。",104030407,},{"开饭了。",104030406,},}, talk_content = "呼……嘻哈……", talk_name = "脉灵杳杳",},
	[104030406] = { talk_id = 104030406, group_id = 1040304, type = 3, next_id = 104030409, talk_content = "呼……唔？哪里有饭？杳杳要吃最大个的苹果，嘻哈……", talk_name = "脉灵杳杳",},
	[104030407] = { talk_id = 104030407, group_id = 1040304, type = 3, next_id = 104030409, talk_content = "呼……咦？在哪里？噬脉生物在哪里？？杳杳这就去通知大家……唔……", talk_name = "脉灵杳杳",},
	[104030408] = { talk_id = 104030408, group_id = 1040304, type = 3, next_id = 104030409, talk_content = "呼……唔？杳杳要玩！我们什么时候开始玩？", talk_name = "脉灵杳杳",},
	[104030409] = { talk_id = 104030409, group_id = 1040304, type = 1, next_id = 104030410, talk_content = "不好意思，其实没有那种东西……只是有点急事想问问你而已。不过你刚才是在睡觉吗？", talk_name = "<nick_name>",},
	[104030410] = { talk_id = 104030410, group_id = 1040304, type = 2, options = {{"所以才睁着眼睛吗？",104030411,},}, talk_content = "杳杳是在睡觉哦，不过没有全身都在睡觉，只是让身体先睡一下，眼睛负责站岗而已，嘻哈。", talk_name = "脉灵杳杳",},
	[104030411] = { talk_id = 104030411, group_id = 1040304, type = 3, next_id = 104030412, talk_content = "对哦，这可是杳杳的特异功能，这样即使和大家一起玩的时候也可以补觉了呢，嘻哈。", talk_name = "脉灵杳杳",},
	[104030412] = { talk_id = 104030412, group_id = 1040304, type = 1, next_id = 104030413, talk_content = "既然可以睁着眼睛睡觉，不知道你有没有看到那边的两个人刚才争执的样子？", talk_name = "<nick_name>",},
	[104030413] = { talk_id = 104030413, group_id = 1040304, type = 1, next_id = 104030414, talk_content = "嘻哈，杳杳不知道哦，因为身体已经睡着了嘛。", talk_name = "脉灵杳杳",},
	[104030414] = { talk_id = 104030414, group_id = 1040304, type = 1, next_id = 104030415, talk_content = "不过杳杳刚才一直在这里，所以眼睛应该已经记录下所有事情了吧。你要和杳杳同调吗？", talk_name = "脉灵杳杳",},
	[104030415] = { talk_id = 104030415, group_id = 1040304, type = 1, talk_content = "嗯，能同调当然是最好的了，谢谢你。", talk_name = "<nick_name>",},
	[104030501] = { talk_id = 104030501, group_id = 1040305, type = 1, next_id = 104030502, talk_content = "原来站着睡觉也能做大事呢，嘻哈！", talk_name = "脉灵杳杳",},
	[104030502] = { talk_id = 104030502, group_id = 1040305, type = 1, talk_content = "如果没什么其他事的话，杳杳可要接着睡觉了哦。", talk_name = "脉灵杳杳",},
	[104030601] = { talk_id = 104030601, group_id = 1040306, type = 4, next_id = 104030602, aside = "<color=#FFEBA2><nick_name>：</color>（想不到事情会是这样……）", talk_content = "（想不到事情会是这样……）", talk_name = "<nick_name>",},
	[104030602] = { talk_id = 104030602, group_id = 1040306, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（总之先回去找追识他们吧。）", talk_content = "（总之先回去找追识他们吧。）", talk_name = "<nick_name>",},
	[104030701] = { talk_id = 104030701, group_id = 1040307, type = 1, next_id = 104030702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "青乌，辛苦你了。有在脉灵那里得到什么情报吗？", talk_name = "追识",},
	[104030702] = { talk_id = 104030702, group_id = 1040307, type = 1, next_id = 104030703, talk_content = "这位脉者，你都看到事情的经过了吧？那你肯定知道我是无辜的了！", talk_name = "牧江",},
	[104030703] = { talk_id = 104030703, group_id = 1040307, type = 1, next_id = 104030704, talk_content = "想想也知道你这种小毛贼不可能无辜！", talk_name = "彭义",},
	[104030704] = { talk_id = 104030704, group_id = 1040307, type = 2, options = {{"是恶索帮成员先动了手。",104030713,},{"是巡卫胡作非为。",104030705,},}, talk_content = "（刚才的确已经在同调的时候看完了事情的经过，现在应该怎样描述呢？）", talk_name = "<nick_name>",},
	[104030705] = { talk_id = 104030705, group_id = 1040307, type = 3, next_id = 104030706, talk_content = "我都看到了，是这名巡卫乱收保护费在先，而且还出言挑衅恶索帮成员。", talk_name = "<nick_name>",},
	[104030706] = { talk_id = 104030706, group_id = 1040307, type = 3, next_id = 104030707, talk_content = "虽然在这之后是恶索帮成员先动了手，但我认为这起事件还是应该由巡卫承担主要责任。", talk_name = "<nick_name>",},
	[104030707] = { talk_id = 104030707, group_id = 1040307, type = 3, next_id = 104030708, talk_content = "原来是这样，想不到你作为持证上岗的巡卫，居然会这么目无法纪。", talk_name = "追识",},
	[104030708] = { talk_id = 104030708, group_id = 1040307, type = 3, next_id = 104030709, talk_content = "没错，这家伙刚才一口一个“禁闭者”地称呼我，如果不是因为这个，我也不会跟他动手！", talk_name = "牧江",},
	[104030709] = { talk_id = 104030709, group_id = 1040307, type = 3, next_id = 104030710, talk_content = "不对……刚才那些话全都是你的一面之词，你这种说谎话的家伙我见多了，肯定是那群恶来给你什么好处了吧？", talk_name = "彭义",},
	[104030710] = { talk_id = 104030710, group_id = 1040307, type = 3, next_id = 104030711, talk_content = "巡卫从来都是按规矩办事，就凭你一个普通脉者也想挑战我们巡卫组织的权威吗？", talk_name = "彭义",},
	[104030711] = { talk_id = 104030711, group_id = 1040307, type = 3, next_id = 104030712, talk_content = "……难道你认为拥有权力就能随意扭曲事实吗？", talk_name = "<nick_name>",},
	[104030712] = { talk_id = 104030712, group_id = 1040307, type = 3, talk_content = "什么乱七八糟的，不跟你说废话了，今天我们巡卫组织必须教教你怎么做人！", talk_name = "彭义",},
	[104030713] = { talk_id = 104030713, group_id = 1040307, type = 3, next_id = 104030714, talk_content = "我都看到了，虽然两方都有不恰当的行为，但是这名恶索帮成员先动手伤人，所以的确应该由他承担主要责任。", talk_name = "<nick_name>",},
	[104030714] = { talk_id = 104030714, group_id = 1040307, type = 3, next_id = 104030715, talk_content = "看看吧，明眼人都知道是谁做了坏事，你小子还有什么好说的？", talk_name = "彭义",},
	[104030715] = { talk_id = 104030715, group_id = 1040307, type = 3, next_id = 104030716, talk_content = "你……你们……是你们仗势欺人！如果不是他先说了那种话，我也不会……", talk_name = "牧江",},
	[104030716] = { talk_id = 104030716, group_id = 1040307, type = 3, next_id = 104030717, talk_content = "哦，你是在说那个词啊？我也只是实事求是而已，本来你们恶索帮就是永远不可能离开天月城的。", talk_name = "彭义",},
	[104030717] = { talk_id = 104030717, group_id = 1040307, type = 3, talk_content = "我……我绝对不会允许你随意玷污恶索帮的名誉！！", talk_name = "牧江",},
	[104030801] = { talk_id = 104030801, group_id = 1040308, type = 4, next_id = 104030802, aside = "<color=#FFEBA2>彭义：</color>老弟，你也跟我一起上，我们好好教训教训这个脉者！", talk_content = "老弟，你也跟我一起上，我们好好教训教训这个脉者！", talk_name = "彭义",},
	[104030802] = { talk_id = 104030802, group_id = 1040308, type = 4, next_id = 104030803, aside = "<color=#FFEBA2>追识：</color>想不到他们居然要在这里公然伤人……", talk_content = "想不到他们居然要在这里公然伤人……", talk_name = "追识",},
	[104030803] = { talk_id = 104030803, group_id = 1040308, type = 4, aside = "<color=#FFEBA2>追识：</color>天月城的巡卫组织的确应该好好整治一下了。", talk_content = "天月城的巡卫组织的确应该好好整治一下了。", talk_name = "追识",},
	[104030901] = { talk_id = 104030901, group_id = 1040309, type = 4, next_id = 104030902, aside = "<color=#FFEBA2>牧江：</color>四哥，我们两个一起上，今天必须把这个巡卫打趴下！", talk_content = "四哥，我们两个一起上，今天必须把这个巡卫打趴下！", talk_name = "牧江",},
	[104030902] = { talk_id = 104030902, group_id = 1040309, type = 4, next_id = 104030903, aside = "<color=#FFEBA2>追识：</color>事态有点超出控制了，青乌。", talk_content = "事态有点超出控制了，青乌。", talk_name = "追识",},
	[104030903] = { talk_id = 104030903, group_id = 1040309, type = 4, aside = "<color=#FFEBA2>追识：</color>还是先想办法让他们冷静下来吧。", talk_content = "还是先想办法让他们冷静下来吧。", talk_name = "追识",},
	[104031001] = { talk_id = 104031001, group_id = 1040310, type = 1, next_id = 104031002, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "这、这就是脉者的实力吗？几下子就把那家伙和他的同伴全打倒了……", talk_name = "牧江",},
	[104031002] = { talk_id = 104031002, group_id = 1040310, type = 1, next_id = 104031003, talk_content = "这位脉者，你的名字是叫青乌对吧？刚才真是多谢你了！", talk_name = "牧江",},
	[104031003] = { talk_id = 104031003, group_id = 1040310, type = 2, options = {{"只是说了该说的话而已。",104031004,},{"小事小事。",104031004,},}, talk_content = "要不是你帮我说话，可能我现在已经被巡卫带走了。多亏有你和追识司长在，你们真是帮了大忙了！", talk_name = "牧江",},
	[104031004] = { talk_id = 104031004, group_id = 1040310, type = 3, next_id = 104031005, talk_content = "嗯，我随后就去安排人手追查那名巡卫的去向，有维安司的成员在，绝对不可能让他就这么跑掉。", talk_name = "追识",},
	[104031005] = { talk_id = 104031005, group_id = 1040310, type = 3, next_id = 104031006, talk_content = "但即便如此，治安混乱也是由你们两个共同造成的，所以请你现在就到事务局做一下登记吧。", talk_name = "追识",},
	[104031006] = { talk_id = 104031006, group_id = 1040310, type = 3, talk_content = "啊……？好、好吧，追识司长还真是深明大义啊……", talk_name = "牧江",},
	[104031101] = { talk_id = 104031101, group_id = 1040311, type = 1, next_id = 104031102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "都给我冷静一下，如果你们继续在我面前寻衅滋事，等待你们的会是比之前严重几倍的处罚。", talk_name = "追识",},
	[104031102] = { talk_id = 104031102, group_id = 1040311, type = 1, next_id = 104031103, talk_content = "我当然可以冷静，前提是先让这个家伙受到惩罚！", talk_name = "牧江",},
	[104031103] = { talk_id = 104031103, group_id = 1040311, type = 1, next_id = 104031104, talk_content = "我们两个谁受惩罚还不一定呢，您说是吧司长？", talk_name = "彭义",},
	[104031104] = { talk_id = 104031104, group_id = 1040311, type = 1, next_id = 104031105, talk_content = "脉灵的同调情况我已经作为证据记录下来了，谁错谁对我会交给公务人员去处理。", talk_name = "追识",},
	[104031105] = { talk_id = 104031105, group_id = 1040311, type = 1, next_id = 104031106, talk_content = "现在，请你们二位马上前往事务局做登记，等待之后的处罚措施。", talk_name = "追识",},
	[104031106] = { talk_id = 104031106, group_id = 1040311, type = 1, next_id = 104031107, talk_content = "寻衅滋事罪在帝江律法中最高可以判处八年有期徒刑，这句话还需要我说第二遍吗？", talk_name = "追识",},
	[104031107] = { talk_id = 104031107, group_id = 1040311, type = 1, next_id = 104031108, talk_content = "不、不需要了……追识司长……", talk_name = "牧江",},
	[104031108] = { talk_id = 104031108, group_id = 1040311, type = 1, next_id = 104031109, talk_content = "算了算了，您消消气，我们这就去……", talk_name = "彭义",},
	[104031109] = { talk_id = 104031109, group_id = 1040311, type = 1, talk_content = "（追识生起气来很可怕……看来来来说的果然没错。）", talk_name = "<nick_name>",},
	[104031201] = { talk_id = 104031201, group_id = 1040312, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（不管怎样事情算是结束了，接下来找追识谈谈引荐的事吧。）", talk_content = "（不管怎样事情算是结束了，接下来找追识谈谈引荐的事吧。）", talk_name = "<nick_name>",},
	[104031301] = { talk_id = 104031301, group_id = 1040313, type = 1, next_id = 104031302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "事情终于圆满解决了，多谢你的帮忙，青乌。", talk_name = "追识",},
	[104031302] = { talk_id = 104031302, group_id = 1040313, type = 1, next_id = 104031303, talk_content = "现在已经没有其他人在了，应该可以和我说说你的事情了吧，青乌？", talk_name = "追识",},
	[104031303] = { talk_id = 104031303, group_id = 1040313, type = 1, next_id = 104031304, talk_content = "我的事情……你都知道了吗，追识？", talk_name = "<nick_name>",},
	[104031304] = { talk_id = 104031304, group_id = 1040313, type = 2, child_type = 21, options = {{"那他应该没把事情的后续告诉你吧？",104031305,},{"你也和王土站在一边吗？",104031310,},}, talk_content = "嗯，再怎么说维安司也是这次委托的发起方之一，郭先生已经和我说了你们上次谈判的事情。", talk_name = "追识",},
	[104031305] = { talk_id = 104031305, group_id = 1040313, type = 3, next_id = 104031306, icon = "interaction_4", talk_content = "后续？郭先生只说你们并没有在酬劳上谈拢，后来还出了什么其他事吗？", talk_name = "追识",},
	[104031306] = { talk_id = 104031306, group_id = 1040313, type = 3, next_id = 104031307, talk_content = "嗯，的确出了事。郭中郎叫了几个手下想要从我这里抢走溶液，但被我打趴下了。", talk_name = "<nick_name>",},
	[104031307] = { talk_id = 104031307, group_id = 1040313, type = 3, next_id = 104031308, talk_content = "原来还有这样的事……虽然郭先生的一些见解我并不赞同，但他的权柄的确不容小觑，以后还是小心些比较好。", talk_name = "追识",},
	[104031308] = { talk_id = 104031308, group_id = 1040313, type = 3, next_id = 104031309, talk_content = "我明白，追识，所以你应该不是和他站在一边的对吗？", talk_name = "<nick_name>",},
	[104031309] = { talk_id = 104031309, group_id = 1040313, type = 3, next_id = 104031312, talk_content = "嗯，我一开始的确希望和王土达成合作，但如果王土执意要出下策，我们维安司也不可能助纣为虐。", talk_name = "追识",},
	[104031310] = { talk_id = 104031310, group_id = 1040313, type = 3, next_id = 104031311, icon = "interaction_4", talk_content = "维安司只会站在公理这边，青乌。我们和王土的确是合作伙伴，但这不代表我们会无条件支持王土的一切行动。", talk_name = "追识",},
	[104031311] = { talk_id = 104031311, group_id = 1040313, type = 3, next_id = 104031312, talk_content = "我对郭先生的想法也有自己的判断。如果他真的另有图谋，维安司当然不可能助纣为虐。", talk_name = "追识",},
	[104031312] = { talk_id = 104031312, group_id = 1040313, type = 1, next_id = 104031313, talk_content = "另外，对于离歌小姐的事我也很遗憾。想不到悬天会无所顾忌到这种程度，要是我们早些防范……", talk_name = "追识",},
	[104031313] = { talk_id = 104031313, group_id = 1040313, type = 1, next_id = 104031314, talk_content = "事情已经发生了，现在想要只靠我们扳倒悬天也不现实，但我的确有些事情需要你帮忙，追识。", talk_name = "<nick_name>",},
	[104031314] = { talk_id = 104031314, group_id = 1040313, type = 2, options = {{"（和追识说明引荐信的事）",104031315,},}, talk_content = "那不妨说说看吧，只要是我帮得上的，我一定会尽力。", talk_name = "追识",},
	[104031315] = { talk_id = 104031315, group_id = 1040313, type = 3, next_id = 104031316, talk_content = "嗯……写引荐信的确是我力所能及的事，如果你的情况只是缺少证件，我会详细和事务局说明。", talk_name = "追识",},
	[104031316] = { talk_id = 104031316, group_id = 1040313, type = 3, next_id = 104031317, talk_content = "至于你自己只要记得去事务局抽血化验就好，之后的结果会由公务人员通知给你。", talk_name = "追识",},
	[104031317] = { talk_id = 104031317, group_id = 1040313, type = 3, next_id = 104031318, talk_content = "我很抱歉，青乌，离歌小姐的失踪和我们的委托有关，但我们的活动受到诸多限制，可能无法做到太多。", talk_name = "追识",},
	[104031318] = { talk_id = 104031318, group_id = 1040313, type = 3, next_id = 104031319, talk_content = "作为补偿，我只能尽快写好引荐信，然后亲自去推进办理流程，尽量让印鉴快些送到你手上。", talk_name = "追识",},
	[104031319] = { talk_id = 104031319, group_id = 1040313, type = 3, next_id = 104031320, talk_content = "谢谢你，追识，这样的帮助对我们已经足够了。", talk_name = "<nick_name>",},
	[104031320] = { talk_id = 104031320, group_id = 1040313, type = 3, next_id = 104031321, talk_content = "另外……虽然由我来说这些可能不太合适，但我和那位名叫离歌的小姐有过几面之缘，她是个不普通的人。", talk_name = "追识",},
	[104031321] = { talk_id = 104031321, group_id = 1040313, type = 3, next_id = 104031322, talk_content = "如果她真的受到控制，想必她现在也一定在想办法尽快脱身，回到你们身边。", talk_name = "追识",},
	[104031322] = { talk_id = 104031322, group_id = 1040313, type = 3, next_id = 104031323, talk_content = "不要太沮丧，青乌，既然离歌小姐对“望月”很重要，你们就更应该相信她才是。", talk_name = "追识",},
	[104031323] = { talk_id = 104031323, group_id = 1040313, type = 3, next_id = 104031324, talk_content = "“相信”吗……我知道了。", talk_name = "<nick_name>",},
	[104031324] = { talk_id = 104031324, group_id = 1040313, type = 3, talk_content = "就算那家伙不是什么好人，我也不会任由她这样平白无故地消失……至少这次绝对不会。", talk_name = "<nick_name>",},
	[104031401] = { talk_id = 104031401, group_id = 1040314, type = 1, next_id = 104031402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "抱歉，刚才的态度有些强硬。如果不是不得已，我还是愿意和平解决一切。", talk_name = "追识",},
	[104031402] = { talk_id = 104031402, group_id = 1040314, type = 1, next_id = 104031403, talk_content = "虽然过程有一点波折，但事情终于算是解决了。多谢你的帮忙，青乌。", talk_name = "追识",},
	[104031403] = { talk_id = 104031403, group_id = 1040314, type = 1, next_id = 104031404, talk_content = "现在已经没有其他人在了，应该可以和我说说你的事情了吧，青乌？", talk_name = "追识",},
	[104031404] = { talk_id = 104031404, group_id = 1040314, type = 1, next_id = 104031405, talk_content = "我的事情……你都知道了吗，追识？", talk_name = "<nick_name>",},
