	[104031413] = { talk_id = 104031413, group_id = 1040314, type = 1, next_id = 104031414, talk_content = "另外，对于离歌小姐的事我也很遗憾。想不到悬天会无所顾忌到这种程度，要是我们早些防范……", talk_name = "追识",},
	[104031414] = { talk_id = 104031414, group_id = 1040314, type = 1, next_id = 104031415, talk_content = "事情已经发生了，现在想要只靠我们扳倒悬天也不现实，但我的确有些事情需要你帮忙，追识。", talk_name = "<nick_name>",},
	[104031415] = { talk_id = 104031415, group_id = 1040314, type = 2, options = {{"（和追识说明引荐信的事）",104031416,},}, talk_content = "那不妨说说看吧，只要是我帮得上的，我一定会尽力。", talk_name = "追识",},
	[104031416] = { talk_id = 104031416, group_id = 1040314, type = 3, next_id = 104031417, talk_content = "嗯……写引荐信的确是我力所能及的事，如果你的情况只是缺少证件，我会详细和事务局说明。", talk_name = "追识",},
	[104031417] = { talk_id = 104031417, group_id = 1040314, type = 3, next_id = 104031418, talk_content = "至于你自己只要记得去事务局抽血化验就好，之后的结果会由公务人员通知给你。", talk_name = "追识",},
	[104031418] = { talk_id = 104031418, group_id = 1040314, type = 3, next_id = 104031419, talk_content = "我很抱歉，青乌，离歌小姐的失踪和我们的委托有关，但我们的活动受到诸多限制，可能无法做到太多。", talk_name = "追识",},
	[104031419] = { talk_id = 104031419, group_id = 1040314, type = 3, next_id = 104031420, talk_content = "作为补偿，我只能尽快写好引荐信，然后亲自去推进办理流程，尽量让印鉴快些送到你手上。", talk_name = "追识",},
	[104031420] = { talk_id = 104031420, group_id = 1040314, type = 3, next_id = 104031421, talk_content = "谢谢你，追识，这样的帮助对我们已经足够了。", talk_name = "<nick_name>",},
	[104031421] = { talk_id = 104031421, group_id = 1040314, type = 3, next_id = 104031422, talk_content = "另外……虽然由我来说这些可能不太合适，但我和那位名叫离歌的小姐有过几面之缘，她是个不普通的人。", talk_name = "追识",},
	[104031422] = { talk_id = 104031422, group_id = 1040314, type = 3, next_id = 104031423, talk_content = "如果她真的受到控制，想必她现在也一定在想办法尽快脱身，回到你们身边。", talk_name = "追识",},
	[104031423] = { talk_id = 104031423, group_id = 1040314, type = 3, next_id = 104031424, talk_content = "不要太沮丧，青乌，既然离歌小姐对“望月”很重要，你们就更应该相信她才是。", talk_name = "追识",},
	[104031424] = { talk_id = 104031424, group_id = 1040314, type = 3, next_id = 104031425, talk_content = "“相信”吗……我知道了。", talk_name = "<nick_name>",},
	[104031425] = { talk_id = 104031425, group_id = 1040314, type = 3, talk_content = "就算那家伙不是什么好人，我也不会任由她这样平白无故地消失……至少这次绝对不会。", talk_name = "<nick_name>",},
	[104031501] = { talk_id = 104031501, group_id = 1040315, type = 1, next_id = 104031502, talk_content = "接下来只要去事务局抽血化验就可以，之后的结果会由工作人员通知给你。", talk_name = "追识",},
	[104031502] = { talk_id = 104031502, group_id = 1040315, type = 1, talk_content = "地上的情况比天月城更复杂，青乌，一定要万事小心。", talk_name = "追识",},
	[104031601] = { talk_id = 104031601, group_id = 1040316, type = 4, next_id = 104031602, aside = "<color=#FFEBA2><nick_name>：</color>（找到了，那里果然有只脉灵。）", talk_content = "（找到了，那里果然有只脉灵。）", talk_name = "<nick_name>",},
	[104031602] = { talk_id = 104031602, group_id = 1040316, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（要是能和它同调，应该就能看到这里之前发生的事了吧。）", talk_content = "（要是能和它同调，应该就能看到这里之前发生的事了吧。）", talk_name = "<nick_name>",},
	[104031701] = { talk_id = 104031701, group_id = 1040317, type = 5, next_id = 104031702, aside = "<color=#FFEBA2>彭义：</color>刚才发给你们的保护费通知收到没有？这都给你们宽限几天了？", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "刚才发给你们的保护费通知收到没有？这都给你们宽限几天了？", talk_name = "彭义",},
	[104031702] = { talk_id = 104031702, group_id = 1040317, type = 5, next_id = 104031703, aside = "<color=#FFEBA2>牧江：</color>喂，你又是什么人啊？我们在这街坊住得好好的，凭什么给你们交保护费？", talk_content = "喂，你又是什么人啊？我们在这街坊住得好好的，凭什么给你们交保护费？", talk_name = "牧江",},
	[104031703] = { talk_id = 104031703, group_id = 1040317, type = 5, next_id = 104031704, aside = "<color=#FFEBA2>彭义：</color>嘁，新来的吧？按时交保护费是“禁闭者”必须承担的义务，没听说过吗？", talk_content = "嘁，新来的吧？按时交保护费是“禁闭者”必须承担的义务，没听说过吗？", talk_name = "彭义",},
	[104031704] = { talk_id = 104031704, group_id = 1040317, type = 5, next_id = 104031705, aside = "<color=#FFEBA2>牧江：</color>什么？你……你再说一遍那个词试试？", talk_content = "什么？你……你再说一遍那个词试试？", talk_name = "牧江",},
	[104031705] = { talk_id = 104031705, group_id = 1040317, type = 5, next_id = 104031706, aside = "<color=#FFEBA2>彭义：</color>我说“禁闭者”，说的就是你们这些一辈子出不了天月城的家伙，你能拿我怎么样？", talk_content = "我说“禁闭者”，说的就是你们这些一辈子出不了天月城的家伙，你能拿我怎么样？", talk_name = "彭义",},
	[104031706] = { talk_id = 104031706, group_id = 1040317, type = 5, next_id = 104031707, aside = "<color=#FFEBA2>牧江：</color>你这家伙……！", talk_content = "你这家伙……！", talk_name = "牧江",},
	[104031707] = { talk_id = 104031707, group_id = 1040317, type = 5, aside = "<color=#FFEBA2>彭义：</color>你小子居然敢动手？！", talk_content = "你小子居然敢动手？！", talk_name = "彭义",},
	[104031801] = { talk_id = 104031801, group_id = 1040318, type = 4, aside = "<color=#FFEBA2>彭义：</color>停、停一下！这家伙太强，我们先撤！君子报仇十年不晚！", talk_content = "停、停一下！这家伙太强，我们先撤！君子报仇十年不晚！", talk_name = "彭义",},
	[104031901] = { talk_id = 104031901, group_id = 1040319, type = 4, aside = "<color=#FFEBA2>四哥：</color>老弟，这个拉架的脉者太厉害了，我……我先撤了！", talk_content = "老弟，这个拉架的脉者太厉害了，我……我先撤了！", talk_name = "四哥",},
	[104040101] = { talk_id = 104040101, group_id = 1040401, type = 1, next_id = 104040102, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，我想办理一份加急的通行印鉴。办理人名叫<nick_name>，引荐人是维安司的追识小姐，请问可以吗？", talk_name = "<nick_name>",},
	[104040102] = { talk_id = 104040102, group_id = 1040401, type = 1, next_id = 104040103, talk_content = "姓名是<nick_name>对吗？刚才追识小姐已经为您进行了预约。", talk_name = "公务人员",},
	[104040103] = { talk_id = 104040103, group_id = 1040401, type = 1, next_id = 104040104, talk_content = "证件遗失的事我们已经了解了。在收到追识小姐的引荐信后，我们就会尽快核实您的身份，这一点请不用担心。", talk_name = "公务人员",},
	[104040104] = { talk_id = 104040104, group_id = 1040401, type = 2, options = {{"（犹豫）",104040106,},{"（伸）",104040105,},}, talk_content = "那么接下来请伸出手臂，我们马上进行抽血化验。", talk_name = "公务人员",},
	[104040105] = { talk_id = 104040105, group_id = 1040401, type = 3, next_id = 104040111, talk_content = "（手臂上传来轻微的刺痛。）",},
	[104040106] = { talk_id = 104040106, group_id = 1040401, type = 3, next_id = 104040107, talk_content = "……", talk_name = "公务人员",},
	[104040107] = { talk_id = 104040107, group_id = 1040401, type = 3, next_id = 104040108, talk_content = "……", talk_name = "<nick_name>",},
	[104040108] = { talk_id = 104040108, group_id = 1040401, type = 3, next_id = 104040109, talk_content = "请不必紧张，抽血只是天月城审查的常见流程而已。还是说……您是怕痛吗？", talk_name = "公务人员",},
	[104040109] = { talk_id = 104040109, group_id = 1040401, type = 3, next_id = 104040110, talk_content = "……当然没有，来吧。", talk_name = "<nick_name>",},
	[104040110] = { talk_id = 104040110, group_id = 1040401, type = 3, next_id = 104040111, talk_content = "（感受到了刻骨铭心的疼痛。）",},
	[104040111] = { talk_id = 104040111, group_id = 1040401, type = 1, talk_content = "可以了，感谢您的配合。印鉴办理结果出来后我们会第一时间通知您。", talk_name = "公务人员",},
	[104040201] = { talk_id = 104040201, group_id = 1040402, type = 2, options = {{"没事。",},{"关于追识……",104040204,},{"这里是什么地方？",104040202,},}, talk_content = "印鉴办理结果还需要一些时间才能拿到，请问您还有什么事吗？", talk_name = "公务人员",},
	[104040202] = { talk_id = 104040202, group_id = 1040402, type = 3, next_id = 104040203, talk_content = "这里是城内通用居民事务的办理机构，平时负责居民安全、福利保障、住房、医疗、通行等资质的申请和办理。", talk_name = "公务人员",},
	[104040203] = { talk_id = 104040203, group_id = 1040402, type = 3, talk_content = "说到资质申请，我建议您之后尽快补办一下身份证件。在证件缺失的情况下很多生活琐事都会受到阻碍呢。", talk_name = "公务人员",},
	[104040204] = { talk_id = 104040204, group_id = 1040402, type = 3, next_id = 104040205, talk_content = "追识小姐因为维安司的公务原因经常出入事务局，我们的日常工作也受了她不少关照。", talk_name = "公务人员",},
	[104040205] = { talk_id = 104040205, group_id = 1040402, type = 3, talk_content = "而且追识小姐个性认真严谨，能够得到她的亲自引荐，想必您也应该是位正直可靠的脉者吧。", talk_name = "公务人员",},
	[104040301] = { talk_id = 104040301, group_id = 1040403, type = 5, next_id = 104040302, aside = "<color=#FFEBA2>？？？：</color>这位脉者，麻烦留步一下。", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "这位脉者，麻烦留步一下。", talk_name = "？？？",},
	[104040302] = { talk_id = 104040302, group_id = 1040403, type = 5, next_id = 104040303, aside = "<color=#FFEBA2>？？？：</color>通行印鉴的办理程序是很复杂的，抽血化验之后要到我这里来接受审核，前台的那位小姐没有告诉你吗？", talk_content = "通行印鉴的办理程序是很复杂的，抽血化验之后要到我这里来接受审核，前台的那位小姐没有告诉你吗？", talk_name = "？？？",},
	[104040303] = { talk_id = 104040303, group_id = 1040403, type = 5, next_id = 104040304, aside = "<color=#FFEBA2><nick_name>：</color>等等，你是……？", talk_content = "等等，你是……？", talk_name = "<nick_name>",},
	[104040304] = { talk_id = 104040304, group_id = 1040403, type = 5, aside = "<color=#FFEBA2>隐：</color>自我介绍一下，我是天月事务局的特别检察官，你可以叫我隐。", talk_content = "自我介绍一下，我是天月事务局的特别检察官，你可以叫我隐。", talk_name = "隐",},
	[104040401] = { talk_id = 104040401, group_id = 1040404, type = 1, next_id = 104040402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你的情况我已经大致了解了。你的名字叫<nick_name>，名号青乌，想要办一份加急通行印鉴，没错吧？", talk_name = "隐",},
	[104040402] = { talk_id = 104040402, group_id = 1040404, type = 1, next_id = 104040403, talk_content = "的确是这样没错，但我已经申请了个人引荐，难道还有什么问题吗？", talk_name = "<nick_name>",},
	[104040403] = { talk_id = 104040403, group_id = 1040404, type = 1, next_id = 104040404, talk_content = "看来我拿到的资料没有错误，你的确是刚来到这里不久，不了解天月城的法规也算正常。", talk_name = "隐",},
	[104040404] = { talk_id = 104040404, group_id = 1040404, type = 1, next_id = 104040405, talk_content = "在天月城，一切资质证明都是需要经过严格的审核的，尤其是你现在要办理的通行印鉴。", talk_name = "隐",},
	[104040405] = { talk_id = 104040405, group_id = 1040404, type = 2, child_type = 21, options = {{"我这里有人可以帮忙督促。",104040406,},{"听说引荐信可以代替证件审查。",104040410,},{"那该怎么办？",104040414,},}, talk_content = "如果连身份证件这么重要的东西都拿不出，印鉴办理就会十分困难，要做到加急几乎是不可能的，这点你明白吗？", talk_name = "隐",},
	[104040406] = { talk_id = 104040406, group_id = 1040404, type = 3, next_id = 104040407, icon = "interaction_4", talk_content = "那么方便告诉我你请了什么样的人物帮忙吗？", talk_name = "隐",},
	[104040407] = { talk_id = 104040407, group_id = 1040404, type = 3, next_id = 104040408, talk_content = "维安司司长追识，如果你长期在事务局工作的话应该也认识。", talk_name = "<nick_name>",},
	[104040408] = { talk_id = 104040408, group_id = 1040404, type = 3, next_id = 104040409, talk_content = "是追识……小姐吗？那位的确是在事务局里说得上话的人物，但即使是大人物也需要按流程办事。", talk_name = "隐",},
	[104040409] = { talk_id = 104040409, group_id = 1040404, type = 3, next_id = 104040414, talk_content = "以追识小姐秉公执法的态度，印鉴办理完毕大概怎么也需要等一个月以上的时间吧。", talk_name = "隐",},
	[104040410] = { talk_id = 104040410, group_id = 1040404, type = 3, next_id = 104040411, icon = "interaction_4", talk_content = "引荐信只是证明你的身份的手段之一，要想进一步证明还需要接受一些后续审查才行。", talk_name = "隐",},
	[104040411] = { talk_id = 104040411, group_id = 1040404, type = 3, next_id = 104040412, talk_content = "都有什么样的审查？", talk_name = "<nick_name>",},
	[104040412] = { talk_id = 104040412, group_id = 1040404, type = 3, next_id = 104040413, talk_content = "通常的审查步骤包括家庭情况调查、社会关系调查、工资情况调查和一系列的身体检查，另外还有……", talk_name = "隐",},
	[104040413] = { talk_id = 104040413, group_id = 1040404, type = 3, next_id = 104040414, talk_content = "……只是出趟远门而已，你们天月城是要把人扒掉一层皮吗？", talk_name = "<nick_name>",},
	[104040414] = { talk_id = 104040414, group_id = 1040404, type = 1, next_id = 104040415, icon = "interaction_4", talk_content = "所以该怎么办，真的没有快速办好印鉴的方法了吗？", talk_name = "<nick_name>",},
	[104040415] = { talk_id = 104040415, group_id = 1040404, type = 1, next_id = 104040416, talk_content = "让我想想……对了，要办加急印鉴就需要证明你的身份值得认可，而证明的方式就是为天月城做出更多贡献。", talk_name = "隐",},
	[104040416] = { talk_id = 104040416, group_id = 1040404, type = 2, options = {{"证明价值？",104040417,},}, talk_content = "在我们天月城自古有能者多得的惯例。如果你能证明自己的价值，就可以早一些拿到印鉴呢。", talk_name = "隐",},
	[104040417] = { talk_id = 104040417, group_id = 1040404, type = 3, next_id = 104040418, talk_content = "没错，清除噬脉灾害、维护城市安全，这些都是证明自己价值的方式。", talk_name = "隐",},
	[104040418] = { talk_id = 104040418, group_id = 1040404, type = 1, next_id = 104040419, talk_content = "我看你好像有些摸不着头脑。这样吧，我这里恰好有一件紧急的事件需要处理。", talk_name = "隐",},
	[104040419] = { talk_id = 104040419, group_id = 1040404, type = 1, next_id = 104040420, talk_content = "不知你有没有听说过一个名为青门的宗门？那个宗门平时都会负责处理城市中的噬脉灾害。", talk_name = "隐",},
	[104040420] = { talk_id = 104040420, group_id = 1040404, type = 1, next_id = 104040421, talk_content = "不过最近灾害发生得越来越频繁，即使是他们也会有些力不从心。", talk_name = "隐",},
	[104040421] = { talk_id = 104040421, group_id = 1040404, type = 2, options = {{"能不能先拿印鉴……",104040422,},{"成交。",104040423,},}, talk_content = "如果你能帮助他们清除灾害，我就认可你的个人价值，帮你尽快办理印鉴，这样的条件你意下如何？", talk_name = "隐",},
	[104040422] = { talk_id = 104040422, group_id = 1040404, type = 3, next_id = 104040423, talk_content = "你是想把特别检察官的考核条件当做儿戏吗，青乌？", talk_name = "隐",},
	[104040423] = { talk_id = 104040423, group_id = 1040404, type = 1, next_id = 104040424, talk_content = "发生噬脉灾害的地点我已经帮你标记好了，到了那里就直接动手解决吧，我会让青门的联络人帮你做好记录的。", talk_name = "隐",},
	[104040424] = { talk_id = 104040424, group_id = 1040404, type = 1, talk_content = "那就请好好加油了，青乌。", talk_name = "隐",},
	[104040501] = { talk_id = 104040501, group_id = 1040405, type = 2, options = {{"没有问题。",},{"完成以后要怎么办？",104040506,},{"青门是什么样的地方？",104040502,},}, talk_content = "关于这次的考核你还有什么问题吗，青乌？", talk_name = "隐",},
	[104040502] = { talk_id = 104040502, group_id = 1040405, type = 3, next_id = 104040503, talk_content = "青门是帝江最大的宗门，平日里主要负责和清除噬脉生物有关的术法和仪式，那里的门徒大多也以此为生。", talk_name = "隐",},
	[104040503] = { talk_id = 104040503, group_id = 1040405, type = 3, next_id = 104040504, talk_content = "但最近噬脉灾害盛行，即使是青门也有人手不足的时候。", talk_name = "隐",},
	[104040504] = { talk_id = 104040504, group_id = 1040405, type = 3, next_id = 104040505, talk_content = "因此他们委托了我，希望我借资质审核的机会派有能的脉者处理此事。", talk_name = "隐",},
	[104040505] = { talk_id = 104040505, group_id = 1040405, type = 3, talk_content = "我很信任你的能力呢，青乌。", talk_name = "隐",},
	[104040506] = { talk_id = 104040506, group_id = 1040405, type = 3, next_id = 104040507, talk_content = "完成后就请到事务局回报吧，我们会根据你的完成情况决定是否为你增加其他审查流程。", talk_name = "隐",},
	[104040507] = { talk_id = 104040507, group_id = 1040405, type = 3, talk_content = "所以请好好努力吧，清除的怪物越多就越有可能马上拿到印鉴了。", talk_name = "隐",},
	[104040601] = { talk_id = 104040601, group_id = 1040406, type = 1, next_id = 104040602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你好，请问你是青门的人吗？我叫<nick_name>，来这里负责清理噬脉灾害的。", talk_name = "<nick_name>",},
	[104040602] = { talk_id = 104040602, group_id = 1040406, type = 2, options = {{"找错地方了吗？",104040604,},{"那我走了。",104040603,},}, talk_content = "你好，我是青门的门徒青门芸，不过我们好像没有委托过脉者清理噬脉灾害才对……", talk_name = "青门芸",},
	[104040603] = { talk_id = 104040603, group_id = 1040406, type = 3, next_id = 104040605, talk_content = "不，等等，先别走！你真的能帮我们清理噬脉灾害吗？", talk_name = "青门芸",},
	[104040604] = { talk_id = 104040604, group_id = 1040406, type = 3, next_id = 104040605, talk_content = "等等！虽然不知道你要找什么地方，但你真的能帮我们清理噬脉灾害吗？", talk_name = "青门芸",},
	[104040605] = { talk_id = 104040605, group_id = 1040406, type = 1, next_id = 104040606, talk_content = "最近噬脉生物闹得很凶，青门为每一位门徒都分配了负责的区域。", talk_name = "青门芸",},
	[104040606] = { talk_id = 104040606, group_id = 1040406, type = 1, next_id = 104040607, talk_content = "但我才刚入宗门没几天，术法也懂得不多，完全没能力解决那些怪物……", talk_name = "青门芸",},
	[104040607] = { talk_id = 104040607, group_id = 1040406, type = 1, next_id = 104040608, talk_content = "可以的话请你一定帮帮我，不然我都不知道该怎么向师兄师姐交差了……", talk_name = "青门芸",},
	[104040608] = { talk_id = 104040608, group_id = 1040406, type = 1, next_id = 104040609, talk_content = "如果是普通噬脉生物的话应该不成问题，不过我大概需要你帮忙记录一下清理情况……", talk_name = "<nick_name>",},
	[104040609] = { talk_id = 104040609, group_id = 1040406, type = 1, next_id = 104040610, talk_content = "没问题，不管让我帮忙记录什么都可以，只要你能清除噬脉生物就是帮我的大忙了！", talk_name = "青门芸",},
	[104040610] = { talk_id = 104040610, group_id = 1040406, type = 1, talk_content = "我负责的区域就在那边，那就全部拜托给你了，<nick_name>！", talk_name = "青门芸",},
	[104040701] = { talk_id = 104040701, group_id = 1040407, type = 1, next_id = 104040702, talk_content = "太好了，从天而降的吉星居然真的来了，我就知道主祝大人的话肯定没错！", talk_name = "青门芸",},
	[104040702] = { talk_id = 104040702, group_id = 1040407, type = 1, talk_content = "……", talk_name = "<nick_name>",},
	[104040801] = { talk_id = 104040801, group_id = 1040408, type = 4, next_id = 104040802, aside = "<color=#FFEBA2><nick_name>：</color>（青门芸所说的区域应该就是这里吧。这些藤蔓……也是气脉破坏的产物吗？）", talk_content = "（青门芸所说的区域应该就是这里吧。这些藤蔓……也是气脉破坏的产物吗？）", talk_name = "<nick_name>",},
	[104040802] = { talk_id = 104040802, group_id = 1040408, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（总之先清理噬脉生物吧。）", talk_content = "（总之先清理噬脉生物吧。）", talk_name = "<nick_name>",},
	[104040901] = { talk_id = 104040901, group_id = 1040409, type = 4, next_id = 104040902, aside = "<color=#FFEBA2><nick_name>：</color>（虽然不清楚这些藤蔓是从哪里来的，但总归不会是好东西。）", talk_content = "（虽然不清楚这些藤蔓是从哪里来的，但总归不会是好东西。）", talk_name = "<nick_name>",},
	[104040902] = { talk_id = 104040902, group_id = 1040409, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（一把火烧干净吧。）", talk_content = "（一把火烧干净吧。）", talk_name = "<nick_name>",},
	[104041001] = { talk_id = 104041001, group_id = 1040410, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（这样应该算是清理干净了，回去找青门芸吧。）", talk_content = "（这样应该算是清理干净了，回去找青门芸吧。）", talk_name = "<nick_name>",},
	[104041101] = { talk_id = 104041101, group_id = 1040411, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"包你满意。",104041102,},{"都搞定了。",104041102,},}, talk_content = "<nick_name>你来了！那边的噬脉生物都清理完了吗？", talk_name = "青门芸",},
	[104041102] = { talk_id = 104041102, group_id = 1040411, type = 3, next_id = 104041103, talk_content = "太谢谢你了，要是没有你在，我大概明天就要被青门除名了吧……", talk_name = "青门芸",},
	[104041103] = { talk_id = 104041103, group_id = 1040411, type = 2, options = {{"主祝大人是？",104041104,},}, talk_content = "不对，应该说如果不是因为主祝大人，我根本不会回到老家继承宗门的法术。唉，我果然还是不适合这一行啊。", talk_name = "青门芸",},
	[104041104] = { talk_id = 104041104, group_id = 1040411, type = 3, next_id = 104041105, talk_content = "主祝大人是我们青门礼法的第四十代传人，也是宗门里最尊贵的人。不过她平时深居简出，常人很难见上一面。", talk_name = "青门芸",},
	[104041105] = { talk_id = 104041105, group_id = 1040411, type = 3, next_id = 104041106, talk_content = "顺便一提，主祝大人的祝舞可是帝江一绝呢，我之前就是因为看到她在仪式上的仙姿才决定回到青门当祝士的。", talk_name = "青门芸",},
	[104041106] = { talk_id = 104041106, group_id = 1040411, type = 3, next_id = 104041107, talk_content = "之后<nick_name>你也可以抽时间来旁观我们青门的礼法，说不定有机会亲眼见到主祝大人哦。", talk_name = "青门芸",},
	[104041107] = { talk_id = 104041107, group_id = 1040411, type = 3, next_id = 104041108, talk_content = "嗯，之后有时间我会去的，不过现在我还有些急事。", talk_name = "<nick_name>",},
	[104041108] = { talk_id = 104041108, group_id = 1040411, type = 3, next_id = 104041109, talk_content = "好的好的，你是需要我帮忙记录噬脉生物的清理情况对吧？", talk_name = "青门芸",},
	[104041109] = { talk_id = 104041109, group_id = 1040411, type = 3, next_id = 104041110, talk_content = "刚才你战斗的时候我已经全部记录好了，现场的照片也已经拍下来了。", talk_name = "青门芸",},
	[104041110] = { talk_id = 104041110, group_id = 1040411, type = 3, next_id = 104041111, talk_content = "嗯，谢谢了。如果事务局那边需要提供口头证明，也请你帮忙一下可以吗？", talk_name = "<nick_name>",},
	[104041111] = { talk_id = 104041111, group_id = 1040411, type = 3, talk_content = "当然可以，从此以后你就是我的救命恩人了，<nick_name>！", talk_name = "青门芸",},
	[104041201] = { talk_id = 104041201, group_id = 1040412, type = 1, next_id = 104041202, talk_content = "不过说起来，事务局居然会需要清理噬脉生物的证明吗？感觉有点新鲜呢。", talk_name = "青门芸",},
	[104041202] = { talk_id = 104041202, group_id = 1040412, type = 1, next_id = 104041203, talk_content = "嗯，是办理通行印鉴需要的特殊考核，虽然我也是第一次听说。", talk_name = "<nick_name>",},
	[104041203] = { talk_id = 104041203, group_id = 1040412, type = 1, talk_content = "应该是针对脉者的考核方式吧，我们这些普通人不知道也很正常啦。", talk_name = "青门芸",},
	[104041301] = { talk_id = 104041301, group_id = 1040413, type = 1, next_id = 104041302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "您好，您就是登记名为青乌的脉者对吗？", talk_name = "前台",},
	[104041302] = { talk_id = 104041302, group_id = 1040413, type = 1, next_id = 104041303, talk_content = "没错，我是来提交我的特殊考核结果的。", talk_name = "<nick_name>",},
	[104041303] = { talk_id = 104041303, group_id = 1040413, type = 1, next_id = 104041304, talk_content = "特殊考核？我好像没太明白您的意思，请问是什么样的考核呢？", talk_name = "前台",},
	[104041304] = { talk_id = 104041304, group_id = 1040413, type = 1, next_id = 104041305, talk_content = "是办理通行印鉴的辅助考核，我已经按约定清理了青门区域的噬脉生物，需要提供现场的照片吗？", talk_name = "<nick_name>",},
	[104041305] = { talk_id = 104041305, group_id = 1040413, type = 1, next_id = 104041306, talk_content = "不，我觉得这件事似乎有什么误会……其实我刚才是想告诉您，您的加急印鉴已经办理好了。", talk_name = "前台",},
	[104041306] = { talk_id = 104041306, group_id = 1040413, type = 1, next_id = 104041307, talk_content = "办理好了？可是考核……", talk_name = "<nick_name>",},
	[104041307] = { talk_id = 104041307, group_id = 1040413, type = 2, options = {{"总觉得好像上当了……",104041314,},{"是一位名叫隐的小姐告诉我的。",104041311,},{"是“特别检察官”告诉我的。",104041308,},}, talk_content = "事务局从来没有过清理噬脉生物这样的考核，办理印鉴也不存在这样的流程。不知道您是从哪里听说这种考核的？", talk_name = "前台",},
	[104041308] = { talk_id = 104041308, group_id = 1040413, type = 3, next_id = 104041309, talk_content = "“特别检察官”？我们这里并没有这样的岗位，您能说说那位“检察官”有什么特征吗？", talk_name = "前台",},
	[104041309] = { talk_id = 104041309, group_id = 1040413, type = 3, next_id = 104041310, talk_content = "是个小个子的女生，长着鹿角，穿着一条绿色的裙子……", talk_name = "<nick_name>",},
	[104041310] = { talk_id = 104041310, group_id = 1040413, type = 3, next_id = 104041317, talk_content = "……等一下，那不就是青门的主祝大人吗？这么说来，您就是主祝大人提到过的那位朋友对吧？", talk_name = "前台",},
	[104041311] = { talk_id = 104041311, group_id = 1040413, type = 3, next_id = 104041312, talk_content = "隐？您说的这位小姐是不是长着鹿角，还穿着一条绿色的裙子？", talk_name = "前台",},
	[104041312] = { talk_id = 104041312, group_id = 1040413, type = 3, next_id = 104041313, talk_content = "是的，这么说你认识她？", talk_name = "<nick_name>",},
	[104041313] = { talk_id = 104041313, group_id = 1040413, type = 3, next_id = 104041317, talk_content = "您真会开玩笑，那位可是青门的主祝大人呢。对了，这么说来，您就是主祝大人提到过的那位朋友对吧？", talk_name = "前台",},
	[104041314] = { talk_id = 104041314, group_id = 1040413, type = 3, next_id = 104041315, talk_content = "那个告诉我考核内容的人是个穿着绿色裙子的小个子女生，名字叫隐，她应该不是你们事务局的人吧？", talk_name = "<nick_name>",},
	[104041315] = { talk_id = 104041315, group_id = 1040413, type = 3, next_id = 104041316, talk_content = "名叫隐的小个子女生……", talk_name = "前台",},
	[104041316] = { talk_id = 104041316, group_id = 1040413, type = 3, next_id = 104041317, talk_content = "那不就是青门的主祝大人吗？这么说来，您就是主祝大人提到过的那位朋友对吧？", talk_name = "前台",},
	[104041317] = { talk_id = 104041317, group_id = 1040413, type = 1, next_id = 104041318, talk_content = "等等，你说什么主祝……还有什么朋友？", talk_name = "<nick_name>",},
	[104041318] = { talk_id = 104041318, group_id = 1040413, type = 1, next_id = 104041319, talk_content = "我说您和青门主祝青门隐大人啊，您不会觉得我现在还听不出您的玩笑吧？", talk_name = "前台",},
	[104041319] = { talk_id = 104041319, group_id = 1040413, type = 1, next_id = 104041320, talk_content = "主祝大人前不久刚刚到我们事务局处理噬脉灾害的问题，当时您也正好在办理印鉴，主祝大人还和我说起过您呢。", talk_name = "前台",},
	[104041320] = { talk_id = 104041320, group_id = 1040413, type = 1, next_id = 104041321, talk_content = "她告诉我您是一位能力很强的脉者，平时就喜欢为大家排忧解难，这次也主动提出要帮助青门一起清理噬脉生物。", talk_name = "前台",},
	[104041321] = { talk_id = 104041321, group_id = 1040413, type = 2, options = {{"等等，这中间好像确实有点误会……",104041322,},}, talk_content = "她还说您随后就会来说明噬脉生物清理的情况，只不过您为人比较严肃，做什么事都喜欢用考核的标准要求自己。", talk_name = "前台",},
	[104041322] = { talk_id = 104041322, group_id = 1040413, type = 3, next_id = 104041323, talk_content = "看来主祝大人说的果然没错，您真是个又正直又严于律己的好人啊，哈哈！", talk_name = "前台",},
	[104041323] = { talk_id = 104041323, group_id = 1040413, type = 2, options = {{"这未免太奇怪了吧……",104041324,},{"停，停。",104041324,},}, talk_content = "不过容我好奇一下，您平时也会这样吗？考核标准又是什么？难道是消灭3只怪物算及格，5只怪物算满分……", talk_name = "前台",},
	[104041324] = { talk_id = 104041324, group_id = 1040413, type = 3, talk_content = "好了，先不跟您闲聊了，记得拿好您的印鉴，下次我们如果有噬脉灾害相关的事务，一定也会第一个联系您的！", talk_name = "前台",},
	[104041401] = { talk_id = 104041401, group_id = 1040414, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（化验结果出来应该还要等一阵子，先在附近走走吧。）", talk_content = "（化验结果出来应该还要等一阵子，先在附近走走吧。）", talk_name = "<nick_name>",},
	[104041501] = { talk_id = 104041501, group_id = 1040415, type = 4, aside = "<color=#FFEBA2>大厅广播：</color>办理通行印鉴的客户，请到1号窗口进行抽血化验，谢谢配合。", talk_content = "办理通行印鉴的客户，请到1号窗口进行抽血化验，谢谢配合。", talk_name = "大厅广播",},
	[104041601] = { talk_id = 104041601, group_id = 1040416, type = 4, next_id = 104041602, aside = "<color=#FFEBA2><nick_name>：</color>（那个叫做隐的女孩……总觉得有些奇怪，不知道她说的考核到底靠不靠谱。）", talk_content = "（那个叫做隐的女孩……总觉得有些奇怪，不知道她说的考核到底靠不靠谱。）", talk_name = "<nick_name>",},
	[104041602] = { talk_id = 104041602, group_id = 1040416, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（算了，宁可信其有不可信其无，要是通行印鉴真要等很久才能办好，那可就大事不妙了。）", talk_content = "（算了，宁可信其有不可信其无，要是通行印鉴真要等很久才能办好，那可就大事不妙了。）", talk_name = "<nick_name>",},
	[104041701] = { talk_id = 104041701, group_id = 1040417, type = 4, next_id = 104041702, aside = "<color=#FFEBA2><nick_name>：</color>（隐标记的地方应该就是这附近了。）", talk_content = "（隐标记的地方应该就是这附近了。）", talk_name = "<nick_name>",},
	[104041702] = { talk_id = 104041702, group_id = 1040417, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（前面那位……从着装上看大概是青门的人，过去问问吧。）", talk_content = "（前面那位……从着装上看大概是青门的人，过去问问吧。）", talk_name = "<nick_name>",},
	[104041801] = { talk_id = 104041801, group_id = 1040418, type = 4, next_id = 104041802, aside = "<color=#FFEBA2><nick_name>：</color>（事情都结束了，接下来就回事务局去汇报一下成果吧。）", talk_content = "（事情都结束了，接下来就回事务局去汇报一下成果吧。）", talk_name = "<nick_name>",},
	[104041802] = { talk_id = 104041802, group_id = 1040418, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（希望那位隐小姐人还没走。）", talk_content = "（希望那位隐小姐人还没走。）", talk_name = "<nick_name>",},
	[104041901] = { talk_id = 104041901, group_id = 1040419, type = 4, next_id = 104041902, aside = "<color=#FFEBA2><nick_name>：</color>（好像没有看到隐在哪里，是去忙其他事了吗？）", talk_content = "（好像没有看到隐在哪里，是去忙其他事了吗？）", talk_name = "<nick_name>",},
	[104041902] = { talk_id = 104041902, group_id = 1040419, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>（算了，先找前台说明一下吧。）", talk_content = "（算了，先找前台说明一下吧。）", talk_name = "<nick_name>",},
	[104042001] = { talk_id = 104042001, group_id = 1040420, type = 1, next_id = 104042002, talk_content = "您和主祝大人相处得这么好，平时一定是很亲密的朋友吧？", talk_name = "前台",},
	[104042002] = { talk_id = 104042002, group_id = 1040420, type = 1, next_id = 104042003, talk_content = "难怪二话不说就帮主祝大人的门人清除了噬脉灾害，朋友就是应该互相帮助嘛。", talk_name = "前台",},
	[104042003] = { talk_id = 104042003, group_id = 1040420, type = 1, talk_content = "……", talk_name = "<nick_name>",},
	[104050101] = { talk_id = 104050101, group_id = 1040501, type = 4, next_id = 104050102, aside = "<color=#FFEBA2>小五：</color><nick_name>，印鉴的办理还顺利吗？", call_icon = "Textures/Icon/Single/CampIcon/Lige.png", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "<nick_name>，印鉴的办理还顺利吗？", talk_name = "小五",},
	[104050102] = { talk_id = 104050102, group_id = 1040501, type = 4, next_id = 104050103, aside = "<color=#FFEBA2><nick_name>：</color>……非要说的话，应该算是不顺中的大顺吧。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "……非要说的话，应该算是不顺中的大顺吧。", talk_name = "<nick_name>",},
	[104050103] = { talk_id = 104050103, group_id = 1040501, type = 4, next_id = 104050104, aside = "<color=#FFEBA2>小五：</color>我不太理解你的意思，你刚刚是在学离歌说话吗？", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "我不太理解你的意思，你刚刚是在学离歌说话吗？", talk_name = "小五",},
	[104050104] = { talk_id = 104050104, group_id = 1040501, type = 4, next_id = 104050105, aside = "<color=#FFEBA2><nick_name>：</color>一时半会儿解释不清楚，总之现在印鉴已经拿到了。接下来我应该到什么地方去，空港吗？", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "一时半会儿解释不清楚，总之现在印鉴已经拿到了。接下来我应该到什么地方去，空港吗？", talk_name = "<nick_name>",},
	[104050105] = { talk_id = 104050105, group_id = 1040501, type = 4, next_id = 104050106, aside = "<color=#FFEBA2>小五：</color>空港我已经提前联络过了，他们在地上的站点离余辜所说的那个区域大约有五十公里远，短时间内很难赶过去。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "空港我已经提前联络过了，他们在地上的站点离余辜所说的那个区域大约有五十公里远，短时间内很难赶过去。", talk_name = "小五",},
	[104050106] = { talk_id = 104050106, group_id = 1040501, type = 4, next_id = 104050107, aside = "<color=#FFEBA2>小五：</color>我们现在必须速战速决，<nick_name>，不如试试通过钟悬到地上去吧。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "我们现在必须速战速决，<nick_name>，不如试试通过钟悬到地上去吧。", talk_name = "小五",},
	[104050107] = { talk_id = 104050107, group_id = 1040501, type = 4, next_id = 104050108, aside = "<color=#FFEBA2><nick_name>：</color>钟悬？听起来不太像交通工具的名字。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "钟悬？听起来不太像交通工具的名字。", talk_name = "<nick_name>",},
	[104050108] = { talk_id = 104050108, group_id = 1040501, type = 4, next_id = 104050109, aside = "<color=#FFEBA2>小五：</color>是一种连通天上和地下的古老装置。虽然通过钟悬旅行对普通人来说有些危险，但如果是你的话应该没问题的。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "是一种连通天上和地下的古老装置。虽然通过钟悬旅行对普通人来说有些危险，但如果是你的话应该没问题的。", talk_name = "小五",},
	[104050109] = { talk_id = 104050109, group_id = 1040501, type = 4, aside = "<color=#FFEBA2>小五：</color>我已经帮你标记好了钟悬的位置，如果路上有什么问题的话，记得和我联系。", call_man_icon = "小五;Textures/Icon/Single/HeadIcon/SquRwuda.png", child_type = 41, talk_content = "我已经帮你标记好了钟悬的位置，如果路上有什么问题的话，记得和我联系。", talk_name = "小五",},
	[104050201] = { talk_id = 104050201, group_id = 1040502, type = 1, next_id = 104050202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "喂，停一下，你这家伙急急忙忙的要做什么？", talk_name = "巡卫",},
	[104050202] = { talk_id = 104050202, group_id = 1040502, type = 1, next_id = 104050203, talk_content = "我要用那边的钟悬到地上去，这是我的通行印鉴。", talk_name = "<nick_name>",},
	[104050203] = { talk_id = 104050203, group_id = 1040502, type = 2, options = {{"我需要尽快到地上去。",104050204,},{"空港离我要去的地方太远了。",104050204,},}, talk_content = "让我看看……青乌？我问你，为什么放着好端端的空港不用，非要到这种地方来？", talk_name = "巡卫",},
	[104050204] = { talk_id = 104050204, group_id = 1040502, type = 3, next_id = 104050205, talk_content = "这钟悬是老古董了，平时都不会启动的，现在也不知道灵光不灵光。", talk_name = "巡卫",},
	[104050205] = { talk_id = 104050205, group_id = 1040502, type = 2, child_type = 21, options = {{"我体格好得很。",104050206,},{"不能帮忙维修一下吗？",104050209,},{"明知道有问题为什么平时不维护？",104050212,},}, talk_content = "非要用倒也不是不行，但这玩意可不保险，要是你出点什么问题上面就该找我算账了。", talk_name = "巡卫",},
	[104050206] = { talk_id = 104050206, group_id = 1040502, type = 3, next_id = 104050207, icon = "interaction_4", talk_content = "就算能徒手跳下三层楼，沿着墙跑上摩天大厦也不行吗？", talk_name = "<nick_name>",},
	[104050207] = { talk_id = 104050207, group_id = 1040502, type = 3, next_id = 104050208, talk_content = "就算能徒……等等等等，你说的都是什么啊，你们脉者现在连这种事都能做到了吗？", talk_name = "巡卫",},
	[104050208] = { talk_id = 104050208, group_id = 1040502, type = 3, next_id = 104050214, talk_content = "总之不管你能从几层楼跳下来，万米高空摔下去一样渣也不剩，明白了吗？", talk_name = "巡卫",},
	[104050209] = { talk_id = 104050209, group_id = 1040502, type = 3, next_id = 104050210, icon = "interaction_4", talk_content = "你这家伙说得轻松，这东西维修起来可不是那么容易的事，全天月城都没几个人懂。", talk_name = "巡卫",},
	[104050210] = { talk_id = 104050210, group_id = 1040502, type = 3, next_id = 104050211, talk_content = "但这里既然只有你一个人管理，你就应该知道维修的方法吧？不然如果出了问题该怎么办？", talk_name = "<nick_name>",},
	[104050211] = { talk_id = 104050211, group_id = 1040502, type = 3, next_id = 104050214, talk_content = "好吧好吧，我的确知道这东西该怎么修，但修钟悬也是需要成本的，不让你用也是为你考虑嘛。", talk_name = "巡卫",},
	[104050212] = { talk_id = 104050212, group_id = 1040502, type = 3, next_id = 104050213, icon = "interaction_4", talk_content = "你这家伙说得轻松，你知道钟悬的日常维护要花多少钱吗？", talk_name = "巡卫",},
	[104050213] = { talk_id = 104050213, group_id = 1040502, type = 3, next_id = 104050214, talk_content = "电梯检修、系统维护、传送中枢控制、导航地图更新……反正是没人用的老玩意，谁愿意当那冤大头啊。", talk_name = "巡卫",},
	[104050214] = { talk_id = 104050214, group_id = 1040502, type = 1, next_id = 104050215, talk_content = "但你本来就是这个设施的管理者不是吗？明明有设施却不让人用，看来是时候跟巡卫组织反映一下了。", talk_name = "<nick_name>",},
	[104050215] = { talk_id = 104050215, group_id = 1040502, type = 1, next_id = 104050216, talk_content = "你……行吧，你如果一定要用我也不拦着你，但临时使用得加钱，这是上面的规定。", talk_name = "巡卫",},
	[104050216] = { talk_id = 104050216, group_id = 1040502, type = 1, next_id = 104050217, talk_content = "总共需要多少钱，你说个数吧。", talk_name = "<nick_name>",},
	[104050217] = { talk_id = 104050217, group_id = 1040502, type = 1, next_id = 104050218, talk_content = "这样吧，钟悬使用费、安全保险费和临时维护费加起来总共收你20000约币，这价格接受不？", talk_name = "巡卫",},
	[104050218] = { talk_id = 104050218, group_id = 1040502, type = 1, next_id = 104050219, talk_content = "（这价格一听就有水分……但去地上找余辜是要紧事，这次只能先按照他的意思来了。）", talk_name = "<nick_name>",},
	[104050219] = { talk_id = 104050219, group_id = 1040502, type = 2, options = {{"<color=#fed78c>砍价</color>",8,},}, talk_content = "（不过20000约币也太贵了，找到余辜之后大概会有很多需要用钱的场合，还是先试着还一下价吧。）", talk_name = "<nick_name>",},
	[104050301] = { talk_id = 104050301, group_id = 1040503, type = 1, next_id = 104050302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你这家伙嘴皮子还挺利索……算了，那就破例给你个优惠价吧。", talk_name = "巡卫",},
	[104050302] = { talk_id = 104050302, group_id = 1040503, type = 1, next_id = 104050303, talk_content = "不过这钟悬确实挺长时间不用了，你在这等等，我上去检修一下。", talk_name = "巡卫",},
	[104050303] = { talk_id = 104050303, group_id = 1040503, type = 5, next_id = 104050304, back_text = "巡卫花了一些时间检修了钟悬……",},
	[104050304] = { talk_id = 104050304, group_id = 1040503, type = 2, options = {{"教教我吧。",104050305,},{"不知道。",104050305,},}, talk_content = "行了，钟悬现在已经可以正常使用了，你应该知道怎么用吧？", talk_name = "巡卫",},
	[104050305] = { talk_id = 104050305, group_id = 1040503, type = 3, next_id = 104050306, talk_content = "什么啊，我还以为你这家伙上来就点名要用钟悬应该懂得很多呢。", talk_name = "巡卫",},
	[104050306] = { talk_id = 104050306, group_id = 1040503, type = 3, next_id = 104050307, talk_content = "你从下面的大门进去，坐悬浮梯到顶层，然后设置好自己要去的地点。", talk_name = "巡卫",},
	[104050307] = { talk_id = 104050307, group_id = 1040503, type = 3, next_id = 104050308, talk_content = "做完这些之后，可以按照古人的方法对着钟悬祷告……好吧，不祷告也行。", talk_name = "巡卫",},
	[104050308] = { talk_id = 104050308, group_id = 1040503, type = 3, next_id = 104050309, talk_content = "然后敲响上面的大钟，钟悬就会启动。下落的时候记得保持好平衡，在太阳附近的境界线那把身体调转过来就好。", talk_name = "巡卫",},
	[104050309] = { talk_id = 104050309, group_id = 1040503, type = 3, next_id = 104050310, talk_content = "需要注意的事项就这么多了，敲响钟悬即代表安全协议生效，之后出了什么问题就是你一个人的责任了。", talk_name = "巡卫",},
	[104050310] = { talk_id = 104050310, group_id = 1040503, type = 3, talk_content = "那祝你旅途愉快，哈……当然不愉快也行。", talk_name = "巡卫",},
	[104050401] = { talk_id = 104050401, group_id = 1040504, type = 1, next_id = 104050402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "用钟悬旅行就得这价格，要是便宜岂不是人人都来用了？那我们巡卫光是救人就有的忙了。", talk_name = "巡卫",},
	[104050402] = { talk_id = 104050402, group_id = 1040504, type = 1, next_id = 104050403, talk_content = "也罢，那就按照你说的价格来吧。", talk_name = "<nick_name>",},
	[104050403] = { talk_id = 104050403, group_id = 1040504, type = 1, next_id = 104050404, talk_content = "这还差不多。不过这钟悬确实挺长时间不用了，你在这等等，我上去检修一下。", talk_name = "巡卫",},
	[104050404] = { talk_id = 104050404, group_id = 1040504, type = 5, next_id = 104050405, back_text = "巡卫花了一些时间检修了钟悬……",},
	[104050405] = { talk_id = 104050405, group_id = 1040504, type = 2, options = {{"教教我吧。",104050406,},{"不知道。",104050406,},}, talk_content = "行了，钟悬现在已经可以正常使用了，你应该知道怎么用吧？", talk_name = "巡卫",},
	[104050406] = { talk_id = 104050406, group_id = 1040504, type = 3, next_id = 104050407, talk_content = "什么啊，我还以为你这家伙上来就点名要用钟悬应该懂得很多呢。", talk_name = "巡卫",},
	[104050407] = { talk_id = 104050407, group_id = 1040504, type = 3, next_id = 104050408, talk_content = "你从下面的大门进去，坐悬浮梯到顶层，然后设置好自己要去的地点。", talk_name = "巡卫",},
	[104050408] = { talk_id = 104050408, group_id = 1040504, type = 3, next_id = 104050409, talk_content = "做完这些之后，可以按照古人的方法对着钟悬祷告……好吧，不祷告也行。", talk_name = "巡卫",},
	[104050409] = { talk_id = 104050409, group_id = 1040504, type = 3, next_id = 104050410, talk_content = "然后敲响上面的大钟，钟悬就会启动。下落的时候记得保持好平衡，在太阳附近的临界线那把身体调转过来就好。", talk_name = "巡卫",},
	[104050410] = { talk_id = 104050410, group_id = 1040504, type = 3, next_id = 104050411, talk_content = "需要注意的事项就这么多了，敲响钟悬即代表安全协议生效，之后出了什么问题就是你一个人的责任了。", talk_name = "巡卫",},
	[104050411] = { talk_id = 104050411, group_id = 1040504, type = 3, talk_content = "那祝你旅途愉快，哈……当然不愉快也行。", talk_name = "巡卫",},
	[104050501] = { talk_id = 104050501, group_id = 1040505, type = 1, next_id = 104050502, talk_content = "没想到现在还有人这么喜欢用钟悬，不会是那个什么反倒悬协会的吧？", talk_name = "巡卫",},
	[104050502] = { talk_id = 104050502, group_id = 1040505, type = 1, talk_content = "幸亏只是脉者，要是普通人都来观光，还不知道要出什么麻烦呢。", talk_name = "巡卫",},
	[104050601] = { talk_id = 104050601, group_id = 1040506, type = 1, next_id = 104050602, fade_in = 1, fade_out = true, hide_player = true, talk_content = "这里就是大钟悬的顶层了吧。按照刚才那个巡卫的说法，应该先设置好目的地……", talk_name = "<nick_name>",},
	[104050602] = { talk_id = 104050602, group_id = 1040506, type = 1, next_id = 104050603, talk_content = "“创生地”，嗯，余辜说的就是这个地方。", talk_name = "<nick_name>",},
	[104050603] = { talk_id = 104050603, group_id = 1040506, type = 2, options = {{"（闭上眼睛）",104050604,},}, talk_content = "接下来的话，来试试祷告吧。", talk_name = "<nick_name>",},
	[104050604] = { talk_id = 104050604, group_id = 1040506, type = 3, next_id = 104050605, talk_content = "……", talk_name = "<nick_name>",},
	[104050605] = { talk_id = 104050605, group_id = 1040506, type = 1, next_id = 104050606, talk_content = "（从恢复意识起，我就生活在这座名为天月城的城市里。过往、身份、地位，这些于我而言只剩下一片空白。）", talk_name = "<nick_name>",},
	[104050606] = { talk_id = 104050606, group_id = 1040506, type = 1, next_id = 104050607, talk_content = "（自称知晓我的过去的只有我的同伴，离歌和小五。而现在，离歌落到了悬天的手里。）", talk_name = "<nick_name>",},
	[104050607] = { talk_id = 104050607, group_id = 1040506, type = 1, next_id = 104050608, talk_content = "（悬天似乎掌握着和我有关的重要秘密。虽然不知道最终目的如何，但他们始终都站在“望月”的对立面。）", talk_name = "<nick_name>",},
	[104050608] = { talk_id = 104050608, group_id = 1040506, type = 1, next_id = 104050609, talk_content = "（总之，蕴含着古老智慧的钟悬……虽然不知道你能不能听到我的话，但我还是在这里向你祷告。）", talk_name = "<nick_name>",},
	[104050609] = { talk_id = 104050609, group_id = 1040506, type = 2, child_type = 21, options = {{"能够找回自己的身份。",104050610,},{"能够揭穿悬天的阴谋。",104050613,},{"能够把离歌平安带回。",104050615,},}, talk_content = "（希望我……）", talk_name = "<nick_name>",},
	[104050610] = { talk_id = 104050610, group_id = 1040506, type = 3, next_id = 104050611, icon = "interaction_4", talk_content = "（虽然作为失忆者在天月城的生活并没有太多不顺，离歌和小五也一直在帮助我。）", talk_name = "<nick_name>",},
	[104050611] = { talk_id = 104050611, group_id = 1040506, type = 3, next_id = 104050612, talk_content = "（但无论如何，我还是想要知道自己究竟是什么人，曾经做过什么，以后又要到哪里去。）", talk_name = "<nick_name>",},
	[104050612] = { talk_id = 104050612, group_id = 1040506, type = 3, next_id = 104050619, talk_content = "（毕竟……没有过去的我始终不算是完整的我。）", talk_name = "<nick_name>",},
	[104050613] = { talk_id = 104050613, group_id = 1040506, type = 3, next_id = 104050614, icon = "interaction_4", talk_content = "（悬天……那个企业一直都在和我们作对。不管是研究奇怪的溶液还是操纵怪物，听起来都绝对不是好事。）", talk_name = "<nick_name>",},
	[104050614] = { talk_id = 104050614, group_id = 1040506, type = 3, next_id = 104050619, talk_content = "（一定要揭穿悬天的阴谋，不管他们要对我们和这座城市做些什么，都必须阻止他们才行。）", talk_name = "<nick_name>",},
	[104050615] = { talk_id = 104050615, group_id = 1040506, type = 3, next_id = 104050616, icon = "interaction_4", talk_content = "（其他愿望全都远在天边，我现在最迫切希望的，无非只是让离歌平安回来而已。）", talk_name = "<nick_name>",},
	[104050616] = { talk_id = 104050616, group_id = 1040506, type = 3, next_id = 104050617, talk_content = "（虽然我直到现在都不知道她所谓的“做大事”到底意味着什么，也不知道她有着怎样的过去……）", talk_name = "<nick_name>",},
	[104050617] = { talk_id = 104050617, group_id = 1040506, type = 3, next_id = 104050618, talk_content = "（但不论如何，她都是我和这个世界为数不多的联系之一。）", talk_name = "<nick_name>",},
	[104050618] = { talk_id = 104050618, group_id = 1040506, type = 3, next_id = 104050619, talk_content = "（如果我落到了悬天手里，离歌和小五……一定也会不惜代价去做相同的事吧。）", talk_name = "<nick_name>",},
	[104050619] = { talk_id = 104050619, group_id = 1040506, type = 1, talk_content = "那么，现在就出发吧。", talk_name = "<nick_name>",},
