	[202010106] = { talk_id = 202010106, group_id = 2020101, type = 2, options = {{"就帮帮她吧。",202010107,},{"有报酬的话……",202010108,},}, talk_content = "一直瞄着这边呢……青乌，你要帮她吗？", talk_name = "司命",},
