	[202010403] = { talk_id = 202010403, group_id = 2020104, type = 3, next_id = 202010405, talk_content = "嘿嘿，青乌你也是上道了呢。", talk_name = "游商",},
	[202010404] = { talk_id = 202010404, group_id = 2020104, type = 3, next_id = 202010405, talk_content = "唉，那你看着就好。", talk_name = "司命",},
	[202010405] = { talk_id = 202010405, group_id = 2020104, type = 1, next_id = 202010406, talk_content = "呃，所以是有什么问题吗？", talk_name = "司命",},
	[202010406] = { talk_id = 202010406, group_id = 2020104, type = 1, next_id = 202010407, talk_content = "直白点说吧，你这家伙到底运的是什么货。", talk_name = "司命",},
	[202010407] = { talk_id = 202010407, group_id = 2020104, type = 1, next_id = 202010408, talk_content = "这……", talk_name = "游商",},
	[202010408] = { talk_id = 202010408, group_id = 2020104, type = 1, next_id = 202010409, talk_content = "别想装蒜，刚刚护送你这一路碰见的敌人也太多了。", talk_name = "司命",},
	[202010409] = { talk_id = 202010409, group_id = 2020104, type = 1, next_id = 202010410, talk_content = "虽说那些杂鱼们是变得比平常活跃，但这个量怎么想都不对路。", talk_name = "司命",},
	[202010410] = { talk_id = 202010410, group_id = 2020104, type = 1, next_id = 202010411, options = {{"该不会是危险品吧？",202010107,},}, talk_content = "而能够解释这点的唯一线索，恐怕就是你的货物有什么能吸引噬脉生物的特质。", talk_name = "司命",},
	[202010411] = { talk_id = 202010411, group_id = 2020104, type = 1, next_id = 202010412, talk_content = "那得看过才知道，快，打开你的背包，肯定是什么超级值钱的东西！", talk_name = "司命",},
	[202010412] = { talk_id = 202010412, group_id = 2020104, type = 1, next_id = 202010413, talk_content = "这……这。", talk_name = "游商",},
	[202010413] = { talk_id = 202010413, group_id = 2020104, type = 1, talk_content = "！！！", talk_name = "噬脉生物",},
	[202010501] = { talk_id = 202010501, group_id = 2020105, type = 4, next_id = 202010502, aside = "<color=#FFEBA2>司命：</color>这群噬脉生物也太会碍事了吧！", talk_content = "这群噬脉生物也太会碍事了吧！", talk_name = "司命",},
	[202010502] = { talk_id = 202010502, group_id = 2020105, type = 4, next_id = 202010503, aside = "<color=#FFEBA2>游商：</color>果然上天是站在我这边的，两位大恩大德小人没齿难忘！", talk_content = "果然上天是站在我这边的，两位大恩大德小人没齿难忘！", talk_name = "游商",},
	[202010503] = { talk_id = 202010503, group_id = 2020105, type = 4, aside = "<color=#FFEBA2>司命：</color>难忘你就别逃！", talk_content = "难忘你就别逃！", talk_name = "司命",},
	[202010601] = { talk_id = 202010601, group_id = 2020106, type = 1, next_id = 202010602, talk_content = "嗤，竟然被哪个游商逃了。", talk_name = "司命",},
	[202010602] = { talk_id = 202010602, group_id = 2020106, type = 1, next_id = 202010603, talk_content = "那怎么办，她背包里的东西是会引来怪物的不是吗？", talk_name = "叙慕",},
	[202010603] = { talk_id = 202010603, group_id = 2020106, type = 1, next_id = 202010604, talk_content = "这人的最终目的是进城里去，要是她没被噬脉生物吃掉的话那总能碰见的。", talk_name = "司命",},
	[202010604] = { talk_id = 202010604, group_id = 2020106, type = 1, next_id = 202010605, talk_content = "到时候得狠狠勒索一笔……那东西肯定很值钱。", talk_name = "司命",},
	[202010605] = { talk_id = 202010605, group_id = 2020106, type = 1, talk_content = "我就知道……", talk_name = "叙慕",},
	[202020101] = { talk_id = 202020101, group_id = 2020201, type = 1, next_id = 202020102, talk_content = "唔？", talk_name = "摊贩",},
	[202020102] = { talk_id = 202020102, group_id = 2020201, type = 1, next_id = 202020103, talk_content = "喂，你这里是卖什么的？", talk_name = "司命",},
	[202020103] = { talk_id = 202020103, group_id = 2020201, type = 1, next_id = 202020104, talk_content = "一上来这样问也太失礼了吧……", talk_name = "叙慕",},
	[202020104] = { talk_id = 202020104, group_id = 2020201, type = 1, next_id = 202020105, talk_content = "你不也是因为好奇才靠到这来的！", talk_name = "司命",},
	[202020105] = { talk_id = 202020105, group_id = 2020201, type = 1, next_id = 202020106, talk_content = "所以呢，这是什么店？", talk_name = "司命",},
	[202020106] = { talk_id = 202020106, group_id = 2020201, type = 1, next_id = 202020107, talk_content = "我这应该叫摊而不是店。", talk_name = "摊贩",},
	[202020107] = { talk_id = 202020107, group_id = 2020201, type = 1, next_id = 202020108, talk_content = "摊？", talk_name = "司命",},
	[202020108] = { talk_id = 202020108, group_id = 2020201, type = 2, options = {{"可以",6,},}, talk_content = "嗯，是个游乐用的小摊，主要经营打气球之类的小游戏，对了，两位要不要来试试？", talk_name = "摊贩",},
	[202020109] = { talk_id = 202020109, group_id = 2020201, type = 3, next_id = 202020110, talk_content = "是的，今天是我第一次出摊，刚好缺了点测试这个小游戏的帮手。", talk_name = "摊贩",},
	[202020110] = { talk_id = 202020110, group_id = 2020201, type = 3, next_id = 202020111, talk_content = "想让我们做测试呐……有报酬吗？", talk_name = "司命",},
	[202020111] = { talk_id = 202020111, group_id = 2020201, type = 3, next_id = 202020112, talk_content = "我这里有些小礼品，假如你们能成功通过游戏的话那我可以给你们送一些哦。", talk_name = "摊贩",},
	[202020112] = { talk_id = 202020112, group_id = 2020201, type = 3, talk_content = "一言为定，上吧青乌！", talk_name = "司命",},
	[202020201] = { talk_id = 202020201, group_id = 2020202, type = 2, options = {{"有些难。",202020203,},{"太简单了。",202020202,},}, talk_content = "恭喜你，成功过关了，顺带问问，你觉得这个游戏的难度如何？", talk_name = "摊贩",},
	[202020202] = { talk_id = 202020202, group_id = 2020202, type = 3, next_id = 202020204, talk_content = "太简单了吗，也好，毕竟脉者的简单其实就相当于普通人的困难了。", talk_name = "摊贩",},
	[202020203] = { talk_id = 202020203, group_id = 2020202, type = 3, next_id = 202020204, talk_content = "看来是我难度设置有些问题，我知道了，后面会改善的。", talk_name = "摊贩",},
	[202020204] = { talk_id = 202020204, group_id = 2020202, type = 1, next_id = 202020205, talk_content = "所以说我们帮你搞定那啥测试了，奖励呢？", talk_name = "司命",},
	[202020205] = { talk_id = 202020205, group_id = 2020202, type = 1, next_id = 202020206, talk_content = "我已经准备好了，奖励就是这些小物品以及可以再参加一次打气球游戏的奖卷哦？", talk_name = "摊贩",},
	[202020206] = { talk_id = 202020206, group_id = 2020202, type = 1, talk_content = "这不是摆明了想让我们白打工吗！", talk_name = "司命",},
	[202020301] = { talk_id = 202020301, group_id = 2020203, type = 1, talk_content = "摊现在还在收拾，请稍后再来吧。", talk_name = "摊贩",},
	[202020401] = { talk_id = 202020401, group_id = 2020204, type = 1, talk_content = "气球我都放好了，你准备好就到摊位去吧。", talk_name = "摊贩",},
	[202030101] = { talk_id = 202030101, group_id = 2020301, type = 1, next_id = 202030102, talk_content = "那边的脉者，你就是青乌对吧？", talk_name = "流丽",},
	[202030102] = { talk_id = 202030102, group_id = 2020301, type = 1, next_id = 202030103, talk_content = "哦……你知道我们？", talk_name = "司命",},
	[202030103] = { talk_id = 202030103, group_id = 2020301, type = 2, options = {{"我不太想自己广为人知。",202030104,},{"所以你喊住我想做什么？",202030105,},}, talk_content = "之前有听司长说过，对了，我是一位维安司警。", talk_name = "流丽",},
	[202030104] = { talk_id = 202030104, group_id = 2020301, type = 3, next_id = 202030106, talk_content = "毕竟悬天的家伙可是盯着我们呢，总之你这小司警要说些啥？", talk_name = "司命",},
	[202030105] = { talk_id = 202030105, group_id = 2020301, type = 3, next_id = 202030106, talk_content = "一看就知道是有东西要拜托我们了。", talk_name = "司命",},
	[202030106] = { talk_id = 202030106, group_id = 2020301, type = 1, next_id = 202030107, talk_content = "是这样的，最近因为噬脉生物的问题导致城内也不太安稳，一些流氓盗贼也混进了城中。", talk_name = "流丽",},
	[202030107] = { talk_id = 202030107, group_id = 2020301, type = 1, next_id = 202030108, talk_content = "假若他们只是来避避风头倒没什么关系，但没想到他们还干起了偷鸡摸狗的勾当。", talk_name = "流丽",},
	[202030108] = { talk_id = 202030108, group_id = 2020301, type = 1, next_id = 202030109, talk_content = "截止到昨天，我们已经有好几个仓库被盗，损失了不少财物……", talk_name = "流丽",},
	[202030109] = { talk_id = 202030109, group_id = 2020301, type = 1, next_id = 202030110, talk_content = "所以是要我们捉住犯人吗？", talk_name = "司命",},
	[202030110] = { talk_id = 202030110, group_id = 2020301, type = 1, next_id = 202030111, talk_content = "不，为了捉住犯人我们已经组织了专项组，我只是想要拜托青乌你们帮我去搬运一批货物。", talk_name = "流丽",},
	[202030111] = { talk_id = 202030111, group_id = 2020301, type = 1, next_id = 202030112, talk_content = "这个拜托其他人不就好了。", talk_name = "司命",},
	[202030112] = { talk_id = 202030112, group_id = 2020301, type = 1, next_id = 202030113, talk_content = "呃，现在城内鱼龙混杂，维安司人手不足，为了货物不再失窃，只能拜托能信任的人来了。", talk_name = "流丽",},
	[202030113] = { talk_id = 202030113, group_id = 2020301, type = 1, talk_content = "所以说我们就是被信任的人呀……哼哼，没办法，就帮帮你吧。", talk_name = "司命",},
	[202030201] = { talk_id = 202030201, group_id = 2020302, type = 1, next_id = 202030202, talk_content = "一件，三件，要带走的数应该都对上了吧……", talk_name = "像是工人的人",},
	[202030202] = { talk_id = 202030202, group_id = 2020302, type = 1, next_id = 202030203, talk_content = "这不是已经有人在了吗，喂！", talk_name = "司命",},
	[202030203] = { talk_id = 202030203, group_id = 2020302, type = 1, next_id = 202030204, talk_content = "哇！", talk_name = "像是工人的人",},
	[202030204] = { talk_id = 202030204, group_id = 2020302, type = 2, options = {{"哇！",202030205,},{"你们在干什么？",202030206,},}, talk_content = "哇！", talk_name = "司命",},
	[202030205] = { talk_id = 202030205, group_id = 2020302, type = 3, next_id = 202030208, talk_content = "青乌你这家伙在这面无表情的凑什么热闹！", talk_name = "司命",},
	[202030206] = { talk_id = 202030206, group_id = 2020302, type = 3, next_id = 202030207, talk_content = "这应该问她才对！", talk_name = "司命",},
	[202030207] = { talk_id = 202030207, group_id = 2020302, type = 3, next_id = 202030208, talk_content = "我这不是被吓到了嘛……", talk_name = "像是工人的人",},
	[202030208] = { talk_id = 202030208, group_id = 2020302, type = 1, next_id = 202030209, talk_content = "所以你这家伙到底在这里干什么？", talk_name = "司命",},
	[202030209] = { talk_id = 202030209, group_id = 2020302, type = 1, next_id = 202030210, talk_content = "呃……呃，那你们又是来干啥的？", talk_name = "像是工人的人",},
	[202030210] = { talk_id = 202030210, group_id = 2020302, type = 1, next_id = 202030211, talk_content = "我们是受拜托来运货的人，结果刚来到就发现你在点货了，难不成那个货车司机拜托了两个人吗？", talk_name = "司命",},
	[202030211] = { talk_id = 202030211, group_id = 2020302, type = 1, next_id = 202030212, talk_content = "……，看来就是你说的那样呢，我也受到拜托要来搬货了，我脚下这些就是准备要送走的货物。", talk_name = "像是工人的人",},
	[202030212] = { talk_id = 202030212, group_id = 2020302, type = 2, options = {{"（有些可疑。）",202030213,},{"你真的是搬货的吗？",202030214,},}, talk_content = "数量也不多，这些我一个人就能搬完，抱歉，好像让你们白跑一趟了。", talk_name = "像是工人的人",},
	[202030213] = { talk_id = 202030213, group_id = 2020302, type = 3, next_id = 202030217, talk_content = "（嘘，先闭嘴！）", talk_name = "司命",},
	[202030214] = { talk_id = 202030214, group_id = 2020302, type = 3, next_id = 202030215, talk_content = "哇，哇！", talk_name = "司命",},
	[202030215] = { talk_id = 202030215, group_id = 2020302, type = 3, next_id = 202030216, talk_content = "司命，你又在大叫什么？", talk_name = "叙慕",},
	[202030216] = { talk_id = 202030216, group_id = 2020302, type = 3, next_id = 202030217, talk_content = "青乌你先闭嘴！", talk_name = "司命",},
	[202030217] = { talk_id = 202030217, group_id = 2020302, type = 1, next_id = 202030218, talk_content = "呃，所以两位可以先离开了，接下来的事情我一个人办就可以。", talk_name = "像是工人的人",},
	[202030218] = { talk_id = 202030218, group_id = 2020302, type = 1, talk_content = "嗯嗯，不用我们干活自然是最好不过啦，走吧青乌。", talk_name = "司命",},
	[202030301] = { talk_id = 202030301, group_id = 2020303, type = 1, next_id = 202030302, talk_content = "司命，那人不是很可疑吗？", talk_name = "叙慕",},
	[202030302] = { talk_id = 202030302, group_id = 2020303, type = 1, next_id = 202030303, talk_content = "嘻嘻，那家伙肯定就是小警司口中的盗窃犯没错。", talk_name = "司命",},
	[202030303] = { talk_id = 202030303, group_id = 2020303, type = 1, next_id = 202030304, talk_content = "所以说你在打什么算盘，刚刚直接指证不就完了。", talk_name = "叙慕",},
	[202030304] = { talk_id = 202030304, group_id = 2020303, type = 1, next_id = 202030305, talk_content = "这样就不能一网打尽了呀。", talk_name = "司命",},
	[202030305] = { talk_id = 202030305, group_id = 2020303, type = 1, next_id = 202030306, talk_content = "……这我倒没想到。", talk_name = "叙慕",},
	[202030306] = { talk_id = 202030306, group_id = 2020303, type = 1, talk_content = "此乃司命三十六计，放贼归山。", talk_name = "司命",},
	[202030401] = { talk_id = 202030401, group_id = 2020304, type = 4, next_id = 202030402, aside = "<color=#FFEBA2>司命：</color>时间差不多了，我们到城外去看看有啥人接应那家伙吧。", talk_content = "时间差不多了，我们到城外去看看有啥人接应那家伙吧。", talk_name = "司命",},
	[202030402] = { talk_id = 202030402, group_id = 2020304, type = 4, next_id = 202030403, aside = "<color=#FFEBA2>叙慕：</color>你知道他位置吗？", talk_content = "你知道他位置吗？", talk_name = "叙慕",},
	[202030403] = { talk_id = 202030403, group_id = 2020304, type = 4, aside = "<color=#FFEBA2>司命：</color>怎么想也不可能一个人把货搬回老巢吧。", talk_content = "怎么想也不可能一个人把货搬回老巢吧。", talk_name = "司命",},
	[202030501] = { talk_id = 202030501, group_id = 2020305, type = 4, next_id = 202030502, aside = "<color=#FFEBA2>司命：</color>你看，青乌，就在那边。", talk_content = "你看，青乌，就在那边。", talk_name = "司命",},
	[202030502] = { talk_id = 202030502, group_id = 2020305, type = 4, next_id = 202030503, aside = "<color=#FFEBA2>叙慕：</color>就如你说的，好几个人在那。", talk_content = "就如你说的，好几个人在那。", talk_name = "叙慕",},
	[202030503] = { talk_id = 202030503, group_id = 2020305, type = 4, aside = "<color=#FFEBA2>司命：</color>直接上吧。", talk_content = "直接上吧。", talk_name = "司命",},
	[202030601] = { talk_id = 202030601, group_id = 2020306, type = 4, aside = "<color=#FFEBA2>盗贼：</color>你们是什么人，难不成是维安司的……", talk_content = "你们是什么人，难不成是维安司的……", talk_name = "盗贼",},
	[202030701] = { talk_id = 202030701, group_id = 2020307, type = 4, next_id = 202030702, aside = "<color=#FFEBA2>司命：</color>这样就……", talk_content = "这样就……", talk_name = "司命",},
	[202030702] = { talk_id = 202030702, group_id = 2020307, type = 4, next_id = 202030703, aside = "<color=#FFEBA2>盗贼：</color>可恶，怎么可能在这里被捉住。", talk_content = "可恶，怎么可能在这里被捉住。", talk_name = "盗贼",},
	[202030703] = { talk_id = 202030703, group_id = 2020307, type = 4, next_id = 202030704, aside = "<color=#FFEBA2>司命：</color>有人跑了，快追上去！", talk_content = "有人跑了，快追上去！", talk_name = "司命",},
	[202030704] = { talk_id = 202030704, group_id = 2020307, type = 4, aside = "<color=#FFEBA2>叙慕：</color>我知道！", talk_content = "我知道！", talk_name = "叙慕",},
	[202030801] = { talk_id = 202030801, group_id = 2020308, type = 4, next_id = 202030802, aside = "<color=#FFEBA2>盗贼：</color>我投降，投降了！", talk_content = "我投降，投降了！", talk_name = "盗贼",},
	[202030802] = { talk_id = 202030802, group_id = 2020308, type = 4, aside = "<color=#FFEBA2>司命：</color>哼哼，知道我们的厉害没有。", talk_content = "哼哼，知道我们的厉害没有。", talk_name = "司命",},
	[202030901] = { talk_id = 202030901, group_id = 2020309, type = 1, next_id = 202030902, talk_content = "总算是把人捉住了，接下来得把人送到司警那边……", talk_name = "叙慕",},
	[202030902] = { talk_id = 202030902, group_id = 2020309, type = 2, options = {{"你为什么会在这里？",202030903,},{"看来能省点力了。",202030903,},}, talk_content = "接下来直接由我们接手就可以，辛苦两位了。", talk_name = "流丽",},
	[202030903] = { talk_id = 202030903, group_id = 2020309, type = 3, next_id = 202030904, talk_content = "因为有居民说听见了打闹声，我顺着他们说的方向就找到了倒在地上的盗贼们，还发现了些脚印。", talk_name = "流丽",},
	[202030904] = { talk_id = 202030904, group_id = 2020309, type = 3, next_id = 202030905, talk_content = "于是一路找过来了哦？", talk_name = "司命",},
	[202030905] = { talk_id = 202030905, group_id = 2020309, type = 3, next_id = 202030906, talk_content = "是的，没想到你们竟然能捉住盗贼，真的太让我佩服了。", talk_name = "流丽",},
	[202030906] = { talk_id = 202030906, group_id = 2020309, type = 3, next_id = 202030907, talk_content = "佩服光用嘴是没用的，最好还有点物质什么的，你懂的吧？", talk_name = "司命",},
	[202030907] = { talk_id = 202030907, group_id = 2020309, type = 3, next_id = 202030908, talk_content = "……", talk_name = "流丽",},
	[202030908] = { talk_id = 202030908, group_id = 2020309, type = 3, next_id = 202030909, talk_content = "要不也一起把司命拷回去吧。", talk_name = "叙慕",},
	[202030909] = { talk_id = 202030909, group_id = 2020309, type = 3, talk_content = "喂！", talk_name = "司命",},
	[202040101] = { talk_id = 202040101, group_id = 2020401, type = 1, next_id = 202040102, talk_content = "你是脉者对吧，我这里有个报酬十分丰厚的任务。", talk_name = "安检员",},
	[202040102] = { talk_id = 202040102, group_id = 2020401, type = 1, next_id = 202040103, talk_content = "十分丰厚？", talk_name = "司命",},
	[202040103] = { talk_id = 202040103, group_id = 2020401, type = 1, next_id = 202040104, talk_content = "不，我现在有别的事……", talk_name = "叙慕",},
	[202040104] = { talk_id = 202040104, group_id = 2020401, type = 1, next_id = 202040105, talk_content = "只是听听也无妨吧。", talk_name = "安检员",},
	[202040105] = { talk_id = 202040105, group_id = 2020401, type = 1, next_id = 202040106, talk_content = "只是听听的话。", talk_name = "叙慕",},
	[202040106] = { talk_id = 202040106, group_id = 2020401, type = 1, next_id = 202040107, talk_content = "就如你们所知的，创生地中大部分据点都受到了噬脉生物的侵扰，一些还落到了怪物手中。", talk_name = "安检员",},
	[202040107] = { talk_id = 202040107, group_id = 2020401, type = 1, next_id = 202040108, talk_content = "所以我们希望有人去定期打击那些噬脉生物，为我们清理据点。", talk_name = "安检员",},
	[202040108] = { talk_id = 202040108, group_id = 2020401, type = 1, next_id = 202040109, talk_content = "定期和清理，听起来像是你觉得那些据点肯定会丢好几次那样。", talk_name = "司命",},
	[202040109] = { talk_id = 202040109, group_id = 2020401, type = 1, next_id = 202040110, talk_content = "实际上我们就是拿怪物们没什么办法，在找到根治怪物活跃这个状况的办法之前就只能拜托脉者定起清理据点了。", talk_name = "安检员",},
	[202040110] = { talk_id = 202040110, group_id = 2020401, type = 2, options = {{"我考虑下。",202040112,},{"可以。",202040111,},}, talk_content = "如何，能帮帮我们吗？", talk_name = "安检员",},
	[202040111] = { talk_id = 202040111, group_id = 2020401, type = 3, talk_content = "谢谢，据点的位置我为你标记好了，解决了以后就请回来报告吧。", talk_name = "安检员",},
	[202040112] = { talk_id = 202040112, group_id = 2020401, type = 3, talk_content = "嗯，有空的话请务必帮帮我。", talk_name = "安检员",},
	[202040201] = { talk_id = 202040201, group_id = 2020402, type = 4, aside = "<color=#FFEBA2>司命：</color>这里就是目标的据点了吧，赶紧动手吧。", talk_content = "这里就是目标的据点了吧，赶紧动手吧。", talk_name = "司命",},
	[202040301] = { talk_id = 202040301, group_id = 2020403, type = 1, next_id = 202040302, talk_content = "我们按照约定把据点清理干净了。", talk_name = "司命",},
	[202040302] = { talk_id = 202040302, group_id = 2020403, type = 1, next_id = 202040303, talk_content = "谢谢，我这边也得到线路重新接入系统的信息了。", talk_name = "安检员",},
	[202040303] = { talk_id = 202040303, group_id = 2020403, type = 1, next_id = 202040304, talk_content = "果然拜托单独脉者是正确的，要是靠司警的话恐怕下个月都没法恢复系统呢。", talk_name = "安检员",},
	[202040304] = { talk_id = 202040304, group_id = 2020403, type = 1, next_id = 202040305, talk_content = "的确那些司警看起来是挺忙的，话说你口中的系统是什么呐？", talk_name = "司命",},
	[202040305] = { talk_id = 202040305, group_id = 2020403, type = 1, talk_content = "呃……这个就是商业机密了，总之请收下清理据点的报酬吧，假若下次还有问题我会再找你们的。", talk_name = "安检员",},
	[202050101] = { talk_id = 202050101, group_id = 2020501, type = 1, next_id = 202050102, hide_all_entity = true, hide_player = true, talk_content = "1号确认，2号确认……糟糕，又是4号这笨蛋！这样下去我的研究还怎么……", talk_name = "傅生",},
	[202050102] = { talk_id = 202050102, group_id = 2020501, type = 1, next_id = 202050103, talk_content = "请问你是在找什么东西吗？", talk_name = "叙慕",},
	[202050103] = { talk_id = 202050103, group_id = 2020501, type = 1, next_id = 202050104, talk_content = "确切来说是在找脉灵啦。唉，早知道这么麻烦，当初就不应该一时兴起选择脉灵作为研究领域……", talk_name = "傅生",},
	[202050104] = { talk_id = 202050104, group_id = 2020501, type = 1, next_id = 202050105, talk_content = "是那种圆滚滚的小生物吧。它们和其他气脉动物有什么区别吗？", talk_name = "叙慕",},
	[202050105] = { talk_id = 202050105, group_id = 2020501, type = 1, next_id = 202050106, talk_content = "你不知道，它们要是能像普通动物一样乖乖待在研究所里，我可要烧高香了。", talk_name = "傅生",},
	[202050106] = { talk_id = 202050106, group_id = 2020501, type = 1, next_id = 202050107, talk_content = "实际上，它们像气脉一样行踪不定，还兼具动物的灵性，喜欢和人类开玩笑。要我说啊，简直像一朵会说话的云一样。", talk_name = "傅生",},
	[202050107] = { talk_id = 202050107, group_id = 2020501, type = 2, options = {{"如果是脉者或许可以办到。",202050108,},{"只要跑得够快就可以吧。",202050108,},}, talk_content = "尤其是这个4号，它特别喜欢捉迷藏，还要拉着人类陪它一起玩。唉，真是麻烦的爱好……", talk_name = "傅生",},
	[202050108] = { talk_id = 202050108, group_id = 2020501, type = 3, next_id = 202050109, talk_content = "道理确实是这样。不过就像我刚才说的，抓住脉灵可不像抓普通动物那么容易。", talk_name = "傅生",},
	[202050109] = { talk_id = 202050109, group_id = 2020501, type = 3, next_id = 202050110, talk_content = "正好我这里有一台新近开发的仪器，可以定位脉灵的方位和距离，或许可以拿来试试。", talk_name = "傅生",},
	[202050110] = { talk_id = 202050110, group_id = 2020501, type = 3, next_id = 202051001, talk_content = "既然如此，可不可以拜托你帮我找找它？咳，倒也不是担心它，都是因为研究罢了……", talk_name = "傅生",},
	[202050201] = { talk_id = 202050201, group_id = 2020502, type = 4, next_id = 202050202, aside = "<color=#FFEBA2>叙慕：</color>（虽然说起来轻松，但抓脉灵应该的确不是什么容易的事。）", talk_content = "（虽然说起来轻松，但抓脉灵应该的确不是什么容易的事。）", talk_name = "叙慕",},
	[202050202] = { talk_id = 202050202, group_id = 2020502, type = 4, aside = "<color=#FFEBA2>叙慕：</color>（不妨现在就用这台仪器试试吧。）", talk_content = "（不妨现在就用这台仪器试试吧。）", talk_name = "叙慕",},
	[202050301] = { talk_id = 202050301, group_id = 2020503, type = 4, next_id = 202050302, aside = "<color=#FFEBA2>？？？：</color>竹叶长，竹笋香，绿竹林里捉迷藏。", talk_content = "竹叶长，竹笋香，绿竹林里捉迷藏。", talk_name = "？？？",},
	[202050302] = { talk_id = 202050302, group_id = 2020503, type = 4, next_id = 202050303, aside = "<color=#FFEBA2>？？？：</color>竹花黄，秋雨忙，竹下分别无相忘。", talk_content = "竹花黄，秋雨忙，竹下分别无相忘。", talk_name = "？？？",},
	[202050303] = { talk_id = 202050303, group_id = 2020503, type = 4, aside = "<color=#FFEBA2>叙慕：</color>（是脉灵的声音？应该就在不远处，再找找看吧。）", talk_content = "（是脉灵的声音？应该就在不远处，再找找看吧。）", talk_name = "叙慕",},
	[202050401] = { talk_id = 202050401, group_id = 2020504, type = 4, aside = "<color=#FFEBA2>4号：</color>嘻哈，不好不好，被看到了！", talk_content = "嘻哈，不好不好，被看到了！", talk_name = "4号",},
	[202050501] = { talk_id = 202050501, group_id = 2020505, type = 4, next_id = 202050502, aside = "<color=#FFEBA2>4号：</color>快逃快逃，捉鬼人追上来了！", talk_content = "快逃快逃，捉鬼人追上来了！", talk_name = "4号",},
	[202050502] = { talk_id = 202050502, group_id = 2020505, type = 4, aside = "<color=#FFEBA2>叙慕：</color>喂，站住！", talk_content = "喂，站住！", talk_name = "叙慕",},
	[202050601] = { talk_id = 202050601, group_id = 2020506, type = 4, next_id = 202050602, aside = "<color=#FFEBA2>4号：</color>嘻哈，才不会被你抓到呢。", talk_content = "嘻哈，才不会被你抓到呢。", talk_name = "4号",},
	[202050602] = { talk_id = 202050602, group_id = 2020506, type = 4, aside = "<color=#FFEBA2>叙慕：</color>这是什么捉迷藏的游戏吗……", talk_content = "这是什么捉迷藏的游戏吗……", talk_name = "叙慕",},
	[202050701] = { talk_id = 202050701, group_id = 2020507, type = 4, next_id = 202050702, aside = "<color=#FFEBA2>4号：</color>嘻哈，没什么力气了……", talk_content = "嘻哈，没什么力气了……", talk_name = "4号",},
	[202050702] = { talk_id = 202050702, group_id = 2020507, type = 4, aside = "<color=#FFEBA2>4号：</color>不行不行，被抓到的话，就没办法当冠军了……", talk_content = "不行不行，被抓到的话，就没办法当冠军了……", talk_name = "4号",},
	[202050801] = { talk_id = 202050801, group_id = 2020508, type = 1, next_id = 202050802, hide_all_entity = true, hide_player = true, talk_content = "呼……呼……", talk_name = "4号",},
	[202050802] = { talk_id = 202050802, group_id = 2020508, type = 2, options = {{"观察四周",202050803,},}, talk_content = "躲在这里吗，终于抓到你了。不过这个地方看起来……", talk_name = "叙慕",},
	[202050803] = { talk_id = 202050803, group_id = 2020508, type = 3, next_id = 202050804, talk_content = "（周围的气脉竹全都枯黄了。这个小家伙突然睡着，难道是因为气脉的缺失吗？）", talk_name = "叙慕",},
	[202050804] = { talk_id = 202050804, group_id = 2020508, type = 1, next_id = 202050805, talk_content = "（这里既然生长过如此茂密的气脉竹，过去一定也有不少脉灵生活吧。）", talk_name = "叙慕",},
	[202050805] = { talk_id = 202050805, group_id = 2020508, type = 1, next_id = 202050806, talk_content = "嘻哈……竹叶长，竹笋香，唔……", talk_name = "4号",},
	[202050806] = { talk_id = 202050806, group_id = 2020508, type = 1, next_id = 202050807, talk_content = "啊，居然开始说梦话了。", talk_name = "叙慕",},
	[202050807] = { talk_id = 202050807, group_id = 2020508, type = 1, next_id = 202050808, talk_content = "五十五万五千五百个数字，我早就已经数完了……", talk_name = "4号",},
	[202050808] = { talk_id = 202050808, group_id = 2020508, type = 1, talk_content = "明明已经藏了这么久……怎么还是没有人找到我呢……", talk_name = "4号",},
	[202050901] = { talk_id = 202050901, group_id = 2020509, type = 1, next_id = 202050902, hide_all_entity = true, hide_player = true, talk_content = "原来是跑到这里来了吗……", talk_name = "傅生",},
	[202050902] = { talk_id = 202050902, group_id = 2020509, type = 1, next_id = 202050903, talk_content = "你认识这个地方吗？", talk_name = "叙慕",},
	[202050903] = { talk_id = 202050903, group_id = 2020509, type = 1, next_id = 202050904, talk_content = "嗯，这里……其实是我第一次遇到4号的地方。", talk_name = "傅生",},
	[202050904] = { talk_id = 202050904, group_id = 2020509, type = 1, next_id = 202050905, talk_content = "当时这里的气脉竹受到了不小的破坏，我到附近考察时，在一座山洞里发现了它。", talk_name = "傅生",},
	[202050905] = { talk_id = 202050905, group_id = 2020509, type = 1, next_id = 202050906, talk_content = "那时它已经快要消散了，可是嘴里还在念叨着那首捉迷藏的童谣。", talk_name = "傅生",},
	[202050906] = { talk_id = 202050906, group_id = 2020509, type = 1, next_id = 202050907, talk_content = "大概是前不久还在和同伴一起玩耍吧，所以才念念不忘，走到哪里都惦记着要玩捉迷藏的游戏。", talk_name = "傅生",},
	[202050907] = { talk_id = 202050907, group_id = 2020509, type = 1, next_id = 202050908, talk_content = "可是……已经没有脉灵会找到它了吧。", talk_name = "叙慕",},
	[202050908] = { talk_id = 202050908, group_id = 2020509, type = 1, next_id = 202050909, talk_content = "在我们科研人员看来，脉灵的生命是这世间最奇妙的存在，几乎永远介于无限与须臾之间。", talk_name = "傅生",},
	[202050909] = { talk_id = 202050909, group_id = 2020509, type = 1, next_id = 202050910, talk_content = "它们拥有人类的情感和语言，却也同时拥有最无法捉摸的寿命。", talk_name = "傅生",},
	[202050910] = { talk_id = 202050910, group_id = 2020509, type = 2, options = {{"至少你会给它们新的家园。",202050912,},{"至少它们不会觉得悲伤。",202050911,},}, talk_content = "新生的脉灵像人类的孩童一样单纯，并不知道自己会随着气脉的断流而消散，或许还在盼望着伙伴可以长久地陪在自己身边吧。", talk_name = "傅生",},
	[202050911] = { talk_id = 202050911, group_id = 2020509, type = 3, next_id = 202050913, talk_content = "说的也是呢。至少我会尽力保证它们一直如此。", talk_name = "傅生",},
	[202050912] = { talk_id = 202050912, group_id = 2020509, type = 3, next_id = 202050913, talk_content = "说的也是呢。也许这就是我能做到的全部吧。", talk_name = "傅生",},
	[202050913] = { talk_id = 202050913, group_id = 2020509, type = 1, next_id = 202050914, talk_content = "那么走吧，4号。", talk_name = "傅生",},
	[202050914] = { talk_id = 202050914, group_id = 2020509, type = 1, talk_content = "该回家了。", talk_name = "傅生",},
	[202051001] = { talk_id = 202051001, group_id = 2020510, type = 2, hide_all_entity = true, hide_player = true, options = {{"可以试试。",6,},{"现在没什么时间。",202051002,},}, talk_content = "（要不要尝试一下呢？）", talk_name = "叙慕",},
	[202051002] = { talk_id = 202051002, group_id = 2020510, type = 3, talk_content = "这样啊，那我只能自己找找了。", talk_name = "傅生",},
	[202051101] = { talk_id = 202051101, group_id = 2020511, type = 1, talk_content = "那就麻烦你了。路上当心一点，那家伙很狡猾的。", talk_name = "傅生",},
	[202051201] = { talk_id = 202051201, group_id = 2020512, type = 4, aside = "<color=#FFEBA2>叙慕：</color>（好像离目标越来越远了，得赶紧回到正轨才行。）", talk_content = "（好像离目标越来越远了，得赶紧回到正轨才行。）", talk_name = "叙慕",},
	[202060101] = { talk_id = 202060101, group_id = 2020601, type = 1, next_id = 202060102, hide_all_entity = true, hide_player = true, talk_content = "你是脉者对吗？太好了，终于有办法了……", talk_name = "青崖",},
	[202060102] = { talk_id = 202060102, group_id = 2020601, type = 1, next_id = 202060103, talk_content = "慌慌张张的，是有什么急事吗？", talk_name = "叙慕",},
	[202060103] = { talk_id = 202060103, group_id = 2020601, type = 1, next_id = 202060104, talk_content = "唉，不瞒你说，我这里的确有一个急迫的委托。", talk_name = "青崖",},
	[202060104] = { talk_id = 202060104, group_id = 2020601, type = 1, next_id = 202060105, talk_content = "是这么回事，附近的郊区近来一直不太平。", talk_name = "青崖",},
	[202060105] = { talk_id = 202060105, group_id = 2020601, type = 1, next_id = 202060106, talk_content = "那里过去据说是一片乱葬岗，最近一段时间总有噬脉生物出没，还有人类的黑影和怪声。", talk_name = "青崖",},
	[202060106] = { talk_id = 202060106, group_id = 2020601, type = 1, next_id = 202060107, talk_content = "虽然近几天的噬脉生物数量已经减少了一些，但还是搞得人心惶惶。不知道是有人作怪还是什么气脉乱流，真是让人担心……", talk_name = "青崖",},
	[202060107] = { talk_id = 202060107, group_id = 2020601, type = 1, next_id = 202060108, talk_content = "黑影和怪声……难不成是闹鬼？", talk_name = "叙慕",},
	[202060108] = { talk_id = 202060108, group_id = 2020601, type = 1, next_id = 202060109, talk_content = "噫……噫！！", talk_name = "青崖",},
	[202060109] = { talk_id = 202060109, group_id = 2020601, type = 2, options = {{"现在不太方便。",202060110,},{"交给我吧。",6,},}, talk_content = "总、总之事情的情况就是这样，不知道能不能麻烦你帮忙去调查看看？", talk_name = "青崖",},
	[202060110] = { talk_id = 202060110, group_id = 2020601, type = 3, hide_all_entity = true, hide_player = true, talk_content = "既然这样，那就不麻烦你了……", talk_name = "青崖",},
	[202060301] = { talk_id = 202060301, group_id = 2020603, type = 1, talk_content = "那就请到那边去看看吧，拜托你了！", talk_name = "青崖",},
	[202060401] = { talk_id = 202060401, group_id = 2020604, type = 4, next_id = 202060402, aside = "<color=#FFEBA2>叙慕：</color>（这里就是发生怪事的地方……看起来一片太平。）", talk_content = "（这里就是发生怪事的地方……看起来一片太平。）", talk_name = "叙慕",},
	[202060402] = { talk_id = 202060402, group_id = 2020604, type = 4, aside = "<color=#FFEBA2>叙慕：</color>（但如果真的发生过怪事，就一定会留下痕迹。先在这附近调查一下吧。）", talk_content = "（但如果真的发生过怪事，就一定会留下痕迹。先在这附近调查一下吧。）", talk_name = "叙慕",},
	[202060501] = { talk_id = 202060501, group_id = 2020605, type = 1, next_id = 202060502, talk_content = "（肉眼看来没有什么异常。）", talk_name = "叙慕",},
	[202060502] = { talk_id = 202060502, group_id = 2020605, type = 1, next_id = 202060503, talk_content = "（不过空气里有种淡淡的气息……像是噬脉生物的味道。）", talk_name = "叙慕",},
	[202060503] = { talk_id = 202060503, group_id = 2020605, type = 1, talk_content = "（这样说来，这里应该有噬脉生物活动过，但数量尚且应付得来。）", talk_name = "叙慕",},
	[202060601] = { talk_id = 202060601, group_id = 2020606, type = 1, next_id = 202060602, talk_content = "（有噬脉生物的足迹，但却没有人类的。）", talk_name = "叙慕",},
	[202060602] = { talk_id = 202060602, group_id = 2020606, type = 1, next_id = 202060603, talk_content = "（所以并没有人类和它们一起活动吗。）", talk_name = "叙慕",},
	[202060603] = { talk_id = 202060603, group_id = 2020606, type = 1, talk_content = "（眼下的证据还不充足，再找找看吧。）", talk_name = "叙慕",},
	[202060701] = { talk_id = 202060701, group_id = 2020607, type = 1, next_id = 202060702, talk_content = "（石头上刻着模糊的字迹，只能辨认出“义人”“长眠”“冢”几个字）", talk_name = "叙慕",},
	[202060702] = { talk_id = 202060702, group_id = 2020607, type = 1, next_id = 202060703, talk_content = "（很明显，这是一块墓碑。不知道它的主人是谁呢？）", talk_name = "叙慕",},
	[202060703] = { talk_id = 202060703, group_id = 2020607, type = 1, next_id = 202060704, talk_content = "（这里的噬脉生物气息更重，说不定就是散发气息的源头了。）", talk_name = "叙慕",},
