	[202060901] = { talk_id = 202060901, group_id = 2020609, type = 1, next_id = 202060902, hide_all_entity = true, hide_player = true, talk_content = "有异动！何处有异动！", talk_name = "蒙面怪人",},
	[202060902] = { talk_id = 202060902, group_id = 2020609, type = 1, next_id = 202060903, talk_content = "待属下一一清剿！！", talk_name = "蒙面怪人",},
	[202060903] = { talk_id = 202060903, group_id = 2020609, type = 1, next_id = 202060904, talk_content = "等一下，你是什么人？", talk_name = "叙慕",},
	[202060904] = { talk_id = 202060904, group_id = 2020609, type = 1, next_id = 202060905, talk_content = "当然是奉命守护帝江之人！请放心，属下手中的武器将永远对准胆敢破坏这片土地的敌人！", talk_name = "蒙面怪人",},
	[202060905] = { talk_id = 202060905, group_id = 2020609, type = 1, next_id = 202060906, talk_content = "可是我刚才连你的影子都没看到，你是从什么地方出现的……", talk_name = "叙慕",},
	[202060906] = { talk_id = 202060906, group_id = 2020609, type = 1, talk_content = "先不说这些，又有麻烦来了。", talk_name = "叙慕",},
	[202061001] = { talk_id = 202061001, group_id = 2020610, type = 4, next_id = 202061002, aside = "<color=#FFEBA2>叙慕：</color>（本以为刚才已经算是结束了，没想到还会有怪物冒出来。）", talk_content = "（本以为刚才已经算是结束了，没想到还会有怪物冒出来。）", talk_name = "叙慕",},
