	[202061004] = { talk_id = 202061004, group_id = 2020610, type = 4, aside = "<color=#FFEBA2>叙慕：</color>嘴上功夫可以先停一停了！", talk_content = "嘴上功夫可以先停一停了！", talk_name = "叙慕",},
	[202061101] = { talk_id = 202061101, group_id = 2020611, type = 4, next_id = 202061102, aside = "<color=#FFEBA2>叙慕：</color>（数量又减少了，这次应该就算是终结了吧。）", talk_content = "（数量又减少了，这次应该就算是终结了吧。）", talk_name = "叙慕",},
	[202061102] = { talk_id = 202061102, group_id = 2020611, type = 4, aside = "<color=#FFEBA2>叙慕：</color>（好，一鼓作气消灭它们吧。）", talk_content = "（好，一鼓作气消灭它们吧。）", talk_name = "叙慕",},
	[202061201] = { talk_id = 202061201, group_id = 2020612, type = 2, hide_all_entity = true, hide_player = true, options = {{"不要什么功劳都抢啊。",202061202,},{"消灭敌人的明明是我吧……",202061202,},}, talk_content = "属下已经将敌人尽数剿灭了！属下以手中兵刃之名保卫了这片土地！", talk_name = "蒙面怪人",},
	[202061202] = { talk_id = 202061202, group_id = 2020612, type = 3, next_id = 202061203, talk_content = "刚才一走过来就看到你消灭怪物的英姿了，所以现在已经没有怪物了吗？真是太感谢了！", talk_name = "青崖",},
	[202061203] = { talk_id = 202061203, group_id = 2020612, type = 3, next_id = 202061204, talk_content = "嗯，的确已经没有怪物冒出来了，不过还有一件事让我有点在意。", talk_name = "叙慕",},
	[202061204] = { talk_id = 202061204, group_id = 2020612, type = 3, next_id = 202061205, talk_content = "明明已经没有了怪物，但附近却还萦绕着噬脉生物的气息。是你身上有什么特殊的东西吗？", talk_name = "叙慕",},
	[202061205] = { talk_id = 202061205, group_id = 2020612, type = 3, next_id = 202061206, talk_content = "怎么可能呢，我只是个普通人，根本没有沾染那种东西的机会呀。会不会是你身上……", talk_name = "青崖",},
	[202061206] = { talk_id = 202061206, group_id = 2020612, type = 3, next_id = 202061207, talk_content = "不，也不是从我身上散发出来的。那也就是说……", talk_name = "叙慕",},
	[202061207] = { talk_id = 202061207, group_id = 2020612, type = 3, next_id = 202061208, talk_content = "……", talk_name = "叙慕",},
	[202061208] = { talk_id = 202061208, group_id = 2020612, type = 3, next_id = 202061209, talk_content = "……", talk_name = "青崖",},
	[202061209] = { talk_id = 202061209, group_id = 2020612, type = 3, next_id = 202061210, talk_content = "诸位这话是什么意思？", talk_name = "蒙面怪人",},
	[202061210] = { talk_id = 202061210, group_id = 2020612, type = 2, options = {{"为什么不问问你身边这个人呢？",202061211,},{"你说的黑影好像就在我们面前。",202061211,},}, talk_content = "别、别吓我啊，所以那种气息到底是从哪里传来的？该不会和之前说过的黑影什么的有关吧？", talk_name = "青崖",},
	[202061211] = { talk_id = 202061211, group_id = 2020612, type = 3, next_id = 202061212, talk_content = "咦……咦？！", talk_name = "青崖",},
	[202061212] = { talk_id = 202061212, group_id = 2020612, type = 3, talk_content = "可是这里除了我们……根本就没有别人呀……", talk_name = "青崖",},
	[202061301] = { talk_id = 202061301, group_id = 2020613, type = 1, next_id = 202061302, talk_content = "完了完了，不会真的碰到什么不干净的东西……总之我先走了！你处理完事情到城里找我就好！", talk_name = "青崖",},
	[202061302] = { talk_id = 202061302, group_id = 2020613, type = 1, next_id = 202061303, hide_all_entity = true, hide_player = true, talk_content = "欺人太甚！真是欺人太甚！属下明明尽职尽责，为何会有人对属下如此视而不见，还以污名诋毁属下！", talk_name = "蒙面怪人",},
	[202061303] = { talk_id = 202061303, group_id = 2020613, type = 1, next_id = 202061304, talk_content = "不，我看有些问题倒是值得思考一下。", talk_name = "叙慕",},
	[202061304] = { talk_id = 202061304, group_id = 2020613, type = 2, options = {{"你好像不是人类。",202061307,},{"你好像在装神弄鬼。",202061305,},}, talk_content = "（刚刚的地面上没有人类的足迹，加上那块墓碑和那人的话……）", talk_name = "叙慕",},
	[202061305] = { talk_id = 202061305, group_id = 2020613, type = 3, next_id = 202061306, talk_content = "装神弄鬼？！属下一生清白，何必装神弄鬼！", talk_name = "蒙面怪人",},
	[202061306] = { talk_id = 202061306, group_id = 2020613, type = 3, next_id = 202061308, talk_content = "（不对，好像的确是错怪他了。）", talk_name = "叙慕",},
	[202061307] = { talk_id = 202061307, group_id = 2020613, type = 3, next_id = 202061308, talk_content = "没错，这就是唯一的解释。", talk_name = "叙慕",},
	[202061308] = { talk_id = 202061308, group_id = 2020613, type = 1, next_id = 202061309, talk_content = "难道你是说……属下已经……", talk_name = "蒙面怪人",},
	[202061309] = { talk_id = 202061309, group_id = 2020613, type = 1, next_id = 202061310, talk_content = "你已经不存在在这个世界上了，所以脉者以外的普通人根本看不到你。", talk_name = "叙慕",},
	[202061310] = { talk_id = 202061310, group_id = 2020613, type = 1, next_id = 202061311, talk_content = "那块无名的墓碑也是属于你的吧，因为你被葬在这里，所以才会一直在附近徘徊。", talk_name = "叙慕",},
	[202061311] = { talk_id = 202061311, group_id = 2020613, type = 1, next_id = 202061312, talk_content = "可是属下早已对封侯大人立誓，只要一息尚存，就要守护创生地的安宁……", talk_name = "蒙面怪人",},
	[202061312] = { talk_id = 202061312, group_id = 2020613, type = 1, next_id = 202061313, talk_content = "啊……原来是这样吗？属下记得自己不久前从天月来到地面处理紧急动乱事故。", talk_name = "蒙面怪人",},
	[202061313] = { talk_id = 202061313, group_id = 2020613, type = 1, next_id = 202061314, talk_content = "但属下出师不利，在动乱中被一群贼人暗害，之后便陷入一片混沌。", talk_name = "蒙面怪人",},
	[202061314] = { talk_id = 202061314, group_id = 2020613, type = 1, next_id = 202061315, talk_content = "想来属下出现在此地，应是被居民发现后草草下葬……", talk_name = "蒙面怪人",},
	[202061315] = { talk_id = 202061315, group_id = 2020613, type = 1, next_id = 202061316, talk_content = "眼下灾厄刚刚止息，太阳的光辉还没有照遍天上与地下的每一个角落，但属下却再也无法追逐这荣耀的光辉了……", talk_name = "蒙面怪人",},
	[202061316] = { talk_id = 202061316, group_id = 2020613, type = 1, next_id = 202061317, talk_content = "太阳……你认为是太阳守护了这片土地吗？", talk_name = "叙慕",},
	[202061317] = { talk_id = 202061317, group_id = 2020613, type = 1, next_id = 202061318, talk_content = "如若太阳没有升起，创生地将万古如夜。", talk_name = "蒙面怪人",},
	[202061318] = { talk_id = 202061318, group_id = 2020613, type = 1, next_id = 202061319, talk_content = "属下的夙愿便是守护太阳，守护太阳与封侯治下的万物。", talk_name = "蒙面怪人",},
	[202061319] = { talk_id = 202061319, group_id = 2020613, type = 1, next_id = 202061320, talk_content = "是属下办事不力，辜负了封侯大人的期待……", talk_name = "蒙面怪人",},
	[202061320] = { talk_id = 202061320, group_id = 2020613, type = 1, talk_content = "（场面有点难以控制了，先让他冷静一下吧。）", talk_name = "叙慕",},
	[202061401] = { talk_id = 202061401, group_id = 2020614, type = 1, next_id = 202061402, hide_all_entity = true, hide_player = true, talk_content = "（果然对于很多人来说，太阳已经完全成了圣物，就算告诉他们过去发生的一切，他们也不会相信吧。）", talk_name = "叙慕",},
	[202061402] = { talk_id = 202061402, group_id = 2020614, type = 1, next_id = 202061403, talk_content = "（尤其是那个家伙……太阳对他而言已经成为了无可替代的信念。）", talk_name = "叙慕",},
	[202061403] = { talk_id = 202061403, group_id = 2020614, type = 1, next_id = 202061404, talk_content = "（虽然不知道他身上的气息还会不会招来怪物，但他对附近居民的困扰已经摆在眼前了。）", talk_name = "叙慕",},
	[202061404] = { talk_id = 202061404, group_id = 2020614, type = 2, options = {{"消灭怪人",202061405,},{"放走怪人",202061413,},}, talk_content = "（要怎么做呢？干脆送他上路，还是放任他继续游荡下去？）", talk_name = "叙慕",},
	[202061405] = { talk_id = 202061405, group_id = 2020614, type = 3, next_id = 202061406, talk_content = "游历的脉者，你果然还是决定那么做吗？", talk_name = "蒙面怪人",},
	[202061406] = { talk_id = 202061406, group_id = 2020614, type = 3, next_id = 202061407, talk_content = "你都猜到了吗？", talk_name = "叙慕",},
	[202061407] = { talk_id = 202061407, group_id = 2020614, type = 3, next_id = 202061408, talk_content = "从我知道自己已经不在人世的那一刻起，我就明白自己已经不再是守护者，而是他人眼中的邪祟。", talk_name = "蒙面怪人",},
	[202061408] = { talk_id = 202061408, group_id = 2020614, type = 3, next_id = 202061409, talk_content = "“凡人若想凝视太阳的光焰，自己的身躯便会燃烧殆尽”，从选择靠近太阳的那天，我就已经做好了这样的觉悟。", talk_name = "蒙面怪人",},
	[202061409] = { talk_id = 202061409, group_id = 2020614, type = 3, next_id = 202061410, talk_content = "只是在消散之前，我还有最后一个愿望。", talk_name = "蒙面怪人",},
	[202061410] = { talk_id = 202061410, group_id = 2020614, type = 3, next_id = 202061411, talk_content = "说吧。", talk_name = "叙慕",},
	[202061411] = { talk_id = 202061411, group_id = 2020614, type = 3, next_id = 202061412, talk_content = "给我最后一次挥刀的机会，让我同你堂堂正正地比试一次。", talk_name = "蒙面怪人",},
	[202061412] = { talk_id = 202061412, group_id = 2020614, type = 3, talk_content = "让属下……问心无愧地倒在日光之下。", talk_name = "蒙面怪人",},
	[202061413] = { talk_id = 202061413, group_id = 2020614, type = 3, next_id = 202061414, talk_content = "游历的脉者，你果然还是打算消灭我吗？", talk_name = "蒙面怪人",},
	[202061414] = { talk_id = 202061414, group_id = 2020614, type = 3, next_id = 202061415, talk_content = "你都猜到了吗？", talk_name = "叙慕",},
	[202061415] = { talk_id = 202061415, group_id = 2020614, type = 3, next_id = 202061416, talk_content = "从我知道自己已经不在人世的那一刻起，我就明白自己已经不再是守护者，而是他人眼中的邪祟。", talk_name = "蒙面怪人",},
	[202061416] = { talk_id = 202061416, group_id = 2020614, type = 3, next_id = 202061417, talk_content = "“凡人若想凝视太阳的光焰，自己的身躯便会燃烧殆尽”，从选择靠近太阳的那天，我就已经做好了这样的觉悟。", talk_name = "蒙面怪人",},
	[202061417] = { talk_id = 202061417, group_id = 2020614, type = 3, next_id = 202061418, talk_content = "若非如此，我也想象不到自己该去往何方了。", talk_name = "蒙面怪人",},
	[202061418] = { talk_id = 202061418, group_id = 2020614, type = 3, next_id = 202061419, talk_content = "把太阳当成人生的目标吗，我大概明白了。", talk_name = "叙慕",},
	[202061419] = { talk_id = 202061419, group_id = 2020614, type = 3, next_id = 202061420, talk_content = "虽然刚才也这么想过，但是我现在并不打算消灭你。", talk_name = "叙慕",},
	[202061420] = { talk_id = 202061420, group_id = 2020614, type = 3, next_id = 202061421, talk_content = "既然已经为太阳赌上了一生，今后就暂时休息一下，做点想做的事吧。", talk_name = "叙慕",},
	[202061421] = { talk_id = 202061421, group_id = 2020614, type = 3, next_id = 202061422, talk_content = "想做的事吗……这我从来没有想过。", talk_name = "蒙面怪人",},
	[202061422] = { talk_id = 202061422, group_id = 2020614, type = 3, next_id = 202061423, talk_content = "我这一生都为太阳而活。太阳刚刚降临时，我央求过母亲在门前种下一棵树苗。", talk_name = "蒙面怪人",},
	[202061423] = { talk_id = 202061423, group_id = 2020614, type = 3, next_id = 202061424, talk_content = "我说，只要树苗长大，我就可以爬到树顶，伸手就能够碰到太阳。", talk_name = "蒙面怪人",},
	[202061424] = { talk_id = 202061424, group_id = 2020614, type = 3, next_id = 202061425, talk_content = "后来，我终于如愿以偿地碰到了太阳。但无论是那棵树还是我的母亲，都已经再也见不到了。", talk_name = "蒙面怪人",},
	[202061425] = { talk_id = 202061425, group_id = 2020614, type = 3, next_id = 202061426, talk_content = "那么……就凭借记忆再回那里看看吧。", talk_name = "蒙面怪人",},
	[202061426] = { talk_id = 202061426, group_id = 2020614, type = 3, next_id = 202061427, talk_content = "就在此地分别吧，远道而来的脉者。", talk_name = "蒙面怪人",},
	[202061427] = { talk_id = 202061427, group_id = 2020614, type = 3, next_id = 202061428, talk_content = "但我只是孤魂野鬼，不知何时就会消散不见，你我怕是无缘再遇了。", talk_name = "蒙面怪人",},
	[202061428] = { talk_id = 202061428, group_id = 2020614, type = 3, next_id = 202061429, talk_content = "不过幸好我还记得你。", talk_name = "蒙面怪人",},
	[202061429] = { talk_id = 202061429, group_id = 2020614, type = 3, next_id = 202061430, talk_content = "分别之前，要不要告诉我你的名字？", talk_name = "司命",},
	[202061430] = { talk_id = 202061430, group_id = 2020614, type = 2, options = {{"再见，阿余。",202061431,},{"很高兴认识你，阿余。",202061431,},}, talk_content = "阿余，那是我成为近卫之前的名字。已经很久没有人这样叫过我了。", talk_name = "蒙面怪人",},
	[202061431] = { talk_id = 202061431, group_id = 2020614, type = 3, talk_content = "最后一次……谢谢你。", talk_name = "阿余",},
	[202061501] = { talk_id = 202061501, group_id = 2020615, type = 1, next_id = 202061502, hide_all_entity = true, hide_player = true, talk_content = "（果然是一缕游魂，倒下之后就消失不见了。）", talk_name = "叙慕",},
	[202061502] = { talk_id = 202061502, group_id = 2020615, type = 1, next_id = 202061503, talk_content = "（从他身上掉下一个铭牌，上面写着“阿余”两个字，这应该就是他的名字吧。）", talk_name = "叙慕",},
	[202061503] = { talk_id = 202061503, group_id = 2020615, type = 1, next_id = 202061504, talk_content = "（阿余……听起来只是一个普通人的名字。）", talk_name = "叙慕",},
	[202061504] = { talk_id = 202061504, group_id = 2020615, type = 1, next_id = 202061505, talk_content = "（追随太阳和反抗太阳，听上去像是性命攸关的重大抉择，但做出这些抉择的，绝大多数也都只是普通人而已。）", talk_name = "叙慕",},
	[202061505] = { talk_id = 202061505, group_id = 2020615, type = 1, talk_content = "那么就在此分别吧，阿余。", talk_name = "叙慕",},
	[202061601] = { talk_id = 202061601, group_id = 2020616, type = 1, talk_content = "（石头上有一行崭新的刻文，“阿余长眠于此”。）", talk_name = "叙慕",},
	[202061701] = { talk_id = 202061701, group_id = 2020617, type = 2, hide_all_entity = true, hide_player = true, options = {{"举手之劳而已。",202061702,},{"不必客气。",202061702,},}, talk_content = "真不知道该怎么感谢你，要不是你出手帮忙，我们还不知道要担惊受怕多久呢。", talk_name = "青崖",},
	[202061702] = { talk_id = 202061702, group_id = 2020617, type = 3, next_id = 202061703, talk_content = "是你太客气了。给你的报酬也已经准备好了，请你一定收下！", talk_name = "青崖",},
	[202061703] = { talk_id = 202061703, group_id = 2020617, type = 2, options = {{"我说了只是闹鬼而已。",202061704,},{"没有人，你听错了。",202061704,},}, talk_content = "不过有件事还想问问你。之前说到的黑影，到底是什么人啊？", talk_name = "青崖",},
	[202061704] = { talk_id = 202061704, group_id = 2020617, type = 3, talk_content = "啊……啊啊？！", talk_name = "青崖",},
	[202070101] = { talk_id = 202070101, group_id = 2020701, type = 1, next_id = 202070102, talk_content = "嗯，说得有道理。", talk_name = "叙慕",},
	[202070102] = { talk_id = 202070102, group_id = 2020701, type = 1, talk_content = "现在就开始吧……", talk_name = "叙慕",},
	[202070201] = { talk_id = 202070201, group_id = 2020702, type = 1, next_id = 202070202, talk_content = "钟悬的大门似乎被一股无形的力量堵住了，就算用佩从的开锁能力也无法打开。", talk_name = "青乌",},
	[202070202] = { talk_id = 202070202, group_id = 2020702, type = 1, talk_content = "试试用其他方法进去吧。", talk_name = "青乌",},
	[202070301] = { talk_id = 202070301, group_id = 2020703, type = 1, talk_content = "（听起来不是什么好事，去看看吧。）", talk_name = "青乌",},
	[202070401] = { talk_id = 202070401, group_id = 2020704, type = 4, aside = "<color=#FFEBA2>恶来：</color>哇！你是什么人……不对，你开的那是什么东西！", talk_content = "哇！你是什么人……不对，你开的那是什么东西！", talk_name = "恶来",},
	[202070501] = { talk_id = 202070501, group_id = 2020705, type = 1, talk_content = "（大悬钟的门被箱子抵住了，需要清除障碍才能正常开启。）",},
	[202070601] = { talk_id = 202070601, group_id = 2020706, type = 4, aside = "<color=#FFEBA2>恶来：</color>兄弟们，给我上！", talk_content = "兄弟们，给我上！", talk_name = "恶来",},
	[202070701] = { talk_id = 202070701, group_id = 2020707, type = 1, next_id = 202070702, talk_content = "我刚刚看到了，是你解决了楼上的家伙对吧，真是太帅了！", talk_name = "符淮",},
	[202070702] = { talk_id = 202070702, group_id = 2020707, type = 1, talk_content = "对了，虽然不算是什么好东西，还请你收下这个吧。", talk_name = "符淮",},
	[202080101] = { talk_id = 202080101, group_id = 2020801, type = 1, next_id = 202080102, hide_all_entity = true, hide_player = true, talk_content = "每次都是一个人跑走又一个人遇险，就不能跟我一起行动吗，真是的……", talk_name = "山杏",},
	[202080102] = { talk_id = 202080102, group_id = 2020801, type = 2, options = {{"发生什么事了吗？",202080103,},}, talk_content = "现在，现在该怎么办……不管了，还是先报警吧！", talk_name = "山杏",},
	[202080103] = { talk_id = 202080103, group_id = 2020801, type = 3, next_id = 202080104, talk_content = "啊，你是位脉者对吗？", talk_name = "山杏",},
	[202080104] = { talk_id = 202080104, group_id = 2020801, type = 2, options = {{"有点急事，可以先联系维安警吗？",202080109,},{"交给我吧。",202080105,},}, talk_content = "我的姐姐被噬脉生物困住了，可以请你帮忙救救她吗？", talk_name = "山杏",},
	[202080105] = { talk_id = 202080105, group_id = 2020801, type = 3, next_id = 202080106, talk_content = "太好了，太好了，这样我就放心了……", talk_name = "山杏",},
	[202080106] = { talk_id = 202080106, group_id = 2020801, type = 3, next_id = 202080107, talk_content = "都怪她一直坚持要带我到处求医，还信了什么偏方，说用吸收噬脉生物气息生长的未明草煎药就可以治好我的病……", talk_name = "山杏",},
	[202080107] = { talk_id = 202080107, group_id = 2020801, type = 3, next_id = 202080108, talk_content = "不管我怎么劝她都不听，非要独自到噬脉生物聚集的地方采药，咳咳……结果到最后还是自己被困住……", talk_name = "山杏",},
	[202080108] = { talk_id = 202080108, group_id = 2020801, type = 3, next_id = 6, talk_content = "先不多说了，姐姐就在前面那个路口，请你把她救出来，带她回来找我吧。", talk_name = "山杏",},
	[202080109] = { talk_id = 202080109, group_id = 2020801, type = 3, talk_content = "这样吗……也好，那我现在就报警求助吧。", talk_name = "山杏",},
	[202080201] = { talk_id = 202080201, group_id = 2020802, type = 4, next_id = 202080202, aside = "<color=#FFEBA2>？？？：</color>嘁，好疼！这些家伙的武器上到底有什么东西……", talk_content = "嘁，好疼！这些家伙的武器上到底有什么东西……", talk_name = "？？？",},
	[202080202] = { talk_id = 202080202, group_id = 2020802, type = 4, next_id = 202080203, aside = "<color=#FFEBA2>？？？：</color>别过来，我警告你们！要是敢弄坏我包里的东西别怪我不客气！", talk_content = "别过来，我警告你们！要是敢弄坏我包里的东西别怪我不客气！", talk_name = "？？？",},
	[202080203] = { talk_id = 202080203, group_id = 2020802, type = 4, aside = "<color=#FFEBA2>青乌：</color>（那个女孩应该就是我要找的人了，先动手解决麻烦吧。）", talk_content = "（那个女孩应该就是我要找的人了，先动手解决麻烦吧。）", talk_name = "青乌",},
	[202080301] = { talk_id = 202080301, group_id = 2020803, type = 4, next_id = 202080302, aside = "<color=#FFEBA2>山棠：</color>谢谢谢谢，你来得太及时了！", talk_content = "谢谢谢谢，你来得太及时了！", talk_name = "山棠",},
	[202080302] = { talk_id = 202080302, group_id = 2020803, type = 4, next_id = 202080303, aside = "<color=#FFEBA2>青乌：</color>是你妹妹委托我来救你的。", talk_content = "是你妹妹委托我来救你的。", talk_name = "青乌",},
	[202080303] = { talk_id = 202080303, group_id = 2020803, type = 4, next_id = 202080304, aside = "<color=#FFEBA2>山棠：</color>什么，山杏也跟来了？她没有什么事吧？", talk_content = "什么，山杏也跟来了？她没有什么事吧？", talk_name = "山棠",},
	[202080304] = { talk_id = 202080304, group_id = 2020803, type = 4, aside = "<color=#FFEBA2>青乌：</color>她没有碰上怪物。比起这个，先保护好自己吧。", talk_content = "她没有碰上怪物。比起这个，先保护好自己吧。", talk_name = "青乌",},
	[202080401] = { talk_id = 202080401, group_id = 2020804, type = 1, next_id = 202080402, hide_all_entity = true, hide_player = true, talk_content = "呼，总算没事了。不愧是脉者，三下五除二就解决了一大群怪物！", talk_name = "山棠",},
	[202080402] = { talk_id = 202080402, group_id = 2020804, type = 2, options = {{"下次不要随便冒险了。",202080404,},{"没有受伤吧？",202080403,},}, talk_content = "要是我也能学几招就可以保护山杏了，可惜……", talk_name = "山棠",},
	[202080403] = { talk_id = 202080403, group_id = 2020804, type = 3, next_id = 202080406, talk_content = "没事没事，就是刚才和怪物交手的时候挨了几下，这种伤算不了什么。", talk_name = "山棠",},
	[202080404] = { talk_id = 202080404, group_id = 2020804, type = 3, next_id = 202080405, talk_content = "知道了，下次我肯定小心一点，绝对不会惊醒那些怪物的。", talk_name = "山棠",},
	[202080405] = { talk_id = 202080405, group_id = 2020804, type = 3, next_id = 202080406, talk_content = "（结果还是要来吗……好像有点理解山杏的心情了。）", talk_name = "青乌",},
	[202080406] = { talk_id = 202080406, group_id = 2020804, type = 1, next_id = 202080407, talk_content = "总之谢谢你了，要不是你赶来帮忙，我还不知道该怎么办呢。", talk_name = "山棠",},
	[202080407] = { talk_id = 202080407, group_id = 2020804, type = 1, talk_content = "我们现在赶紧回去找山杏会合吧。", talk_name = "山棠",},
	[202080501] = { talk_id = 202080501, group_id = 2020805, type = 1, next_id = 202080502, hide_all_entity = true, hide_player = true, talk_content = "你们终于回来了……不对，谢谢你把我姐姐这个大麻烦带回来。", talk_name = "山杏",},
	[202080502] = { talk_id = 202080502, group_id = 2020805, type = 1, next_id = 202080503, talk_content = "好了我知道错了，你别生气嘛。下次，下次我一定不再到处乱跑了！", talk_name = "山棠",},
	[202080503] = { talk_id = 202080503, group_id = 2020805, type = 1, next_id = 202080504, talk_content = "因为那个赤脚医生一句话就自己闯到怪物堆里，还说什么不让我担心，咳咳……", talk_name = "山杏",},
	[202080504] = { talk_id = 202080504, group_id = 2020805, type = 1, next_id = 202080505, talk_content = "不试试看也不知道效果嘛。再说这药草是吸收噬脉生物气息长成的，听起来就有以毒攻毒的效果，说不定真能治好你那种怪病呢。", talk_name = "山棠",},
	[202080505] = { talk_id = 202080505, group_id = 2020805, type = 2, options = {{"你们说的怪病是？",202080506,},}, talk_content = "反正你就只知道往危险的地方跑，早知道就不该救你！", talk_name = "山杏",},
	[202080506] = { talk_id = 202080506, group_id = 2020805, type = 3, next_id = 202080507, talk_content = "说来有点复杂，但我妹妹的确得了一种怪病。", talk_name = "山棠",},
	[202080507] = { talk_id = 202080507, group_id = 2020805, type = 3, next_id = 202080508, talk_content = "我们一家人过去是在地上生活的，后来因为气脉枯萎才搬去了天月城，山杏也是在天月城上出生的。", talk_name = "山棠",},
	[202080508] = { talk_id = 202080508, group_id = 2020805, type = 3, next_id = 202080509, talk_content = "但山杏一出生就身体虚弱，还得了一种所有出生在城里的人都会有的怪病。", talk_name = "山棠",},
	[202080509] = { talk_id = 202080509, group_id = 2020805, type = 3, next_id = 202080510, talk_content = "天月城的医生都没法治好这种怪病，所以我才要带着她到地上来。只要能治好她，我绝对是什么都愿意做的。", talk_name = "山棠",},
	[202080510] = { talk_id = 202080510, group_id = 2020805, type = 3, next_id = 202080511, talk_content = "我都说了我不需要你这样……咳咳，爸妈也希望你好好待在天月城，和家人在一起。", talk_name = "山杏",},
	[202080511] = { talk_id = 202080511, group_id = 2020805, type = 3, next_id = 202080512, talk_content = "但现在治好你的机会就放在眼前，总不能尝试都不尝试一下吧。我已经采到那种未明草了，我们回去试试，一定有用的！", talk_name = "山棠",},
	[202080512] = { talk_id = 202080512, group_id = 2020805, type = 3, next_id = 202080513, talk_content = "嗯……反正都这么费力地采回来了，就再相信你一次吧。", talk_name = "山杏",},
	[202080513] = { talk_id = 202080513, group_id = 2020805, type = 2, options = {{"回去好好养病吧。",202080514,},{"不必客气。",202080514,},}, talk_content = "这次真是麻烦你了，脉者。给你的报酬我已经准备好了，虽然钱数不多，但还是希望你能收下。", talk_name = "山杏",},
	[202080514] = { talk_id = 202080514, group_id = 2020805, type = 3, talk_content = "嗯，假如之后有机会，就请到天月城再会吧。", talk_name = "山杏",},
	[202080601] = { talk_id = 202080601, group_id = 2020806, type = 1, talk_content = "请你快去救我姐姐吧，不然我担心她真的会受伤……", talk_name = "山杏",},
	[202080701] = { talk_id = 202080701, group_id = 2020807, type = 1, talk_content = "我们现在就回去找山杏吧，她肯定等急了。", talk_name = "山棠",},
	[202090101] = { talk_id = 202090101, group_id = 2020901, type = 4, next_id = 202090102, aside = "<color=#FFEBA2>袁二斧：</color>平时的鏖战不就是大家伙一起打打架、喝点酒乐一乐吗，怎么这次搞这么大阵仗？", talk_content = "平时的鏖战不就是大家伙一起打打架、喝点酒乐一乐吗，怎么这次搞这么大阵仗？", talk_name = "袁二斧",},
	[202090102] = { talk_id = 202090102, group_id = 2020901, type = 4, next_id = 202090103, aside = "<color=#FFEBA2>董三钺：</color>这你可就不知道了。这次鏖战的奖品据说是好货，看见没？就那边那把宝剑，王土钢雕金的，据说在市场上能卖大几十万呢！", talk_content = "这你可就不知道了。这次鏖战的奖品据说是好货，看见没？就那边那把宝剑，王土钢雕金的，据说在市场上能卖大几十万呢！", talk_name = "董三钺",},
	[202090103] = { talk_id = 202090103, group_id = 2020901, type = 4, next_id = 202090104, aside = "<color=#FFEBA2>袁二斧：</color>真的假的，这么大的馅饼能掉我们头上？", talk_content = "真的假的，这么大的馅饼能掉我们头上？", talk_name = "袁二斧",},
	[202090104] = { talk_id = 202090104, group_id = 2020901, type = 4, aside = "<color=#FFEBA2>董三钺：</color>想得美！没看见二公子的车停在那边吗？有他在场，咱们别说被馅饼砸了，连闻闻味儿的资格都没有！", talk_content = "想得美！没看见二公子的车停在那边吗？有他在场，咱们别说被馅饼砸了，连闻闻味儿的资格都没有！", talk_name = "董三钺",},
	[202090201] = { talk_id = 202090201, group_id = 2020902, type = 2, options = {{"听说奖品不错，我也想试试。",202090202,},{"你们说的鏖战有意思吗？",202090202,},}, talk_content = "那边那个，对，说你呢，没人告诉过你偷听别人说话很没礼貌吗？", talk_name = "董三钺",},
	[202090202] = { talk_id = 202090202, group_id = 2020902, type = 3, next_id = 202090203, talk_content = "去去去，不该打听的事别瞎打听。", talk_name = "董三钺",},
	[202090203] = { talk_id = 202090203, group_id = 2020902, type = 3, next_id = 202090204, talk_content = "我们这鏖战是有准入门槛的，要是什么猫猫狗狗都能参加，那可要出大乱子了。", talk_name = "董三钺",},
	[202090204] = { talk_id = 202090204, group_id = 2020902, type = 3, talk_content = "说得对，这不是你该来的地方，劝你从哪儿来回哪儿去吧。", talk_name = "袁二斧",},
	[202090301] = { talk_id = 202090301, group_id = 2020903, type = 1, next_id = 202090302, talk_content = "哎哟，你这长相，让我想想……你就是那个青乌吧？之前就在城里见过你了。", talk_name = "孔方儿",},
	[202090302] = { talk_id = 202090302, group_id = 2020903, type = 2, options = {{"我是来报名参赛的。",202090303,},{"只是随便看看。",202090314,},}, talk_content = "你是来给鏖战下注的对吗？今天街坊邻居不多，算你个优惠价，200约币的押金不过分吧？", talk_name = "孔方儿",},
	[202090303] = { talk_id = 202090303, group_id = 2020903, type = 3, next_id = 202090304, talk_content = "报名？这……实话跟你说，我们这场鏖战不支持外地人报名。", talk_name = "孔方儿",},
	[202090304] = { talk_id = 202090304, group_id = 2020903, type = 3, next_id = 202090305, talk_content = "我听说天月城的鏖战向来是人人皆可参与，怎么还有外地人不能报名的规矩？", talk_name = "青乌",},
	[202090305] = { talk_id = 202090305, group_id = 2020903, type = 3, next_id = 202090306, talk_content = "哎呀这个……算了算了，看在你帮过不少街坊的份上，我就偷偷告诉你……", talk_name = "孔方儿",},
	[202090306] = { talk_id = 202090306, group_id = 2020903, type = 2, options = {{"浊水帮？",202090307,},}, talk_content = "这鏖战是浊水帮举办的，那边那群家伙都是他们自己人，获胜者也是早就内定好的。", talk_name = "孔方儿",},
	[202090307] = { talk_id = 202090307, group_id = 2020903, type = 3, next_id = 202090308, talk_content = "就是个盘踞在附近居民区的小帮派，本事大小先不提，狐假虎威上是的确有一套的。咳……你可别跟他们说这是我告诉你的。", talk_name = "孔方儿",},
	[202090308] = { talk_id = 202090308, group_id = 2020903, type = 3, next_id = 202090309, talk_content = "那把剑又是什么来头？", talk_name = "青乌",},
	[202090309] = { talk_id = 202090309, group_id = 2020903, type = 3, next_id = 202090310, talk_content = "那剑听说原本是附近一家铸剑行的镇店之宝，很早以前就被浊水帮老大看上了。", talk_name = "孔方儿",},
	[202090310] = { talk_id = 202090310, group_id = 2020903, type = 3, next_id = 202090311, talk_content = "后来那家店破产，浊水帮老大就趁机提议资产充公，然后要求举办这场鏖战，把那把剑定为鏖战的头彩奖品。", talk_name = "孔方儿",},
	[202090311] = { talk_id = 202090311, group_id = 2020903, type = 2, options = {{"得治治这些家伙。",202090312,},{"这不是还是明抢吗？",202090312,},}, talk_content = "听说铸剑行老板的后人不答应，但也拗不过浊水帮的人。虽然明知道这次比赛的头彩肯定是他们的，可大家谁都不敢说什么。", talk_name = "孔方儿",},
	[202090312] = { talk_id = 202090312, group_id = 2020903, type = 3, next_id = 202090313, talk_content = "早就听说过你喜欢路见不平拔刀相助，但也没必要为了我们惹上浊水帮的人，不然不知道哪一天他们就会回来找你算账。", talk_name = "孔方儿",},
	[202090313] = { talk_id = 202090313, group_id = 2020903, type = 3, talk_content = "这次的事你就假装自己只是路过，别出声，也别拔剑，这样对你更好。", talk_name = "孔方儿",},
	[202090314] = { talk_id = 202090314, group_id = 2020903, type = 3, talk_content = "原来是这样，那你随意，我就不打扰了哈。", talk_name = "孔方儿",},
	[202090401] = { talk_id = 202090401, group_id = 2020904, type = 1, next_id = 202090402, talk_content = "孔方儿，这都到点了，鏖战怎么还不开始啊？大伙都等急了！", talk_name = "袁二斧",},
	[202090402] = { talk_id = 202090402, group_id = 2020904, type = 1, next_id = 202090403, talk_content = "不好意思哈各位，跟朋友聊天耽误了点事，现在鏖战可以准备开始了！", talk_name = "孔方儿",},
	[202090403] = { talk_id = 202090403, group_id = 2020904, type = 1, next_id = 202090404, talk_content = "先生们女士们，欢迎来到本次由浊水帮主办，街坊邻里们共同赞助的鏖战现场！", talk_name = "孔方儿",},
	[202090404] = { talk_id = 202090404, group_id = 2020904, type = 1, next_id = 202090405, talk_content = "本次鏖战奖励丰厚，参赛者们也是热情空前啊。在大家下注前，我们先来介绍一下大赛的重量级嘉宾，浊水帮二公子——", talk_name = "孔方儿",},
	[202090405] = { talk_id = 202090405, group_id = 2020904, type = 1, next_id = 202090406, talk_content = "让开！我说了让开听到没有？！我也住在这附近，凭什么不让我参赛？", talk_name = "小茧",},
	[202090406] = { talk_id = 202090406, group_id = 2020904, type = 1, talk_content = "我也说了闲杂人等不准靠近鏖场，这场我们浊水帮今天包了，你听到没有？", talk_name = "金百千",},
	[202090501] = { talk_id = 202090501, group_id = 2020905, type = 4, next_id = 202090502, aside = "<color=#FFEBA2>小茧：</color>少说废话！宁王在天月城建鏖场是为了武学的精神，不是为了让你们这些家伙作威作福的！", talk_content = "少说废话！宁王在天月城建鏖场是为了武学的精神，不是为了让你们这些家伙作威作福的！", talk_name = "小茧",},
	[202090502] = { talk_id = 202090502, group_id = 2020905, type = 4, next_id = 202090503, aside = "<color=#FFEBA2>小茧：</color>别认怂啊，现在就让我参赛，让你们的人跟我堂堂正正打一场！", talk_content = "别认怂啊，现在就让我参赛，让你们的人跟我堂堂正正打一场！", talk_name = "小茧",},
	[202090503] = { talk_id = 202090503, group_id = 2020905, type = 4, next_id = 202090504, aside = "<color=#FFEBA2>金百千：</color>“武学的精神”？武学在百家院里早就已经不存在了，只有有权有钱的人才能当老大，这就是规矩！", talk_content = "“武学的精神”？武学在百家院里早就已经不存在了，只有有权有钱的人才能当老大，这就是规矩！", talk_name = "金百千",},
	[202090504] = { talk_id = 202090504, group_id = 2020905, type = 4, next_id = 202090505, aside = "<color=#FFEBA2>金百千：</color>你这小丫头又有什么资格跟我们叫嚣？凭你有脉者上天入地的功夫，还是凭你有宁王塔的通行证？", talk_content = "你这小丫头又有什么资格跟我们叫嚣？凭你有脉者上天入地的功夫，还是凭你有宁王塔的通行证？", talk_name = "金百千",},
	[202090505] = { talk_id = 202090505, group_id = 2020905, type = 4, aside = "<color=#FFEBA2>小茧：</color>就凭你们这群不配称为武者的家伙没资格挥那把剑！", talk_content = "就凭你们这群不配称为武者的家伙没资格挥那把剑！", talk_name = "小茧",},
	[202090601] = { talk_id = 202090601, group_id = 2020906, type = 2, options = {{"还是不要和他们冲突为好。",202090603,},{"为什么一定要参加这场鏖战？",202090602,},}, talk_content = "你……也是和那些家伙一伙的？不对，你看上去好像更像一名剑士……", talk_name = "小茧",},
	[202090602] = { talk_id = 202090602, group_id = 2020906, type = 3, next_id = 202090604, talk_content = "我想要得到那把剑，仅此而已。", talk_name = "小茧",},
	[202090603] = { talk_id = 202090603, group_id = 2020906, type = 3, next_id = 202090604, talk_content = "我才没什么心思和这帮杂碎起冲突，我只想得到那把剑，仅此而已。", talk_name = "小茧",},
	[202090604] = { talk_id = 202090604, group_id = 2020906, type = 1, next_id = 202090605, talk_content = "闲话聊够了没有？识相就给我滚远一点，你们两个都是，别搅了浊水帮的兴致！", talk_name = "金百千",},
	[202090605] = { talk_id = 202090605, group_id = 2020906, type = 1, next_id = 202090606, talk_content = "等等，你为什么一定要拿到那把剑？是为了卖钱还是为了见义勇为？", talk_name = "青乌",},
	[202090606] = { talk_id = 202090606, group_id = 2020906, type = 1, next_id = 202090607, talk_content = "不……是因为那是我父亲铸的剑。", talk_name = "小茧",},
	[202090607] = { talk_id = 202090607, group_id = 2020906, type = 1, next_id = 202090608, talk_content = "你父亲？我还说那是我爷爷的剑呢！你怎么证明那是你家的剑？你叫它一声它能答应吗？", talk_name = "金百千",},
	[202090608] = { talk_id = 202090608, group_id = 2020906, type = 1, next_id = 202090609, talk_content = "小小年纪不知道学好，光学会了点胡搅蛮缠的本事，要我说就是缺管教！", talk_name = "金百千",},
	[202090609] = { talk_id = 202090609, group_id = 2020906, type = 1, next_id = 202090610, talk_content = "我没说谎！那把剑剑身上有十四道流云纹，代表铸剑花费了十四载年岁。剑柄上还有一个古书的“茧”字，是我父亲专门为我刻的……", talk_name = "小茧",},
	[202090610] = { talk_id = 202090610, group_id = 2020906, type = 1, next_id = 202090611, talk_content = "我说的没有半句假话，你们要是不相信，尽管自己去看看！", talk_name = "小茧",},
	[202090611] = { talk_id = 202090611, group_id = 2020906, type = 1, talk_content = "呵，老子才没那个闲工夫。那边那个脉者，你不是挺有好奇心吗？去看看到底怎么回事！", talk_name = "金百千",},
	[202090701] = { talk_id = 202090701, group_id = 2020907, type = 1, next_id = 202090702, talk_content = "听好了，给我放规矩点，现在可没人能证明那是你家的剑！", talk_name = "金百千",},
	[202090702] = { talk_id = 202090702, group_id = 2020907, type = 1, talk_content = "你……你等着，很快就能证明了！", talk_name = "小茧",},
	[202090801] = { talk_id = 202090801, group_id = 2020908, type = 1, next_id = 202090802, talk_content = "得了，今天出了这回事，老大肯定又要拿咱们做文章了。", talk_name = "袁二斧",},
	[202090802] = { talk_id = 202090802, group_id = 2020908, type = 1, talk_content = "行了你，跟你没关系就别吱声！", talk_name = "董三钺",},
	[202090901] = { talk_id = 202090901, group_id = 2020909, type = 1, talk_content = "那个女孩看着有些眼熟，该不会是……", talk_name = "孔方儿",},
	[202091001] = { talk_id = 202091001, group_id = 2020910, type = 1, next_id = 202091002, talk_content = "（剑刃薄如宣纸，散发出阵阵寒光，无论多不识货都看得出这是把神兵。）",},
	[202091002] = { talk_id = 202091002, group_id = 2020910, type = 1, next_id = 202091003, talk_content = "（一、二、三……果然不多不少，正好十四道云纹。）", talk_name = "青乌",},
	[202091003] = { talk_id = 202091003, group_id = 2020910, type = 1, talk_content = "（剑柄上的刻字也对上了，这样的证据已经足够表明这把剑的归属了。）", talk_name = "青乌",},
	[202091101] = { talk_id = 202091101, group_id = 2020911, type = 2, options = {{"该受管教的是你们才对。",202091102,},{"她说的没错。",202091102,},}, talk_content = "怎么样？那丫头说的是真是假？", talk_name = "金百千",},
	[202091102] = { talk_id = 202091102, group_id = 2020911, type = 3, next_id = 202091103, talk_content = "这……！我知道了，你们两个是一伙的对吗？合起伙来跟我们挑事！", talk_name = "金百千",},
	[202091103] = { talk_id = 202091103, group_id = 2020911, type = 3, next_id = 202091104, talk_content = "才没有！", talk_name = "小茧",},
	[202091104] = { talk_id = 202091104, group_id = 2020911, type = 3, next_id = 202091105, talk_content = "哈，就算你能说出那剑上的花纹又能怎么样？最多就是你同伙通风报信罢了！", talk_name = "金百千",},
	[202091105] = { talk_id = 202091105, group_id = 2020911, type = 2, options = {{"你刚才说赢得鏖战对吗？",202091106,},{"我就知道……",202091106,},}, talk_content = "退一万步讲，就算那剑过去真是你的，现在也早就易主了。现在，只有赢得鏖战的人才是它真正的主人。", talk_name = "金百千",},
	[202091106] = { talk_id = 202091106, group_id = 2020911, type = 3, next_id = 202091107, talk_content = "等等，你刚才说赢得鏖战对吗？", talk_name = "青乌",},
	[202091107] = { talk_id = 202091107, group_id = 2020911, type = 3, next_id = 202091108, talk_content = "好啊，这可是你说的。那我就只好赢下鏖战，拿到那把剑了。", talk_name = "青乌",},
	[202091108] = { talk_id = 202091108, group_id = 2020911, type = 3, next_id = 202091109, talk_content = "什……什么？！谁允许你来参加鏖战的？孔方儿！不是说不准把浊水帮之外的人放进来吗？！", talk_name = "金百千",},
	[202091109] = { talk_id = 202091109, group_id = 2020911, type = 3, next_id = 202091110, talk_content = "这……既然青乌来都来了，那就一起玩一玩嘛。", talk_name = "孔方儿",},
	[202091110] = { talk_id = 202091110, group_id = 2020911, type = 3, next_id = 202091111, talk_content = "（小声）这位可是脉者里出了名的能打，劝各位还是好好思考一下。", talk_name = "孔方儿",},
	[202091111] = { talk_id = 202091111, group_id = 2020911, type = 3, talk_content = "算……算了！参加就参加，谅你也不敢打出什么名堂来！", talk_name = "金百千",},
	[202091201] = { talk_id = 202091201, group_id = 2020912, type = 4, next_id = 202091202, aside = "<color=#FFEBA2>金百千：</color>按照规矩，参赛选手不得使用脉力。比赛中打赢一场拿末彩，打赢两场拿中彩，连胜三场就是头彩。", talk_content = "按照规矩，参赛选手不得使用脉力。比赛中打赢一场拿末彩，打赢两场拿中彩，连胜三场就是头彩。", talk_name = "金百千",},
	[202091202] = { talk_id = 202091202, group_id = 2020912, type = 4, aside = "<color=#FFEBA2>金百千：</color>比赛中途随时可以退出，可要是输了哪一场，就一分钱也拿不到了。怎么样？接受的话就站到鏖场上来吧。", talk_content = "比赛中途随时可以退出，可要是输了哪一场，就一分钱也拿不到了。怎么样？接受的话就站到鏖场上来吧。", talk_name = "金百千",},
	[202091301] = { talk_id = 202091301, group_id = 2020913, type = 1, next_id = 202091302, talk_content = "青乌，我也跟你一起参赛！我们一起打趴这群家伙！", talk_name = "小茧",},
	[202091302] = { talk_id = 202091302, group_id = 2020913, type = 1, talk_content = "还嫌这鏖场不够乱吗？金五金六，把这丫头看好了，别让她自己跑到场上去！", talk_name = "金百千",},
	[202091401] = { talk_id = 202091401, group_id = 2020914, type = 1, next_id = 202091402, talk_content = "咳……虽说现在这儿是他们的场子，但你要是真想小小地教训一下他们，我还是支持你的。", talk_name = "孔方儿",},
	[202091402] = { talk_id = 202091402, group_id = 2020914, type = 2, options = {{"还是先不参加了吧……",202091404,},{"准备好参加鏖战了。",202091403,},}, talk_content = "所以要参加鏖战吗？如果你临时变卦了也没关系，比赛中途随时都可以选择退赛。", talk_name = "孔方儿",},
	[202091403] = { talk_id = 202091403, group_id = 2020914, type = 3, talk_content = "好哇，那就到那边去准备吧。祝你好运。", talk_name = "孔方儿",},
	[202091404] = { talk_id = 202091404, group_id = 2020914, type = 3, next_id = 202091405, talk_content = "打算退赛了吗？这样也好，现在是最方便全身而退的时候嘛。", talk_name = "孔方儿",},
	[202091405] = { talk_id = 202091405, group_id = 2020914, type = 3, talk_content = "回头还是我来想办法安慰一下那姑娘吧，希望她不要做什么傻事……", talk_name = "孔方儿",},
	[202091501] = { talk_id = 202091501, group_id = 2020915, type = 1, next_id = 202091502, talk_content = "哈，名声还挺吓人的，实际上胆子也不怎么大。惹了我们浊水帮可没什么好下场。", talk_name = "金百千",},
	[202091502] = { talk_id = 202091502, group_id = 2020915, type = 1, talk_content = "青乌，你……你别管这家伙！我会想办法在鏖场上打败他们的！", talk_name = "小茧",},
	[202091601] = { talk_id = 202091601, group_id = 2020916, type = 1, talk_content = "没关系，要我说你退赛可能是好事呢。接下来的事情我来处理就好。", talk_name = "孔方儿",},
	[202091701] = { talk_id = 202091701, group_id = 2020917, type = 1, next_id = 202091702, talk_content = "哟，你怎么出来了？本来还想看看要是你赢了鏖战，老大会是什么脸色呢。", talk_name = "袁二斧",},
	[202091702] = { talk_id = 202091702, group_id = 2020917, type = 1, talk_content = "青乌要是赢了，第一个当老大下酒菜的就是你小子！", talk_name = "董三钺",},
	[202091801] = { talk_id = 202091801, group_id = 2020918, type = 1, next_id = 202091802, talk_content = "好的，看来我们的选手们已经准备好了。那么就由我介绍一下——", talk_name = "孔方儿",},
	[202091802] = { talk_id = 202091802, group_id = 2020918, type = 1, next_id = 202091803, talk_content = "第一场鏖战，由惩恶扬善的神秘脉者青乌，对阵浊水帮“百家院之鹰”刘阿大！", talk_name = "孔方儿",},
	[202091803] = { talk_id = 202091803, group_id = 2020918, type = 1, next_id = 202091804, talk_content = "青……青乌，告诉你……对……对上我，算你倒霉！", talk_name = "刘阿大",},
	[202091804] = { talk_id = 202091804, group_id = 2020918, type = 1, talk_content = "这话还是晚点再说吧，接招！", talk_name = "青乌",},
	[202091901] = { talk_id = 202091901, group_id = 2020919, type = 1, next_id = 202091902, talk_content = "精彩的胜利！第一场比赛脉者青乌胜出，浊水帮选手请再接再厉！", talk_name = "孔方儿",},
	[202091902] = { talk_id = 202091902, group_id = 2020919, type = 2, options = {{"见好就收。",202091905,},{"继续参赛。",202091903,},}, talk_content = "那么请问青乌选手还要继续吗？如果现在退出可以拿到末彩，请慎重考虑哦。", talk_name = "孔方儿",},
	[202091903] = { talk_id = 202091903, group_id = 2020919, type = 3, next_id = 202091904, talk_content = "不愧是声名远扬的脉者青乌，的确是实力与魄力兼具呢！那么，有请第二位选手，浊水帮“百家院之影”阿萧登场！", talk_name = "孔方儿",},
	[202091904] = { talk_id = 202091904, group_id = 2020919, type = 3, talk_content = "闲话少说，让我看看你的实力吧脉者。", talk_name = "阿萧",},
	[202091905] = { talk_id = 202091905, group_id = 2020919, type = 3, talk_content = "决定见好就收了吗？那么恭喜青乌选手获得本场鏖战末彩，欢迎下次再来参赛！", talk_name = "孔方儿",},
	[202092001] = { talk_id = 202092001, group_id = 2020920, type = 1, next_id = 202092002, talk_content = "恭喜青乌获得中彩！竟然连胜浊水帮两位实力选手，青乌的能力的确不容小觑啊。", talk_name = "孔方儿",},
	[202092002] = { talk_id = 202092002, group_id = 2020920, type = 2, options = {{"先退出吧。",202092005,},{"还要继续。",202092003,},}, talk_content = "那么……青乌选手还要继续吗？友情提示，鏖战场数越多，风险也就越高哦。", talk_name = "孔方儿",},
	[202092003] = { talk_id = 202092003, group_id = 2020920, type = 3, next_id = 202092004, talk_content = "还要继续吗？看来青乌选手的胆识远超我们想象呢。", talk_name = "孔方儿",},
	[202092004] = { talk_id = 202092004, group_id = 2020920, type = 3, talk_content = "不过连打两场鏖战，青乌选手肯定也累了吧？那么比赛先暂停十分钟，可以先来我这里领取一下前两场的奖励哦。", talk_name = "孔方儿",},
	[202092005] = { talk_id = 202092005, group_id = 2020920, type = 3, talk_content = "好哇，那么恭喜青乌选手获得本场鏖战中彩，欢迎下次再来参赛哦。", talk_name = "孔方儿",},
	[202092101] = { talk_id = 202092101, group_id = 2020921, type = 2, options = {{"还算应付得了。",202092102,},{"这种对手不算什么。",202092102,},}, talk_content = "青乌你终于来了，刚才那两场比赛看得我心惊胆战的，幸好你没事！", talk_name = "孔方儿",},
	[202092102] = { talk_id = 202092102, group_id = 2020921, type = 3, next_id = 202092103, talk_content = "早就听说你很能打了，可没想到你居然这么能打。刚才那两个都是浊水帮实力拔尖的好手，居然一下子就被你打服了。", talk_name = "孔方儿",},
	[202092103] = { talk_id = 202092103, group_id = 2020921, type = 2, options = {{"那你们和小茧怎么办？",202092105,},{"可是只剩下一场了……",202092104,},}, talk_content = "不过到此为止成绩也算不错了，青乌，还是不要再继续了吧。", talk_name = "孔方儿",},
	[202092104] = { talk_id = 202092104, group_id = 2020921, type = 3, next_id = 202092106, talk_content = "青乌，你还不明白吗？就算那些家伙打不过你，他们也不会把头彩给你的。", talk_name = "孔方儿",},
	[202092105] = { talk_id = 202092105, group_id = 2020921, type = 3, next_id = 202092106, talk_content = "剑的事我们会再想办法争取，但如果惹火了浊水帮老大，麻烦可就不止这一点了。", talk_name = "孔方儿",},
	[202092106] = { talk_id = 202092106, group_id = 2020921, type = 1, next_id = 202092107, talk_content = "那些人的个性你也看到了，如果在他们自己的地盘上让他们丢了脸，往后他们一定会想方设法报复你的。", talk_name = "孔方儿",},
	[202092107] = { talk_id = 202092107, group_id = 2020921, type = 1, next_id = 202092108, talk_content = "另外，你应该看到那个二公子的车了吧？那家伙虽然没什么真本事，但在逞能上重来没输过。要是让他知道——", talk_name = "孔方儿",},
	[202092108] = { talk_id = 202092108, group_id = 2020921, type = 2, options = {{"难道你就是……",202092109,},{"……",202092109,},}, talk_content = "如果你们说的是青乌连胜两场鏖战的事，我已经知道了。", talk_name = "？？？",},
	[202092109] = { talk_id = 202092109, group_id = 2020921, type = 3, next_id = 202092110, talk_content = "这……原来是二公子您来了啊。我们是在说青乌的事来着。", talk_name = "孔方儿",},
	[202092110] = { talk_id = 202092110, group_id = 2020921, type = 1, next_id = 202092111, talk_content = "您看，今天大家打得尽兴，跟青乌也算是以武会友。既然鏖战的目的达到了，要不然今天这场……就先到这儿？", talk_name = "孔方儿",},
	[202092111] = { talk_id = 202092111, group_id = 2020921, type = 1, next_id = 202092112, talk_content = "我看这鏖战打得火热，大家也对最终的奖品很感兴趣，为什么要在兴头上停下呢？", talk_name = "金无赦",},
	[202092112] = { talk_id = 202092112, group_id = 2020921, type = 2, options = {{"输了可别反悔。",202092113,},{"没什么不行的。",202092113,},}, talk_content = "你看起来很能打啊，青乌。不如跟我会会如何？", talk_name = "金无赦",},
	[202092113] = { talk_id = 202092113, group_id = 2020921, type = 3, talk_content = "口气还真不小啊。既然这样，就到鏖场上去吧。", talk_name = "金无赦",},
	[202092201] = { talk_id = 202092201, group_id = 2020922, type = 1, next_id = 202092202, talk_content = "都看到了吗，各位？这位勇气可嘉的脉者想要在我们浊水帮的地盘上逞逞威风，这可真是件稀奇事啊。", talk_name = "金无赦",},
	[202092202] = { talk_id = 202092202, group_id = 2020922, type = 1, talk_content = "青乌，看来没人告诉过你我们浊水帮自己的鏖战规则啊？也罢，今天就来好好教教你吧。", talk_name = "金无赦",},
	[202092301] = { talk_id = 202092301, group_id = 2020923, type = 4, next_id = 202092302, aside = "<color=#FFEBA2>金无赦：</color>有两下子，不过实话告诉你，还没有什么人敢单枪匹马来砸我们浊水帮的场。", talk_content = "有两下子，不过实话告诉你，还没有什么人敢单枪匹马来砸我们浊水帮的场。", talk_name = "金无赦",},
	[202092302] = { talk_id = 202092302, group_id = 2020923, type = 4, next_id = 202092303, aside = "<color=#FFEBA2>金无赦：</color>兄弟们，给我一起上！", talk_content = "兄弟们，给我一起上！", talk_name = "金无赦",},
	[202092303] = { talk_id = 202092303, group_id = 2020923, type = 4, next_id = 202092304, aside = "<color=#FFEBA2>青乌：</color>……！", talk_content = "……！", talk_name = "青乌",},
	[202092304] = { talk_id = 202092304, group_id = 2020923, type = 4, next_id = 202092305, aside = "<color=#FFEBA2>小茧：</color>你……你耍赖！青乌快走！你一个人打不过他们的！", talk_content = "你……你耍赖！青乌快走！你一个人打不过他们的！", talk_name = "小茧",},
	[202092305] = { talk_id = 202092305, group_id = 2020923, type = 4, aside = "<color=#FFEBA2>金无赦：</color>浊水帮的地盘，自然要由浊水帮定规矩。青乌，我们的人已经封锁鏖场了，你就在这里好好跟大伙打个痛快吧。", talk_content = "浊水帮的地盘，自然要由浊水帮定规矩。青乌，我们的人已经封锁鏖场了，你就在这里好好跟大伙打个痛快吧。", talk_name = "金无赦",},
	[202092401] = { talk_id = 202092401, group_id = 2020924, type = 4, next_id = 202092402, aside = "<color=#FFEBA2>金十二：</color>怎……怎么回事！二公子，有噬脉乱流！", talk_content = "怎……怎么回事！二公子，有噬脉乱流！", talk_name = "金十二",},
	[202092402] = { talk_id = 202092402, group_id = 2020924, type = 4, next_id = 202092403, aside = "<color=#FFEBA2>金无赦：</color>噬脉乱流算什么，都给我上，先把青乌打服再说！", talk_content = "噬脉乱流算什么，都给我上，先把青乌打服再说！", talk_name = "金无赦",},
	[202092403] = { talk_id = 202092403, group_id = 2020924, type = 4, next_id = 202092404, aside = "<color=#FFEBA2>金阿五：</color>呃啊啊！！别打我，别打我！", talk_content = "呃啊啊！！别打我，别打我！", talk_name = "金阿五",},
	[202092404] = { talk_id = 202092404, group_id = 2020924, type = 4, next_id = 202092405, aside = "<color=#FFEBA2>金无赦：</color>这群饭桶……！可恶，你们看好场子，我先撤了！孔方儿！别让怪物砸坏了我的车！", talk_content = "这群饭桶……！可恶，你们看好场子，我先撤了！孔方儿！别让怪物砸坏了我的车！", talk_name = "金无赦",},
	[202092405] = { talk_id = 202092405, group_id = 2020924, type = 4, next_id = 202092406, aside = "<color=#FFEBA2>孔方儿：</color>危急关头哪有时间管这个啊二公子！你们快撤，我帮你们断后！", talk_content = "危急关头哪有时间管这个啊二公子！你们快撤，我帮你们断后！", talk_name = "孔方儿",},
	[202092406] = { talk_id = 202092406, group_id = 2020924, type = 4, aside = "<color=#FFEBA2>青乌：</color>（呃……之前战斗时候暂时封闭了气脉，现在居然还有点吃力。要是可以借助外力的话……）", talk_content = "（呃……之前战斗时候暂时封闭了气脉，现在居然还有点吃力。要是可以借助外力的话……）", talk_name = "青乌",},
	[202092501] = { talk_id = 202092501, group_id = 2020925, type = 1, next_id = 202092502, talk_content = "（嗯，果然是把好剑，气脉开始恢复了。）", talk_name = "青乌",},
	[202092502] = { talk_id = 202092502, group_id = 2020925, type = 1, next_id = 202092503, talk_content = "别急青乌，我和你一起对付它们！", talk_name = "小茧",},
	[202092503] = { talk_id = 202092503, group_id = 2020925, type = 1, talk_content = "嗯，你自己小心一点！", talk_name = "青乌",},
	[202092601] = { talk_id = 202092601, group_id = 2020926, type = 1, next_id = 202092602, talk_content = "呼……终于结束了。你打架还挺有两下子的嘛，青乌。", talk_name = "小茧",},
	[202092602] = { talk_id = 202092602, group_id = 2020926, type = 1, next_id = 202092603, talk_content = "你也不错，能直面噬脉生物的普通人没有几个。没受伤吧？", talk_name = "青乌",},
	[202092603] = { talk_id = 202092603, group_id = 2020926, type = 1, next_id = 202092604, talk_content = "嗯，我没事。倒是那群家伙，看起来挺威风，一遇上怪物就跑得那么快，居然还敢说自己是这里的主人。", talk_name = "小茧",},
	[202092604] = { talk_id = 202092604, group_id = 2020926, type = 1, next_id = 202092605, talk_content = "不，应该说多亏了那些怪物让他们这么急急忙忙地逃走，这才留下了这把剑。", talk_name = "青乌",},
	[202092605] = { talk_id = 202092605, group_id = 2020926, type = 1, next_id = 202092606, talk_content = "现在可以跟我说说那把剑的事了吗？为什么那么想要找回它？", talk_name = "青乌",},
	[202092606] = { talk_id = 202092606, group_id = 2020926, type = 1, next_id = 202092607, talk_content = "嗯……你可能已经听说了吧，我父亲曾经是这附近有名的铸剑师。", talk_name = "小茧",},
	[202092607] = { talk_id = 202092607, group_id = 2020926, type = 1, next_id = 202092608, talk_content = "他一直把铸剑当成自己人生的目标，还说自己肯定会干这行一辈子。", talk_name = "小茧",},
	[202092608] = { talk_id = 202092608, group_id = 2020926, type = 1, next_id = 202092609, talk_content = "直到后来，天月城在几十年间变成一座让人害怕的大都市，小商小铺在一夜之间烟消云散，高楼大厦拔地而起……", talk_name = "小茧",},
	[202092609] = { talk_id = 202092609, group_id = 2020926, type = 1, next_id = 202092610, talk_content = "封侯和悬天把大部分的武器制造权都紧紧握在手里，不知什么时候，整个城市都把我们拒之门外了。", talk_name = "小茧",},
	[202092610] = { talk_id = 202092610, group_id = 2020926, type = 2, options = {{"只要有手艺在就有出路。",202092612,},{"你父亲没有做些什么吗？",202092611,},}, talk_content = "后来的事情你也知道了。家里的铸剑行破产了，附近的混混趁火打劫，店铺里值钱的东西被一扫而空。", talk_name = "小茧",},
	[202092611] = { talk_id = 202092611, group_id = 2020926, type = 3, next_id = 202092613, talk_content = "父亲吗……父亲已经不在了。在那之后没多久，父亲就因为心病过世了。", talk_name = "小茧",},
	[202092612] = { talk_id = 202092612, group_id = 2020926, type = 3, next_id = 202092613, talk_content = "手艺吗……可是父亲已经不在了。在那之后没多久，他就因为心病过世了。", talk_name = "小茧",},
	[202092613] = { talk_id = 202092613, group_id = 2020926, type = 1, next_id = 202092614, talk_content = "那把剑是父亲的心爱之物，每一寸钢刃上都有他的指印。这几个月来我一直在寻找它的下落，为的就是不让它落到那群混混手里。", talk_name = "小茧",},
	[202092614] = { talk_id = 202092614, group_id = 2020926, type = 1, next_id = 202092615, talk_content = "因为“利剑之刃只为武者而开，不为强权而开”，这是父亲告诉我的。", talk_name = "小茧",},
	[202092615] = { talk_id = 202092615, group_id = 2020926, type = 1, next_id = 202092616, talk_content = "不过现在，看到你挥舞那把剑的样子，我就知道它已经找到了下一任主人。", talk_name = "小茧",},
	[202092616] = { talk_id = 202092616, group_id = 2020926, type = 2, options = {{"不，你才是它的主人。（交还剑）",202092619,},{"嗯，我会的。（收下剑）",202092617,},}, talk_content = "青乌，你是这场鏖战的胜者，更是值得使用这把剑的人。请代替父亲好好保管它。", talk_name = "小茧",},
	[202092617] = { talk_id = 202092617, group_id = 2020926, type = 3, next_id = 202092618, talk_content = "这就对了。要是能看到这幅画面，父亲肯定会很欣慰的。", talk_name = "小茧",},
	[202092618] = { talk_id = 202092618, group_id = 2020926, type = 1, talk_content = "那我们就此别过吧青乌。有缘的话，我们会再见面的。", talk_name = "小茧",},
	[202092619] = { talk_id = 202092619, group_id = 2020926, type = 3, next_id = 202092620, talk_content = "你才是它真正的主人，不止因为你是它的传人，更因为你即使身为普通人，却仍然有直面怪物的勇气。", talk_name = "青乌",},
	[202092620] = { talk_id = 202092620, group_id = 2020926, type = 1, next_id = 202092621, talk_content = "拿着它吧，然后记得，无论如何都要在这座城市好好生活下去。", talk_name = "青乌",},
	[202092621] = { talk_id = 202092621, group_id = 2020926, type = 1, next_id = 202092622, talk_content = "青乌……", talk_name = "小茧",},
	[202092622] = { talk_id = 202092622, group_id = 2020926, type = 1, next_id = 202092623, talk_content = "嗯，我会记住你说的话的。", talk_name = "小茧",},
	[202092623] = { talk_id = 202092623, group_id = 2020926, type = 1, talk_content = "那我们就此别过吧青乌。有缘的话，我会带着它再次和你见面的。", talk_name = "小茧",},
	[202100101] = { talk_id = 202100101, group_id = 2021001, type = 4, next_id = 202100102, aside = "<color=#FFEBA2>？？？：</color>喂，喂？你好？终于接通了，你就是注册名为青乌的脉者对吧？", call_icon = "Textures/Icon/Single/CampIcon/Weizhizhenyin.png", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "喂，喂？你好？终于接通了，你就是注册名为青乌的脉者对吧？", talk_name = "？？？",},
	[202100102] = { talk_id = 202100102, group_id = 2021001, type = 4, next_id = 202100103, aside = "<color=#FFEBA2>青乌：</color>你是什么人，为什么会知道我的名字？", call_man_icon = "？？？;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "你是什么人，为什么会知道我的名字？", talk_name = "青乌",},
	[202100103] = { talk_id = 202100103, group_id = 2021001, type = 4, next_id = 202100104, aside = "<color=#FFEBA2>优优：</color>这不算什么。自我介绍一下，这里是天月城最高人气的少女偶像，公共交通界的先锋，午刻共享汽车客服优优小姐。", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "这不算什么。自我介绍一下，这里是天月城最高人气的少女偶像，公共交通界的先锋，午刻共享汽车客服优优小姐。", talk_name = "优优",},
	[202100104] = { talk_id = 202100104, group_id = 2021001, type = 4, next_id = 202100105, aside = "<color=#FFEBA2>青乌：</color>共享汽车客服？等一下，我最近应该没有违反过什么交通守则。", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "共享汽车客服？等一下，我最近应该没有违反过什么交通守则。", talk_name = "青乌",},
	[202100105] = { talk_id = 202100105, group_id = 2021001, type = 4, next_id = 202100106, aside = "<color=#FFEBA2>优优：</color>不不，我可不是只会乱收罚款的巡卫。长话短说，我打来电话是想要请求你的帮助。", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "不不，我可不是只会乱收罚款的巡卫。长话短说，我打来电话是想要请求你的帮助。", talk_name = "优优",},
	[202100106] = { talk_id = 202100106, group_id = 2021001, type = 4, next_id = 202100107, aside = "<color=#FFEBA2>优优：</color>我所在的午刻优优128号车上发生了一些可能造成安全和财产隐患的极危问题，简单来说，我们被劫持了！", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "我所在的午刻优优128号车上发生了一些可能造成安全和财产隐患的极危问题，简单来说，我们被劫持了！", talk_name = "优优",},
	[202100107] = { talk_id = 202100107, group_id = 2021001, type = 4, next_id = 202100108, aside = "<color=#FFEBA2>青乌：</color>劫持？出什么事了？", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "劫持？出什么事了？", talk_name = "青乌",},
	[202100108] = { talk_id = 202100108, group_id = 2021001, type = 4, next_id = 202100109, aside = "<color=#FFEBA2>优优：</color>有人带着一包红外判定为武器装备的东西上了车，还有一包疑似炸药的危险物品，所以汽车向我发送了警报。", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "有人带着一包红外判定为武器装备的东西上了车，还有一包疑似炸药的危险物品，所以汽车向我发送了警报。", talk_name = "优优",},
	[202100109] = { talk_id = 202100109, group_id = 2021001, type = 4, next_id = 202100110, aside = "<color=#FFEBA2>优优：</color>我通过录音系统听到他们计划和他人交易这些武器，之后逃离天月城，这意味着我们公司的财产安全将在不久后受到威胁。", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "我通过录音系统听到他们计划和他人交易这些武器，之后逃离天月城，这意味着我们公司的财产安全将在不久后受到威胁。", talk_name = "优优",},
	[202100110] = { talk_id = 202100110, group_id = 2021001, type = 4, next_id = 202100111, aside = "<color=#FFEBA2>优优：</color>守护公司财产安全是优优的责任，守护天月城的安全则是脉者的责任。跟我有着这么多共同点的你一定不会袖手旁观对吧？", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "守护公司财产安全是优优的责任，守护天月城的安全则是脉者的责任。跟我有着这么多共同点的你一定不会袖手旁观对吧？", talk_name = "优优",},
	[202100111] = { talk_id = 202100111, group_id = 2021001, type = 4, next_id = 202100112, aside = "<color=#FFEBA2>青乌：</color>虽然你刚才这番话的疑点多得让人难以分析……但走私的确不是件小事。这忙我帮了。", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "虽然你刚才这番话的疑点多得让人难以分析……但走私的确不是件小事。这忙我帮了。", talk_name = "青乌",},
	[202100112] = { talk_id = 202100112, group_id = 2021001, type = 4, next_id = 202100113, aside = "<color=#FFEBA2>优优：</color>爽快！你这朋友本偶像也交定了！", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "爽快！你这朋友本偶像也交定了！", talk_name = "优优",},
	[202100113] = { talk_id = 202100113, group_id = 2021001, type = 4, aside = "<color=#FFEBA2>优优：</color>事不宜迟，请你搭乘标记好的午刻优优车辆，跟随车上的定位系统追上128号车。天月城的安宁就交给你来守护了！", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 41, talk_content = "事不宜迟，请你搭乘标记好的午刻优优车辆，跟随车上的定位系统追上128号车。天月城的安宁就交给你来守护了！", talk_name = "优优",},
	[202100201] = { talk_id = 202100201, group_id = 2021002, type = 4, next_id = 202100202, aside = "<color=#FFEBA2>优优：</color>午刻优优332号为你服务，愿你拥有美好的一天。", talk_content = "午刻优优332号为你服务，愿你拥有美好的一天。", talk_name = "优优",},
	[202100202] = { talk_id = 202100202, group_id = 2021002, type = 4, next_id = 202100203, aside = "<color=#FFEBA2>优优：</color>青乌你来啦，我就知道你肯定不会对罪恶坐视不管的！", talk_content = "青乌你来啦，我就知道你肯定不会对罪恶坐视不管的！", talk_name = "优优",},
	[202100203] = { talk_id = 202100203, group_id = 2021002, type = 4, next_id = 202100204, aside = "<color=#FFEBA2>青乌：</color>等等，你和刚才的客服是同一个人吗？", talk_content = "等等，你和刚才的客服是同一个人吗？", talk_name = "青乌",},
	[202100204] = { talk_id = 202100204, group_id = 2021002, type = 4, next_id = 202100205, aside = "<color=#FFEBA2>优优：</color>准确来说全城的优优客服都是我一个人哦，只不过平时车上使用的都是简易的语音助手，只有遇到麻烦时我才会亲自出现。", talk_content = "准确来说全城的优优客服都是我一个人哦，只不过平时车上使用的都是简易的语音助手，只有遇到麻烦时我才会亲自出现。", talk_name = "优优",},
	[202100205] = { talk_id = 202100205, group_id = 2021002, type = 4, aside = "<color=#FFEBA2>优优：</color>先不说闲话，导航系统已经检测到恶来车辆的位置了。抓紧跟上它，青乌，别让它跑远了！", talk_content = "先不说闲话，导航系统已经检测到恶来车辆的位置了。抓紧跟上它，青乌，别让它跑远了！", talk_name = "优优",},
	[202100301] = { talk_id = 202100301, group_id = 2021003, type = 4, next_id = 202100302, aside = "<color=#FFEBA2>青乌：</color>说起来，你不是偶像吗？为什么同时又是共享汽车客服？", talk_content = "说起来，你不是偶像吗？为什么同时又是共享汽车客服？", talk_name = "青乌",},
	[202100302] = { talk_id = 202100302, group_id = 2021003, type = 4, next_id = 202100303, aside = "<color=#FFEBA2>优优：</color>在天月城有份兼职也不是什么稀奇事，再说成为客服也可以和粉丝们多多接触，不是吗？", talk_content = "在天月城有份兼职也不是什么稀奇事，再说成为客服也可以和粉丝们多多接触，不是吗？", talk_name = "优优",},
	[202100303] = { talk_id = 202100303, group_id = 2021003, type = 4, next_id = 202100304, aside = "<color=#FFEBA2>优优：</color>不过自从录制了电子版本的语音助手，平时公司就不怎么需要我出马了。就因为这个，现在很多人都以为优优是个机器人呢。", talk_content = "不过自从录制了电子版本的语音助手，平时公司就不怎么需要我出马了。就因为这个，现在很多人都以为优优是个机器人呢。", talk_name = "优优",},
	[202100304] = { talk_id = 202100304, group_id = 2021003, type = 4, aside = "<color=#FFEBA2>青乌：</color>……所以才让恶来放松了警惕吗？", talk_content = "……所以才让恶来放松了警惕吗？", talk_name = "青乌",},
	[202100401] = { talk_id = 202100401, group_id = 2021004, type = 4, aside = "<color=#FFEBA2>优优：</color>系统检测到你离目标车的距离过近，千万不要被他们发现了，青乌！", talk_content = "系统检测到你离目标车的距离过近，千万不要被他们发现了，青乌！", talk_name = "优优",},
	[202100501] = { talk_id = 202100501, group_id = 2021005, type = 4, aside = "<color=#FFEBA2>优优：</color>系统检测到目标距离过远，不要让他们离开视线太久，青乌！", talk_content = "系统检测到目标距离过远，不要让他们离开视线太久，青乌！", talk_name = "优优",},
	[202100601] = { talk_id = 202100601, group_id = 2021006, type = 4, aside = "<color=#FFEBA2>优优：</color>就是这里了。和他们交易的人应该很快就会到，我们就在这里把车停好，等着他们把赃物拿出来吧。", talk_content = "就是这里了。和他们交易的人应该很快就会到，我们就在这里把车停好，等着他们把赃物拿出来吧。", talk_name = "优优",},
	[202100701] = { talk_id = 202100701, group_id = 2021007, type = 4, next_id = 202100702, aside = "<color=#FFEBA2>优优：</color>青乌，他们出现了！记得用相机把他们交易的画面拍下来，这可是珍贵的证据！", talk_content = "青乌，他们出现了！记得用相机把他们交易的画面拍下来，这可是珍贵的证据！", talk_name = "优优",},
	[202100702] = { talk_id = 202100702, group_id = 2021007, type = 4, next_id = 202100703, aside = "<color=#FFEBA2>恶索帮成员：</color>乌鸦，东西带来了吗？", talk_content = "乌鸦，东西带来了吗？", talk_name = "恶索帮成员",},
	[202100703] = { talk_id = 202100703, group_id = 2021007, type = 4, next_id = 202100704, aside = "<color=#FFEBA2>恶来：</color>带来了，云雀。整整三批，全是高级货。", talk_content = "带来了，云雀。整整三批，全是高级货。", talk_name = "恶来",},
	[202100704] = { talk_id = 202100704, group_id = 2021007, type = 4, next_id = 202100705, aside = "<color=#FFEBA2>恶索帮成员：</color>按老规矩，先给你四成的款子，剩下的要等验完货再补上。", talk_content = "按老规矩，先给你四成的款子，剩下的要等验完货再补上。", talk_name = "恶索帮成员",},
	[202100705] = { talk_id = 202100705, group_id = 2021007, type = 4, next_id = 202100706, aside = "<color=#FFEBA2>恶来：</color>那可不行。天月城里现在对这东西查得越来越严了，这是我们干的最后一票。", talk_content = "那可不行。天月城里现在对这东西查得越来越严了，这是我们干的最后一票。", talk_name = "恶来",},
	[202100706] = { talk_id = 202100706, group_id = 2021007, type = 4, next_id = 202100707, aside = "<color=#FFEBA2>恶来：</color>咱么这么久的交情，你还信不过我吗？信得过就直接一手交钱一手交货，别磨磨唧唧的。", talk_content = "咱么这么久的交情，你还信不过我吗？信得过就直接一手交钱一手交货，别磨磨唧唧的。", talk_name = "恶来",},
	[202100707] = { talk_id = 202100707, group_id = 2021007, type = 4, aside = "<color=#FFEBA2>恶索帮成员：</color>这……你再让我考虑考虑。", talk_content = "这……你再让我考虑考虑。", talk_name = "恶索帮成员",},
	[202100801] = { talk_id = 202100801, group_id = 2021008, type = 1, next_id = 202100802, fade_in = 1, fade_out = true, hide_player = true, talk_content = "青乌，你听见他们说的没有？这是他们干的最后一票。", talk_name = "优优",},
	[202100802] = { talk_id = 202100802, group_id = 2021008, type = 1, next_id = 202100803, talk_content = "根据我对天月城走私犯的群体画像分析，他们在这批货出手后有85%的几率会销毁证据，然后绕过守卫直接逃离天月城。", talk_name = "优优",},
	[202100803] = { talk_id = 202100803, group_id = 2021008, type = 2, options = {{"说得也对。",202100808,},{"不交给维安司处理吗？",202100804,},}, talk_content = "所有封土都是各自为治，一旦他们到了其他封土，就很难受到帝江的约束了。依我看，不如先下手为强，让他们尝尝我们的厉害。", talk_name = "优优",},
	[202100804] = { talk_id = 202100804, group_id = 2021008, type = 3, next_id = 202100805, talk_content = "根据我对最近的维安司位置测算，等到司警出现，他们将有62%的几率溜之大吉。", talk_name = "优优",},
	[202100805] = { talk_id = 202100805, group_id = 2021008, type = 3, next_id = 202100806, talk_content = "作为天月城的知名正义人士，就算是1%的可能性也不该给罪犯留下，对不对？而且我可不愿意因为车被恶来炸上天而负债打工……", talk_name = "优优",},
	[202100806] = { talk_id = 202100806, group_id = 2021008, type = 3, next_id = 202100807, talk_content = "其实后半句才是你的真实想法对吧？", talk_name = "青乌",},
	[202100807] = { talk_id = 202100807, group_id = 2021008, type = 3, talk_content = "别在意这么多啦，记得动作快点，我的命运就交给你了！", talk_name = "优优",},
	[202100808] = { talk_id = 202100808, group_id = 2021008, type = 3, next_id = 202100809, talk_content = "作为天月城的知名正义人士，就算一丝一毫的可能性也不该给罪犯留下，对不对？而且我可不愿意因为车被恶来炸上天而负债打工。", talk_name = "优优",},
	[202100809] = { talk_id = 202100809, group_id = 2021008, type = 3, next_id = 202100810, talk_content = "其实后半句才是你的真实想法对吧？", talk_name = "青乌",},
	[202100810] = { talk_id = 202100810, group_id = 2021008, type = 3, talk_content = "别在意这么多啦，上吧青乌，把他们打得落花流水！", talk_name = "优优",},
	[202100901] = { talk_id = 202100901, group_id = 2021009, type = 4, next_id = 202100902, aside = "<color=#FFEBA2>恶索帮成员：</color>嘁，就知道你们不是诚心交易的，怎么背后还跟了尾巴！", talk_content = "嘁，就知道你们不是诚心交易的，怎么背后还跟了尾巴！", talk_name = "恶索帮成员",},
	[202100902] = { talk_id = 202100902, group_id = 2021009, type = 4, next_id = 202100903, aside = "<color=#FFEBA2>恶来：</color>这笔买卖我可没对外透露过，我看尾巴是你带来的吧！", talk_content = "这笔买卖我可没对外透露过，我看尾巴是你带来的吧！", talk_name = "恶来",},
	[202100903] = { talk_id = 202100903, group_id = 2021009, type = 4, aside = "<color=#FFEBA2>青乌：</color>你们要是知道尾巴是谁叫来的，大概会惊掉下巴吧……", talk_content = "你们要是知道尾巴是谁叫来的，大概会惊掉下巴吧……", talk_name = "青乌",},
	[202101001] = { talk_id = 202101001, group_id = 2021010, type = 4, next_id = 202101002, aside = "<color=#FFEBA2>恶来：</color>不要恋战，兄弟们！这家伙不好对付，引爆炸弹，风紧扯呼！", talk_content = "不要恋战，兄弟们！这家伙不好对付，引爆炸弹，风紧扯呼！", talk_name = "恶来",},
	[202101002] = { talk_id = 202101002, group_id = 2021010, type = 4, aside = "<color=#FFEBA2>优优：</color>不好！检测到车体温度过高，紧急防爆装置已启动——", talk_content = "不好！检测到车体温度过高，紧急防爆装置已启动——", talk_name = "优优",},
	[202101101] = { talk_id = 202101101, group_id = 2021011, type = 4, next_id = 202101102, aside = "<color=#FFEBA2>青乌：</color>（问题算是暂时解决了，接下来到善后工作的时间了。）", talk_content = "（问题算是暂时解决了，接下来到善后工作的时间了。）", talk_name = "青乌",},
	[202101102] = { talk_id = 202101102, group_id = 2021011, type = 4, aside = "<color=#FFEBA2>青乌：</color>（恶来短信里说到的赃款应该就在这附近的垃圾箱里，去确认一下吧。）", talk_content = "（恶来短信里说到的赃款应该就在这附近的垃圾箱里，去确认一下吧。）", talk_name = "青乌",},
	[202101201] = { talk_id = 202101201, group_id = 2021012, type = 1, next_id = 202101202, fade_in = 1, fade_out = true, hide_player = true, talk_content = "（包裹里果然装着武器，从型号和制式看都不像是公制，而且也并未看到任何制造许可和安全证明。）", talk_name = "青乌",},
	[202101202] = { talk_id = 202101202, group_id = 2021012, type = 1, next_id = 202101203, talk_content = "（果然这样的地下产业在天月城已经蓬勃发展起来了。）", talk_name = "青乌",},
	[202101203] = { talk_id = 202101203, group_id = 2021012, type = 1, next_id = 202101204, talk_content = "青乌……比起这个，能不能先帮我把车上的炸弹取下来？", talk_name = "优优",},
	[202101204] = { talk_id = 202101204, group_id = 2021012, type = 1, next_id = 202101205, talk_content = "不用担心，又不是定时炸弹，只要没人引爆就不会随便爆炸的。", talk_name = "青乌",},
	[202101205] = { talk_id = 202101205, group_id = 2021012, type = 1, talk_content = "话是这么说没错，但还是让它离车远一点比较好……毕竟程序计算也有出现纰漏的时候嘛，嘿嘿。", talk_name = "优优",},
	[202101301] = { talk_id = 202101301, group_id = 2021013, type = 1, next_id = 202101302, fade_in = 1, fade_out = true, hide_player = true, talk_content = "的确是货真价实的炸弹。虽然不是什么大规模杀伤性武器，但一次全引爆应该也足够把车炸上天了。", talk_name = "青乌",},
	[202101302] = { talk_id = 202101302, group_id = 2021013, type = 1, next_id = 202101303, talk_content = "果然是这样……公司的车都是高科技产品，只要被他们炸掉一台就足够我花五年的工资赔偿了……", talk_name = "优优",},
	[202101303] = { talk_id = 202101303, group_id = 2021013, type = 1, talk_content = "……天月城偶像的生活已经惨淡到这个地步了吗？", talk_name = "青乌",},
	[202101401] = { talk_id = 202101401, group_id = 2021014, type = 1, next_id = 202101402, fade_in = 1, fade_out = true, hide_player = true, talk_content = "我还有一个问题……人工智能也会负债吗？", talk_name = "青乌",},
	[202101402] = { talk_id = 202101402, group_id = 2021014, type = 1, next_id = 202101403, talk_content = "为了让人工智能完全融入生活情境，我们在出厂时就拥有自己的社会身份，所以也拥有和公司纪律考勤挂钩的虚拟财产。", talk_name = "优优",},
	[202101403] = { talk_id = 202101403, group_id = 2021014, type = 1, next_id = 202101404, talk_content = "就连虚拟偶像的舞台曝光度和资金赞助也和这些相关，可以说是职业生涯的代名词了呢。", talk_name = "优优",},
	[202101404] = { talk_id = 202101404, group_id = 2021014, type = 1, talk_content = "变成了人工智能也照样要自愿打工……的意思吗？", talk_name = "青乌",},
	[202101501] = { talk_id = 202101501, group_id = 2021015, type = 1, next_id = 202101502, fade_in = 1, fade_out = true, hide_player = true, talk_content = "（包裹里果然装着武器，从型号和制式看都不像是公制，而且也并未看到任何制造许可和安全证明。）", talk_name = "青乌",},
	[202101502] = { talk_id = 202101502, group_id = 2021015, type = 1, talk_content = "（果然这样的地下产业在天月城已经蓬勃发展起来了。）", talk_name = "青乌",},
	[202101601] = { talk_id = 202101601, group_id = 2021016, type = 1, next_id = 202101602, fade_in = 1, fade_out = true, hide_player = true, talk_content = "危机已经解除了。那群恶来的确是打算用你们的车来运送赃物，不过他们低估了你的智能程度。", talk_name = "青乌",},
	[202101602] = { talk_id = 202101602, group_id = 2021016, type = 1, next_id = 202101603, talk_content = "低估超级少女偶像将是他们此生犯下的最严重的错误。天月城罪犯守则该加一条内容了，绝对不要用共享汽车运送赃物。", talk_name = "优优",},
	[202101603] = { talk_id = 202101603, group_id = 2021016, type = 1, next_id = 202101604, talk_content = "好了，赃物和赃款都已经凑齐了，接下来帮忙联系一下维安司吧。", talk_name = "青乌",},
	[202101604] = { talk_id = 202101604, group_id = 2021016, type = 1, next_id = 202101605, talk_content = "在你调查期间我已经联系过了，他们应该不久就会安排人手过来调查，所以这些东西就先由我代替看管好了。", talk_name = "优优",},
	[202101605] = { talk_id = 202101605, group_id = 2021016, type = 1, next_id = 202101606, talk_content = "感谢你在危急时刻伸出援手，青乌。虽然我在舞台上收到大批大批的掌声是常有的事，但受到别人的紧急救助还是第一次。", talk_name = "优优",},
	[202101606] = { talk_id = 202101606, group_id = 2021016, type = 2, options = {{"听起来不像虚拟客服能做的事。",202101607,},{"首先你要有一只手。",202101607,},}, talk_content = "这种情况该怎么处理呢……不然来一次亲密握手互动怎么样？又或者你还是更喜欢签名照一点？", talk_name = "优优",},
	[202101607] = { talk_id = 202101607, group_id = 2021016, type = 3, next_id = 202101608, talk_content = "唉，要不是我的意识被拘禁在公司网络里也不会这样了，这就是身为数据的坏处嘛。", talk_name = "优优",},
	[202101608] = { talk_id = 202101608, group_id = 2021016, type = 3, next_id = 202101609, talk_content = "那就这么决定了。下次如果青乌遇到危险或者困难，乘上任何一辆午刻优优，我就会立即为你提供帮助。", talk_name = "优优",},
	[202101609] = { talk_id = 202101609, group_id = 2021016, type = 2, options = {{"最好真的是智力上的……",202101610,},}, talk_content = "“就像真正的朋友一样”，那句话是这么说的吧？常见的动画剧集里主角也要拥有一位提供智力协助的伙伴，是这样对吧？", talk_name = "优优",},
	[202101610] = { talk_id = 202101610, group_id = 2021016, type = 3, next_id = 202101611, talk_content = "哼哼，那么就这么说定了哦。另外，我已经把一笔钱款打到你的账户了，就当作这次你帮我的谢礼吧。", talk_name = "优优",},
	[202101611] = { talk_id = 202101611, group_id = 2021016, type = 3, talk_content = "我们下次再见，青乌，少女偶像优优永远伴你左右！", talk_name = "优优",},
	[202101701] = { talk_id = 202101701, group_id = 2021017, type = 1, next_id = 202101702, talk_content = "午刻总部负责人马上就会到达，接下来的事情就不需要你操心啦。", talk_name = "优优",},
	[202101702] = { talk_id = 202101702, group_id = 2021017, type = 1, talk_content = "本偶像还要在这里看管好赃物，我们回见，青乌！", talk_name = "优优",},
	[202101801] = { talk_id = 202101801, group_id = 2021018, type = 4, next_id = 202101802, aside = "<color=#FFEBA2>优优：</color>他们把车停下了，青乌。看来交易地点就在这附近。", talk_content = "他们把车停下了，青乌。看来交易地点就在这附近。", talk_name = "优优",},
	[202101802] = { talk_id = 202101802, group_id = 2021018, type = 4, aside = "<color=#FFEBA2>青乌：</color>嗯，我去找个视野开阔的地点观察一下，我们电话联系。", talk_content = "嗯，我去找个视野开阔的地点观察一下，我们电话联系。", talk_name = "青乌",},
	[202101901] = { talk_id = 202101901, group_id = 2021019, type = 5, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true,},
	[202102001] = { talk_id = 202102001, group_id = 2021020, type = 5, next_id = 202102002, aside = "<color=#FFEBA2>青乌：</color>下面有几个家伙聚在一起，不过手上并没有拿着什么可疑物品。优优，他们是你车上的乘客吗？", call_icon = "Textures/Icon/Single/CampIcon/Weizhizhenyin.png", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "下面有几个家伙聚在一起，不过手上并没有拿着什么可疑物品。优优，他们是你车上的乘客吗？", talk_name = "青乌",},
	[202102002] = { talk_id = 202102002, group_id = 2021020, type = 5, next_id = 202102003, aside = "<color=#FFEBA2>优优：</color>嗯，其中两个和监控影像对得上号，其他两个应该是他们的“客户”吧。至于交易物，可能是为了掩人耳目放在了暗处。", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "嗯，其中两个和监控影像对得上号，其他两个应该是他们的“客户”吧。至于交易物，可能是为了掩人耳目放在了暗处。", talk_name = "优优",},
	[202102003] = { talk_id = 202102003, group_id = 2021020, type = 5, next_id = 202102004, aside = "<color=#FFEBA2>优优：</color>对了青乌，出手之前有办法确认他们的身份吗？罪犯之前在车上提到了主谋的存在，如果能顺藤摸瓜，就能解决一桩大案呢。", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "对了青乌，出手之前有办法确认他们的身份吗？罪犯之前在车上提到了主谋的存在，如果能顺藤摸瓜，就能解决一桩大案呢。", talk_name = "优优",},
	[202102004] = { talk_id = 202102004, group_id = 2021020, type = 5, next_id = 202102005, aside = "<color=#FFEBA2>青乌：</color>顺藤摸瓜解决大案……这个思路让我想起了一位故人。", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "顺藤摸瓜解决大案……这个思路让我想起了一位故人。", talk_name = "青乌",},
	[202102005] = { talk_id = 202102005, group_id = 2021020, type = 5, aside = "<color=#FFEBA2>青乌：</color>用佩从骇入罪犯的手机，应该就能获取到他们的身份了吧。", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "用佩从骇入罪犯的手机，应该就能获取到他们的身份了吧。", talk_name = "青乌",},
	[202102101] = { talk_id = 202102101, group_id = 2021021, type = 5, next_id = 202102102, aside = "<color=#FFEBA2>青乌：</color>怎么样，恶来的身份确认了吗？", call_icon = "Textures/Icon/Single/CampIcon/Weizhizhenyin.png", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "怎么样，恶来的身份确认了吗？", talk_name = "青乌",},
	[202102102] = { talk_id = 202102102, group_id = 2021021, type = 5, next_id = 202102103, aside = "<color=#FFEBA2>优优：</color>不仅确认了身份，还有额外收获。罪犯把赃款藏了起来，而且打算在这次交易之后就毁车离境。", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "不仅确认了身份，还有额外收获。罪犯把赃款藏了起来，而且打算在这次交易之后就毁车离境。", talk_name = "优优",},
	[202102103] = { talk_id = 202102103, group_id = 2021021, type = 5, next_id = 202102104, aside = "<color=#FFEBA2>优优：</color>原来如此，怪不得他们会用共享汽车运货，毕竟只要逃到帝江外就很难被约束了。", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "原来如此，怪不得他们会用共享汽车运货，毕竟只要逃到帝江外就很难被约束了。", talk_name = "优优",},
	[202102104] = { talk_id = 202102104, group_id = 2021021, type = 5, next_id = 202102105, aside = "<color=#FFEBA2>优优：</color>不能让他们得逞，也不能让他们毁坏车辆。青乌……帮人帮到底，你知道我接下来要说什么了吧？", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "不能让他们得逞，也不能让他们毁坏车辆。青乌……帮人帮到底，你知道我接下来要说什么了吧？", talk_name = "优优",},
	[202102105] = { talk_id = 202102105, group_id = 2021021, type = 5, next_id = 202102106, aside = "<color=#FFEBA2>青乌：</color>……如果我受了什么伤，午刻公司可以理赔吗？", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "……如果我受了什么伤，午刻公司可以理赔吗？", talk_name = "青乌",},
	[202102106] = { talk_id = 202102106, group_id = 2021021, type = 5, aside = "<color=#FFEBA2>优优：</color>这些你大可放心，本偶像会为你料理好一切的。上吧青乌，把他们打得落花流水！", call_man_icon = "优优;Textures/Icon/Single/HeadIcon/SquRWeizhirenyuan.png", child_type = 51, talk_content = "这些你大可放心，本偶像会为你料理好一切的。上吧青乌，把他们打得落花流水！", talk_name = "优优",},
	[202102201] = { talk_id = 202102201, group_id = 2021022, type = 1, talk_content = "（这里应该就是收货人提到的藏匿点，先把障碍物清理干净吧。）", talk_name = "青乌",},
	[202102301] = { talk_id = 202102301, group_id = 2021023, type = 2, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, options = {{"打开箱子",202102302,},}, talk_content = "（里面有一个掩埋在垃圾下面的箱子，看来应该是这里了。）", talk_name = "青乌",},
	[202102302] = { talk_id = 202102302, group_id = 2021023, type = 3, next_id = 202102303, talk_content = "（箱子里装满了约币，原来现在地下交易的武器价格已经炒到这样的高度了。）", talk_name = "青乌",},
	[202102303] = { talk_id = 202102303, group_id = 2021023, type = 3, next_id = 202102304, talk_content = "（还有一张清单……TY005机枪20把，居然是公制的型号。这应该是仿制品吧？）", talk_name = "青乌",},
	[202102304] = { talk_id = 202102304, group_id = 2021023, type = 3, talk_content = "（总之确认好了就先交给优优看管吧。接下来要去拆炸弹……那位偶像该等急了。）", talk_name = "青乌",},
	[202102401] = { talk_id = 202102401, group_id = 2021024, type = 1, next_id = 202102402, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "车厢里的确有一包武器，款式比之前在巡卫那里看到的粗糙了些，这就是恶来仿制的公制枪械吧。", talk_name = "青乌",},
	[202102402] = { talk_id = 202102402, group_id = 2021024, type = 2, options = {{"拆除炸弹",202102403,},}, talk_content = "说起来，可以先帮我拆除一下炸弹吗？虽然应该不是定时的型号，但还是有意外爆炸的可能性对吧……啊哈哈……", talk_name = "优优",},
	[202102403] = { talk_id = 202102403, group_id = 2021024, type = 3, next_id = 202102404, talk_content = "好了，这下你和你的公司财产应该都安全了。不过这包炸弹是危险品，你确定能在维安人员赶到前保管好吗？", talk_name = "青乌",},
	[202102404] = { talk_id = 202102404, group_id = 2021024, type = 2, options = {{"说到炸车……",202102405,},}, talk_content = "保管精密产品可是我的专长，毕竟这全帝江联网的高智能大脑也不是白长的，只要不会把车炸飞一切都好说。", talk_name = "优优",},
	[202102405] = { talk_id = 202102405, group_id = 2021024, type = 3, next_id = 202102406, talk_content = "说到这个，我还有一个问题……你只是个人工智能，为什么还要为公司的损失赔偿？", talk_name = "青乌",},
	[202102406] = { talk_id = 202102406, group_id = 2021024, type = 3, next_id = 202102407, talk_content = "为了让人工智能完全融入生活情境，我们在被制造出来的时候就拥有自己的社会身份，所以也拥有和工作业绩挂钩的虚拟财产。", talk_name = "优优",},
	[202102407] = { talk_id = 202102407, group_id = 2021024, type = 3, next_id = 202102408, talk_content = "既然有业绩自然也有相应的奖惩措施，就连虚拟偶像的舞台曝光度和资金赞助也和这些相关，可以说是职业生涯的代名词了呢。", talk_name = "优优",},
	[202102408] = { talk_id = 202102408, group_id = 2021024, type = 3, talk_content = "变成了机器人也照样要自愿打工……的意思吗？", talk_name = "青乌",},
	[202102501] = { talk_id = 202102501, group_id = 2021025, type = 1, talk_content = "（没有看起来像是赃款的东西，只有还没来得及处理的垃圾……呃，是谁把没吃完的榴莲馅饼扔在这里的？）", talk_name = "青乌",},
	[202102601] = { talk_id = 202102601, group_id = 2021026, type = 1, talk_content = "（一个干干净净的垃圾箱，里面没有任何可疑物品。）", talk_name = "青乌",},
	[202102701] = { talk_id = 202102701, group_id = 2021027, type = 1, next_id = 202102702, fade_in = 1, fade_out = true, hide_player = true, talk_content = "车厢里的确有一包武器，款式比之前在巡卫那里看到的粗糙了些，这就是恶来仿制的公制枪械吧。", talk_name = "青乌",},
	[202102702] = { talk_id = 202102702, group_id = 2021027, type = 2, child_type = 21, options = {{"（拆除炸弹）",202102703,},{"先联系午刻总部吧.",202102705,},}, talk_content = "说起来，可以先帮忙拆除一下炸弹吗？虽然应该不是定时的型号，但还是有意外爆炸的可能性对吧……啊哈哈……", talk_name = "优优",},
	[202102703] = { talk_id = 202102703, group_id = 2021027, type = 3, next_id = 202102704, icon = "interaction_4", talk_content = "幸亏只是简易炸弹，拆下来很容易，不过如果真被引爆也足够把整辆车炸上天了。", talk_name = "青乌",},
	[202102704] = { talk_id = 202102704, group_id = 2021027, type = 3, next_id = 202102707, talk_content = "喂，不要把可怕的事说得那么轻松啊！", talk_name = "优优",},
	[202102705] = { talk_id = 202102705, group_id = 2021027, type = 3, next_id = 202102706, icon = "interaction_4", talk_content = "这些本偶像在你刚才调查的时候就想到了，罪犯的信息也已经一并上传，他们绝对逃不出天月城的。", talk_name = "优优",},
	[202102706] = { talk_id = 202102706, group_id = 2021027, type = 3, next_id = 202102707, talk_content = "既然这样，事情应该没什么后患了，那就来拆炸弹吧。", talk_name = "青乌",},
	[202102707] = { talk_id = 202102707, group_id = 2021027, type = 1, next_id = 202102708, talk_content = "好了，这下你们的公司财产应该都安全了。不过这包炸弹是危险品，你确定能保管好吗？", talk_name = "青乌",},
	[202102708] = { talk_id = 202102708, group_id = 2021027, type = 2, next_id = 202102709, talk_content = "不用担心，炸弹的型号已经同步给专业人员了，午刻总部的负责人也会很快赶来，只要不把车炸飞一切都好说。", talk_name = "优优",},
	[202102709] = { talk_id = 202102709, group_id = 2021027, type = 1, next_id = 202102710, talk_content = "总之现在情况都清楚了。那群恶来的确是打算用你们的车来运送赃物，不过他们大概是把你当成了普通的虚拟客服，这才放松了警惕。", talk_name = "青乌",},
	[202102710] = { talk_id = 202102710, group_id = 2021027, type = 1, next_id = 202102711, talk_content = "等等，所以这场麻烦的起因归根结底是我的知名度太低吗！", talk_name = "优优",},
	[202102711] = { talk_id = 202102711, group_id = 2021027, type = 1, next_id = 202102712, talk_content = "唔……不管原因如何，总之这次事件解决还是多亏了你的帮忙呢。谢谢你啦，青乌。", talk_name = "优优",},
	[202102712] = { talk_id = 202102712, group_id = 2021027, type = 2, options = {{"感觉不如约币有性价比。",202102713,},{"不如还是按照客服的方法来吧。",202102713,},}, talk_content = "按照偶像的方法……不如就送你一套我的独家签名照当做报酬如何？", talk_name = "优优",},
	[202102713] = { talk_id = 202102713, group_id = 2021027, type = 3, next_id = 202102714, talk_content = "喂，我的签名照在未来可是值大价钱的东西，过了这村就没这店了哦。", talk_name = "优优",},
	[202102714] = { talk_id = 202102714, group_id = 2021027, type = 1, next_id = 202102715, talk_content = "好了好了，不开玩笑了。我已经向总部汇报好了，过一会儿他们就会把这次的酬金打到你的账户的。", talk_name = "优优",},
	[202102715] = { talk_id = 202102715, group_id = 2021027, type = 1, next_id = 202102716, talk_content = "另外，如果你之后在午刻共享汽车上遇到麻烦，也可以随时呼叫我帮忙。只要是我帮得上的，我一定会尽力哦。", talk_name = "优优",},
	[202102716] = { talk_id = 202102716, group_id = 2021027, type = 1, talk_content = "就这么说定啦，那我们下次再见吧，青乌。少女偶像优优永远伴你左右！", talk_name = "优优",},
	[202110101] = { talk_id = 202110101, group_id = 2021101, type = 2, options = {{"我就是。",202110102,},{"我好像不认识你。",202110102,},}, talk_content = "请问……青乌大人在茶馆吗？", talk_name = "魏老板",},
	[202110102] = { talk_id = 202110102, group_id = 2021101, type = 1, next_id = 202110103, talk_content = "您就是青乌大人？可算是找到您了！", talk_name = "魏老板",},
	[202110103] = { talk_id = 202110103, group_id = 2021101, type = 1, next_id = 202110104, talk_content = "之前有问题都是联系离歌小姐，可这段时间怎么都找不到她……", talk_name = "魏老板",},
	[202110104] = { talk_id = 202110104, group_id = 2021101, type = 1, next_id = 202110105, talk_content = "实在是无计可施，我才不得不找上门来。", talk_name = "魏老板",},
	[202110105] = { talk_id = 202110105, group_id = 2021101, type = 2, options = {{"什么事情说来听听。",202110106,},{"离歌是这样形容我的吗……",202110107,},}, talk_content = "离歌小姐曾交代过，有位叫青乌的脉者大人心肠热络，有麻烦找青乌大人就对了。", talk_name = "魏老板",},
	[202110106] = { talk_id = 202110106, group_id = 2021101, type = 3, next_id = 202110108, talk_content = "真爽快啊，不愧是青乌大人。", talk_name = "魏老板",},
	[202110107] = { talk_id = 202110107, group_id = 2021101, type = 3, next_id = 202110108, talk_content = "您之前出手帮忙的事迹，咱们也是有所耳闻的，您就别谦虚啦。", talk_name = "魏老板",},
	[202110108] = { talk_id = 202110108, group_id = 2021101, type = 1, next_id = 202110109, talk_content = "说正事吧，我在附近的锦绣街开了一家餐饮店名叫百味园，地段位置好所以生意还算不错。", talk_name = "魏老板",},
	[202110109] = { talk_id = 202110109, group_id = 2021101, type = 1, next_id = 202110110, talk_content = "谁知树大招风，不知怎地铺面被人惦记上了。", talk_name = "魏老板",},
	[202110110] = { talk_id = 202110110, group_id = 2021101, type = 1, next_id = 202110111, talk_content = "近日来天晓得哪里来了一帮子“食客”，每天都来店里捣乱，不是说吃出了苍蝇就是点一碗面坐一整天。", talk_name = "魏老板",},
	[202110111] = { talk_id = 202110111, group_id = 2021101, type = 1, next_id = 202110112, talk_content = "各个都凶神恶煞的，明显就是想让我生意做不下去卷铺盖走人。", talk_name = "魏老板",},
	[202110112] = { talk_id = 202110112, group_id = 2021101, type = 2, child_type = 21, options = {{"无非是为了钱，我去和他们谈拢。",202110115,},{"把他们全部打跑不就行了。",202110113,},}, talk_content = "大人，您看能不能帮帮忙，小本生意可经不起折腾啊！", talk_name = "魏老板",},
	[202110113] = { talk_id = 202110113, group_id = 2021101, type = 3, next_id = 202110114, icon = "interaction_4", talk_content = "直接动手不太好吧……能不撕破脸皮最好别生事，大家以后低头不见抬头见的。", talk_name = "魏老板",},
	[202110114] = { talk_id = 202110114, group_id = 2021101, type = 3, next_id = 202110117, talk_content = "大人您秀个三拳两脚，把他们唬住不敢乱来就行了。", talk_name = "魏老板",},
	[202110115] = { talk_id = 202110115, group_id = 2021101, type = 3, next_id = 202110116, icon = "interaction_4", talk_content = "那帮家伙可是吃硬不吃软的。", talk_name = "魏老板",},
	[202110116] = { talk_id = 202110116, group_id = 2021101, type = 3, next_id = 202110117, talk_content = "最好是能吓唬吓唬那帮瘪三，灭灭他们气焰。", talk_name = "魏老板",},
	[202110117] = { talk_id = 202110117, group_id = 2021101, type = 1, next_id = 202110118, talk_content = "我有分寸。", talk_name = "青乌",},
	[202110118] = { talk_id = 202110118, group_id = 2021101, type = 1, talk_content = "谢天谢地，那青乌大人去我店里看看？百味园很好找的，我给你标在地图上。", talk_name = "魏老板",},
	[202110201] = { talk_id = 202110201, group_id = 2021102, type = 1, next_id = 202110202, talk_content = "喂喂喂，还有没有王法，我兄弟在这破店吃了碗面，就上吐下泻被送去医院了，这黑店是没人管了吗？", talk_name = "帮众甲",},
	[202110202] = { talk_id = 202110202, group_id = 2021102, type = 1, next_id = 202110203, talk_content = "连碗面都能吃出问题，这店家有多黑心简直不敢想！", talk_name = "帮众乙",},
	[202110203] = { talk_id = 202110203, group_id = 2021102, type = 1, next_id = 202110204, talk_content = "你们说你们的朋友吃面吃出问题，有什么证据吗？", talk_name = "青乌",},
	[202110204] = { talk_id = 202110204, group_id = 2021102, type = 1, next_id = 202110205, talk_content = "你谁啊，关你什么事？", talk_name = "帮众甲",},
	[202110205] = { talk_id = 202110205, group_id = 2021102, type = 1, next_id = 202110206, talk_content = "要是店家真有问题，那可以直接拿出证据控告店家，何必在这里闹事？", talk_name = "青乌",},
	[202110206] = { talk_id = 202110206, group_id = 2021102, type = 1, next_id = 202110207, talk_content = "还是说，你们其实拿不出证据？只不过单纯想捣乱？", talk_name = "青乌",},
	[202110207] = { talk_id = 202110207, group_id = 2021102, type = 1, next_id = 202110208, talk_content = "哎……你该不是黑店派来存心找茬的吧?想挨揍？", talk_name = "帮众甲",},
	[202110208] = { talk_id = 202110208, group_id = 2021102, type = 1, next_id = 202110209, talk_content = "老兄，这家伙是脉者，别太冲动。", talk_name = "帮众乙",},
	[202110209] = { talk_id = 202110209, group_id = 2021102, type = 1, next_id = 202110210, talk_content = "（还是得动手才行吗？）", talk_name = "青乌",},
	[202110210] = { talk_id = 202110210, group_id = 2021102, type = 1, next_id = 202110211, talk_content = "咳咳，几位稍安勿躁，有事好好说。", talk_name = "？？？",},
	[202110211] = { talk_id = 202110211, group_id = 2021102, type = 1, next_id = 202110212, talk_content = "在下方师爷，是这几位弟兄的前辈，不介意叫我声老方就好。", talk_name = "方师爷",},
	[202110212] = { talk_id = 202110212, group_id = 2021102, type = 1, next_id = 202110213, talk_content = "这位脉者大人，听您刚才的话，是想替店家出头？", talk_name = "方师爷",},
	[202110213] = { talk_id = 202110213, group_id = 2021102, type = 1, next_id = 202110214, talk_content = "你们也不是简单想要赔偿吧，我看是冲着恶意搞垮店家去的。", talk_name = "青乌",},
	[202110214] = { talk_id = 202110214, group_id = 2021102, type = 1, next_id = 202110215, talk_content = "明人不说暗话，无非是谈条件平息事情罢了，该有的礼数我们清楚。", talk_name = "方师爷",},
	[202110215] = { talk_id = 202110215, group_id = 2021102, type = 1, next_id = 202110216, talk_content = "不过，店门口杵着吵，似乎有些不太讲究吧，不免让人笑话。", talk_name = "方师爷",},
	[202110216] = { talk_id = 202110216, group_id = 2021102, type = 1, next_id = 202110217, talk_content = "要不，咱们移步到别处慢慢谈？换个地方不挡着人家做生意。", talk_name = "方师爷",},
	[202110217] = { talk_id = 202110217, group_id = 2021102, type = 1, next_id = 202110218, talk_content = "至于问题怎么解决，条件怎么谈，到地方再慢慢说清楚。", talk_name = "方师爷",},
	[202110218] = { talk_id = 202110218, group_id = 2021102, type = 1, next_id = 202110219, talk_content = "（僵持下去也没意义，不妨跟他们走一遭）", talk_name = "青乌",},
	[202110219] = { talk_id = 202110219, group_id = 2021102, type = 1, next_id = 202110220, talk_content = "那就别堵在店门口，带路吧。", talk_name = "青乌",},
	[202110220] = { talk_id = 202110220, group_id = 2021102, type = 1, talk_content = "脉者大人爽快。这边请！", talk_name = "方师爷",},
	[202110301] = { talk_id = 202110301, group_id = 2021103, type = 5, next_id = 202110302, aside = "<color=#FFEBA2>青乌：</color>带我来这里，恐怕不是来谈事情的吧？", talk_content = "带我来这里，恐怕不是来谈事情的吧？", talk_name = "青乌",},
	[202110302] = { talk_id = 202110302, group_id = 2021103, type = 5, next_id = 202110303, aside = "<color=#FFEBA2>方师爷：</color>呵，既然傻傻跟着来了，就不和你废话。", talk_content = "呵，既然傻傻跟着来了，就不和你废话。", talk_name = "方师爷",},
	[202110303] = { talk_id = 202110303, group_id = 2021103, type = 5, next_id = 202110304, aside = "<color=#FFEBA2>方师爷：</color>不管你是谁找来的，今天都要给你个教训！让人知道管闲事的下场！", talk_content = "不管你是谁找来的，今天都要给你个教训！让人知道管闲事的下场！", talk_name = "方师爷",},
	[202110304] = { talk_id = 202110304, group_id = 2021103, type = 5, aside = "<color=#FFEBA2>方师爷：</color>弟兄们出来，给我拿下这家伙！", talk_content = "弟兄们出来，给我拿下这家伙！", talk_name = "方师爷",},
	[202110401] = { talk_id = 202110401, group_id = 2021104, type = 4, aside = "<color=#FFEBA2>方师爷：</color>给我一起上！莫放跑了！", talk_content = "给我一起上！莫放跑了！", talk_name = "方师爷",},
	[202110501] = { talk_id = 202110501, group_id = 2021105, type = 5, next_id = 202110502, aside = "<color=#FFEBA2>方师爷：</color>你……你有本事给我等着！", talk_content = "你……你有本事给我等着！", talk_name = "方师爷",},
	[202110502] = { talk_id = 202110502, group_id = 2021105, type = 5, next_id = 202110503, aside = "<color=#FFEBA2>方师爷：</color>我把其他弟兄们全都叫来，给你点颜色看看！", talk_content = "我把其他弟兄们全都叫来，给你点颜色看看！", talk_name = "方师爷",},
	[202110503] = { talk_id = 202110503, group_id = 2021105, type = 5, next_id = 202110504, aside = "<color=#FFEBA2>青乌：</color>我等着，看你能叫来多少人。", talk_content = "我等着，看你能叫来多少人。", talk_name = "青乌",},
	[202110504] = { talk_id = 202110504, group_id = 2021105, type = 5, next_id = 202110505, aside = "<color=#FFEBA2>青乌：</color>索性帮忙帮到底，彻底打服他们。", talk_content = "索性帮忙帮到底，彻底打服他们。", talk_name = "青乌",},
	[202110505] = { talk_id = 202110505, group_id = 2021105, type = 5, aside = "<color=#FFEBA2>青乌：</color>不过，这里现成的材料倒是可以好好利用。", talk_content = "不过，这里现成的材料倒是可以好好利用。", talk_name = "青乌",},
	[202110601] = { talk_id = 202110601, group_id = 2021106, type = 4, next_id = 202110602, aside = "<color=#FFEBA2>青乌：</color>制造炮台的的图纸还不够……", talk_content = "制造炮台的的图纸还不够……", talk_name = "青乌",},
	[202110602] = { talk_id = 202110602, group_id = 2021106, type = 4, aside = "<color=#FFEBA2>青乌：</color>先拍照扫描一下吧。", talk_content = "先拍照扫描一下吧。", talk_name = "青乌",},
	[202110701] = { talk_id = 202110701, group_id = 2021107, type = 5, aside = "<color=#FFEBA2>青乌：</color>趁着他还没回来，赶快在这个区域建造两个炮台防御吧。", talk_content = "趁着他还没回来，赶快在这个区域建造两个炮台防御吧。", talk_name = "青乌",},
	[202110801] = { talk_id = 202110801, group_id = 2021108, type = 4, next_id = 202110802, aside = "<color=#FFEBA2>方师爷：</color>有胆子，居然没跑！", talk_content = "有胆子，居然没跑！", talk_name = "方师爷",},
	[202110802] = { talk_id = 202110802, group_id = 2021108, type = 4, aside = "<color=#FFEBA2>方师爷：</color>那就不要客气，弟兄们给我招呼！", talk_content = "那就不要客气，弟兄们给我招呼！", talk_name = "方师爷",},
	[202110901] = { talk_id = 202110901, group_id = 2021109, type = 5, next_id = 202110902, aside = "<color=#FFEBA2>方师爷：</color>别打了别打了，大人饶了我们吧……", talk_content = "别打了别打了，大人饶了我们吧……", talk_name = "方师爷",},
	[202110902] = { talk_id = 202110902, group_id = 2021109, type = 5, next_id = 202110903, aside = "<color=#FFEBA2>青乌：</color>饶了你也可以，只不过别再来骚扰店家。", talk_content = "饶了你也可以，只不过别再来骚扰店家。", talk_name = "青乌",},
	[202110903] = { talk_id = 202110903, group_id = 2021109, type = 5, aside = "<color=#FFEBA2>方师爷：</color>一定一定，你借我个胆我也不敢了！", talk_content = "一定一定，你借我个胆我也不敢了！", talk_name = "方师爷",},
	[202111001] = { talk_id = 202111001, group_id = 2021110, type = 1, next_id = 202111002, talk_content = "青乌大人，太感谢了。", talk_name = "魏老板",},
	[202111002] = { talk_id = 202111002, group_id = 2021110, type = 1, next_id = 202111003, talk_content = "所以这帮人是什么来头，你有头绪吗？", talk_name = "青乌",},
	[202111003] = { talk_id = 202111003, group_id = 2021110, type = 1, next_id = 202111004, talk_content = "那个方师爷，就是附近恶来徒的狗头军师。", talk_name = "魏老板",},
	[202111004] = { talk_id = 202111004, group_id = 2021110, type = 1, next_id = 202111005, talk_content = "十有八九是生意红火，帮派的人眼馋了想赶走我霸占店铺。", talk_name = "魏老板",},
	[202111005] = { talk_id = 202111005, group_id = 2021110, type = 1, next_id = 202111006, talk_content = "平时都是小卒出来闹事，今天算是把大鱼钓出来了。", talk_name = "魏老板",},
	[202111006] = { talk_id = 202111006, group_id = 2021110, type = 1, next_id = 202111007, talk_content = "这下他们知道我有脉者撑腰，以后都不敢来惹事了！", talk_name = "魏老板",},
	[202111007] = { talk_id = 202111007, group_id = 2021110, type = 1, next_id = 202111008, talk_content = "你其实就是想让我狠狠教训他们吧……", talk_name = "青乌",},
	[202111008] = { talk_id = 202111008, group_id = 2021110, type = 1, next_id = 202111009, talk_content = "是他们有眼不识泰山，不知青乌大人的厉害要硬碰硬，当然得吃苦头了。", talk_name = "魏老板",},
	[202111009] = { talk_id = 202111009, group_id = 2021110, type = 1, next_id = 202111010, talk_content = "百味园总算能正常营业了，望月茶室解决问题的手段名不虚传啊。", talk_name = "魏老板",},
	[202111010] = { talk_id = 202111010, group_id = 2021110, type = 1, talk_content = "一点帮忙的谢礼，青乌大人您务必要收下！", talk_name = "魏老板",},
	[202120201] = { talk_id = 202120201, group_id = 2021202, type = 1, next_id = 202120202, talk_content = "你就是青乌对吧？百闻不如一见，我是如影。", talk_name = "如影",},
	[202120202] = { talk_id = 202120202, group_id = 2021202, type = 1, talk_content = "竞速赛准备得怎么样了？准备好就把车停在停放点吧，我在这里等着你。", talk_name = "如影",},
	[202120301] = { talk_id = 202120301, group_id = 2021203, type = 1, next_id = 202120302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "一、二、三……很好，大家都到齐了。", talk_name = "如影",},
	[202120302] = { talk_id = 202120302, group_id = 2021203, type = 1, next_id = 202120303, talk_content = "那么我就来说明一下吧。欢迎大家参加“急速轨道”杯城市竞速赛，我是这次比赛的主办人兼主持人如影。", talk_name = "如影",},
	[202120303] = { talk_id = 202120303, group_id = 2021203, type = 1, next_id = 202120304, talk_content = "就如大家所见，这次来参加比赛的都是天月城各界的优秀车手，想必每一位都实力不俗。", talk_name = "如影",},
	[202120304] = { talk_id = 202120304, group_id = 2021203, type = 1, next_id = 202120305, talk_content = "废话就不多讲了，只要赢过其他车手，就能拿到更好的奖励，第一名还有终极大奖。准备好参赛了吗？", talk_name = "如影",},
	[202120305] = { talk_id = 202120305, group_id = 2021203, type = 1, next_id = 202120306, talk_content = "所以奖品到底是什么啊？不会比了半天最后奖励只有几张约币吧？", talk_name = "吴芥",},
	[202120306] = { talk_id = 202120306, group_id = 2021203, type = 1, next_id = 202120307, talk_content = "那当然不会，不过现在告诉你就没意思了。要想拿到大奖，就努力比其他人跑得更快点吧。", talk_name = "如影",},
	[202120307] = { talk_id = 202120307, group_id = 2021203, type = 1, next_id = 202120308, talk_content = "好啊，你可别说话不算数！", talk_name = "包崇",},
	[202120308] = { talk_id = 202120308, group_id = 2021203, type = 1, talk_content = "说到做到。现在离比赛开始还有几分钟，大家先随意聊聊天准备一下，准备好了就回车上系好安全带吧。", talk_name = "如影",},
	[202120401] = { talk_id = 202120401, group_id = 2021204, type = 1, next_id = 202120402, talk_content = "（桌上放着比赛资质证明和赛车路段许可……看来如影没有骗人。）", talk_name = "<nick_name>",},
	[202120402] = { talk_id = 202120402, group_id = 2021204, type = 1, talk_content = "（资质证明下面……压着一块天月杯竞速赛的选手佩章？如影竟然有这种东西，想必是参加过职业比赛吧。）", talk_name = "<nick_name>",},
	[202120501] = { talk_id = 202120501, group_id = 2021205, type = 1, next_id = 202120502, talk_content = "青乌，很高兴你能来。看你的架势，应该是胜券在握了吧？", talk_name = "如影",},
	[202120502] = { talk_id = 202120502, group_id = 2021205, type = 1, talk_content = "我可是很看好你的，这次就拿出全部实力比上一比吧。", talk_name = "如影",},
	[202120601] = { talk_id = 202120601, group_id = 2021206, type = 1, next_id = 202120602, talk_content = "总觉得那个如影长得有点眼熟啊，她是哪个职业赛车手吗？", talk_name = "吴芥",},
	[202120602] = { talk_id = 202120602, group_id = 2021206, type = 1, next_id = 202120603, talk_content = "我也这么觉得，她长得有点像蝉联过几届天月杯冠军的“飞廉”，但那位车手自从第18届之后就没露过面了。", talk_name = "包崇",},
	[202120603] = { talk_id = 202120603, group_id = 2021206, type = 1, next_id = 202120604, talk_content = "眼睛、嘴巴……到处都有一点像，但又明显不是同一个人，可能是姐妹之类的吧。", talk_name = "包崇",},
	[202120604] = { talk_id = 202120604, group_id = 2021206, type = 1, talk_content = "原来还有这层关系啊，等下比赛结束得找她问问。", talk_name = "吴芥",},
	[202120701] = { talk_id = 202120701, group_id = 2021207, type = 4, next_id = 202120702, aside = "<color=#FFEBA2>如影：</color>比赛时间到了，大家都准备好了吧？", talk_content = "比赛时间到了，大家都准备好了吧？", talk_name = "如影",},
	[202120702] = { talk_id = 202120702, group_id = 2021207, type = 4, next_id = 202120703, aside = "<color=#FFEBA2>如影：</color>这次比赛的路线已经同步到各位的导航系统了，记得按照路线驾驶，抄近道可是违规的。", talk_content = "这次比赛的路线已经同步到各位的导航系统了，记得按照路线驾驶，抄近道可是违规的。", talk_name = "如影",},
	[202120703] = { talk_id = 202120703, group_id = 2021207, type = 4, next_id = 202120704, aside = "<color=#FFEBA2>如影：</color>另外，比赛需要的对讲系统也已经打开了，如果有什么不适或者临时想退赛，都可以通过对讲机跟我说哦。", talk_content = "另外，比赛需要的对讲系统也已经打开了，如果有什么不适或者临时想退赛，都可以通过对讲机跟我说哦。", talk_name = "如影",},
	[202120704] = { talk_id = 202120704, group_id = 2021207, type = 4, aside = "<color=#FFEBA2>如影：</color>好了，我数三二一，大家就全力冲刺吧！", talk_content = "好了，我数三二一，大家就全力冲刺吧！", talk_name = "如影",},
	[202120801] = { talk_id = 202120801, group_id = 2021208, type = 4, next_id = 202120802, aside = "<color=#FFEBA2>吴芥：</color>超过你咯，这次大奖绝对是我的！", talk_content = "超过你咯，这次大奖绝对是我的！", talk_name = "吴芥",},
	[202120802] = { talk_id = 202120802, group_id = 2021208, type = 4, aside = "<color=#FFEBA2>林伍：</color>别……别嚣张！拿冠军的还不一定是谁呢！", talk_content = "别……别嚣张！拿冠军的还不一定是谁呢！", talk_name = "林伍",},
	[202120901] = { talk_id = 202120901, group_id = 2021209, type = 4, next_id = 202120902, aside = "<color=#FFEBA2>包崇：</color>喂喂，一个个跑得也太快了吧！", talk_content = "喂喂，一个个跑得也太快了吧！", talk_name = "包崇",},
	[202120902] = { talk_id = 202120902, group_id = 2021209, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>的确，不过我也不会认输的。", talk_content = "的确，不过我也不会认输的。", talk_name = "<nick_name>",},
	[202121001] = { talk_id = 202121001, group_id = 2021210, type = 4, next_id = 202121002, aside = "<color=#FFEBA2>如影：</color>终点近在眼前，加油啊各位！", talk_content = "终点近在眼前，加油啊各位！", talk_name = "如影",},
	[202121002] = { talk_id = 202121002, group_id = 2021210, type = 4, aside = "<color=#FFEBA2>如影：</color>看到大奖了吗？它在跟你们招手呢！", talk_content = "看到大奖了吗？它在跟你们招手呢！", talk_name = "如影",},
	[202121101] = { talk_id = 202121101, group_id = 2021211, type = 4, next_id = 202121102, aside = "<color=#FFEBA2>如影：</color>厉害啊，青乌！我就知道你技术不赖！", talk_content = "厉害啊，青乌！我就知道你技术不赖！", talk_name = "如影",},
	[202121102] = { talk_id = 202121102, group_id = 2021211, type = 4, next_id = 202121103, aside = "<color=#FFEBA2>如影：</color>我宣布比赛到此结束！大伙表现都很精彩，我今天一饱眼福啊。", talk_content = "我宣布比赛到此结束！大伙表现都很精彩，我今天一饱眼福啊。", talk_name = "如影",},
	[202121103] = { talk_id = 202121103, group_id = 2021211, type = 4, aside = "<color=#FFEBA2>如影：</color>各位的排名已经显示在记分板上了，完成比赛的选手就可以到我这里领取自己的奖励了。", talk_content = "各位的排名已经显示在记分板上了，完成比赛的选手就可以到我这里领取自己的奖励了。", talk_name = "如影",},
	[202121201] = { talk_id = 202121201, group_id = 2021212, type = 1, next_id = 202121202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "青乌，你来了！刚才的比赛完成得真的很不错，无论是速度还是技巧都是一流的，我真是没看错人。", talk_name = "如影",},
	[202121202] = { talk_id = 202121202, group_id = 2021212, type = 2, options = {{"过奖过奖。",202121204,},{"我很满意。",202121203,},}, talk_content = "成绩的话让我看看……啊，有了！恭喜你啊青乌，拿了比赛的第一名！", talk_name = "如影",},
	[202121203] = { talk_id = 202121203, group_id = 2021212, type = 3, next_id = 202121205, talk_content = "确实是很值得开心的成绩啊，不过我早就听说你做过不少厉害的事，这样的胜利对你来说应该很常见了吧？", talk_name = "如影",},
	[202121204] = { talk_id = 202121204, group_id = 2021212, type = 3, next_id = 202121205, talk_content = "别谦虚了，这是你应得的，应该说不愧是天月城名声在外的青乌啊。", talk_name = "如影",},
	[202121205] = { talk_id = 202121205, group_id = 2021212, type = 1, next_id = 202121206, talk_content = "你刚才的技术即使从职业比赛的角度也挑不出什么毛病，不少选手还应该向你学习才是。", talk_name = "如影",},
	[202121206] = { talk_id = 202121206, group_id = 2021212, type = 1, next_id = 202121207, talk_content = "这么说来，你应该也是职业选手吧，如影？", talk_name = "<nick_name>",},
	[202121207] = { talk_id = 202121207, group_id = 2021212, type = 2, child_type = 21, options = {{"可你很熟悉职业比赛程序。",202121208,},{"可你有一块职业选手佩章。",202121209,},}, talk_content = "我？哈哈，我可不是什么职业选手。对于那种悬天主办的假模假式的比赛，我不怎么感兴趣。", talk_name = "如影",},
	[202121208] = { talk_id = 202121208, group_id = 2021212, type = 3, next_id = 202121213, icon = "interaction_4", talk_content = "想要举行私人比赛总得懂点天月城的规矩，我可不想比赛还没结束就带大伙去吃牢饭。", talk_name = "如影",},
	[202121209] = { talk_id = 202121209, group_id = 2021212, type = 3, next_id = 202121210, icon = "interaction_4", talk_content = "我在你的桌子上看到一块职业选手佩章，那不是你的东西吗？", talk_name = "<nick_name>",},
	[202121210] = { talk_id = 202121210, group_id = 2021212, type = 3, next_id = 202121211, talk_content = "佩章啊……只是我替别人保管的东西而已。佩章的主人已经不在人世了，而那个就是她曾经存在过的证明。", talk_name = "如影",},
	[202121211] = { talk_id = 202121211, group_id = 2021212, type = 3, next_id = 202121212, talk_content = "原来是这样吗……抱歉，我不是有意提起这件事。", talk_name = "<nick_name>",},
	[202121212] = { talk_id = 202121212, group_id = 2021212, type = 3, next_id = 202121213, talk_content = "哈哈，没事没事，她本人也不会介意的。", talk_name = "如影",},
	[202121213] = { talk_id = 202121213, group_id = 2021212, type = 1, next_id = 202121214, talk_content = "说了这么多有的没的，还是要再次恭喜你拿到这么好的名次，青乌。", talk_name = "如影",},
	[202121214] = { talk_id = 202121214, group_id = 2021212, type = 1, talk_content = "大奖已经帮你准备好了，拿着吧。期待之后在更大的舞台上看到你。", talk_name = "如影",},
	[202121301] = { talk_id = 202121301, group_id = 2021213, type = 1, next_id = 202121302, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "青乌，你来了！刚才的比赛完成得真的很不错，无论是速度还是技巧都是一流的，我真是没看错人。", talk_name = "如影",},
	[202121302] = { talk_id = 202121302, group_id = 2021213, type = 2, options = {{"感觉还不够满意。",202121304,},{"我已经很满意了。",202121303,},}, talk_content = "成绩的话让我看看……唔，虽然没有拿到第一名，但还是很不错的名次呢。", talk_name = "如影",},
	[202121303] = { talk_id = 202121303, group_id = 2021213, type = 3, next_id = 202121305, talk_content = "的确，不过我早就听说你做过不少厉害的事，这样的成绩对你来说应该很常见了吧？", talk_name = "如影",},
	[202121304] = { talk_id = 202121304, group_id = 2021213, type = 3, next_id = 202121305, talk_content = "没关系，再厉害的车手也有失误的时候，我知道你的实力肯定不止如此。", talk_name = "如影",},
	[202121305] = { talk_id = 202121305, group_id = 2021213, type = 1, next_id = 202121306, talk_content = "虽然有点小遗憾，但你刚才的技术即使从职业比赛的角度也挑不出什么毛病，不少选手还应该向你学习才是。", talk_name = "如影",},
