	[202121307] = { talk_id = 202121307, group_id = 2021213, type = 2, child_type = 21, options = {{"可你很熟悉职业比赛程序。",202121308,},{"可你有一块职业选手佩章。",202121309,},}, talk_content = "我？哈哈，我可不是什么职业选手。对于那种悬天主办的假模假式的比赛，我不怎么感兴趣。", talk_name = "如影",},
	[202121308] = { talk_id = 202121308, group_id = 2021213, type = 3, next_id = 202121313, icon = "interaction_4", talk_content = "想要举行私人比赛总得懂点天月城的规矩，我可不想比赛还没结束就带大伙去吃牢饭。", talk_name = "如影",},
	[202121309] = { talk_id = 202121309, group_id = 2021213, type = 3, next_id = 202121310, icon = "interaction_4", talk_content = "我在你的桌子上看到一块职业选手佩章，那不是你的东西吗？", talk_name = "<nick_name>",},
	[202121310] = { talk_id = 202121310, group_id = 2021213, type = 3, next_id = 202121311, talk_content = "佩章啊……只是我替别人保管的东西而已。佩章的主人已经不在人世了，而那个就是她曾经存在过的证明。", talk_name = "如影",},
	[202121311] = { talk_id = 202121311, group_id = 2021213, type = 3, next_id = 202121312, talk_content = "原来是这样吗……抱歉，我不是有意提起这件事。", talk_name = "<nick_name>",},
	[202121312] = { talk_id = 202121312, group_id = 2021213, type = 3, next_id = 202121313, talk_content = "哈哈，没事没事，她本人也不会介意的。", talk_name = "如影",},
	[202121313] = { talk_id = 202121313, group_id = 2021213, type = 1, next_id = 202121314, talk_content = "说了这么多有的没的，还是要再次感谢你能来参赛，青乌。", talk_name = "如影",},
	[202121314] = { talk_id = 202121314, group_id = 2021213, type = 1, talk_content = "奖品已经帮你准备好了，拿着吧。期待之后在更大的舞台上看到你。", talk_name = "如影",},
	[202121401] = { talk_id = 202121401, group_id = 2021214, type = 1, talk_content = "谢谢你能来捧场，青乌。如果有机会，我会再来找你的。", talk_name = "如影",},
	[202121601] = { talk_id = 202121601, group_id = 2021216, type = 1, next_id = 202121602, talk_content = "又见面了，青乌！为了和你比赛，我练习了一整天呢。", talk_name = "如影",},
	[202121602] = { talk_id = 202121602, group_id = 2021216, type = 1, talk_content = "那就把车停在起点线前面吧。等你准备好了，我们就开始。", talk_name = "如影",},
	[202121701] = { talk_id = 202121701, group_id = 2021217, type = 1, next_id = 202121702, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "又见面了，青乌！真高兴你能来找我。为了和你比赛，我练习了一整天呢。", talk_name = "如影",},
	[202121702] = { talk_id = 202121702, group_id = 2021217, type = 1, next_id = 202121703, talk_content = "其实不瞒你说，我一直想找人一起比试赛车，之前的比赛也是想找一位合适的对手。", talk_name = "如影",},
	[202121703] = { talk_id = 202121703, group_id = 2021217, type = 2, options = {{"那就来一决胜负吧。",202121704,},{"我不会留情的。",202121704,},}, talk_content = "你在比赛里的表现很亮眼，青乌。要是能跟你讨教一下，我会很开心的。", talk_name = "如影",},
	[202121704] = { talk_id = 202121704, group_id = 2021217, type = 3, next_id = 202121705, talk_content = "要的就是这种气势，我也一定会全力以赴的。", talk_name = "如影",},
	[202121705] = { talk_id = 202121705, group_id = 2021217, type = 3, talk_content = "那就开始吧，青乌。路线我已经发给你了，规则还跟之前一样，我数三二一，就向着目标冲刺吧！", talk_name = "如影",},
	[202121801] = { talk_id = 202121801, group_id = 2021218, type = 4, next_id = 202121802, aside = "<color=#FFEBA2>如影：</color>青乌，今天状态不错啊，是马达换了新的吗？", talk_content = "青乌，今天状态不错啊，是马达换了新的吗？", talk_name = "如影",},
	[202121802] = { talk_id = 202121802, group_id = 2021218, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>只是实力又提升了而已，小心了！", talk_content = "只是实力又提升了而已，小心了！", talk_name = "<nick_name>",},
	[202121901] = { talk_id = 202121901, group_id = 2021219, type = 4, next_id = 202121902, aside = "<color=#FFEBA2>如影：</color>来加速吧，前面有一段弯道，该较量一下了。", talk_content = "来加速吧，前面有一段弯道，该较量一下了。", talk_name = "如影",},
	[202121902] = { talk_id = 202121902, group_id = 2021219, type = 4, aside = "<color=#FFEBA2>如影：</color>要不要试试漂移，青乌？", talk_content = "要不要试试漂移，青乌？", talk_name = "如影",},
	[202122001] = { talk_id = 202122001, group_id = 2021220, type = 5, next_id = 202122002, aside = "<color=#FFEBA2>巡卫：</color>前面的两辆车，你们违反了天月城交通法规，请立即停下！", fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "前面的两辆车，你们违反了天月城交通法规，请立即停下！", talk_name = "巡卫",},
	[202122002] = { talk_id = 202122002, group_id = 2021220, type = 5, next_id = 202122003, aside = "<color=#FFEBA2><nick_name>：</color>如影，你听到了吗？后面好像有巡卫。", talk_content = "如影，你听到了吗？后面好像有巡卫。", talk_name = "<nick_name>",},
	[202122003] = { talk_id = 202122003, group_id = 2021220, type = 5, next_id = 202122004, aside = "<color=#FFEBA2>如影：</color>奇怪，我举行比赛都是申请了资质的，这段比赛路线也是提前预定好的，怎么会违反交通法规？", talk_content = "奇怪，我举行比赛都是申请了资质的，这段比赛路线也是提前预定好的，怎么会违反交通法规？", talk_name = "如影",},
	[202122004] = { talk_id = 202122004, group_id = 2021220, type = 5, next_id = 202122005, aside = "<color=#FFEBA2>巡卫：</color>前面的两辆车，我再说一次，请立即停下！", talk_content = "前面的两辆车，我再说一次，请立即停下！", talk_name = "巡卫",},
	[202122005] = { talk_id = 202122005, group_id = 2021220, type = 5, next_id = 202122006, aside = "<color=#FFEBA2><nick_name>：</color>他们好像很坚持。要停车吗，如影？", talk_content = "他们好像很坚持。要停车吗，如影？", talk_name = "<nick_name>",},
	[202122006] = { talk_id = 202122006, group_id = 2021220, type = 5, aside = "<color=#FFEBA2>如影：</color>算了，那就先停车看看他们在搞什么名堂吧。要是惹急了他们可就不妙了。", talk_content = "算了，那就先停车看看他们在搞什么名堂吧。要是惹急了他们可就不妙了。", talk_name = "如影",},
	[202122101] = { talk_id = 202122101, group_id = 2021221, type = 4, aside = "<color=#FFEBA2>巡卫：</color>天月城巡卫，两个人都下车！", talk_content = "天月城巡卫，两个人都下车！", talk_name = "巡卫",},
	[202122201] = { talk_id = 202122201, group_id = 2021222, type = 1, next_id = 202122202, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "你就是如影是吧？你涉嫌违规举办竞速比赛，现在需要跟我们到公务所走一趟。", talk_name = "巡卫甲",},
	[202122202] = { talk_id = 202122202, group_id = 2021222, type = 1, next_id = 202122203, talk_content = "喂，睁着眼说瞎话的水平不要太高啊。我五天前才在事务所办理了比赛资质证明，记录还在我手机上呢。", talk_name = "如影",},
	[202122203] = { talk_id = 202122203, group_id = 2021222, type = 1, next_id = 202122204, talk_content = "要我拿出纸质证明给你们看吗？", talk_name = "如影",},
	[202122204] = { talk_id = 202122204, group_id = 2021222, type = 1, next_id = 202122205, talk_content = "证明？你们这些街头混混我见多了，全都是拿着张假证横行霸道。", talk_name = "巡卫乙",},
	[202122205] = { talk_id = 202122205, group_id = 2021222, type = 2, child_type = 21, options = {{"你们这是仗势欺人。",202122206,},{"我们要联系事务局确认。",202122210,},{"把你们的巡卫证件拿出来看看。",202122214,},}, talk_content = "别以为用这种东西就能唬住我们！", talk_name = "巡卫乙",},
	[202122206] = { talk_id = 202122206, group_id = 2021222, type = 3, next_id = 202122207, icon = "interaction_4", talk_content = "你们这是仗势欺人，别以为自己是巡卫就可以在城市里胡作非为。", talk_name = "<nick_name>",},
	[202122207] = { talk_id = 202122207, group_id = 2021222, type = 3, next_id = 202122208, talk_content = "想抓人就拿出凭据来证明一下，不然就算我们拒捕，帝江律法也会站在我们这边，这点你不知道吗？", talk_name = "<nick_name>",},
	[202122208] = { talk_id = 202122208, group_id = 2021222, type = 3, next_id = 202122209, talk_content = "用得着你一个小小脉者教我们巡卫律法是什么吗？我们说犯法就是犯法了，哪来那么多废话？", talk_name = "巡卫甲",},
	[202122209] = { talk_id = 202122209, group_id = 2021222, type = 3, next_id = 202122220, talk_content = "那个叫如影的，跟我们走一趟，要是敢抗拒别怪我们不客气！", talk_name = "巡卫甲",},
	[202122210] = { talk_id = 202122210, group_id = 2021222, type = 3, next_id = 202122211, icon = "interaction_4", talk_content = "在事务局办理了比赛证明就一定会有记录吧？只要查查记录就什么都清楚了。", talk_name = "<nick_name>",},
	[202122211] = { talk_id = 202122211, group_id = 2021222, type = 3, next_id = 202122212, talk_content = "说得对啊青乌，我这就联系事务局，你们等着。", talk_name = "如影",},
	[202122212] = { talk_id = 202122212, group_id = 2021222, type = 3, next_id = 202122213, talk_content = "等着什么等着！我们就是从事务局那里得到通知来逮捕你的，记录里根本没有你的资质证明。", talk_name = "巡卫甲",},
	[202122213] = { talk_id = 202122213, group_id = 2021222, type = 3, next_id = 202122220, talk_content = "总之有什么话跟我们回局里再说，我们会把情况查清楚的。", talk_name = "巡卫甲",},
	[202122214] = { talk_id = 202122214, group_id = 2021222, type = 3, next_id = 202122215, icon = "interaction_4", talk_content = "是这样吗？我现在怀疑你们巡卫的身份也是假的，能把你们的证件拿出来给我看看吗？", talk_name = "<nick_name>",},
	[202122215] = { talk_id = 202122215, group_id = 2021222, type = 3, next_id = 202122216, talk_content = "嘁，你又是什么人，凭什么查我们巡卫的证件？", talk_name = "巡卫甲",},
	[202122216] = { talk_id = 202122216, group_id = 2021222, type = 3, next_id = 202122217, talk_content = "只是恰好在事务局有几个朋友，巡卫的证件我见多了，一眼就能看出谁在拿着假证横行霸道。", talk_name = "<nick_name>",},
	[202122217] = { talk_id = 202122217, group_id = 2021222, type = 3, next_id = 202122218, talk_content = "对于正规持证上岗的巡卫来说，这应该不是什么问题吧？", talk_name = "<nick_name>",},
	[202122218] = { talk_id = 202122218, group_id = 2021222, type = 3, next_id = 202122219, talk_content = "这家伙……老大，我们……", talk_name = "巡卫乙",},
	[202122219] = { talk_id = 202122219, group_id = 2021222, type = 3, next_id = 202122220, talk_content = "你怕什么怕，区区一个脉者能有什么能耐？别管这家伙，先把嫌疑人带走再说！", talk_name = "巡卫甲",},
	[202122220] = { talk_id = 202122220, group_id = 2021222, type = 1, talk_content = "（不对，这些巡卫有问题……不能让他们就这么带走如影。）", talk_name = "<nick_name>",},
	[202122301] = { talk_id = 202122301, group_id = 2021223, type = 4, next_id = 202122302, aside = "<color=#FFEBA2>如影：</color>我说了我没有违规，我要去公务所跟你们的上级谈谈！", talk_content = "我说了我没有违规，我要去公务所跟你们的上级谈谈！", talk_name = "如影",},
	[202122302] = { talk_id = 202122302, group_id = 2021223, type = 4, aside = "<color=#FFEBA2>巡卫乙：</color>这句话我也听过太多次了，都是被抓的时候嘴硬，到了公务所就哑火。", talk_content = "这句话我也听过太多次了，都是被抓的时候嘴硬，到了公务所就哑火。", talk_name = "巡卫乙",},
	[202122401] = { talk_id = 202122401, group_id = 2021224, type = 4, next_id = 202122402, aside = "<color=#FFEBA2>巡卫甲：</color>可恶，居然还敢打上来。我们抓她跟你有什么关系？", talk_content = "可恶，居然还敢打上来。我们抓她跟你有什么关系？", talk_name = "巡卫甲",},
	[202122402] = { talk_id = 202122402, group_id = 2021224, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>就算没关系也不能放任你们乱来。", talk_content = "就算没关系也不能放任你们乱来。", talk_name = "<nick_name>",},
	[202122501] = { talk_id = 202122501, group_id = 2021225, type = 4, aside = "<color=#FFEBA2>巡卫甲：</color>这家伙是硬茬，先撤，抓人的事之后再说！", talk_content = "这家伙是硬茬，先撤，抓人的事之后再说！", talk_name = "巡卫甲",},
	[202122601] = { talk_id = 202122601, group_id = 2021226, type = 1, next_id = 202122602, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "青乌，谢谢你出手救我。你没受伤吧？", talk_name = "如影",},
	[202122602] = { talk_id = 202122602, group_id = 2021226, type = 1, next_id = 202122603, talk_content = "我没事，对付这样不讲道理的巡卫对我来说不算难事。", talk_name = "<nick_name>",},
	[202122603] = { talk_id = 202122603, group_id = 2021226, type = 1, next_id = 202122604, talk_content = "那就好，果然你们脉者在打架这方面都是轻轻松松的。不过话说回来，有件事我倒是有点怀疑。", talk_name = "如影",},
	[202122604] = { talk_id = 202122604, group_id = 2021226, type = 1, next_id = 202122605, talk_content = "我在举行比赛前明明已经跟街道负责人联络好了，就算巡卫再想揩油水，也不该这么随意跟公务处对着干。", talk_name = "如影",},
	[202122605] = { talk_id = 202122605, group_id = 2021226, type = 1, talk_content = "青乌，我需要确认一件事。趁他们逃走了，检查一下他们开过来的车吧。", talk_name = "如影",},
	[202122701] = { talk_id = 202122701, group_id = 2021227, type = 1, next_id = 202122702, talk_content = "如果要确认他们的目的就只能检查一下他们留下的东西，这应该不算难事。", talk_name = "如影",},
	[202122702] = { talk_id = 202122702, group_id = 2021227, type = 1, talk_content = "青乌，麻烦你跟我一起找找吧。", talk_name = "如影",},
	[202122801] = { talk_id = 202122801, group_id = 2021228, type = 1, next_id = 202122802, fade_in = 1, fade_out = true, hide_all_entity = true, hide_player = true, talk_content = "找到了，车里有一个通讯器，上面的标志是……", talk_name = "<nick_name>",},
	[202122802] = { talk_id = 202122802, group_id = 2021228, type = 1, next_id = 202122803, talk_content = "……悬天。", talk_name = "<nick_name>",},
	[202122803] = { talk_id = 202122803, group_id = 2021228, type = 2, options = {{"你和悬天有什么过节吗？",202122804,},{"被发现？",202122804,},}, talk_content = "……果然是他们，想不到做成这样还是会被他们发现。", talk_name = "如影",},
	[202122804] = { talk_id = 202122804, group_id = 2021228, type = 3, next_id = 202122805, talk_content = "这个……唉，如果是跟救了我的青乌你来说，应该也没关系吧。", talk_name = "如影",},
	[202122805] = { talk_id = 202122805, group_id = 2021228, type = 1, next_id = 202122806, talk_content = "抱歉，之前的确骗了你。我在几年前确实是名职业车手，还有个在公众面前更有知名度的名字，飞廉。", talk_name = "如影",},
	[202122806] = { talk_id = 202122806, group_id = 2021228, type = 1, next_id = 202122807, talk_content = "在拿过几次好成绩之后，天月杯的主办方悬天就请我去了公司聊天。", talk_name = "如影",},
	[202122807] = { talk_id = 202122807, group_id = 2021228, type = 1, next_id = 202122808, talk_content = "他们给了我一笔钱，要求我必须在下次比赛输给他们的主推选手。", talk_name = "如影",},
	[202122808] = { talk_id = 202122808, group_id = 2021228, type = 2, options = {{"你有注意到溶液的效果吗？",202122811,},{"又是溶液……",202122809,},}, talk_content = "那位选手我碰巧遇到了，他的身体素质的确和常人大有不同，而且还在注射一种奇怪的溶液。", talk_name = "如影",},
	[202122809] = { talk_id = 202122809, group_id = 2021228, type = 3, next_id = 202122810, talk_content = "又是溶液……果然是悬天在搞鬼把戏。", talk_name = "<nick_name>",},
	[202122810] = { talk_id = 202122810, group_id = 2021228, type = 3, next_id = 202122813, talk_content = "嗯，我觉得那大概是什么非法药剂，总之我拒绝了他们的要求，还举报了他们违规参赛的情况。", talk_name = "如影",},
	[202122811] = { talk_id = 202122811, group_id = 2021228, type = 3, next_id = 202122812, talk_content = "没有哦，我也只是巧合之下碰见，还没来得及细看就被悬天的人用很蛮横的态度赶走了。", talk_name = "如影",},
	[202122812] = { talk_id = 202122812, group_id = 2021228, type = 3, next_id = 202122813, talk_content = "我觉得那大概是什么非法药剂，总之我拒绝了他们的要求，还举报了他们违规参赛的情况。", talk_name = "如影",},
	[202122813] = { talk_id = 202122813, group_id = 2021228, type = 1, next_id = 202122814, talk_content = "在那之后，我就经常在住处遇到来路不明的危险分子，有几次都差点没命。", talk_name = "如影",},
	[202122814] = { talk_id = 202122814, group_id = 2021228, type = 2, options = {{"也太危险了……",202122815,},}, talk_content = "最严重的一次……他们甚至直接偷换了我赛车的刹车片，害得我险些从悬崖上掉下去。", talk_name = "如影",},
	[202122815] = { talk_id = 202122815, group_id = 2021228, type = 3, next_id = 202122816, talk_content = "为了避开他们，我只好修改了登记姓名，更换了住所，还整了容。", talk_name = "如影",},
	[202122816] = { talk_id = 202122816, group_id = 2021228, type = 1, next_id = 202122817, talk_content = "我对外宣称飞廉已经意外遇难，亲手葬送了自己职业车手的生涯。", talk_name = "如影",},
	[202122817] = { talk_id = 202122817, group_id = 2021228, type = 1, next_id = 202122818, talk_content = "可我还是放不下赛车，所以才组织了这次比赛。本以为不会被他们发现，谁知还是出了这样的事……", talk_name = "如影",},
	[202122818] = { talk_id = 202122818, group_id = 2021228, type = 1, next_id = 202122819, talk_content = "如影，那种溶液并不是非法药剂，而是悬天的秘密之一，他们就是因为被你撞破了秘密才要对你灭口。", talk_name = "<nick_name>",},
	[202122819] = { talk_id = 202122819, group_id = 2021228, type = 1, next_id = 202122820, talk_content = "悬天也是我的敌人，虽然我也不清楚溶液的具体作用，但我可以确定那不是什么好东西。", talk_name = "<nick_name>",},
	[202122820] = { talk_id = 202122820, group_id = 2021228, type = 1, next_id = 202122821, talk_content = "是这样吗……其实我也隐约猜到了。唉，要是当时再果断一些，说不定就能揭穿悬天的阴谋了……", talk_name = "如影",},
	[202122821] = { talk_id = 202122821, group_id = 2021228, type = 1, next_id = 202122822, talk_content = "能做到这样已经很了不起了，如影。现在你该做的只有保护好自己的安全。", talk_name = "<nick_name>",},
	[202122822] = { talk_id = 202122822, group_id = 2021228, type = 1, next_id = 202122823, talk_content = "嗯……你说得也对。但事到如今，如果要避开他们，我就只能离开天月城了吧。", talk_name = "如影",},
	[202122823] = { talk_id = 202122823, group_id = 2021228, type = 1, next_id = 202122824, talk_content = "想想还挺不舍的，明明这里有我的梦想，还有像你这样的伙伴，但我却只能……", talk_name = "如影",},
	[202122824] = { talk_id = 202122824, group_id = 2021228, type = 1, next_id = 202122825, talk_content = "没关系，躲起来也只是暂时的手段，像悬天这样的企业总有一天会被我们彻底颠覆的不是吗？", talk_name = "<nick_name>",},
	[202122825] = { talk_id = 202122825, group_id = 2021228, type = 1, next_id = 202122826, talk_content = "等到那天，你就能重新回到天月城比赛了，我和那几位同伴……大概也算是完成了所谓的“大事”吧。", talk_name = "<nick_name>",},
	[202122826] = { talk_id = 202122826, group_id = 2021228, type = 1, next_id = 202122827, talk_content = "嗯，我就知道青乌你肯定不是普通人。有你这样的对手，悬天一定嚣张不了多久的。", talk_name = "如影",},
	[202122827] = { talk_id = 202122827, group_id = 2021228, type = 2, options = {{"可不要食言。",202122828,},{"我答应你。",202122828,},}, talk_content = "那我们约好了，等到那时候再比试一次，看看到底谁的技术更强。", talk_name = "如影",},
	[202122828] = { talk_id = 202122828, group_id = 2021228, type = 3, talk_content = "好啊，那这些东西就当做我预付的谢礼了？你可不要忘了，青乌，我们总有一天会再见的。", talk_name = "如影",},
	[202122901] = { talk_id = 202122901, group_id = 2021229, type = 1, talk_content = "别忘了我们的约定，青乌。等到悬天倒台那天，我会再来找你的！", talk_name = "如影",},
	[501010101] = { talk_id = 501010101, group_id = 5010101, type = 2, options = {{"我在找维安司。",501010102,},{"你是什么人？",501010106,},{"能和我说说这个地方吗？",501010117,},{"安息是一个地方吗？",501010125,},{"我懂的，再见。",501010124,},}, talk_content = "看你面相不像是本地人呢，虽然我们的法律不像安息那么严厉，但要是你敢犯罪的话我可不会放过你的。", talk_name = "让安虢",},
	[501010102] = { talk_id = 501010102, group_id = 5010101, type = 3, next_id = 501010103, talk_content = "城里面最大的建筑就是维安司，应该一眼就能看见的。", talk_name = "让安虢",},
	[501010103] = { talk_id = 501010103, group_id = 5010101, type = 3, next_id = 501010104, talk_content = "我倒是一眼就知道了。", talk_name = "司命",},
	[501010104] = { talk_id = 501010104, group_id = 5010101, type = 3, next_id = 501010105, talk_content = "话说回来你们是外面来的人吧，切记不要在熙来惹是生非。", talk_name = "让安虢",},
	[501010105] = { talk_id = 501010105, group_id = 5010101, type = 3, talk_content = "好神气的人呐。", talk_name = "司命",},
	[501010106] = { talk_id = 501010106, group_id = 5010101, type = 3, next_id = 501010107, talk_content = "我吗，我叫让安虢，是熙来城的维安司司警。", talk_name = "让安虢",},
	[501010107] = { talk_id = 501010107, group_id = 5010101, type = 3, next_id = 501010108, talk_content = "虽然才刚上任几个月，但我已经是经历过大风大浪的人了。", talk_name = "让安虢",},
	[501010108] = { talk_id = 501010108, group_id = 5010101, type = 3, next_id = 501010109, talk_content = "是啥大风大浪？", talk_name = "司命",},
	[501010109] = { talk_id = 501010109, group_id = 5010101, type = 3, next_id = 501010110, talk_content = "在之前的维和行动中我和同伴们一起捉住了创生主义的首领，那可是个很恐怖的家伙噢。", talk_name = "让安虢",},
	[501010110] = { talk_id = 501010110, group_id = 5010101, type = 3, next_id = 501010111, talk_content = "不过最终还是被我们打败了就是了。", talk_name = "让安虢",},
	[501010111] = { talk_id = 501010111, group_id = 5010101, type = 3, next_id = 501010112, talk_content = "维和行动……听上去不太太平呐。", talk_name = "司命",},
	[501010112] = { talk_id = 501010112, group_id = 5010101, type = 3, next_id = 501010113, talk_content = "创生主义的人私藏武器，还经常在城内外宣扬他们的过激思想，之前城内的确是不太太平。", talk_name = "让安虢",},
	[501010113] = { talk_id = 501010113, group_id = 5010101, type = 3, next_id = 501010114, talk_content = "但现在已经不需要再担心了，毕竟他们的首领已经被我们捉住了。", talk_name = "让安虢",},
	[501010114] = { talk_id = 501010114, group_id = 5010101, type = 3, next_id = 501010115, talk_content = "创生主义……在城内行动的时候得多注意一下了。", talk_name = "叙慕",},
	[501010115] = { talk_id = 501010115, group_id = 5010101, type = 3, next_id = 501010116, talk_content = "怎么，你难不成认识这个组织吗？", talk_name = "司命",},
	[501010116] = { talk_id = 501010116, group_id = 5010101, type = 3, talk_content = "我只是不想再被其他组织盯上而已。", talk_name = "叙慕",},
	[501010117] = { talk_id = 501010117, group_id = 5010101, type = 3, next_id = 501010118, talk_content = "一时半会我也不知道怎么介绍这个地方……如你所见，这个熙来城是个挺小的城市。", talk_name = "让安虢",},
	[501010118] = { talk_id = 501010118, group_id = 5010101, type = 3, next_id = 501010119, talk_content = "作为本地人，平常要是有人问我这样的问题那我高低得推荐你去城里的几个好店逛逛。", talk_name = "让安虢",},
	[501010119] = { talk_id = 501010119, group_id = 5010101, type = 3, next_id = 501010120, talk_content = "但可惜的，城外最近发生了不少事情，所以很多路边摊贩都离开这个地方了。", talk_name = "让安虢",},
	[501010120] = { talk_id = 501010120, group_id = 5010101, type = 3, next_id = 501010121, talk_content = "假若你是要来购买什么特产或者是旅行的游客的话，那最好再走远一些，走到主城天月去。", talk_name = "让安虢",},
	[501010121] = { talk_id = 501010121, group_id = 5010101, type = 3, next_id = 501010122, talk_content = "听上去是个很大的城市？", talk_name = "司命",},
	[501010122] = { talk_id = 501010122, group_id = 5010101, type = 3, next_id = 501010123, talk_content = "嗯，主城是比这里大上好几倍的地方，城内建筑也很漂亮，和那里比这里就和乡下没什么区别呢。", talk_name = "让安虢",},
	[501010123] = { talk_id = 501010123, group_id = 5010101, type = 3, talk_content = "后去看看吧。", talk_name = "叙慕",},
	[501010124] = { talk_id = 501010124, group_id = 5010101, type = 3, talk_content = "嗯，明白就好。", talk_name = "让安虢",},
	[501010125] = { talk_id = 501010125, group_id = 5010101, type = 3, next_id = 501010126, talk_content = "安息是内陆一个以严厉法律闻名的国家。", talk_name = "让安虢",},
	[501010126] = { talk_id = 501010126, group_id = 5010101, type = 3, next_id = 501010127, talk_content = "听说在那个国家之中有着一套很特别的定罪机制，执法人员们都有管理人赋予的名为天眼的东西。", talk_name = "让安虢",},
	[501010127] = { talk_id = 501010127, group_id = 5010101, type = 3, next_id = 501010128, talk_content = "天眼？", talk_name = "叙慕",},
	[501010128] = { talk_id = 501010128, group_id = 5010101, type = 3, next_id = 501010129, talk_content = "说是看一眼就能知道这个人是否有犯罪倾向，或是是否犯了罪这样……", talk_name = "让安虢",},
	[501010129] = { talk_id = 501010129, group_id = 5010101, type = 3, next_id = 501010130, talk_content = "……", talk_name = "叙慕",},
	[501010130] = { talk_id = 501010130, group_id = 5010101, type = 3, next_id = 501010131, talk_content = "我当初听见的时候也觉得很玄乎，但这个似乎已经被运用好多年了。", talk_name = "让安虢",},
	[501010131] = { talk_id = 501010131, group_id = 5010101, type = 3, talk_content = "所以那个地方的治安也是特别好呢。", talk_name = "让安虢",},
	[501020101] = { talk_id = 501020101, group_id = 5010201, type = 2, options = {{"什么是辩日？",501020102,},{"你是什么人？",501020108,},{"能和我说说这个地方吗？",501020113,},{"我还有别的事情，再见。",501020117,},}, talk_content = "怎么，你也要和我辩日吗？", talk_name = "司徒献",},
	[501020102] = { talk_id = 501020102, group_id = 5010201, type = 3, next_id = 501020103, talk_content = "就是讨论天上的太阳，我自从出生就一直看着那个东西了。", talk_name = "司徒献",},
	[501020103] = { talk_id = 501020103, group_id = 5010201, type = 3, next_id = 501020104, talk_content = "那你对“太阳”是怎么想的？", talk_name = "叙慕",},
	[501020104] = { talk_id = 501020104, group_id = 5010201, type = 3, next_id = 501020105, talk_content = "我觉得很漂亮噢，和负责日夜的太阳不同，这个太阳偶尔会发出特殊的光芒，样子也很特别。", talk_name = "司徒献",},
	[501020105] = { talk_id = 501020105, group_id = 5010201, type = 3, next_id = 501020106, talk_content = "据说其他地方的太阳也和这里的太阳长得不一样，长大后我一定要去看看。", talk_name = "司徒献",},
	[501020106] = { talk_id = 501020106, group_id = 5010201, type = 3, next_id = 501020107, talk_content = "还真是个不知死活的小子。", talk_name = "司命",},
	[501020107] = { talk_id = 501020107, group_id = 5010201, type = 3, talk_content = "（感觉大家对太阳的想法和我理解的不太一样。）", talk_name = "叙慕",},
	[501020108] = { talk_id = 501020108, group_id = 5010201, type = 3, next_id = 501020109, talk_content = "司徒献，是这个城里的神童，虽说是其他人给我的称号呢。", talk_name = "司徒献",},
	[501020109] = { talk_id = 501020109, group_id = 5010201, type = 3, next_id = 501020110, talk_content = "所以你是个很聪明的家伙？", talk_name = "司命",},
	[501020110] = { talk_id = 501020110, group_id = 5010201, type = 3, next_id = 501020111, talk_content = "是挺聪明的，其他事情就不能再说了。", talk_name = "司徒献",},
	[501020111] = { talk_id = 501020111, group_id = 5010201, type = 3, next_id = 501020112, talk_content = "因为大人们说过不要和陌生人说话。", talk_name = "司徒献",},
	[501020112] = { talk_id = 501020112, group_id = 5010201, type = 3, talk_content = "现在才来这句！？", talk_name = "司命",},
	[501020113] = { talk_id = 501020113, group_id = 5010201, type = 3, next_id = 501020114, talk_content = "那就引用书上的一句介绍语吧。", talk_name = "司徒献",},
	[501020114] = { talk_id = 501020114, group_id = 5010201, type = 3, next_id = 501020115, talk_content = "九个太阳照耀了八方土地，而这个绿意旺盛，生物繁茂的创生地就是其中一方。", talk_name = "司徒献",},
	[501020115] = { talk_id = 501020115, group_id = 5010201, type = 3, next_id = 501020116, talk_content = "九个太阳……只有八方？", talk_name = "叙慕",},
	[501020116] = { talk_id = 501020116, group_id = 5010201, type = 3, talk_content = "嗯，因为有一个太阳已经不再发出光亮了，真是可惜，要是我早出生一些的话……", talk_name = "司徒献",},
	[501020117] = { talk_id = 501020117, group_id = 5010201, type = 3, talk_content = "再见。", talk_name = "司徒献",},
	[501030101] = { talk_id = 501030101, group_id = 5010301, type = 2, options = {{"这是在卖什么？",501030102,},{"可以白送吗？",501030113,},{"不需要，谢谢。",501030115,},{"<color=#fed78c>商店</color>",1,},{"<color=#fed78c>砍价</color>",8,},}, talk_content = "走过路过不要错过，现在下订可以享受八五折优惠，八五折优惠~", talk_name = "多闻",},
	[501030102] = { talk_id = 501030102, group_id = 5010301, type = 3, next_id = 501030103, talk_content = "食品，日用，护身用的武器装备，不过现在可选的不多哦。", talk_name = "多闻",},
	[501030103] = { talk_id = 501030103, group_id = 5010301, type = 3, next_id = 501030104, talk_content = "现在？", talk_name = "司命",},
	[501030104] = { talk_id = 501030104, group_id = 5010301, type = 3, next_id = 501030105, talk_content = "是的，最近噬脉生物们异常活跃，为了减少危险，这里的货物运输都被暂停了。", talk_name = "多闻",},
	[501030105] = { talk_id = 501030105, group_id = 5010301, type = 3, next_id = 501030106, talk_content = "就是没新东西的意思吧，没趣，我们走吧青乌。", talk_name = "司命",},
	[501030106] = { talk_id = 501030106, group_id = 5010301, type = 3, talk_content = "（我们也没什么钱吧。）", talk_name = "叙慕",},
	[501030107] = { talk_id = 501030107, group_id = 5010301, type = 3, next_id = 501030108, talk_content = "小人名叫多闻，是熙来城内的百玲珑分店的揽客员。", talk_name = "多闻",},
	[501030108] = { talk_id = 501030108, group_id = 5010301, type = 3, next_id = 501030109, talk_content = "如大家所知的那样，百玲珑是包揽了好几个大城市贸易的大型商会。", talk_name = "多闻",},
	[501030109] = { talk_id = 501030109, group_id = 5010301, type = 3, next_id = 501030110, talk_content = "故而经营的范围也很广泛，上至富贵名器，下至日常杂品，我们样样皆全。", talk_name = "多闻",},
	[501030110] = { talk_id = 501030110, group_id = 5010301, type = 3, next_id = 501030111, talk_content = "不过……", talk_name = "多闻",},
	[501030111] = { talk_id = 501030111, group_id = 5010301, type = 3, next_id = 501030112, talk_content = "不过？", talk_name = "司命",},
	[501030112] = { talk_id = 501030112, group_id = 5010301, type = 3, talk_content = "要是客人想要好东西的话我建议移步到天月城呢。", talk_name = "多闻",},
	[501030113] = { talk_id = 501030113, group_id = 5010301, type = 3, next_id = 501030114, talk_content = "抱歉，本店暂时不提供货品试用……", talk_name = "多闻",},
	[501030114] = { talk_id = 501030114, group_id = 5010301, type = 3, talk_content = "青乌，你这家伙还挺大胆的呢。", talk_name = "司命",},
	[501030115] = { talk_id = 501030115, group_id = 5010301, type = 3, talk_content = "不要紧，欢迎随时光临哦。", talk_name = "多闻",},
	[501040101] = { talk_id = 501040101, group_id = 5010401, type = 2, options = {{"你在叹什么气？",501040102,},{"你是什么人？",501040111,},{"能和我说说气脉的事情吗？",501040117,},{"（先别打扰他吧。）",501040127,},}, talk_content = "唉，气脉的状态是越来越坏了……可是……", talk_name = "科生",},
	[501040102] = { talk_id = 501040102, group_id = 5010401, type = 3, next_id = 501040103, talk_content = "我在感叹气脉的衰弱而已，脉灵的数量也在减少，唉……", talk_name = "科生",},
	[501040103] = { talk_id = 501040103, group_id = 5010401, type = 3, next_id = 501040104, talk_content = "城内的其他人倒不像你那样担心气脉呢。", talk_name = "叙慕",},
	[501040104] = { talk_id = 501040104, group_id = 5010401, type = 3, next_id = 501040105, talk_content = "那是因为他们没有察觉倒事情的严重性，气脉衰弱是大乱将至的预兆。", talk_name = "科生",},
	[501040105] = { talk_id = 501040105, group_id = 5010401, type = 3, next_id = 501040106, talk_content = "最近的一次气脉衰弱是距今三百年前的四凶乱世，那个事件留下的灾害如今还在继续。", talk_name = "科生",},
	[501040106] = { talk_id = 501040106, group_id = 5010401, type = 3, next_id = 501040107, talk_content = "比如？", talk_name = "叙慕",},
	[501040107] = { talk_id = 501040107, group_id = 5010401, type = 3, next_id = 501040108, talk_content = "从这里一直向东走能找到一个无色的地带……那就是不再有气脉流过的土地的结局。", talk_name = "科生",},
	[501040108] = { talk_id = 501040108, group_id = 5010401, type = 3, next_id = 501040109, talk_content = "从三百年前到现在，那里面没有任何改变，也没有任何生物能在其中活动。", talk_name = "科生",},
	[501040109] = { talk_id = 501040109, group_id = 5010401, type = 3, next_id = 501040110, talk_content = "不……应该说除了噬脉生物没有任何生物能在其中活动才是。", talk_name = "科生",},
	[501040110] = { talk_id = 501040110, group_id = 5010401, type = 3, talk_content = "唉，真不知道之后还会出什么事。", talk_name = "科生",},
	[501040111] = { talk_id = 501040111, group_id = 5010401, type = 3, next_id = 501040112, talk_content = "我吗，我叫科生，一个研究院的小院士而已，研究方向是气脉能量的转化。", talk_name = "科生",},
	[501040112] = { talk_id = 501040112, group_id = 5010401, type = 3, next_id = 501040113, talk_content = "嗯……虽然快要被开除了。", talk_name = "科生",},
	[501040113] = { talk_id = 501040113, group_id = 5010401, type = 3, next_id = 501040114, talk_content = "是因为总叹气偷懒吗？", talk_name = "司命",},
	[501040114] = { talk_id = 501040114, group_id = 5010401, type = 3, next_id = 501040115, talk_content = "是因为我发表的文章被创生主义的人当成宣传的利器了。", talk_name = "科生",},
	[501040115] = { talk_id = 501040115, group_id = 5010401, type = 3, next_id = 501040116, talk_content = "明明我只是想要和大家说说创生地现在的气脉污染而已，唉……", talk_name = "科生",},
	[501040116] = { talk_id = 501040116, group_id = 5010401, type = 3, talk_content = "（这人一直在叹气呢。）", talk_name = "科生",},
	[501040117] = { talk_id = 501040117, group_id = 5010401, type = 3, next_id = 501040118, talk_content = "你为什么要听这个……也罢，有人陪我聊天也好。", talk_name = "科生",},
	[501040118] = { talk_id = 501040118, group_id = 5010401, type = 3, next_id = 501040119, talk_content = "气脉是这个世界的生命之源这点你应该知道吧？", talk_name = "科生",},
	[501040119] = { talk_id = 501040119, group_id = 5010401, type = 3, next_id = 501040120, talk_content = "虽说世界每一个存在的组成都不太一样，但毫无疑问的，每个存在之中都有气脉能量的存在。", talk_name = "科生",},
	[501040120] = { talk_id = 501040120, group_id = 5010401, type = 3, next_id = 501040121, talk_content = "而气脉本身也能被你这样的脉者运用，变化成强大的，能够扭曲自然现象的力量。", talk_name = "科生",},
	[501040121] = { talk_id = 501040121, group_id = 5010401, type = 3, next_id = 501040122, talk_content = "于是在人类的发展历史之中，一直都有人提出将气脉作为能源使用的课题。", talk_name = "科生",},
	[501040122] = { talk_id = 501040122, group_id = 5010401, type = 3, next_id = 501040123, talk_content = "听着像是之前都没这么干呢。", talk_name = "叙慕",},
	[501040123] = { talk_id = 501040123, group_id = 5010401, type = 3, next_id = 501040124, talk_content = "因为找不到合适的技术，但最终在五十年前这个问题也被解决了。", talk_name = "科生",},
	[501040124] = { talk_id = 501040124, group_id = 5010401, type = 3, next_id = 501040125, talk_content = "人们将气脉变作电力，文明就此获得了大量的能源，技术也得到了发展。", talk_name = "科生",},
	[501040125] = { talk_id = 501040125, group_id = 5010401, type = 3, next_id = 501040126, talk_content = "呃……其实我想问的是气脉到底是什么东西。", talk_name = "叙慕",},
	[501040126] = { talk_id = 501040126, group_id = 5010401, type = 3, talk_content = "这个……就没人知道了。", talk_name = "科生",},
	[501040127] = { talk_id = 501040127, group_id = 5010401, type = 3, talk_content = "唉……", talk_name = "科生",},
	[501050101] = { talk_id = 501050101, group_id = 5010501, type = 2, options = {{"在叫我吗？",501050102,},{"你是什么人？",501050108,},{"能和我说说这个地方吗？",501050112,},{"再见！",501050115,},}, talk_content = "竟然让我禁足，这些人到底明不明白我有多重要，喂，你！", talk_name = "陈佗",},
	[501050102] = { talk_id = 501050102, group_id = 5010501, type = 3, next_id = 501050103, talk_content = "没错，就是你，看着呆头呆脑的，但是传个话应该可以吧。", talk_name = "陈佗",},
	[501050103] = { talk_id = 501050103, group_id = 5010501, type = 3, next_id = 501050104, talk_content = "给我带个话给维安司，让他们尽快把我在外面的仓库夺回来。", talk_name = "陈佗",},
	[501050104] = { talk_id = 501050104, group_id = 5010501, type = 3, next_id = 501050105, talk_content = "可恶，只要能够打开仓库拿到证据的话，那我就能坐上那个位置……", talk_name = "陈佗",},
	[501050105] = { talk_id = 501050105, group_id = 5010501, type = 3, next_id = 501050106, talk_content = "青乌，我们还是别参和这件事比较好。", talk_name = "司命",},
	[501050106] = { talk_id = 501050106, group_id = 5010501, type = 3, next_id = 501050107, talk_content = "我觉得你是正确的。", talk_name = "叙慕",},
	[501050107] = { talk_id = 501050107, group_id = 5010501, type = 3, talk_content = "喂，等等，你们要去哪！", talk_name = "陈佗",},
	[501050108] = { talk_id = 501050108, group_id = 5010501, type = 3, next_id = 501050109, talk_content = "什么，在这里生活竟然不认得我吗，我可是悬天的监察长。", talk_name = "陈佗",},
	[501050109] = { talk_id = 501050109, group_id = 5010501, type = 3, next_id = 501050110, talk_content = "悬天……？", talk_name = "叙慕",},
	[501050110] = { talk_id = 501050110, group_id = 5010501, type = 3, next_id = 501050111, talk_content = "怎么，你为啥反应如此之大？", talk_name = "陈佗",},
	[501050111] = { talk_id = 501050111, group_id = 5010501, type = 3, talk_content = "我们还是先离开这里吧，青乌。", talk_name = "司命",},
	[501050112] = { talk_id = 501050112, group_id = 5010501, type = 3, next_id = 501050113, talk_content = "去去，我可没时间理会你这种人！", talk_name = "陈佗",},
	[501050113] = { talk_id = 501050113, group_id = 5010501, type = 3, next_id = 501050114, talk_content = "可恶，那些人肯定是打算趁我禁足的这段时间不断在封侯耳边说我坏话……", talk_name = "陈佗",},
	[501050114] = { talk_id = 501050114, group_id = 5010501, type = 3, talk_content = "要是封侯真的发怒的话……呃，我可不想成为太阳的食物……", talk_name = "陈佗",},
	[501050115] = { talk_id = 501050115, group_id = 5010501, type = 3, talk_content = "喂，等等！", talk_name = "陈佗",},
	[501060101] = { talk_id = 501060101, group_id = 5010601, type = 2, options = {{"再见",501060113,},{"你是什么人？",501060110,},{"在城里耕田？",501060102,},}, talk_content = "好不容易开垦出来的田地可不能浪费了。", talk_name = "鲁木头",},
	[501060102] = { talk_id = 501060102, group_id = 5010601, type = 3, next_id = 501060103, talk_content = "你这就不懂了吧，最近城里流行的就是这样的家庭农场。", talk_name = "鲁木头",},
	[501060103] = { talk_id = 501060103, group_id = 5010601, type = 3, next_id = 501060104, talk_content = "就是有点难看。", talk_name = "司命",},
	[501060104] = { talk_id = 501060104, group_id = 5010601, type = 3, next_id = 501060105, talk_content = "毕竟我们创生地不是红城，没什么耕作经验，我能照着书上还原成这样就已经很厉害了。", talk_name = "鲁木头",},
	[501060105] = { talk_id = 501060105, group_id = 5010601, type = 3, next_id = 501060106, talk_content = "所以准备在这里种点什么，稻谷之类的吗？", talk_name = "叙慕",},
	[501060106] = { talk_id = 501060106, group_id = 5010601, type = 3, next_id = 501060107, talk_content = "嗯……要不你们来决定吧，像你这样脉者应该会四处游走，期间能搞到不少种子。", talk_name = "鲁木头",},
	[501060107] = { talk_id = 501060107, group_id = 5010601, type = 3, next_id = 501060108, talk_content = "把想要种的种子拿过来，由我来负责栽培，收成以后我也会分一些的。", talk_name = "鲁木头",},
	[501060108] = { talk_id = 501060108, group_id = 5010601, type = 3, next_id = 501060109, talk_content = "也不是不行，青乌，之后搞点好吃的过来吧。", talk_name = "司命",},
	[501060109] = { talk_id = 501060109, group_id = 5010601, type = 3, talk_content = "（种这地方真的能有收成吗？）", talk_name = "叙慕",},
	[501060110] = { talk_id = 501060110, group_id = 5010601, type = 3, next_id = 501060111, talk_content = "熙来的普通居民，还是个耕田的，我这个人没什么好说的。", talk_name = "鲁木头",},
	[501060111] = { talk_id = 501060111, group_id = 5010601, type = 3, next_id = 501060112, talk_content = "这家伙把烂地叫田吗？", talk_name = "司命",},
	[501060112] = { talk_id = 501060112, group_id = 5010601, type = 3, talk_content = "我都听见了。", talk_name = "鲁木头",},
	[501060113] = { talk_id = 501060113, group_id = 5010601, type = 3, talk_content = "啊，哦……再见。", talk_name = "鲁木头",},
	[501070101] = { talk_id = 501070101, group_id = 5010701, type = 2, options = {{"你在发愁吗？",501070118,},{"你是什么人？",501070108,},{"“除祸”是什么？",501070102,},}, talk_content = "假若“除祸”能发挥效果的的话……", talk_name = "青门毕生",},
	[501070102] = { talk_id = 501070102, group_id = 5010701, type = 3, next_id = 501070103, talk_content = "就是平息气脉，最近噬脉生物群起而动，无论城内城外的气脉都变得絮乱。", talk_name = "青门毕生",},
	[501070103] = { talk_id = 501070103, group_id = 5010701, type = 3, next_id = 501070104, talk_content = "气脉一旦絮乱的话就会导致各种问题，至少现在城里的能源提供已经变得不太稳定了。", talk_name = "青门毕生",},
	[501070104] = { talk_id = 501070104, group_id = 5010701, type = 3, next_id = 501070105, talk_content = "所以平息气脉是要怎么做？", talk_name = "司命",},
	[501070105] = { talk_id = 501070105, group_id = 5010701, type = 3, next_id = 501070106, talk_content = "就是献舞呀，会这么问就代表你们是外地人吧。", talk_name = "青门毕生",},
	[501070106] = { talk_id = 501070106, group_id = 5010701, type = 3, next_id = 501070107, talk_content = "的确是刚到这里不久。", talk_name = "司命",},
	[501070107] = { talk_id = 501070107, group_id = 5010701, type = 3, talk_content = "那过几日后再来这里吧，那时候有一场小型的“除祸”，到时候让你们看看真正的“除祸”。", talk_name = "青门毕生",},
	[501070108] = { talk_id = 501070108, group_id = 5010701, type = 3, next_id = 501070109, talk_content = "青门的“除祸”，名曰青门毕生。", talk_name = "青门毕生",},
	[501070109] = { talk_id = 501070109, group_id = 5010701, type = 3, next_id = 501070110, talk_content = "青门？", talk_name = "司命",},
	[501070110] = { talk_id = 501070110, group_id = 5010701, type = 3, next_id = 501070111, talk_content = "没想到竟然有人不认识我们的名号，青门是创生地最大的“除祸”宗门。", talk_name = "青门毕生",},
	[501070111] = { talk_id = 501070111, group_id = 5010701, type = 3, next_id = 501070112, talk_content = "我们统辖着创生地下全部的祭礼事宜，无论大小都可以找我们帮忙。", talk_name = "青门毕生",},
	[501070112] = { talk_id = 501070112, group_id = 5010701, type = 3, next_id = 501070113, talk_content = "具体是做啥？", talk_name = "司命",},
	[501070113] = { talk_id = 501070113, group_id = 5010701, type = 3, next_id = 501070114, talk_content = "那就因人而异了，有些人只是普通的祝福，而有些人可能需要我们排除灾祸。", talk_name = "青门毕生",},
	[501070114] = { talk_id = 501070114, group_id = 5010701, type = 3, next_id = 501070115, talk_content = "总之只要有求，我们都会出手。", talk_name = "青门毕生",},
	[501070115] = { talk_id = 501070115, group_id = 5010701, type = 3, next_id = 501070116, talk_content = "怎样，我看你也是个脉者，要不要试试加入我们？", talk_name = "青门毕生",},
	[501070116] = { talk_id = 501070116, group_id = 5010701, type = 3, next_id = 501070117, talk_content = "嗯……暂时还是不了。", talk_name = "叙慕",},
	[501070117] = { talk_id = 501070117, group_id = 5010701, type = 3, talk_content = "真可惜。", talk_name = "青门毕生",},
	[501070118] = { talk_id = 501070118, group_id = 5010701, type = 3, next_id = 501070119, talk_content = "哦，嗯……噬脉生物侵蚀气脉的速度太快了。", talk_name = "青门毕生",},
	[501070119] = { talk_id = 501070119, group_id = 5010701, type = 3, next_id = 501070120, talk_content = "即便是利用“除祸”延缓侵蚀……我们，这片创生地的气脉又能撑多久呢。", talk_name = "青门毕生",},
	[501070120] = { talk_id = 501070120, group_id = 5010701, type = 3, talk_content = "唉。", talk_name = "青门毕生",},
	[501080101] = { talk_id = 501080101, group_id = 5010801, type = 2, options = {{"不，我还不饿……",501080109,},{"这里是？",501080102,},{"<color=#fed78c>能帮我制作点东西吗？</color>",5,},}, talk_content = "欢迎光临，里面的，贵客一位！", talk_name = "纪晴",},
	[501080102] = { talk_id = 501080102, group_id = 5010801, type = 3, next_id = 501080103, talk_content = "这里可是熙来老字号酒家，创生地可是这个世界上食用材料最丰富的地方。", talk_name = "纪晴",},
	[501080103] = { talk_id = 501080103, group_id = 5010801, type = 3, next_id = 501080104, talk_content = "天上飞的地上走的海里游的我们这里都能吃到哦。", talk_name = "纪晴",},
	[501080104] = { talk_id = 501080104, group_id = 5010801, type = 3, next_id = 501080105, talk_content = "那噬脉生物能吃到吗？", talk_name = "司命",},
	[501080105] = { talk_id = 501080105, group_id = 5010801, type = 3, next_id = 501080106, talk_content = "呃……我想除了客官你们大概也没其他人会吃那个了。", talk_name = "纪晴",},
	[501080106] = { talk_id = 501080106, group_id = 5010801, type = 3, next_id = 501080107, talk_content = "嘻嘻，我也就是问问而已，总之这里似乎是个饭馆，青乌我们等下来着填填肚子吧。", talk_name = "司命",},
	[501080107] = { talk_id = 501080107, group_id = 5010801, type = 3, next_id = 501080108, talk_content = "之后吧。", talk_name = "叙慕",},
	[501080108] = { talk_id = 501080108, group_id = 5010801, type = 3, talk_content = "哦，小的在这恭候两位哦！", talk_name = "纪晴",},
	[501080109] = { talk_id = 501080109, group_id = 5010801, type = 3, next_id = 501080110, talk_content = "不饿也可以先可以进店里看看哦，或者逛着逛着就想坐下了呢。", talk_name = "纪晴",},
	[501080110] = { talk_id = 501080110, group_id = 5010801, type = 3, talk_content = "听上去里面的东西好像很好吃的样子呢。", talk_name = "司命",},
	[501090101] = { talk_id = 501090101, group_id = 5010901, type = 2, options = {{"我只是随意看看",501090109,},{"我想问些城里的事情",501090108,},{"都有什么吃的？",501090102,},{"<color=#fed78c>我要买东西</color>",1,},{"<color=#fed78c>我要卖东西</color>",7,},}, talk_content = "是客人啊，要点些什么吗？", talk_name = "纪火望",},
	[501090102] = { talk_id = 501090102, group_id = 5010901, type = 3, next_id = 501090103, talk_content = "肉类，素菜，时下推荐的是净水白面。", talk_name = "纪火望",},
	[501090103] = { talk_id = 501090103, group_id = 5010901, type = 3, next_id = 501090104, talk_content = "怎么听上去和开水白面差不多。", talk_name = "司命",},
	[501090104] = { talk_id = 501090104, group_id = 5010901, type = 3, next_id = 501090105, talk_content = "做法就是那样，但是净水白面两样都是讲究的东西。", talk_name = "纪火望",},
	[501090105] = { talk_id = 501090105, group_id = 5010901, type = 3, next_id = 501090106, talk_content = "净水取自化生，别名生命之水，在化生能被用来制作自动木人。", talk_name = "纪火望",},
	[501090106] = { talk_id = 501090106, group_id = 5010901, type = 3, next_id = 501090107, talk_content = "而白面则是采用自红城，那地方盛产谷稻，其中作为白面材料的殊白谷一年只收一巡，珍贵的很。", talk_name = "纪火望",},
	[501090107] = { talk_id = 501090107, group_id = 5010901, type = 3, talk_content = "这么一说就觉得高级很多了，喂，青乌，给我整一碗！", talk_name = "司命",},
	[501090108] = { talk_id = 501090108, group_id = 5010901, type = 3, talk_content = "要打听情报的话去别家吧，我只关注自己的菜品，没留意别的事情。", talk_name = "纪火望",},
	[501090109] = { talk_id = 501090109, group_id = 5010901, type = 3, talk_content = "纪晴那丫头，又强拉客人进门了……", talk_name = "纪火望",},
	[501100101] = { talk_id = 501100101, group_id = 5011001, type = 2, options = {{"（还是别靠近吧。）",501100114,},{"封侯是什么人？",501100102,},}, talk_content = "可恶，突然就和我说被解雇了，要是能见到封侯的话……", talk_name = "莎鹿",},
	[501100102] = { talk_id = 501100102, group_id = 5011001, type = 3, next_id = 501100103, talk_content = "封侯就是天月城的城主，受王土之命统辖创生地的人呐。", talk_name = "莎鹿",},
	[501100103] = { talk_id = 501100103, group_id = 5011001, type = 3, next_id = 501100104, talk_content = "你听我说，封侯从太阳升起以后就一直将自己关在宁王塔上闭门不出。", talk_name = "莎鹿",},
	[501100104] = { talk_id = 501100104, group_id = 5011001, type = 3, next_id = 501100105, talk_content = "呃……就什么事都不管了。", talk_name = "莎鹿",},
	[501100105] = { talk_id = 501100105, group_id = 5011001, type = 3, next_id = 501100106, talk_content = "呃……可恶，结果悬天那些家伙就肆意妄为，封侯，封侯我可是一直都有在努力工作的！", talk_name = "莎鹿",},
	[501100106] = { talk_id = 501100106, group_id = 5011001, type = 3, next_id = 501100107, talk_content = "封侯和太阳，听到了有趣的情报了呢。", talk_name = "司命",},
	[501100107] = { talk_id = 501100107, group_id = 5011001, type = 3, next_id = 501100108, talk_content = "喂，关于那个太阳你还知道些什么？", talk_name = "司命",},
	[501100108] = { talk_id = 501100108, group_id = 5011001, type = 3, next_id = 501100109, talk_content = "知道，不，我什么都不知道，但好像创生主义的人把太阳叫作强良。", talk_name = "莎鹿",},
	[501100109] = { talk_id = 501100109, group_id = 5011001, type = 3, next_id = 501100110, talk_content = "给太阳取名吗？", talk_name = "叙慕",},
	[501100110] = { talk_id = 501100110, group_id = 5011001, type = 3, next_id = 501100111, talk_content = "嗯，每个地方的太阳好像还不太一样，大概是机器的代号之类的。", talk_name = "莎鹿",},
	[501100111] = { talk_id = 501100111, group_id = 5011001, type = 3, next_id = 501100112, talk_content = "话说回来，太阳到底是怎样做出来的呢……", talk_name = "莎鹿",},
	[501100112] = { talk_id = 501100112, group_id = 5011001, type = 3, next_id = 501100113, talk_content = "陷入沉默了……", talk_name = "叙慕",},
	[501100113] = { talk_id = 501100113, group_id = 5011001, type = 3, talk_content = "看来没办法继续问了呢。", talk_name = "司命",},
	[501100114] = { talk_id = 501100114, group_id = 5011001, type = 3, next_id = 501100115, talk_content = "君子不立危墙之下，发酒疯之人最为恐怖。", talk_name = "司命",},
	[501100115] = { talk_id = 501100115, group_id = 5011001, type = 3, next_id = 501100116, talk_content = "你还会吟诗？", talk_name = "叙慕",},
	[501100116] = { talk_id = 501100116, group_id = 5011001, type = 3, talk_content = "些许，些许，嘻嘻。", talk_name = "司命",},
	[501110101] = { talk_id = 501110101, group_id = 5011101, type = 2, options = {{"那这个房子要怎么办？",501110112,},{"不太平指的是？",501110109,},{"王土是什么地方？",501110102,},}, talk_content = "就算是创生地也不太平了，回头打点些关系到王土去吧。", talk_name = "侯通",},
	[501110102] = { talk_id = 501110102, group_id = 5011101, type = 3, next_id = 501110103, talk_content = "王土是当今世界最大的势力，创生地以及其他有人的地方其实都曾是王土的一部分。", talk_name = "侯通",},
	[501110103] = { talk_id = 501110103, group_id = 5011101, type = 3, next_id = 501110104, talk_content = "曾……现在不是了吗？", talk_name = "叙慕",},
	[501110104] = { talk_id = 501110104, group_id = 5011101, type = 3, next_id = 501110105, talk_content = "理论上还是，只是自从太阳升起以后王土对每个地区的把控力都变弱了。", talk_name = "侯通",},
	[501110105] = { talk_id = 501110105, group_id = 5011101, type = 3, next_id = 501110106, talk_content = "而且本来也采取的是领土分封的策略……随着世代更迭，各个地方都开始慢慢脱离王土的控制了。", talk_name = "侯通",},
	[501110106] = { talk_id = 501110106, group_id = 5011101, type = 3, next_id = 501110107, talk_content = "但虽然如此，王土还是当今最强的存在，他们要是有意的话那应该很快就能让各个领土重回自己手下吧。", talk_name = "侯通",},
	[501110107] = { talk_id = 501110107, group_id = 5011101, type = 3, next_id = 501110108, talk_content = "听上去挺厉害的，司命你有去过那里吗？", talk_name = "叙慕",},
	[501110108] = { talk_id = 501110108, group_id = 5011101, type = 3, talk_content = "我没去过，我的活动范围离王土太远了。", talk_name = "司命",},
	[501110109] = { talk_id = 501110109, group_id = 5011101, type = 3, next_id = 501110110, talk_content = "噬脉生物，创生主义。", talk_name = "侯通",},
	[501110110] = { talk_id = 501110110, group_id = 5011101, type = 3, next_id = 501110111, talk_content = "人也好，怪物也好都在作妖作乱，这就是我说的不太平。", talk_name = "侯通",},
	[501110111] = { talk_id = 501110111, group_id = 5011101, type = 3, talk_content = "要是封侯再不出面解决这些问题的话，这个地方就完了。", talk_name = "侯通",},
	[501110112] = { talk_id = 501110112, group_id = 5011101, type = 3, next_id = 501110113, talk_content = "卖了就可以了，怎么，你有兴趣吗？", talk_name = "侯通",},
	[501110113] = { talk_id = 501110113, group_id = 5011101, type = 3, next_id = 501110114, talk_content = "我们可是有拯救世界之任务，没办法在一个地方停留太久，买了也没啥用。", talk_name = "司命",},
	[501110114] = { talk_id = 501110114, group_id = 5011101, type = 3, next_id = 501110115, talk_content = "……就算要买我也没那么多钱。", talk_name = "叙慕",},
	[501110115] = { talk_id = 501110115, group_id = 5011101, type = 3, talk_content = "听上去你们比我还忙呢，哈哈，要是有意思就再来找我吧。", talk_name = "侯通",},
	[501120101] = { talk_id = 501120101, group_id = 5011201, type = 2, options = {{"（不靠近）",501120117,},{"你是什么人？",501120112,},{"什么天下无敌？",501120102,},}, talk_content = "天下无敌，天下无敌！", talk_name = "田光耀",},
	[501120102] = { talk_id = 501120102, group_id = 5011201, type = 3, next_id = 501120103, talk_content = "怎么，你想知道吗？", talk_name = "田光耀",},
	[501120103] = { talk_id = 501120103, group_id = 5011201, type = 3, next_id = 501120104, talk_content = "你不肯说就算了，青乌我们走。", talk_name = "司命",},
	[501120104] = { talk_id = 501120104, group_id = 5011201, type = 3, next_id = 501120105, talk_content = "等等，我说我说，我刚搞到了一个很厉害的佩从！", talk_name = "田光耀",},
	[501120105] = { talk_id = 501120105, group_id = 5011201, type = 3, next_id = 501120106, talk_content = "有多厉害？", talk_name = "司命",},
	[501120106] = { talk_id = 501120106, group_id = 5011201, type = 3, next_id = 501120107, talk_content = "你可别和其他人说，那个佩从可是能够隐身的。", talk_name = "田光耀",},
	[501120107] = { talk_id = 501120107, group_id = 5011201, type = 3, next_id = 501120108, talk_content = "隐……什么！？", talk_name = "司命",},
	[501120108] = { talk_id = 501120108, group_id = 5011201, type = 3, next_id = 501120109, talk_content = "嘘，嘘。", talk_name = "田光耀",},
	[501120109] = { talk_id = 501120109, group_id = 5011201, type = 3, next_id = 501120110, talk_content = "只要有了这个的话赚大钱就不是梦了，对了，你们要不要和我搭个伙？", talk_name = "田光耀",},
	[501120110] = { talk_id = 501120110, group_id = 5011201, type = 3, next_id = 501120111, talk_content = "报警吧，青乌。", talk_name = "司命",},
	[501120111] = { talk_id = 501120111, group_id = 5011201, type = 3, talk_content = "嗯。", talk_name = "叙慕",},
	[501120112] = { talk_id = 501120112, group_id = 5011201, type = 3, next_id = 501120113, talk_content = "哼，我可是天月城恶来帮出身的大盗田光耀，你们可以好好记住我的名字。", talk_name = "田光耀",},
	[501120113] = { talk_id = 501120113, group_id = 5011201, type = 3, next_id = 501120114, talk_content = "恶来帮？", talk_name = "司命",},
	[501120114] = { talk_id = 501120114, group_id = 5011201, type = 3, next_id = 501120115, talk_content = "恶来帮都没听过吗，天月城的东北一整块地都是属于我们的领土。", talk_name = "田光耀",},
	[501120115] = { talk_id = 501120115, group_id = 5011201, type = 3, next_id = 501120116, talk_content = "虽然明面上还是受封侯控制，但其实里面可是恶来的私人地方哦。", talk_name = "田光耀",},
	[501120116] = { talk_id = 501120116, group_id = 5011201, type = 3, talk_content = "听上去是个挺危险的组织呢。", talk_name = "司命",},
	[501120117] = { talk_id = 501120117, group_id = 5011201, type = 3, talk_content = "嗯，那家伙看上去有点怪怪的。", talk_name = "司命",},
	[501130101] = { talk_id = 501130101, group_id = 5011301, type = 2, options = {{"再见",501130108,},{"你找脉者做什么？",501130102,},}, talk_content = "脉者，哪里有脉者呢。", talk_name = "和春",},
	[501130102] = { talk_id = 501130102, group_id = 5011301, type = 3, next_id = 501130103, talk_content = "因为我想要知道成为脉者的方式。", talk_name = "和春",},
	[501130103] = { talk_id = 501130103, group_id = 5011301, type = 3, next_id = 501130104, talk_content = "那没办法呢，脉者可是与生俱来的天赋。", talk_name = "司命",},
	[501130104] = { talk_id = 501130104, group_id = 5011301, type = 3, next_id = 501130105, talk_content = "不可能，我之前遇见过一个人，他说他是开悟成为脉者的。", talk_name = "和春",},
	[501130105] = { talk_id = 501130105, group_id = 5011301, type = 3, next_id = 501130106, talk_content = "开悟是什么东西。", talk_name = "司命",},
	[501130106] = { talk_id = 501130106, group_id = 5011301, type = 3, next_id = 501130107, talk_content = "我当时也想问这个问题，但是回过头来那人就不见了。", talk_name = "和春",},
	[501130107] = { talk_id = 501130107, group_id = 5011301, type = 3, talk_content = "看来是作梦了。", talk_name = "司命",},
	[501130108] = { talk_id = 501130108, group_id = 5011301, type = 3, talk_content = "果然只能再去一次那个地方了。", talk_name = "和春",},
	[501140101] = { talk_id = 501140101, group_id = 5011401, type = 2, options = {{"再见",501140112,},{"有我能帮上忙的地方吗？",501140106,},{"能和我说说这个地方吗？",501140102,},}, talk_content = "下次又有新的维安警要被派遣到这边……宿舍又不够用了。", talk_name = "王云",},
	[501140102] = { talk_id = 501140102, group_id = 5011401, type = 3, next_id = 501140103, talk_content = "真突然呐，你是路过的旅客吧，真亏你在这种时势过来。", talk_name = "王云",},
	[501140103] = { talk_id = 501140103, group_id = 5011401, type = 3, next_id = 501140104, talk_content = "这里就是个叫熙来的小城而已，原本是维安警的一个根据点，结果随着人越来多越多规模就大起来了。", talk_name = "王云",},
	[501140104] = { talk_id = 501140104, group_id = 5011401, type = 3, next_id = 501140105, talk_content = "先是维安司的分局，然后是宿舍，接下来是闻到商机而来的各种商户。", talk_name = "王云",},
	[501140105] = { talk_id = 501140105, group_id = 5011401, type = 3, talk_content = "最近听说还要再来一批移居民……唉，看来得把防护墙再往外推一些了。", talk_name = "王云",},
	[501140106] = { talk_id = 501140106, group_id = 5011401, type = 3, next_id = 501140107, talk_content = "虽然我是需要，但你应该帮不上什么忙吧。", talk_name = "王云",},
	[501140107] = { talk_id = 501140107, group_id = 5011401, type = 3, next_id = 501140108, talk_content = "是这样吗，青乌可是很厉害的哦？", talk_name = "司命",},
	[501140108] = { talk_id = 501140108, group_id = 5011401, type = 3, next_id = 501140109, talk_content = "我自然相信脉者很厉害，但是我的工作是工程建筑，让脉者来与我一起建房子感觉怪怪的不是吗？", talk_name = "王云",},
	[501140109] = { talk_id = 501140109, group_id = 5011401, type = 3, next_id = 501140110, talk_content = "所以这里的房子都是你建的？", talk_name = "司命",},
	[501140110] = { talk_id = 501140110, group_id = 5011401, type = 3, next_id = 501140111, talk_content = "那肯定不是，我只是最近才到这里来上岗的，没想到一上来就接到扩建的任务。", talk_name = "王云",},
	[501140111] = { talk_id = 501140111, group_id = 5011401, type = 3, talk_content = "我再自己思考一下吧，不劳烦你们了。", talk_name = "王云",},
	[501140112] = { talk_id = 501140112, group_id = 5011401, type = 3, talk_content = "再见。", talk_name = "王云",},
	[501150101] = { talk_id = 501150101, group_id = 5011501, type = 2, options = {{"这个是非卖品，抱歉。",501150113,},{"（讲价）",501150103,},{"会吃坏肚子的。",501150102,},}, talk_content = "我喜欢你带着的肥鸟，现在就卖给我！", talk_name = "仲舒",},
	[501150102] = { talk_id = 501150102, group_id = 5011501, type = 3, talk_content = "才不会吃坏肚子，你当我是什么有毒的东西吗！", talk_name = "司命",},
	[501150103] = { talk_id = 501150103, group_id = 5011501, type = 3, next_id = 501150104, talk_content = "我最多可以出三万王币，这可是爸爸给我的零花钱哦……不够吗？", talk_name = "仲舒",},
	[501150104] = { talk_id = 501150104, group_id = 5011501, type = 3, next_id = 501150105, talk_content = "就三万还想买我，不过给你摸摸倒是可以。", talk_name = "司命",},
	[501150105] = { talk_id = 501150105, group_id = 5011501, type = 3, next_id = 501150106, talk_content = "这么说来王币是什么币种，价值高吗？", talk_name = "叙慕",},
	[501150106] = { talk_id = 501150106, group_id = 5011501, type = 3, next_id = 501150107, talk_content = "王币是由王土发行，王土及属下的封土来保障价值的现代唯一纸币，因为唯一所以价值也肯定高。", talk_name = "司命",},
	[501150107] = { talk_id = 501150107, group_id = 5011501, type = 3, next_id = 501150108, talk_content = "其他地方不会发行纸币了吗？", talk_name = "叙慕",},
	[501150108] = { talk_id = 501150108, group_id = 5011501, type = 3, next_id = 501150109, talk_content = "其他地方顶多会发行些代用卷，就我所知是这样的。", talk_name = "司命",},
	[501150109] = { talk_id = 501150109, group_id = 5011501, type = 3, next_id = 501150110, talk_content = "那三万王币算是什么水平？", talk_name = "叙慕",},
	[501150110] = { talk_id = 501150110, group_id = 5011501, type = 3, next_id = 501150111, talk_content = "普通人三四个月的工资吧，只能说这小鬼挺有钱的。", talk_name = "司命",},
	[501150111] = { talk_id = 501150111, group_id = 5011501, type = 3, next_id = 501150112, talk_content = "……", talk_name = "叙慕",},
	[501150112] = { talk_id = 501150112, group_id = 5011501, type = 3, talk_content = "等等，你该不会真想把我卖了吧？", talk_name = "司命",},
	[501150113] = { talk_id = 501150113, group_id = 5011501, type = 3, next_id = 501150114, talk_content = "哼哼，果然小孩子都无法抵挡成熟神鸟的魅力呀。", talk_name = "司命",},
	[501150114] = { talk_id = 501150114, group_id = 5011501, type = 3, talk_content = "……", talk_name = "叙慕",},
	[501160101] = { talk_id = 501160101, group_id = 5011601, type = 2, options = {{"暂时没有了。",501160110,},{"这里能做什么？",501160106,},{"说说现在的状况",501160102,},}, talk_content = "义人们，有什么想知道的吗？", talk_name = "朱嫦",},
	[501160102] = { talk_id = 501160102, group_id = 5011601, type = 3, next_id = 501160103, talk_content = "最近噬脉生物们的行动异常活跃，在前些日子，我们熙来管辖的最后一个据点也因此沦陷了。", talk_name = "朱嫦",},
	[501160103] = { talk_id = 501160103, group_id = 5011601, type = 3, next_id = 501160104, talk_content = "所以现在城外遍布危机，我们也不得不在收回据点之前停止一切货物，人员的运输，移动工作。", talk_name = "朱嫦",},
	[501160104] = { talk_id = 501160104, group_id = 5011601, type = 3, next_id = 501160105, talk_content = "虽然这样的方法很好的减少了损失，但始终不是长久之策，所以……", talk_name = "朱嫦",},
	[501160105] = { talk_id = 501160105, group_id = 5011601, type = 3, talk_content = "义人，请你去收复周围的据点，帮助这座熙来城的居民吧。", talk_name = "朱嫦",},
	[501160106] = { talk_id = 501160106, group_id = 5011601, type = 3, next_id = 501160107, talk_content = "这里是维安司，是市政和执法机构的集合体，而我是这里的负责人。", talk_name = "朱嫦",},
	[501160107] = { talk_id = 501160107, group_id = 5011601, type = 3, next_id = 501160108, talk_content = "名义上叫维安司长，但实际上你叫我市长都可以。", talk_name = "朱嫦",},
	[501160108] = { talk_id = 501160108, group_id = 5011601, type = 3, next_id = 501160109, talk_content = "总之，义人可以在这里查询各种信息，接受我们的委托等等。", talk_name = "朱嫦",},
	[501160109] = { talk_id = 501160109, group_id = 5011601, type = 3, talk_content = "当然，现在因为人手不足所以无法提供这些服务就是了。", talk_name = "朱嫦",},
	[501160110] = { talk_id = 501160110, group_id = 5011601, type = 3, talk_content = "嗯，那祝义人马到功成。", talk_name = "朱嫦",},
	[501170101] = { talk_id = 501170101, group_id = 5011701, type = 2, options = {{"我再逛逛。",501170111,},{"有没有什么推荐",501170102,},{"<color=#fed78c>我要买东西</color>",1,},}, talk_content = "欢迎光临，要买点什么吗？", talk_name = "安琪",},
	[501170102] = { talk_id = 501170102, group_id = 5011701, type = 3, next_id = 501170103, talk_content = "推荐那自然各种生态产物了，皮毛，奶类，肉食。", talk_name = "安琪",},
	[501170103] = { talk_id = 501170103, group_id = 5011701, type = 3, next_id = 501170104, talk_content = "这些可是创生地远近闻名的经济基本。", talk_name = "安琪",},
	[501170104] = { talk_id = 501170104, group_id = 5011701, type = 3, next_id = 501170105, talk_content = "对了，看两位大概是旅人吧，我这里刚好进货了一个优质的旅行包，只需五千哦。", talk_name = "安琪",},
	[501170105] = { talk_id = 501170105, group_id = 5011701, type = 3, next_id = 501170106, talk_content = "五千也太贵了吧。", talk_name = "司命",},
	[501170106] = { talk_id = 501170106, group_id = 5011701, type = 3, next_id = 501170107, talk_content = "这可是特挑真皮，我们百玲珑的商品可不怕什么货比三家呢。", talk_name = "安琪",},
	[501170107] = { talk_id = 501170107, group_id = 5011701, type = 3, next_id = 501170108, talk_content = "真有这么好？", talk_name = "司命",},
	[501170108] = { talk_id = 501170108, group_id = 5011701, type = 3, next_id = 501170109, talk_content = "真有这么好。", talk_name = "安琪",},
	[501170109] = { talk_id = 501170109, group_id = 5011701, type = 3, next_id = 501170110, talk_content = "好，我们买了。", talk_name = "司命",},
	[501170110] = { talk_id = 501170110, group_id = 5011701, type = 3, talk_content = "不，我没说要吧……", talk_name = "叙慕",},
	[501170111] = { talk_id = 501170111, group_id = 5011701, type = 3, talk_content = "好的，有选中的叫我一声就可以。", talk_name = "安琪",},
	[501180101] = { talk_id = 501180101, group_id = 5011801, type = 2, options = {{"你们自己决定吧。",501180106,},{"我认为可以。",501180105,},{"大恶鬼是？",501180102,},}, talk_content = "你也认为我们该玩大恶鬼对吧！", talk_name = "天白",},
	[501180102] = { talk_id = 501180102, group_id = 5011801, type = 3, next_id = 501180103, talk_content = "你没玩过吗，指的是其中一个人扮演恶鬼，然后将其他躲起来的恶鬼找到并吃掉的游戏。", talk_name = "天白",},
	[501180103] = { talk_id = 501180103, group_id = 5011801, type = 3, next_id = 501180104, talk_content = "好猎奇的游戏，话说这不是捉迷藏吗。", talk_name = "司命",},
	[501180104] = { talk_id = 501180104, group_id = 5011801, type = 3, talk_content = "嗯，是这样，大人们都说从前创生地这里有过一只恶鬼云云的，所以就把这游戏叫作大恶鬼了。", talk_name = "天白",},
	[501180105] = { talk_id = 501180105, group_id = 5011801, type = 3, talk_content = "好，所以我这里两票，接下来我们玩大恶鬼！", talk_name = "天白",},
	[501180106] = { talk_id = 501180106, group_id = 5011801, type = 3, talk_content = "唔，所以该玩什么呢。", talk_name = "天白",},
	[501190101] = { talk_id = 501190101, group_id = 5011901, type = 2, options = {{"（不靠近）",501190117,},{"你是什么人？",501190112,},{"什么天下无敌？",501190102,},}, talk_content = "哼哼，我已经天下无敌了！", talk_name = "田光耀",},
	[501190102] = { talk_id = 501190102, group_id = 5011901, type = 3, next_id = 501190103, talk_content = "怎么，你想知道吗？", talk_name = "田光耀",},
	[501190103] = { talk_id = 501190103, group_id = 5011901, type = 3, next_id = 501190104, talk_content = "你不肯说就算了，青乌我们走。", talk_name = "司命",},
	[501190104] = { talk_id = 501190104, group_id = 5011901, type = 3, next_id = 501190105, talk_content = "等等，我说我说，我刚搞到了一个很厉害的佩从！", talk_name = "田光耀",},
	[501190105] = { talk_id = 501190105, group_id = 5011901, type = 3, next_id = 501190106, talk_content = "有多厉害？", talk_name = "司命",},
	[501190106] = { talk_id = 501190106, group_id = 5011901, type = 3, next_id = 501190107, talk_content = "你可别和其他人说，那个佩从可是能够隐身的。", talk_name = "田光耀",},
	[501190107] = { talk_id = 501190107, group_id = 5011901, type = 3, next_id = 501190108, talk_content = "隐……什么！？", talk_name = "司命",},
	[501190108] = { talk_id = 501190108, group_id = 5011901, type = 3, next_id = 501190109, talk_content = "嘘，嘘。", talk_name = "田光耀",},
	[501190109] = { talk_id = 501190109, group_id = 5011901, type = 3, next_id = 501190110, talk_content = "只要有了这个的话赚大钱就不是梦了，对了，你们要不要和我搭个伙？", talk_name = "田光耀",},
	[501190110] = { talk_id = 501190110, group_id = 5011901, type = 3, next_id = 501190111, talk_content = "小孩子就别想这些事情了，我们走吧。", talk_name = "司命",},
	[501190111] = { talk_id = 501190111, group_id = 5011901, type = 3, talk_content = "嗯。", talk_name = "叙慕",},
	[501190112] = { talk_id = 501190112, group_id = 5011901, type = 3, next_id = 501190113, talk_content = "哼，我可是天月城恶来帮出身的大盗田光耀，你们可以好好记住我的名字。", talk_name = "田光耀",},
	[501190113] = { talk_id = 501190113, group_id = 5011901, type = 3, next_id = 501190114, talk_content = "恶来帮？", talk_name = "司命",},
	[501190114] = { talk_id = 501190114, group_id = 5011901, type = 3, next_id = 501190115, talk_content = "恶来帮都没听过吗，天月城的东北一整块地都是属于我们的领土。", talk_name = "田光耀",},
	[501190115] = { talk_id = 501190115, group_id = 5011901, type = 3, next_id = 501190116, talk_content = "虽然明面上还是受封侯控制，但其实里面可是恶来的私人地方哦。", talk_name = "田光耀",},
	[501190116] = { talk_id = 501190116, group_id = 5011901, type = 3, talk_content = "应该是小朋友自己编名字的组织吧……", talk_name = "司命",},
	[501190117] = { talk_id = 501190117, group_id = 5011901, type = 3, talk_content = "嗯，那家伙看上去有点怪怪的。", talk_name = "司命",},
	[501200101] = { talk_id = 501200101, group_id = 5012001, type = 1, next_id = 501200102, talk_content = "唉，真不想陪这两个小鬼玩游戏。", talk_name = "江红",},
	[501200102] = { talk_id = 501200102, group_id = 5012001, type = 1, next_id = 501200103, talk_content = "装啥呢，不想陪你直接走不就得了。", talk_name = "司命",},
	[501200103] = { talk_id = 501200103, group_id = 5012001, type = 1, talk_content = "哼，我只是家里大人拜托我照顾他们两才站在这里的。", talk_name = "江红",},
	[501210101] = { talk_id = 501210101, group_id = 5012101, type = 2, options = {{"你们自己决定吧。",501210109,},{"我认为可以。",501210104,},{"你会玩吗？",501210102,},}, talk_content = "接下来要玩的应该是麻将才对，你说如何？", talk_name = "小蓝",},
	[501210102] = { talk_id = 501210102, group_id = 5012101, type = 3, next_id = 501210103, talk_content = "当然会呀，熙来这里的大人们没事聚在一起就玩那个。", talk_name = "小蓝",},
	[501210103] = { talk_id = 501210103, group_id = 5012101, type = 3, talk_content = "虽然没玩过，但我看都看懂了。", talk_name = "小蓝",},
	[501210104] = { talk_id = 501210104, group_id = 5012101, type = 3, next_id = 501210105, talk_content = "对吧对吧，之前来这里宣传创生主义的人也说他很喜欢那个游戏呢。", talk_name = "小蓝",},
	[501210105] = { talk_id = 501210105, group_id = 5012101, type = 3, next_id = 501210106, talk_content = "创生主义是啥？", talk_name = "司命",},
	[501210106] = { talk_id = 501210106, group_id = 5012101, type = 3, next_id = 501210107, talk_content = "唔，我也不知道，他们说他们在保护创生地什么的。", talk_name = "小蓝",},
	[501210107] = { talk_id = 501210107, group_id = 5012101, type = 3, next_id = 501210108, talk_content = "不过后来被维安警发现，那人就赶紧跑走了。", talk_name = "小蓝",},
	[501210108] = { talk_id = 501210108, group_id = 5012101, type = 3, talk_content = "不就是个可疑人物吗！", talk_name = "司命",},
	[501210109] = { talk_id = 501210109, group_id = 5012101, type = 3, talk_content = "不就是因为定不下来才问你的嘛！", talk_name = "小蓝",},
	[501220101] = { talk_id = 501220101, group_id = 5012201, type = 2, options = {{"再见",501220110,},{"发生什么事了吗？",501220102,},}, talk_content = "大人们最近都不准我们到外面玩了……", talk_name = "让鹿",},
	[501220102] = { talk_id = 501220102, group_id = 5012201, type = 3, next_id = 501220103, talk_content = "好像说是噬脉生物到处跑之类的。", talk_name = "让鹿",},
	[501220103] = { talk_id = 501220103, group_id = 5012201, type = 3, next_id = 501220104, talk_content = "而且……", talk_name = "让鹿",},
	[501220104] = { talk_id = 501220104, group_id = 5012201, type = 3, next_id = 501220105, talk_content = "而且？", talk_name = "司命",},
	[501220105] = { talk_id = 501220105, group_id = 5012201, type = 3, next_id = 501220106, talk_content = "你们可别告诉别人哦。", talk_name = "让鹿",},
	[501220106] = { talk_id = 501220106, group_id = 5012201, type = 3, next_id = 501220107, talk_content = "我的父亲是维安司的头头，他说外面那些据点全被噬脉生物霸占了。", talk_name = "让鹿",},
	[501220107] = { talk_id = 501220107, group_id = 5012201, type = 3, next_id = 501220108, talk_content = "要是继续下去的话这里或者也会被噬脉生物攻击也说不定。", talk_name = "让鹿",},
	[501220108] = { talk_id = 501220108, group_id = 5012201, type = 3, next_id = 501220109, talk_content = "这种状况下你还挺冷静的嘛。", talk_name = "司命",},
	[501220109] = { talk_id = 501220109, group_id = 5012201, type = 3, talk_content = "嗯，因为父亲和我说过没问题的。", talk_name = "让鹿",},
	[501220110] = { talk_id = 501220110, group_id = 5012201, type = 3, talk_content = "啊，再见。", talk_name = "让鹿",},
	[501230101] = { talk_id = 501230101, group_id = 5012301, type = 2, options = {{"再见",501230106,},{"辉光蜻蜓？",501230102,},{"<color=#fed78c>我看看。</color>",1,},}, talk_content = "欢迎来到辉光蜻蜓露天店，要看看货吗老板！", talk_name = "张嘉媛",},
	[501230102] = { talk_id = 501230102, group_id = 5012301, type = 3, next_id = 501230103, talk_content = "难道老板是第一次来这边吗，那可得好好给您介绍一下我们辉光蜻蜓呐。", talk_name = "张嘉媛",},
	[501230103] = { talk_id = 501230103, group_id = 5012301, type = 3, next_id = 501230104, talk_content = "辉光蜻蜓是最近才在世界上兴起的工匠行会，业务范围从日用五金到战斗装备都应有仅有。", talk_name = "张嘉媛",},
	[501230104] = { talk_id = 501230104, group_id = 5012301, type = 3, next_id = 501230105, talk_content = "假若客人你有相应材料和设计图的话我们也会承接各种定做业务。", talk_name = "张嘉媛",},
	[501230105] = { talk_id = 501230105, group_id = 5012301, type = 3, talk_content = "总之只要你在路上看见我背后这样绿黑相间的车辆就尽管搭话吧，辉光蜻蜓一定能为你提供最优秀的服务的！", talk_name = "张嘉媛",},
	[501230106] = { talk_id = 501230106, group_id = 5012301, type = 3, talk_content = "不看看吗，真可惜。", talk_name = "张嘉媛",},
	[501240101] = { talk_id = 501240101, group_id = 5012401, type = 1, next_id = 501240102, talk_content = "熙来这里大人很少对吧。", talk_name = "卢米",},
	[501240102] = { talk_id = 501240102, group_id = 5012401, type = 1, next_id = 501240103, talk_content = "是呢，这是为什么？", talk_name = "司命",},
	[501240103] = { talk_id = 501240103, group_id = 5012401, type = 1, next_id = 501240104, talk_content = "因为大人们都去天月城工作了。", talk_name = "卢米",},
	[501240104] = { talk_id = 501240104, group_id = 5012401, type = 1, next_id = 501240105, talk_content = "相对于这个什么都没有小城，去天月赚钱才是真正的出路。", talk_name = "卢米",},
	[501240105] = { talk_id = 501240105, group_id = 5012401, type = 1, next_id = 501240106, talk_content = "唉，要是这里也能变成大城市的话……", talk_name = "卢米",},
	[501240106] = { talk_id = 501240106, group_id = 5012401, type = 1, talk_content = "真是个小大人。", talk_name = "司命",},
	[501250101] = { talk_id = 501250101, group_id = 5012501, type = 2, options = {{"再见",501250108,},{"你和悬天相熟吗？",501250102,},}, talk_content = "悬天的人说走就走，本以为今天还能讨到糖果的。", talk_name = "振港",},
	[501250102] = { talk_id = 501250102, group_id = 5012501, type = 3, next_id = 501250103, talk_content = "很熟哦，他们之前在城里住的时候我每天都找他们玩。", talk_name = "振港",},
	[501250103] = { talk_id = 501250103, group_id = 5012501, type = 3, next_id = 501250104, talk_content = "你和他们都玩什么？", talk_name = "叙慕",},
	[501250104] = { talk_id = 501250104, group_id = 5012501, type = 3, next_id = 501250105, talk_content = "就是很普通的游戏啊，跳远，卡牌之类的，赢过他们的话还能有糖拿呢。", talk_name = "振港",},
	[501250105] = { talk_id = 501250105, group_id = 5012501, type = 3, next_id = 501250106, talk_content = "听着就和友好邻居一样……这群家伙还真会装。", talk_name = "司命",},
	[501250106] = { talk_id = 501250106, group_id = 5012501, type = 3, next_id = 501250107, talk_content = "所以你要不要也说说给我听你所认识的悬天是啥样子的？", talk_name = "叙慕",},
	[501250107] = { talk_id = 501250107, group_id = 5012501, type = 3, talk_content = "回头吧。", talk_name = "司命",},
	[501250108] = { talk_id = 501250108, group_id = 5012501, type = 3, talk_content = "唔，所以你也没糖果是吗……", talk_name = "振港",},
	[501260101] = { talk_id = 501260101, group_id = 5012601, type = 2, options = {{"再见",501260117,},{"你在这里做什么？",501260114,},{"你是谁",501260111,},{"有什么方法？",501260102,},{"<color=#fed78c>升级天赋</color>",2,},}, talk_content = "“竹”是气脉的节点，为了不让节点衰弱下去，我们得想想办法。", talk_name = "菈妮",},
	[501260102] = { talk_id = 501260102, group_id = 5012601, type = 3, next_id = 501260103, talk_content = "方法有很多，但最有效的办法应该是把离竹的脉灵找回来。", talk_name = "菈妮",},
	[501260103] = { talk_id = 501260103, group_id = 5012601, type = 3, next_id = 501260104, talk_content = "气脉凝聚的地方会形成象征的竹，而为了调顺其中能量，脉灵又从竹中出生。", talk_name = "菈妮",},
	[501260104] = { talk_id = 501260104, group_id = 5012601, type = 3, next_id = 501260105, talk_content = "脉灵就是之前见过的小动物吧。", talk_name = "叙慕",},
	[501260105] = { talk_id = 501260105, group_id = 5012601, type = 3, next_id = 501260106, talk_content = "是捏，黑白相间的，那个的确有调顺气脉，平息风雨的能力。", talk_name = "司命",},
	[501260106] = { talk_id = 501260106, group_id = 5012601, type = 3, next_id = 501260107, talk_content = "所以说那些脉灵出生以后就会离开竹子吗？", talk_name = "叙慕",},
	[501260107] = { talk_id = 501260107, group_id = 5012601, type = 3, next_id = 501260108, talk_content = "不，让他们离开的原因有很多，或许迷路，或许灾害，或许是在寻找同伴。", talk_name = "菈妮",},
	[501260108] = { talk_id = 501260108, group_id = 5012601, type = 3, next_id = 501260109, talk_content = "但总之一旦竹与脉灵分离那节点就只有被噬脉生物摧毁一途。", talk_name = "菈妮",},
	[501260109] = { talk_id = 501260109, group_id = 5012601, type = 3, next_id = 501260110, talk_content = "两位，假若你们有闲的话就请留心路上的脉灵，并且将他们带到“竹”所在的地方吧。", talk_name = "菈妮",},
	[501260110] = { talk_id = 501260110, group_id = 5012601, type = 3, talk_content = "我们会注意的。", talk_name = "叙慕",},
	[501260111] = { talk_id = 501260111, group_id = 5012601, type = 3, next_id = 501260112, talk_content = "我是菈妮，一个从远方而来的“除祸”，节点再这么衰弱下去就糟糕了。", talk_name = "菈妮",},
	[501260112] = { talk_id = 501260112, group_id = 5012601, type = 3, next_id = 501260113, talk_content = "我在路上也见过很多这样的“竹”，只是一两个应该不要紧吧？", talk_name = "叙慕",},
	[501260113] = { talk_id = 501260113, group_id = 5012601, type = 3, talk_content = "积少成多，一根接着一根的话那可不是闹着玩的。", talk_name = "司命",},
	[501260114] = { talk_id = 501260114, group_id = 5012601, type = 3, next_id = 501260115, talk_content = "我在等待脉灵回归竹的日子，眼前的竹已经十分衰弱，倘若我一离开就会变成枯萎了吧。", talk_name = "菈妮",},
	[501260115] = { talk_id = 501260115, group_id = 5012601, type = 3, next_id = 501260116, talk_content = "这人虽然说话文绉绉的，但眼神一直盯着这边呢，青乌。", talk_name = "司命",},
	[501260116] = { talk_id = 501260116, group_id = 5012601, type = 3, talk_content = "是打算让我帮忙吗？", talk_name = "叙慕",},
	[501260117] = { talk_id = 501260117, group_id = 5012601, type = 3, talk_content = "再见。", talk_name = "菈妮",},
	[501270101] = { talk_id = 501270101, group_id = 5012701, type = 2, options = {{"再见",501250108,},{"你和悬天相熟吗？",501250102,},}, talk_content = "万岁，是客人耶，来买点东西吧？", talk_name = "尚",},
	[501270102] = { talk_id = 501270102, group_id = 5012701, type = 3, next_id = 501270103, talk_content = "没有，我在这里呆一天了，除了两位跟旁边那个念念叨叨的人以外就没有其他人了。", talk_name = "尚",},
	[501270103] = { talk_id = 501270103, group_id = 5012701, type = 3, next_id = 501270104, talk_content = "那为什么不去人更多的地方？", talk_name = "司命",},
	[501270104] = { talk_id = 501270104, group_id = 5012701, type = 3, next_id = 501270105, talk_content = "因为小人相信富贵险中求，意外就是赚钱最好的时机。", talk_name = "尚",},
	[501270105] = { talk_id = 501270105, group_id = 5012701, type = 3, next_id = 501270106, talk_content = "所以呢？", talk_name = "司命",},
	[501270106] = { talk_id = 501270106, group_id = 5012701, type = 3, next_id = 501270107, talk_content = "就是说小人带了一堆回复药品，只等一个受了重伤的旅行者好高价卖药呢，嘿嘿。", talk_name = "尚",},
	[501270107] = { talk_id = 501270107, group_id = 5012701, type = 3, talk_content = "黑商啊……", talk_name = "司命",},
	[501270108] = { talk_id = 501270108, group_id = 5012701, type = 3, talk_content = "唉，是不是该收摊了。", talk_name = "尚",},
	[501280101] = { talk_id = 501280101, group_id = 5012801, type = 1, next_id = 501280102, talk_content = "天地五行阴阳法，一窑一掌道化间。", talk_name = "渺",},
	[501280102] = { talk_id = 501280102, group_id = 5012801, type = 2, options = {{"暂时不用了。",501280115,},{"你是什么人？",501280111,},{"你的窑能做什么？",501280103,},{"<color=#fed78c>这些东西你要么</color>",7,},}, talk_content = "咱一眼就看出二位贵客风度不凡，想必是可以通晓天道之人。怎样，要不要来动手开窑，体会一下天地万象尽在掌握的感觉？", talk_name = "渺",},
	[501280103] = { talk_id = 501280103, group_id = 5012801, type = 3, next_id = 501280104, talk_content = "咱的窑无所不能无所不有，大如弥天鲲鹏，小如朝露芥子，都可从窑内炼化而得……", talk_name = "渺",},
	[501280104] = { talk_id = 501280104, group_id = 5012801, type = 3, next_id = 501280105, talk_content = "说简单点啊你这神棍！", talk_name = "司命",},
	[501280105] = { talk_id = 501280105, group_id = 5012801, type = 3, next_id = 501280106, talk_content = "咳……用晓畅易懂的方式来说，只要客官把必要的材料放进窑里，就可以炼制出所需的物件。", talk_name = "渺",},
	[501280106] = { talk_id = 501280106, group_id = 5012801, type = 3, next_id = 501280107, talk_content = "这土包子真有那么厉害吗，不会是招摇撞骗的吧？", talk_name = "司命",},
	[501280107] = { talk_id = 501280107, group_id = 5012801, type = 3, next_id = 501280108, talk_content = "有眼不识泰山，咱这可是王土御造的宝窑，龙首通天鱼尾指地，炼化诸物都是以气脉为媒，自然神妙超凡。", talk_name = "渺",},
	[501280108] = { talk_id = 501280108, group_id = 5012801, type = 3, next_id = 501280109, talk_content = "都有什么能炼的？", talk_name = "叙慕",},
	[501280109] = { talk_id = 501280109, group_id = 5012801, type = 3, next_id = 501280110, talk_content = "还是这位客官识货。咱这宝窑和一般的窑不同，可采世间万物炼化成为脉者提升实力的珍宝，您这样的脉者是万万不该错过的。", talk_name = "渺",},
	[501280110] = { talk_id = 501280110, group_id = 5012801, type = 3, talk_content = "多说无益，两位若是还不相信，只要开窑一试便知。", talk_name = "渺",},
	[501280111] = { talk_id = 501280111, group_id = 5012801, type = 3, next_id = 501280112, talk_content = "小道无名无姓，江湖人称为渺，是个闯荡四方的行士。", talk_name = "渺",},
	[501280112] = { talk_id = 501280112, group_id = 5012801, type = 3, next_id = 501280113, talk_content = "别看咱一副穷酸样，在开窑炼化这方面可还没碰上过什么敌手。就算是在天月城，咱的名声说出来也是无人不晓的。", talk_name = "渺",},
	[501280113] = { talk_id = 501280113, group_id = 5012801, type = 3, next_id = 501280114, talk_content = "完全没听说过捏，之后要是在天月城没打听到你就给我们打三折如何？", talk_name = "司命",},
	[501280114] = { talk_id = 501280114, group_id = 5012801, type = 3, talk_content = "那自然没问题，咱就悉听尊便了。", talk_name = "渺",},
	[501280115] = { talk_id = 501280115, group_id = 5012801, type = 3, talk_content = "无妨无妨，咱掐指一算，便知二位不多时日还会再来此地，咱们到时再见。", talk_name = "渺",},
	[501280201] = { talk_id = 501280201, group_id = 5012802, type = 1, next_id = 501280202, talk_content = "艾芊水果铺，物美价廉，童叟无欺。", talk_name = "郝艾芊",},
	[501280202] = { talk_id = 501280202, group_id = 5012802, type = 2, options = {{"不需要，谢谢。",},}, talk_content = "欢迎光临，要不要来点什么？", talk_name = "郝艾芊",},
	[501280301] = { talk_id = 501280301, group_id = 5012803, type = 2, options = {{"还是先不用了……",501280302,},}, talk_content = "考虑好了吗？", talk_name = "郝艾芊",},
	[501280302] = { talk_id = 501280302, group_id = 5012803, type = 1, talk_content = "欢迎下次再来呢。", talk_name = "郝艾芊",},
	[501280401] = { talk_id = 501280401, group_id = 5012804, type = 1, next_id = 501280402, talk_content = "嘿！你还没付钱呢！", talk_name = "郝艾芊",},
	[501280402] = { talk_id = 501280402, group_id = 5012804, type = 2, options = {{"（转头就走）（警报）",501280403,},{"价格还是太贵了。（还价）",8,},}, talk_content = "站住别动！", talk_name = "郝艾芊",},
	[501280403] = { talk_id = 501280403, group_id = 5012804, type = 3, talk_content = "（店家触发了警报。）",},
	[501280501] = { talk_id = 501280501, group_id = 5012805, type = 2, next_id = 501280307, options = {{"不需要了，谢谢。",501280502,},}, talk_content = "这么合算的价格可是千载难逢呢，要不要再买点什么？", talk_name = "郝艾芊",},
	[501280502] = { talk_id = 501280502, group_id = 5012805, type = 1, talk_content = "不要了吗？太可惜了。欢迎下次再来哦。", talk_name = "郝艾芊",},
	[501280601] = { talk_id = 501280601, group_id = 5012806, type = 1, next_id = 501280602, talk_content = "你涉嫌违反天月城法规，现将你逮捕！", talk_name = "守卫",},
	[501280602] = { talk_id = 501280602, group_id = 5012806, type = 2, options = {{"抗拒",501280603,},{"狡辩",501280604,},{"投降",501280605,},}, talk_content = "立即停止你的行为！", talk_name = "守卫",},
	[501280603] = { talk_id = 501280603, group_id = 5012806, type = 3, talk_content = "嫌疑人拘捕，追击开始！", talk_name = "守卫",},
	[501280604] = { talk_id = 501280604, group_id = 5012806, type = 3, next_id = 501280606, talk_content = "人赃俱获，还想狡辩！", talk_name = "守卫",},
	[501280605] = { talk_id = 501280605, group_id = 5012806, type = 3, talk_content = "下不为例，速速离开。", talk_name = "守卫",},
	[501280606] = { talk_id = 501280606, group_id = 5012806, type = 1, talk_content = "驱逐出城！", talk_name = "守卫",},
	[501280701] = { talk_id = 501280701, group_id = 5012807, type = 1, talk_content = "屡教不改，追击开始！", talk_name = "守卫",},
	[501280801] = { talk_id = 501280801, group_id = 5012808, type = 2, options = {{"投降",501280802,},{"继续逃跑",501280803,},}, talk_content = "不要再做无谓的抵抗了。", talk_name = "守卫",},
	[501280802] = { talk_id = 501280802, group_id = 5012808, type = 3, talk_content = "算你识相。", talk_name = "守卫",},
	[501280803] = { talk_id = 501280803, group_id = 5012808, type = 3, talk_content = "死不悔改！", talk_name = "守卫",},
	[501280901] = { talk_id = 501280901, group_id = 5012809, type = 1, talk_content = "算了，看在今天是活动日的份上，就给你优惠一点吧。", talk_name = "郝艾芊",},
	[501281001] = { talk_id = 501281001, group_id = 5012810, type = 1, next_id = 501281002, options = {{"暂时不用了。",},}, talk_content = "1111", talk_name = "青乌",},
	[501281002] = { talk_id = 501281002, group_id = 5012810, type = 2, talk_content = "2222", talk_name = "青乌",},
	[601010101] = { talk_id = 601010101, group_id = 6010101, type = 2, options = {{"想要什么？",3,},}, talk_content = "嘻哈，我有想要的东西。", talk_name = "脉灵",},
	[601010201] = { talk_id = 601010201, group_id = 6010102, type = 2, options = {{"（触碰气脉。）",2,},}, talk_content = "（有种温暖的感觉。）",},
	[601010301] = { talk_id = 601010301, group_id = 6010103, type = 5,},
	[601010401] = { talk_id = 601010401, group_id = 6010104, type = 5,},
	[601010501] = { talk_id = 601010501, group_id = 6010105, type = 5,},
	[601010601] = { talk_id = 601010601, group_id = 6010106, type = 2, options = {{"什么功劳，让我也听听。",601010602,},{"你好。",601010602,},}, talk_content = "喂，喂，人呢？不会打算一个人把功劳全揽了吧？", talk_name = "可疑路人",},
	[601010602] = { talk_id = 601010602, group_id = 6010106, type = 3, next_id = 601010603, talk_content = "别插话，我有急事……啊，哇！", talk_name = "可疑路人",},
	[601010603] = { talk_id = 601010603, group_id = 6010106, type = 3, talk_content = "你到底是怎么……算了不管了，送上门来就别想跑了！", talk_name = "可疑路人",},
	[601010701] = { talk_id = 601010701, group_id = 6010107, type = 1, next_id = 601010702, talk_content = "在城里好端端的就遇上气脉乱流了，真吓人。", talk_name = "郭郦",},
	[601010702] = { talk_id = 601010702, group_id = 6010107, type = 1, talk_content = "这是给你的报酬，请收下吧，谢谢你出手救我。", talk_name = "郭郦",},
	[601010801] = { talk_id = 601010801, group_id = 6010108, type = 1, next_id = 601010802, talk_content = "所有人都不准动！双手举起来！", talk_name = "？？？",},
	[601010802] = { talk_id = 601010802, group_id = 6010108, type = 1, next_id = 601010803, talk_content = "啊！你……你是什么人？", talk_name = "澜璟",},
	[601010803] = { talk_id = 601010803, group_id = 6010108, type = 1, next_id = 601010804, talk_content = "看不见我手里拿的是什么吗？去把值钱的东西全都拿出来！", talk_name = "劫匪",},
	[601010804] = { talk_id = 601010804, group_id = 6010108, type = 1, next_id = 601010805, talk_content = "好、好……我这就去……", talk_name = "澜璟",},
	[601010805] = { talk_id = 601010805, group_id = 6010108, type = 1, next_id = 601010806, talk_content = "还有你，乖乖靠墙站好，别耍小花招！", talk_name = "劫匪",},
	[601010806] = { talk_id = 601010806, group_id = 6010108, type = 2, child_type = 21, options = {{"不能袖手旁观。",601010807,},{"111",601010808,},{"暂且静观其变吧。",601010809,},}, talk_content = "（居然遇上劫匪了，现在该怎么办呢……）", talk_name = "青乌",},
	[601010807] = { talk_id = 601010807, group_id = 6010108, type = 3, icon = "d_1001", talk_content = "大伙一起上，别让她坏了我们的好事！", talk_name = "劫匪",},
	[601010808] = { talk_id = 601010808, group_id = 6010108, type = 3, conditions = {7011001,}, icon = "interaction_4", talk_content = "大伙一起上，别让她坏了我们的好事！", talk_name = "劫匪",},
	[601010809] = { talk_id = 601010809, group_id = 6010108, type = 3, icon = "interaction_4", talk_content = "大伙一起上，别让她坏了我们的好事！", talk_name = "劫匪",},
	[601010901] = { talk_id = 601010901, group_id = 6010109, type = 2, options = {{"（触碰气脉。）",6,},}, talk_content = "你好。", talk_name = "守卫",},
	[601011001] = { talk_id = 601011001, group_id = 6010110, type = 2, options = {{"（触碰气脉。）",6,},}, talk_content = "你好。", talk_name = "守卫",},
	[601011101] = { talk_id = 601011101, group_id = 6010111, type = 2, options = {{"（触碰气脉。）",6,},}, talk_content = "你好。", talk_name = "守卫",},
	[601011201] = { talk_id = 601011201, group_id = 6010112, type = 2, options = {{"离开",},{"<color=#fed78c>启用和窑</color>",5,},}, talk_content = "（内部翻腾着奇异的能量。）",},
	[601011301] = { talk_id = 601011301, group_id = 6010113, type = 4, next_id = 601011302, aside = "<color=#FFEBA2>郭郦：</color>是你啊，怎么急急忙忙的，碰到什么麻烦了吗？", talk_content = "是你啊，怎么急急忙忙的，碰到什么麻烦了吗？", talk_name = "郭郦",},
	[601011302] = { talk_id = 601011302, group_id = 6010113, type = 4, aside = "<color=#FFEBA2>郭郦：</color>开我的车，青乌。", talk_content = "开我的车，青乌。", talk_name = "郭郦",},
	[601011401] = { talk_id = 601011401, group_id = 6010114, type = 4, next_id = 601011402, aside = "<color=#FFEBA2>巡卫：</color>奇怪，刚刚还在这附近，难道跟丢了？", talk_content = "奇怪，刚刚还在这附近，难道跟丢了？", talk_name = "巡卫",},
	[601011402] = { talk_id = 601011402, group_id = 6010114, type = 4, aside = "<color=#FFEBA2>巡卫：</color>你们几个，到其他街区再搜查一下，找到马上通知我！", talk_content = "你们几个，到其他街区再搜查一下，找到马上通知我！", talk_name = "巡卫",},
	[601011501] = { talk_id = 601011501, group_id = 6010115, type = 5,},
	[601011601] = { talk_id = 601011601, group_id = 6010116, type = 1, next_id = 601011602, talk_content = "真的没事了吗……", talk_name = "澜璟",},
	[601011602] = { talk_id = 601011602, group_id = 6010116, type = 1, next_id = 601011603, talk_content = "太谢谢你了，要是没有你都不知道会发生什么。", talk_name = "澜璟",},
	[601011603] = { talk_id = 601011603, group_id = 6010116, type = 1, next_id = 601011801, talk_content = "店里的东西请随便挑选吧，今天全部免费！", talk_name = "澜璟",},
	[601011701] = { talk_id = 601011701, group_id = 6010117, type = 4, aside = "<color=#FFEBA2>澜璟：</color>这也拿太多了，再拿下去我们小店要破产了……", talk_content = "这也拿太多了，再拿下去我们小店要破产了……", talk_name = "澜璟",},
	[601011801] = { talk_id = 601011801, group_id = 6010118, type = 4, aside = "<color=#FFEBA2>？？？：</color>不想惹麻烦就把嘴闭好了！", talk_content = "不想惹麻烦就把嘴闭好了！", talk_name = "？？？",},
	[601011901] = { talk_id = 601011901, group_id = 6010119, type = 1, next_id = 601011902, talk_content = "等一下，先别着急走啊。", talk_name = "咖啡馆店长",},
	[601011902] = { talk_id = 601011902, group_id = 6010119, type = 1, next_id = 601011903, talk_content = "你刚刚拿东西的视频我已经拍下来了，劝你识相点，留下1000约币。", talk_name = "咖啡馆店长",},
	[601011903] = { talk_id = 601011903, group_id = 6010119, type = 2, child_type = 21, options = {{"破财免灾（-1000约币）",601011904,},{"我没有偷东西！",601011905,},}, talk_content = "不然我现在就通知守卫，告诉他们你偷我东西！", talk_name = "咖啡馆店长",},
	[601011904] = { talk_id = 601011904, group_id = 6010119, type = 3, icon = "interaction_4", talk_content = "喂，是守卫吗？我店里有小偷！", talk_name = "咖啡馆店长",},
	[601011905] = { talk_id = 601011905, group_id = 6010119, type = 3, icon = "interaction_4", talk_content = "喂，是守卫吗？我店里有小偷！", talk_name = "咖啡馆店长",},
	[601012001] = { talk_id = 601012001, group_id = 6010120, type = 1, next_id = 601012002, talk_content = "都怪我一时糊涂，居然真以为是研究所里的教授找我要研究资料，谁知道那些家伙居然这么无孔不入。", talk_name = "洛笙",},
	[601012002] = { talk_id = 601012002, group_id = 6010120, type = 2, options = {{"需要帮忙吗？",601012003,},}, talk_content = "那两份资料里有绝密信息，要是被他们破译了不知道会被拿来做什么……", talk_name = "洛笙",},
	[601012003] = { talk_id = 601012003, group_id = 6010120, type = 3, next_id = 601012004, talk_content = "你是……脉者吗？我是枢密院的研究员，现在有两份资料因为我自己的疏忽落到了悬天手里。", talk_name = "洛笙",},
	[601012004] = { talk_id = 601012004, group_id = 6010120, type = 3, next_id = 601012005, talk_content = "要是你能帮忙把资料取回来就好了，可是这里面有不少卫士在巡逻，靠你一个人肯定没办法闯进去的……", talk_name = "洛笙",},
	[601012005] = { talk_id = 601012005, group_id = 6010120, type = 3, talk_content = "这不算什么难事，就交给我吧。", talk_name = "青乌",},
	[601012101] = { talk_id = 601012101, group_id = 6010121, type = 1, next_id = 601012102, talk_content = "悬天重地，闲人免进！", talk_name = "悬天卫士",},
	[601012102] = { talk_id = 601012102, group_id = 6010121, type = 1, talk_content = "再不速速离开，小心刀枪无眼！", talk_name = "悬天卫士",},
	[601012201] = { talk_id = 601012201, group_id = 6010122, type = 4, next_id = 601012202, aside = "<color=#FFEBA2>青乌：</color>（资料获取到了，悬天终端上的信息也已经同步删除了。）", talk_content = "（资料获取到了，悬天终端上的信息也已经同步删除了。）", talk_name = "青乌",},
	[601012202] = { talk_id = 601012202, group_id = 6010122, type = 4, aside = "<color=#FFEBA2>青乌：</color>（不过另一份文件似乎正在被敌人看管，试试能不能引开敌人吧。）", talk_content = "（不过另一份文件似乎正在被敌人看管，试试能不能引开敌人吧。）", talk_name = "青乌",},
	[601012301] = { talk_id = 601012301, group_id = 6010123, type = 1, next_id = 601012302, talk_content = "立刻联运，货物立刻达！", talk_name = "翟远",},
	[601012302] = { talk_id = 601012302, group_id = 6010123, type = 2, options = {{"离开",},{"询问生意状况",601012303,},{"打听有趣消息",601012305,},{"<color=#fed78c>接委托</color>",12,},}, talk_content = "有什么想问的吗？", talk_name = "翟远",},
	[601012303] = { talk_id = 601012303, group_id = 6010123, type = 3, next_id = 601012304, talk_content = "快过节了，寄件堆得像小山一样。", talk_name = "翟远",},
	[601012304] = { talk_id = 601012304, group_id = 6010123, type = 3, next_id = 601012302, talk_content = "生意好是没错，但是人手不够啊，这是要累死我们吗……", talk_name = "翟远",},
	[601012305] = { talk_id = 601012305, group_id = 6010123, type = 3, next_id = 601012306, talk_content = "前段时间来的一个新同事，送货效率是大家平均水平的四倍！", talk_name = "翟远",},
	[601012306] = { talk_id = 601012306, group_id = 6010123, type = 3, next_id = 601012307, talk_content = "我们怎么问，他都不肯告诉我们怎么做到的。", talk_name = "翟远",},
	[601012307] = { talk_id = 601012307, group_id = 6010123, type = 3, next_id = 601012308, talk_content = "听保安卫大爷说，他是去求了神行符才能这么快赶路。", talk_name = "翟远",},
	[601012308] = { talk_id = 601012308, group_id = 6010123, type = 3, next_id = 601012302, talk_content = "不知是真是假，太邪乎了吧！", talk_name = "翟远",},
	[601012309] = { talk_id = 601012309, group_id = 6010123, type = 1, talk_name = "翟远",},
	[601012401] = { talk_id = 601012401, group_id = 6010124, type = 1, next_id = 601012402, talk_content = "各种火锅今天有折扣！", talk_name = "汤韵",},
	[601012402] = { talk_id = 601012402, group_id = 6010124, type = 2, options = {{"离开",},{"询问生意状况",601012403,},{"打听有趣消息",601012406,},{"<color=#fed78c>接委托</color>",12,},}, talk_content = "有什么想问的吗？", talk_name = "汤韵",},
	[601012403] = { talk_id = 601012403, group_id = 6010124, type = 3, next_id = 601012404, talk_content = "最近生意没有预想中好呢……", talk_name = "汤韵",},
	[601012404] = { talk_id = 601012404, group_id = 6010124, type = 3, next_id = 601012405, talk_content = "是因为餐饮业竞争太激烈了吗？三个月内这条街上都新开了四家火锅店了。", talk_name = "汤韵",},
	[601012405] = { talk_id = 601012405, group_id = 6010124, type = 3, next_id = 601012402, talk_content = "不知道过段时间能留下几家呢？", talk_name = "汤韵",},
	[601012406] = { talk_id = 601012406, group_id = 6010124, type = 3, next_id = 601012407, talk_content = "悄悄告诉你，可不能传出去！", talk_name = "汤韵",},
	[601012407] = { talk_id = 601012407, group_id = 6010124, type = 3, next_id = 601012408, talk_content = "昨天前台接到预定包房的电话，你猜是谁打来的？", talk_name = "汤韵",},
	[601012408] = { talk_id = 601012408, group_id = 6010124, type = 3, next_id = 601012409, talk_content = "是元夕坊的戏班要来聚餐！说不定可以看到当家师傅斋无格！", talk_name = "汤韵",},
	[601012409] = { talk_id = 601012409, group_id = 6010124, type = 3, next_id = 601012410, talk_content = "这消息千万别泄露，要不然店面会被粉丝们挤爆的！", talk_name = "汤韵",},
	[601012410] = { talk_id = 601012410, group_id = 6010124, type = 3, next_id = 601012402, talk_content = "对话选项3", talk_name = "汤韵",},
	[601012411] = { talk_id = 601012411, group_id = 6010124, type = 1, talk_content = "再见", talk_name = "汤韵",},
	[601012501] = { talk_id = 601012501, group_id = 6010125, type = 2, options = {{"离开",},{"<color=#fed78c>尝试砍价</color>",8,},{"<color=#fed78c>购物</color>",1,},}, talk_content = "欢迎光临，要不要来点什么？", talk_name = "商人",},
	[601012502] = { talk_id = 601012502, group_id = 6010125, type = 3, next_id = 601012503, talk_content = "造物馆的生意谈不上旺季淡季。", talk_name = "林婳",},
	[601012503] = { talk_id = 601012503, group_id = 6010125, type = 3, next_id = 601012504, talk_content = "客人们的需求千奇百怪，没有固定的规律。", talk_name = "林婳",},
	[601012504] = { talk_id = 601012504, group_id = 6010125, type = 3, next_id = 601012501, talk_content = "但只要能满足客人的要求树立口碑，就不愁生意。", talk_name = "林婳",},
	[601012505] = { talk_id = 601012505, group_id = 6010125, type = 3, next_id = 601012506, talk_content = "那我只告诉你，不要让别人知道噢？", talk_name = "林婳",},
	[601012506] = { talk_id = 601012506, group_id = 6010125, type = 3, next_id = 601012507, talk_content = "原以为店长只是个摄影爱好者，其实他是名资历很深的coser哦！", talk_name = "林婳",},
	[601012507] = { talk_id = 601012507, group_id = 6010125, type = 3, next_id = 601012508, talk_content = "光看他的样子想不到吧？我也是好不容易从网上蛛丝马迹中发现的。", talk_name = "林婳",},
	[601012508] = { talk_id = 601012508, group_id = 6010125, type = 3, next_id = 601012501, talk_content = "你问我为什么费劲去找？这……这不关你的事！", talk_name = "林婳",},
	[601012601] = { talk_id = 601012601, group_id = 6010126, type = 2, options = {{"离开",},{"询问生意状况",601012602,},{"打听有趣消息",601012604,},{"<color=#fed78c>接委托</color>",12,},}, talk_content = "离歌分舍，为您服务。", talk_name = "章典",},
	[601012602] = { talk_id = 601012602, group_id = 6010126, type = 3, next_id = 601012603, talk_content = "嗨，事务所的生意，不就是看天吃饭。", talk_name = "章典",},
	[601012603] = { talk_id = 601012603, group_id = 6010126, type = 3, next_id = 601012601, talk_content = "有时候三天不开张，开张一单生意吃半个月的情况都有呢。", talk_name = "章典",},
	[601012604] = { talk_id = 601012604, group_id = 6010126, type = 3, next_id = 601012605, talk_content = "最近有单帮忙找宠物狗的生意，可让我们吃尽苦头。", talk_name = "章典",},
	[601012605] = { talk_id = 601012605, group_id = 6010126, type = 3, next_id = 601012606, talk_content = "主人说是只可爱的小狗狗，我们就只派了俩人去。", talk_name = "章典",},
	[601012606] = { talk_id = 601012606, group_id = 6010126, type = 3, next_id = 601012607, talk_content = "结果那狗有快200斤重，没有三五个大汉根本按不住。", talk_name = "章典",},
	[601012607] = { talk_id = 601012607, group_id = 6010126, type = 3, next_id = 601012601, talk_content = "害的我们事务所全员出动，耗了一晚上才抓回来！", talk_name = "章典",},
	[601012701] = { talk_id = 601012701, group_id = 6010127, type = 2, options = {{"离开",},{"闲谈",601012703,},{"终战排行",601012702,},{"<color=#fed78c>梦魇入口</color>",11,},{"<color=#fed78c>论辩测试</color>",8,},}, talk_content = "我是选候终战的主理人志杰，想参加选候终战就来找我吧！", talk_name = "选候主理人",},
	[601012702] = { talk_id = 601012702, group_id = 6010127, type = 3, next_id = 601012701, talk_content = "本功能尚未开放哦，想看的话敬请期待吧。", talk_name = "选候主理人",},
	[601012703] = { talk_id = 601012703, group_id = 6010127, type = 3, next_id = 601012701, talk_content = "最近涌现了许多新脉者参加选候终战，好期待新人们的表现啊！", talk_name = "选候主理人",},
