	[601013302] = { talk_id = 601013302, group_id = 6010133, type = 4, aside = "<color=#FFEBA2>斋无格：</color>这样重要的装置可能会安放放在高处。可以请你再找找看吗？", call_man_icon = "斋无格;Textures/Icon/Single/HeadIcon/SquRZhaiwuge.png", child_type = 41, talk_content = "这样重要的装置可能会安放放在高处。可以请你再找找看吗？", talk_name = "斋无格",},
	[601013401] = { talk_id = 601013401, group_id = 6010134, type = 4, next_id = 601013402, aside = "<color=#FFEBA2>斋无格：</color>太好了青乌，看来加油站已经正常运行了！", call_icon = "Textures/Icon/Single/CampIcon/Yuanxifang.png", call_man_icon = "斋无格;Textures/Icon/Single/HeadIcon/SquRZhaiwuge.png", child_type = 41, talk_content = "太好了青乌，看来加油站已经正常运行了！", talk_name = "斋无格",},
	[601013402] = { talk_id = 601013402, group_id = 6010134, type = 4, aside = "<color=#FFEBA2>斋无格：</color>快来我这集合吧，托你的福，商队的可以在这里休整补给了。", call_man_icon = "斋无格;Textures/Icon/Single/HeadIcon/SquRZhaiwuge.png", child_type = 41, talk_content = "快来我这集合吧，托你的福，商队的可以在这里休整补给了。", talk_name = "斋无格",},
	[601013501] = { talk_id = 601013501, group_id = 6010135, type = 2, not_skip = true, options = {{"离开",601013503,},{"询问原因",601013502,},}, talk_content = "你是觉醒者？拜托，可以帮我一个忙吗？", talk_name = "工人",},
	[601013502] = { talk_id = 601013502, group_id = 6010135, type = 3, next_id = 601013601, not_skip = true, talk_content = "本来我在用吊车卸箱子，不小心操作失误，箱子全倒下来了。", talk_name = "工人",},
	[601013503] = { talk_id = 601013503, group_id = 6010135, type = 3, not_skip = true, talk_content = "惨了，这下要被开除……", talk_name = "工人",},
	[601013601] = { talk_id = 601013601, group_id = 6010136, type = 2, not_skip = true, options = {{"所以是你自作自受啊",601013602,},{"竟然做出这么危险的事",601013602,},}, talk_content = "怪我不该昨晚熬夜打游戏，开吊车的时候竟然打瞌睡。", talk_name = "工人",},
	[601013602] = { talk_id = 601013602, group_id = 6010136, type = 3, next_id = 601013603, not_skip = true, talk_content = "还好当时附近没什么人，要不然伤着人可就真闯大祸了……", talk_name = "工人",},
	[601013603] = { talk_id = 601013603, group_id = 6010136, type = 3, next_id = 601013701, not_skip = true, talk_content = "唉，工头就快回来了，要是被他看见，我肯定要被炒鱿鱼的！", talk_name = "工人",},
	[601013701] = { talk_id = 601013701, group_id = 6010137, type = 2, not_skip = true, options = {{"拒绝",601013702,},{"答应要求",601015201,},}, talk_content = "觉醒者，能求你在工头回来前帮帮我，把箱子移回去吗？", talk_name = "工人",},
	[601013702] = { talk_id = 601013702, group_id = 6010137, type = 3, not_skip = true, talk_content = "好吧……我明白了，我自己承担责任吧。", talk_name = "工人",},
	[601013801] = { talk_id = 601013801, group_id = 6010138, type = 2, not_skip = true, options = {{"我还有其他事要忙",601013802,},{"继续帮忙",601013803,},}, talk_content = "时间不多了，得加快速度了！", talk_name = "工人",},
	[601013802] = { talk_id = 601013802, group_id = 6010138, type = 3, next_id = 601014201, not_skip = true, talk_content = "啊……这下完蛋了……", talk_name = "工人",},
	[601013803] = { talk_id = 601013803, group_id = 6010138, type = 3, not_skip = true, talk_content = "嗯嗯，靠你了！", talk_name = "工人",},
	[601013901] = { talk_id = 601013901, group_id = 6010139, type = 1, next_id = 601013902, not_skip = true, talk_content = "惨了！已经来不及了，", talk_name = "工人",},
	[601013902] = { talk_id = 601013902, group_id = 6010139, type = 1, not_skip = true, talk_content = "唉，这下工头不会放过我的。", talk_name = "工人",},
	[601014001] = { talk_id = 601014001, group_id = 6010140, type = 1, next_id = 601014002, not_skip = true, talk_content = "哎哎哎，你要把箱子运哪去啊？", talk_name = "工人",},
	[601014002] = { talk_id = 601014002, group_id = 6010140, type = 1, next_id = 601014003, not_skip = true, talk_content = "算了，你还是放下吧，已经来不及补救了。", talk_name = "工人",},
	[601014003] = { talk_id = 601014003, group_id = 6010140, type = 1, not_skip = true, talk_content = "唉，这下工头不会放过我的。", talk_name = "工人",},
	[601014101] = { talk_id = 601014101, group_id = 6010141, type = 1, next_id = 601014102, not_skip = true, talk_content = "这么快就把所有箱子重新码好了！", talk_name = "工人",},
	[601014102] = { talk_id = 601014102, group_id = 6010141, type = 1, next_id = 601014103, not_skip = true, talk_content = "完美！工头应该发现不了。", talk_name = "工人",},
	[601014103] = { talk_id = 601014103, group_id = 6010141, type = 1, not_skip = true, talk_content = "太感谢了，这是我的一点礼物，虽然不多但是请务必收下！", talk_name = "工人",},
	[601014201] = { talk_id = 601014201, group_id = 6010142, type = 1, not_skip = true, talk_content = "惨了，这下要被开除……", talk_name = "工人",},
	[601014301] = { talk_id = 601014301, group_id = 6010143, type = 1, next_id = 601014302, not_skip = true, talk_content = "你要去哪里呀！", talk_name = "工人",},
	[601014302] = { talk_id = 601014302, group_id = 6010143, type = 1, next_id = 601014303, not_skip = true, talk_content = "如果你要去其他地方的话，那还是算了吧。", talk_name = "工人",},
	[601014303] = { talk_id = 601014303, group_id = 6010143, type = 1, not_skip = true, talk_content = "唉，这下工头不会放过我的。", talk_name = "工人",},
	[601014401] = { talk_id = 601014401, group_id = 6010144, type = 2, not_skip = true, options = {{"默默走开",601014402,},{"关心脉灵",601014403,},}, talk_content = "呜……过不去，怎么都想都过不去吧？", talk_name = "脉灵",},
	[601014402] = { talk_id = 601014402, group_id = 6010144, type = 3, not_skip = true, talk_content = "唉……怎么办呢？", talk_name = "脉灵",},
	[601014403] = { talk_id = 601014403, group_id = 6010144, type = 3, next_id = 601014404, not_skip = true, talk_content = "刚才，我和小伙伴们在散步，突然被树下的亮晶晶吸引了！", talk_name = "脉灵",},
	[601014404] = { talk_id = 601014404, group_id = 6010144, type = 3, next_id = 601014405, not_skip = true, talk_content = "结果一晃神，小伙伴们就穿过马路，走远了！", talk_name = "脉灵",},
	[601014405] = { talk_id = 601014405, group_id = 6010144, type = 3, next_id = 601014501, not_skip = true, talk_content = "可是我怕，我不敢自己过马路，我怕车……", talk_name = "脉灵",},
	[601014501] = { talk_id = 601014501, group_id = 6010145, type = 2, not_skip = true, options = {{"离开",601014504,},{"帮助脉灵",601014502,},}, talk_content = "嗯，该怎么办呢？", talk_name = "脉灵",},
	[601014502] = { talk_id = 601014502, group_id = 6010145, type = 3, next_id = 601014503, not_skip = true, talk_content = "真的呀，那就谢谢你啦！", talk_name = "脉灵",},
	[601014503] = { talk_id = 601014503, group_id = 6010145, type = 3, not_skip = true, talk_content = "我的伙伴们就在那个方向！", talk_name = "脉灵",},
	[601014504] = { talk_id = 601014504, group_id = 6010145, type = 3, not_skip = true, talk_content = "呜呜，那好吧，我在原地等等我的伙伴们。", talk_name = "脉灵",},
	[601014601] = { talk_id = 601014601, group_id = 6010146, type = 2, not_skip = true, options = {{"小菜一碟",601014602,},{"其实我还有别的事",601015101,},}, talk_content = "加油呀，靠你了", talk_name = "脉灵",},
	[601014602] = { talk_id = 601014602, group_id = 6010146, type = 3, not_skip = true, talk_content = "（脉灵期待地看着你）", talk_name = "脉灵",},
	[601014701] = { talk_id = 601014701, group_id = 6010147, type = 1, next_id = 601014801, not_skip = true, talk_content = "哇！要掉下去了！", talk_name = "脉灵",},
	[601014801] = { talk_id = 601014801, group_id = 6010148, type = 1, not_skip = true, talk_content = "还好我反应快！不要再这样了！", talk_name = "脉灵",},
	[601014901] = { talk_id = 601014901, group_id = 6010149, type = 1, next_id = 601014902, not_skip = true, talk_content = "你要去哪里呀！", talk_name = "脉灵",},
	[601014902] = { talk_id = 601014902, group_id = 6010149, type = 1, not_skip = true, talk_content = "如果你不想带我过去的话，那还是把我放回原地吧……", talk_name = "脉灵",},
	[601015001] = { talk_id = 601015001, group_id = 6010150, type = 1, next_id = 601015002, not_skip = true, talk_content = "哇！真的带我过来了！", talk_name = "脉灵",},
	[601015002] = { talk_id = 601015002, group_id = 6010150, type = 1, not_skip = true, talk_content = "收下这些吧！我先去找我的同伴了！", talk_name = "脉灵",},
	[601015101] = { talk_id = 601015101, group_id = 6010151, type = 1, not_skip = true, talk_content = "呜呜，那好吧，我在原地等等我的伙伴们。", talk_name = "脉灵",},
	[601015201] = { talk_id = 601015201, group_id = 6010152, type = 1, not_skip = true, talk_content = "太感谢了！我会想尽办法报答你的！", talk_name = "工人",},
	[601019101] = { talk_id = 601019101, group_id = 6010191, type = 2, options = {{"我可以出钱买下来。",601019201,},{"（转头就走）",601019201,},}, talk_content = "不是你的就不要乱动，把东西放下！", talk_name = "咖啡馆店长",},
	[601019102] = { talk_id = 601019102, group_id = 6010191, type = 3, talk_content = "喂，是维安司吗？这里有人抢我东西！", talk_name = "咖啡馆店长",},
	[601019201] = { talk_id = 601019201, group_id = 6010192, type = 2, options = {{"不好意思，刚才只是不小心。",601019202,},{"那又如何？",601019203,},}, talk_content = "擅自攻击天月城守卫可是违法行为，劝你考虑清楚。", talk_name = "守卫",},
	[601019202] = { talk_id = 601019202, group_id = 6010192, type = 1, talk_content = "没关系，下不为例。", talk_name = "守卫",},
	[601019203] = { talk_id = 601019203, group_id = 6010192, type = 1, talk_content = "那就别怪我不客气了。", talk_name = "守卫",},
	[601019301] = { talk_id = 601019301, group_id = 6010193, type = 2, child_type = 21, options = {{"是我唐突了……",601019302,},{"你休想制服我！",601019303,},}, talk_content = "你涉嫌扰乱天月城秩序，现在依法将你逮捕。", talk_name = "守卫",},
	[601019302] = { talk_id = 601019302, group_id = 6010193, type = 1, talk_content = "无妨，现在轮到法规来处置你了。", talk_name = "守卫",},
	[601019303] = { talk_id = 601019303, group_id = 6010193, type = 1, icon = "d_1001", talk_content = "敬酒不吃吃罚酒！", talk_name = "守卫",},
	[601019401] = { talk_id = 601019401, group_id = 6010194, type = 4, aside = "<color=#FFEBA2>？？？：</color>救命！救命！", talk_content = "救命！救命！", talk_name = "？？？",},
	[601019501] = { talk_id = 601019501, group_id = 6010195, type = 1, next_id = 601019502, talk_content = "幸亏有你在，不然我这店就要被人砸了。对了，小小心意，还请你不要推辞。", talk_name = "咖啡馆店长",},
	[601019502] = { talk_id = 601019502, group_id = 6010195, type = 1, next_id = 601019503, talk_content = "我这里还有些水果，就在那边的柜台上，想要就一起送给你了。", talk_name = "咖啡馆店长",},
	[601019503] = { talk_id = 601019503, group_id = 6010195, type = 1, talk_content = "我还有点事，你自己去拿就好，不用客气。", talk_name = "咖啡馆店长",},
