	[602010101] = { talk_id = 602010101, group_id = 6020101, type = 1, next_id = 602010102, talk_content = "幸运摇摇乐，有胆你就来！一等奖中奖率高达30%，动动手即可参与！走过路过不要错过！", talk_name = "陶贞",},
	[602010102] = { talk_id = 602010102, group_id = 6020101, type = 1, next_id = 602010103, talk_content = "呀，这边的脉者，要不要来一发抽奖试试手气？报名费只要两杯奶茶钱，参加开心一天，错过后悔一年哦！", talk_name = "陶贞",},
	[602010103] = { talk_id = 602010103, group_id = 6020101, type = 1, next_id = 602010104, talk_content = "中奖率真有那么高吗？", talk_name = "青乌",},
	[602010104] = { talk_id = 602010104, group_id = 6020101, type = 1, next_id = 602010105, talk_content = "那是当然。按照规则，抽到蓝球得一等奖，黄球得二等奖，绿球得三等奖，白球无奖。", talk_name = "陶贞",},
	[602010105] = { talk_id = 602010105, group_id = 6020101, type = 2, options = {{"那就试试看吧。",602010107,},{"我再考虑考虑。",602010106,},}, talk_content = "所有奖品的中奖率总和有80%呢，是不是很心动？现在就来试试看吧？", talk_name = "陶贞",},
	[602010106] = { talk_id = 602010106, group_id = 6020101, type = 3, talk_content = "好好好，不过这么好的机会可不多见，错过这次就没下次了哦？", talk_name = "陶贞",},
	[602010107] = { talk_id = 602010107, group_id = 6020101, type = 2, child_type = 21, options = {{"随便抽一个。（-200约币）",602010108,},{"总觉得不太对劲……",602010109,},}, talk_content = "（要抽取哪一个奖球呢？）", talk_name = "青乌",},
	[602010108] = { talk_id = 602010108, group_id = 6020101, type = 3, icon = "interaction_4", talk_content = "哎呀，真可惜，是白色小球呢。客官有需要可以再来，说不定下次就能中大奖了哦。", talk_name = "陶贞",},
