	[602010110] = { talk_id = 602010110, group_id = 6020101, type = 3, talk_content = "客官还真是谨慎啊，那就再考虑一下吧。不过如果考虑太久，大奖可就被别人拿走了哦。", talk_name = "陶贞",},
