	[602010209] = { talk_id = 602010209, group_id = 6020102, type = 3, next_id = 602010210, icon = "interaction_4", talk_content = "蓝……蓝色……？这怎么可能……", talk_name = "陶贞",},
	[602010210] = { talk_id = 602010210, group_id = 6020102, type = 3, next_id = 602010211, talk_content = "抽到蓝色小球就可以拿一等奖，对吧？", talk_name = "青乌",},
	[602010211] = { talk_id = 602010211, group_id = 6020102, type = 2, options = {{"我可没有耍赖。",602010212,},{"耍赖的人是你才对吧。",602010212,},}, talk_content = "可是我明明……不对，你肯定是耍赖了！你刚刚摸球的时候偷偷看了球的位置！", talk_name = "陶贞",},
	[602010212] = { talk_id = 602010212, group_id = 6020102, type = 3, talk_content = "我们可不能纵容你这种破坏抽奖规则的人，快把奖球交出来！", talk_name = "陶贞",},
